/*Data for the table `ad_activity` */

insert  into `ad_activity`(`id`,`name`,`description`,`image`,`priority`,`type`,`is_active`,`created_at`,`updated_at`) values (1,'Huấn luyện và dẫn dắt','Học viên được dẫn dắt bởi những chuyên viên trong lĩnh vực Xuất nhập khẩu, logistics giàu kinh nghiệm. Chúng tôi không phân biệt điểm xuất phát của bạn mà chỉ quan tâm đến mục tiêu của bạn là gì và dẫn dắt bạn đi đúng hướng.','/2b/2b/b2/5a66b00e36a31.png',10,1,1,'2018-01-14 17:36:06','2018-01-24 11:02:02'),(2,'Kết nối hoạt động thực tế','Học viên được học thật làm thật trên bộ chứng từ của doanh nghiệp (thanh toán,CO, vận tải, bảo hiểm…), được hỗ trợ kết nối, giao lưu với doanh nghiệp và các nhà tuyển dụng trong nghề, đi thực tế tại các DN, kho bãi, cảng, sân bay…','/5b/5b/b5/5a66b01ce7108.png',9,1,1,'2018-01-14 17:40:39','2018-01-24 11:02:38'),(3,'Phát triển tư duy công viêc','Học viên được đào tạo bằng phương  pháp phát triển tư duy tích cực, chủ động giúp bạn tự khám phá bản thân, tự giải quyết được những tình huống khó khăn để từ đó khẳng định được vai trò và vị trí của mình trong doanh nghiệp.','/0e/44/40/5a6a245884c26.png',8,1,1,'2018-01-14 17:40:58','2018-01-26 01:39:20'),(4,'Hoạt động 1','','/fb/76/6f/5a66b2b06d1f4.jpg',0,2,1,'2018-01-14 17:42:48','2018-01-24 11:54:44'),(5,'Học viên tự tin','','/83/74/48/5a66bab52e554.jpg',90,2,1,'2018-01-14 17:43:08','2018-01-30 11:12:25'),(6,'Học tập trên lớp','','/23/f6/62/5a66bacf28d4d.jpg',99,2,1,'2018-01-14 17:43:38','2018-01-30 11:13:02'),(8,'Hoạt động 4','','/de/1a/ad/5a66baf944245.jpg',0,2,1,'2018-01-16 22:29:35','2018-01-23 11:32:57'),(9,'Thực hành trên lớp','','/8e/c6/68/5a66bb0c2fb44.jpg',96,2,1,'2018-01-18 20:32:21','2018-01-30 11:14:36'),(10,'hd 6','','/df/74/4d/5a66e9744e4bc.jpg',0,2,1,'2018-01-23 11:34:02','2018-01-23 14:51:16'),(11,'hd7','','/ab/ca/aa/5a66bb4799264.jpg',0,2,1,'2018-01-23 11:34:15','2018-01-23 11:35:53'),(12,'Đi thực tế thông quan','','/97/4b/b9/5a66bb6eccd0b.jpg',98,2,1,'2018-01-23 11:34:54','2018-01-30 11:15:16'),(13,'hd9','','/8c/23/38/5a6