/*Table structure for table `ad_article` */

DROP TABLE IF EXISTS `ad_article`;

CREATE TABLE `ad_article` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` text NOT NULL COMMENT 'Tiêu đề bài viết',
  `alttitle` varchar(255) DEFAULT NULL COMMENT 'Tiêu đề rút gọn',
  `header` longtext COMMENT 'Trích yếu',
  `body` longtext COMMENT 'Nội dung bài viết trên web',
  `image_path` varchar(255) DEFAULT NULL COMMENT 'Đường dẫn ảnh đại diện',
  `attributes` bigint(20) DEFAULT NULL COMMENT 'Thuộc tính của bài viết: khuyến mại, ',
  `hit_count` bigint(20) DEFAULT '0' COMMENT 'Số lượt truy cập',
  `priority` bigint(20) NOT NULL COMMENT 'Độ ưu tiên hiển thị',
  `published_time` datetime DEFAULT NULL COMMENT 'Thời gian xuất bản',
  `expiredate_time` datetime DEFAULT NULL COMMENT 'Thời gian kết thúc bản tin',
  `meta` varchar(255) DEFAULT NULL COMMENT 'Nội dung meta',
  `keywords` varchar(255) DEFAULT NULL COMMENT 'Nội dung keywords',
  `author` varchar(255) DEFAULT NULL COMMENT 'Tác giả',
  `other_link` longtext COMMENT 'Các link liên quan',
  `is_active` bigint(20) NOT NULL DEFAULT '0' COMMENT 'Trạng thái hiển thị (-1: Bản nháp, 0- Chờ duyệt, 1- Đã duyệt, 2- Đã đăng)',
  `is_hot` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Hiển thị trên trang chủ (0- ko hiển thị, 1- hiển thị)',
  `lang` varchar(10) NOT NULL COMMENT 'Đa ngôn ngữ',
  `slug` varchar(255) NOT NULL COMMENT 'chuyển đổi url',
  `category_id` bigint(20) DEFAULT NULL COMMENT 'Id của danh mục tin tức',
  `created_by` bigint(20) DEFAULT NULL,
  `updated_by` bigint(20) DEFAULT NULL,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `slug` (`slug`),
  KEY `category_id_idx` (`category_id`),
  KEY `created_by_idx` (`created_by`),
  KEY `updated_by_idx` (`updated_by`)
) ENGINE=MyISAM AUTO_INCREMENT=292 DEFAULT CHARSET=utf8;

/*Data for the table `ad_article` */

insert  into `ad_article`(`id`,`title`,`alttitle`,`header`,`body`,`image_path`,`attributes`,`hit_count`,`priority`,`published_time`,`expiredate_time`,`meta`,`keywords`,`author`,`other_link`,`is_active`,`is_hot`,`lang`,`slug`,`category_id`,`created_by`,`updated_by`,`created_at`,`updated_at`) values (215,'HƯỚNG DẪN VIẾT EMAIL CHUYÊN NGHIỆP','','Như chúng ta đã biết, trong thời buổi ứng dụng CNTT như hiện nay thì email là một trong những phương tiện liên lạc thường xuyên, nhất là trong môi trường công sở, các công ty nước ngoài. Tuy nhiên, sau khi check mail của nhiều học viên tôi nhận thấy rằng một số bạn chưa có kỹ năng viết email cơ bản. Sau đây, tôi sẽ hướng dẫn các bạn cách viết email một cách chuyên nghiệp nhất.','<p>Như ch&uacute;ng ta đ&atilde; biết, trong thời buổi ứng dụng CNTT như hiện nay th&igrave; email l&agrave; một trong những phương tiện li&ecirc;n lạc thường xuy&ecirc;n, nhất l&agrave; trong m&ocirc;i trường c&ocirc;ng sở, c&aacute;c c&ocirc;ng ty nước ngo&agrave;i. Tuy nhi&ecirc;n, sau khi check mail của nhiều học vi&ecirc;n t&ocirc;i nhận thấy rằng một số bạn chưa c&oacute; kỹ năng viết email cơ bản. Sau đ&acirc;y, t&ocirc;i sẽ hướng dẫn c&aacute;c bạn c&aacute;ch viết email một c&aacute;ch chuy&ecirc;n nghiệp nhất.</p>\r\n\r\n<p><strong>1. Ti&ecirc;u đề email:</strong></p>\r\n\r\n<p>Đầu ti&ecirc;n khi nhận một email, người đọc lu&ocirc;n nh&igrave;n v&agrave;o ti&ecirc;u đề email (Subject) l&agrave; trước nhất. Ti&ecirc;u đề email l&agrave; g&igrave;? L&agrave; c&aacute;i m&agrave; khiến người đọc quyết định mở hay kh&ocirc;ng mở, mở ngay hay để sau email của bạn. Ti&ecirc;u đề email n&ecirc;n ngắn gọn, s&uacute;c t&iacute;ch v&agrave; thể hiện được nội dung ch&iacute;nh muốn truyền tải. Dưới đ&acirc;y l&agrave; một số v&iacute; dụ về ti&ecirc;u đề sai m&agrave; phần lớn sinh vi&ecirc;n hay mắc phải v&agrave; một số mẫu ti&ecirc;u đề tốt để c&aacute;c em c&oacute; thể tham khảo:</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/2.%20HD%20viet%20email/tieu-de-email-mau-check-and-cross.jpg\" style=\"height:506px; width:886px\" /></p>\r\n\r\n<p>Trong trường hợp email được gửi đi gửi lại, forward nhiều lần, mail outlook sẽ xảy ra t&igrave;nh trạng tự động th&ecirc;m <em>&ldquo;Re:&rdquo;</em> v&agrave; <em>&ldquo;Fw&rdquo;</em> nhiều lần li&ecirc;n tiếp v&agrave;o ti&ecirc;u đề email: <em>Re: Re: Fw: Re: Fw: Re: Re:</em> Nếu l&agrave; một người cẩn thận v&agrave; tinh tế, bạn kh&ocirc;ng n&ecirc;n để nguy&ecirc;n một email c&oacute; đầu ti&ecirc;u đề như vậy. Bạn ho&agrave;n to&agrave;n c&oacute; thể chủ động x&oacute;a bớt, chỉ để lại 1 lần <em>Fw:</em> hay <em>Re:</em> l&uacute;c ch&iacute;nh bạn gửi đi m&agrave; th&ocirc;i.</p>\r\n\r\n<p><strong>2. Địa chỉ email:</strong></p>\r\n\r\n<p>Điều thứ 2 sau khi đọc ti&ecirc;u đề, người nhận sẽ liếc mắt xem &ldquo;ai l&agrave; người gửi email đ&oacute;?&rdquo;. Nếu đ&oacute; l&agrave; <em>&ldquo;tinhdonphuong@&hellip;&rdquo;, &ldquo;maiyeuem123@&hellip;&rdquo;, &ldquo;cobelolem@&hellip;&rdquo;</em> th&igrave; r&otilde; r&agrave;ng bạn <strong>&ldquo;fail&rdquo;</strong> ngay lập tức v&agrave; d&ugrave; ti&ecirc;u đề tr&ecirc;n c&oacute; hay cỡ n&agrave;o th&igrave; bạn cũng chỉ được đ&aacute;nh gi&aacute; l&agrave; đứa &ldquo;trẻ tr&acirc;u&rdquo;, kh&ocirc;ng hơn. Ti&ecirc;u đề email phải chứa t&ecirc;n của m&igrave;nh, một c&aacute;ch nghi&ecirc;m t&uacute;c. C&oacute; thể họ t&ecirc;n qu&aacute; d&agrave;i, bạn chỉ cần đặt họ-t&ecirc;n v&agrave; một v&agrave;i con số c&oacute; &yacute; nghĩa để nhận dạng/ph&acirc;n biệt email của bạn l&agrave; duy nhất. Hoặc kết hợp với một t&ecirc;n gọi tiếng Anh m&agrave; bạn sẽ sử dụng n&oacute; trong c&ocirc;ng việc. Nếu bạn đang c&oacute; một địa chỉ email đầy c&aacute; t&iacute;nh như v&iacute; dụ n&ecirc;u tr&ecirc;n, h&atilde;y tạo ngay cho m&igrave;nh một t&agrave;i khoản email mới v&agrave; chuy&ecirc;n nghiệp nh&eacute;!</p>\r\n\r\n<p><strong>3. Gửi cho ai th&igrave; ch&agrave;o người đ&oacute;:</strong></p>\r\n\r\n<p>Tất nhi&ecirc;n, điều thứ 3 rất đơn giản. Bạn gửi mail cho ai th&igrave; lời mở đầu h&atilde;y d&agrave;nh cho họ. Trong trường hợp th&acirc;n thiết, bạn c&oacute; thể <em>&ldquo;Hi/Xin ch&agrave;o/Ch&agrave;o bạn-anh-em&hellip; + [t&ecirc;n ri&ecirc;ng] + [dấu phẩy]&rdquo;</em>. Tại sao lại l&agrave; dấu phẩy? Người Việt Nam d&ugrave;ng dấu chấm than cuối c&acirc;u để biểu cảm (trong c&acirc;u ch&agrave;o c&oacute; &yacute; nghĩa t&iacute;ch cực), nhưng người nước ngo&agrave;i, đ&oacute; thể hiện sự nhấn mạnh (như l&agrave; bạn g&agrave;o v&agrave;o mặt họ). Vậy n&ecirc;n, để lịch sự v&agrave; &ldquo;&ecirc;m &aacute;i&rdquo; nhất cho một khởi đầu, bạn chỉ cần đặt dấu phẩy th&ocirc;i nh&eacute;. V&iacute; dụ: <em>Hi Tom, &hellip; Hi Ana, &hellip;</em> C&ograve;n đối với trường hợp trịnh trọng hơn, lịch sự hơn nữa th&igrave; &ldquo;<em>Dear Mr/Miss/Msr + [t&ecirc;n ri&ecirc;ng/Họ &ndash; nếu l&agrave; người nước ngo&agrave;i] + [dấu phẩy]&rdquo;</em>; <em>&ldquo;K&iacute;nh gửi &ocirc;ng/b&agrave;/anh/chị&hellip;/Gửi/Th&acirc;n gửi bạn/em&hellip;&rdquo;</em></p>\r\n\r\n<p>Nếu bạn muốn hơn một người đọc v&agrave; ch&uacute; &yacute; đến email của m&igrave;nh, h&atilde;y chọn 1-2 người để <em>&ldquo;Dear&rdquo;</em> v&agrave; một số người c&ograve;n lại, bạn d&ugrave;ng <em>&ldquo;Cc&rdquo;</em>, mặc d&ugrave; danh s&aacute;ch những người Cc nhiều hơn nhưng bạn cần nhấn mạnh t&ecirc;n một số người &ldquo;nhất thiết phải đọc email n&agrave;y&rdquo;, tr&igrave;nh b&agrave;y như sau:</p>\r\n\r\n<p><em>&ldquo;Dear Mr &amp; Msr James,</em></p>\r\n\r\n<p><em>Cc: Mr Celis, Miss Mclean &amp; Miss Moris&rdquo;</em></p>\r\n\r\n<p><strong>4. Nội dung email:</strong></p>\r\n\r\n<p>Thứ tư, đối với email trong c&ocirc;ng việc, t&ugrave;y thời điểm ph&aacute;t sinh để c&oacute; c&aacute;ch viết email cũng như tr&igrave;nh b&agrave;y nội dung ph&ugrave; hợp.</p>\r\n\r\n<p>Nếu bạn l&agrave; người tạo email đầu ti&ecirc;n để tr&igrave;nh b&agrave;y một vấn đề. Vấn đề được n&oacute;i đến ở đ&acirc;y l&agrave; mục đ&iacute;ch ch&iacute;nh m&agrave; email bạn muốn truyền tải. T&ugrave;y mục đ&iacute;ch v&agrave; t&iacute;nh chất c&ocirc;ng việc để bạn c&oacute; c&aacute;ch diễn đạt ph&ugrave; hợp v&agrave; để bắt đầu triển khai vấn đề, nhất thiết th&acirc;n mail của bạn phải c&oacute; đủ 3 phần: <strong>Mở, Th&acirc;n v&agrave; Kết</strong>. <em>Mở b&agrave;i</em>: tr&igrave;nh b&agrave;y vấn đề một c&aacute;ch r&otilde; r&agrave;ng, cụ thể, <em>Th&acirc;n b&agrave;i</em>: đề cập đến phương &aacute;n/phương ph&aacute;p giải quyết vấn đề n&ecirc;u tr&ecirc;n hay nhận định của bản th&acirc;n bạn, <em>Kết b&agrave;i</em>: Y&ecirc;u cầu nhận được g&oacute;p &yacute; hoặc y&ecirc;u cầu nhận được thư hồi đ&aacute;p cho c&aacute;c c&acirc;u hỏi li&ecirc;n quan đến vấn đề.</p>\r\n\r\n<p>Từ email thứ 2 trở đi, nội dung thư chỉ cần đi thẳng v&agrave;o vấn đề cần giải quyết, kh&ocirc;ng cần phải d&agrave;i d&ograve;ng văn tự. Chỉ cần phải đảm bảo: <em>1) dễ hiểu để người đọc kh&ocirc;ng mất qu&aacute; nhiều thời gian ph&acirc;n t&iacute;ch email của bạn, 2) kh&ocirc;ng qu&aacute; d&agrave;i, để người đọc lu&ocirc;n vui l&ograve;ng v&agrave; sẵn s&agrave;ng d&agrave;nh thời gian đọc thư của bạn, 3) đủ &yacute; v&agrave; truyền tải được &yacute; ch&iacute;nh cũng như đạt mục đ&iacute;ch của email.</em></p>\r\n\r\n<p><strong>5. C&aacute;ch tr&igrave;nh b&agrave;y email:</strong></p>\r\n\r\n<p>Thứ năm, giữa c&aacute;c phần của email lu&ocirc;n để khoảng trống c&aacute;ch nhau 1 d&ograve;ng (enter), email tr&ocirc;ng sẽ tho&aacute;ng v&agrave; gi&uacute;p người đọc dễ theo d&otilde;i c&aacute;c &yacute; ch&iacute;nh. Ng&ocirc;n ngữ email n&ecirc;n ngắn gọn, s&uacute;c t&iacute;ch, kh&ocirc;ng lan man c&acirc;y c&agrave; ra c&acirc;y k&ecirc;, kh&ocirc;ng lạm dụng c&aacute;c t&iacute;nh từ cảm x&uacute;c trong email c&ocirc;ng việc; tr&aacute;nh sử dụng c&aacute;c c&acirc;u cảm th&aacute;n hay những c&acirc;u đ&ugrave;a cợt; tr&aacute;nh viết những cấu tr&uacute;c v&agrave; từ ngữ kh&oacute; hiểu. Tr&aacute;nh d&ugrave;ng những từ ngữ mang t&iacute;nh ẩn &yacute;, ẩn dụ, b&oacute;ng gi&oacute;, đối với tiếng Anh, n&ecirc;n d&ugrave;ng những từ đơn giản v&agrave; phổ th&ocirc;ng thay v&igrave; cố gắng thể hiện m&igrave;nh bằng việc d&ugrave;ng những từ ngữ mang t&iacute;nh học thuật, phức tạp v&agrave; &iacute;t gặp.</p>\r\n\r\n<p>Trong một email chỉ n&ecirc;n sử dụng thống nhất 1 font chữ, 1-2 size chữ, 1-2 m&agrave;u chữ trong trường hợp bạn muốn nhấn mạnh đến 1 số cụm từ/c&acirc;u quan trọng. Kh&ocirc;ng t&ugrave;y tiện viết tắt, viết hoa; viết thường t&ecirc;n ri&ecirc;ng cũng l&agrave; một điều bất lịch sự. C&aacute;c bạn chỉ n&ecirc;n viết tắt những từ/cụm từ th&ocirc;ng dụng chuy&ecirc;n ng&agrave;nh thay v&igrave; viết tắt theo style của ch&iacute;nh bạn, đặc biệt <strong>KH&Ocirc;NG SỬ DỤNG teen code</strong> trong email như: mk, hok, đc, bik, j, pm&hellip;; viết đầy đủ đại từ nh&acirc;n xưng: &ocirc;ng, b&agrave;, anh, chị, em,&hellip; thay v&igrave; &ocirc;, b, a, c, e&hellip;</p>\r\n\r\n<p>Khi bạn d&ugrave;ng c&acirc;u c&oacute; liệt k&ecirc;, trong tiếng Việt bạn c&oacute; thể d&ugrave;ng dấu ba chấm &hellip; để chỉ &ldquo;c&ograve;n nữa, v&acirc;n v&acirc;n&rdquo; nhưng trong tiếng Anh bạn h&atilde;y cẩn thận, d&ugrave;ng etc. c&oacute; nghĩa l&agrave; &ldquo;v&acirc;n v&acirc;n&rdquo;, c&ograve;n nếu bạn d&ugrave;ng dấu ba chấm sẽ khiến người đọc (l&agrave; người nước ngo&agrave;i) nghĩ rằng bạn đang ngập ngừng, chưa n&oacute;i hết c&acirc;u.</p>\r\n\r\n<p>C&oacute; thể gạch đầu d&ograve;ng để t&aacute;ch biệt c&aacute;c &yacute;/c&aacute;c vấn đề m&agrave; bạn cần truyền tải trong email.</p>\r\n\r\n<p><strong>6. Sử dụng h&igrave;nh ảnh/file đ&iacute;nh k&egrave;m:</strong></p>\r\n\r\n<p>Thứ s&aacute;u, file đ&iacute;nh k&egrave;m l&agrave; một phần rất quan trọng trong email c&ocirc;ng việc. Để đ&iacute;nh k&egrave;m, đầu ti&ecirc;n bạn cần kiểm tra số lượng file sẽ đ&iacute;nh k&egrave;m để đảm bảo gửi ĐỦ &ndash; kh&ocirc;ng thừa, kh&ocirc;ng thiếu. Chu đ&aacute;o hơn, bạn n&ecirc;n đổi t&ecirc;n file giống t&ecirc;n t&agrave;i liệu bạn gửi để người nhận cũng như ch&iacute;nh bạn dễ d&agrave;ng hơn trong lưu trữ v&agrave; t&igrave;m kiếm lại sau n&agrave;y, thay v&igrave; những t&ecirc;n file l&agrave; một cụm số/chữ c&aacute;i kh&ocirc;ng li&ecirc;n quan g&igrave; đến file bạn gửi (trừ khi bạn forward email v&agrave; giữ nguy&ecirc;n mail gốc).</p>\r\n\r\n<p>Trong file đ&iacute;nh k&egrave;m, những g&igrave; bạn cần người xem lưu &yacute; bạn đều c&oacute; thể đ&aacute;nh dấu bằng c&aacute;c c&ocirc;ng cụ c&oacute; sẵn trong word, excel, pdf hoặc paint v&agrave; nhắc người đọc ch&uacute; &yacute; những chỗ bạn đ&atilde; b&ocirc;i đỏ/khoanh tr&ograve;n/đ&aacute;nh dấu&hellip;</p>\r\n\r\n<p><strong>7. Chữ k&yacute; cuối thư (signature):</strong></p>\r\n\r\n<p>Thứ bảy, đầu thư bạn kh&ocirc;ng phải giới thiệu qu&aacute; nhiều về m&igrave;nh th&igrave; chữ k&yacute; cuối thư ch&iacute;nh l&agrave; l&uacute;c bạn thể hiện m&igrave;nh l&agrave; ai, đến từ đ&acirc;u, bạn chuy&ecirc;n nghiệp như thế n&agrave;o một c&aacute;ch khi&ecirc;m nhường v&agrave; đ&uacute;ng l&uacute;c. V&igrave; l&uacute;c n&agrave;y người đọc thư sẽ quan t&acirc;m đến bạn l&agrave; ai sau khi ki&ecirc;n nhẫn đọc hết thư của bạn. Do đ&oacute;, chữ k&yacute; cuối thư l&agrave; một phần giới thiệu bản th&acirc;n rất lịch sự, tế nhị m&agrave; bạn rất cần thiết phải c&agrave;i đặt cho t&agrave;i khoản email c&aacute; nh&acirc;n của m&igrave;nh cũng như email c&ocirc;ng ty cấp cho bạn. Cấu tr&uacute;c một chữ k&yacute; bao gồm c&aacute;c phần sau:</p>\r\n\r\n<p><em>Lời cảm ơn</em></p>\r\n\r\n<p><em>[Họ t&ecirc;n đầy đủ hoặc chỉ ghi họ+t&ecirc;n] + (Ms/Mr =&gt; để người nhận email dễ xưng h&ocirc;)</em></p>\r\n\r\n<p><em>Chức vụ/Vị tr&iacute; c&ocirc;ng t&aacute;c</em></p>\r\n\r\n<p><em>Tel: M&atilde; v&ugrave;ng quốc gia + Số điện thoại c&ocirc;ng ty + (số m&aacute;y lẻ &ndash; nếu c&oacute;)</em></p>\r\n\r\n<p><em>Cellphone: M&atilde; v&ugrave;ng quốc gia + Số c&aacute; nh&acirc;n</em></p>\r\n\r\n<p><em>Email: </em></p>\r\n\r\n<p><em>Skype/Whatapps: Một v&agrave;i ứng dụng chat trực tuyến c&ocirc;ng ty cho ph&eacute;p sử dụng trong c&ocirc;ng việc</em></p>\r\n\r\n<p>V&iacute; dụ:</p>\r\n\r\n<p><strong>Thanks and best regards,</strong></p>\r\n\r\n<p>Nếu bạn l&agrave; sinh vi&ecirc;n, chữ k&yacute; đơn giản hơn:</p>\r\n\r\n<p>Lời cảm ơn</p>\r\n\r\n<p>[Họ t&ecirc;n đầy đủ hoặc chỉ ghi họ+t&ecirc;n] + (Ms/Mr =&gt; để người nhận email dễ xưng h&ocirc;)</p>\r\n\r\n<p>T&ecirc;n khoa/ng&agrave;nh/trường học</p>\r\n\r\n<p>Tel: M&atilde; v&ugrave;ng quốc gia + Số c&aacute; nh&acirc;n</p>\r\n\r\n<p>Email:</p>\r\n\r\n<p>Skype/Whatapps: Một v&agrave;i ứng dụng chat trực tuyến bạn c&oacute; thể sử dụng</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/2.%20HD%20viet%20email/signature-chu-ky-chan-mail.jpg\" style=\"height:192px; width:558px\" /></p>\r\n\r\n<p><strong>8. Sử dụng t&iacute;nh năng Cc v&agrave; Bcc:</strong></p>\r\n\r\n<p>Thứ t&aacute;m, ngo&agrave;i mục <em>To (Đến)</em> để điền địa chỉ email của người c&oacute; tr&aacute;ch nhiệm trực tiếp đối với việc xử l&yacute; email n&agrave;y, th&igrave; c&oacute; 2 mục cũng d&ugrave;ng để điền th&ecirc;m t&ecirc;n những người được nhận email n&oacute;i tr&ecirc;n l&agrave; <em>Cc</em> (<em>Carbon copy</em>) v&agrave; <em>Bcc (Blind carbon copy).</em> Hiểu một c&aacute;ch đơn giản, những địa chỉ email trong danh s&aacute;ch <em>Bcc</em> l&agrave; những email được giấu đi, những người nhận kh&aacute;c kh&ocirc;ng nh&igrave;n thấy địa chỉ email của họ trừ người gửi email đ&oacute; cho họ; những địa chỉ email được <em>Cc</em> th&igrave; mọi người đều nh&igrave;n thấy được. Nếu bạn muốn gửi email cho một tập email m&agrave; kh&ocirc;ng muốn lộ danh s&aacute;ch những người c&ugrave;ng được nhận email đ&oacute; th&igrave; d&ugrave;ng chế độ <em>Bcc</em> l&agrave; ph&ugrave; hợp.</p>\r\n\r\n<p><strong>9. Cẩn thận trước khi ấn n&uacute;t &ldquo;send&rdquo;</strong></p>\r\n\r\n<p>Thứ ch&iacute;n, h&atilde;y tập th&oacute;i quen nhập địa chỉ email sau c&ugrave;ng để tr&aacute;nh trường hợp &ldquo;lỡ tay cướp c&ograve;&rdquo; hoặc bất kỳ sơ suất n&agrave;o khi bạn chưa kịp đọc lại email trước khi ấn gửi. Sau khi soạn xong email, kiểm tra đầy đủ c&aacute;c file đ&iacute;nh k&egrave;m cần thiết cũng như nội dung tr&igrave;nh b&agrave;y trong email, l&uacute;c đ&oacute; bạn mới nhập t&ecirc;n người nhận v&agrave;o.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/2.%20HD%20viet%20email/huong-dan-cach-viet-email-chuyen-nghiep.jpg\" style=\"height:539px; width:949px\" /></p>\r\n\r\n<p>Ngo&agrave;i ra, nếu bạn c&agrave;i đặt hơn 1 t&agrave;i khoản v&agrave;o outlook, khi gửi thư đi, bạn phải ch&uacute; &yacute; lựa chọn đ&uacute;ng địa chỉ email muốn gửi (&Ocirc; <em>From</em> &ndash; h&igrave;nh tr&ecirc;n). Chuyện g&igrave; sẽ xảy ra nếu gửi email cho đối t&aacute;c thay v&igrave; gửi từ t&agrave;i khoản email n&agrave;y th&igrave; bạn lại gửi nhầm từ t&agrave;i khoản email kh&aacute;c? Hết sức cẩn thận thao t&aacute;c n&agrave;y nh&eacute;!</p>\r\n\r\n<p>Đối với trường hợp <em>Reply all</em>, bạn kh&ocirc;ng nhất thiết phải gửi lại email đ&oacute; cho to&agrave;n bộ người nhận trong danh s&aacute;ch được nhận. Trong qu&aacute; tr&igrave;nh l&agrave;m việc, để &yacute; một ch&uacute;t bạn sẽ biết ai l&agrave; người cần đọc/nhận email trả lời của bạn để chủ động x&oacute;a bớt những địa chỉ kh&ocirc;ng cần thiết để tr&aacute;nh l&agrave;m phiền đến họ v&igrave; c&oacute; thể họ rất bận v&agrave; cũng kh&ocirc;ng c&oacute; thời gian quan t&acirc;m đến bạn, c&acirc;u trả lời của bạn cũng kh&ocirc;ng phải phận sự giải quyết của họ. Vậy n&ecirc;n, ch&uacute; &yacute; r&agrave; so&aacute;t danh s&aacute;ch trước khi reply all nh&eacute;!</p>\r\n\r\n<p><strong>10. Kh&ocirc;ng c&oacute; g&igrave; l&agrave; b&iacute; mật</strong></p>\r\n\r\n<p><strong><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/2.%20HD%20viet%20email/bao-mat-email-cong-viec.jpg\" style=\"height:530px; width:450px\" /></strong></p>\r\n\r\n<p>Cuối c&ugrave;ng, trong c&ocirc;ng việc, tuyệt đối bạn kh&ocirc;ng n&ecirc;n d&ugrave;ng email c&ocirc;ng việc để l&agrave;m việc c&aacute; nh&acirc;n hoặc trao đổi với ai đ&oacute; những chuyện c&oacute; t&iacute;nh chất c&aacute; nh&acirc;n, ri&ecirc;ng tư. C&aacute;c bạn n&ecirc;n biết, email l&agrave; một hệ thống điện tử, v&agrave; người chủ của bạn c&oacute; quyền c&agrave;i đặt những t&iacute;nh năng thăm d&ograve;/theo d&otilde;i hoạt động của bạn qua email. V&igrave; vậy, h&atilde;y chỉ d&ugrave;ng email c&ocirc;ng ty cho c&ocirc;ng việc. V&agrave; những g&igrave; bạn muốn người kh&aacute;c giữ b&iacute; mật, tốt nhất kh&ocirc;ng n&ecirc;n trao đổi qua bất kỳ h&igrave;nh thức mạng g&igrave; (t&ugrave;y mức độ nghi&ecirc;m trọng m&agrave; bạn g&aacute;n cho b&iacute; mật đ&oacute;). C&ograve;n nếu như bạn kh&ocirc;ng muốn ai biết được b&iacute; mật của m&igrave;nh? Tốt nhất bạn kh&ocirc;ng n&ecirc;n c&oacute; b&iacute; mật.</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; 10 điều nhắc nhở d&agrave;nh cho c&aacute;c bạn sinh vi&ecirc;n mới ra trường để biết c&aacute;ch viết email cũng như sử dụng email đ&uacute;ng c&aacute;ch v&agrave; chuy&ecirc;n nghiệp ngay cả khi kh&ocirc;ng được c&ocirc;ng ty hướng dẫn.</p>\r\n\r\n<p>Ms Sarah.</p>','/b6/c4/4b/5a6a0f601e410.jpg',NULL,17043,0,'2018-01-26 00:00:00',NULL,'Cuối cùng, trong công việc, tuyệt đối bạn không nên dùng email công việc để làm việc cá nhân hoặc trao đổi với ai đó những chuyện có tính chất cá nhân, riêng tư. Các bạn nên biết, email là một hệ thống điện tử, và người chủ của bạn có quyền cài đặt những','cách viết email công việc, cấu trúc email chuẩn, tai nạn với email, viết email chuyên nghiệp','','',2,0,'vi','huong-dan-viet-email-chuyen-nghiep',5,27,26,'2018-01-26 00:06:19','2018-02-04 16:55:00'),(214,'THỦ TỤC NHẬP KHẨU HÀNG MẪU KHÔNG THANH TOÁN','','Hàng mẫu không thanh toán thường được yêu cầu khi nhà Xuất khẩu muốn gửi hàng để nhà nhập khẩu được kiểm tra chất lượng, bày mẫu tại showroom, đăng ký làm các thủ tục trước khi nhập khẩu cả lô hàng.','<p>H&agrave;ng mẫu kh&ocirc;ng thanh to&aacute;n thường được y&ecirc;u cầu khi nh&agrave; Xuất khẩu muốn gửi h&agrave;ng để nh&agrave; nhập khẩu được kiểm tra chất lượng, b&agrave;y mẫu tại showroom, đăng k&yacute; l&agrave;m c&aacute;c thủ tục trước khi nhập khẩu cả l&ocirc; h&agrave;ng.</p>\r\n\r\n<h2><strong>Bước 1: X&aacute;c nhận h&agrave;ng mẫu kh&ocirc;ng thanh to&aacute;n</strong></h2>\r\n\r\n<p>Sau khi 2 b&ecirc;n đ&atilde; đ&agrave;m ph&aacute;n xong v&agrave; thỏa thuận về việc gửi h&agrave;ng mẫu, l&uacute;c n&agrave;y nh&agrave; xuất khẩu sẽ ph&aacute;t h&agrave;nh Agreement Letter về việc đồng &yacute; gửi h&agrave;ng mẫu kh&ocirc;ng thanh to&aacute;n (hoặc Letter of Agreement-LOA). Nội dung x&aacute;c nhận n&agrave;y được thể hiện trong LOA bằng cụm từ như (Regarding to the demand of Party A&rsquo;s production, Party A will receive the goods which Party B completed shipping gift as details&hellip;)</p>\r\n\r\n<p>Nội dung của Thư x&aacute;c nhận gửi h&agrave;ng mẫu:</p>\r\n\r\n<ul>\r\n	<li>Tr&ecirc;n LOA ghi số v&agrave; ng&agrave;y ph&aacute;t h&agrave;nh LOA.</li>\r\n	<li>Th&ocirc;ng tin t&ecirc;n v&agrave; địa chỉ b&ecirc;n gửi h&agrave;ng v&agrave; b&ecirc;n nhận h&agrave;ng</li>\r\n	<li>Th&ocirc;ng tin chi tiết về h&agrave;ng h&oacute;a, thỏa thuận về việc gửi h&agrave;ng mẫu kh&ocirc;ng thanh to&aacute;n</li>\r\n	<li>Điều kiện thanh to&aacute;n (Payment term) l&agrave; phần cần được quan t&acirc;m v&agrave; ch&uacute; &yacute; nhất. C&aacute;ch ghi điều khoản thanh to&aacute;n được thể hiện:</li>\r\n</ul>\r\n\r\n<p>&ndash; No payment: kh&ocirc;ng thanh to&aacute;n</p>\r\n\r\n<p>&ndash; Non-commercial Value: h&agrave;ng kh&ocirc;ng c&oacute; gi&aacute; trị thương mại:</p>\r\n\r\n<ul>\r\n	<li>Điều kiện giao h&agrave;ng Incoterms.</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/thu-thoa-thuan-hang-phi-mau-dich.jpg\" style=\"height:1200px; width:849px\" /></p>\r\n\r\n<h2><strong>Bước 2: L&agrave;m thủ tục Hải quan nhận h&agrave;ng</strong></h2>\r\n\r\n<p>Bộ hồ sơ hải quan cho loại h&igrave;nh nhập phi mậu dịch (h&agrave;ng mẫu kh&ocirc;ng thanh to&aacute;n) bao gồm:</p>\r\n\r\n<ul>\r\n	<li>Thư x&aacute;c nhận về việc đồng &yacute; gửi h&agrave;ng mẫu kh&ocirc;ng thanh to&aacute;n từ người gửi h&agrave;ng: 01 bản sao</li>\r\n	<li>Invoice trong trường hợp n&agrave;y l&agrave; Non-commercial Invoice (H&oacute;a đơn phi thương mại), h&igrave;nh thức như CI (Commercial Invoice) vẫn c&oacute; đơn gi&aacute;, số lượng, tổng tiền như b&igrave;nh thường nhưng Payment term ghi giống tr&ecirc;n LOA, phần ghi ch&uacute; th&ecirc;m Remarks n&ecirc;n ghi l&agrave; &ldquo;Sample&rdquo; (h&agrave;ng mẫu) v&agrave; &ldquo;Value for customs purpose only&rdquo; (Trị gi&aacute; chỉ d&ugrave;ng cho mục đ&iacute;ch l&agrave;m thủ tục hải quan). Tuy 2 b&ecirc;n kh&ocirc;ng phải thanh to&aacute;n cho nhau tiền h&agrave;ng nhưng vẫn phải thanh to&aacute;n cước vận chuyển (v&agrave; c&aacute;c loại ph&iacute;, phụ ph&iacute; ph&aacute;t sinh như l&ocirc; h&agrave;ng b&igrave;nh thường), vẫn phải nộp thuế Nhập khẩu (nếu c&oacute;) v&agrave; thuế VAT. Phần tr&aacute;ch nhiệm về chi ph&iacute; n&agrave;y do 2 b&ecirc;n tự đ&agrave;m ph&aacute;n, thỏa thuận với nhau (&aacute;p dụng theo Incoterms chẳng hạn).</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Giấy giới thiệu (hoặc giấy ủy quyền) &ndash; 3 bản gốc</li>\r\n	<li>C&ocirc;ng văn xin mở tờ khai nhập khẩu phi mậu dịch (loại h&igrave;nh H11): K&iacute;nh gửi Chi cục Hải quan nơi mở tờ khai &ndash; 1 bản gốc</li>\r\n	<li>Bộ chứng từ gồm: LOA, Vận đơn, Invoice &ndash; mỗi chứng từ 1 bản sao y</li>\r\n	<li>Tờ khai hải quan cho trường hợp nhập khẩu h&agrave;ng mẫu (h&agrave;ng cho, biếu, tặng, bảo h&agrave;nh miễn ph&iacute;&hellip; kh&ocirc;ng thanh to&aacute;n) gọi l&agrave; h&igrave;nh thức nhập phi mậu dịch v&agrave; mở tờ khai nhập phi mậu dịch (loại h&igrave;nh H11). Tr&ecirc;n mặt tờ khai hải quan c&oacute; một số th&ocirc;ng tin khai b&aacute;o Hải quan kh&aacute;c với h&agrave;ng th&ocirc;ng thường như sau:</li>\r\n</ul>\r\n\r\n<p>&ndash; M&atilde; loại h&igrave;nh H11 (Xuất khẩu phi mậu dịch, m&atilde; loại h&igrave;nh H21)</p>\r\n\r\n<p>&ndash; Phương thức thanh to&aacute;n (ở phần h&oacute;a đơn thương mại): Kh&ocirc;ng thanh to&aacute;n &ndash; KHONGTT.</p>\r\n\r\n<p>&ndash; Ph&acirc;n loại h&igrave;nh thức h&oacute;a đơn: chọn m&atilde; B &ndash; H&oacute;a đơn kh&ocirc;ng phải trả tiền.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/to-khai-nhap-phi-mau-dich-h11.jpg\" style=\"height:1200px; width:927px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/h%C3%A0ng%20m%E1%BA%ABu.jpg\" style=\"height:233px; width:632px\" /></p>\r\n\r\n<p>C&aacute;c em ch&uacute; &yacute; về định mức h&agrave;ng mẫu của tổ chức, c&aacute; nh&acirc;n m&agrave; Hải quan cho ph&eacute;p như sau:</p>\r\n\r\n<ol>\r\n	<li>H&agrave;ng h&oacute;a l&agrave; h&agrave;ng mẫu của tổ chức, c&aacute; nh&acirc;n ở Việt Nam gửi cho c&aacute; nh&acirc;n ở nước ngo&agrave;i; h&agrave;ng mẫu của tổ chức, c&aacute; nh&acirc;n ở nước ngo&agrave;i gửi cho c&aacute; nh&acirc;n ở Việt Nam c&oacute; trị gi&aacute; kh&ocirc;ng vượt qu&aacute; 1.000.000 (một triệu) đồng hoặc trị gi&aacute; h&agrave;ng h&oacute;a vượt qu&aacute; 1.000.000 (một triệu) đồng nhưng tổng số tiền thuế phải nộp dưới 100.000 (một trăm ngh&igrave;n) đồng được miễn thuế xuất khẩu, thuế nhập khẩu.</li>\r\n	<li>H&agrave;ng h&oacute;a l&agrave; h&agrave;ng mẫu của tổ chức, c&aacute; nh&acirc;n ờ Việt Nam gửi cho tồ chức ở nước ngo&agrave;i; h&agrave;ng mẫu của tổ chức, c&aacute; nh&acirc;n ở nước ngo&agrave;i gửi cho tổ chức ở Việt Nam c&oacute; trị gi&aacute; kh&ocirc;ng vượt qu&aacute; 30.000.000 (ba mươi triệu) đồng được x&eacute;t miễn thuế xuất khẩu, thuế nhập khẩu.</li>\r\n	<li>H&agrave;ng h&oacute;a l&agrave; h&agrave;ng mẫu quy định ở tr&ecirc;n kh&ocirc;ng thuộc danh mục mặt h&agrave;ng cấm nhập khẩu, cấm xuất khẩu, tạm ngừng xuất khẩu, tạm ngừng nhập khẩu.</li>\r\n</ol>\r\n\r\n<p>Ms Sarah</p>','/04/b9/90/5a69c5ce9b26f.jpg',NULL,17765,0,'2018-01-25 00:00:00',NULL,'Hàng mẫu không thanh toán thường được yêu cầu khi nhà Xuất khẩu muốn gửi hàng để nhà nhập khẩu được kiểm tra chất lượng, bày mẫu tại showroom, đăng ký làm các thủ tục trước khi nhập khẩu cả lô hàng.','thủ tục nhập khẩu hàng phi mậu dịch, nhập khẩu hàng mẫu, nhập khẩu hàng không thanh toán, tờ khai phi mậu dịch','','',2,0,'vi','thu-tuc-nhap-khau-hang-mau-khong-thanh-toan',9,27,27,'2018-01-25 18:55:58','2018-03-04 19:46:53'),(216,'9 CÁCH ĐỂ CÓ ĐƯỢC ĐƠN HÀNG XUẤT KHẨU TỪ NGƯỜI MUA NƯỚC NGOÀI','','Có được đơn hàng xuất khẩu là nhiệm vụ chính của bất kỳ doanh nghiệp xuất khẩu nào. Mỗi cá nhân kinh doanh hay công ty xuất nhập khẩu nào đều có chiến lược marketing của riêng mình để có được đơn đặt hàng xuất khẩu. Nếu không nhận được một đơn đặt hàng nào thì công ty khó có thể tồn tại.\r\nViệc đầu tiên của bất kỳ công ty xuất khẩu nào đó là tập trung vào các chiến lược để có được đơn đặt hàng xuất khẩu từ đó bán được sản phẩm của mình.\r\nLàm thế nào để có được đơn đặt hàng xuất khẩu?','<p>C&oacute; được&nbsp;đơn h&agrave;ng xuất khẩu l&agrave; nhiệm vụ ch&iacute;nh của bất kỳ doanh nghiệp xuất khẩu n&agrave;o.&nbsp;Mỗi c&aacute; nh&acirc;n kinh doanh hay c&ocirc;ng ty xuất nhập khẩu n&agrave;o đều c&oacute; chiến lược marketing của ri&ecirc;ng m&igrave;nh để c&oacute; được đơn đặt h&agrave;ng xuất khẩu.&nbsp;Nếu kh&ocirc;ng nhận được một đơn đặt h&agrave;ng n&agrave;o th&igrave; c&ocirc;ng ty kh&oacute; c&oacute; thể tồn tại.</p>\r\n\r\n<p>Việc đầu ti&ecirc;n của bất kỳ c&ocirc;ng ty xuất khẩu n&agrave;o đ&oacute; l&agrave; tập trung v&agrave;o c&aacute;c chiến lược để c&oacute; được đơn đặt h&agrave;ng xuất khẩu từ đ&oacute; b&aacute;n được sản phẩm của m&igrave;nh.</p>\r\n\r\n<p>L&agrave;m thế n&agrave;o để c&oacute; được đơn đặt h&agrave;ng xuất khẩu?</p>\r\n\r\n<p><strong>1. Nhờ đến c&ocirc;ng nghệ v&agrave; internet:</strong></p>\r\n\r\n<p>C&ocirc;ng nghệ đ&atilde; thay đổi cuộc sống của con người tr&ecirc;n khắp thế giới trong hai thập kỷ qua.&nbsp;C&ocirc;ng nghệ th&ocirc;ng tin đ&oacute;ng một vai tr&ograve; quan trọng trong tất cả c&aacute;c lĩnh vực đặc biệt l&agrave; trong kinh doanh quốc tế.&nbsp;Trước những năm 1990, trao đổi th&ocirc;ng tin li&ecirc;n lạc của người mua nước ngo&agrave;i l&agrave; một nhiệm vụ v&ocirc; c&ugrave;ng kh&oacute; khăn v&agrave; tốn k&eacute;m.&nbsp;Ng&agrave;y nay &nbsp;mọi chuyện đ&atilde; thay đổi.&nbsp;T&ocirc;i sẽ n&oacute;i với bạn c&ocirc;ng cụ đầu ti&ecirc;n v&ocirc; c&ugrave;ng hiệu quả trong kinh doanh quốc tế l&agrave; việc sử dụng c&aacute;c dịch vụ internet.</p>\r\n\r\n<p>L&agrave;m thế n&agrave;o để sử dụng Internet hiệu quả trong tiếp thị sản phẩm của bạn?</p>\r\n\r\n<p>a) Như t&ocirc;i đ&atilde; đề cập trong b&agrave;i viết trước, ra mắt của một trang web chất lượng l&agrave; một sự phản &aacute;nh về quy m&ocirc; v&agrave; danh tiếng của c&ocirc;ng ty bạn v&agrave; bạn c&oacute; thể cập nhật th&ocirc;ng tin cần thiết về sản phẩm mới.&nbsp;Bạn sẽ nhận được sự t&iacute;n nhiệm ban đầu về sản phẩm v&agrave; c&ocirc;ng ty của bạn.&nbsp;H&atilde;y đăng tải l&ecirc;n h&igrave;nh ảnh của sản phẩm, nh&agrave; m&aacute;y sản xuất, kho h&agrave;ng, qu&aacute; tr&igrave;nh sản xuất&hellip;để tăng cường độ tin cậy của bạn trong thị trường quốc tế giữa h&agrave;ng ng&agrave;n đối thủ.</p>\r\n\r\n<p>b) Sử dụng Search Engine Optimization (SEO) đ&oacute;ng một vai tr&ograve; quan trọng trong việc gi&uacute;p c&aacute;c kh&aacute;ch h&agrave;ng t&igrave;m kiếm bạn dễ d&agrave;ng hơn chỉ sau 1 v&agrave;i lần search từ kh&oacute;a. C&ograve;n g&igrave; tuyệt vời hơn khi bạn lu&ocirc;n được hiện l&ecirc;n đầu ti&ecirc;n so với đối thủ. Nếu kh&ocirc;ng tự l&agrave;m được, h&atilde;y t&igrave;m đến những chuy&ecirc;n gia marketing online nhờ trợ gi&uacute;p. Bạn chưa biết rằng họ c&oacute; thể l&agrave;m những điều g&igrave; cho doanh nghiệp của bạn đ&acirc;u.</p>\r\n\r\n<p>c) Tham gia c&aacute;c phương tiện truyền th&ocirc;ng x&atilde; hội như facebook, twitter, Instagram, Plaxo, Pinterest, Linked in&hellip; Đừng qu&ecirc;n để lại những comment ph&ugrave; hợp hay post b&agrave;i khi c&oacute; sự cho ph&eacute;p của Admin.</p>\r\n\r\n<p>d) Sử dụng Youtube, Flickr, tạp ch&iacute; điện tử&hellip; c&oacute; thể được sử dụng để đẩy mạnh việc marketing của bạn. H&atilde;y quay, chụp lại h&igrave;nh ảnh của doanh nghiệp, nh&acirc;n vi&ecirc;n c&ocirc;ng ty, c&ocirc;ng đoạn sản xuất. Bạn c&oacute; thể tự m&igrave;nh l&agrave;m những thước phim ngắn bằng h&igrave;nh ảnh k&egrave;m nhạc nền (c&oacute; bản quyền) v&agrave; up l&ecirc;n Youtube, t&igrave;m c&aacute;ch share n&oacute; tr&ecirc;n mạng x&atilde; hội hoặc đ&iacute;nh k&egrave;m link v&agrave;o email ch&agrave;o h&agrave;ng gửi cho đối t&aacute;c.</p>\r\n\r\n<p>e) Viết c&aacute;c b&agrave;i tr&ecirc;n trang web của bạn sẽ gi&uacute;p độc giả biết được bạn l&agrave; ai? Bạn đang b&aacute;n g&igrave;&hellip;?</p>\r\n\r\n<p><strong>2. N&acirc;ng cao khả năng đ&agrave;m ph&aacute;n, giao tiếp:</strong></p>\r\n\r\n<p>Giao tiếp đ&agrave;m ph&aacute;n đ&oacute;ng&nbsp;một vai tr&ograve; quan trọng trong kinh doanh quốc tế.&nbsp;Nếu bạn giao tiếp tốt với người mua để thuyết phục họ về chất lượng v&agrave; gi&aacute; cả của sản phẩm, người mua chắc chắn sẽ đề nghị bạn gửi h&agrave;ng mẫu v&agrave; sau đ&oacute; l&agrave; những đơn h&agrave;ng tiếp theo.</p>\r\n\r\n<p><strong>3. Chủ động gửi mẫu&nbsp;theo y&ecirc;u cầu của người mua.</strong></p>\r\n\r\n<p>Trong khi gửi&nbsp;mẫu xuất khẩu,&nbsp;<em><u>&iacute;t nhất l&agrave; hai bộ mẫu</u></em>&nbsp;n&ecirc;n được r&uacute;t ra cho ph&ugrave; hợp.&nbsp;Một bộ bạn d&ugrave;ng để gửi cho người mua hoặc kh&aacute;ch h&agrave;ng bạn cho l&agrave; tiềm năng.&nbsp;Mẫu c&ograve;n lại d&ugrave;ng để lưu trữ, điều n&agrave;y gi&uacute;p bạn sản&nbsp;xuất cho ph&ugrave; hợp với c&aacute;c mẫu m&agrave; bạn gửi đến người mua trong khi sản xuất ngay khi bạn c&oacute; được đơn đặt h&agrave;ng từ họ.</p>\r\n\r\n<p><strong>4. Tham dự hội chợ thương mại,&nbsp;triển l&atilde;m sản phẩm của bạn:</strong></p>\r\n\r\n<p>C&oacute; rất nhiều hội chợ Thương mại quốc tế được tổ chức trong phạm vi trong nước v&agrave; ở nước ngo&agrave;i, nơi bạn c&oacute; thể trưng b&agrave;y sản phẩm của m&igrave;nh để thu h&uacute;t người mua tại thị trường hướng tới.</p>\r\n\r\n<p><strong>5. T&igrave;m đến Cơ quan X&uacute;c tiến xuất khẩu:</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; nơi nhiều nguồn th&ocirc;ng tin đ&atilde; được ph&acirc;n chia theo sản phẩm, ng&agrave;nh h&agrave;ng, v&ugrave;ng địa l&yacute; dựa v&agrave;o c&aacute;c mức độ kinh doanh quốc tế kh&aacute;c nhau của doanh nghiệp.&nbsp;C&aacute;c cơ quan n&agrave;y sẽ hướng dẫn bạn c&aacute;ch marketing xuất khẩu hiệu quả trong thị trường quốc tế.&nbsp;Họ cũng c&oacute; c&aacute;c g&oacute;i mở rộng dịch vụ như việc t&igrave;m kiếm người mua cho sản phẩm của bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/4.%209%20cach%20co%20don%20hang/ho-tro-doanh-nghiep-xuat-nhap-khau.jpg\" style=\"height:1200px; width:1600px\" /></p>\r\n\r\n<p><em>Ảnh: Internet</em></p>\r\n\r\n<p><strong>6. Đại sứ qu&aacute;n của Ch&iacute;nh phủ:</strong></p>\r\n\r\n<p>Đại sứ qu&aacute;n của Ch&iacute;nh phủ c&ograve;n c&oacute; nhiệm vụ x&uacute;c tiến cả về kinh doanh tại nước ngo&agrave;i, họ gi&uacute;p c&aacute;c nh&agrave; xuất khẩu t&igrave;m thấy cơ hội kinh doanh tại nước sở tại hoặc t&igrave;m kiếm c&aacute;c nguồn lực hỗ trợ cho doanh nghiệp của nước m&igrave;nh</p>\r\n\r\n<p><strong>7. Thiết lập gặp gỡ đối t&aacute;c:</strong></p>\r\n\r\n<p>Khi bạn c&oacute; một li&ecirc;n hệ từ đối t&aacute;c l&agrave; người mua, bạn c&oacute; thể thiết lập quan hệ c&aacute; nh&acirc;n để từ đ&oacute; tạo dựng quan hệ kinh doanh về sau. Thỉnh thoảng gửi mail hỏi thăm, thư ch&uacute;c mừng, thư thăm hỏi l&agrave; c&aacute;ch thể hiện sự quan t&acirc;m đến đối t&aacute;c.</p>\r\n\r\n<p><strong>8. Chỉ định đại l&yacute;:</strong></p>\r\n\r\n<p>Một số nh&agrave; xuất khẩu chỉ định một đại l&yacute; ở nước ngo&agrave;i m&agrave; họ hướng tới, c&aacute;c đại l&yacute; n&agrave;y c&oacute; tr&aacute;ch nhiệm giao dịch với người mua tiềm năng v&agrave; xử l&yacute; c&aacute;c đơn h&agrave;ng gi&uacute;p c&ocirc;ng ty bạn (bao gồm cả những vấn đề về kỹ thuật hay giao nhận</p>\r\n\r\n<p><strong>9. Th&ocirc;ng qua địa chỉ li&ecirc;n lạc của bạn b&egrave; v&agrave; người th&acirc;n:</strong></p>\r\n\r\n<p>Mối quan hệ c&aacute; nh&acirc;n của bạn b&egrave; v&agrave; người th&acirc;n cũng gi&uacute;p tạo ra một mối quan hệ tốt giữa người mua v&agrave; người b&aacute;n. H&atilde;y nhờ người quen giới thiệu cho c&aacute;c thương vụ l&agrave;m ăn họ biết, hay đơn giản l&agrave; gi&uacute;p bạn kết nối với những người mua tiềm năng tại nước ngo&agrave;i. Đơn giản nhất l&agrave; h&atilde;y n&oacute;i ra bạn đang b&aacute;n c&aacute;i g&igrave; v&agrave; mong muốn hiện nay của bạn ra sao? Bạn sẽ nhận được những cuộc điện thoại bất ngờ từ người th&acirc;n xung quanh đ&oacute;.</p>\r\n\r\n<p>T&ocirc;i hy vọng, những lời khuy&ecirc;n ở tr&ecirc;n về c&aacute;ch để c&oacute; được đơn h&agrave;ng xuất khẩu gi&uacute;p bạn tham khảo v&agrave;o sổ tay Khởi nghiệp kinh doanh Xuất khẩu của m&igrave;nh.</p>\r\n\r\n<p>Mrs Jade.</p>','/db/90/0d/5a6a0f3e88941.jpg',NULL,5984,0,'2018-01-26 00:00:00',NULL,'Có được đơn hàng xuất khẩu là nhiệm vụ chính của bất kỳ doanh nghiệp xuất khẩu nào. Vậy làm thế nào để bạn nhận được nhiều đơn hàng xuất khẩu nhất có thể. Dưới đây là 9 cách phổ biến giúp bạn có thêm khách hàng tiềm năng, hãy cùng rà soát lại xem bạn đã t','tìm kiếm khách hàng nước ngoài, nhân viên kinh doanh xuất khẩu, sales xuất khẩu, khách hàng nước ngoài, hợp đồng xuất khẩu, đơn hàng xuất khẩu','','',2,0,'vi','9-cach-de-co-duoc-don-hang-xuat-khau-tu-nguoi-mua-nuoc-ngoai',6,27,26,'2018-01-26 00:09:18','2018-03-03 11:56:59'),(217,'10 CÂU HỎI LỰA CHỌN CÔNG TY FORWARDER PHÙ HỢP','','Sự thật là có rất nhiều công ty Xuất nhập khẩu lớn lại lựa chọn Công ty Forwarder nhỏ hay nhiều Công ty tư nhân, kim ngạch Xuất nhập khẩu thấp nhưng lựa chọn Công ty Forwarder lớn. Chắc hẳn, đến đây bạn có nhiều câu hỏi rằng làm thế nào để lựa chọn công ty Forwarder làm dịch vụ cho mình, công ty mình cho phù hợp đây?','<p>L&agrave;m thế n&agrave;o để những Nh&agrave; xuất khẩu, nh&agrave; nhập khẩu c&oacute; thể x&aacute;c định v&agrave; lựa chọn c&ocirc;ng ty Forwarder ph&ugrave; hợp?</p>\r\n\r\n<p>Để bắt đầu chủ đề n&agrave;y, Ad&nbsp;muốn n&oacute;i rằng quy m&ocirc; của c&ocirc;ng ty xuất khẩu hay nhập khẩu kh&ocirc;ng quyết&nbsp;định được quy m&ocirc; của C&ocirc;ng ty Forwarder m&agrave; họ lựa chọn. Sự thật l&agrave; c&oacute; rất nhiều c&ocirc;ng ty Xuất nhập khẩu lớn lại lựa chọn C&ocirc;ng ty Forwarder nhỏ hay nhiều C&ocirc;ng ty tư nh&acirc;n, kim ngạch Xuất nhập khẩu thấp nhưng lựa chọn C&ocirc;ng ty Forwarder lớn. Chắc hẳn, đến đ&acirc;y bạn c&oacute; nhiều c&acirc;u hỏi rằng l&agrave;m thế n&agrave;o để lựa chọn c&ocirc;ng ty Forwarder l&agrave;m dịch vụ cho m&igrave;nh, c&ocirc;ng ty m&igrave;nh cho ph&ugrave; hợp đ&acirc;y?</p>\r\n\r\n<p><strong>Bạn n&ecirc;n tự đặt ra 10 c&acirc;u hỏi dưới đ&acirc;y trước khi lựa chọn&nbsp;C&ocirc;ng ty Forwarder cho ph&ugrave; hợp</strong></p>\r\n\r\n<ol>\r\n	<li>Điều kiện Incoterms n&agrave;o được sử dụng trong hợp đồng giữa bạn v&agrave; đối t&aacute;c. Từ đ&oacute; bạn c&oacute; thể ph&acirc;n định được tr&aacute;ch nhiệm v&agrave; rủi ro m&agrave; m&igrave;nh phải chịu khi bắt đầu giao h&agrave;ng hoặc khi đ&atilde; giao h&agrave;ng xong.</li>\r\n	<li>Bạn cần loại dịch vụ n&agrave;o?</li>\r\n</ol>\r\n\r\n<ul>\r\n	<li>Door to door: dịch vụ vận chuyển Quốc tế trọn g&oacute;i từ kho nước người xuất khẩu sang kho nước người nhập khẩu c&oacute; bao gồm cả l&agrave;m thủ tục Hải quan.</li>\r\n	<li>Door to port: vận chuyển từ kho đến Cảng/ s&acirc;n bay</li>\r\n	<li>Port to Door: vận chuyển từ Cảng về kho người nhận</li>\r\n	<li>Port to Port: vận chuyển từ Cảng xếp h&agrave;ng đến Cảng dỡ h&agrave;ng (chỉ book cước)</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:.5in\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/5.%2010%20cau%20hoi%20fwd/cac-dich-vu-van-chuyen-quoc-te-service-infographic.jpg\" style=\"height:850px; width:1500px\" /><em>Ảnh: Internet</em></p>\r\n\r\n<p>3. Địa điểm lưu kho h&agrave;ng h&oacute;a (nh&agrave; m&aacute;y, kho h&agrave;ng&hellip;) v&agrave; địa điểm nhận h&agrave;ng, nắm r&otilde; v&ugrave;ng địa l&yacute;</p>\r\n\r\n<p>4. Gi&aacute; trị l&ocirc; h&agrave;ng? K&iacute;ch thước, khối lượng, thể t&iacute;ch&hellip;l&ocirc; h&agrave;ng?&nbsp;H&agrave;ng của bạn liệu c&oacute; qu&aacute; tải, qu&aacute; khổ?</p>\r\n\r\n<p>5. Liệu l&ocirc; h&agrave;ng c&oacute; cần vận chuyển đa phương thức hay chỉ cần book qua phương tiện vận tải&nbsp; nhất&nbsp; định</p>\r\n\r\n<p>6. H&agrave;ng được dự định đ&oacute;ng v&agrave;o loại container n&agrave;o (cont 20&rsquo; hay 40&rsquo;, cont thường hay cont lạnh&hellip;)</p>\r\n\r\n<p>7. H&agrave;ng h&oacute;a được đ&oacute;ng g&oacute;i thế n&agrave;o? C&oacute; cần sử dụng dịch vụ xếp, dỡ h&agrave;ng? Dịch vụ bao g&oacute;i h&agrave;ng h&oacute;a</p>\r\n\r\n<p>8. H&agrave;ng của bạn c&oacute; phải h&agrave;ng nguy hiểm? Nếu c&oacute;, bạn đ&atilde; chuẩn bị sẵn MSDS (Material Safety Data Sheet- bảng chỉ dẫn về an to&agrave;n h&agrave;ng h&oacute;a) d&ugrave;ng cho những h&agrave;ng h&oacute;a c&oacute; thể g&acirc;y mất an to&agrave;n, ch&aacute;y nổ, ăn m&ograve;n&hellip;trong qu&aacute; tr&igrave;nh vận chuyển.</p>\r\n\r\n<p>9. L&ocirc; h&agrave;ng c&oacute; y&ecirc;u cầu cần giấy ph&eacute;p xuất nhập khẩu, giấy kiểm tra chuy&ecirc;n ng&agrave;nh. Những loại giấy tờ n&agrave;y sẽ phụ thuộc t&ugrave;y v&agrave;o từng mặt h&agrave;ng v&agrave; y&ecirc;u cầu tại nơi xuất, nhập khẩu. Nếu h&agrave;ng h&oacute;a cần c&aacute;c giấy tờ n&agrave;y, liệu bạn đ&atilde; biết c&aacute;ch chuẩn bị hồ sơ v&agrave; xin cấp ph&eacute;p?</p>\r\n\r\n<p>10. Bạn c&oacute; cần bất cứ dịch vụ n&agrave;o đặc biệt v&iacute; dụ như: l&agrave;m chứng từ, xin c&aacute;c giấy kiểm dịch động thực vật, l&agrave;m việc với cơ quan hữu quan, bốc v&agrave; dỡ h&agrave;ng, khai b&aacute;o Hải quan, nộp thuế hộ, ph&acirc;n t&iacute;ch ph&acirc;n loại h&agrave;ng h&oacute;a, h&agrave;ng lẻ&hellip;</p>\r\n\r\n<p>Mrs Jade.</p>','/cb/93/3c/5a6a107ed96c1.png',NULL,1696,0,'2018-01-26 00:00:00',NULL,'Bạn có nhiều câu hỏi rằng làm thế nào để lựa chọn công ty Forwarder làm dịch vụ cho mình, công ty mình cho phù hợp đây? Bạn nên tự đặt ra 10 câu hỏi dưới đây trước khi lựa chọn Công ty Forwarder cho phù hợp. \r\n1. Điều kiện Incoterms nào được sử dụng trong','làm thế nào để tìm forwarder tốt, lựa chọn forwarder, công ty logistics chuyên nghiệp, cách tìm forwarder, khai thuê hải quan','','',2,0,'vi','10-cau-hoi-lua-chon-cong-ty-forwarder-phu-hop',8,27,26,'2018-01-26 00:14:38','2018-03-03 13:20:28'),(218,'AI XÁC MINH TRỌNG LƯỢNG HÀNG HÓA TRONG CONTAINER','','Vì sao phải xác minh trọng lượng hàng hóa trong Container và quy trình ra sao.Hãy cùng Ad điểm qua quá trình tổng quát của các chuyến hàng container tương tự dưới đây:\r\n\r\n1.Shipper book tàu với hãng tàu hoặc qua các công ty Forwarder, ở bước này thông tin về trọng lượng, thể tích của hàng hóa ước tính sẽ được các công ty vận tải/hãng tàu đòi hỏi chủ hàng.\r\n\r\n2. Shipper đóng hàng vào container rồi vận chuyển ra bãi tập kết cont tại Cảng','<p>V&igrave; sao phải x&aacute;c minh trọng lượng h&agrave;ng h&oacute;a trong Container v&agrave; quy tr&igrave;nh ra sao.H&atilde;y c&ugrave;ng Ad điểm qua qu&aacute; tr&igrave;nh tổng qu&aacute;t của c&aacute;c chuyến h&agrave;ng container tương tự dưới đ&acirc;y:</p>\r\n\r\n<p>1.Shipper book t&agrave;u với h&atilde;ng t&agrave;u hoặc qua c&aacute;c c&ocirc;ng ty Forwarder, ở bước n&agrave;y th&ocirc;ng tin về trọng lượng, thể t&iacute;ch của h&agrave;ng h&oacute;a ước t&iacute;nh sẽ được c&aacute;c c&ocirc;ng ty vận tải/h&atilde;ng t&agrave;u đ&ograve;i hỏi chủ h&agrave;ng.</p>\r\n\r\n<p>2. Shipper đ&oacute;ng h&agrave;ng v&agrave;o container rồi vận chuyển ra b&atilde;i tập kết cont tại Cảng</p>\r\n\r\n<p>3.&nbsp;Nh&acirc;n&nbsp;vi&ecirc;n Cảng vụ nhận cont v&agrave; v&agrave;o dữ liệu trọng lượng được cung cấp bởi người gửi h&agrave;ng sau đ&oacute; cập nhật l&ecirc;n hệ thống nội bộ (c&oacute; nơi v&agrave;o bằng sổ tay)</p>\r\n\r\n<p>4. Những th&ocirc;ng tin n&agrave;y trở th&agrave;nh cơ sở cho việc lập danh s&aacute;ch h&agrave;ng h&oacute;a được bốc l&ecirc;n t&agrave;u v&agrave; kế hoạch để xếp h&agrave;ng tr&ecirc;n t&agrave;u cho ph&ugrave; hợp.</p>\r\n\r\n<p>5. Shipper gửi h&atilde;ng t&agrave;u th&ocirc;ng tin để l&agrave;m Vận đơn (gọi l&agrave; Shipping Instruction), c&aacute;c th&ocirc;ng tin n&agrave;y sẽ gồm c&oacute; trọng lượng của h&agrave;ng h&oacute;a .Trọng lượng n&agrave;y c&oacute; thể giống hoặc kh&ocirc;ng giống với trọng lượng từ l&uacute;c đặt booking (do l&uacute;c đặt booking chưa đ&oacute;ng h&agrave;ng n&ecirc;n chưa c&oacute; trọng lượng thực tế).</p>\r\n\r\n<p>6. T&ugrave;y thuộc v&agrave;o c&aacute;c h&atilde;ng t&agrave;u m&agrave; họ c&oacute; thể cho ph&eacute;p độ sai lệch tr&ecirc;n mỗi container từ 1-2 tấn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/6.%20VGM/vgm-xac-dinh-trong-luong-container1.jpg\" style=\"height:114px; width:442px\" /></p>\r\n\r\n<p>7. Nếu c&aacute;c h&atilde;ng t&agrave;unghi&ecirc;m ngặt, họ sẽ th&ocirc;ng b&aacute;o nếu c&oacute; bất kỳ sự thay đổi lớn giữa trọng lượng ước t&iacute;nh l&uacute;c đặt booking v&agrave; trọng lượng trong SI (Shipping Instruction)</p>\r\n\r\n<p>8. Nếu trọng lượng khai b&aacute;o tr&ecirc;n SI nhiều hơn trọng lượng cho ph&eacute;p, h&atilde;ng t&agrave;u sẽ th&ocirc;ng b&aacute;o với kh&aacute;ch h&agrave;ng v&agrave; c&oacute; thể phạt chủ h&agrave;ng v&igrave; việc khai b&aacute;o sai n&agrave;y.</p>\r\n\r\n<p>9. Nhưng sự thay đổi thực sự trong trọng lượng sẽ được giữa trọng lượng thực tế của h&agrave;ng h&oacute;a đ&oacute;ng trong container v&agrave; trọng lượng của h&agrave;ng h&oacute;a tuy&ecirc;n bố khi container được đưa v&agrave;o thiết bị đầu cuối theo điểm 3 v&agrave; 4 ở tr&ecirc;n &hellip;</p>\r\n\r\n<p>Cũng xin chia sẻ th&ecirc;m với c&aacute;c bạn rằng, bắt đầu từ 1/7/2016, theo quy định mới của IMO tức Tổ chức H&agrave;ng hải Quốc tế th&igrave; c&aacute;c container trước khi l&ecirc;n t&agrave;u cần phải được c&acirc;n.</p>\r\n\r\n<p>Chủ h&agrave;ng c&oacute; thể c&acirc;n to&agrave;n bộ container trong đ&oacute; đ&atilde; c&oacute; h&agrave;ng hoặc c&acirc;n ri&ecirc;ng h&agrave;ng sau đ&oacute; cộng với khối lượng của container. Sau qu&aacute; tr&igrave;nh n&agrave;y, chủ h&agrave;ng sẽ cung cấp cho b&ecirc;n ph&iacute;a Cảng vụ hoặc&nbsp;thuyền trưởng tờ phiếu c&acirc;n c&oacute; x&aacute;c nhận của đơn vị c&acirc;n h&agrave;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/6.%20VGM/vgm-xac-dinh-trong-luong-container.jpg\" style=\"height:449px; width:1024px\" /></p>\r\n\r\n<p>Container sẽ kh&ocirc;ng được v&agrave;o Cảng trong một số trường hợp sau:</p>\r\n\r\n<p>TH1: Container sẽ kh&ocirc;ng được v&agrave;o Cảng nếu như kh&ocirc;ng c&oacute; Phiếu x&aacute;c nhận khối lượng to&agrave;n bộ VGM- Verified Gross Mass</p>\r\n\r\n<p>TH2: Khối lượng của to&agrave;n container vượt qu&aacute; khối lượng to&agrave;n bộ ghi tr&ecirc;n vỏ container</p>\r\n\r\n<p>Với trường hợp đ&oacute;ng h&agrave;ng v&agrave;o container tại Cảng,b&ecirc;n Cảng vụ sẽ c&oacute; dịch vụ c&acirc;n v&agrave; cấp phiếu VGM.</p>\r\n\r\n<p>C&aacute;c bạn c&oacute; thể đọc th&ecirc;m c&aacute;c quy định được hướng dẫn chi tiết tại C&ocirc;ng văn 2428/CHHVN-VTDVHH v&agrave; 1007/TB-TCT n&agrave;y nh&eacute;.</p>\r\n\r\n<p>Mrs Jade</p>','/34/dd/d3/5a6a10fe4515c.jpg',NULL,1216,0,'2018-01-26 00:00:00',NULL,'Vì sao phải xác minh trọng lượng hàng hóa trong Container và quy trình ra sao.Hãy cùng Ad điểm qua quá trình tổng quát của các chuyến hàng container tương tự dưới đây:\r\n1.Shipper book tàu với hãng tàu hoặc qua các công ty Forwarder','VGM là gì? ai xác nhận trên phiếu VGM? cân container, xuất khẩu đường biển','','',2,0,'vi','ai-xac-minh-trong-luong-hang-hoa-trong-container',8,27,26,'2018-01-26 00:16:46','2018-03-03 13:22:21'),(219,'BA LÝ DO CHÍNH ĐỂ THUÊ DỊCH VỤ LOGISTICS','','Khi bạn sở hữu một doanh nghiệp Xuất nhập khẩu quy mô nhỏ và vừa, hẳn là bạn có rất nhiều thứ phải làm và đối mặt hàng ngày.\r\n\r\nNếu các công việc đó liên quan đến bán hàng, vận chuyển hàng hóa thì cách tốt nhất là nên làm đơn giản hóa trong khả năng của Doanh nghiệp.Có một số cách được liệt kê trong bài viết này cho phép bạn vận chuyển hàng hóa mà không phải lo bất cứ điều gì đó là qua dịch vụ Logistics bên thứ ba (Third Party Logistics)','<p>Khi bạn sở hữu một doanh nghiệp Xuất nhập khẩu quy m&ocirc; nhỏ v&agrave; vừa, hẳn l&agrave; bạn c&oacute; rất nhiều thứ phải l&agrave;m v&agrave; đối mặt h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>Nếu c&aacute;c c&ocirc;ng việc đ&oacute; li&ecirc;n quan đến b&aacute;n h&agrave;ng, vận chuyển h&agrave;ng h&oacute;a th&igrave; c&aacute;ch tốt nhất l&agrave; n&ecirc;n l&agrave;m đơn giản h&oacute;a trong khả năng của Doanh nghiệp.</p>\r\n\r\n<p>C&oacute; một số c&aacute;ch được liệt k&ecirc; trong b&agrave;i viết n&agrave;y cho ph&eacute;p bạn vận chuyển h&agrave;ng h&oacute;a m&agrave; kh&ocirc;ng phải lo bất cứ điều g&igrave; đ&oacute; l&agrave; qua dịch vụ Logistics b&ecirc;n thứ ba (Third Party Logistics)</p>\r\n\r\n<p>Nếu bạn đang c&oacute; c&ocirc;ng ty tại một th&agrave;nh phố lớn như H&agrave; Nội, Đ&agrave; Nẵng, TP Hồ Ch&iacute; Minh, việc sử dụng dịch vụ cung cấp Logistics b&ecirc;n thứ ba c&oacute; danh tiếng sẽ gi&uacute;p bạn giảm thiểu tối đa những lo lắng xung quanh vấn đề vận chuyển h&agrave;ng h&oacute;a.</p>\r\n\r\n<p>Dưới đ&acirc;y l&agrave; ba lợi &iacute;ch k&egrave;m theo m&agrave; bạn sẽ nhận được khi thu&ecirc; vận chuyển, đ&oacute;ng g&oacute;i h&agrave;ng h&oacute;a qua C&ocirc;ng ty Logistics b&ecirc;n thứ ba (3PL). Vậy 3PL l&agrave; g&igrave;?</p>\r\n\r\n<p>3PL l&agrave; nh&agrave; cung cấp c&aacute;c dịch vụ vận chuyển container, hệ&nbsp; thống t&iacute;ch hợp kho b&atilde;i v&agrave; giao vận&hellip;từ đ&oacute; tăng hiệu quả hoạt động sản xuất kinh doanh của kh&aacute;ch h&agrave;ng l&agrave; c&aacute;c C&ocirc;ng ty Xuất nhập khẩu, thương mại, nh&agrave; sản xuất&hellip;</p>\r\n\r\n<h2><strong>1.&nbsp;&nbsp;&nbsp; </strong><strong>Logistics b&ecirc;n thứ ba c&oacute;&nbsp;mạng lưới rộng khắp v&agrave; sẵn s&agrave;ng sử dụng</strong></h2>\r\n\r\n<p>Đ&acirc;y l&agrave; lợi &iacute;ch lớn nhất từ 3PL v&igrave; từ họ m&agrave; bạn được kết nối với Mạng lưới Logistics to&agrave;n cầu, rộng khắp. Bạn c&oacute; quyền lựa chọn C&ocirc;ng ty từ những C&ocirc;ng ty lớn v&agrave; danh tiếng nhất trong lĩnh vực n&agrave;y. Kh&ocirc;ng chỉ c&oacute; vậy, bạn sẽ c&oacute; được những mức gi&aacute; tốt về cước vận chuyển- dịch vụ từ đ&oacute; n&acirc;ng cao khả năng sử dụng nguồn vốn của m&igrave;nh hợp l&yacute;.</p>\r\n\r\n<p>Để l&agrave;m được điều n&agrave;y, trước khi K&yacute; hợp đồng với bất cứ&nbsp;C&ocirc;ng ty Logistics n&agrave;o ch&uacute;ng ta n&ecirc;n đ&agrave;m ph&aacute;n c&aacute;c điều khoản, chi ph&iacute; dịch vụ, gi&aacute; cước&hellip;</p>\r\n\r\n<h2><strong>2.&nbsp;&nbsp;&nbsp; </strong><strong>Thu&ecirc; Logistics b&ecirc;n thứ ba gi&uacute;p bạn tiết kiệm tiền v&agrave; thời gian</strong></h2>\r\n\r\n<p>Đối với hầu hết c&aacute;c chủ doanh nghiệp, việc tối ưu h&oacute;a t&agrave;i ch&iacute;nh v&agrave; thời gian lu&ocirc;n được đưa l&ecirc;n h&agrave;ng đầu. N&oacute; kh&ocirc;ng trực tiếp gi&uacute;p bạn tiết kiệm tiền nhưng gi&aacute; trị m&agrave; n&oacute; mang lại nhờ những hoạt động hiệu quả trong việc bao g&oacute;i, vận chuyển h&agrave;ng h&oacute;a, kho&nbsp;b&atilde;i&hellip;</p>\r\n\r\n<h2><strong>3.&nbsp;&nbsp;&nbsp; </strong><strong>Logistics b&ecirc;n thứ ba c&oacute; t&iacute;nh linh hoạt</strong></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/7.%203%20ly%20do%20thue%20LOG/Ba-ly-do-chinh-de-thue-dich-vu-Logistics-ben-thu-ba.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p>Khi sử dụng dịch vụ qua một C&ocirc;ng ty Logistics bạn được chủ động để lựa chọn những g&igrave; l&agrave; ph&ugrave; hợp nhất với Doanh nghiệp của m&igrave;nh. Nếu bạn c&oacute; 1 l&ocirc; h&agrave;ng lẻ, chưa chắc g&igrave; h&atilde;ng t&agrave;u đ&atilde; nhận h&agrave;ng của bạn nhưng nếu đi&nbsp;qua một C&ocirc;ng ty Logistics th&igrave; điều n&agrave;y th&agrave;nh đơn giản hơn bao giờ hết. Điều bạn cần l&agrave; bỏ thời gian ra để t&igrave;m kiếm c&aacute;c c&ocirc;ng ty n&agrave;y với dịch vụ v&agrave; gi&aacute; cả tốt nhất</p>\r\n\r\n<p>Với những lợi &iacute;ch kể tr&ecirc;n, kh&ocirc;ng c&ograve;n nghi ngờ g&igrave; nữa tại sao 3PL lại ng&agrave;y c&agrave;ng trở n&ecirc;n phổ biến v&agrave; ph&aacute;t triển nhanh.</p>\r\n\r\n<p>Mrs Jade.</p>','/35/1d/d3/5a6a11561b0c2.jpg',NULL,1186,0,'2018-01-26 00:00:00',NULL,'Nếu các công việc đó liên quan đến bán hàng, vận chuyển hàng hóa thì cách tốt nhất là nên làm đơn giản hóa trong khả năng của Doanh nghiệp.Có một số cách được liệt kê trong bài viết này cho phép bạn vận chuyển hàng hóa mà không phải lo bất cứ điều gì đó l','công ty logistics là gì? dịch vụ logistics bên thứ ba, logistics hay forwarder, chọn công ty logistics, khai thuê hải quan','','',2,0,'vi','ba-ly-do-chinh-de-thue-dich-vu-logistics',8,27,26,'2018-01-26 00:18:14','2018-03-03 13:24:07'),(220,'CÁCH TRẢ LỜI THƯ HỎI HÀNG VÀ LÀM BÁO GIÁ','','Có phải mỗi ngày bạn nhận được hàng trăm email hỏi hàng từ những khách hàng tiềm năng và bạn bối rối không biết phải trả lời ai trước? Bạn có nhận thấy rất nhiều khách hàng không trả lời email sau khi bạn gửi Báo giá cho họ? Sau đây Ad sẽ chỉ cho bạn cách làm thế nào để trả lời thư hỏi hàng của khách hàng theo những bước sau:','<p>C&oacute; phải mỗi ng&agrave;y bạn nhận được h&agrave;ng trăm email hỏi h&agrave;ng từ những kh&aacute;ch h&agrave;ng tiềm năng v&agrave; bạn bối rối kh&ocirc;ng biết phải trả lời ai trước? Bạn c&oacute; nhận thấy rất nhiều kh&aacute;ch h&agrave;ng kh&ocirc;ng trả lời email sau khi bạn gửi B&aacute;o gi&aacute; cho họ? Sau đ&acirc;y Ad sẽ chỉ cho bạn c&aacute;ch l&agrave;m thế n&agrave;o để trả lời thư hỏi h&agrave;ng của kh&aacute;ch h&agrave;ng theo những bước sau:</p>\r\n\r\n<ul>\r\n	<li>Ph&acirc;n loại thư hỏi h&agrave;ng</li>\r\n	<li>Trả lời thư hỏi h&agrave;ng của người mua: sau khi đ&atilde; ph&acirc;n loại thư hỏi h&agrave;ng th&igrave; t&ugrave;y v&agrave;o từng loại thư hỏi h&agrave;ng m&agrave; bạn sẽ c&oacute; những trả lời kh&aacute;c nhau.&nbsp;</li>\r\n</ul>\r\n\r\n<p>Sau đ&acirc;y ch&uacute;ng ta c&ugrave;ng t&igrave;m hiểu c&aacute;ch thức Ph&acirc;n loại thư hỏi h&agrave;ng&nbsp;để đưa ra những ch&agrave;o h&agrave;ng (Offer) c&ugrave;ng c&aacute;c b&aacute;o gi&aacute; (Quotation) sao cho ph&ugrave; hợp.</p>\r\n\r\n<h2><strong>Bước 1: Ph&acirc;n loại&nbsp;trước khi trả lời thư hỏi h&agrave;ng</strong></h2>\r\n\r\n<p>Trong trường hợp bạn nhận được rất nhiều thư hỏi h&agrave;ng nhưng bạn kh&ocirc;ng biết chọn trả lời thư hỏi h&agrave;ng&nbsp;n&agrave;o trước. Ch&uacute;ng ta sẽ s&agrave;ng lọc v&agrave; ph&acirc;n loại c&aacute;c thư hỏi h&agrave;ng để sắp xếp thứ tự ưu ti&ecirc;n trả lời email bằng c&aacute;ch đ&aacute;nh gi&aacute; nội dung thư hỏi h&agrave;ng theo c&aacute;c ti&ecirc;u ch&iacute; sau đ&acirc;y:</p>\r\n\r\n<ul>\r\n	<li>Cung cấp th&ocirc;ng tin cụ thể, r&otilde; r&agrave;ng v&agrave; căn cứ mức độ chi tiết của thư hỏi h&agrave;ng, thể hiện người mua nghi&ecirc;m t&uacute;c, thật sự cần th&ocirc;ng tin hồi đ&aacute;p</li>\r\n	<li>Cung cấp th&ocirc;ng tin cụ thể, r&otilde; r&agrave;ng nhưng vắn tắt, ngắn gọn, cũng l&agrave; người mua tiềm năng, thật sự cần th&ocirc;ng tin hồi đ&aacute;p</li>\r\n	<li>Thư hỏi h&agrave;ng chung chung, kh&ocirc;ng cụ thể</li>\r\n</ul>\r\n\r\n<h3>1.<strong>Thư hỏi h&agrave;ng cụ thể v&agrave; chi tiết:</strong></h3>\r\n\r\n<p style=\"margin-left:.5in\">Đ&acirc;y l&agrave; thư hỏi h&agrave;ng từ người mua tiềm năng với th&ocirc;ng tin y&ecirc;u cầu đầy đủ, chi tiết, r&otilde; r&agrave;ng về số lượng h&agrave;ng, nước xuất khẩu, thời gian nhận h&agrave;ng, h&igrave;nh ảnh minh họa&hellip; v&agrave; đặc biệt với một số thư hỏi h&agrave;ng c&oacute; giới thiệu về c&ocirc;ng ty của họ.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; <img alt=\"Mẫu thư hỏi hàng cụ thể và chi tiết\" src=\"/uploads/ckfinder/images/%E1%BA%A3nh%201.jpg\" style=\"height:410px; width:429px\" />&nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\r\n<h3><strong>=&gt; C&aacute;ch trả lời:</strong></h3>\r\n\r\n<ul>\r\n	<li>Ưu ti&ecirc;n trả lời đầu ti&ecirc;n</li>\r\n	<li>Dựa theo y&ecirc;u cầu cụ thể của kh&aacute;ch h&agrave;ng, đưa ra m&ocirc; tả chi tiết về sản phẩm</li>\r\n	<li>Đề cập đến kinh nghiệm của bạn trong lĩnh vực xuất khẩu ở c&aacute;c thị trường kh&aacute;c</li>\r\n	<li>N&oacute;i về những dịch vụ gia tăng m&agrave; bạn c&oacute; thể cung cấp</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/2.jpg\" style=\"height:432px; width:619px\" />&nbsp; &nbsp; &nbsp;</p>\r\n\r\n<h3><strong>2. Thư hỏi h&agrave;ng cụ thể nhưng tr&igrave;nh b&agrave;y ngắn gọn:</strong></h3>\r\n\r\n<p>Những thư hỏi h&agrave;ng kiểu n&agrave;y thưởng b&agrave;y tỏ sự quan t&acirc;m về những sản phẩm cụ thể nhưng kh&ocirc;ng qu&aacute; nhiều c&aacute;c y&ecirc;u cầu ri&ecirc;ng biệt kh&aacute;c, thường dựa tr&ecirc;n những đặc điểm c&oacute; sẵn khi người b&aacute;n ch&agrave;o h&agrave;ng.</p>\r\n\r\n<p>&nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/3.jpg\" style=\"height:147px; width:633px\" /> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<h3><strong>=&gt; C&aacute;ch trả lời:</strong></h3>\r\n\r\n<ul>\r\n	<li>Phản hồi trong c&ugrave;ng ng&agrave;y</li>\r\n	<li>T&igrave;m kiếm th&ocirc;ng tin kh&aacute;ch h&agrave;ng tr&ecirc;n Internet hoặc c&aacute;c k&ecirc;nh th&ocirc;ng tin kh&aacute;c</li>\r\n	<li>Đưa ra m&ocirc; tả sản phẩm v&agrave; b&aacute;o gi&aacute; theo y&ecirc;u cầu kh&aacute;ch h&agrave;ng</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/4.jpg\" style=\"height:455px; width:640px\" /> &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</p>\r\n\r\n<h3><strong>3.&nbsp;Thư hỏi h&agrave;ng kiểu chung chung</strong></h3>\r\n\r\n<p>Thư hỏi h&agrave;ng kiểu chung chung chỉ muốn đề cập về những chủng loại h&agrave;ng hoặc theo cả một danh mục mặt h&agrave;ng m&agrave; kh&ocirc;ng n&oacute;i đến sản phẩm cụ thể hay y&ecirc;u cầu đặc biệt n&agrave;o.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/5.jpg\" style=\"height:235px; width:640px\" /></p>\r\n\r\n<h3>=&gt; <strong>C&aacute;ch trả lời</strong></h3>\r\n\r\n<ul>\r\n	<li>Mức độ ưu ti&ecirc;n ở sau nhưng cũng n&ecirc;n phản hồi trong c&ugrave;ng ng&agrave;y</li>\r\n	<li>T&igrave;m kiếm th&ocirc;ng tin kh&aacute;ch h&agrave;ng qua Internet hoặc c&aacute;c k&ecirc;nh kh&aacute;c</li>\r\n	<li>Cố gắng phỏng đo&aacute;n những g&igrave; kh&aacute;ch h&agrave;ng muốn</li>\r\n	<li>Đưa ra b&aacute;o gi&aacute; ph&ugrave; hợp với những g&igrave; phỏng đo&aacute;n trong Inquiry</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/6.jpg\" style=\"height:357px; width:638px\" /></p>\r\n\r\n<p>Với 3 loại thư hỏi h&agrave;ng như đ&atilde; đề cập ở tr&ecirc;n, Ad gợi &yacute; bạn về sự ưu ti&ecirc;n trả lời thư hỏi h&agrave;ng theo thứ tự sau:</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#FF0000\"><strong>Thư hỏi h&agrave;ng cụ thể, chi tiết-&gt; thư hỏi h&agrave;ng cụ thể nhưng ngắn gọn-&gt; thư hỏi h&agrave;ng chung chung</strong></span></span></p>\r\n\r\n<h2><strong>Bước 2: Trả lời&nbsp;thư hỏi h&agrave;ng từ người mua</strong></h2>\r\n\r\n<p>Sau khi lọc v&agrave; sắp xếp thứ tự ưu ti&ecirc;n của thư hỏi h&agrave;ng ch&uacute;ng ta n&ecirc;n xem x&eacute;t c&aacute;ch thức phản hồi Inquiry. &nbsp;Đầu ti&ecirc;n, h&atilde;y t&igrave;m hiểu xem những g&igrave; người mua thường quan t&acirc;m nhất khi nhận được phản hồi từ người b&aacute;n theo một số những điều dưới đ&acirc;y:</p>\r\n\r\n<ul>\r\n	<li>Sự thấu hiểu nhu cầu kh&aacute;ch h&agrave;ng từ người b&aacute;n (th&ocirc;ng qua c&aacute;ch diễn đạt, những th&ocirc;ng tin trong b&aacute;o gi&aacute;)</li>\r\n	<li>Sự phản hồi kịp thời từ người b&aacute;n</li>\r\n	<li>Người b&aacute;n c&oacute; thật chuy&ecirc;n nghiệp hay kh&ocirc;ng? (qua c&aacute;ch trả lời thư hỏi h&agrave;ng, l&agrave;m b&aacute;o gi&aacute;, ng&ocirc;n ngữ giao tiếp)</li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Bởi vậy h&atilde;y để người mua ấn tượng với sự phản hồi th&ocirc;ng minh từ m&igrave;nh theo những c&aacute;ch sau:</strong></span></p>\r\n\r\n<h3><strong>1. Nghi&ecirc;n cứu xem ai l&agrave; kh&aacute;ch h&agrave;ng của m&igrave;nh</strong></h3>\r\n\r\n<p style=\"margin-left:.5in\">Một thư hỏi h&agrave;ng thường được cung cấp đến&nbsp; bạn th&ocirc;ng&nbsp; tin từ người mua (c&oacute; thể bao gồm: t&ecirc;n c&ocirc;ng ty nhập khẩu, địa chỉ, email, số điện thoại&hellip;). Một số người mua sẽ c&oacute; phần giới thiệu ngắn về c&ocirc;ng ty của họ, về quốc gia nơi họ nhập h&agrave;ng, mạng lưới b&aacute;n h&agrave;ng&hellip;Những th&ocirc;ng tin hữu &iacute;ch n&agrave;y sẽ gi&uacute;p bạn đưa ra b&aacute;o gi&aacute; ph&ugrave; hợp với nhu cầu v&agrave; thị trường của người mua.</p>\r\n\r\n<p style=\"margin-left:.5in\">Một số người mua nếu kh&ocirc;ng đề cập nhiều về c&ocirc;ng ty của họ, bạn c&oacute; thể sử dụng Internet để search th&ecirc;m th&ocirc;ng&nbsp; tin hoặc sử dụng c&aacute;c k&ecirc;nh kh&aacute;c (v&iacute; dụ qua đại sứ qu&aacute;n, qua ph&ograve;ng thương vụ&hellip;)</p>\r\n\r\n<p style=\"margin-left:.5in\">Hiểu biết về quốc gia nhập khẩu h&agrave;ng h&oacute;a sẽ gi&uacute;p người b&aacute;n thuận tiện hơn trong việc đưa ra b&aacute;o gi&aacute; chi tiết. V&iacute; dụ , người mua đến&nbsp; từ Mỹ v&agrave; ph&acirc;n phối h&agrave;ng h&oacute;a ngay tại quốc gia n&agrave;y, bạn n&ecirc;n tiếp cận về c&aacute;c chi ph&iacute; vận tải bao gồm hay c&aacute;c giấy tờ y&ecirc;u cầu để h&agrave;ng h&oacute;a c&oacute; thể nhập khẩu được v&agrave;o Mỹ từ đ&oacute; đưa ra lời gợi &yacute; cho kh&aacute;ch h&agrave;ng của m&igrave;nh.</p>\r\n\r\n<h3><strong>2. Thời gian phản hồi</strong></h3>\r\n\r\n<p style=\"margin-left:.5in\">H&atilde;y cố gắng phản hồi c&aacute;c thư hỏi h&agrave;ng trong ng&agrave;y. Nếu bạn kh&ocirc;ng c&oacute; khả năng đưa ra b&aacute;o gi&aacute; trong ng&agrave;y, h&atilde;y viết thư cho họ v&agrave; th&ocirc;ng b&aacute;o đ&atilde; nhận được thư hỏi h&agrave;ng v&agrave; cần th&ecirc;m thời gian để kiểm tra c&aacute;c th&ocirc;ng tin trước khi đưa ra b&aacute;o gi&aacute;, đừng qu&ecirc;n n&oacute;i với kh&aacute;ch h&agrave;ng về thời gian bạn sẽ gửi lại b&aacute;o gi&aacute; cho họ. Nếu người mua kh&ocirc;ng hồi đ&aacute;p lại ở lần đầu ti&ecirc;n, đừng nản ch&iacute;, h&atilde;y gửi mail hoặc gọi điện cho họ để x&aacute;c nhận xem họ đ&atilde; nhận được email gần đ&acirc;y nhất hay kh&ocirc;ng? Th&ocirc;ng thường, ở lần đầu ti&ecirc;n bạn c&oacute; thể gửi cho họ catalogue sản phẩm mới nhất hoặc sản phẩm tiềm năng, xu hướng mới của thị trường&hellip;điều n&agrave;y sẽ l&agrave;m kh&aacute;ch h&agrave;ng&nbsp; ch&uacute; &yacute; đến bạn hơn.</p>\r\n\r\n<h3><strong>3.&nbsp;Bạn đ&atilde; sẵn s&agrave;ng l&agrave;m b&aacute;o gi&aacute; gửi đến kh&aacute;ch h&agrave;ng?</strong></h3>\r\n\r\n<p style=\"margin-left:.5in\">Một b&aacute;o gi&aacute; ti&ecirc;u chuẩn thường bao gồm những nội dung sau đ&acirc;y:</p>\r\n\r\n<ul>\r\n	<li>Gi&aacute;: B&aacute;o gi&aacute; kh&aacute;c nhau sẽ t&ugrave;y thuộc v&agrave;o điều kiện giao h&agrave;ng cụ thể v&agrave; Cảng đ&iacute;ch.</li>\r\n	<li>Logistics: Chọn c&ocirc;ng ty Forwarders, c&ocirc;ng ty Logistics uy t&iacute;n để đảm bảo vận chuyển h&agrave;ng h&oacute;a đến đ&iacute;ch một c&aacute;ch an to&agrave;n v&agrave; đương nhi&ecirc;n cước ph&iacute; phải thật hợp l&yacute;.</li>\r\n	<li>Thời gian giao h&agrave;ng: Đề cập đến thời gian giao h&agrave;ng dự kiến</li>\r\n	<li>M&ocirc; tả sản phẩm: cung cấp th&ocirc;ng tin chi tiết sản phẩm, th&ocirc;ng số kỹ thuật&hellip; để chứng minh bạn l&agrave; l&agrave; cung cấp chuy&ecirc;n nghiệp.</li>\r\n	<li>Đ&oacute;ng g&oacute;i: Cung cấp th&ocirc;ng tin chi tiết về quy c&aacute;ch đ&oacute;ng g&oacute;i sản phẩm bao gồm b&ecirc;n trong v&agrave; b&ecirc;n ngo&agrave;i, số lượng container 20&rsquo; hay 40&rsquo;,h&agrave;ng LCL hay FCL..</li>\r\n	<li>H&igrave;nh ảnh sản phẩm: h&igrave;nh ảnh sản phẩm phải thật r&otilde; r&agrave;ng v&agrave; chi tiết. Nếu c&oacute; thể h&atilde;y sử dụng những chiếc m&aacute;y ảnh chuy&ecirc;n dụng v&agrave; c&ocirc;ng nghệ Photoshop để l&agrave;m t&ocirc;n l&ecirc;n đường n&eacute;t sản phẩm. H&atilde;y nhớ rằng, trước khi mua sản phẩm của bạn hoặc quyết định lấy mẫu, kh&aacute;ch h&agrave;ng chỉ biết được sản phẩm của bạn qua h&igrave;nh ảnh m&agrave; th&ocirc;i.</li>\r\n	<li>H&agrave;ng mẫu: Chuẩn bị h&agrave;ng mẫu cho từng mặt h&agrave;ng v&agrave; sẵn s&agrave;ng gửi cho kh&aacute;ch h&agrave;ng nếu cần, bạn c&oacute; thể miễn ph&iacute; h&agrave;ng mẫu hoặc đưa ra ch&iacute;nh s&aacute;ch h&agrave;ng mẫu ri&ecirc;ng biệt.</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/7.jpg\" style=\"height:784px; width:633px\" /></p>\r\n\r\n<h3><strong>4.&nbsp;Mẹo giao tiếp hiệu quả qua email</strong></h3>\r\n\r\n<p>Để kh&aacute;ch h&agrave;ng đ&aacute;nh gi&aacute; được sự chuy&ecirc;n nghiệp của bạn, h&atilde;y cố gắng để c&oacute; được những email giao dịch thật chuy&ecirc;n nghiệp.</p>\r\n\r\n<ul>\r\n	<li>Ti&ecirc;u đề email: n&ecirc;n ngắn gọn v&agrave; cụ thể. N&oacute; gi&uacute;p những kh&aacute;ch h&agrave;ng tiềm năng dễ d&agrave;ng t&igrave;m thấy b&aacute;o gi&aacute;, ngo&agrave;i ra ti&ecirc;u đề email c&ograve;n gi&uacute;p kh&aacute;ch h&agrave;ng dễ ph&acirc;n loại trong hộp thư đến</li>\r\n</ul>\r\n\r\n<p>V&iacute; dụ: B&aacute;o gi&aacute; mặt h&agrave;ng A từ c&ocirc;ng ty XYZ</p>\r\n\r\n<ul>\r\n	<li>Th&acirc;n email: cỡ chữ n&ecirc;n vừa phải. Kh&ocirc;ng n&ecirc;n d&ugrave;ng chữ hoa trong to&agrave;n bộ email. Những &yacute; ch&iacute;nh c&oacute; thể được b&ocirc;i đậm hoặc đ&aacute;nh dấu bằng m&agrave;u kh&aacute;c để kh&aacute;ch h&agrave;ng ch&uacute; &yacute; hơn.</li>\r\n	<li>Chữ k&yacute;: Chữ k&yacute; cũng c&oacute; thể đ&aacute;nh gi&aacute; được sự chuy&ecirc;n nghiệp của bạn v&agrave; qua đ&oacute; kh&aacute;ch h&agrave;ng c&oacute; được những th&ocirc;ng tin li&ecirc;n lạc cơ bản. Những th&ocirc;ng tin cơ bản n&ecirc;n đề cập trong phần chữ k&yacute; như: t&ecirc;n c&ocirc;ng ty, địa chỉ, website, điện thoại, email, skype</li>\r\n</ul>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/8.jpg\" style=\"height:211px; width:633px\" /></p>\r\n\r\n<p>Hy vọng với b&agrave;i viết n&agrave;y của Ad, c&aacute;c bạn sẽ t&iacute;ch lũy một số&nbsp;kỹ năng để trả lời thư hỏi h&agrave;ng từ đ&oacute; c&oacute; th&ecirc;m nhiều kh&aacute;ch h&agrave;ng hơn. Đừng qu&ecirc;n ủng hộ Ad bằng c&aacute;ch comment ph&iacute;a dưới b&agrave;i viết để ch&uacute;ng ta kết nối với CLB nh&eacute;.</p>','/c1/71/1c/5a6a1213abde3.jpg',NULL,15138,0,'2018-01-26 00:00:00',NULL,'Có phải mỗi ngày bạn nhận được hàng trăm email hỏi hàng từ những khách hàng tiềm năng và bạn bối rối không biết phải trả lời ai trước? Bạn có nhận thấy rất nhiều khách hàng không trả lời email sau khi bạn gửi Báo giá cho họ? Dưới đây là một số cách giúp N','inquiry, trả lời thư hỏi hàng, làm báo giá hàng xuất, form báo giá hàng xuất khẩu, form email chào hàng, hỏi hàng, cách chọn thư hỏi hàng chất lượng','','',2,0,'vi','cach-tra-loi-thu-hoi-hang-va-lam-bao-gia',6,27,26,'2018-01-26 00:21:23','2018-03-03 13:25:40'),(221,'BÍ QUYẾT TRA MÃ HS CHƯA AI DẠY BẠN- PHẦN 1','','Nghiệp vụ tra mã HS là một trong những công việc mà nhân viên xuất nhập khẩu nào cũng phải biết. Tuy nhiên không phải ai cũng nắm được các cách tra mã HS để tiết kiệm thời gian mà vẫn đảm bảo tính hợp lý của mã HS với hàng hóa. Đầu tiên, để nắm được kiến thức tổng quát về mã HS và cách tra mã HS, các em phải nắm được 6 quy tắc phân loại hàng hóa kinh điển dưới đây.','<p><strong>Nhằm thống nhất c&aacute;ch ph&acirc;n loại h&agrave;ng h&oacute;a cho tất cả c&aacute;c quốc gia, C&ocirc;ng ước HS quy định&nbsp;việc &aacute;p m&atilde; HS phải tu&acirc;n theo tuần tự 6 quy tắc n&agrave;y.&nbsp;</strong></p>\r\n\r\n<p>T&ugrave;y trường hợp cụ thể, để ph&acirc;n loại h&agrave;ng h&oacute;a s&aacute;t với danh mục HS nhất, ta phải dựa v&agrave;o một hay một số căn cứ sau đ&acirc;y:</p>\r\n\r\n<ul>\r\n	<li>Danh mục h&agrave;ng h&oacute;a xuất khẩu, nhập khẩu Việt Nam (danh mục HS); Biểu thuế xuất khẩu; Biểu thuế Nhập khẩu</li>\r\n	<li>C&aacute;c chi tiết, th&ocirc;ng số kỹ thuật được m&ocirc; tả dựa tr&ecirc;n thực tế h&agrave;ng h&oacute;a</li>\r\n	<li>T&agrave;i liệu kỹ thuật, h&igrave;nh ảnh minh họa, catalogue, tem, nh&atilde;n m&aacute;c</li>\r\n	<li>Kết quả ph&acirc;n t&iacute;ch ph&acirc;n loại, kết quả gi&aacute;m định h&agrave;ng h&oacute;a</li>\r\n</ul>\r\n\r\n<p><strong><u>QUY TẮC 1</u></strong>:&nbsp;Từ kh&oacute;a trong m&ocirc; tả t&ecirc;n h&agrave;ng + Ch&uacute; giải</p>\r\n\r\n<p>Dựa v&agrave;o&nbsp;<strong>t&ecirc;n đ&iacute;ch danh&nbsp;</strong>của mặt h&agrave;ng, ch&uacute;ng ta c&oacute; thể định h&igrave;nh được mặt h&agrave;ng của m&igrave;nh thuộc&nbsp;<strong>Phần, Chương</strong>&nbsp;n&agrave;o dựa v&agrave;o t&ecirc;n<strong>&nbsp;Phần, Chương&nbsp;</strong>v&agrave;&nbsp;<strong>Ch&uacute; giải Phần, Ch&uacute; giải Chương</strong>;</p>\r\n\r\n<p>Dựa v&agrave;o c&aacute;ch diễn giải ở c&aacute;c ch&uacute; giải, ta c&oacute; được c&aacute;c chỉ dẫn để t&igrave;m đến Chương, Nh&oacute;m chứa mặt h&agrave;ng ta đang t&igrave;m kiếm. C&aacute;c ch&uacute; giải Chương v&agrave; Ph&acirc;n chương (nếu c&oacute;) c&oacute; t&iacute;nh chất quyết định đến việc ph&acirc;n loại h&agrave;ng h&oacute;a, &aacute;p dụng cho cả 6 quy tắc.</p>\r\n\r\n<p>V&iacute; dụ: X&aacute;c định m&atilde; HS của băng truyền bằng cao su lưu h&oacute;a d&ugrave;ng cho m&aacute;y vận chuyển h&agrave;ng h&oacute;a tự động</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/bang-truyen-cao-su-luu-hoa.jpg\" style=\"height:340px; width:550px\" /></p>\r\n\r\n<ul>\r\n	<li>X&aacute;c định được băng tải bằng cao su thuộc nh&oacute;m 4010 (Chương 40)</li>\r\n	<li>Trong nh&oacute;m 4010 được chi tiết nhỏ hơn theo chất liệu gia cố, k&iacute;ch thước, thiết kế&hellip; Vậy, ch&uacute;ng ta cần y&ecirc;u cầu b&ecirc;n b&aacute;n cung cấp th&ecirc;m chi tiết mặt h&agrave;ng (catalogue, t&agrave;i liệu kỹ thuật&hellip;) sao cho&nbsp;<strong><u>s&aacute;t với m&ocirc; tả trong biểu thuế nhất</u></strong>để dẫn ch&uacute;ng ta đến m&atilde; HS 8 chữ số cuối c&ugrave;ng.</li>\r\n</ul>\r\n\r\n<p><strong><u>QUY TẮC 2</u></strong>: C&aacute;ch tra m&atilde; HS đối với c&aacute;c sản phẩm c&oacute; nhiều th&agrave;nh phần chất liệu hoặc c&oacute; cấu tạo đặc biệt</p>\r\n\r\n<p><em><strong>Quy tắc 2a)</strong></em>&nbsp;Sản phẩm chưa ho&agrave;n chỉnh hoặc chưa ho&agrave;n thiện, chưa lắp r&aacute;p hoặc th&aacute;o rời.<em>&nbsp;</em></p>\r\n\r\n<p>Một mặt h&agrave;ng chưa ho&agrave;n chỉnh hoặc chưa ho&agrave;n thiện, c&oacute; thể thiếu một số bộ phận phụ hoặc phụ kiện nhưng cơ bản đ&atilde; mang đặc trưng&nbsp;của sản phẩm ho&agrave;n chỉnh th&igrave; được ph&acirc;n loại như sản phẩm đ&atilde; ho&agrave;n thiện.</p>\r\n\r\n<p>VD: Chiếc xe đạp thiếu y&ecirc;n xe v&agrave; tay l&aacute;i vẫn được ph&acirc;n loại như&nbsp;xe đạp ho&agrave;n thiện.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/ma-hs-xe-dap-khong-yen-khong-tay-lai.jpg\" style=\"height:362px; width:550px\" /></p>\r\n\r\n<p>C&aacute;c &ldquo;h&agrave;ng h&oacute;a ở dạng chưa lắp r&aacute;p hoặc th&aacute;o rời&rdquo; được hiểu&nbsp;l&agrave; c&aacute;c bộ phận, linh kiện cấu th&agrave;nh h&agrave;ng h&oacute;a ph&ugrave; hợp để lắp r&aacute;p được với nhau bằng những dụng cụ, thiết bị đơn giản</p>\r\n\r\n<p>VD: Ph&acirc;n loại h&agrave;ng h&oacute;a cho sản phẩm l&agrave;:&nbsp;Linh kiện m&aacute;y đồng hồ c&aacute; nh&acirc;n ho&agrave;n chỉnh chưa lắp r&aacute;p</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/dong-ho-thao-roi.jpg\" style=\"height:379px; width:550px\" /></p>\r\n\r\n<p>Một số sản phẩm do t&iacute;nh đặc th&ugrave;, trong Danh mục HS c&oacute; ph&acirc;n chia theo sản phẩm chưa lắp r&aacute;p ho&agrave;n chỉnh th&igrave; ta c&oacute; thể dễ d&agrave;ng &aacute;p m&atilde; HS ngay, c&ograve;n đối với c&aacute;c h&agrave;ng h&oacute;a kh&aacute;c, ta vẫn &aacute;p dụng quy tắc 2a)&nbsp;như b&igrave;nh thường.</p>\r\n\r\n<p><em><strong>Quy tắc&nbsp;2b):</strong></em>&nbsp;H&agrave;ng h&oacute;a l&agrave; hợp chất của nhiều chất, hoặc hỗn hợp của nhiều nguy&ecirc;n liệu, chất liệu.</p>\r\n\r\n<p>TH1: C&aacute;c&nbsp;<em><strong>đơn chất</strong></em>&nbsp;của hợp chất hoặc c&aacute;c&nbsp;<em><strong>nguy&ecirc;n liệu, chất liệu</strong></em>&nbsp;tạo th&agrave;nh sản phẩm cuối c&ugrave;ng nếu&nbsp;<em><strong>thuộc c&ugrave;ng một nh&oacute;m</strong></em>&nbsp;th&igrave; hợp chất hoặc sản phẩm cũng thuộc nh&oacute;m đ&oacute;.</p>\r\n\r\n<p>Giả sử: Hỗn hợp gồm&nbsp;<em><strong>2 th&agrave;nh phần đều thuộc nh&oacute;m 2907</strong></em>&nbsp;th&igrave; hỗn hợp đ&oacute; được ph&acirc;n loại v&agrave;o nh&oacute;m 2907.</p>\r\n\r\n<p>TH2:&nbsp;C&aacute;c&nbsp;<em><strong>đơn chất</strong></em>&nbsp;của hợp chất hoặc c&aacute;c&nbsp;<em><strong>nguy&ecirc;n liệu, chất liệu</strong></em>&nbsp;tạo th&agrave;nh sản phẩm cuối c&ugrave;ng nếu&nbsp;<em><strong>thuộc c&aacute;c nh&oacute;m kh&aacute;c nhau&nbsp;</strong></em>th&igrave; ph&acirc;n loại theo chất cơ bản nhất mang lại t&iacute;nh chất đặc trưng hơn của sản phẩm.</p>\r\n\r\n<p>VD: Dao bằng inox c&aacute;n gỗ d&ugrave;ng trong nh&agrave; bếp&nbsp;&rArr; dao bằng inox mang lại t&iacute;nh chất đặc trưng&nbsp;cho&nbsp;sản phẩm &rArr; ph&acirc;n loại theo&nbsp;dao chất liệu bằng kim loại (Nh&oacute;m 8211)</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/dao-inox-can-go.jpg\" style=\"height:234px; width:550px\" /></p>\r\n\r\n<p>C&ograve;n tiếp...</p>','/18/ef/f1/5a6a13284da5e.jpg',NULL,5930,0,'2018-01-26 00:00:00',NULL,'Nghiệp vụ tra mã HS là một trong những công việc mà nhân viên xuất nhập khẩu nào cũng phải biết. Tuy nhiên không phải ai cũng nắm được các cách tra mã HS để tiết kiệm thời gian mà vẫn đảm bảo tính hợp lý của mã HS với hàng hóa. Nhằm thống nhất cách phân l','cách tra mã HS, 6 quy tắc phân loại hàng hóa, biểu thuế xuất nhập khẩu, tra cứu biểu thuế, bí quyết tra mã HS','','',2,0,'vi','bi-quyet-tra-ma-hs-chua-ai-day-ban-phan-1',8,27,26,'2018-01-26 00:26:00','2018-03-03 13:27:01'),(222,'6 QUY TẮC PHÂN LOẠI HÀNG HÓA - PHẦN 2','','Ở trong bài viết trước Ad đã hướng dẫn các em Cách tra mã HS theo Quy tắc 1, Quy tắc 2a, 2b, 2c. Trong bài viết này Ad hy vọng rằng các em sẽ là 1 Master về cách tra mã HS khi Ad sẽ chia sẻ 3 bí quyết còn lại.','<p><strong><u>QUY TẮC 3</u></strong>:&nbsp;H&agrave;ng h&oacute;a &nbsp;<em><u>thoạt nh&igrave;n c&oacute; thể ph&acirc;n loại v&agrave;o hai hay nhiều nh&oacute;m kh&aacute;c nhau</u></em></p>\r\n\r\n<p><strong>C&aacute;ch &nbsp;tra m&atilde; HS</strong>&nbsp;của c&aacute;c mặt h&agrave;ng thuộc quy tắc 3- l&agrave; một trong những quy tắc g&acirc;y tranh c&atilde;i nhiều nhất được h&igrave;nh ảnh h&oacute;a bằng c&aacute;c v&iacute; dụ dưới đ&acirc;y</p>\r\n\r\n<p>VD: Đinh t&aacute;n h&igrave;nh ống bằng th&eacute;p d&ugrave;ng để lắp r&aacute;p c&aacute;c m&aacute; phanh, l&oacute;t m&aacute; phanh</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/dinh-tan-hinh-ong-co-chan-xoe.jpg\" style=\"height:431px; width:550px\" /></p>\r\n\r\n<p>Đinh t&aacute;n được nhắc đến trong nh&oacute;m 7318 (73182300) v&agrave; nh&oacute;m 8308, nhưng trong 8308 được m&ocirc; tả cụ thể hơn &ldquo;Đinh t&aacute;n h&igrave;nh ống hoặc đinh t&aacute;n c&oacute; ch&acirc;n x&ograve;e&rdquo;&nbsp;&rArr; ph&acirc;n loại v&agrave;o nh&oacute;m 8308.</p>\r\n\r\n<p><strong><em>Quy tắc 3b)</em></strong>&nbsp;H&agrave;ng h&oacute;a l&agrave; hỗn hợp của nhiều chất liệu, nguy&ecirc;n liệu, nhiều bộ phận cấu th&agrave;nh kh&aacute;c nhau&nbsp;hoặc h&agrave;ng h&oacute;a được đ&oacute;ng g&oacute;i dạng bộ m&agrave; c&aacute;c th&agrave;nh phần cấu th&agrave;nh bộ/hỗn hợp thuộc c&aacute;c nh&oacute;m kh&aacute;c nhau.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/bo-dung-cu-ve.jpg\" style=\"height:292px; width:550px\" />VD: Bộ dụng cụ vẽ gồm: một thước, một v&ograve;ng t&iacute;nh, một compa, một b&uacute;t ch&igrave; v&agrave; c&aacute;i gọt&nbsp;b&uacute;t ch&igrave;, đựng trong t&uacute;i nhựa.</p>\r\n\r\n<p><strong>C&aacute;ch tra m&atilde; HS</strong>&nbsp;cho v&iacute; dụ tr&ecirc;n như sau:<br />\r\nThước:&nbsp;Nh&oacute;m 90.17<br />\r\nV&ograve;ng t&iacute;nh: Nh&oacute;m 90.17<br />\r\nCompa: Nh&oacute;m 90.17<br />\r\nB&uacute;t ch&igrave;: Nh&oacute;m 96.09<br />\r\nGọt b&uacute;t ch&igrave;: Nh&oacute;m 82.14<br />\r\nT&uacute;i nhựa: Nh&oacute;m 42.02.</p>\r\n\r\n<p><strong><em>Quy tắc 3c)</em></strong>&nbsp;Khi h&agrave;ng h&oacute;a kh&ocirc;ng ph&acirc;n loại được theo quy tắc 3a) v&agrave; 3b) th&igrave; &aacute;p dụng quy tắc 3c), h&agrave;ng h&oacute;a sẽ được ph&acirc;n loại v&agrave;o&nbsp;<strong><u>nh&oacute;m cuối c&ugrave;ng theo thứ tự đ&aacute;nh số</u></strong>&nbsp;trong số c&aacute;c nh&oacute;m tương đương được xem x&eacute;t.</p>\r\n\r\n<p>VD:&nbsp;Băng tải một mặt chất liệu&nbsp;plastic v&agrave; một mặt chất liệu&nbsp;cao su. Theo đ&oacute;, mặt h&agrave;ng tr&ecirc;n được ph&acirc;n loại v&agrave;o Nh&oacute;m 4010.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/bang-truyen-bang-tai-cong-nghiep.jpg\" style=\"height:413px; width:550px\" /></p>\r\n\r\n<p><strong><u>QUY TẮC 4:</u></strong>&nbsp;Ph&acirc;n loại theo h&agrave;ng h&oacute;a giống ch&uacute;ng nhất</p>\r\n\r\n<p>Khi mặt h&agrave;ng n&agrave;o đ&oacute; kh&ocirc;ng thể ph&acirc;n loại được theo c&aacute;c Quy tắc 1 đến Quy tắc 3 th&igrave; ta c&oacute; thể ph&acirc;n loại dựa tr&ecirc;n những m&ocirc; tả của mặt h&agrave;ng đ&oacute; so với m&ocirc; tả trong Danh mục HS để t&igrave;m ra nh&oacute;m h&agrave;ng h&oacute;a c&oacute; m&ocirc; tả giống hoặc gần giống với ch&uacute;ng nhất.</p>\r\n\r\n<p>VD: Khớp nối ống h&igrave;nh chữ T, bằng nhựa. M&ocirc; tả n&agrave;y giống với c&aacute;c sản phẩm l&agrave; phụ kiện d&ugrave;ng để gh&eacute;p nối ống&nbsp;&rArr; Thuộc nh&oacute;m phụ kiện gh&eacute;p nối ống, bằng plastic.&nbsp;&rArr; Ph&acirc;n loại v&agrave;o nh&oacute;m 3917.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/cut-noi-ong-bang-plastic.jpg\" style=\"height:320px; width:500px\" /></p>\r\n\r\n<p><strong><u>QUY TẮC 5:</u></strong>&nbsp;Ph&acirc;n loại h&agrave;ng h&oacute;a l&agrave; hộp đựng, bao b&igrave;</p>\r\n\r\n<p><strong><em>Quy tắc 5a)</em></strong>&nbsp;Bao b&igrave; đặc biệt được hiểu l&agrave;:</p>\r\n\r\n<ul>\r\n	<li>C&aacute;c bao b&igrave; c&oacute;&nbsp;<strong><u>h&igrave;nh d&aacute;ng th&iacute;ch hợp&nbsp;hoặc&nbsp;đặc biệt</u></strong>để chứa h&agrave;ng h&oacute;a hoặc bộ h&agrave;ng h&oacute;a x&aacute;c định</li>\r\n	<li>Bao b&igrave; hầu hết được sử dụng trong&nbsp;<strong><u>thời gian d&agrave;i, đi k&egrave;m</u></strong>với sản phẩm khi b&aacute;n</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/bao-da-may-anh.jpg\" style=\"height:343px; width:550px\" />&rArr; Được ph&acirc;n loại c&ugrave;ng nh&oacute;m với sản phẩm m&agrave; n&oacute; chứa đựng.</p>\r\n\r\n<p>VD:</p>\r\n\r\n<p>Hộp đựng đồ trang sức: Nh&oacute;m 7113<br />\r\nBao da đựng m&aacute;y cạo r&acirc;u bằng điện: Nh&oacute;m 8510<br />\r\nBao da đựng ống nh&ograve;m, hộp k&iacute;nh viễn vọng: Nh&oacute;m 9005<br />\r\nHộp đựng đ&agrave;n ghita:&nbsp;Nh&oacute;m 9202</p>\r\n\r\n<p><strong><em>Quy tắc 5b)</em></strong>&nbsp;Bao b&igrave; th&ocirc;ng thường&nbsp;&rArr; ph&acirc;n loại theo sản phẩm n&oacute; chứa đựng</p>\r\n\r\n<ul>\r\n	<li>Kh&ocirc;ng phải l&agrave; c&aacute;c bao b&igrave; đ&atilde; n&ecirc;u tại quy tắc 5a)</li>\r\n	<li>D&ugrave;ng để đ&oacute;ng g&oacute;i chứa đựng h&agrave;ng h&oacute;a, kh&ocirc;ng t&aacute;ch rời h&agrave;ng h&oacute;a m&agrave; n&oacute; chứa đựng, kể cả trong qu&aacute; tr&igrave;nh vận chuyển</li>\r\n	<li>Bao b&igrave; chỉ d&ugrave;ng một lần, kh&ocirc;ng sử dụng lặp lại</li>\r\n	<li>Gi&aacute; trị của bao b&igrave; rất nhỏ, đ&atilde; được t&iacute;nh trong gi&aacute; h&agrave;ng h&oacute;a</li>\r\n</ul>\r\n\r\n<p>VD: Bao b&igrave; b&aacute;nh kẹo, m&igrave; ăn liền; b&igrave;nh ga mini chứa ga</p>\r\n\r\n<p>C&aacute;c h&agrave;ng h&oacute;a kh&ocirc;ng &aacute;p dụng quy tắc 5b):&nbsp;Bao b&igrave; c&oacute; thể d&ugrave;ng lặp lại, v&iacute; dụ:&nbsp;th&ugrave;ng kim loại hoặc b&igrave;nh sắt, th&eacute;p đựng kh&iacute; đốt dạng n&eacute;n hoặc lỏng (B&igrave;nh ga gia đ&igrave;nh)</p>\r\n\r\n<p><strong><u>QUY TẮC 6</u></strong>:&nbsp;C&aacute;ch ph&acirc;n loại h&agrave;ng h&oacute;a theo danh mục HS v&agrave; so s&aacute;nh c&ugrave;ng cấp độ:</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/9.%20tra%20ma%20HS%201-2/quy-tac-6-phan-loai-theo-cung-cap-do.jpg\" style=\"height:318px; width:550px\" /></p>\r\n\r\n<p>Việc ph&acirc;n loại h&agrave;ng h&oacute;a v&agrave;o mỗi chương, v&agrave;o mỗi nh&oacute;m v&agrave; ph&acirc;n nh&oacute;m phải ph&ugrave; hợp với ch&uacute; giải c&oacute; li&ecirc;n quan. Khi so s&aacute;nh c&aacute;c sản phẩm ở c&aacute;c nh&oacute;m, ph&acirc;n nh&oacute;m kh&aacute;c nhau ta phải so s&aacute;nh c&ugrave;ng cấp độ.</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; tổng hợp c&aacute;c Quy tắc ph&acirc;n loại h&agrave;ng h&oacute;a do m&igrave;nh tổng hợp v&agrave; t&iacute;ch lũy từ kiến thức l&yacute; thuyết v&agrave; hiểu biết đ&uacute;c r&uacute;t từ kinh nghiệm trong qu&aacute; tr&igrave;nh l&agrave;m việc của ch&iacute;nh bản th&acirc;n m&igrave;nh rất mong gi&uacute;p &iacute;ch được cho c&aacute;c em&nbsp;tự học&nbsp;tốt hơn!</p>\r\n\r\n<p>C&aacute;c em h&atilde;y ủng hộ cho CLB bằng việc chia sẻ b&agrave;i viết n&agrave;y đến những bạn cần nh&eacute;!</p>\r\n\r\n<p>Ms Sarah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>','/70/ef/f7/5a6a14bd2acdb.jpg',NULL,3436,0,'2018-01-26 00:00:00',NULL,'Cách  tra mã HS của các mặt hàng thuộc quy tắc 3- là một trong những quy tắc gây tranh cãi nhiều nhất được hình ảnh hóa bằng các ví dụ dưới đây\r\nVD: Đinh tán hình ống bằng thép dùng để lắp ráp các má phanh, lót má phanh','cách tra mã HS, 6 quy tắc phân loại hàng hóa, biểu thuế xuất nhập khẩu, tra cứu biểu thuế, bí quyết tra mã HS','','',2,0,'vi','6-quy-tac-phan-loai-hang-hoa-phan-2',8,27,26,'2018-01-26 00:32:45','2018-03-03 13:29:11'),(223,'CÁC BƯỚC TRONG QUY TRÌNH THANH TOÁN L/C','','Phương thức tín dụng chứng từ được áp dụng trong các trường hợp như:\r\n\r\n+ Trong buôn bán với các đối tác mới.\r\n\r\n+ Do quy định hay tập quán thương mại và thanh toán.\r\n\r\n+ Do yêu cầu của các nhà bảo hiểm tín dụng.','<h1>Phương thức t&iacute;n dụng chứng từ được &aacute;p dụng trong c&aacute;c trường hợp như:</h1>\r\n\r\n<p>+ Trong bu&ocirc;n b&aacute;n với c&aacute;c đối t&aacute;c mới.</p>\r\n\r\n<p>+ Do quy định hay tập qu&aacute;n thương mại v&agrave; thanh to&aacute;n.</p>\r\n\r\n<p>+ Do y&ecirc;u cầu của c&aacute;c nh&agrave; bảo hiểm t&iacute;n dụng.</p>\r\n\r\n<p>+ Do c&aacute;c y&ecirc;u cầu về quản l&yacute; ngoại hối</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/11.%20thanh%20toan%20LC/Letter-of-Credit.jpg\" style=\"height:320px; width:480px\" /></p>\r\n\r\n<p>Trong b&agrave;i học n&agrave;y, ch&uacute;ng ta sẽ t&igrave;m hiểu c&aacute;c bước trong quy tr&igrave;nh thanh to&aacute;n theo phương ph&aacute;p&nbsp;<strong>T&iacute;n dụng chứng từ</strong>&nbsp;hay c&ograve;n gọi l&agrave; LC (Letter of Credit)</p>\r\n\r\n<p>Ghi ch&uacute;:</p>\r\n\r\n<p>+ Importer/ the applicant: Nh&agrave; nhập khẩu/ người mở LC</p>\r\n\r\n<p>+ Exporter/ the beneficiary: Nh&agrave; xuất khẩu/ người hưởng lợi</p>\r\n\r\n<p>+ Issuing bank: Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh</p>\r\n\r\n<p>+ Advising bank: Ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o</p>\r\n\r\n<h1>Quy tr&igrave;nh thanh to&aacute;n tr&ecirc;n sẽ trải qua c&aacute;c bước sau:</h1>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/11.%20thanh%20toan%20LC/Cac-buoc-trong-quy-trinh-thanh-toan-Tin-dung-chung-tu(1).jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p><strong>Bước 0:&nbsp;</strong>Nh&agrave; NK v&agrave; nh&agrave; XK tiến h&agrave;nh k&yacute; kết hợp đồng ngoại thương, trong đ&oacute; quy định thanh to&aacute;n bằng phương thức t&iacute;n dụng chứng từ (LC)</p>\r\n\r\n<p><strong>Bước 1:&nbsp;</strong>Căn cứ v&agrave;o hợp đồng ngoại thương đ&atilde; k&yacute;, Nh&agrave; NK gửi đơn y&ecirc;u cầu mở thư t&iacute;n dụng đến Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh v&agrave; tiến h&agrave;nh k&yacute; quỹ (nếu c&oacute;). K&yacute; quỹ c&oacute; thể 100% hoặc dưới 100% t&ugrave;y mức độ uy t&iacute;n của doanh nghiệp theo đ&aacute;nh gi&aacute; của Ng&acirc;n h&agrave;ng nơi mở LC.</p>\r\n\r\n<p>Hồ sơ mở LC bạn c&oacute; thể tham khảo tại c&aacute;c Ng&acirc;n &nbsp;h&agrave;ng hoặc đơn giản đến với CLB Tập sự XNK để tham gia kh&oacute;a học thực tế Nghiệp vụ Xuất nhập khẩu. Ch&uacute;ng t&ocirc;i sẽ c&ugrave;ng bạn thực h&agrave;nh l&agrave;m bộ hồ sơ xin mở LC v&agrave; hướng dẫn c&aacute;ch l&agrave;m việc với Ng&acirc;n h&agrave;ng sao cho hiệu quả.</p>\r\n\r\n<p><strong>Bước 2:&nbsp;</strong>Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh thư t&iacute;n dụng xem x&eacute;t, nếu thấy hợp l&yacute; sẽ ph&aacute;t h&agrave;nh thư t&iacute;n dụng (LC) qua Ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o&nbsp;cho Người Xuất khẩu hưởng lợi.</p>\r\n\r\n<p><strong>Bước 3:&nbsp;</strong>Ng&acirc;n h&agrave;ng đại l&yacute; sẽ tiến h&agrave;nh th&ocirc;ng b&aacute;o thư t&iacute;n dụng v&agrave; chuyển bản gốc thư t&iacute;n dụng cho Người hưởng lợi (Người xuất khẩu)</p>\r\n\r\n<p><strong>Bước 4:&nbsp;</strong>Nh&agrave; Xuất khẩu tiến h&agrave;nh kiểm tra L/C, nếu thấy ph&ugrave; hợp th&igrave; giao h&agrave;ng cho Nh&agrave; Nhập &nbsp;khẩu, c&ograve;n kh&ocirc;ng ph&ugrave; hợp th&igrave; đề nghị Nh&agrave; nhập khẩu tu chỉnh L/C.</p>\r\n\r\n<p><strong>Bước 5:&nbsp;</strong>Nh&agrave; Xuất khẩu xuất tr&igrave;nh chứng từ cho Ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o đ&ograve;i tiền.</p>\r\n\r\n<p><strong>Bước 6:&nbsp;</strong>Ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o sau khi đ&atilde; kiểm tra chứng từ th&igrave; chuyển bộ chứng từ do nh&agrave; nhập khẩu chuyển&nbsp;sang để ng&acirc;n h&agrave;ng mở L/C xem x&eacute;t trả tiền.</p>\r\n\r\n<p><strong>Bước 7:</strong>&nbsp;Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh kiểm tra v&agrave; th&ocirc;ng b&aacute;o kết quả kiểm tra bộ chứng từ cho Người y&ecirc;u cầu</p>\r\n\r\n<p><strong>Bước 8:</strong>&nbsp;Người y&ecirc;u cầu (Nh&agrave; nhập khẩu) sau khi được th&ocirc;ng b&aacute;o về chứng từ nếu trường hợp chứng từ c&oacute; sự kh&aacute;c biệt đề nghị tu chỉnh hoặc chấp nhận thanh to&aacute;n đồng thời nhận bộ chứng từ để đi nhận h&agrave;ng.</p>\r\n\r\n<p><strong>Bước 9:</strong>&nbsp;Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh y&ecirc;u cầu Ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o thanh to&aacute;n tiền cho Nh&agrave; xuất khẩu (Người thụ hưởng)</p>\r\n\r\n<p><strong>Bước 10:</strong>&nbsp;Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh tiến h&agrave;nh ch&iacute;nh thức ghi c&oacute; trong t&agrave;i khoản của người hưởng lợi</p>\r\n\r\n<p>Mrs Jade.</p>','/6e/cc/c6/5a6a15411aa4e.jpg',NULL,23297,0,'2018-01-26 00:00:00',NULL,'chúng ta sẽ tìm hiểu các bước trong quy trình thanh toán theo phương pháp Tín dụng chứng từ hay còn gọi là LC (Letter of Credit)\r\n\r\nGhi chú:\r\n\r\n+ Importer/ the applicant: Nhà nhập khẩu/ người mở LC\r\n\r\n+ Exporter/ the beneficiary: Nhà xuất khẩu/ người hưởn','thanh toán quốc tế, tín dụng chứng từ là gì, quy trình thanh toán LC, các bước thanh toán LC, thủ tục làm LC, nội dung trong thư tín dụng LC','','',2,0,'vi','cac-buoc-trong-quy-trinh-thanh-toan-l-c',7,27,26,'2018-01-26 00:34:57','2018-03-03 13:33:48'),(224,'CÁC LOẠI HỐI PHIẾU TRONG THANH TOÁN QUỐC TẾ','','Để phân loại Hối phiếu trong thanh toán quốc tế người ta căn cứ vào thời gian thanh toán, chứng từ đi kèm hay người ký phát hối phiếu. Chúng ta hãy cùng đi sâu tìm hiểu các loại hối phiếu dưới đây:\r\n• Hối phiếu thương mại:\r\n\r\nĐây là Hối phiếu do người bán kí phát đòi tiền ng mua khi người bán đã hoàn thành nghĩa vụ quy định trong Hợp đồng ngoại thương.\r\n\r\n• Hối phiếu ngân hàng:','<p>Để&nbsp;<strong>ph&acirc;n loại Hối phiếu trong thanh to&aacute;n quốc tế</strong>&nbsp;người ta căn cứ v&agrave;o thời gian thanh to&aacute;n, chứng từ đi k&egrave;m hay người k&yacute; ph&aacute;t hối phiếu. Ch&uacute;ng ta h&atilde;y c&ugrave;ng đi s&acirc;u t&igrave;m hiểu c&aacute;c loại hối phiếu dưới đ&acirc;y:</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/12.%20Cac%20loai%20hoi%20phieu/BillOfExchange.jpg\" style=\"height:665px; width:935px\" /></p>\r\n\r\n<h2><strong>*Căn cứ v&agrave;o thời hạn thanh to&aacute;n</strong></h2>\r\n\r\n<h3>&bull; Hối phiếu trả ngay:</h3>\r\n\r\n<p>Hối phiếu trả ngay l&agrave; Hối phiếu quy định Người bị k&iacute; ph&aacute;t phải trả tiền ngay khi Hối phiếu được xuất tr&igrave;nh. Tuy nhi&ecirc;n &ldquo;trả ngay&rdquo; ở đ&acirc;y c&ograve;n phụ thuộc v&agrave;o tập qu&aacute;n thanh to&aacute;n (đ&ocirc;i khi l&agrave; trả v&agrave;o ng&agrave;y kế tiếp của ng&agrave;y xuất tr&igrave;nh).</p>\r\n\r\n<h3>&bull; Hối phiếu c&oacute; k&igrave; hạn:</h3>\r\n\r\n<p>Hối phiếu c&oacute; k&igrave; hạn quy định người bị k&iacute; ph&aacute;t trả tiền khi Hối phiếu đến hạn thanh to&aacute;n ghi tr&ecirc;n Hối phiếu (Nếu quy định thời hạn trả chậm Hối phiếu l&agrave; X ng&agrave;y kể từ ng&agrave;y xuất tr&igrave;nh th&igrave; ng&agrave;y trả tiền l&agrave; X ng&agrave;y t&iacute;nh từ ng&agrave;y Người bị k&iacute; ph&aacute;t chấp nhận Hối phiếu trả đi)</p>\r\n\r\n<h2><strong>*Căn cứ v&agrave;o chứng từ đi k&egrave;m</strong></h2>\r\n\r\n<h3>&bull; Hối phiếu trơn:</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; loại Hối phiếu m&agrave; việc trả tiền chỉ dựa tr&ecirc;n Hối phiếu, kh&ocirc;ng dựa v&agrave;o chứng từ k&egrave;m theo. Trong TTQT, loại Hối phiếu n&agrave;y thường d&ugrave;ng để thu tiền cước ph&iacute; vận tải, bảo hiểm, hoa hồng&hellip;hoặc được d&ugrave;ng để đ&ograve;i tiền mua h&agrave;ng của nh&agrave; nhập khẩu tin cậy.</p>\r\n\r\n<h3>&bull; Hối phiếu k&egrave;m chứng từ:</h3>\r\n\r\n<p>Loại hối phiếu n&agrave;y được d&ugrave;ng khi việc trả tiền dựa tr&ecirc;n cả Hối phiếu v&agrave; chứng từ k&egrave;m theo. Loại Hối phiếu n&agrave;y thường được sử dụng l&agrave;m phương tiện đ&ograve;i tiền của phương thức thanh to&aacute;n k&egrave;m chứng từ.</p>\r\n\r\n<h2><strong>*Căn cứ v&agrave;o t&iacute;nh chuyển nhượng</strong></h2>\r\n\r\n<h3>&bull; Hối phiếu đ&iacute;ch danh:</h3>\r\n\r\n<p>Hối phiếu đ&iacute;ch danh sẽ ghi r&otilde; t&ecirc;n người thụ hưởng, kh&ocirc;ng thể chuyển nhượng bằng c&aacute;ch k&iacute; hậu. Loại Hối phiếu n&agrave;y &iacute;t được sử dụng trong TTQT v&igrave; thường muốn thu tiền ng nước ngo&agrave;i th&igrave; phải chuyển nhượng Hối phiếu cho Ng&acirc;n h&agrave;ng</p>\r\n\r\n<h3>&bull; Hối phiếu theo lệnh:</h3>\r\n\r\n<p>Loại hối phiếu n&agrave;y sẽ ghi r&otilde; t&ecirc;n người thụ hưởng k&egrave;m từ &ldquo;Theo Lệnh&rdquo;, c&oacute; thể chuyển nhượng dễ d&agrave;ng bằng c&aacute;ch k&iacute; hậu, do đ&oacute; được sử dụng rộng r&atilde;i trong thanh to&aacute;n.</p>\r\n\r\n<h2><strong>*Căn cứ v&agrave;o người k&iacute; ph&aacute;t Hối phiếu</strong></h2>\r\n\r\n<h3>&bull; Hối phiếu thương mại:</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; Hối phiếu do người b&aacute;n k&iacute; ph&aacute;t đ&ograve;i tiền ng mua khi người b&aacute;n đ&atilde; ho&agrave;n th&agrave;nh nghĩa vụ quy định trong Hợp đồng ngoại thương.</p>\r\n\r\n<h3>&bull; Hối phiếu ng&acirc;n h&agrave;ng:</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; hối phiếu do Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh ra lệnh cho Ng&acirc;n h&agrave;ng đại l&iacute; của m&igrave;nh tr&iacute;ch 1 số tiền nhất định từ t&agrave;i khoản của Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh Hối phiếu để trả cho người thụ hưởng chỉ định tr&ecirc;n Hối phiếu.</p>\r\n\r\n<h2><strong>*Căn cứ v&agrave;o trạng th&aacute;i chấp nhận</strong></h2>\r\n\r\n<p>&bull; Hối phiếu chưa được k&iacute; chấp nhận<br />\r\n&bull; Hối phiếu đ&atilde; được người bị k&iacute; ph&aacute;t k&iacute; chấp nhận: c&oacute; 2 loại Hối phiếu chấp nhận (1) Chấp phiếu thương mại, (2) Chấp phiếu ng&acirc;n h&agrave;ng</p>\r\n\r\n<h2><strong>*Căn cứ v&agrave;o loại tiền ghi tr&ecirc;n Hối phiếu</strong></h2>\r\n\r\n<p>&bull; Hối phiếu ngoại tệ<br />\r\n&bull; Hối phiếu nội tệ</p>\r\n\r\n<h2><strong>*Căn cứ v&agrave;o cơ sở h&igrave;nh th&agrave;nh Hối phiếu</strong></h2>\r\n\r\n<p>&bull; Hối phiếu khống: Hối phiếu được lập m&agrave; kh&ocirc;ng dựa tr&ecirc;n giao dịch hợp đồng cơ sở n&agrave;o.<br />\r\n&bull; Hối phiếu thực: Hối phiếu thương mại (dựa tr&ecirc;n HĐ thương mại), Hối phiếu ng&acirc;n h&agrave;ng (dựa tr&ecirc;n HĐ cung ứng dịch vụ&nbsp;chuyển tiền k&iacute; kết giữa Ng&acirc;n h&agrave;ng v&agrave; người y&ecirc;u cầu chuyển tiền).</p>\r\n\r\n<p>Mrs Jade.</p>','/d9/25/5d/5a6a15a00ee8c.jpg',NULL,3828,0,'2018-01-26 00:00:00',NULL,'Để phân loại Hối phiếu trong thanh toán quốc tế người ta căn cứ vào thời gian thanh toán, chứng từ đi kèm hay người ký phát hối phiếu. Chúng ta hãy cùng đi sâu tìm hiểu các loại hối phiếu dưới đây:\r\n• Hối phiếu thương mại:\r\n\r\nĐây là Hối phiếu do người bán','các loại hối phiếu, thanh toán quốc tế, hối phiếu trả ngay, hối phiếu có kỳ hạn, hối phiếu trơn, hối phiếu kèm chứng từ, hối phiếu đích danh, hối phiếu theo lệnh, hối phiếu thương mại, hối phiếu ngân hàng, hối phiếu khống, hối phiếu thực','','',2,0,'vi','cac-loai-hoi-phieu-trong-thanh-toan-quoc-te',7,27,26,'2018-01-26 00:36:32','2018-03-03 13:35:03'),(225,'CÁC MỐC THỜI GIAN QUAN TRỌNG TRONG L/C','','Khi chúng ta thực hiện đơn yêu cầu mở LC, đến đoạn thực hiện điền vào các mốc thời gian chắc hẳn không ít bạn băn khoăn. Hiểu được tâm tư đó, hôm nay CLB HAN EXIM sẽ giúp bạn nắm được ba mốc quan trọng nhất trong thư tín dụng LC','<p>Khi ch&uacute;ng ta thực hiện đơn y&ecirc;u cầu mở LC, đến đoạn thực hiện điền v&agrave;o c&aacute;c mốc thời gian chắc hẳn kh&ocirc;ng &iacute;t bạn băn khoăn. Hiểu được t&acirc;m tư đ&oacute;, h&ocirc;m nay CLB HAN EXIM sẽ gi&uacute;p bạn nắm được ba mốc quan trọng nhất trong thư t&iacute;n dụng LC</p>\r\n\r\n<p><strong>Mốc thứ nhất đ&oacute; l&agrave; ng&agrave;y mở LC</strong></p>\r\n\r\n<p>Ng&agrave;y mở L/C l&agrave; ng&agrave;y bắt đầu ph&aacute;t sinh cam kết của Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh đối với Người hưởng lợi L/C, l&agrave; ng&agrave;y bắt đầu t&iacute;nh thời hạn hiệu lực của L/C v&agrave; cuối c&ugrave;ng l&agrave; căn cứ để Người Xuất khẩu kiểm tra xem Người Nhập khẩu thực hiện việc mở L/C c&oacute; đ&uacute;ng hạn như quy định trong hợp đồng ngoại thương hay kh&ocirc;ng.</p>\r\n\r\n<p><strong>Mốc thứ hai l&agrave; ng&agrave;y hết hạn LC</strong></p>\r\n\r\n<p>L&agrave; ng&agrave;y kết th&uacute;c thời hạn hiệu lực của LC. Nếu chứng từ xuất tr&igrave;nh sau ng&agrave;y hết hạn LC th&igrave; sẽ kh&ocirc;ng c&oacute; gi&aacute; trị thanh to&aacute;n. Mốc thời gian n&agrave;y cần được xem x&eacute;t thật kỹ v&agrave; tốt nhất cần căn ke sau ng&agrave;y giao h&agrave;ng dự kiến một khoảng thời gian hợp l&yacute;.</p>\r\n\r\n<p><strong>Mốc thứ ba thời hạn giao h&agrave;ng</strong></p>\r\n\r\n<p>Thời hạn giao h&agrave;ng được ghi trong L/C v&agrave; do hợp đồng mua b&aacute;n quy định, c&oacute; mối quan hệ chặt chẽ với thời hạn hiệu của L/C. Nếu hai b&ecirc;n thỏa thuận thời hạn giao h&agrave;ng được gia hạn th&igrave; ng&acirc;n h&agrave;ng mở thư t&iacute;n dụng cũng chấp thuận để thời hạn hiệu lực k&eacute;o d&agrave;i th&ecirc;m một khoảng thời gian tương ứng.</p>\r\n\r\n<p><strong>Mốc thứ &nbsp;tư l&agrave; thời gian trả tiền</strong></p>\r\n\r\n<p>Thời hạn trả tiền c&oacute; thể nằm trong thời hạn hiệu lực của LC hoặc c&oacute; thể nằm ngo&agrave;i thời hạn hiệu lực của LC nếu l&agrave; trả tiền sau, c&oacute; kỳ hạn.</p>\r\n\r\n<p>Giữa c&aacute;c mốc thời gian tr&ecirc;n ch&uacute;ng ta cần lưu &yacute;:</p>\r\n\r\n<ul>\r\n	<li>Ngày giao hàng phải nằm trong thờ hạn hi&ecirc;̣u lực của L/C và kh&ocirc;ng được trùng vói ngày h&ecirc;́t hạn hi&ecirc;̣u lực của L/C</li>\r\n	<li>Ngày phát hành L/C phải trước ngày giao hàng m&ocirc;̣t thời gian hợp lý, kh&ocirc;ng được trùng với ngày giao hàng. Thời gian hợp lí này được tính t&ocirc;́i thi&ecirc;̉u bằng t&ocirc;̉ng s&ocirc;́ của s&ocirc;́ ngày c&acirc;̀n phải có đ&ecirc;̉ th&ocirc;ng báo mở L/C, s&ocirc;́ ngày lưu L/C ở Ng&acirc;n hàng th&ocirc;ng báo , s&ocirc;́ ngày chu&acirc;̉n bị hàng đ&ecirc;̉ giao cho Người nh&acirc;̣p kh&acirc;̉u</li>\r\n	<li>Ngày h&ecirc;́t hạn hi&ecirc;̣u lực của L/C phải sau ngày giao hàng m&ocirc;̣t thời gian hợp lí. Thời gian này bao g&ocirc;̀m s&ocirc;́ ngày l&acirc;̣p b&ocirc;̣ chứng từ, s&ocirc;́ ngày v&acirc;̣n chuy&ecirc;̉n chứng từ đ&ecirc;́n Ng&acirc;n hàng phát hành L/C hoặc đ&ecirc;́n địa đi&ecirc;̉m xu&acirc;́t trình chứng từ quy định trong L/C, s&ocirc;́ ngày lưu giữ chứng từ tại Ng&acirc;n hàng th&ocirc;ng báo và 7 ngày Ng&acirc;n hàng đ&ecirc;̉ Ng&acirc;n hàng ki&ecirc;̉m tra chứng từ</li>\r\n	<li>Nếu kh&ocirc;ng quy định thời hạn xuất tr&igrave;nh của L/C th&igrave; thời hạn xuất tr&igrave;nh được xem là ngày h&ecirc;́t hạn thanh toán hoặc thương lượng thanh toán.</li>\r\n</ul>\r\n\r\n<p>Ch&uacute;ng ta c&oacute; thể tham khảo th&ecirc;m trong ICC c&oacute; quy định r&otilde; r&agrave;ng về c&aacute;c mốc thời gian tr&ecirc;n.</p>','/b3/11/1b/5a6a15f85e854.jpg',NULL,10295,0,'2018-01-26 00:00:00',NULL,'Mốc thứ nhất đó là ngày mở LC: Ngày mở L/C là ngày bắt đầu phát sinh cam kết của Ngân hàng phát hành đối với Người hưởng lợi L/C, là ngày bắt đầu tính thời hạn hiệu lực của L/C và cuối cùng là căn cứ để Người Xuất khẩu kiểm tra xem Người Nhập khẩu thực hi','những chú ý khi mở LC, các mốc thời gian quan trọng khi mở LC, ngày mở LC, ngày hết hạn LC, thời gian thanh toán, thư tín dụng LC, thanh toán quốc tế','','',2,0,'vi','cac-moc-thoi-gian-quan-trong-trong-l-c',7,27,26,'2018-01-26 00:38:00','2018-03-03 13:36:18'),(226,'CÁC NỘI DUNG TRONG THƯ TÍN DỤNG','','Thư tín dụng là gì?\r\n\r\nThư tín dụng (L/C) là một chứng thư do Ngân hàng phát hành mở theo yêu cầu của Người NK (Người yêu cầu mở thư tín dụng) nhằm nêu lên những cam kết trả tiền cho Người XK (Người hưởng lợi) với điều kiện người này phải thực hiện đúng và đầy đủ những điều khoản và điều kiện quy định trong lá thư đó (hoặc người này phải xuất trình được những chứng từ phù hợp).','<h2>Thư t&iacute;n dụng l&agrave; g&igrave;?</h2>\r\n\r\n<p>Thư t&iacute;n dụng (L/C) l&agrave; một chứng thư do Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh mở theo y&ecirc;u cầu của Người NK (Người y&ecirc;u cầu mở thư t&iacute;n dụng) nhằm n&ecirc;u l&ecirc;n những cam kết trả tiền cho Người XK (Người hưởng lợi) với điều kiện người n&agrave;y phải thực hiện đ&uacute;ng v&agrave; đầy đủ những điều khoản v&agrave; điều kiện quy định trong l&aacute; thư đ&oacute; (hoặc người n&agrave;y phải xuất tr&igrave;nh được những chứng từ ph&ugrave; hợp).</p>\r\n\r\n<h2>T&iacute;nh chất cơ bản của L/C:</h2>\r\n\r\n<p>+ L/C độc lập với hợp đồng cơ sở.</p>\r\n\r\n<p>+ L/C chỉ giao dịch bằng chứng từ v&agrave; thanh to&aacute;n chỉ căn cứ v&agrave;o chứng từ.</p>\r\n\r\n<p>+ L/C y&ecirc;u cầu tu&acirc;n thủ chặt chẽ của bộ chứng từ.</p>\r\n\r\n<p>+ L/C l&agrave; c&ocirc;ng cụ thanh to&aacute;n, hạn chế rủi ro; Tuy nhi&ecirc;n, cũng c&oacute; thể l&agrave; c&ocirc;ng cụ từ chối thanh to&aacute;n v&agrave; lừa đảo.</p>\r\n\r\n<h2>Nội dung chủ yếu của thư t&iacute;n dụng bao gồm:</h2>\r\n\r\n<p>Lưu &yacute;: Để kiểm tra nội dung của LC người ta sẽ dựa v&agrave;o Hợp đồng ngoại thương do b&ecirc;n Nhập khẩu v&agrave; b&ecirc;n Xuất khẩu k&yacute; kết:</p>\r\n\r\n<p>(1) Số hiệu L/C</p>\r\n\r\n<p>(2) Địa điểm ph&aacute;t h&agrave;nh L/C</p>\r\n\r\n<p>(3) Ng&agrave;y mở L/C</p>\r\n\r\n<p>(4) Loại L/C</p>\r\n\r\n<p>(5) T&ecirc;n, địa chỉ của những người c&oacute; li&ecirc;n quan</p>\r\n\r\n<p>(6) Số tiền của L/C</p>\r\n\r\n<p>(7) Thời hạn hiệu lực của L/C</p>\r\n\r\n<p>(8) Thời hạn xuất tr&igrave;nh chứng từ</p>\r\n\r\n<p>(9) Thời hạn trả tiền của L/C</p>\r\n\r\n<p>(10) Thời hạn giao h&agrave;ng</p>\r\n\r\n<p>(11) Điều khoản về h&agrave;ng h&oacute;a</p>\r\n\r\n<p>(12) Điều khoản về vận tải, giao nhận h&agrave;ng h&oacute;a</p>\r\n\r\n<p>(13) C&aacute;c chứng từ Người XK phải xuất tr&igrave;nh</p>\r\n\r\n<p>(14) Sự cam kết trả tiền của NH ph&aacute;t h&agrave;nh L/C</p>\r\n\r\n<p>(15) Những điều khoản đặc biệt kh&aacute;c</p>\r\n\r\n<p>(16) Chữ k&yacute; của NH ph&aacute;t h&agrave;nh</p>\r\n\r\n<p>Bạn c&oacute; muốn được tự tay m&igrave;nh ho&agrave;n th&agrave;nh bộ hồ sơ thanh to&aacute;n LC qua c&aacute;c Ng&acirc;n h&agrave;ng tại Việt Nam? H&atilde;y đến với CLB HAN EXIM để được tham gia c&aacute;c lớp Huấn luyện nghiệp vụ kh&ocirc;ng chỉ về Thanh to&aacute;n quốc tế m&agrave; c&ograve;n về c&aacute;c nghiệp vụ Xuất nhập khẩu kh&aacute;c.</p>','/1a/f2/21/5a6a1663c58e3.jpg',NULL,1666,0,'2018-01-26 00:00:00',NULL,'Thư tín dụng là gì?\r\n\r\nThư tín dụng (L/C) là một chứng thư do Ngân hàng phát hành mở theo yêu cầu của Người NK (Người yêu cầu mở thư tín dụng) nhằm nêu lên những cam kết trả tiền cho Người XK (Người hưởng lợi) với điều kiện người này phải thực hiện đúng v','thư tín dụng LC, nội dung trong thư tín dụng LC, tính chất cơ bản của LC, thanh toán quốc tế','','',2,0,'vi','cac-noi-dung-trong-thu-tin-dung',7,27,26,'2018-01-26 00:39:47','2018-03-03 13:37:00'),(227,'CÁCH LẬP HỐI PHIẾU BẰNG DA/DP','','Hối phiếu do người bán (nhà xuất khẩu) lập, nó có thể được viết bằng tay hay đánh máy. Đương nhiên, với sự phát triển của công nghệ hiện nay thì chúng ta sẽ sử Soạn Hối phiếu bằng Word hoặc Excel, hãy cùng khởi động 2 phần mềm này để lập Hối phiếu','<p>Trong c&aacute;c phần trước, ch&uacute;ng ta đ&atilde; c&ugrave;ng t&igrave;m hiểu về Hối phiếu trong Thanh to&aacute;n quốc tế. Trong b&agrave;i viết n&agrave;y, Jade sẽ hướng dẫn c&aacute;c bạn c&aacute;ch soạn một Hối phiếu theo đ&uacute;ng chuẩn.</p>\r\n\r\n<p>Hối phiếu do người b&aacute;n (nh&agrave; xuất khẩu) lập, n&oacute; c&oacute; thể được viết bằng tay hay đ&aacute;nh m&aacute;y. Đương nhi&ecirc;n, với sự ph&aacute;t triển của c&ocirc;ng nghệ hiện nay th&igrave; ch&uacute;ng ta sẽ sử Soạn Hối phiếu bằng Word hoặc Excel, h&atilde;y c&ugrave;ng khởi động 2 phần mềm n&agrave;y để lập Hối phiếu với Jade.</p>\r\n\r\n<h2>Y&ecirc;u cầu chung về h&igrave;nh thức của Hối phiếu</h2>\r\n\r\n<p>&ndash; Phải l&agrave;m bằng văn bản<br />\r\n&ndash; H&igrave;nh mẫu kh&ocirc;ng ảnh hưởng đến gi&aacute; trị ph&aacute;p l&iacute; của Hối phiếu: c&oacute; thể viết tay, đ&aacute;nh m&aacute;y, in sẵn, n&ecirc;n tr&aacute;nh viết v&agrave; in bằng b&uacute;t dễ phai, mực đỏ.<br />\r\n&ndash; Số bản: 1 hoặc nhiều hơn 1 (đều l&agrave; bản gốc)</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/h%E1%BB%91i%20phi%E1%BA%BFu.jpg\" /></p>\r\n\r\n<h3>i. Ti&ecirc;u đề Hối phiếu (1)</h3>\r\n\r\n<p>Luật ULB1930, Luật VN: Bắt buộc phải ghi (c&ugrave;ng thứ tiếng lập Hối phiếu), kh&ocirc;ng ghi th&igrave; Hối phiếu v&ocirc; hiệu (nhưng theo luật Anh, Mỹ th&igrave; kh&ocirc;ng cần ghi miễn trong nội dung c&oacute; diễn đạt Exchange for&hellip;)</p>\r\n\r\n<p>=&gt; Trong thực tế thường ghi&nbsp;<strong><em>Bill of Exchange</em></strong>&nbsp;hoặc&nbsp;<strong><em>Draft</em></strong></p>\r\n\r\n<h3>ii. Số hiệu Hối phiếu (No&hellip;)</h3>\r\n\r\n<p>&ndash; C&aacute;c Hối phiếu sẽ được ghi số để dễ d&agrave;ng khi dẫn chiếu<br />\r\n&ndash; Số hiệu Hối phiếu do người k&iacute; ph&aacute;t đặt ra (nếu d&ugrave;ng trong L/C th&igrave; thường l&agrave; số hiệu quy định trong L/C)<br />\r\n&ndash; Đ&acirc;y l&agrave; nội dung kh&ocirc;ng bắt buộc, kh&ocirc;ng l&agrave;m ảnh hưởng gi&aacute; trị ph&aacute;p l&iacute; của Hối phiếu</p>\r\n\r\n<h3>iii. Một lệnh thanh to&aacute;n v&ocirc; điều kiện một số tiền x&aacute;c định (2)</h3>\r\n\r\n<p>&bull; V&ocirc; điều kiện<br />\r\n&ndash; Hối phiếu l&agrave; mệnh lệnh, kh&ocirc;ng phải y&ecirc;u cầu<br />\r\n&ndash; Người k&iacute; ph&aacute;t khi đưa ra lệnh th&igrave; kh&ocirc;ng k&egrave;m theo điều kiện hay l&iacute; do n&agrave;o<br />\r\n&ndash; Người bị k&iacute; ph&aacute;t c&oacute; thể thanh to&aacute;n/ chấp nhận thanh to&aacute;n hoặc từ chối thanh to&aacute;n (tuy nhi&ecirc;n việc thanh to&aacute;n/ chấp nhận thanh to&aacute;n l&agrave; v&ocirc; điều kiện)<br />\r\n&bull; Số tiền ghi tr&ecirc;n Hối phiếu: N&ecirc;n ghi bằng số v&agrave; ghi bằng chữ k&egrave;m theo đơn vị tiền tệ<br />\r\n&ndash; Luật CCCCN Trung Quốc v&agrave; ISBP: Phải đồng thời ghi bằng số v&agrave; ghi bằng chữ, nếu c&oacute; sự sai kh&aacute;c th&igrave; Hối phiếu sẽ v&ocirc; hiệu<br />\r\n&ndash; Theo ULB 1930: kh&ocirc;ng bắt buộc phải ghi cả chữ v&agrave; số, c&oacute; thể ghi bằng chữ hoặc số. C&ograve;n cho ph&eacute;p ghi nhiều hơn một lần bằng chữ hoặc nhiều hơn một lần bằng số.<br />\r\n+ Nếu c&oacute; sự kh&aacute;c nhau giữa c&aacute;c số tiền ghi bằng chữ hoặc giữa c&aacute;c số tiền ghi bằng số =&gt; chọn số tiền nhỏ hơn để thanh to&aacute;n<br />\r\n+ C&oacute; sự sai kh&aacute;c giữa số tiền ghi số v&agrave; ghi chữ =&gt; bằng chữ<br />\r\n&ndash; Luật Anh, Mỹ: nếu c&oacute; sai kh&aacute;c giữa số v&agrave; chữ =&gt; bằng chữ<br />\r\n&ndash; Luật CCCN 2005:<br />\r\n+ Phải ghi cả bằng chữ v&agrave; bằng số<br />\r\n+ Nếu c&oacute; sai kh&aacute;c giữa chữ v&agrave; số =&gt; bằng chữ<br />\r\n+ Nếu số tiền bằng số/ hoặc bằng chữ nhiều lần m&agrave; kh&ocirc;ng tr&ugrave;ng nhau =&gt; thanh to&aacute;n số tiền nhỏ hơn.</p>\r\n\r\n<p>iv. Địa điểm v&agrave; thời gian k&iacute; ph&aacute;t (7)</p>\r\n\r\n<p>Địa điểm: Hối phiếu được lập ở đ&acirc;u th&igrave; ghi địa điểm ở đ&oacute;. Đối với Hối phiếu quốc tế, địa điểm k&iacute; ph&aacute;t c&oacute; &yacute; nghĩa quan trọng để suy ra nguồn luật điều chỉnh Hối phiếu.</p>\r\n\r\n<p>&ndash; Nơi lập hối phiếu: ở nước người ph&aacute;t h&agrave;nh hối phiếu (người xuất khẩu)</p>\r\n\r\n<p>&ndash; Ng&agrave;y lập hối phiếu: kh&ocirc;ng được sớm hơn ng&agrave;y lập h&oacute;a đơn, kh&ocirc;ng sớm hơn ng&agrave;y mở L/C v&agrave; nằm trong thời gian hiệu lực của L/C.</p>\r\n\r\n<h3><strong>v. Thời hạn Thanh to&aacute;n (4)</strong></h3>\r\n\r\n<h4>&ndash; Thanh to&aacute;n ngay:</h4>\r\n\r\n<p>+ Payable at sight: Thanh to&aacute;n ngay</p>\r\n\r\n<p>+ Payable on presentation: Thanh to&aacute;n khi xuất tr&igrave;nh Hối phiếu</p>\r\n\r\n<p>+ Payable on demand: Thanh to&aacute;n khi c&oacute; y&ecirc;u cầu</p>\r\n\r\n<h4>&ndash; Thanh to&aacute;n sau:</h4>\r\n\r\n<p>+ At Xdays after sight of this&hellip;:&nbsp;Thanh to&aacute;n tại 1 ng&agrave;y nhất định sau ng&agrave;y k&yacute; ph&aacute;t hối phiếu</p>\r\n\r\n<p>+ At Xdays after acceptance: Thanh to&aacute;n v&agrave;o 1 ng&agrave;y sau khi được chấp nhận Hối hiếu</p>\r\n\r\n<p>+ On the future fixed day: &nbsp;Thanh to&aacute;n 1 ng&agrave;y nhất định trong tương lai</p>\r\n\r\n<p>+ At Xdays after BL date of this&hellip;: Thanh to&aacute;n tại 1 ng&agrave;y nhất định sau ng&agrave;y k&yacute; vận đơn</p>\r\n\r\n<p>+ At Xdays after shipment date of this: Thanh to&aacute;n 1 ng&agrave;y nhất định sau ng&agrave;y giao h&agrave;ng</p>\r\n\r\n<h3>vi. T&ecirc;n người thụ hưởng (6)</h3>\r\n\r\n<p>T&ecirc;n người thụ hưởng c&oacute; thể l&agrave; người k&iacute; ph&aacute;t, hoặc người kh&aacute;c người k&iacute; ph&aacute;t chỉ định, hoặc bất k&igrave; người n&agrave;o được chuyển nhượng bằng c&aacute;ch k&iacute; hậu, hoặc người cầm phiếu. (đ&iacute;ch danh, theo lệnh, trao tay)</p>\r\n\r\n<h3>vii. T&ecirc;n (3) v&agrave; địa chỉ của người bị k&iacute; ph&aacute;t (5)</h3>\r\n\r\n<p>&ndash; L&agrave; ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh L/C nếu thanh to&aacute;n bằng L/C<br />\r\n&ndash; L&agrave; nh&agrave; Nhập khẩu nếu thanh to&aacute;n bằng phương thức kh&aacute;c (mở sổ, nhờ thu&hellip;)<br />\r\n&ndash; Phải ghi địa điểm thanh to&aacute;n nếu kh&ocirc;ng sẽ thanh to&aacute;n tại địa chỉ của người bị k&iacute; ph&aacute;t</p>\r\n\r\n<h3>viii. T&ecirc;n v&agrave; địa chỉ của người k&iacute; ph&aacute;t (8)</h3>\r\n\r\n<p>Phải ghi t&ecirc;n v&agrave; địa chỉ của người k&iacute; ph&aacute;t v&igrave; người k&iacute; ph&aacute;t sẽ l&agrave; người cuối c&ugrave;ng thanh to&aacute;n cho người thụ hưởng trong trường hợp Hối phiếu đ&atilde; chuyển nhượng nhưng Người bị k&iacute; ph&aacute;t từ chối thanh to&aacute;n/kh&ocirc;ng chấp nhận.</p>\r\n\r\n<h3>ix. Chữ k&iacute; người k&iacute; ph&aacute;t (8)</h3>\r\n\r\n<p>Chữ k&yacute; của người k&yacute; ph&aacute;t muốn c&oacute; hiệu lực phải l&agrave; chữ k&yacute; của người c&oacute; đủ năng lực h&agrave;nh vi v&agrave; năng lực ph&aacute;p l&yacute;. Chữ k&yacute; phải được ghi ở g&oacute;c dưới b&ecirc;n phải của tờ hối phiếu v&agrave; người k&yacute; ph&aacute;t hối phiếu phải k&yacute; t&ecirc;n bằng chữ k&yacute; th&ocirc;ng dụng trong giao dịch. C&aacute;c chữ k&yacute; dưới dạng in, photocopy v&agrave; đ&oacute;ng dấu&hellip;m&agrave; kh&ocirc;ng phải viết tay đều kh&ocirc;ng c&oacute; gi&aacute; trị ph&aacute;p l&yacute;</p>\r\n\r\n<p>Ch&uacute;ng ta c&ugrave;ng tham khảo 2 mẫu Hối phiếu sau đ&acirc;y được lập bằng file Word</p>\r\n\r\n<p>Hối phiếu trong Phương thức Nhờ thu</p>\r\n\r\n<p>Hối phiếu trong phương thức t&iacute;n dụng chứng từ.</p>\r\n\r\n<p>Mrs Jade.</p>','/1f/d2/21/5a6a16c7de313.jpg',NULL,16098,0,'2018-01-26 00:00:00',NULL,'Hối phiếu do người bán (nhà xuất khẩu) lập, nó có thể được viết bằng tay hay đánh máy. Đương nhiên, với sự phát triển của công nghệ hiện nay thì chúng ta sẽ sử Soạn Hối phiếu bằng Word hoặc Excel, hãy cùng khởi động 2 phần mềm này để lập Hối phiếu','cách lập hối phiếu, Bill of Exchange là gì, ký phát hối phiếu, hối phiếu thanh toán ngay, hối phiếu thanh toán sau, thanh toán quốc tế','','',2,0,'vi','cach-lap-hoi-phieu-bang-da-dp',7,27,26,'2018-01-26 00:41:27','2018-03-03 13:39:43'),(228,'CÁC ĐẶC ĐIỂM CỦA HỐI PHIẾU TRONG THANH TOÁN QUỐC TẾ','','\"Hối phiếu là mệnh lệnh vô điều kiện của 1 người kí phát (Drawer) cho 1 người khác (Drawer), yêu cầu người này khi nhìn thấy hối phiếu hoặc 1 ngày cụ thể nhất định hoặc đến 1 ngày có thể xác định trong tương lai phải trả 1 số tiền nhất định cho 1 người nào đó hoặc theo lệnh của người này trả cho người khác hoặc trả cho người cầm Hối phiếu.\"','<h2><strong>1. Kh&aacute;i niệm</strong></h2>\r\n\r\n<p>&bull; Theo đạo luật hối phiếu Anh 1882: &ldquo;<strong>Hối phiếu</strong>&nbsp;l&agrave; mệnh lệnh v&ocirc; điều kiện của 1 người k&iacute; ph&aacute;t (Drawer) cho 1 người kh&aacute;c (Drawer), y&ecirc;u cầu người n&agrave;y khi nh&igrave;n thấy&nbsp;<strong>hối phiếu</strong>&nbsp;hoặc 1 ng&agrave;y cụ thể nhất định hoặc đến 1 ng&agrave;y c&oacute; thể x&aacute;c định trong tương lai phải trả 1 số tiền nhất định cho 1 người n&agrave;o đ&oacute; hoặc theo lệnh của người n&agrave;y trả cho người kh&aacute;c hoặc trả cho người cầm Hối phiếu.&rdquo;</p>\r\n\r\n<p><br />\r\n&bull; Theo Luật C&ocirc;ng cụ chuyển nhượng của VN năm 2005 &ldquo;<strong>Hối phiếu đ&ograve;i nợ</strong>&nbsp;l&agrave; giấy tờ c&oacute; gi&aacute; do Người k&iacute; ph&aacute;t lập, y&ecirc;u cầu Người bị k&iacute; ph&aacute;t thanh to&aacute;n kh&ocirc;ng điều kiện 1 số tiền x&aacute;c định khi c&oacute; y&ecirc;u cầu hoặc v&agrave;o 1 thời điểm nhất định trong tương lai cho&nbsp;<em>Người thụ hưởng</em>&rdquo;.</p>\r\n\r\n<h2><strong>2. Đặc điểm hối phiếu:</strong></h2>\r\n\r\n<h3><em>* T&iacute;nh trừu tượng:</em></h3>\r\n\r\n<ul>\r\n	<li>HP đc lập dựa tr&ecirc;n c&aacute;c giao dịch hợp đồng cơ sở (Giao dịch cơ sở của HP thương mại l&agrave; HĐồng thương mại, của HP ng&acirc;n h&agrave;ng l&agrave; HĐ cung ứng d.vụ chuyển tiền k&iacute; kết giữa Ng&acirc;n h&agrave;ng v&agrave; ng y&ecirc;u cầu chuyển tiền) , nhưng HP n&agrave;y trở th&agrave;nh nghỉa vụ trả tiền độc lập với HĐ cơ sở.</li>\r\n	<li>Hiệu lực của HP kh&ocirc;ng phụ thuộc v&agrave;o nguy&ecirc;n nh&acirc;n sinh ra HP =&gt; HP khống bị luật c&aacute;c nước cấm ( HP khống l&agrave; HP kh&ocirc;ng được h&igrave;nh th&agrave;nh tr&ecirc;n giao dịch cơ sở)</li>\r\n</ul>\r\n\r\n<h3><em>*&nbsp;T&iacute;nh bắt buộc trả tiền</em></h3>\r\n\r\n<ul>\r\n	<li>Việc trả tiền của HP l&agrave; v&ocirc; điều kiện trừ khi HP k&iacute; ph&aacute;t sai luật.</li>\r\n	<li>Người k&iacute; ph&aacute;t l&agrave; người chịu tr&aacute;ch nhiệm cuối c&ugrave;ng trong việc thanh to&aacute;n cho ng thụ hưởng (nếu khi đ&atilde; chuyển nhượng m&agrave; bị người bị k&iacute; ph&aacute;t từ chối thanh to&aacute;n/chấp nhận)</li>\r\n</ul>\r\n\r\n<h3><em>* T&iacute;nh lưu th&ocirc;ng</em></h3>\r\n\r\n<ul>\r\n	<li>HP l&agrave; chứng từ c&oacute; gi&aacute;, c&oacute; t&iacute;nh trừu tượng, v&agrave; c&oacute; t&iacute;nh bắt buộc trả tiền n&ecirc;n HP c&oacute; thể lưu th&ocirc;ng.</li>\r\n	<li>HP c&oacute; thể d&ugrave;ng để thanh to&aacute;n tiền mua h&agrave;ng/trả nợ, chuyển nhượng, cầm cố, thế chấp, vay vốn, chiết khấu v&agrave; t&aacute;i chiết khấu.</li>\r\n</ul>\r\n\r\n<h3><em>*HP l&agrave; tr&aacute;i vụ 1 b&ecirc;n</em></h3>\r\n\r\n<ul>\r\n	<li>HP l&agrave; c&ocirc;ng cụ do người ph&aacute;t h&agrave;nh y&ecirc;u cầu ng bị k&iacute; ph&aacute;t thực hiện nghĩa vụ d&acirc;n sự trả tiền, v&igrave; vậy nghĩa vụ nh&acirc;n sự n&agrave;y c&oacute; được thực hiện hay kh&ocirc;ng phụ thuộc v&agrave;o sự chấp nhận của người bị k&iacute; ph&aacute;t.</li>\r\n</ul>\r\n\r\n<p>HP sẽ v&ocirc; hiệu nếu người bị k&iacute; ph&aacute;t từ chối thanh to&aacute;n 1 c&aacute;ch hợp ph&aacute;p hoặc bị ph&aacute; sản.</p>\r\n\r\n<p>Mrs Jade.</p>','/a2/e4/4a/5a6a170704e63.jpg',NULL,2305,0,'2018-01-26 00:00:00',NULL,'Hối phiếu là mệnh lệnh vô điều kiện của 1 người kí phát (Drawer) cho 1 người khác (Drawer), yêu cầu người này khi nhìn thấy hối phiếu hoặc 1 ngày cụ thể nhất định hoặc đến 1 ngày có thể xác định trong tương lai phải trả 1 số tiền nhất định cho 1 người này','đặc điểm của hối phiếu, hối phiếu là gì, cách lập hối phiếu, thanh toán quốc tế','','',2,0,'vi','cac-dac-diem-cua-hoi-phieu-trong-thanh-toan-quoc-te',7,27,26,'2018-01-26 00:42:31','2018-03-03 13:40:54'),(229,'PHÂN BIỆT DEMURRAGE VÀ DETENTION','','Phí demurrage và detention là hai loại phí thường gây lẫn lộn cho nhân viên xuất nhập khẩu. Làm sao để một nhân viên mới vào nghề phân biệt phí demurrage và detention cũng như áp dụng nó trong thực tế thành thạo như một nhân viên nhiều kinh nghiệm','<p><strong>Trong học phần về quy tr&igrave;nh xuất-nhập khẩu đường biển, ad c&oacute; hướng dẫn c&aacute;c em c&aacute;ch ph&acirc;n biệt&nbsp;<em>Ph&iacute; demurrage v&agrave; detention</em>&nbsp;cũng như c&aacute;ch l&agrave;m việc với h&atilde;ng t&agrave;u để thương lượng xin mức demurrage v&agrave; detention c&oacute; lợi cho m&igrave;nh. B&agrave;i viết n&agrave;y ad tổng hợp lại cho c&aacute;c em nội dung cần lưu &yacute; trong thực tế về&nbsp;<em>Ph&iacute; demurrage v&agrave; detention</em>&nbsp;để c&aacute;c em nắm r&otilde; hơn nh&eacute;.&nbsp;<em>Ph&iacute; demurrage v&agrave; detention</em>&nbsp;l&agrave; hai loại ph&iacute;&nbsp;thường g&acirc;y lẫn lộn cho nh&acirc;n vi&ecirc;n xuất nhập khẩu. L&agrave;m sao để một nh&acirc;n vi&ecirc;n mới v&agrave;o nghề&nbsp;ph&acirc;n biệt&nbsp;<em>ph&iacute; demurrage v&agrave; detentio</em>n cũng như &aacute;p dụng n&oacute; trong thực tế th&agrave;nh thạo&nbsp;như một nh&acirc;n vi&ecirc;n nhiều kinh nghiệm? H&atilde;y c&ugrave;ng CLB Y&ecirc;u&nbsp;Xuất Nhập Khẩu H&agrave; Nội HAN EXIM CLUB t&igrave;m hiểu về điều n&agrave;y nh&eacute;:</strong></p>\r\n\r\n<p>Trong những trường hợp h&agrave;ng h&oacute;a được vận chuyển bằng container nh&igrave;n chung th&igrave;:</p>\r\n\r\n<p><strong>Demurrage (lưu b&atilde;i):</strong>&nbsp;ph&iacute; lưu container h&agrave;ng tại b&atilde;i</p>\r\n\r\n<p><strong>Detention (lưu container):&nbsp;</strong>ph&iacute; lưu container tại kho của chủ h&agrave;ng</p>\r\n\r\n<p><strong><u>H&atilde;y c&ugrave;ng xem n&oacute; c&oacute; vai tr&ograve; g&igrave;?</u></strong></p>\r\n\r\n<p><strong>Nhập khẩu</strong>:</p>\r\n\r\n<p>Một container được dỡ khỏi t&agrave;u ng&agrave;y 02/07/2016&nbsp;sau đ&oacute; được hạ xuống b&atilde;i CY (<em>container yard</em>) chờ l&agrave;m thủ tục hải quan. Theo đ&uacute;ng ti&ecirc;u chuẩn th&igrave; sẽ c&oacute; 7 ng&agrave;y miễn ph&iacute; từ ng&agrave;y dỡ h&agrave;ng khỏi t&agrave;u, như vậy h&atilde;ng t&agrave;u sẽ t&iacute;nh ph&iacute;&nbsp;<strong>Demurrage</strong>&nbsp;bắt đầu từ ng&agrave;y 09/07/2016. Nếu ng&agrave;y 12/07 container mới ra khỏi b&atilde;i để giao cho người nhập khẩu th&igrave; H&atilde;ng t&agrave;u sẽ t&iacute;nh ph&iacute; Demurrage 4 ng&agrave;y t&iacute;nh từ ng&agrave;y 09/07/2016&nbsp;đến 12/07/2016.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/17.%20DEM%20DET/demurrage-full-container.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p>Sau khi container được nhận tại kho của chủ h&agrave;ng v&agrave; dỡ h&agrave;ng xong, th&ocirc;ng thường c&aacute;c h&atilde;ng t&agrave;u cho 7 ng&agrave;y miễn ph&iacute; để trả lại container rỗng. Sau ng&agrave;y n&agrave;y th&igrave; bắt đầu bị t&iacute;nh ph&iacute; lưu cont-&nbsp;<strong>Detention</strong></p>\r\n\r\n<p><strong>Xuất khẩu:</strong></p>\r\n\r\n<p>Trong trường hợp xuất khẩu, th&ocirc;ng thường h&atilde;ng t&agrave;u cung cấp cho khoảng 5 ng&agrave;y miễn ph&iacute; để nhận container rỗng, đ&oacute;ng h&agrave;ng v&agrave; chở ra B&atilde;i tập kết. Trong trường hợp chậm hơn 5 ng&agrave;y, h&atilde;ng t&agrave;u sẽ t&iacute;nh ph&iacute; lưu cont hay c&ograve;n gọi l&agrave; t&iacute;nh&nbsp;<strong>ph&iacute; Detention</strong>&nbsp;cho những ng&agrave;y m&agrave; kh&aacute;ch h&agrave;ng đang giữ cont c&oacute; thể l&agrave; full h&agrave;ng h&oacute;a hoặc l&agrave; cont rỗng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/17.%20DEM%20DET/detention-empty-container.jpg\" style=\"height:767px; width:987px\" /></p>\r\n\r\n<p>Một khi c&aacute;c container được trả lại b&atilde;i tập kết chờ l&ecirc;n t&agrave;u nhưng người gửi h&agrave;ng v&igrave; l&yacute; do n&agrave;o đ&oacute; như chậm l&agrave;m thủ tục hải quan h&agrave;ng xuất chẳng hạn khiến container kh&ocirc;ng l&ecirc;n t&agrave;u theo đ&uacute;ng kế hoạch. Điều n&agrave;y khiến container bị lưu tại b&atilde;i l&acirc;u hơn thời hạn được cho ph&eacute;p miễn ph&iacute; th&igrave; l&uacute;c n&agrave;y chủ h&agrave;ng xuất bị h&atilde;ng t&agrave;u t&iacute;nh<strong>&nbsp;ph&iacute; Demurrage.</strong></p>\r\n\r\n<p>Trong thực tế, đ&ocirc;i khi hai ph&iacute; n&agrave;y sẽ được gộp chung v&agrave;o thời gian Free time của h&atilde;ng t&agrave;u cho ph&eacute;p, v&iacute; dụ&nbsp;<strong>Free tim</strong>e 14 ng&agrave;y, điều n&agrave;y sẽ gi&uacute;p chủ h&agrave;ng được linh động thời gian. V&iacute; dụ 10 ng&agrave;y lưu b&atilde;i v&agrave; 3 ng&agrave;y lưu cont.</p>\r\n\r\n<p>Ad khuy&ecirc;n c&aacute;c bạn n&ecirc;n thương lượng với h&atilde;ng t&agrave;u (trong trường hợp trực tiếp book) hoặc đề nghị b&ecirc;n Forwarder để họ cho bạn thời gian DEM v&agrave; DET tối đa.</p>\r\n\r\n<p><strong>Với h&agrave;ng nhập: n&ecirc;n xin DEM &gt; DET</strong></p>\r\n\r\n<p><strong>Với h&agrave;ng xuất: n&ecirc;n xin DET&gt;DEM</strong></p>\r\n\r\n<p>C&ograve;n một loại ph&iacute; nữa l&agrave; Ph&iacute; lưu b&atilde;i (Storage Charges) do Quản l&yacute; Cảng vụ thu, ph&iacute; n&agrave;y đ&ocirc;i khi được gộp chung với DEM nhưng cũng c&oacute; trường hợp b&ecirc;n b&atilde;i Cảng thu ri&ecirc;ng của kh&aacute;ch v&agrave; ph&iacute; n&agrave;y cũng kh&ocirc;ng nhiều chỉ&nbsp;khoảng v&agrave;i chục ng&agrave;n đồng.</p>\r\n\r\n<p>Những g&igrave; Ad đ&atilde; đề cập ở tr&ecirc;n l&agrave; c&aacute;ch hiểu phổ biến nhất của việc sử dụng hai thuật ngữ n&agrave;y. Lựa chọn tốt nhất l&agrave; n&ecirc;n kiểm tra với c&aacute;c h&atilde;ng t&agrave;u để nắm r&otilde; hơn thời gian quy định n&agrave;y.</p>\r\n\r\n<p>Mrs Jade.</p>','/be/cd/db/5a6a178b4dbb8.jpg',NULL,2117,0,'2018-01-26 00:00:00',NULL,'Phí demurrage và detention là hai loại phí thường gây lẫn lộn cho nhân viên xuất nhập khẩu. Làm sao để một nhân viên mới vào nghề phân biệt phí demurrage và detention cũng như áp dụng nó trong thực tế thành thạo như một nhân viên nhiều kinh nghiệm.','Demurrage là gì? Detention là gì? Phí lưu cont rỗng, lưu cont tại bãi CY, lưu cont tại kho người xuất khẩu, nhập khẩu','','',2,0,'vi','phan-biet-demurrage-va-detention',6,27,26,'2018-01-26 00:44:43','2018-03-03 13:42:40'),(230,'Hướng dẫn soạn thảo và thiết kế Invoice, Packing list trên Excel chuyên nghiệp','','Đây là video, khổ!','<p>Ch&agrave;o c&aacute;c em,</p>\r\n\r\n<p>Chị đ&atilde; ho&agrave;n th&agrave;nh video hướng dẫn c&aacute;c em c&aacute;ch soạn thảo v&agrave; thiết kế, chỉnh sửa một file Invoice, Packing List tr&ecirc;n Excel chuẩn form. C&aacute;c em h&atilde;y v&agrave;o link b&ecirc;n dưới để tải file Invoice, Packing List mẫu về v&agrave; l&agrave;m theo nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>','/5d/ca/a5/5a6a1804b4b32.jpg',NULL,0,0,NULL,NULL,'','soạn thảo chứng từ xuất nhập khẩu, commercial invoice, packing list, cách soạn thảo chứng từ','','',-1,0,'vi','huong-dan-soan-thao-va-thiet-ke-invoice-packing-list-tren-excel-chuyen-nghiep',5,27,1,'2018-01-26 00:46:44','2018-09-23 00:51:23'),(231,'LÀM THẾ NÀO ĐỂ KHỞI NGHIỆP VỚI CÔNG TY XUẤT NHẬP KHẨU?','','Trong bài trước chúng ta đã tìm hiểu một số bước cơ bản trong quá trình khởi sự kinh doanh Xuất nhập khẩu. Bài viết này chúng ta sẽ cùng tìm hiểu những vấn đề tiếp theo cần được quan tâm khi bạn làm trong lĩnh vực này:\r\nSau khi đã có thời gian liên lạc với các công ty nước ngoài, xây dựng một mối quan hệ tốt với các đại diện của họ hãy list ra một vài công ty – có lẽ trong cùng một nước hoặc vùng lãnh thổ tương tự – để chính xác nhu cầu của họ, hai hoặc ba sản phẩm trong những nhu cầu là gì?','<p>Trong b&agrave;i trước ch&uacute;ng ta đ&atilde; t&igrave;m hiểu một số bước cơ bản trong qu&aacute; tr&igrave;nh khởi sự kinh doanh Xuất nhập khẩu. B&agrave;i viết n&agrave;y ch&uacute;ng ta sẽ c&ugrave;ng t&igrave;m hiểu những vấn đề tiếp theo cần được quan t&acirc;m khi bạn l&agrave;m trong lĩnh vực n&agrave;y:</p>\r\n\r\n<h3><strong>KẾT NỐI S&Acirc;U HƠN</strong></h3>\r\n\r\n<p>Sau khi đ&atilde; c&oacute; thời gian li&ecirc;n lạc với c&aacute;c c&ocirc;ng ty nước ngo&agrave;i, x&acirc;y dựng một mối quan hệ tốt với c&aacute;c đại diện của họ h&atilde;y list ra một v&agrave;i c&ocirc;ng ty &ndash; c&oacute; lẽ trong c&ugrave;ng một nước hoặc v&ugrave;ng l&atilde;nh thổ tương tự &ndash; để ch&iacute;nh x&aacute;c nhu cầu của họ,&nbsp;hai hoặc ba sản phẩm trong những nhu cầu l&agrave; g&igrave;?</p>\r\n\r\n<p>H&atilde;y xem x&eacute;t c&aacute;c phương ph&aacute;p ph&acirc;n phối của họ.&nbsp;Bạn c&oacute; thể l&agrave;m việc trực tiếp với người b&aacute;n bu&ocirc;n của một c&ocirc;ng ty nhập khẩu ở nước ngo&agrave;i, hoa hồng của bạn sẽ thấp hơn, nhưng bạn sẽ kh&ocirc;ng cần phải xử l&yacute; nhiều bước.</p>\r\n\r\n<p>Hoặc, bạn c&oacute; thể cần phải cung cấp danh mục sản phẩm v&agrave; mẫu, l&agrave;m việc với một mạng lưới c&aacute;c c&ocirc;ng ty nhỏ, hoặc người đại diện b&aacute;n h&agrave;ng từ một tập đo&agrave;n lớn.</p>\r\n\r\n<p>Nếu bạn đang nhập khẩu h&agrave;ng h&oacute;a, bạn sẽ cần phải t&igrave;m c&aacute;c nh&agrave; ph&acirc;n phối c&oacute; thể mua số lượng lớn.&nbsp;Một cửa h&agrave;ng b&aacute;n lẻ đơn hoặc hai ba c&ocirc;ng ty nhỏ l&agrave; kh&ocirc;ng đủ ti&ecirc;u thụ sản phẩm m&agrave; bạn đ&atilde; nhập.&nbsp;H&atilde;y ra ngo&agrave;i v&agrave; tiếp x&uacute;c trong c&aacute;c chuỗi b&aacute;n lẻ lớn hơn để hiểu th&ecirc;m về chuỗi cung ứng.</p>\r\n\r\n<h3><strong>NHẬP KHẨU TH&Igrave; SAO?</strong></h3>\r\n\r\n<p>Hiện c&oacute; h&agrave;ng trăm nh&agrave; sản xuất khắp thế giới với khả năng ph&acirc;n phối hạn chế t&igrave;m kiếm một thị trường nước ngo&agrave;i.&nbsp;Nhu cầu của họ ch&iacute;nh l&agrave; động cơ nhập h&agrave;ng của bạn.</p>\r\n\r\n<p>H&atilde;y thuyết phục c&aacute;c nh&agrave; xuất khẩu về quy m&ocirc; v&agrave; uy t&iacute;n của bạn để từ đ&oacute; bạn trở th&agrave;nh nh&agrave; ph&acirc;n phối độc quyền cho sản phẩm đ&oacute; tại thị trường của bạn. Bạn thuyết phục họ bằng việc&nbsp;đưa ra kế hoạch sẽ triển khai việc&nbsp;b&aacute;n h&agrave;ng ra sao, chuẩn bị thủ tục giấy tờ, tiền bạc, lo kh&acirc;u vận chuyển, hải quan, v&agrave; thiết lập mạng lưới b&aacute;n h&agrave;ng&nbsp; thế n&agrave;o. Bạn thấy đấy, t&ocirc;i đ&atilde; chỉ cho bạn c&aacute;ch thức thiết lập kết nối vững chắc hai b&ecirc;n c&ugrave;ng c&oacute; lợi bởi: C&aacute;c nh&agrave; sản xuất họ tăng doanh số b&aacute;n h&agrave;ng, họ c&oacute; một thị trường rộng lớn hơn, v&agrave; lợi nhuận nhiều hơn.&nbsp;C&ograve;n bạn đ&oacute;ng vai tr&ograve; c&ocirc;ng ty nhập khẩu, bạn c&oacute; hoa hồng tr&ecirc;n gi&aacute; của h&agrave;ng ho&aacute;, khả năng tạo sức &eacute;p thị trường khi bạn được độc quyền. Từ đ&acirc;y, đi đ&acirc;u h&atilde;y lu&ocirc;n mang điện thoại theo để sẵn s&agrave;ng trả lời bất cứ cuộc điện thoại n&agrave;o. Bạn c&oacute; thể quảng c&aacute;o sản phẩm c&ocirc;ng ty đ&atilde; nhập tr&ecirc;n c&aacute;c ấn phẩm kinh doanh v&agrave; b&aacute;o ch&iacute;.</p>\r\n\r\n<h3><strong>L&Agrave;M&nbsp;HỢP ĐỒNG NGOẠI THƯƠNG</strong></h3>\r\n\r\n<p>Khi bạn đ&atilde; chắc chắn muốn nhập h&agrave;ng từ đối t&aacute;c l&uacute;c n&agrave;y hai b&ecirc;n sẽ k&yacute; kết&nbsp;hợp đồng Ngoại thương. Nếu được h&atilde;y thu&ecirc;&nbsp;Luật sư của bạn để lập hợp đồng n&agrave;y &ndash; sau đ&oacute; bạn chỉ c&oacute; thể sử dụng lại v&agrave;o c&aacute;c lần sau chỉ cần thay t&ecirc;n của c&aacute;c nh&agrave; sản xuất kh&aacute;c.</p>\r\n\r\n<p>Hai b&ecirc;n cần đ&agrave;m ph&aacute;n kỹ c&agrave;ng c&aacute;c điều khoản của hợp đồng.</p>\r\n\r\n<h3><strong>ĐIỀU KHOẢN GIAO H&Agrave;NG</strong></h3>\r\n\r\n<p>C&aacute;c nh&agrave; sản xuất c&oacute; thể thỏa thuận giao h&agrave;ng k&egrave;m họ thu&ecirc; t&agrave;u, xử l&yacute; vận tải h&agrave;ng h&oacute;a, hoặc bạn c&oacute; thể cần phải l&agrave;m mọi thứ từ nh&agrave; m&aacute;y của họ.</p>\r\n\r\n<p>Bạn th&ecirc;m v&agrave;o hoa hồng m&agrave; bạn muốn với gi&aacute; của h&agrave;ng ho&aacute;.&nbsp;Sau đ&oacute; bạn th&ecirc;m v&agrave;o tất cả c&aacute;c chi ph&iacute; phụ trội nhận h&agrave;ng h&oacute;a từ nh&agrave; m&aacute;y đến kho h&agrave;ng của m&igrave;nh.</p>\r\n\r\n<p>Khi h&agrave;ng h&oacute;a về kho, bạn c&oacute; thể sẽ phải sắp xếp hết c&aacute;c c&ocirc;ng đoạn từ kho vận đến vận chuyển đến c&aacute;c nh&agrave; ph&acirc;n phối, đại l&yacute; kh&aacute;c.</p>\r\n\r\n<p>Bạn sẽ trở n&ecirc;n quen thuộc hơn với Incoterms sử dụng trong tr&iacute;ch dẫn gi&aacute; cả v&agrave; thỏa thuận điều kiện giao h&agrave;ng. Tr&aacute;ch nhiệm của bạn thay đổi theo c&aacute;c điều kiện giao h&agrave;ng cụ thể.&nbsp;Nếu kh&ocirc;ng nắm bắt kỹ lưỡng h&atilde;y Kiểm tra với nh&agrave; giao nhận vận tải/ Forwarders của bạn để hỏi r&otilde; r&agrave;ng về tr&aacute;ch nhiệm của m&igrave;nh.</p>\r\n\r\n<p>Một vận đơn (Bill of Lading) l&agrave; bi&ecirc;n lai cho h&agrave;ng h&oacute;a vận chuyển.&nbsp;N&oacute; được k&yacute; bởi người đại diện h&atilde;ng t&agrave;u hoặc nh&agrave; chu&ecirc;n chở v&agrave; đảm bảo cho người mua rằng h&agrave;ng đ&atilde; được bốc dỡ trong điều kiện tương tự như họ đ&atilde; được chấp nhận.&nbsp;Đ&acirc;y l&agrave; chứng từ quan trọng m&agrave; bạn sẽ cần để ra ng&acirc;n h&agrave;ng để k&yacute; hậu nếu c&oacute;.</p>\r\n\r\n<p>Lu&ocirc;n lu&ocirc;n theo d&otilde;i s&aacute;t ng&agrave;y h&agrave;ng l&ecirc;n t&agrave;u (ETD) v&agrave; ng&agrave;y h&agrave;ng đến (ETA) d&ugrave; bạn l&agrave; nh&agrave; nhập khẩu hay xuất khẩu. Một th&ocirc;ng b&aacute;o h&agrave;ng đến (Arrival Notice) lu&ocirc;n được y&ecirc;u cầu để bạn sẵn s&agrave;ng l&agrave;m c&aacute;c thủ tục Nhận h&agrave;ng, tr&aacute;nh c&aacute;c chi ph&iacute; ph&aacute;t sinh kh&ocirc;ng&nbsp;cần thiết như lưu kho, lưu cont, lưu b&atilde;i&hellip;</p>\r\n\r\n<p>Hai điều kiện giao h&agrave;ng thường được sử dụng nhiều nhất đ&oacute; l&agrave; FOB v&agrave; CIF:</p>\r\n\r\n<p><strong>FOB</strong>&nbsp;l&agrave; một thuật ngữ viết tắt&nbsp;<em>Free On Board</em>, nghĩa l&agrave;&nbsp;<strong>Miễn tr&aacute;ch nhiệm tr&ecirc;n boong t&agrave;u nơi đi</strong>&nbsp;c&ograve;n gọi l&agrave; &rdquo;Giao l&ecirc;n t&agrave;u&rdquo;. N&oacute; l&agrave; một thuật ngữ trong thương mại quốc tế, được thể hiện trong&nbsp;Incoterm. N&oacute; l&agrave; tương tự với&nbsp;FAS, nhưng b&ecirc;n b&aacute;n h&agrave;ng cần phải trả cước ph&iacute; xếp h&agrave;ng l&ecirc;n t&agrave;u. Việc chuyển giao diễn ra khi h&agrave;ng h&oacute;a vượt qua lan can t&agrave;u tại cảng xếp h&agrave;ng. Về mặt quốc tế, thuật ngữ n&agrave;y chỉ r&otilde; cảng xếp h&agrave;ng, v&iacute; dụ &ldquo;FOB New York&rdquo; hay &ldquo;FOB Hải Ph&ograve;ng&rdquo;. C&aacute;c khoản chi ph&iacute; kh&aacute;c như cước vận tải, ph&iacute; bảo hiểm thuộc về tr&aacute;ch nhiệm của b&ecirc;n mua h&agrave;ng</p>\r\n\r\n<p><strong>CIF</strong>, tương tự như CFR ngoại trừ kiện gi&aacute; CIF cần phải c&oacute; bảo hiểm đơn c&oacute; thể chuyển nhượng được để bảo chứng (tiền đảm bảo) cho c&aacute;c rủi ro trong qu&aacute; tr&igrave;nh vận chuyển từ c&aacute;c nh&agrave; bảo hiểm. Gi&aacute; trị của bảo hiểm đơn cần bảo chứng cho gi&aacute; CIF cộng 10 phần trăm v&agrave; khi c&oacute; thể cần phải l&agrave; loại h&igrave;nh tiền tệ đ&atilde; được ghi trong hợp đồng mua b&aacute;n. Lưu &yacute; rằng chỉ c&oacute; bảo chứng cơ bản nhất được y&ecirc;u cầu tương đương với c&aacute;c khoản mục của điều khoản &ldquo;C&rdquo;, v&agrave; b&ecirc;n mua h&agrave;ng th&ocirc;ng thường hay đ&ograve;i hỏi bảo hiểm đơn dạng bảo chứng cho&nbsp;<em>&ldquo;mọi rủi ro&rdquo;</em>&nbsp;(&ldquo;all risks&rdquo;) ph&ugrave; hợp với c&aacute;c khoản mục trong điều khoản &ldquo;A&rdquo;.</p>\r\n\r\n<h3><strong>&nbsp;C&Ocirc;NG TY GIAO NHẬN (FORWARDERS)</strong></h3>\r\n\r\n<p>Forwarder l&agrave; một người chăm s&oacute;c trong những bước quan trọng trong vận chuyển h&agrave;ng h&oacute;a như b&aacute;o gi&aacute; cước vận chuyển, cung cấp th&ocirc;ng tin định tuyến, v&agrave; kh&ocirc;ng gian xếp h&agrave;ng, giao nhận h&agrave;ng h&oacute;a, chuẩn bị t&agrave;i liệu, bảo hiểm, soạn hợp đồng vận chuyển, khai b&aacute;o hải quan, trucking nội v&agrave; kho vận. Để cố định c&aacute;c chi ph&iacute; n&agrave;y cho một l&ocirc; h&agrave;ng, c&ocirc;ng ty Forwarder sẽ b&aacute;o gi&aacute; cho bạn. H&atilde;y y&ecirc;u cầu họ b&aacute;o gi&aacute; tổng hợp All in one ở cuối&nbsp;c&ugrave;ng của B&aacute;o gi&aacute; để&nbsp;tr&aacute;nh&nbsp;bạn tự&nbsp;cộng s&oacute;t bất cứ chi ph&iacute;&nbsp;n&agrave;o.</p>\r\n\r\n<h3><strong>D&Ugrave;NG THƯ T&Iacute;N DỤNG&nbsp;TRONG THANH TO&Aacute;N QUỐC TẾ</strong></h3>\r\n\r\n<p>Một thư t&iacute;n dụng loại bỏ rủi ro t&agrave;i ch&iacute;nh cho bạn, c&aacute;c nh&agrave; sản xuất v&agrave; nh&agrave; ph&acirc;n phối. Một l&aacute; thư kh&ocirc;ng hủy ngang của t&iacute;n dụng đảm bảo bạn đặt h&agrave;ng sẽ kh&ocirc;ng được hủy bỏ bất cứ l&uacute;c n&agrave;o.</p>\r\n\r\n<p>Về cơ bản, c&aacute;c ng&acirc;n h&agrave;ng nắm giữ tiền cho đến khi tất cả chứng từ được xuất tr&igrave;nh. Thư t&iacute;n dụng n&ecirc;u c&aacute;c điều khoản v&agrave; điều kiện đặc biệt l&agrave; Chứng từ y&ecirc;u cầu bằng chứng về vận chuyển h&agrave;ng ho&aacute;. Kh&ocirc;ng bao giờ l&agrave;m việc tr&ecirc;n những lời hứa về thanh to&aacute;n v&igrave; n&oacute; ẩn chứa nhiều nguy cơ, rủi ro.&nbsp;Thư t&iacute;n dụng l&agrave; c&aacute;ch chắc chắn nhất để chuyển c&aacute;c khoản thanh to&aacute;n.</p>\r\n\r\n<h3><strong>MỞ RỘNG KINH DOANH</strong></h3>\r\n\r\n<p>Sau khi bạn đ&atilde; ho&agrave;n th&agrave;nh một v&agrave;i l&ocirc; h&agrave;ng, bạn sẽ cần th&uacute;c đẩy doanh nghiệp xuất nhập khẩu của bạn để c&oacute; được nhiều kh&aacute;ch h&agrave;ng hơn.</p>\r\n\r\n<p>Tham gia tổ chức thương mại, c&aacute;c hiệp hội v&agrave; hội chợ, triển l&atilde;m tại Nước ngo&agrave;i để t&igrave;m kiếm đối t&aacute;c, mở rộng mối quan hệ kinh doanh. Ngo&agrave;i ra, n&ecirc;n kết bạn&nbsp;với tất cả mọi người li&ecirc;n hệ với bạn về xuất nhập khẩu, học hỏi từ những sai lầm v&agrave; th&agrave;nh c&ocirc;ng của họ.</p>\r\n\r\n<p>Bạn đừng qu&ecirc;n T&igrave;nh b&aacute;o marketing để xem đối thủ Cạnh tranh ngo&agrave;i kia đang l&agrave;m g&igrave;, sản phẩm c&oacute; g&igrave; hơn m&igrave;nh v&agrave; c&aacute;ch họ&nbsp; đang b&aacute;n h&agrave;ng, h&atilde;y học hỏi&nbsp; họ nếu họ l&agrave;m tốt hơn m&igrave;nh v&agrave; hạn chế sai lầm của họ.</p>\r\n\r\n<p>Mrs Jade.</p>','/65/6c/c6/5a6a185a9259c.jpg',NULL,1792,0,'2018-01-26 00:00:00',NULL,'Chúng ta sẽ cùng tìm hiểu những vấn đề tiếp theo cần được quan tâm khi bạn làm trong lĩnh vực này:\r\nSau khi đã có thời gian liên lạc với các công ty nước ngoài, xây dựng một mối quan hệ tốt với các đại diện của họ hãy list ra một vài công ty – có lẽ trong','khởi nghiệp xuất nhập khẩu, mở công ty xuất nhập khẩu, quy trình xuất nhập khẩu, tìm kiếm khách hàng, tìm kiếm nhà cung cấp, nghiệp vụ xuất nhập khẩu','','',2,0,'vi','lam-the-nao-de-khoi-nghiep-voi-cong-ty-xuat-nhap-khau',6,27,26,'2018-01-26 00:48:10','2018-03-03 13:44:14'),(232,'MSDS LÀ GÌ? TẦM QUAN TRỌNG CỦA MSDS TRONG VẬN CHUYỂN QUỐC TẾ','','Khi các bạn mua bất cứ một hàng hóa nào hay bất cứ một thiết bị điện tử, điện máy nào đều có kèm theo quyển sách hướng dẫn sử dụng và bảo quản. Trong trường hợp hàng hóa của bạn là hóa chất, thì chúng ta có MSDS nhé!','<h2><strong>MSDS l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p>MSDS l&agrave; từ viết tắt của Material Safety Data Sheet, c&oacute; nghĩa l&agrave; Bảng chỉ dẫn an to&agrave;n h&agrave;ng h&oacute;a (hay c&ograve;n gọi l&agrave; Phiếu an to&agrave;n h&oacute;a chất nếu như h&agrave;ng h&oacute;a l&agrave; h&oacute;a chất). Th&ocirc;ng thường, một MSDS của h&agrave;ng h&oacute;a chất sẽ được thể hiện c&aacute;c th&ocirc;ng tin sau đ&acirc;y:</p>\r\n\r\n<p>&ndash; CHEMICAL PRODUCT AND COMPANY IDENTIFICATION: T&ecirc;n sản phẩm h&oacute;a học diễn giải v&agrave; th&ocirc;ng tin c&ocirc;ng ty/ đơn vị sản xuất sản phẩm đ&oacute; (t&ecirc;n, địa chỉ, c&aacute;c li&ecirc;n hệ)</p>\r\n\r\n<p>&ndash; COMPOSITION / INFORMATION ON INGREDIENTS: Th&agrave;nh phần h&oacute;a học v&agrave; th&ocirc;ng tin về c&aacute;c th&agrave;nh phần của hợp chất đ&oacute;. Trong th&agrave;nh phần n&agrave;y ch&uacute;ng ta LƯU &Yacute; th&ocirc;ng tin về số CAS (Chemical Abstracts Service &ndash; Dịch vụ t&oacute;m tắt h&oacute;a chất): l&agrave; chuỗi số định danh duy nhất cho c&aacute;c nguy&ecirc;n tố h&oacute;a học, c&aacute;c hợp chất h&oacute;a học, c&aacute;c polyme, c&aacute;c chuỗi sinh học, c&aacute;c hỗn hợp v&agrave; c&aacute;c hợp kim. Số CAS nhằm gi&uacute;p ch&uacute;ng ta t&igrave;m kiếm th&ocirc;ng tin li&ecirc;n quan về chất (t&ecirc;n gọi kh&aacute;c, c&ocirc;ng thức h&oacute;a học, ph&acirc;n loại độc tố&hellip;)</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/21.%20MSDS/MSDS1.jpg\" style=\"height:1200px; width:911px\" /></p>\r\n\r\n<p>&ndash; PHYSICAL AND CHEMICAL PROPERTIES: C&aacute;c t&iacute;nh chất vật l&yacute; v&agrave; h&oacute;a học của h&agrave;ng h&oacute;a h&oacute;a chất đ&oacute; (m&agrave;u sắc, h&igrave;nh dạng b&ecirc;n ngo&agrave;i, m&ugrave;i, vị, tỷ trọng ri&ecirc;ng, nhiệt độ n&oacute;ng chảy, nhiệt độ s&ocirc;i, điểm bắt lửa, điểm nổ, điểm tự ch&aacute;y, độ nhớt, tỷ lệ bay hơi, &aacute;p suất hơi, th&agrave;nh phần phần trăm cho ph&eacute;p trong kh&ocirc;ng kh&iacute;, khả năng h&ograve;a tan trong c&aacute;c dung m&ocirc;i như nước, dung m&ocirc;i hữu cơ&hellip;)</p>\r\n\r\n<p>&ndash; HAZARD IDENTIFICATION: X&aacute;c định mức độ nguy hiểm của c&aacute;c th&agrave;nh phần; Đưa ra những khuyến c&aacute;o rủi ro, tai nạn c&oacute; thể xảy ra v&agrave; chỉ dẫn an to&agrave;n,</p>\r\n\r\n<p>&ndash; FIRST AID MEASURES: C&aacute;c biện ph&aacute;p sơ cứu khẩn cấp khi ngộ độc hay bị tai nạn trong khi sử dụng h&oacute;a chất</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/21.%20MSDS/MSDS2.jpg\" style=\"height:1200px; width:923px\" /></p>\r\n\r\n<p>&ndash; FIRE FIGHTING MEASURES: C&aacute;c thiết bị, phương tiện v&agrave; tr&igrave;nh tự, quy chuẩn trong ph&ograve;ng ch&aacute;y-chữa ch&aacute;y</p>\r\n\r\n<p>&ndash; ACCIDENTAL RELEASE MEASURES: C&aacute;c biện ph&aacute;p giảm thiểu rủi ro, tổn thất</p>\r\n\r\n<p>&ndash; HANDLING AND STORAGE: Quy tr&igrave;nh l&agrave;m việc với h&oacute;a chất v&agrave; hướng dẫn xử l&yacute;, c&aacute;c điều kiện bảo quản h&oacute;a chất</p>\r\n\r\n<p>&ndash; EXPOSURE CONTROLS AND PERSONAL PROTECTION: Kiểm so&aacute;t phơi nhiễm v&agrave; bảo hộ c&aacute; nh&acirc;n khi tiếp x&uacute;c với h&oacute;a chất</p>\r\n\r\n<p>&ndash; STABILITY: T&iacute;nh ổn định</p>\r\n\r\n<p>&ndash; TOXICOLOGICAL INFORMATION: Th&ocirc;ng tin về độc t&iacute;nh v&agrave; c&aacute;c hiệu ứng xấu l&ecirc;n sức khỏe con người, chẳng hạn t&aacute;c động xấu tới mắt, da, hệ h&ocirc; hấp, hệ ti&ecirc;u h&oacute;a, khả năng sinh sản cũng như khả năng g&acirc;y ung thư hay g&acirc;y dị biến, đột biến gen. C&aacute;c biểu hiện v&agrave; triệu chứng ngộ độc cấp t&iacute;nh v&agrave; kinh ni&ecirc;n.</p>\r\n\r\n<p>&ndash; ECOLOGICAL INFORMATION: Th&ocirc;ng tin về t&iacute;nh sinh th&aacute;i: C&aacute;c t&aacute;c động xấu l&ecirc;n thủy sinh vật v&agrave; m&ocirc;i trường</p>\r\n\r\n<p>&ndash; DISPOSAL INFORMATION: Th&ocirc;ng tin hướng dẫn phương ph&aacute;p xử l&yacute; phế thải c&oacute; chứa h&oacute;a chất đ&oacute; cũng như xử l&yacute; kho t&agrave;ng theo định kỳ hay khi bị r&ograve; rỉ h&oacute;a chất ra ngo&agrave;i m&ocirc;i trường.</p>\r\n\r\n<p>&ndash; TRANSPORTATION INFORMATION: C&aacute;c quy định về đ&oacute;ng g&oacute;i, tem m&aacute;c v&agrave; vận chuyển (được ph&eacute;p vận chuyển bằng h&igrave;nh thức n&agrave;o, đường kh&ocirc;ng hay đường biển, quy định an to&agrave;n trong qu&aacute; tr&igrave;nh đ&oacute;ng g&oacute;i, vận chuyển như thế n&agrave;o)</p>\r\n\r\n<p>MSDS n&agrave;y do nh&agrave; sản xuất (hoặc người b&aacute;n, người gửi h&agrave;ng) h&oacute;a chất n&agrave;y cung cấp cho h&atilde;ng vận chuyển (h&atilde;ng t&agrave;u hoặc h&atilde;ng h&agrave;ng kh&ocirc;ng). C&aacute;c th&ocirc;ng tin tr&ecirc;n MSDS l&agrave; c&oacute; t&iacute;nh ph&aacute;p l&yacute; trong xử l&yacute; c&aacute;c sự cố li&ecirc;n quan đến l&ocirc; h&agrave;ng (do h&oacute;a chất g&acirc;y ra) n&ecirc;n th&ocirc;ng tin cung cấp tr&ecirc;n MSDS phải đảm bảo t&iacute;nh ch&iacute;nh x&aacute;c cao. MSDS n&agrave;y được gửi k&egrave;m với l&ocirc; h&agrave;ng trong suốt qu&aacute; tr&igrave;nh vận chuyển cho đến khi đến tay người nhập khẩu. Đối với h&agrave;ng h&oacute;a l&agrave; h&oacute;a chất, trong bộ hồ sơ Hải quan n&ecirc;n c&oacute; k&egrave;m theo MSDS (đ&oacute;ng dấu sao y của nh&agrave; nhập khẩu) để x&aacute;c nhận rằng MSDS n&agrave;y đ&atilde; được nh&agrave; nhập khẩu chấp nhận.</p>\r\n\r\n<p>H&atilde;y đọc th&ecirc;m những b&agrave;i viết chất lượng kh&aacute;c của HAN EXIM CLUB dưới đ&acirc;y bạn nh&eacute;!</p>\r\n\r\n<p>Ms Sarah.</p>','/66/95/56/5a6a18e93b00f.jpg',NULL,6163,0,'2018-01-26 00:00:00',NULL,'MSDS là từ viết tắt của Material Safety Data Sheet, có nghĩa là Bảng chỉ dẫn an toàn hàng hóa (hay còn gọi là Phiếu an toàn hóa chất nếu như hàng hóa là hóa chất). Thông thường, một MSDS của hàng hóa chất sẽ được thể hiện các thông tin sau đây:','MSDS là gì? Tại sao cần MSDS? Những nội dung trên MSDS, hàng gì cần MSDS, số CAS là gì?','','',2,0,'vi','msds-la-gi-tam-quan-trong-cua-msds-trong-van-chuyen-quoc-te',8,27,26,'2018-01-26 00:49:59','2018-02-04 18:00:41'),(233,'MỘT NGÀY CỦA NHÂN VIÊN XUẤT NHẬP KHẨU','','Khi bước vào mỗi nghề, mỗi lĩnh vực khác nhau chúng ta luôn tò mò nó sẽ như thế nào, đem lại cho ta cơ hội ra sao. Những thắc mắc của các em không sai. Còn đối với riêng nghề Xuất nhập khẩu- Logistics thì chị muốn nói rằng đó không phải là con đường \"trải đầy hoa hồng\" như người ta thường nói với các em.\r\n\r\nNghề Xuất nhập khẩu-logistics có rất nhiều áp lực chưa nói đến các rủi ro, sai sót mà đôi khi chính chúng ta phải là người nhận trách nhiệm.','<p>C&aacute;c bạn v&agrave; c&aacute;c em th&acirc;n mến,</p>\r\n\r\n<p>Khi bước v&agrave;o mỗi nghề, mỗi lĩnh vực kh&aacute;c nhau&nbsp;ch&uacute;ng ta lu&ocirc;n t&ograve; m&ograve; n&oacute; sẽ như thế n&agrave;o, đem lại cho ta cơ hội ra sao. Những thắc mắc của c&aacute;c em kh&ocirc;ng sai. C&ograve;n đối với ri&ecirc;ng nghề Xuất nhập khẩu- Logistics th&igrave; chị muốn n&oacute;i rằng đ&oacute; kh&ocirc;ng phải l&agrave; con đường &ldquo;trải đầy hoa hồng&rdquo; như người ta thường n&oacute;i với c&aacute;c em.</p>\r\n\r\n<p>Nghề Xuất nhập khẩu-logistics c&oacute; rất nhiều &aacute;p lực chưa n&oacute;i đến c&aacute;c rủi ro, sai s&oacute;t m&agrave; đ&ocirc;i khi ch&iacute;nh ch&uacute;ng ta phải l&agrave; người nhận tr&aacute;ch nhiệm.</p>\r\n\r\n<p>Nghề n&agrave;y mang đến cho c&aacute;c em những cơ hội như mức lương cao, cơ hội&nbsp;được đi nước ngo&agrave;i, tự mở c&ocirc;ng ty ri&ecirc;ng&hellip; C&ograve;n th&aacute;ch thức của nghề n&agrave;y c&aacute;c em lu&ocirc;n cần sự ch&iacute;nh x&aacute;c, tốc độ, khả năng đ&agrave;m ph&aacute;n ở mức cao, am hiểu nghiệp vụ v&agrave; những&nbsp;quy&nbsp;định ph&aacute;p luật li&ecirc;n quan&hellip;</p>\r\n\r\n<p>Cơ&nbsp;hội lu&ocirc;n đi k&egrave;m với th&aacute;ch thức nhưng điều quan trọng ch&uacute;ng ta cần hiểu r&otilde; tr&ecirc;n cả hai phương diện tr&aacute;nh việc chỉ nh&igrave;n thấy cơ hội rồi khi ch&iacute;nh thức l&agrave;m việc thấy kh&oacute; khăn, rủi ro&hellip; dẫn đến hoảng sợ thậm ch&iacute; bỏ nghề. Để c&aacute;c em c&oacute; thể hiểu r&otilde; hơn về nghề n&agrave;y, h&atilde;y đọc b&agrave;i viết dưới đ&acirc;y của chị. Đ&acirc;y l&agrave; c&acirc;u chuyện của c&aacute;c anh chị trong nghề, họ l&agrave; chị, l&agrave;&nbsp;bạn b&egrave; chị&hellip; Hy vọng c&aacute;c em sẽ c&oacute; những suy&nbsp;nghĩ thấu đ&aacute;o v&agrave; nghi&ecirc;m t&uacute;c về nghề Xuất nhập khẩu- Logistics n&agrave;y nh&eacute;.</p>\r\n\r\n<p>Nghề xuất nhập khẩu c&oacute; g&igrave; giống v&agrave; kh&aacute;c với c&aacute;c vị tr&iacute; nh&acirc;n vi&ecirc;n văn ph&ograve;ng như kế to&aacute;n, h&agrave;nh ch&iacute;nh, nh&acirc;n sự&hellip;?</p>\r\n\r\n<p>Giống:</p>\r\n\r\n<ul>\r\n	<li>Đều d&ugrave;ng c&aacute;c thiết bị văn ph&ograve;ng hiện đại, quản l&yacute; nhiều hồ sơ, giấy tờ, giao dịch với c&aacute;c cơ quan b&ecirc;n ngo&agrave;i</li>\r\n	<li>Giao dịch trong v&agrave; ngo&agrave;i tổ chức</li>\r\n</ul>\r\n\r\n<p>Kh&aacute;c:</p>\r\n\r\n<ul>\r\n	<li>Lu&ocirc;n sử dụng tiếng Anh d&ugrave; bất cứ vị tr&iacute; n&agrave;o (nh&acirc;n vi&ecirc;n chứng từ, nh&acirc;n vi&ecirc;n mua h&agrave;ng, nh&acirc;n vi&ecirc;n hiện trường) nhưng t&ugrave;y từng vị tr&iacute; đ&ograve;i hỏi mức độ tiếng Anh của bạn l&agrave; giao tiếp hay đơn thuần l&agrave; tiếng Anh đọc hiểu, tiếng Anh viết</li>\r\n	<li>Sử dụng email thường xuy&ecirc;n nhất. L&yacute; do: c&ocirc;ng việc xuất nhập khẩu đ&ograve;i hỏi việc thực hiện theo quy tr&igrave;nh v&agrave; phải c&oacute; sự li&ecirc;n lạc với b&ecirc;n ngo&agrave;i, b&ecirc;n trong kh&aacute; nhiều</li>\r\n	<li>&Aacute;p lực c&ocirc;ng việc lớn. Một số bạn nếu l&agrave;m kế to&aacute;n c&ograve;n c&oacute; kế to&aacute;n trưởng kiểm so&aacute;t chứng từ đầu ra nhưng với vị tr&iacute; xuất nhập khẩu thường ch&iacute;nh bạn l&agrave; người phải chịu tr&aacute;ch nhiệm về độ ch&iacute;nh x&aacute;c chứng từ, c&aacute;c con số tr&ecirc;n hợp đồng, invoice&hellip; Gi&aacute; trị giao dịch thường t&iacute;nh bằng USD n&ecirc;n sai 1 li l&agrave; đi ng&agrave;n dặm.</li>\r\n	<li>Năng động: Xuất nhập khẩu khiến bạn năng động bởi bạn thường xuy&ecirc;n phải cập nhật c&aacute;c văn bản từ Hải quan, thuế, thường xuy&ecirc;n gặp gỡ giao lưu với kh&aacute;ch h&agrave;ng/ nh&agrave; cung cấp nước ngo&agrave;i n&ecirc;n cơ hội mở rộng mối quan hệ, hiểu biết tăng l&ecirc;n, vượt cả ngo&agrave;i bi&ecirc;n giới.</li>\r\n</ul>\r\n\r\n<p>Sau đ&acirc;y ch&uacute;ng ta c&ugrave;ng xem một ng&agrave;y l&agrave;m việc của Nh&acirc;n vi&ecirc;n xuất nhập khẩu.</p>\r\n\r\n<p><strong>Vị tr&iacute; Nh&acirc;n vi&ecirc;n&nbsp;mua h&agrave;ng nhập khẩu</strong></p>\r\n\r\n<p>Bạn Linh Nhi tuy đ&atilde; tốt nghiệp trường&nbsp;Đại học Ngoại Thương khoa Kinh tế đối ngoại&nbsp;nhưng bạn vẫn c&ograve;n kh&aacute; Ibất ngờ&rdquo; trước cường độ c&ocirc;ng việc của nh&acirc;n vi&ecirc;n xuất nhập khẩu khi mới đang thử&nbsp;việc. Đ&acirc;y l&agrave; mi&ecirc;u tả của bạn ấy sau thời gian thử việc tại vị tr&iacute; Nh&acirc;n vi&ecirc;n mua h&agrave;ng.</p>\r\n\r\n<p>&ldquo;Khi đến văn ph&ograve;ng điều đầu ti&ecirc;n m&igrave;nh phải nhanh nhẹn mở m&aacute;y t&iacute;nh, thời gian chờ m&aacute;y t&iacute;nh l&agrave; l&uacute;c liếc qua nh&igrave;n lịch l&agrave;m việc- cuốn lịch b&agrave;n kh&ocirc;ng thể thiếu với nh&acirc;n vi&ecirc;n xuất nhập khẩu. Cuốn lịch khoanh tr&ograve;n c&aacute;c mốc thời gian n&agrave;o l&agrave; ETD (Estimated Time of Departure- ng&agrave;y khởi h&agrave;nh dự kiến), ETA (Estimateds time of Arrival- ng&agrave;y t&agrave;u v&agrave;o dự kiến), rồi ng&agrave;y mở LC (Letter of Credit- Thư t&iacute;n dụng), ng&agrave;y nhận Shipping doc (chứng từ giao nhận)&hellip; Kh&ocirc;ng những thế mỗi ng&agrave;y quan trọng c&ograve;n được đ&aacute;nh dấu bằng b&uacute;t bi xanh, đỏ. Khi mở m&aacute;y t&iacute;nh th&igrave; m&agrave;n h&igrave;nh sẽ hiện ra ngay c&aacute;i Sticky Note (ghi ch&uacute; tr&ecirc;n Desktop), sau đ&oacute; l&agrave; mở Skype, mở Mail ngay lập tức&hellip; Tiếp đến, m&igrave;nh cần ho&agrave;n th&agrave;nh c&aacute;c giấy tờ cần thiết theo tiến độ, vừa ra ng&acirc;n h&agrave;ng về l&agrave; lại giục chuyển ph&aacute;t nhanh đến lấy thư, vừa lấy được chuyển ph&aacute;t nhanh l&agrave; lại gọi điện &iacute; ới với b&ecirc;n forwarder&hellip; M&igrave;nh thấy thời gian cần phải biết sắp xếp hợp l&yacute;, kh&ocirc;ng c&oacute; thao t&aacute;c n&agrave;o l&agrave; thừa, c&aacute;ch l&agrave;m việc phải thật sự khoa học&rdquo;.</p>\r\n\r\n<p><strong>C&ograve;n sau đ&acirc;y l&agrave; ch&acirc;n dung của vị tr&iacute; Nh&acirc;n vi&ecirc;n hiện trường</strong></p>\r\n\r\n<p>Anh T&acirc;m l&agrave; nh&acirc;n vi&ecirc;n hiện trường l&agrave;m cho c&ocirc;ng&nbsp; ty xuất khẩu n&ocirc;ng sản, anh l&agrave; người trực tiếp giao dịch với đối t&aacute;c, tiến h&agrave;nh thu&ecirc; phương tiện vận tải, thanh to&aacute;n tiền h&agrave;ng v&agrave; l&agrave;m c&aacute;c thủ tục Hải quan để được ph&eacute;p xuất khẩu l&ocirc; h&agrave;ng. H&agrave;ng h&oacute;a n&ocirc;ng sản xuất khi xuất đi cần phải đạt chuẩn theo y&ecirc;u cầu n&ecirc;n anh phải ki&ecirc;m lu&ocirc;n việc lấy mẫu, xin kiểm định thực vật để c&oacute; được giấy x&aacute;c nhận của cơ quan kiểm định.</p>\r\n\r\n<p>Do phải di chuyển nhiều v&agrave; l&agrave;m việc dưới cường độ cao n&ecirc;n trang phục cũng cần sự thoải m&aacute;i, thuận tiện, quần b&ograve; v&agrave; &aacute;o ph&ocirc;ng đ&oacute;ng th&ugrave;ng được anh tận dụng tối đa. Với anh T&acirc;m, việc phải ăn vội cơm hộp buổi trưa dưới c&aacute;i nắng chang chang tại cảng, ngồi chờ xe tải chở h&agrave;ng đến để gi&aacute;m s&aacute;t kh&acirc;u đ&oacute;ng th&ugrave;ng h&agrave;ng l&agrave; điều hết sức b&igrave;nh thường. Anh học được rất nhiều về tinh thần l&agrave;m việc kh&ocirc;ng ngại ng&agrave;y đ&ecirc;m từ những đối t&aacute;c đặc biệt l&agrave;&hellip; c&aacute;nh t&agrave;i xế, bốc dỡ. Từ ng&agrave;y chuyển sang l&agrave;m nh&acirc;n vi&ecirc;n hiện trường xuất nhập khẩu, anh ng&agrave;y c&agrave;ng năng động hơn, mềm mỏng hơn do phải tiếp x&uacute;c nhiều với c&aacute;c cơ quan Nh&agrave; nước.</p>\r\n\r\n<p><strong>C&ograve;n Nh&acirc;n vi&ecirc;n sale xuất nhập khẩu th&igrave; sao?</strong></p>\r\n\r\n<p>Với kinh nghiệm 3 năm quản l&yacute; ph&ograve;ng kinh doanh xuất nhập khẩu, anh Qu&acirc;n chia sẻ về tr&aacute;ch nhiệm của m&igrave;nh tại c&ocirc;ng ty.</p>\r\n\r\n<p>H&agrave;ng ng&agrave;y m&igrave;nh cần l&agrave;m c&aacute;c c&ocirc;ng việc như dưới đ&acirc;y, c&ocirc;ng việc chiếm trọn thời gian của m&igrave;nh tại văn ph&ograve;ng v&agrave; chuyện l&agrave;m th&ecirc;m giờ l&agrave; b&igrave;nh thường v&igrave; m&igrave;nh c&oacute; chỉ ti&ecirc;u doanh số. Đ&acirc;y l&agrave; một số c&ocirc;ng việc m&igrave;nh l&agrave;m tại vị tr&iacute; Sale xuất khẩu</p>\r\n\r\n<ul>\r\n	<li>Quản l&yacute; c&ocirc;ng việc t&igrave;m kiếm v&agrave; chăm s&oacute;c kh&aacute;ch h&agrave;ng xuất khẩu.<br />\r\n	&bull; Đưa ra phương &aacute;n tiếp cận kh&aacute;ch h&agrave;ng, thị trường mục ti&ecirc;u.<br />\r\n	&bull; Gi&aacute;m s&aacute;t v&agrave; kiểm tra c&ocirc;ng việc t&igrave;m kiếm kh&aacute;ch h&agrave;ng th&ocirc;ng qua c&aacute;c website thương mại điện tử quốc tế: alibaba.com,&nbsp;tradekey.com,&nbsp;ecvn.com&hellip;<br />\r\n	&bull; Gi&aacute;m s&aacute;t, kiểm tra thực hiện c&ocirc;ng việc kiểm tra, trả lời th&ocirc;ng tin với đối t&aacute;c quan t&acirc;m.</li>\r\n	<li>B&aacute;o c&aacute;o Tổng Gi&aacute;m Đốc thường xuy&ecirc;n về c&aacute;c kh&aacute;ch h&agrave;ng ph&aacute;t sinh.<br />\r\n	&bull; Tham dự c&aacute;c hoạt động t&igrave;m kiếm kh&aacute;ch h&agrave;ng kh&aacute;c: Hội thảo, hội nghị.<br />\r\n	&bull; Lập kế hoạch, triển khai, gi&aacute;m s&aacute;t triển khai c&ocirc;ng việc chăm s&oacute;c kh&aacute;ch h&agrave;ng.<br />\r\n	&bull; Quản l&yacute;, gi&aacute;m s&aacute;t, triển khai c&ocirc;ng việc tiếp nhận đơn h&agrave;ng, l&agrave;m thủ tục giao h&agrave;ng. Chịu tr&aacute;ch nhiệm trước Tổng Gi&aacute;m Đốc về c&ocirc;ng việc thực hiện đơn h&agrave;ng của kh&aacute;ch h&agrave;ng xuất khẩu đảm bảo đ&uacute;ng tiến độ, đ&uacute;ng mặt h&agrave;ng, đ&uacute;ng quy tr&igrave;nh v&agrave; luật định.</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:.5in\">&bull; Lập kế hoạch cải tiến, thay đổi sản phẩm ph&ugrave; hợp với y&ecirc;u cầu của đối t&aacute;c.<br />\r\n&bull; Chịu tr&aacute;ch nhiệm soạn thảo, bi&ecirc;n dịch, kiểm so&aacute;t nội dung th&ocirc;ng tin giao dịch, đối nội, đối ngoại bằng tiếng anh</p>\r\n\r\n<ul>\r\n	<li>Thực hiện up sản phẩm thường xuy&ecirc;n, để l&ecirc;n top đầu của một ng&agrave;nh h&agrave;ng, chẳng hạn, trung b&igrave;nh một ng&agrave;y phải up 500 1000 sản phẩm của một sản phẩm th&igrave; sản phẩm của m&igrave;nh mới hiện thị l&ecirc;n top đầu. Sau một ng&agrave;y up sản phẩm như vậy, v&agrave;o mỗi s&aacute;ng sẽ check email v&agrave; phần inbox tr&ecirc;n alibaba của m&igrave;nh xem c&oacute; thư hỏi h&agrave;ng v&agrave; kh&aacute;ch h&agrave;ng n&agrave;o quan t&acirc;m đến sản phẩm đ&oacute; hay kh&ocirc;ng.</li>\r\n</ul>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave;&nbsp;<a href=\"https://web.archive.org/web/20170312022908/http:/workstandard.vn/mot-ngay-cua-nhan-vien-xuat-nhap-khau/\">b&agrave;i viết</a>&nbsp;của chị Jade v&agrave; những chia sẻ từ kinh nghiệm thực tế từ bạn b&egrave; của chị. C&ograve;n bạn đ&atilde; c&oacute; những trải nghiệm g&igrave; với nghề n&agrave;y hay chưa bước v&agrave;o nghề bạn thấy sao? H&atilde;y comment facebook ở dưới b&agrave;i viết để kết nối với Ad nh&eacute;.</p>','/0f/fe/e0/5a6a195181c14.jpg',NULL,16269,0,'2018-01-26 00:00:00',NULL,'Nghề Xuất nhập khẩu-logistics có rất nhiều áp lực chưa nói đến các rủi ro, sai sót mà đôi khi chính chúng ta phải là người nhận trách nhiệm.Nghề này mang đến cho các em những cơ hội như mức lương cao, cơ hội được đi nước ngoài, tự mở công ty riêng…','thực tế về nhân viên xuất nhập khẩu, trở thành nhân viên xuất nhập khẩu, áp lực của nhân viên xuất nhập khẩu, những khó khăn của nhân viên xuất nhập khẩu khi mới vào nghề, trở thành nhân viên xuất nhập khẩu chuyên nghiệp, trở thành nhân viên xuất nhập khẩ','','',2,0,'vi','mot-ngay-cua-nhan-vien-xuat-nhap-khau',5,27,26,'2018-01-26 00:52:17','2018-03-03 13:45:27'),(234,'NHÀ NHẬP KHẨU NÊN TRẢ TIỀN TRƯỚC HAY TRẢ TIỀN NGAY','','Nếu làm trong lĩnh vực kinh doanh hay xuất nhập khẩu có lẽ ít nhiều bạn đã nghe thuật ngữ \"cấp tín dụng\" hay còn gọi là \"chiếm dụng vốn\" cũng như các cụm từ như \" trả tiền ngay\". Hãy cùng với HAN EXIM CLUB tìm hiểu về chủ đề này:','<p>Nếu l&agrave;m trong lĩnh vực kinh doanh hay xuất nhập khẩu c&oacute; lẽ &iacute;t nhiều bạn đ&atilde; nghe thuật ngữ &quot;cấp t&iacute;n dụng&quot; hay c&ograve;n gọi l&agrave; &quot;chiếm dụng vốn&quot; cũng như c&aacute;c cụm từ như &quot; trả tiền ngay&quot;. H&atilde;y c&ugrave;ng với HAN EXIM CLUB t&igrave;m hiểu về chủ đề n&agrave;y:</p>\r\n\r\n<p><strong>1.Trả tiền trước</strong></p>\r\n\r\n<p>Trả tiền trước l&agrave; việc người nhập khẩu phải trả cho người xuất khẩu to&agrave;n bộ hoặc một phần tiền h&agrave;ng sau khi k&yacute; hợp đồng hoặc khi hợp đồng được ph&ecirc; duyệt nhưng trước khi người b&aacute;n giao h&agrave;ng.</p>\r\n\r\n<p>Trong thanh to&aacute;n quốc tế, trả tiền trước nhằm hai&nbsp;mục đ&iacute;ch:</p>\r\n\r\n<p><strong>Thứ nhất, trả tiền trước nhằm mục đ&iacute;ch cấp t&iacute;n dụng xuất khẩu. Loại n&agrave;y c&oacute; đặc điểm sau đ&acirc;y:</strong></p>\r\n\r\n<ul>\r\n	<li>Ứng trước cho người xuất khẩu (c&oacute; thể ứng để người xuất khẩu chuẩn bị nguy&ecirc;n liệu th&ocirc;, mua th&ecirc;m m&aacute;y m&oacute;c mới&hellip;)</li>\r\n	<li>Việc trả tiền sẽ được thực hiện x ng&agrave;y sau khi k&yacute; hợp đồng hợp khi hợp đồng c&oacute; hiệu lực.</li>\r\n	<li>Số tiền trả trước lớn hay nhỏ phụ thuộc v&agrave;o nhu cầu vay của người xuất khẩu v&agrave; khả năng cấp t&iacute;n dụng của người nhập khẩu.</li>\r\n	<li>Gi&aacute; cả h&agrave;ng h&oacute;a sẽ rẻ hơn so với trả tiền ngay do l&atilde;i cho vay được c&aacute;ch khấu trừ v&agrave;o gi&aacute; trị h&agrave;ng nhập khẩu.</li>\r\n</ul>\r\n\r\n<p><strong>&nbsp;Thứ hai, trả tiền trước nhằm đặt cọc đảm bảo thực hiện hợp đồng. Đặc điểm của n&oacute; đ&oacute; l&agrave;</strong></p>\r\n\r\n<ul>\r\n	<li>Thời gian trả trước ngắn (10-15 ng&agrave;y trước ng&agrave;y giao h&agrave;ng): đ&acirc;y l&agrave; khoảng thời gian m&agrave; h&agrave;ng h&oacute;a đ&atilde; được sản xuất xong đến hơn 90%. Khoản tiền n&agrave;y chỉ nhằm để người b&aacute;n tin người mua chắc chắn sẽ nhận h&agrave;ng</li>\r\n	<li>Quy định: x ng&agrave;y trước ng&agrave;y giao h&agrave;ng</li>\r\n	<li>Trường hợp &aacute;p dụng: bu&ocirc;n b&aacute;n lần đầu, người bản nghi ngờ khả năng thanh to&aacute;n của người mua.</li>\r\n	<li>Thường kh&ocirc;ng t&iacute;nh l&atilde;i.</li>\r\n	<li>Số tiền ứng trước nhiều hay &iacute;ch phụ thuộc v&agrave;o từng trường hợp cụ thể.</li>\r\n</ul>\r\n\r\n<p><strong>2. Trả tiền ngay</strong></p>\r\n\r\n<p>C&ograve;n trả tiền ngay l&agrave; việc người mua sẽ thanh to&aacute;n tiền hay ngay tại một&nbsp; thời&nbsp; điểm cụ thể, bạn c&oacute; thể tham khảo dưới đ&acirc;y:</p>\r\n\r\n<ul>\r\n	<li>Ngay sau khi người b&aacute;n giao h&agrave;ng xong tại địa điểm quy định trong hợp đồng. Nếu căn cứ theo Incoterm th&igrave; thường tương ứng với c&aacute;c điều kiện như EXW, FAS, FCA</li>\r\n	<li>Ngay sau khi người b&aacute;n giao h&agrave;ng xong tr&ecirc;n phương tiện vận tải tại nơi giao h&agrave;ng chỉ định cũng tương ứng với c&aacute;c điều kiện như FOB, CIF, CFR</li>\r\n	<li>Ngay sau khi chứng từ được xuất tr&igrave;nh (ứng với phương thức DA/DP)</li>\r\n	<li>Ngay sau khi nhận được chứng từ v&agrave; kiểm tra chứng từ (ứng với LC)</li>\r\n	<li>Ngay sau khi nhận h&agrave;ng xong (ứng với TT trả sau) đ&acirc;y l&agrave; loại thanh to&aacute;n m&agrave; nh&agrave; nhập khẩu cấp t&iacute;n dụng cho nh&agrave; xuất khẩu với thời gian l&acirc;u nhất. Thường l&agrave; khi hai b&ecirc;n đ&atilde; c&oacute; sự hiểu biết lẫn nhau, quan hệ l&agrave;m ăn l&acirc;u d&agrave;i.</li>\r\n</ul>\r\n\r\n<p>Ad sẽ &nbsp;c&oacute; tiếp b&agrave;i viết về chủ đề thanh to&aacute;n, c&aacute;c bạn h&atilde;y ủng hộ v&agrave; theo d&otilde;i thường xuy&ecirc;n nha.</p>\r\n\r\n<p>Mrs Jade.</p>','/ee/3d/de/5a6a199dd2f8c.jpg',NULL,1236,0,'2018-01-26 00:00:00',NULL,'Trả tiền trước là việc người nhập khẩu phải trả cho người xuất khẩu toàn bộ hoặc một phần tiền hàng sau khi ký hợp đồng hoặc khi hợp đồng được phê duyệt nhưng trước khi người bán giao hàng.Trong thanh toán quốc tế, trả tiền trước nhằm hai mục đích:\r\nỨng t','Trả tiền trước hay trả tiền ngay, thanh toán quốc tế, phương thức thanh toán quốc tế, phương thức TT, TT là gì?','','',2,0,'vi','nha-nhap-khau-nen-tra-tien-truoc-hay-tra-tien-ngay',7,27,26,'2018-01-26 00:53:33','2018-03-03 19:48:30'),(235,'NHỮNG ĐIỀU CẦN BIẾT VỀ BẢO HIỂM HÀNG HÓA XUẤT NHẬP KHẨU','','Mặc dù bảo hiểm là điều hiển nhiên cần có trong cuộc sống nói chung và trong kinh doanh nói riêng nhưng không phải ai cũng có nhận thức đầy đủ về nó.\r\nViệc các chủ hàng là nhà xuất khẩu/ nhập khẩu không mua bảo hiểm hàng hóa cho lô hàng của mình nhiều khi cũng không phải do cố tình mà có thể do họ lờ đi, thiếu kiến thức.','<p>Mặc d&ugrave; bảo hiểm l&agrave; điều hiển nhi&ecirc;n cần c&oacute; trong cuộc sống n&oacute;i chung v&agrave; trong kinh doanh n&oacute;i ri&ecirc;ng nhưng kh&ocirc;ng phải ai cũng c&oacute; nhận thức đầy đủ về n&oacute;.<br />\r\nViệc c&aacute;c chủ h&agrave;ng l&agrave; nh&agrave; xuất khẩu/ nhập khẩu kh&ocirc;ng mua bảo hiểm h&agrave;ng h&oacute;a cho l&ocirc; h&agrave;ng của m&igrave;nh nhiều khi cũng kh&ocirc;ng phải do cố t&igrave;nh m&agrave; c&oacute; thể do họ lờ đi, thiếu kiến thức.</p>\r\n\r\n<h2><strong>Vậy bảo hiểm h&agrave;ng h&oacute;a l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p>Bất cứ hoạt động vận chuyển h&agrave;ng h&oacute;a từ một nơi n&agrave;y sang nơi kh&aacute;c đều chứa đựng những rủi ro như hư hỏng h&agrave;ng, rủi ro từ thi&ecirc;n nhi&ecirc;n như thi&ecirc;n tai, b&atilde;o, động đất g&acirc;y ra tổn thất một phần hoặc to&agrave;n bộ l&ocirc; h&agrave;ng(do cướp biển chẳng hạn).</p>\r\n\r\n<p>Bảo hiểm h&agrave;ng h&oacute;a trong h&agrave;ng hải ra đời từ rất xa xưa v&agrave; c&oacute; thể n&oacute;i l&agrave; một trong những loại h&igrave;nh bảo hiểm sớm nhất của con người từ thế kỷ thứ 9 trước C&ocirc;ng nguy&ecirc;n v&agrave; n&oacute; đ&atilde; ph&aacute;t triển c&ugrave;ng với nh&acirc;n loại cho đến tận ng&agrave;y nay.</p>\r\n\r\n<p>Việc mua bảo hiểm h&agrave;ng h&oacute;a phải được thực hiện trước khi c&oacute; rủi ro xảy ra c&oacute; thể l&agrave; trước khi h&agrave;ng h&oacute;a được vận chuyển. Để mua bảo hiểm h&agrave;ng h&oacute;a, th&ocirc;ng thường bạn sẽ phải cung cấp c&aacute;c th&ocirc;ng tin sau</p>\r\n\r\n<h2><strong>M&ocirc; tả h&agrave;ng h&oacute;a</strong></h2>\r\n\r\n<p>Việc th&ocirc;ng b&aacute;o cho C&ocirc;ng ty bảo hiểm biết về l&ocirc; h&agrave;ng của bạn định mua bảo hiểm l&agrave; điều bắt buộc. N&oacute; c&ograve;n cụ thể cả về quy c&aacute;ch đ&oacute;ng g&oacute;i nhằm tr&aacute;nh khỏi những sai s&oacute;t, rủi ro m&agrave; kh&ocirc;ng được bảo hiểm khi c&oacute; tổn thất xảy ra.</p>\r\n\r\n<p>Việc đ&oacute;ng g&oacute;i rất quan trọng v&igrave; n&oacute; l&agrave; bao b&igrave; để bảo vệ h&agrave;ng h&oacute;a&nbsp; trong suốt qu&aacute; tr&igrave;nh vận chuyển. Ngo&agrave;i ra, tr&ecirc;n bao b&igrave; c&ograve;n được gắn c&aacute;c k&yacute; m&atilde; hiệu cần thiết để chủ h&agrave;ng c&oacute; thể x&aacute;c định được đ&oacute; l&agrave; h&agrave;ng của m&igrave;nh đặc biệt h&agrave;ng được gửi qua Chuyển ph&aacute;t nhanh hoặc l&ocirc; h&agrave;ng lẻ gửi qua đường biển (LCL). Nếu h&agrave;ng h&oacute;a kh&ocirc;ng c&oacute; t&ecirc;n thương hiệu hoặc k&yacute; m&atilde; hiệu r&otilde; r&agrave;ng th&igrave; khi c&oacute; thất lạc hoặc trộm cắp sẽ rất kh&oacute; x&aacute;c định.</p>\r\n\r\n<h2><strong>Cung cấp h&agrave;nh tr&igrave;nh l&ocirc; h&agrave;ng</strong></h2>\r\n\r\n<p>Điều n&agrave;y c&ograve;n quan trọng hơn cả việc x&aacute;c định H&agrave;nh tr&igrave;nh l&ocirc; h&agrave;ng đi từ Cảng n&agrave;o đến Cảng n&agrave;o. Đ&acirc;y cũng l&agrave; căn cứ để C&ocirc;ng ty bảo hiểm đ&aacute;nh gi&aacute; x&aacute;c suất rủi ro li&ecirc;n quan tới Địa l&yacute; v&iacute; dụ như Thời tiết, kh&iacute; hậu của v&ugrave;ng địa l&yacute; đ&oacute; ảnh hưởng đến điều kiện bảo quản h&agrave;ng h&oacute;a hay kh&ocirc;ng? Ngo&agrave;i ra, ch&iacute;nh trị ở những v&ugrave;ng đ&oacute; c&oacute; ổn định hay kh&ocirc;ng, c&oacute; an to&agrave;n để giao h&agrave;ng hoặc thanh to&aacute;n hay kh&ocirc;ng. H&agrave;nh tr&igrave;nh của con t&agrave;u c&oacute; đi qua những v&ugrave;ng n&oacute;ng về t&igrave;nh trạng Cướp biển hay kh&ocirc;ng?</p>\r\n\r\n<h2><strong>Điều kiện bảo hiểm h&agrave;ng h&oacute;a</strong></h2>\r\n\r\n<p>Khi n&oacute;i đến c&aacute;c điều kiện bảo hiểm h&agrave;ng h&oacute;a n&oacute;i chung được chia th&agrave;h 3 mức độ l&agrave; A,B,C v&agrave; được quy định theo Hiệp hội Bảo l&atilde;nh Quốc tế (IUA). Ngo&agrave;i ra, c&ograve;n c&oacute; một số điều kiện đặc biệt kh&aacute;c với c&aacute;c ng&agrave;nh h&agrave;ng kh&aacute;c nhau như thịt đ&ocirc;ng lạnh, sản phẩm đ&ocirc;ng lạnh, gỗ, than đ&aacute;, xăng dầu&hellip;Vận tải h&agrave;ng kh&ocirc;ng cũng c&oacute; điều khoản ri&ecirc;ng của m&igrave;nh. N&oacute;i chung, c&aacute;c điều kiện bảo hiểm A,B,C c&oacute; thể &aacute;p dụng cho mọi phương thức vận tải. C&aacute;c em h&atilde;y c&ugrave;ng xem x&eacute;t c&aacute;c điều kiện n&agrave;y ở dưới đ&acirc;y:</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:645px\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"3\" style=\"width:118px\">\r\n			<p><strong>Điều kiện bảo hiểm h&agrave;ng h&oacute;a</strong></p>\r\n			</td>\r\n			<td style=\"width:521px\">\r\n			<p><strong>Nội dung</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"3\" style=\"width:35px\">\r\n			<p>A</p>\r\n			</td>\r\n			<td rowspan=\"2\" style=\"width:39px\">\r\n			<p>B</p>\r\n			</td>\r\n			<td style=\"width:39px\">\r\n			<p>C</p>\r\n			</td>\r\n			<td style=\"width:521px\">\r\n			<p>&ndash; Ch&aacute;y, nổ</p>\r\n\r\n			<p>&ndash; T&agrave;u, thuyền bị mắc cạn, chạm đ&aacute;y, ch&igrave;m đắm, lật &uacute;p</p>\r\n\r\n			<p>&ndash; Đ&acirc;m va của t&agrave;u, thuyền, PTVT với bất kỳ vật thể b&ecirc;n ngo&agrave;i</p>\r\n\r\n			<p>&ndash;&nbsp;Phương tiện chuy&ecirc;n chở đường bộ bị lật đổ hay trật b&aacute;nh</p>\r\n\r\n			<p>&ndash; Dỡ h&agrave;ng tại cảng l&aacute;nh nạn</p>\r\n\r\n			<p>&ndash; Hy sinh tổn thất chung</p>\r\n\r\n			<p>&ndash; N&eacute;m h&agrave;ng xuống biển hoặc nước cuốn tr&ocirc;i.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"width:562px\">\r\n			<p>&ndash; Động đất, n&uacute;i lửa phun, s&eacute;t đ&aacute;nh,</p>\r\n\r\n			<p>&ndash; Nước biển, s&ocirc;ng, hồ x&acirc;m nhập v&agrave;o t&agrave;u, thuyền, phương tiện vận chuyển, th&ugrave;ng chứa, xe h&agrave;ng hoặc nơi chứa h&agrave;ng</p>\r\n\r\n			<p>&ndash; Nước cuốn khỏi t&agrave;u, phương tiện chở h&agrave;ng</p>\r\n\r\n			<p>&ndash; Tổn thất to&agrave;n bộ của bất cứ kiện h&agrave;ng n&agrave;o rơi khỏi mạn, rơi mất trong khi xếp, dỡ, chuyển tải.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\" style=\"width:604px\">\r\n			<p>&ndash; Thời tiết xấu</p>\r\n\r\n			<p>&ndash; H&agrave;nh động sai tr&aacute;i.</p>\r\n\r\n			<p>&ndash; Cướp biển.</p>\r\n\r\n			<p>&ndash; C&aacute;c rủi ro đặc biệt.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Điều khoản giao h&agrave;ng</strong></h2>\r\n\r\n<p>Cơ sở x&aacute;c định gi&aacute; trị của l&ocirc; h&agrave;ng dựa v&agrave;o điều kện giao h&agrave;ng để từ đ&oacute; t&iacute;nh được ph&iacute; bảo hiểm. Nếu&nbsp;kh&ocirc;ng c&oacute; thỏa thuận n&agrave;o kh&aacute;c th&igrave; gi&aacute; trị l&ocirc; h&agrave;ng được bảo hiểm thường bằng 110% gi&aacute; CIF hoặc 110% gi&aacute; FOB.</p>\r\n\r\n<p>Đừng qu&ecirc;n rằng chiến tranh v&agrave; đ&igrave;nh c&ocirc;ng c&oacute; thể bị loại trừ khỏi c&aacute;c điều kiện A, B, C kể tr&ecirc;n nhưng bạn ho&agrave;n to&agrave;n c&oacute; thể Mua bảo hiểm bổ sung cho c&aacute;c rủi ro ri&ecirc;ng đ&oacute;.</p>\r\n\r\n<p>Th&ocirc;ng thường, tất cả c&aacute;c nội dung ở tr&ecirc;n sẽ đều xuất hiện trong Đơn y&ecirc;u cầu Bảo hiểm h&agrave;ng h&oacute;a của c&aacute;c C&ocirc;ng ty. C&aacute;c em c&oacute; thể tham khảo mẫu dưới đ&acirc;y</p>','/63/1b/b6/5a6a1a0184231.jpg',NULL,2174,0,'2018-01-26 00:00:00',NULL,'Mặc dù bảo hiểm là điều hiển nhiên cần có trong cuộc sống nói chung và trong kinh doanh nói riêng nhưng không phải ai cũng có nhận thức đầy đủ về nó.Việc các chủ hàng là nhà xuất khẩu/ nhập khẩu không mua bảo hiểm hàng hóa cho lô hàng của mình nhiều khi c','bảo hiểm hàng hóa xuất nhập khẩu, các loại bảo hiểm, tỷ lệ mua bảo hiểm, những rủi ro trong vận chuyển hàng hóa, ai nên mua bảo hiểm','','',2,0,'vi','nhung-dieu-can-biet-ve-bao-hiem-hang-hoa-xuat-nhap-khau',8,27,26,'2018-01-26 00:55:13','2018-03-03 19:49:55'),(236,'NHỮNG ĐIỀU CẦN BIẾT VỀ HÀNG FCL VÀ LCL','','Bạn là nhà xuất khẩu và bán hàng theo điều kiện CIF? Bạn là nhà nhập khẩu mua hàng theo điều kiện FOB?… Chắc hẳn mỗi khi vận chuyển hàng hóa qua container đường biển, các bên giao nhận vận tải luôn hỏi hàng của bạn là LCL hay FCL? Vậy chúng là gì và trường hợp nào dùng? Chúng ta sẽ làm rõ ràng bài viết sau đây của Ad nhé.','<p>Bạn l&agrave; nh&agrave; xuất khẩu v&agrave; b&aacute;n h&agrave;ng theo điều kiện CIF? Bạn l&agrave; nh&agrave; nhập khẩu mua h&agrave;ng theo điều kiện FOB?&hellip; Chắc hẳn mỗi khi vận chuyển h&agrave;ng h&oacute;a qua container đường biển, c&aacute;c b&ecirc;n giao nhận vận tải lu&ocirc;n hỏi h&agrave;ng của bạn l&agrave; LCL hay FCL? Vậy ch&uacute;ng l&agrave; g&igrave; v&agrave; trường hợp n&agrave;o d&ugrave;ng? Ch&uacute;ng ta sẽ l&agrave;m r&otilde; r&agrave;ng b&agrave;i viết sau đ&acirc;y của Ad nh&eacute;.</p>\r\n\r\n<p>FCL v&agrave; LCL l&agrave; hai thuật ngữ để n&oacute;i đến c&aacute;c l&ocirc; h&agrave;ng vận chuyển bằng container.</p>\r\n\r\n<p>FCL l&agrave; thuật ngữ viết tắt của Full Container Load tức l&agrave; h&agrave;ng h&oacute;a của bạn được đ&oacute;ng ri&ecirc;ng trong 1 container m&agrave; kh&ocirc;ng chung với h&agrave;ng của người kh&aacute;c.</p>\r\n\r\n<p>LCL l&agrave; viết tắt của từ Less than Container Load n&oacute;i đến l&ocirc; h&agrave;ng lẻ, thường được gom từ nhiều chủ h&agrave;ng kh&aacute;c nhau trong c&ugrave;ng một container.</p>\r\n\r\n<h2><strong>LCL sẽ gi&uacute;p bạn duy tr&igrave; tồn kho ở mức thấp</strong></h2>\r\n\r\n<p>Nếu bạn kh&ocirc;ng c&oacute; vốn khi nhập h&agrave;ng hoặc kh&ocirc;ng c&oacute; diện t&iacute;ch kho chứa h&agrave;ng lớn cho cả container, đ&oacute; l&agrave; l&uacute;c bạn n&ecirc;n nghĩ đến LCL (h&agrave;ng lẻ). Thay v&igrave; phải mua số lượng lớn từ nh&agrave; cung cấp, bạn c&oacute; thể mua th&agrave;nh c&aacute;c l&ocirc; h&agrave;ng lẻ v&agrave; lu&ocirc;n giữ tồn kho ở mức ổn định v&agrave; c&oacute; t&iacute;nh chủ động. Nguồn vốn nhập h&agrave;ng l&uacute;c n&agrave;y c&oacute; thể được lưu chuyển cho c&aacute;c mục đ&iacute;ch kh&aacute;c ngắn hạn, tr&aacute;nh đọng vốn tại kho h&agrave;ng qu&aacute; nhiều.</p>\r\n\r\n<h2><strong>FCL v&agrave;&nbsp;LCL, h&agrave;ng n&agrave;o rẻ hơn?</strong></h2>\r\n\r\n<p>Điều đ&oacute; đ&uacute;ng, LCL c&oacute; cước ph&iacute; đắt hơn FCL v&agrave; được t&iacute;nh theo gi&aacute; cước của mỗi khối h&agrave;ng hoặc trọng lượng h&agrave;ng. Điều đ&oacute; v&igrave; c&aacute;c c&ocirc;ng ty giao nhận, h&atilde;ng t&agrave;u muốn vận chuyển nguy&ecirc;n container hơn l&agrave; phải t&iacute;nh to&aacute;n l&agrave;m sao để c&aacute;c l&ocirc; h&agrave;ng lẻ c&oacute; thể xếp vừa trong một container. Th&ecirc;m v&agrave;o đ&oacute;, rất nhiều loại chi ph&iacute; của h&agrave;ng lẻ đ&atilde;&nbsp; được cố định kh&ocirc;ng kể trong container c&oacute; nhiều&nbsp; hay &iacute;t h&agrave;ng.</p>\r\n\r\n<h2><strong>FCL v&agrave; LCL,</strong><strong> </strong><strong>h&agrave;ng n&agrave;o vận chuyển nhanh hơn?</strong></h2>\r\n\r\n<p>Khi l&ocirc; h&agrave;ng FCL được đến Cảng đ&iacute;ch, ch&uacute;ng sẽ được dỡ xuống khỏi t&agrave;u v&agrave; được giao đến người mua. Nhưng đối với h&agrave;ng LCL th&igrave; quy tr&igrave;nh n&agrave;y phức tạp v&agrave; mất nhiều thời gian hơn. Người ta phải kiểm đếm h&agrave;ng lẻ từ c&aacute;c l&ocirc; h&agrave;ng kh&aacute;c, c&aacute;c chủ h&agrave;ng kh&aacute;c nhau, h&agrave;ng loạt c&aacute;c giấy tờ chỉ cho một container, sau đ&oacute; sắp xếp để giao cho từng chủ h&agrave;ng. Mỗi kh&acirc;u trong quy tr&igrave;nh nhận h&agrave;ng lẻ cũng c&oacute; thể bị delay v&igrave; nguy&ecirc;n nh&acirc;n n&agrave;o đ&oacute; m&agrave; ảnh hưởng đến to&agrave;n bộ kh&aacute;ch h&agrave;ng c&oacute; h&agrave;ng trong container đ&oacute;. V&iacute; dụ, tại Cảng xếp h&agrave;ng, h&agrave;ng h&oacute;a phải được nh&oacute;m lại v&agrave; xếp l&ecirc;n container. Tại Cảng đến, rủi ro lớn nhất lại từ bước th&ocirc;ng quan nhập khẩu hoặc kiểm h&oacute;a. Khi một l&ocirc; h&agrave;ng trong container c&oacute; vấn đề sẽ khiến to&agrave;n bộ c&aacute;c h&agrave;ng h&oacute;a c&ograve;n lại cũng đều bị kiểm tra. Thời gian nhận h&agrave;ng sẽ bị tr&igrave; ho&atilde;n từ v&agrave;i ng&agrave;y cho đến cả tuần. Trong kinh doanh th&igrave; thời gian qu&yacute; hơn tiền bạc. Chỉ cần c&ugrave;ng một mặt h&agrave;ng đang Hot tr&ecirc;n thị trường m&agrave; đối thủ của bạn đưa ra thị trường trước bạn th&igrave; h&agrave;ng h&oacute;a của bạn nhập về đ&atilde; bị k&eacute;m t&iacute;nh cạnh tranh hơn.</p>\r\n\r\n<h2><strong>LCL l&agrave;m tăng rủi ro h&agrave;ng h&oacute;a bị hư hỏng</strong></h2>\r\n\r\n<p>Nếu l&ocirc; h&agrave;ng l&agrave; LCL, bạn kh&ocirc;ng c&oacute; quyền được chọn chỗ cho h&agrave;ng h&oacute;a của ri&ecirc;ng m&igrave;nh. Điều đ&oacute; c&oacute; thể g&acirc;y nguy hiểm trong l&uacute;c vận chuyển với một số loại h&agrave;ng đặc biệt như chất lỏng, h&agrave;ng nặng, h&agrave;ng c&oacute; m&ugrave;i đặc biệt&hellip;Thay v&igrave; biết ch&iacute;nh x&aacute;c h&agrave;ng g&igrave; được đặt trong container như FCL th&igrave; với LCL bạn sẽ cần thận trọng với c&aacute;c rủi ro chưa biết trước g&acirc;y nguy hại cho h&agrave;ng h&oacute;a của m&igrave;nh. Th&ecirc;m v&agrave;o đ&oacute;, ch&iacute;nh v&igrave; kh&ocirc;ng biết ch&iacute;nh x&aacute;c vị tr&iacute; của h&agrave;ng h&oacute;a trong container n&ecirc;n đ&ocirc;i khi cũng sẽ xảy ra sự nhầm lẫn, thất lạc h&agrave;ng h&oacute;a. Chưa n&oacute;i đến ai sẽ chịu tr&aacute;ch nhiệm nhưng với chủ h&agrave;ng th&igrave; đ&atilde; kh&ocirc;ng c&oacute; h&agrave;ng để b&aacute;n.</p>\r\n\r\n<p>Vậy hy vọng với b&agrave;i viết n&agrave;y c&aacute;c em sẽ c&oacute; th&ecirc;m nhiều kiến thức về vận tải h&agrave;ng h&oacute;a bằng container.</p>\r\n\r\n<p>Mrs Jade.</p>','/9e/c3/39/5a6a1a68c60d6.jpg',NULL,3490,0,'2018-01-26 00:00:00',NULL,'FCL và LCL là hai thuật ngữ để nói đến các lô hàng vận chuyển bằng container.\r\nFCL là thuật ngữ viết tắt của Full Container Load tức là hàng hóa của bạn được đóng riêng trong 1 container mà không chung với hàng của người khác.\r\nLCL là viết tắt của từ Less','Hàng nguyên cont hay hàng lẻ? FCL và LCL là gì? so sánh FCL và LCL, những chú ý khi gửi hàng bằng FCL và LCL','','',2,0,'vi','nhung-dieu-can-biet-ve-hang-fcl-va-lcl',6,27,26,'2018-01-26 00:56:56','2018-03-03 20:05:13'),(237,'NHỮNG LƯU Ý QUAN TRỌNG KHI ĐỌC CHÚ GIẢI TRONG PHÂN LOẠI HÀNG HÓA VÀ TRA MÃ HS','','Để phân loại hàng hóa vào đúng mã HS, điều quan trọng đầu tiên chúng ta cần nắm được là cách đọc các chú giải trong Danh mục HS. Mỗi dấu chấm, dấu phẩy, dấu chấm phẩy trong các chú giải này đều có ý nghĩa phân định phạm vi phân loại hàng hóa rất rõ ràng.','<p>Cấu tr&uacute;c danh mục HS gồm c&oacute; 22 Phần, trong mỗi Phần c&oacute; c&aacute;c Chương. C&aacute;c Chương được xếp theo số thứ tự từ 01 đến 97, chương 98 l&agrave; chương bổ sung:&nbsp;Qui định m&atilde; số v&agrave;&nbsp;<strong><u>mức thuế suất nhập khẩu ưu đ&atilde;i ri&ecirc;ng</u></strong>&nbsp;đối với một số nh&oacute;m mặt h&agrave;ng, mặt h&agrave;ng.</p>\r\n\r\n<p>Cấu tr&uacute;c m&atilde; HS gồm 8 chữ số, bắt đầu bằng 2 chữ số của Chương, m&atilde; HS kh&ocirc;ng thể hiện số thứ tự của Phần.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/26.%20doc%20chu%20giai%20HS/cac-tra-ma-hs-va-doc-chu-giai.jpg\" style=\"height:413px; width:550px\" /></p>\r\n\r\n<p><em>Cấu tr&uacute;c m&atilde; HS</em></p>\r\n\r\n<p>Dựa v&agrave;o c&aacute;ch diễn giải ở c&aacute;c ch&uacute; giải, ta c&oacute; được c&aacute;c chỉ dẫn để t&igrave;m đến Chương, Nh&oacute;m chứa mặt h&agrave;ng ta đang t&igrave;m kiếm. C&aacute;c ch&uacute; giải Chương v&agrave; Ph&acirc;n chương (nếu c&oacute;) c&oacute; t&iacute;nh chất quyết định đến việc ph&acirc;n loại h&agrave;ng h&oacute;a (PLHH), &aacute;p dụng cho to&agrave;n bộ&nbsp;<a href=\"https://web.archive.org/web/20170312181315/http:/tuhocxuatnhapkhau.com/6-quy-tac-phan-loai-hang-hoa-ap-ma-hs/\">6&nbsp;quy tắc PLHH</a>. Trong ch&uacute; giải&nbsp;<strong>Phần, Chương, Ph&acirc;n nh&oacute;m</strong>&nbsp;thường bắt gặp c&aacute;c loại ch&uacute; giải diễn giải sau:</p>\r\n\r\n<p><em>Ch&uacute; giải định nghĩa</em>: c&aacute;c từ thường gặp:&nbsp;<em>c&oacute; nghĩa l&agrave;, được hiểu l&agrave;, nghĩa l&agrave;, kh&aacute;i niệm</em></p>\r\n\r\n<p>VD<em>:&nbsp;</em></p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p>1. Kh&aacute;i niệm &ldquo;l&uacute;a m&igrave; durum&rdquo;&nbsp;<em>c&oacute; nghĩa l&agrave;</em>&nbsp;loại l&uacute;a m&igrave; thuộc lo&agrave;i Triticum durum v&agrave; c&aacute;c giống lai tạo từ việc kết hợp giữa c&aacute;c lo&agrave;i của Triticum durum c&oacute; c&ugrave;ng số nhiễm sắc thể (28) như những lo&agrave;i đ&oacute;.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong><u>Ch&uacute; giải loại trừ</u></strong>: c&aacute;c từ thường gặp:&nbsp;<strong><u>kh&ocirc;ng bao gồm,</u></strong>&nbsp;<strong><u>trừ,</u></strong>&nbsp;<strong><u>loại trừ,</u></strong>&nbsp;<strong><u>kh&ocirc;ng &aacute;p dụng</u></strong>,&nbsp;<strong><u>kh&ocirc;ng thuộc</u></strong></p>\r\n\r\n<p>VD:</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p>Nh&oacute;m n&agrave;y&nbsp;<strong><u>kh&ocirc;ng &aacute;p dụng</u></strong>&nbsp;cho:</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p>(a) Chất chiết xuất từ c&acirc;y cam thảo c&oacute; h&agrave;m lượng tr&ecirc;n 10% t&iacute;nh theo trọng lượng l&agrave; đường sucroza hoặc kẹo (nh&oacute;m 17.04);</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p>(b) Chất chiết xuất từ malt (nh&oacute;m 19.01);</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p>1. Chương n&agrave;y&nbsp;<strong><u>kh&ocirc;ng bao gồm</u></strong>&nbsp;c&aacute;c sản phẩm sau đ&acirc;y đ&atilde; được xếp trong Phần XI: vật liệu thực vật hoặc xơ từ nguy&ecirc;n liệu thực vật đ&atilde; chế biến chủ yếu d&ugrave;ng cho ng&agrave;nh dệt, hoặc c&aacute;c vật liệu thực vật kh&aacute;c, đ&atilde; xử l&yacute; th&iacute;ch hợp chỉ để d&ugrave;ng l&agrave;m vật liệu dệt.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><em>Ch&uacute; giải bao gồm</em>: c&aacute;c từ thường gặp: bao gồm, kể cả, thuộc, &aacute;p dụng</p>\r\n\r\n<p>VD:</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p>C&aacute;c bộ phận của gi&agrave;y, d&eacute;p (<em>kể cả&nbsp;</em>mũi gi&agrave;y đ&atilde; hoặc chưa gắn đế trừ đế ngo&agrave;i); miếng l&oacute;t của gi&agrave;y, d&eacute;p c&oacute; thể th&aacute;o rời, g&oacute;t gi&agrave;y v&agrave; c&aacute;c sản phẩm tương tự; ghệt, quần &ocirc;m s&aacute;t ch&acirc;n v&agrave; c&aacute;c sản phẩm tương tự, v&agrave; c&aacute;c bộ phận của ch&uacute;ng.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p>7. Nh&oacute;m 59.11&nbsp;<em>&aacute;p dụng đối với c&aacute;c loại h&agrave;ng ho&aacute; dưới đ&acirc;y</em>, những loại h&agrave;ng ho&aacute; n&agrave;y kh&ocirc;ng xếp v&agrave;o bất kỳ nh&oacute;m n&agrave;o kh&aacute;c của Phần XI:</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><em><strong>Ch&uacute; giải định hướng</strong></em>:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p>C&aacute;c sản phẩm kh&ocirc;ng giống như vậy&nbsp;<strong><em>sẽ được xếp v&agrave;o nh&oacute;m</em></strong>&nbsp;23.02. Tuy nhi&ecirc;n, mầm ngũ cốc nguy&ecirc;n dạng, đ&atilde; được xay, vỡ mảnh hoặc nghiền, lu&ocirc;n lu&ocirc;n được ph&acirc;n loại v&agrave;o nh&oacute;m 11.04.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p>2. Xỉ, tro v&agrave; cặn chứa arsen, thuỷ ng&acirc;n, tali hoặc c&aacute;c hỗn hợp của ch&uacute;ng, thuộc loại d&ugrave;ng để t&aacute;ch arsen hoặc những kim loại tr&ecirc;n hoặc d&ugrave;ng để sản xuất c&aacute;c hợp chất ho&aacute; học của ch&uacute;ng,&nbsp;<strong><em>được ph&acirc;n loại v&agrave;o ph&acirc;n nh&oacute;m</em></strong>&nbsp;2620.60.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Vai tr&ograve; của dấu c&acirc;u trong Ch&uacute; giải: Gi&uacute;p ta hiểu đ&uacute;ng c&aacute;c m&ocirc; tả trong ch&uacute; giải, ph&acirc;n t&aacute;ch từng mặt h&agrave;ng trong m&ocirc; tả hoặc c&aacute;c ti&ecirc;u ch&iacute; được liệt k&ecirc;.</p>\r\n\r\n<ul>\r\n	<li>Dấu chấm phẩy (;) thể hiện sự ngắt c&acirc;u, th&agrave;nh c&aacute;c phần độc lập, kh&ocirc;ng li&ecirc;n quan g&igrave; đến nhau, kh&aacute;c biệt nhau ho&agrave;n to&agrave;n</li>\r\n</ul>\r\n\r\n<p>VD: Ch&uacute; giải nh&oacute;m 4202: Nh&oacute;m mặt h&agrave;ng trước dấu (;) kh&ocirc;ng đề cập đến chất liệu sản phẩm, c&oacute; nghĩa l&agrave; c&aacute;c mặt h&agrave;ng c&oacute; t&ecirc;n trong nh&oacute;m n&agrave;y kh&ocirc;ng c&oacute; y&ecirc;u cầu về&nbsp;chất liệu; nh&oacute;m mặt h&agrave;ng sau dấu (;) c&oacute; y&ecirc;u cầu về chất liệu, v&igrave; vậy, c&aacute;c mặt h&agrave;ng nằm sau dấu (;) cần phải được l&agrave;m r&otilde; chất liệu v&agrave; đ&uacute;ng c&aacute;c chất liệu được liệt k&ecirc; th&igrave; mới đủ điều kiện xếp v&agrave;o Nh&oacute;m 4202.</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:600px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:331px\">\r\n			<p><strong>H&ograve;m, va ly, xắc đựng đồ nữ trang, cặp t&agrave;i liệu, cặp s&aacute;ch, t&uacute;i cặp học sinh, bao k&iacute;nh, bao ống nh&ograve;m, hộp camera, hộp nhạc cụ, bao s&uacute;ng, bao s&uacute;ng ngắn mắc v&agrave;o y&ecirc;n ngựa v&agrave; c&aacute;c loại đồ chứa tương tự</strong>;&nbsp;<em>t&uacute;i du lịch, t&uacute;i đựng đồ ăn hoặc đồ uống c&oacute; phủ lớp c&aacute;ch, t&uacute;i đựng đồ vệ sinh c&aacute; nh&acirc;n, ba l&ocirc;, t&uacute;i x&aacute;ch tay, t&uacute;i đi chợ, xắc cốt, v&iacute;, t&uacute;i đựng bản đồ, hộp đựng thuốc l&aacute; điếu, hộp đựng thuốc l&aacute; sợi, t&uacute;i để dụng cụ, t&uacute;i thể thao, t&uacute;i đựng chai rượu, hộp đựng đồ trang sức, hộp đựng phấn, hộp đựng dao k&eacute;o v&agrave; c&aacute;c loại t&uacute;i hộp tương tự bằng da thuộc hoặc da thuộc tổng hợp, bằng tấm plastic, bằng vật liệu dệt, bằng sợi lưu h&oacute;a hoặc bằng b&igrave;a, hoặc được phủ to&agrave;n bộ hay chủ yếu&nbsp;</em><strong><em>bằng c&aacute;c vật liệu tr&ecirc;n hoặc bằng giấy.</em></strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<ul>\r\n	<li>Dấu hai chấm: bắt đầu một định nghĩa hoặc một nh&oacute;m từ được liệt k&ecirc;</li>\r\n	<li>Dấu phẩy: liệt k&ecirc; c&aacute;c nh&oacute;m từ c&ugrave;ng cấp độ ph&acirc;n loại</li>\r\n</ul>\r\n\r\n<p>Như vậy, để kh&ocirc;ng bị nhầm lẫn trong qu&aacute; tr&igrave;nh lựa chọn m&atilde; HS ph&ugrave; hợp, ngo&agrave;i việc nắm bắt được t&iacute;nh chất, đặc trưng của mặt h&agrave;ng, ta phải biết c&aacute;ch đọc đ&uacute;ng v&agrave; hiểu đ&uacute;ng c&aacute;c diễn đạt của Ch&uacute; giải đi k&egrave;m.</p>\r\n\r\n<p>Ms Sarah.</p>','/44/6a/a4/5a6a1be798429.jpg',NULL,2370,0,'2018-01-26 00:00:00',NULL,'Cấu trúc danh mục HS gồm có 22 Phần, trong mỗi Phần có các Chương. Các Chương được xếp theo số thứ tự từ 01 đến 97, chương 98 là chương bổ sung: Qui định mã số và mức thuế suất nhập khẩu ưu đãi riêng đối với một số nhóm mặt hàng, mặt hàng.\r\n\r\nCấu trúc mã','chú giải định hướng, chú giải định nghĩa, chú giải loại trừ, chú giải bao gồm, chú giải phần, chú giải chương, chú giải phân nhóm, phân loại hàng hóa, tra mã HS','','',2,0,'vi','nhung-luu-y-quan-trong-khi-doc-chu-giai-trong-phan-loai-hang-hoa-va-tra-ma-hs',8,27,26,'2018-01-26 01:03:19','2018-03-03 20:06:49'),(238,'PHÂN BIỆT SỰ KHÁC NHAU GIỮA PRE-CARRIAGE VÀ ON-CARRIAGE','','Chắc hẳn với các bạn làm trong nghề Xuất nhập khẩu- logistics thì thuật ngữ Carriage, Pre-Carriage và On-Carriage bạn đã nghe rất nhiều. Nhưng hãy cùng Ad tổng hợp lại xem chúng có gì khác nhau bởi nó ảnh hưởng trực tiếp tới các bên như:','<p>Chắc hẳn với c&aacute;c bạn l&agrave;m trong nghề Xuất nhập khẩu- logistics th&igrave; thuật ngữ Carriage, Pre-Carriage v&agrave; On-Carriage bạn đ&atilde; nghe rất nhiều. Nhưng h&atilde;y c&ugrave;ng Ad tổng hợp lại xem ch&uacute;ng c&oacute; g&igrave; kh&aacute;c nhau bởi n&oacute; ảnh hưởng trực tiếp tới c&aacute;c b&ecirc;n như:</p>\r\n\r\n<p>+ Nh&agrave; xuất khẩu</p>\r\n\r\n<p>+ Nh&agrave; nhập khẩu</p>\r\n\r\n<p>+ C&ocirc;ng ty giao vận</p>\r\n\r\n<p>+ H&atilde;ng t&agrave;u</p>\r\n\r\n<p>+ Đại l&yacute; hải quan</p>\r\n\r\n<p>+ C&aacute;c c&ocirc;ng ty bảo hiểm</p>\r\n\r\n<p>+ Ng&acirc;n h&agrave;ng</p>\r\n\r\n<p>V&agrave; c&aacute;c b&ecirc;n li&ecirc;n quan kh&aacute;c, ch&uacute;ng cần được hiểu một c&aacute;ch s&acirc;u sắc nhằm đảm bảo quyền lợi, nghĩa vụ, tr&aacute;ch nhiệm, rủi ro, chi ph&iacute; của c&aacute;c b&ecirc;n li&ecirc;n quan kh&aacute;c&hellip;</p>\r\n\r\n<p>Về cơ bản, sự kh&aacute;c biệt đ&oacute; l&agrave;:</p>\r\n\r\n<p><strong>Pre-Carriage: l&agrave; việc vận chuyển nội địa container từ kho của người b&aacute;n đến trước khi h&agrave;ng được giao l&ecirc;n t&agrave;u.</strong></p>\r\n\r\n<p>V&iacute; dụ: Một container rỗng được điều động bằng xe rơ mi từ h&atilde;ng t&agrave;u/ b&atilde;i cảng đến kho của người xuất khẩu tại Gia L&acirc;m, H&agrave; Nội để đ&oacute;ng h&agrave;ng. Sau đ&oacute;, container lại được chuy&ecirc;n chở ra Cảng Hải Ph&ograve;ng th&igrave; qu&aacute; tr&igrave;nh đ&oacute; gọi l&agrave; &ldquo;pre-carriage&rdquo;.</p>\r\n\r\n<p>Nếu H&atilde;ng t&agrave;u c&oacute; dịch vụ vận chuyển Pre-Carriage cho chủ h&agrave;ng được gọi l&agrave; Carrier Haulage (Người chuy&ecirc;n chở giao h&agrave;ng) th&igrave; trong trường hợp n&agrave;y tr&ecirc;n vận đơn sẽ show th&ocirc;ng tin Nơi nhận h&agrave;ng l&agrave; Gia L&acirc;m, H&agrave; Nội tại mục Place of Receipt</p>\r\n\r\n<p>C&ograve;n nếu việc vận chuyển n&agrave;y do ch&iacute;nh chủ h&agrave;ng hoặc qua dịch vụ của b&ecirc;n thứ ba th&igrave; được gọi l&agrave; Merchant Haulage (Người b&aacute;n giao h&agrave;ng), tr&ecirc;n vận đơn l&uacute;c n&agrave;y sẽ kh&ocirc;ng thể hiện th&ocirc;ng tin g&igrave; ở mục Place of Receipt.</p>\r\n\r\n<p><strong>Carriage: h&agrave;ng h&oacute;a được vận chuyển từ Cảng xếp h&agrave;ng (Port of Loading) đến Cảng dỡ h&agrave;ng (Port of Discharge)</strong></p>\r\n\r\n<p>V&iacute; dụ: H&agrave;ng h&oacute;a đ&oacute;ng container được vận chuyển từ Cảng Hải Ph&ograve;ng, Việt Nam đến Cảng Zhongshan, Trung Quốc.</p>\r\n\r\n<p><strong>On- Carriage: l&agrave; việc vận chuyển Container sau khi dỡ h&agrave;ng tại Cảng đến khi về kho của người nhận h&agrave;ng.</strong></p>\r\n\r\n<p>V&iacute; dụ: Sau khi container được dỡ xuống tại Cảng Hải Ph&ograve;ng, nh&agrave; nhập khẩu ho&agrave;n th&agrave;nh xong c&aacute;c thủ tục Hải quan v&agrave; cần đưa h&agrave;ng về kho của m&igrave;nh tại Vĩnh Y&ecirc;n, Vĩnh Ph&uacute;c.</p>\r\n\r\n<p>Nếu c&ocirc;ng đoạn n&agrave;y được thực hiện bởi H&atilde;ng t&agrave;u th&igrave; tr&ecirc;n vận đơn sẽ ghi ch&uacute; ở mục Place of Delivery: Vĩnh Y&ecirc;n, Vĩnh Ph&uacute;c. C&ograve;n nếu do ch&iacute;nh tự nh&agrave; Nhập khẩu thu&ecirc; xe container hoặc do Forwarder thực hiện th&igrave; mục Place of Delivery sẽ để trống.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/1.jpg\" style=\"height:524px; width:614px\" /></p>\r\n\r\n<p><em>C&aacute;ch ghi Place of Receipt v&agrave; Place of Delivery tr&ecirc;n vận đơn</em></p>\r\n\r\n<p>C&aacute;c bạn ch&uacute; &yacute; rằng, việc điền c&aacute;c th&ocirc;ng tin tr&ecirc;n Bill of Lading về Place of Receipt, Place of Delivery rất quan trọng v&igrave; n&oacute; li&ecirc;n quan đến tr&aacute;ch nhiệm của chủ h&atilde;ng hay người chuy&ecirc;n chở (H&atilde;ng t&agrave;u). V&iacute; dụ, h&atilde;ng t&agrave;u nhận việc b&agrave;n giao container rỗng cho nh&agrave; xuất khẩu về tận kho k&egrave;m theo dịch vụ vận chuyển h&agrave;ng sau khi đ&oacute;ng v&agrave;o container đến khi ra Cảng xếp h&agrave;ng; L&uacute;c n&agrave;y, nếu c&oacute; rủi ro về tai nạn, tổn thất li&ecirc;n quan đến Container hoặc h&agrave;ng h&oacute;a từ Nơi nhận h&agrave;ng (Place of Receipt) đến Cảng xếp h&agrave;ng (Port of Loading) ch&iacute;nh l&agrave; H&atilde;ng t&agrave;u.</p>\r\n\r\n<p>Mrs Jade.</p>\r\n\r\n<p>&nbsp;</p>','/9c/51/19/5a6de0220977e.jpg',NULL,4061,0,'2018-01-26 00:00:00',NULL,'Pre-Carriage: là việc vận chuyển nội địa container từ kho của người bán đến trước khi hàng được giao lên tàu.\r\nVí dụ: Một container rỗng được điều động bằng xe rơ mi từ hãng tàu/ bãi cảng đến kho của người xuất khẩu tại Gia Lâm, Hà Nội để đóng hàng.','Pre-Carriage là gì, On- Carriage là gì? vận chuyển nội địa, trucking, dịch vụ vận chuyển nội địa','','',2,0,'vi','phan-biet-su-khac-nhau-giua-pre-carriage-va-on-carriage',8,27,26,'2018-01-26 01:09:44','2018-03-03 20:09:01'),(239,'PHÂN LOẠI VÀ SO SÁNH CÁC LOẠI NHỜ THU, TRƯỜNG HỢP ÁP DỤNG','','Phân loại: có hai loại nhờ thu là nhờ thu trơn và nhờ thu kèm chứng từ.\r\n\r\nSo sánh các loại nhờ thu:\r\n\r\nGiống nhau: đều là phương thức thanh toán mà trong đó người có các khoản tiền phải thu ghi trên các công cụ thanh toán, nhưng không thể tự thu được từ người bị ký phát mà phải ủy thác cho ngân hàng thu hộ','<p><em>Ph&acirc;n loại:</em>&nbsp;c&oacute; hai loại nhờ thu l&agrave; nhờ thu trơn v&agrave; nhờ thu k&egrave;m chứng từ.</p>\r\n\r\n<p><em>So s&aacute;nh c&aacute;c loại nhờ thu:</em></p>\r\n\r\n<ul>\r\n	<li>Giống nhau: đều l&agrave; phương thức thanh to&aacute;n m&agrave; trong đ&oacute; người c&oacute; c&aacute;c khoản tiền phải thu ghi tr&ecirc;n c&aacute;c c&ocirc;ng cụ thanh to&aacute;n, nhưng kh&ocirc;ng thể tự thu được từ người bị k&yacute; ph&aacute;t m&agrave; phải ủy th&aacute;c cho ng&acirc;n h&agrave;ng thu hộ</li>\r\n</ul>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:605px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:151px\">\r\n			<p>Kh&aacute;c nhau</p>\r\n			</td>\r\n			<td style=\"width:184px\">\r\n			<p>Nhờ thu trơn</p>\r\n			</td>\r\n			<td style=\"width:270px\">\r\n			<p>Nhờ thu k&egrave;m chứng từ</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:151px\">\r\n			<p>Chứng từ nhờ thu</p>\r\n			</td>\r\n			<td style=\"width:184px\">\r\n			<p>Chứng từ t&agrave;i ch&iacute;nh</p>\r\n			</td>\r\n			<td style=\"width:270px\">\r\n			<p>Chứng từ t&agrave;i ch&iacute;nh v&agrave;/hoặc chứng từ thương mại</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:151px\">\r\n			<p>Vai tr&ograve; của Ng&acirc;n h&agrave;ng</p>\r\n			</td>\r\n			<td style=\"width:184px\">\r\n			<p>Chỉ đ&oacute;ng vai tr&ograve; thu hộ</p>\r\n			</td>\r\n			<td style=\"width:270px\">\r\n			<p>Ngo&agrave;i vai tr&ograve; l&agrave; người thu hộ th&igrave; c&ograve;n chịu tr&aacute;ch nhiệm khống chế chứng từ thương mại v&igrave; quyền lợi của người xuất khẩu</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:151px\">\r\n			<p>Mức độ rủi ro</p>\r\n			</td>\r\n			<td style=\"width:184px\">\r\n			<p>Cao, người nhận h&agrave;ng c&oacute; thể kh&ocirc;ng trả tiền hoặc chậm trễ trong việc thanh to&aacute;n</p>\r\n			</td>\r\n			<td style=\"width:270px\">\r\n			<p>Thấp hơn so với nhờ thu trơn, tuy nhi&ecirc;n vẫn c&oacute; rủi ro l&agrave; người nhập khẩu kh&ocirc;ng nhận h&agrave;ng</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Trường hợp &aacute;p dụng:</em>&nbsp;nhờ thu k&egrave;m chứng từ l&agrave; một phương thức rất phổ biến trong thanh to&aacute;n quốc tế, c&ograve;n nhờ thu trơn do chứa đựng nhiều rủi ro n&ecirc;n thường chỉ &aacute;p dụng trong c&aacute;c trường hợp:</p>\r\n\r\n<ul>\r\n	<li>Người hưởng lợi v&agrave; người trả tiền c&oacute; quan hệ thường xuy&ecirc;n v&agrave; phải tin cậy lẫn nhau</li>\r\n	<li>C&aacute;c giao dịch mang t&iacute;nh nội bộ (trong nội c&aacute;c c&ocirc;ng ty li&ecirc;n doanh hay c&aacute;c c&ocirc;ng ty mẹ c&ocirc;ng ty con)</li>\r\n	<li>Để thanh to&aacute;n c&aacute;c dịch vụ ph&iacute; m&agrave; người b&aacute;n cung cấp cho người mua</li>\r\n</ul>','/5e/52/25/5a6a1d9e58b85.jpg',NULL,6861,0,'2018-01-26 00:00:00',NULL,'Phân loại: có hai loại nhờ thu là nhờ thu trơn và nhờ thu kèm chứng từ. Giống nhau: đều là phương thức thanh toán mà trong đó người có các khoản tiền phải thu ghi trên các công cụ thanh toán, nhưng không thể tự thu được từ người bị ký phát mà phải ủy thác','phân loại các hình thức nhờ thu, nhờ thu trơn, nhờ thu kèm chứng từ, thanh toán quốc tế','','',2,0,'vi','phan-loai-va-so-sanh-cac-loai-nho-thu-truong-hop-ap-dung',7,27,26,'2018-01-26 01:10:38','2018-03-03 20:10:04'),(240,'QUY TRÌNH BỒI THƯỜNG BẢO HIỂM HÀNG HÓA','','Trong nghiệp vụ Xuất nhập khẩu, tùy từng điều kiện giao hàng được dẫn chiếu theo Incoterms mà Nhà xuất khẩu hoặc Nhà nhập khẩu sẽ mua Bảo hiểm hàng hóa. Nói đến lợi ích của Bảo hiểm thì ai cũng biết nhưng khi có tổn thất xảy ra các bên cần ứng xử thế nào thì không phải ai cũng biết. Trong bài viết này hôm nay, chúng ta sẽ cùng tìm hiểu về Quy trình bồi thường bảo hiểm hàng hóa của Công ty bảo hiểm.','<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/29.%20quy%20trinh%20boi%20thuong%20BH/Salvage6.jpg\" style=\"height:393px; width:625px\" /></p>\r\n\r\n<p><strong>C&aacute;c bước của quy tr&igrave;nh bồi thường h&agrave;ng h&oacute;a:</strong></p>\r\n\r\n<p>&ndash; Tiếp nhận hồ sơ khiếu nại từ kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p>&ndash; Kiểm tra t&iacute;nh hợp lệ của hồ sơ</p>\r\n\r\n<p>&ndash; T&iacute;nh to&aacute;n bồi thường</p>\r\n\r\n<p>&ndash; Tr&igrave;nh duyệt (một số trường hợp phải l&agrave;m tờ tr&igrave;nh bồi thường thương mại)</p>\r\n\r\n<p>&ndash; X&aacute;c b&aacute;o bồi thường, th&ocirc;ng b&aacute;o bồi thường</p>\r\n\r\n<p>&ndash; Th&ocirc;ng b&aacute;o bồi thường</p>\r\n\r\n<p>&ndash; Thanh to&aacute;n bồi thường</p>\r\n\r\n<p>&ndash; Đ&ograve;i bồi thường người thứ ba, xử l&iacute; t&agrave;i sản bị hư hỏng</p>\r\n\r\n<p><strong>Một số nội dung của c&ocirc;ng t&aacute;c bồi thường:</strong></p>\r\n\r\n<p><em><strong>1. Nguy&ecirc;n tắc bồi thường:</strong></em></p>\r\n\r\n<p>&ndash; STBH l&agrave; giới hạn tối đa của STBT. Tuy nhi&ecirc;n c&aacute;c khoản tiền sau (ngo&agrave;i số<br />\r\ntiền tổn thất) cũng được bồi thường như c&aacute;c chi ph&iacute; đ&atilde; chi ra để cứu vớt h&agrave;ng, chi ph&iacute; cứu nạn, ph&iacute; gi&aacute;m định, tiền đ&oacute;ng g&oacute;p TTC d&ugrave; STBT vượt qu&aacute; STBH.</p>\r\n\r\n<p>&ndash; Bồi thường bằng tiền kh&ocirc;ng bồi thường bằng hiện vật. Th&ocirc;ng thường nộp ph&iacute; bảo hiểm bằng loại tiền tệ n&agrave;o th&igrave; bồi thường sẽ bằng loại tiền tệ đ&oacute;.</p>\r\n\r\n<p>&ndash; Khi trả tiền bồi thường, người bảo hiểm sẽ khấu trừ c&aacute;c khoản tiền m&agrave; người được bảo hiểm đ&atilde; đ&ograve;i được ở người thứ ba.</p>\r\n\r\n<p><em><strong>=&gt;T&iacute;nh to&aacute;n bồi thường:</strong></em></p>\r\n\r\n<p>Sau khi x&aacute;c định tổn thất thuộc phạm vi tr&aacute;ch nhiệm bồi thường bảo hiểm, BTV sẽ dựa tr&ecirc;n cơ sở tổn thất hoặc ước t&iacute;nh, so s&aacute;nh STBH v&agrave; GTBH để x&aacute;c định đ&uacute;ng STBT.</p>\r\n\r\n<p><strong>2. Nguy&ecirc;n tắc chung bồi thường TTC</strong></p>\r\n\r\n<p>&ndash; Người bảo hiểm bồi thường cho người được bảo hiểm phần đ&oacute;ng g&oacute;p v&agrave;o TTC d&ugrave; h&agrave;ng được bảo hiểm theo điều kiện n&agrave;o. Nếu STBH thấp hơn gi&aacute; trị phải đ&oacute;ng g&oacute;p TTC, người bảo hiểm chỉ bồi thường theo tỉ lệ giữa STBH v&agrave; gi&aacute; trị phải đ&oacute;ng g&oacute;p TTC.</p>\r\n\r\n<p>&ndash; Kh&ocirc;ng bồi thường trực tiếp cho người được bảo hiểm m&agrave; thanh to&aacute;n cho người t&iacute;nh to&aacute;n TTC do h&atilde;ng t&agrave;u chỉ định.</p>\r\n\r\n<p>&ndash; STBT n&agrave;y được cộng th&ecirc;m hay khấu trừ phần ch&ecirc;nh lệch giữa số tiền đ&atilde; đ&oacute;ng g&oacute;p v&agrave; số tiền phải đ&oacute;ng g&oacute;p TTC.</p>\r\n\r\n<p><strong>3. Nguy&ecirc;n tắc chung bồi thường TTR</strong></p>\r\n\r\n<p>&ndash; Đối với TTTB thực tế: Bồi thường to&agrave;n bộ STBH.</p>\r\n\r\n<p>&ndash; Đối với TTTB ước t&iacute;nh: Bồi thường to&agrave;n bộ STBH nếu người được bảo hiểm từ bỏ h&agrave;ng. Trường hợp người được bảo hiểm kh&ocirc;ng từ bỏ h&agrave;ng hoặc xin từ bỏ nhưng người bảo hiểm kh&ocirc;ng chấp nhận, sẽ bồi thường theo mức độ tổn thất thực tế.</p>\r\n\r\n<p>&ndash; Với TTBP: Bồi thường số kiện, số bao h&agrave;ng bị thiếu, mất hay gi&aacute; trị trọng lượng số h&agrave;ng rời bị thiếu, mất hoặc bồi thường theo mức giảm gi&aacute; trị thương mại của phần h&agrave;ng bị tổn thất.</p>\r\n\r\n<p>&ndash; Tổn thất về chi ph&iacute;: ngo&agrave;i tổn thất h&agrave;ng do những rủi ro được bảo hiểm g&acirc;y ra, c&oacute; một số loại chi phi khi ph&aacute;t sinh được bồi thường (đ&atilde; đề cập ở mục 1.2.3 &ndash; C&aacute;c loại chi ph&iacute;). Ngo&agrave;i ra, nếu trong HĐBH c&oacute; ấn định mức miễn thường của c&ocirc;ng ty bảo hiểm th&igrave; khi tổn thất xảy ra, khi x&aacute;c định mức bồi thường phải t&iacute;nh đến mức miễn thường n&agrave;y.</p>\r\n\r\n<p><em><strong>=&gt; Hồ sơ bồi thường:</strong></em></p>\r\n\r\n<p>Hồ sơ bồi thường bao gồm c&aacute;c chứng từ ch&iacute;nh như sau:</p>\r\n\r\n<p>&ndash; Thư khiếu nại đ&ograve;i bồi thường của người được bảo hiểm</p>\r\n\r\n<p>&ndash; Bản ch&iacute;nh của HĐBH/ Đơn bảo hiểm v&agrave; giấy sửa đổi bổ sung (nếu c&oacute;)</p>\r\n\r\n<p>&ndash; Bản ch&iacute;nh của vận tải đơn v&agrave;/ hoặc Hợp đồng chuy&ecirc;n chở</p>\r\n\r\n<p>&ndash; Thư dự kh&aacute;ng/ th&ocirc;ng b&aacute;o tổn thất, Bi&ecirc;n bản gi&aacute;m định</p>\r\n\r\n<p>&ndash; Giấy bi&ecirc;n nhận của người chuy&ecirc;n chở khi giao h&agrave;ng v&agrave; phiếu ghi trọng lượng tại nơi nhận h&agrave;ng cuối c&ugrave;ng.</p>\r\n\r\n<p>&ndash; C&aacute;c chứng từ giao nhận h&agrave;ng của cảng hoặc của cơ quan chức năng</p>\r\n\r\n<p>&ndash; C&ocirc;ng văn, thư từ trao đổi của Người được bảo hiểm với Người chuy&ecirc;n chở v&agrave; c&aacute;c b&ecirc;n kh&aacute;c về tr&aacute;ch nhiệm của họ đối với tổn thất.</p>\r\n\r\n<p>&ndash; H&oacute;a đơn/ bi&ecirc;n lai v&agrave; c&aacute;c chi ph&iacute; kh&aacute;c</p>\r\n\r\n<p>Trường hợp c&aacute;c chứng từ trong hồ sơ khiếu nại chưa đủ sức thuyết phục để chứng minh tổn thất th&igrave; c&oacute; thể y&ecirc;u cầu kh&aacute;ch h&agrave;ng cung cấp th&ecirc;m một số chứng từ kh&aacute;c như: Hợp đồng mua b&aacute;n, thư t&iacute;n dụng, lược khai, nhật k&iacute; h&agrave;ng hải, phiếu kiểm đếm, giấy chứng nhận đăng kiểm, c&aacute;c bi&ecirc;n lai của cơ quan c&oacute; li&ecirc;n quan&hellip; Hồ sơ bồi thường phải được lưu trữ trong 10 năm.</p>\r\n\r\n<p><strong>DANH MỤC TỪ VIẾT TẮT</strong></p>\r\n\r\n<p><em>1. XNK: Xuất nhập khẩu</em></p>\r\n\r\n<p><em>2. HĐBH: Hợp đồng bảo hiểm</em></p>\r\n\r\n<p><em>3. BHPNT: Bảo hiểm hiểm phi nh&acirc;n thọ</em></p>\r\n\r\n<p><em>4. DNBH: Doanh nghiệp bảo hiểm</em></p>\r\n\r\n<p><em>5. TBH: T&aacute;i bảo hiểm</em></p>\r\n\r\n<p><em>6. ĐLBH: Đại l&iacute; bảo hiểm</em></p>\r\n\r\n<p><em>7. BHHH: Bảo hiểm h&agrave;ng h&oacute;a</em></p>\r\n\r\n<p><em>8. KTV: Khai th&aacute;c vi&ecirc;n</em></p>\r\n\r\n<p><em>9. BTV: Bồi thường vi&ecirc;n</em></p>\r\n\r\n<p><em>10. GĐV: gi&aacute;m định vi&ecirc;n</em></p>\r\n\r\n<p><em>11. ĐVKT: Đơn vị khai th&aacute;c</em></p>\r\n\r\n<p><em>12. ĐVGĐ: Đơn vị gi&aacute;m định</em></p>\r\n\r\n<p><em>13. TTBP: Tổn thất bộ phận</em></p>\r\n\r\n<p><em>14. TTTB: Tổn thất to&agrave;n bộ</em></p>\r\n\r\n<p><em>15. TTC: Tổn thất chung</em></p>\r\n\r\n<p><em>16. TTR: Tổn thất ri&ecirc;ng</em></p>\r\n\r\n<p><em>17. GTBH: Gi&aacute; trị bảo hiểm</em></p>\r\n\r\n<p><em>18. STBH: Số tiền bảo hiểm</em></p>\r\n\r\n<p><em>19. STBT: Số tiền bồi thường</em></p>','/59/f2/25/5a6a1e065bdf8.jpg',NULL,1867,0,'2018-01-26 00:00:00',NULL,'Trong nghiệp vụ Xuất nhập khẩu, tùy từng điều kiện giao hàng được dẫn chiếu theo Incoterms mà Nhà xuất khẩu hoặc Nhà nhập khẩu sẽ mua Bảo hiểm hàng hóa. Nói đến lợi ích của Bảo hiểm thì ai cũng biết nhưng khi có tổn thất xảy ra các bên cần ứng xử thế nào ','bảo hiểm hàng hóa xuất nhập khẩu, các loại bảo hiểm, tỷ lệ mua bảo hiểm, những rủi ro trong vận chuyển hàng hóa, ai nên mua bảo hiểm','','',2,0,'vi','quy-trinh-boi-thuong-bao-hiem-hang-hoa',8,27,26,'2018-01-26 01:12:22','2018-03-03 20:11:25'),(241,'QUY TRÌNH XỬ LÝ BOOKING REQUEST CỦA KHÁCH HÀNG TẠI CÔNG TY FORWARDER','','Sau khi nhà nhập khẩu và nhà xuất khẩu ký kết hợp đồng ngoại thương theo điều kiện giao hàng cụ thể nào đó theo Incoterm thì đó là lúc phát sinh vấn đề tìm Công ty vận tải. Nếu làm việc tại các Công ty vận tải Quốc tế (Forwarder hoặc Công ty Logistics) thì các em đang đóng vai trò là người xử lý các thông tin mà khách hàng hỏi (Booking Request).','<p>Như c&aacute;c bạn&nbsp;đ&atilde; biết, sau khi nh&agrave; nhập khẩu v&agrave; nh&agrave; xuất khẩu k&yacute; kết hợp đồng ngoại thương theo điều kiện giao h&agrave;ng cụ thể n&agrave;o đ&oacute; theo Incoterm th&igrave; đ&oacute; l&agrave; l&uacute;c ph&aacute;t sinh vấn đề t&igrave;m C&ocirc;ng ty vận tải. Nếu l&agrave;m việc tại c&aacute;c C&ocirc;ng ty vận tải Quốc tế (Forwarder hoặc C&ocirc;ng ty Logistics) th&igrave; c&aacute;c em đang đ&oacute;ng vai tr&ograve; l&agrave; người xử l&yacute; c&aacute;c th&ocirc;ng tin m&agrave; kh&aacute;ch h&agrave;ng hỏi (Booking Request).</p>\r\n\r\n<h2><strong>Nghiệp vụ xử l&yacute;&nbsp;Booking đ&oacute; diễn ra như sau:</strong></h2>\r\n\r\n<p><strong><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/30.%20booking-request/booking-request.jpg\" style=\"height:627px; width:1280px\" /></strong></p>\r\n\r\n<h2><strong>Bước 1: B&aacute;o gi&aacute;</strong></h2>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng điền v&agrave;o Booking Request, thường mỗi c&ocirc;ng ty Forwarder c&oacute; mẫu cho kh&aacute;ch h&agrave;ng điền hoặc nh&acirc;n vi&ecirc;n Sales cước sẽ trao đổi v&agrave; ghi lại th&ocirc;ng tin rồi điền gi&uacute;p kh&aacute;ch h&agrave;ng, sau đ&oacute; đưa cho kh&aacute;ch h&agrave;ng x&aacute;c nhận. Từ c&aacute;c th&ocirc;ng tin n&agrave;y, c&ocirc;ng ty Forwarder sẽ t&igrave;m c&aacute;c Shipping Line ph&ugrave; hợp. Qu&aacute; tr&igrave;nh n&agrave;y c&oacute; thể diễn ra từ khoảng 3 cho đến 12 tiếng sau khi nhận được Booking&nbsp;Request.</p>\r\n\r\n<p><strong>Mẫu Booking Request gồm c&aacute;c th&ocirc;ng tin cơ bản sau:</strong></p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:683px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<ul>\r\n				<li>Vessel Name&nbsp;&amp; Voyage (T&ecirc;n t&agrave;u v&agrave; số chuyến đi lựa chọn): nếu kh&aacute;ch h&agrave;ng đ&atilde; c&oacute; dự định chuyến t&agrave;u muốn Book</li>\r\n			</ul>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Port of Loading (Cảng xếp h&agrave;ng)</p>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Port of Discharge (Cảng dỡ h&agrave;ng)</p>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Container Type (Loại Container)</p>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Commodity (M&ocirc; tả h&agrave;ng h&oacute;a)</p>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Tonnage (Trọng tải)</p>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Payment Place- (Prepaid / Collect) (Nơi trả cước, ai trả? Cước trả trước hay trả sau)</p>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Stuffing Area&nbsp;&nbsp; &ndash;&nbsp;&nbsp; (&nbsp;Shipper&rsquo;s Premises&nbsp; /&nbsp;Port Area ): Nơi nhận h&agrave;ng c&oacute; thể l&agrave; tại kho nh&agrave; cung cấp, khu vực gần Cảng thường d&ugrave;ng trong trường hợp H&atilde;ng t&agrave;u nhận vận chuyển Inland cho l&ocirc; h&agrave;ng.</p>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Stuffing Date: Ng&agrave;y h&agrave;ng đi dự kiến</p>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Who will Perform Oncarriagea: ai l&agrave;m vận chuyển nội địa nước nhập khẩu</p>\r\n			</td>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:53px\">\r\n			<p>&nbsp;Adress Details: Địa chỉ</p>\r\n			</td>\r\n			<td style=\"height:53px\">&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><strong>Bước 2: Đ&agrave;m ph&aacute;n</strong></h2>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng v&agrave; C&ocirc;ng ty Forwarder sẽ đ&agrave;m ph&aacute;n với nhau về lịch t&agrave;u, gi&aacute; cước v&agrave; c&aacute;c điều kiện k&egrave;m theo sau đ&oacute; đi đến thỏa thuận bằng việc C&ocirc;ng ty Forwarder gửi cho kh&aacute;ch h&agrave;ng Booking Confirmation (X&aacute;c nhận đặt chỗ) v&agrave; y&ecirc;u cầu kh&aacute;ch h&agrave;ng x&aacute;c nhận lại qua mail.</p>\r\n\r\n<p><em>Mẫu booking confirm</em></p>\r\n\r\n<h2><strong>Bước 3: Li&ecirc;n hệ</strong></h2>\r\n\r\n<p>C&ocirc;ng ty Forwarder li&ecirc;n hệ với nh&agrave; cung cấp của kh&aacute;ch h&agrave;ng để x&aacute;c nhận lại c&aacute;c th&ocirc;ng tin trong Booking. Sau đ&oacute;, c&ocirc;ng ty Forwarder mới ch&iacute;nh thức đặt chỗ với h&atilde;ng t&agrave;u. Trong thực tế, qu&aacute; tr&igrave;nh li&ecirc;n hệ n&agrave;y được thực hiện qua email.</p>\r\n\r\n<h2><strong>Bước 4: Vận chuyển nội địa</strong></h2>\r\n\r\n<p>T&ugrave;y từng điều kiện giao h&agrave;ng m&agrave; C&ocirc;ng ty Forwarder hoặc ch&iacute;nh kh&aacute;ch h&agrave;ng sẽ l&agrave; người vận chuyển nội địa l&ocirc; h&agrave;ng ra Cảng đi.</p>\r\n\r\n<h2><strong>Bước 5: Thỏa thuận th&ecirc;m</strong></h2>\r\n\r\n<p>C&ocirc;ng ty Forwarder sẽ khai b&aacute;o hải quan gi&uacute;p kh&aacute;ch h&agrave;ng (nếu c&oacute;) v&agrave; bắt đầu theo d&otilde;i to&agrave;n bộ qu&aacute; tr&igrave;nh vận tải h&agrave;ng h&oacute;a từ khi h&agrave;ng l&ecirc;n t&agrave;u.</p>\r\n\r\n<h2><strong>Bước 6: Gửi Shipping Instruction</strong></h2>\r\n\r\n<p>C&ocirc;ng ty Forwarder y&ecirc;u cầu kh&aacute;ch h&agrave;ng gửi Shipping Instruction để từ đ&oacute; l&agrave;m Bill of Lading bản nh&aacute;p. Sau đ&oacute;, gửi Draft Bill of Lading cho kh&aacute;ch h&agrave;ng kiểm tra v&agrave; y&ecirc;u cầu kh&aacute;ch h&agrave;ng x&aacute;c nhận c&aacute;c th&ocirc;ng tin qua mail, kh&ocirc;ng qu&ecirc;n b&aacute;o với kh&aacute;ch h&agrave;ng rằng cần Confirm lại trước bao l&acirc;u nếu kh&ocirc;ng to&agrave;n bộ th&ocirc;ng tin coi như kh&ocirc;ng c&oacute; g&igrave; thay đổi.</p>\r\n\r\n<p>Mẫu Shipping Instruction</p>\r\n\r\n<h2><strong>Bước 8: Gửi th&ocirc;ng tin đến h&atilde;ng t&agrave;u l&agrave;m MBL</strong></h2>\r\n\r\n<p>Tại bước n&agrave;y, c&ocirc;ng ty Forwarder sẽ gửi th&ocirc;ng tin để h&atilde;ng t&agrave;u l&agrave;m Master Bill v&agrave; x&aacute;c nhận MBL cũng th&ocirc;ng qua một Shipping Instruction như ph&iacute;a tr&ecirc;n nhưng chỉ kh&aacute;c th&ocirc;ng tin Shipper v&agrave; Consignee</p>\r\n\r\n<h2><strong>Bước 9: Ph&aacute;t h&agrave;nh HBL</strong></h2>\r\n\r\n<p>C&ocirc;ng ty Forwarder sau khi nhận được MBL từ h&atilde;ng t&agrave;u, từ đ&oacute; sẽ ph&aacute;t h&agrave;nh HLB v&agrave; gửi cho kh&aacute;ch h&agrave;ng đồng thời y&ecirc;u cầu kh&aacute;ch h&agrave;ng thanh to&aacute;n c&aacute;c khoản ph&iacute; trước khi giao HBL gốc cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<h2><strong>Bước 10: Gửi HBL gốc</strong></h2>\r\n\r\n<p>Gửi đầy đủ 1 bộ Bill of Lading cho kh&aacute;ch h&agrave;ng, thường l&agrave; 03 bản gốc v&agrave; 03 bản sao, c&oacute; đầy đủ chữ k&yacute; của H&atilde;ng t&agrave;u, nh&agrave; vận chuyển hoặc đại diện nh&agrave; vận chuyển.</p>\r\n\r\n<p>Như vậy, dựa v&agrave;o quy tr&igrave;nh tr&ecirc;n c&aacute;c em thấy rằng tất cả những g&igrave; kh&aacute;ch h&agrave;ng phải l&agrave;m đ&oacute; l&agrave;: Đưa ra th&ocirc;ng tin để nhận b&aacute;o gi&aacute; (Booking Request)&ndash;&gt; Nhận Booking Confirmation&ndash;&gt;Trả tiền dịch vụ. C&aacute;c c&ocirc;ng đoạn hậu cần kh&aacute;c sẽ do C&ocirc;ng ty Forwarder v&agrave; H&atilde;ng t&agrave;u thực hiện.</p>\r\n\r\n<p>Hy vọng với b&agrave;i viết n&agrave;y c&aacute;c em đ&atilde; h&igrave;nh dung ra được quy tr&igrave;nh xử l&yacute; th&ocirc;ng tin Booking của c&aacute;c nh&agrave; vận tải Quốc tế, nếu đi s&acirc;u hơn nữa th&igrave; c&aacute;c em c&ograve;n bất ngờ v&igrave; sự tự động h&oacute;a trong c&aacute;c kh&acirc;u của chuỗi Logistics Quốc tế. Hẹn c&aacute;c em ở b&agrave;i viết kh&aacute;c nh&eacute;.</p>\r\n\r\n<p>Mrs Jade.</p>','/76/d5/57/5a6a1edc96554.jpg',NULL,10243,0,'2018-01-26 00:00:00',NULL,'Sau khi nhà nhập khẩu và nhà xuất khẩu ký kết hợp đồng ngoại thương theo điều kiện giao hàng cụ thể nào đó theo Incoterm thì đó là lúc phát sinh vấn đề tìm Công ty vận tải. Nếu làm việc tại các Công ty vận tải Quốc tế (Forwarder hoặc Công ty Logistics) th','booking request là gì, xử lý booking request, các nội dung chính trên booking request, từ vựng tiếng anh trên booking request','','',2,0,'vi','quy-trinh-xu-ly-booking-request-cua-khach-hang-tai-cong-ty-forwarder',8,27,26,'2018-01-26 01:15:56','2018-03-03 20:12:40'),(242,'QUYỀN VÀ NGHĨA VỤ CỦA NGƯỜI KÝ PHÁT VÀ NGƯỜI BỊ KÝ PHÁT HỐI PHIẾU','','Nếu thấu hiểu Quyền và nghĩa vụ của bên ký phát và bên bị  ký phát trong Hối phiếu sẽ đảm bảo lợi ích của từng bên. Bạn hãy cùng tìm hiểu điều này trong bài viết dưới đây do Ad tổng hợp nhé.','<p><strong>Nếu thấu hiểu Quyền v&agrave; nghĩa vụ của b&ecirc;n k&yacute; ph&aacute;t v&agrave; b&ecirc;n bị &nbsp;k&yacute; ph&aacute;t trong Hối phiếu sẽ đảm bảo lợi &iacute;ch của từng b&ecirc;n. Bạn h&atilde;y c&ugrave;ng t&igrave;m hiểu điều n&agrave;y trong b&agrave;i viết dưới đ&acirc;y do Ad tổng hợp nh&eacute;.</strong></p>\r\n\r\n<p><strong>Người k&iacute; ph&aacute;t</strong></p>\r\n\r\n<h3>&nbsp; &nbsp; &nbsp; Quyền lợi</h3>\r\n\r\n<ul>\r\n	<li>Tạo lập Hối phiếu để đ&ograve;i tiền Người bị ph&aacute;t hoặc bất cứ người n&agrave;o do anh ta chỉ định</li>\r\n	<li>Tạo lập Hối phiếu quy định việc trả tiền theo lệnh của Người k&iacute; ph&aacute;t hoặc theo lệnh của bất cứ người n&agrave;o do Người k&iacute; ph&aacute;t chỉ định.</li>\r\n	<li>Nhận tiền từ Người bị k&iacute; ph&aacute;t Hối phiếu</li>\r\n	<li>Xin chiết khấu Hối phiếu tại Ng&acirc;n h&agrave;ng để nhận tiền trước khi HP đến hạn trả tiền</li>\r\n	<li>Xin thế chấp Hối phiếu tại Ng&acirc;n h&agrave;ng để vay tiền</li>\r\n	<li>Chuyển nhượng quyền hưởng lợi HP cho một hay nhiều người hoặc hủy bỏ tờ HP</li>\r\n	<li>C&aacute;c quyền ph&aacute;p l&iacute; đối với c&aacute;c lợi &iacute;ch tương lai của HP như quyền khiếu nại trước T&ograve;a &aacute;n hay trọng t&agrave;i khi c&oacute; vi phạm</li>\r\n</ul>\r\n\r\n<h3>Nghĩa vụ</h3>\r\n\r\n<ul>\r\n	<li>Trường hợp HP đ&atilde; được chuyển nhượng cho người kh&aacute;c m&agrave; người đ&oacute; kh&ocirc;ng thu được tiền của HP th&igrave; người k&iacute; ph&aacute;t c&oacute; nghĩa vụ phải trả tiền cho người đ&oacute;</li>\r\n	<li>Ng k&iacute; ph&aacute;t đ&atilde; k&iacute; t&ecirc;n kh&ocirc;ng phải l&agrave; t&ecirc;n của m&igrave;nh sẽ phải chịu tr&aacute;ch nhiệm như thể l&agrave; k&iacute; t&ecirc;n của m&igrave;nh</li>\r\n	<li>Người k&iacute; ph&aacute;t c&oacute; thể phủ nhận hay hạn chế tr&aacute;ch nhiệm của m&igrave;nh bằng lời văn ghi tr&ecirc;n HP. Nhưng ,quy định n&agrave;y chỉ c&oacute; gi&aacute; trị r&agrave;ng buộc ri&ecirc;ng đối với người k&iacute; ph&aacute;t HP</li>\r\n</ul>\r\n\r\n<h2>Người bị k&iacute; ph&aacute;t</h2>\r\n\r\n<h3>Quyền lợi</h3>\r\n\r\n<ul>\r\n	<li>Chịu&nbsp;tr&aacute;ch nhiệm với HP trước khi k&iacute; chấp nhận thanh to&aacute;n HP</li>\r\n	<li>Chấp nhận hoặc từ chối trả tiền HP</li>\r\n	<li>Thu lại HP hoạc hủy bỏ n&oacute; sau khi đ&atilde; trả tiền HP</li>\r\n	<li>Thực hiện nghĩa vụ quy định tr&ecirc;n HP chỉ khi n&agrave;o HP đến hạn thanh to&aacute;n hoặc chấp nhận thanh to&aacute;n</li>\r\n	<li>Kiểm tra d&acirc;y chuyền chuyển nhượng xem c&oacute; hợp thức hay kh&ocirc;ng trước khi thực hiện nghĩa vụ trả tiền HP</li>\r\n</ul>\r\n\r\n<h3>Nghĩa vụ</h3>\r\n\r\n<ul>\r\n	<li>Trả tiền HP đối với HP trả tiền ngay khi xuất tr&igrave;nh</li>\r\n	<li>Chấp nhận thanh to&aacute;n đối với HP trả chậm sau khi HP được xuất tr&igrave;nh</li>\r\n	<li>Thực hiện c&aacute;c nghĩa vụ kh&aacute;c do Luật HP quy định</li>\r\n</ul>\r\n\r\n<p>Mrs Jade.</p>','/f0/44/4f/5a6a1f1c18ccc.jpg',NULL,1448,0,'2018-01-26 00:00:00',NULL,'Một số quyền lợi của Hối phiếu như: đòi tiền Người bị phát hoặc bất cứ người nào do anh ta chỉ định. Nhận tiền từ Người bị kí phát Hối phiếu Xin chiết khấu Hối phiếu tại Ngân hàng để nhận tiền trước khi HP đến hạn trả tiền','thanh toán quốc tế, người bị ký phát hối phiếu, người ký phát hối phiếu, hối phiếu trong thanh toán quốc tế, HP, hối phiếu','','',2,0,'vi','quyen-va-nghia-vu-cua-nguoi-ky-phat-va-nguoi-bi-ky-phat-hoi-phieu',7,27,26,'2018-01-26 01:17:00','2018-03-03 20:21:26'),(243,'SỰ KHÁC NHAU GIỮA PROFORMA INVOICE VÀ COMMERCIAL INVOICE','','Proforma Invoice được phát hành ngay khi Nhà xuất khẩu- Nhà nhập khẩu bắt đầu giao dịch và nó cũng không phải là hóa đơn hay chứng từ xác nhận cuối cùng. Nó có thể được sửa chữa, điều chỉnh ngay cả khi hai bên đã ký kết trên Hóa đơn chiếu lệ. Thông thường, Proforma Invoice được phát hành trước cả khi ký hợp đồng.','<p>C&aacute;c em th&acirc;n mến, khi học về c&aacute;c loại chứng từ trong Xuất nhập khẩu kh&ocirc;ng &iacute;t em đ&atilde; hỏi Ad c&aacute;ch ph&acirc;n biệt&nbsp;Proforma Invoice v&agrave; Commercial Invoice. Nay Ad sẽ gi&uacute;p em&nbsp;c&aacute;ch hiểu đơn giản nhất về hai loại chứng từ n&agrave;y:</p>\r\n\r\n<h2><strong>*Proforma Invoice l&agrave; g&igrave;?</strong></h2>\r\n\r\n<p>Proforma Invoice hay c&ograve;n gọi l&agrave; H&oacute;a đơn chiếu lệ, l&agrave; một bản dự thảo h&oacute;a đơn được soạn bởi Nh&agrave; xuất khẩu. N&oacute; c&oacute; thể bao gồm c&aacute;c điều khoản, điều kiện ch&iacute;nh của một giao dịch thương mại quốc tế.</p>\r\n\r\n<p>Proforma Invoice được ph&aacute;t h&agrave;nh ngay khi Nh&agrave; xuất khẩu- Nh&agrave; nhập khẩu bắt đầu giao dịch v&agrave; n&oacute; cũng kh&ocirc;ng phải l&agrave; h&oacute;a đơn hay chứng từ x&aacute;c nhận cuối c&ugrave;ng. N&oacute; c&oacute; thể được sửa chữa, điều chỉnh ngay cả khi hai b&ecirc;n đ&atilde; k&yacute; kết tr&ecirc;n H&oacute;a đơn chiếu lệ. Th&ocirc;ng thường, Proforma Invoice được ph&aacute;t h&agrave;nh trước cả khi k&yacute; hợp đồng. C&oacute; thể n&oacute;i Proforma Invoice l&agrave; những mặt h&agrave;ng m&agrave; nh&agrave; nhập khẩu đ&atilde; lựa chọn trong Quotation rồi sau đ&oacute; đ&agrave;m ph&aacute;n với đối t&aacute;c l&agrave; nh&agrave; xuất khẩu, sau đ&oacute; nh&agrave; xuất khẩu sẽ issue ra PI ( viết tắt của của Proforma Invoice). C&aacute;c em c&ugrave;ng nh&igrave;n mẫu PI dưới đ&acirc;y nh&eacute;:</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/Pi.png\" style=\"height:465px; width:785px\" /></p>\r\n\r\n<h2><strong>*Commercial Invoice trong thực tế</strong></h2>\r\n\r\n<p>H&oacute;a đơn thương mại cũng c&oacute; những chi tiết tương tự như H&oacute;a đơn chiếu lệ ngoại trừ một điểm đ&oacute; l&agrave; H&oacute;a đơn Thương mại chỉ được ph&aacute;t h&agrave;nh khi hai b&ecirc;n đ&atilde; ho&agrave;n th&agrave;nh giao dịch, đ&agrave;m ph&aacute;n, thậm ch&iacute; sau khi Nh&agrave; xuất khẩu đ&atilde; sản xuất v&agrave; xếp h&agrave;ng l&ecirc;n phương tiện vận tải v&agrave; n&oacute; kh&ocirc;ng được ph&eacute;p sửa chữa hoặc điều chỉnh t&ugrave;y tiện. Nếu c&oacute; sửa chữa th&igrave; phải c&oacute; x&aacute;c nhận của b&ecirc;n ph&aacute;t h&agrave;nh. Dưới đ&acirc;y l&agrave; mẫu Commercial Invoice</p>\r\n\r\n<p>Đến đ&acirc;y c&aacute;c em đ&atilde; ph&acirc;n biệt Proforma Invoice v&agrave; Commercial Invoice được chưa? Theo th&ocirc;ng lệ, tập qu&aacute;n Kinh doanh với một số thị trường, nhất l&agrave; thị trường Trung Quốc, Ad thấy rằng c&oacute; một số điểm sau c&aacute;c em n&ecirc;n ch&uacute; &yacute;:</p>\r\n\r\n<h3>&middot;<strong>Nếu một Proforma Invoice sửa đổi th&igrave; n&ecirc;n l&agrave;m thế n&agrave;o?</strong></h3>\r\n\r\n<p>Bạn c&oacute; thể thay đổi số Proforma Invoice Number</p>\r\n\r\n<p>V&iacute; dụ: ph&aacute;t h&agrave;nh lần 1 l&agrave; PI2101IMK01, ph&aacute;t h&agrave;nh lần 2 l&agrave; PI2101IMK02</p>\r\n\r\n<h3>&middot;<strong>V&igrave; sao Proforma Invoice hay c&oacute; sự điều chỉnh, thay đổi</strong></h3>\r\n\r\n<p>Thực tế, Proforma Invoice (viết tắt l&agrave; PI) c&oacute; chức năng giống như một b&aacute;o gi&aacute; của người xuất&nbsp; khẩu gửi cho người nhập khẩu. Sau khi nhận được PI, hai b&ecirc;n c&oacute; thể sẽ đ&agrave;m ph&aacute;n những nội dung sau li&ecirc;n quan đến việc sửa lại PI:</p>\r\n\r\n<ul>\r\n	<li>Nh&agrave; nhập khẩu nhận thấy gi&aacute; qu&aacute; cao v&agrave; y&ecirc;u cầu giảm gi&aacute;</li>\r\n	<li>Nh&agrave; nhập khẩu thấy thời gian giao h&agrave;ng qu&aacute; xa v&agrave; y&ecirc;u cầu ng&agrave;y giao h&agrave;ng gần hơn so với PI đầu</li>\r\n	<li>Nh&agrave; nhập khẩu thay đổi số lượng, c&oacute; thể tăng hoặc giảm của 1 model hoặc th&ecirc;m, bớt model.</li>\r\n	<li>Thay đổi phương thức vận chuyển, c&oacute; thể từ đường biển sang đường Air</li>\r\n	<li>Liệu c&oacute; thể d&ugrave;ng H&oacute;a đơn chiếu lệ thay thế cho H&oacute;a đơn thương mại để khai b&aacute;o Hải quan v&agrave; khi thanh to&aacute;n qua LC?</li>\r\n	<li>Kh&ocirc;ng thể d&ugrave;ng PI để khai b&aacute;o hải quan</li>\r\n	<li>Ti&ecirc;u đề của h&oacute;a đơn phải đề dưới dạng Invoice hoặc Commercial Invoice</li>\r\n	<li>Th&ecirc;m v&agrave;o đ&oacute;, trong LC thường c&oacute; quy định kh&ocirc;ng chấp nhận Proforma Invoice thay thế Commercial Invoice</li>\r\n</ul>\r\n\r\n<p>Hy vọng với b&agrave;i viết n&agrave;y c&aacute;c em đ&atilde; ph&acirc;n biệt được hai chứng từ n&agrave;y v&agrave; biết c&aacute;ch sử dụng cho ph&ugrave; hợp. C&aacute;c em c&oacute; thể tham khảo th&ecirc;m c&aacute;c b&agrave;i viết hữu &iacute;ch dưới đ&acirc;y v&agrave; đừng qu&ecirc;n Share với bạn b&egrave;.</p>\r\n\r\n<p>Mrs Jade.</p>','/00/e8/80/5a6a1f79138f0.jpg',NULL,20983,0,'2018-01-26 00:00:00',NULL,'Khi học về các loại chứng từ trong Xuất nhập khẩu không ít bạn băn khoăn cách phân biệt Proforma Invoice và Commercial Invoice. Sự thật để phân biệt chúng rất đơn giản. Proforma Invoice hay còn gọi là Hóa đơn chiếu lệ, là một bản dự thảo hóa đơn được soạn','Proforma Invoice và Commercial Invoice, Proforma Invoice là gì, ý nghĩa của Proforma Invoice, hóa đơn chiếu lệ là gì','','',2,0,'vi','su-khac-nhau-giua-proforma-invoice-va-commercial-invoice',6,27,26,'2018-01-26 01:18:33','2018-03-03 20:25:03'),(244,'THỎA THUẬN GIỮA NGƯỜI MUA VÀ NGƯỜI BÁN TRONG XUẤT KHẨU VÀ NHẬP KHẨU HÀNG HÓA','','Mở đầu bài viết, tôi có một câu hỏi \"Bạn là nhà Xuất khẩu hay Nhập khẩu\". Cho dù bạn đang nhập khẩu hoặc xuất khẩu hàng hóa, và dù bạn có hiểu đối tác của mình đến đâu thì bạn vẫn cần các thỏa thuận kinh doanh dưới dạng văn bản hay còn gọi là Hợp đồng mua bán, Hợp đồng ngoại thương','<p>Mở đầu b&agrave;i viết, t&ocirc;i c&oacute; một c&acirc;u hỏi &rdquo;Bạn l&agrave; nh&agrave; Xuất khẩu hay Nhập khẩu&rdquo;. Cho d&ugrave; bạn đang nhập khẩu hoặc xuất khẩu h&agrave;ng h&oacute;a, v&agrave; d&ugrave; bạn c&oacute; hiểu đối t&aacute;c của m&igrave;nh đến đ&acirc;u th&igrave; bạn vẫn cần c&aacute;c thỏa thuận kinh doanh dưới dạng văn bản hay c&ograve;n gọi l&agrave; Hợp đồng mua b&aacute;n,&nbsp;<strong>Hợp đồng ngoại thương</strong>. C&aacute;c bạn thường hay tự l&ecirc;n mạng v&agrave; copy lấy một số mẫu Hợp đồng ngoại thương nhưng đ&ocirc;i khi chưa thực sự hiểu về c&aacute;c điều khoản trong đ&oacute;. Một số điểm ch&iacute;nh sau đ&acirc;y cần phải được nh&agrave; nhập khẩu v&agrave; nh&agrave; xuất khẩu thỏa thuận, đ&agrave;m ph&aacute;n chi tiết trước khi ra một hợp đồng ho&agrave;n chỉnh:</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/1(1).jpg\" style=\"height:614px; width:630px\" /></p>\r\n\r\n<h2><strong>Điều khoản về sản phẩm, t&ecirc;n h&agrave;ng:</strong></h2>\r\n\r\n<p>Bạn cần phải biết r&otilde; về th&ocirc;ng số kỹ thuật, đặc t&iacute;nh của sản phẩm, c&agrave;ng ch&iacute;nh x&aacute;c bao nhi&ecirc;u c&agrave;ng tốt.&nbsp;Nếu h&agrave;ng h&oacute;a của bạn c&oacute; nhiều chủng loại th&igrave; khuyến kh&iacute;ch c&aacute;c bạn m&ocirc; tả phần n&agrave;y dưới dạng bảng biểu theo form dưới đ&acirc;y:</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" style=\"width:729px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:57px\">\r\n			<p><strong>No</strong></p>\r\n			</td>\r\n			<td style=\"width:154px\">\r\n			<p><strong>Description of goods</strong></p>\r\n			</td>\r\n			<td style=\"width:68px\">\r\n			<p><strong>Code</strong></p>\r\n			</td>\r\n			<td style=\"width:57px\">\r\n			<p><strong>CBM (unit)</strong></p>\r\n			</td>\r\n			<td style=\"width:75px\">\r\n			<p><strong>Weight (kg/unit)</strong></p>\r\n			</td>\r\n			<td style=\"width:94px\">\r\n			<p><strong>Quantity</strong></p>\r\n\r\n			<p><strong>(pcs)</strong></p>\r\n			</td>\r\n			<td style=\"width:110px\">\r\n			<p><strong>Prices (USD)</strong></p>\r\n\r\n			<p><strong>FOB HCM Port, VN, Incoterm 2010</strong></p>\r\n			</td>\r\n			<td style=\"width:115px\">\r\n			<p><strong>Amount (USD)</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:57px\">\r\n			<p>01</p>\r\n			</td>\r\n			<td style=\"width:154px\">\r\n			<p>Bamboo lamp</p>\r\n			</td>\r\n			<td style=\"width:68px\">\r\n			<p>BA76M</p>\r\n			</td>\r\n			<td style=\"width:57px\">\r\n			<p>0.5</p>\r\n			</td>\r\n			<td style=\"width:75px\">\r\n			<p>0.4</p>\r\n			</td>\r\n			<td style=\"width:94px\">\r\n			<p>200</p>\r\n			</td>\r\n			<td style=\"width:110px\">\r\n			<p>25.00</p>\r\n			</td>\r\n			<td style=\"width:115px\">\r\n			<p>5000</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:57px\">\r\n			<p>02</p>\r\n			</td>\r\n			<td style=\"width:154px\">\r\n			<p>Bamboo lamp</p>\r\n			</td>\r\n			<td style=\"width:68px\">\r\n			<p>BA76K</p>\r\n			</td>\r\n			<td style=\"width:57px\">\r\n			<p>0.7</p>\r\n			</td>\r\n			<td style=\"width:75px\">\r\n			<p>0.72</p>\r\n			</td>\r\n			<td style=\"width:94px\">\r\n			<p>300</p>\r\n			</td>\r\n			<td style=\"width:110px\">\r\n			<p>15.00</p>\r\n			</td>\r\n			<td style=\"width:115px\">\r\n			<p>4500</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:57px\">\r\n			<p>03</p>\r\n			</td>\r\n			<td style=\"width:154px\">\r\n			<p>Handmade bamboo handbag</p>\r\n			</td>\r\n			<td style=\"width:68px\">\r\n			<p>BH080</p>\r\n			</td>\r\n			<td style=\"width:57px\">\r\n			<p>0.9</p>\r\n			</td>\r\n			<td style=\"width:75px\">\r\n			<p>0.36</p>\r\n			</td>\r\n			<td style=\"width:94px\">\r\n			<p>1500</p>\r\n			</td>\r\n			<td style=\"width:110px\">\r\n			<p>8.50</p>\r\n			</td>\r\n			<td style=\"width:115px\">\r\n			<p>12750</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:57px\">\r\n			<p>04</p>\r\n			</td>\r\n			<td style=\"width:154px\">\r\n			<p>Bamboo handbag</p>\r\n			</td>\r\n			<td style=\"width:68px\">\r\n			<p>BH081</p>\r\n			</td>\r\n			<td style=\"width:57px\">\r\n			<p>0.3</p>\r\n			</td>\r\n			<td style=\"width:75px\">\r\n			<p>0.35</p>\r\n			</td>\r\n			<td style=\"width:94px\">\r\n			<p>1000</p>\r\n			</td>\r\n			<td style=\"width:110px\">\r\n			<p>10.00</p>\r\n			</td>\r\n			<td style=\"width:115px\">\r\n			<p>10000</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:57px\">\r\n			<p>05</p>\r\n			</td>\r\n			<td style=\"width:154px\">\r\n			<p>Basket</p>\r\n			</td>\r\n			<td style=\"width:68px\">\r\n			<p>CR008</p>\r\n			</td>\r\n			<td style=\"width:57px\">\r\n			<p>0.4</p>\r\n			</td>\r\n			<td style=\"width:75px\">\r\n			<p>0.27</p>\r\n			</td>\r\n			<td style=\"width:94px\">\r\n			<p>3000</p>\r\n			</td>\r\n			<td style=\"width:110px\">\r\n			<p>2.00</p>\r\n			</td>\r\n			<td style=\"width:115px\">\r\n			<p>6000</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"7\" style=\"width:614px\">\r\n			<p>Say in USD: Thirty eight thousands two hundreds and fifty US dollars</p>\r\n			</td>\r\n			<td style=\"width:115px\">\r\n			<p>38250</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><strong>Phạm vi l&atilde;nh thổ:&nbsp;</strong></h2>\r\n\r\n<p>Quy định về sự độc quyền trong ph&acirc;n phối sản phẩm. C&aacute;c bạn sẽ gặp nhiều trong mua b&aacute;n giao dịch c&aacute;c sản phẩm đ&atilde; c&oacute; thương hiệu từ trước.</p>\r\n\r\n<h2><strong>Gi&aacute;:&nbsp;</strong></h2>\r\n\r\n<p>Gi&aacute; h&agrave;ng h&oacute;a dưới điều kiện g&igrave;? Tham khảo Incoterms c&aacute;c phi&ecirc;n bản gần nhất. Mỗi điều kiện kh&aacute;c nhau th&igrave; gi&aacute; sẽ kh&aacute;c nhau. Điều&nbsp; n&agrave;y phụ thuộc khi người mua, người b&aacute;n c&oacute; nghĩa vụ v&agrave; tr&aacute;ch nhiệm l&agrave;m những phần c&ocirc;ng việc n&agrave;o ( vận chuyển, cước ph&iacute;, bốc dỡ&hellip;)</p>\r\n\r\n<h2><strong>Phương thức thanh to&aacute;n:</strong></h2>\r\n\r\n<p>Bạn sẽ sử dụng thư t&iacute;n dụng (L/C), hay Điện chuyển tiền (T/T), hoặc d&ugrave;ng Hối phiếu&nbsp; trong Nhờ thu (DA/DP)&hellip;</p>\r\n\r\n<h2><strong>Thời gian thanh to&aacute;n:</strong></h2>\r\n\r\n<p>Thời gian thanh to&aacute;n bạn sẽ quy định trả ngay hay trả chậm? trả sau khi giao h&agrave;ng bao l&acirc;u? H&atilde;y c&acirc;n nhắc kỹ điều n&agrave;y v&igrave; n&oacute; li&ecirc;n quan đến t&agrave;i ch&iacute;nh v&agrave; d&ograve;ng tiền trong c&ocirc;ng ty bạn.</p>\r\n\r\n<h2><strong>C&aacute;c yếu tố về thời gian thanh to&aacute;n rất quan trọng v&igrave; n&oacute; li&ecirc;n quan đến uy t&iacute;n của Doanh nghiệp:</strong></h2>\r\n\r\n<p>Nếu l&agrave; lần đầu ti&ecirc;n hai b&ecirc;n giao dịch với nhau, khuyến kh&iacute;ch c&aacute;c bạn d&ugrave;ng Phương&nbsp; thức thanh to&aacute;n&nbsp;<strong>T&iacute;n dụng chứng từ</strong>&nbsp;(L/C) hoặc T/T trả ngay khi h&agrave;ng giao l&ecirc;n t&agrave;u v&agrave; ph&aacute;t h&agrave;nh vận đơn c&oacute; chữ k&yacute; của H&atilde;ng t&agrave;u/ nh&agrave; chuy&ecirc;n chở.</p>\r\n\r\n<h2><strong>Điều kiện giao h&agrave;ng:</strong></h2>\r\n\r\n<p>Điều n&agrave;y quy định địa điểm bốc, dỡ h&agrave;ng h&oacute;a. C&oacute; cho ph&eacute;p chuyển tải hay kh&ocirc;ng? H&agrave;ng h&oacute;a c&oacute; được giao từng phần hay giao ngay một lần.</p>\r\n\r\n<h2><strong>Phần n&agrave;y trong hợp đồng&nbsp; được thể hiện bằng c&aacute;c thuật ngữ như:</strong></h2>\r\n\r\n<p>Port of loading (Cảng xếp h&agrave;ng)</p>\r\n\r\n<p>Port of discharge (Cảng dỡ h&agrave;ng)</p>\r\n\r\n<p>Trans shipment (Chuyển tải)</p>\r\n\r\n<p>Partial shipment (Giao h&agrave;ng từng phần)</p>\r\n\r\n<h2><strong>Bảo h&agrave;nh v&agrave; dịch vụ sau b&aacute;n:</strong></h2>\r\n\r\n<p>Nếu c&oacute; c&aacute;c vấn đề về lỗi sản phẩm sẽ được giải quyết thế n&agrave;o. Điều n&agrave;y sẽ đượcquy định r&otilde; về thời gian bảo h&agrave;nh sản phẩm v&agrave; chi ph&iacute; bảo h&agrave;nh. C&oacute; thể k&egrave;m theo h&agrave;ng h&oacute;a, linh kiện&nbsp; bảo h&agrave;nh được gửi k&egrave;m theo m&agrave; kh&ocirc;ng phải trả ph&iacute; hay c&ograve;n gọi l&agrave; FOC (Free of Charge).</p>\r\n\r\n<h2><strong>Thứ tự thời gian dẫn v&agrave; tăng&nbsp;</strong><em>gi&aacute;:</em></h2>\r\n\r\n<p><em>Thời gian</em>&nbsp;l&agrave; thời gian cần thiết để vận chuyển sản phẩm cho c&ocirc;ng ty mua sản phẩm.&nbsp;Khi đ&agrave;m ph&aacute;n với nh&agrave; cung cấp, bạn cần phải r&otilde; r&agrave;ng về người chịu tr&aacute;ch nhiệm cho bất kỳ sự gia tăng về vật chất hoặc vận chuyển từ thời điểm đặt h&agrave;ng được đặt v&agrave; thời gian thực tế c&oacute; sẵn cho l&ocirc; h&agrave;ng.</p>\r\n\r\n<h2><strong>Nh&atilde;n hiệu h&agrave;ng h&oacute;a, bản quyền, bằng s&aacute;ng chế:</strong></h2>\r\n\r\n<p>Ai sẽ đăng k&yacute; v&agrave; đứng t&ecirc;n, n&oacute; được diễn ra ở đ&acirc;u. Đặc biệt khi hai b&ecirc;n mua b&aacute;n c&aacute;c sản phẩm li&ecirc;n quan đến Sở hữu tr&iacute; tuệ như S&aacute;ch, tiểu tuyết, băng đĩa&hellip;</p>\r\n\r\n<h2><strong>Chấm dứt hợp đồng:</strong></h2>\r\n\r\n<p>Trong những trường hợp n&agrave;o th&igrave; Hợp đồng chấm dứt? Cần quy định r&otilde; những điều n&agrave;y trong hợp đồng.</p>\r\n\r\n<h2><strong>Giải quyết tranh chấp:</strong></h2>\r\n\r\n<p>Nếu sản phẩm bị lỗi hoặc c&oacute; một sự hiểu lầm về một số kh&iacute;a cạnh của hợp đồng mua b&aacute;n hoặc b&aacute;n h&agrave;ng, ai v&agrave; nơi n&agrave;o sẽ giải quyết tranh chấp đ&oacute;. H&atilde;y lựa chọn b&ecirc;n thứ ba uy t&iacute;n v&iacute; dụ như Trọng t&agrave;i Quốc tế tại nước thứ ba hoặc Trung t&acirc;m trọng t&agrave;i tại nước người mua/ người b&aacute;n. Tại Việt Nam c&aacute;c bạn c&oacute; thể li&ecirc;n hệ với VINACONTROL (<a href=\"https://web.archive.org/web/20170311235518/http:/www.vinacontrol.com.vn/\">vinacontrol.com.vn</a>)</p>\r\n\r\n<p>Sẽ c&ograve;n những b&agrave;i viết về chủ đề n&agrave;y nữa. C&aacute;c bạn h&atilde;y ủng hộ bằng c&aacute;ch comment dưới b&agrave;i viết nh&eacute;.</p>\r\n\r\n<p>Mrs Jade.</p>','/af/fa/aa/5a6a1fd68610c.jpg',NULL,1425,0,'2018-01-26 00:00:00',NULL,'Các bạn thường hay tự lên mạng và copy lấy một số mẫu Hợp đồng ngoại thương nhưng đôi khi chưa thực sự hiểu về các điều khoản trong đó. Một số điểm chính sau đây cần phải được nhà nhập khẩu và nhà xuất khẩu thỏa thuận, đàm phán chi tiết trước khi ra một h','hợp đồng ngoại thương, các tiêu chí trên hợp đồng ngoại thương, soạn thảo hợp đồng ngoại thương','','',2,0,'vi','thoa-thuan-giua-nguoi-mua-va-nguoi-ban-trong-xuat-khau-va-nhap-khau-hang-hoa',6,27,26,'2018-01-26 01:20:06','2018-03-03 20:26:10'),(245,'TIÊU CHÍ KHI LẬP HÓA ĐƠN THƯƠNG MẠI (COMMERCIAL INVOICE)','','Hóa đơn thương mại hay còn gọi là Commercial Invoice là chứng từ không thể thiếu trong Xuất nhập khẩu. Thông tin trên hóa đơn thương mại gồm những nội dung chính gì? Tầm quan trọng của chúng như thế nào? Chúng ta sẽ cùng nhau tìm hiểu các tiêu chí chính trên hóa đơn thương mại và cách để chuẩn bị Hóa đơn thương mại theo các nội dung dưới đây nhé:','<p>Như ch&uacute;ng ta đ&atilde; biết, h&oacute;a đơn thương mại hay c&ograve;n gọi l&agrave;&nbsp;Commercial Invoice l&agrave; chứng từ kh&ocirc;ng thể thiếu trong Xuất nhập khẩu. Th&ocirc;ng tin tr&ecirc;n h&oacute;a đơn thương mại gồm những nội dung ch&iacute;nh g&igrave;? Tầm quan trọng của ch&uacute;ng như thế n&agrave;o? Ch&uacute;ng ta sẽ c&ugrave;ng nhau t&igrave;m hiểu c&aacute;c ti&ecirc;u ch&iacute; ch&iacute;nh tr&ecirc;n h&oacute;a đơn thương mại v&agrave; c&aacute;ch để chuẩn bị H&oacute;a đơn thương mại theo c&aacute;c nội dung dưới đ&acirc;y nh&eacute;:</p>\r\n\r\n<p>1. Người xuất khẩu/người gửi h&agrave;ng (Exporter/Shipper): Ghi r&otilde; t&ecirc;n v&agrave; địa chỉ đầy đủ của người gửi h&agrave;ng, t&ecirc;n quốc gia xuất khẩu.</p>\r\n\r\n<p>2. Người nhập khẩu/người nhận h&agrave;ng (Importer/Consignee): T&ecirc;n C&ocirc;ng ty, địa chỉ, số điện thoại li&ecirc;n hệ. Nếu c&oacute; thể ghi ch&uacute; th&ecirc;m PIC (Person In Charge) &ndash; người chịu tr&aacute;ch nhiệm về việc theo d&otilde;i l&ocirc; h&agrave;ng đ&oacute;.</p>\r\n\r\n<p>3. Số h&oacute;a đơn thương mại v&agrave; ng&agrave;y th&aacute;ng ph&aacute;t h&agrave;nh: Đ&acirc;y&nbsp;l&agrave; số tham chiếu được lập bởi người b&aacute;n v&agrave; bất cứ h&oacute;a đơn thương mại n&agrave;o cũng&nbsp;phải c&oacute; số H&oacute;a đơn. Số h&oacute;a đơn thương mại c&ograve;n được sử dụng để l&agrave;m thủ tục khai b&aacute;o hải quan xuất khẩu, nhập khẩu. Người xuất khẩu hoặc nhập khẩu c&oacute; thể lưu hồ sơ theo số h&oacute;a đơn thương mại.</p>\r\n\r\n<p>4. Th&ocirc;ng tin tham chiếu kh&aacute;c (Nếu c&oacute;): Bạn c&oacute; thể th&ecirc;m đến bất kỳ th&ocirc;ng tin tham khảo kh&aacute;c li&ecirc;n quan đến l&ocirc; h&agrave;ng hoặc th&ecirc;m th&ocirc;ng tin do người mua y&ecirc;u cầu. V&iacute; dụ số LC, t&ecirc;n nh&acirc;n vi&ecirc;n b&aacute;n đơn h&agrave;ng n&agrave;y, số đơn đặt h&agrave;ng (Purchase Order) &hellip;</p>\r\n\r\n<p>5. Nước sản xuất (Country of Origin): Th&ocirc;ng tin n&agrave;y c&oacute; thể cho th&ecirc;m v&agrave;o hoặc kh&ocirc;ng, tuy nhi&ecirc;n để hợp l&yacute; h&oacute;a khi khai b&aacute;o hải quan th&igrave; n&ecirc;n cho v&agrave;o. Nh&acirc;n vi&ecirc;n chứng từ c&oacute; thể tự &ldquo;chế&rdquo; th&ecirc;m th&ocirc;ng tin n&agrave;y v&agrave;o h&oacute;a đơn thương mại (trường hợp kh&ocirc;ng &aacute;p dụng C/O cho l&ocirc; h&agrave;ng đang thực hiện) dựa tr&ecirc;n th&ocirc;ng tin thực tế h&agrave;ng h&oacute;a.</p>\r\n\r\n<p>6. T&ecirc;n cảng đ&iacute;ch v&agrave; quốc gia: Đ&acirc;y l&agrave; quốc gia nơi h&agrave;ng h&oacute;a được chuyển đến cuối c&ugrave;ng</p>\r\n\r\n<p>7. Phương thức vận chuyển: Bằng đường biển (by sea) hoặc đường h&agrave;ng kh&ocirc;ng (by air). Kh&ocirc;ng nhất thiết phải ghi t&ecirc;n phương tiện vận chuyển v&agrave; số hiệu v&igrave; h&oacute;a đơn thương mại thường được&nbsp;ph&aacute;t h&agrave;nh&nbsp; trước khi giao h&agrave;ng v&agrave; chưa c&oacute; số hiệu cụ thể của phương tiện.</p>\r\n\r\n<p>8. Cảng bốc h&agrave;ng (Port of Loading): C&oacute; thể ghi t&ecirc;n&nbsp;cảng bốc h&agrave;ng v&agrave; quốc gia tương ứng. Đ&oacute; c&oacute; thể l&agrave; s&acirc;n bay hoặc cảng biển.</p>\r\n\r\n<p>9. Cảng dỡ h&agrave;ng (Port of Discharge): T&ecirc;n cảng dỡ h&agrave;ng, nơi m&agrave; h&agrave;ng h&oacute;a được dỡ xuống&nbsp;từ m&aacute;y bay hoặc t&agrave;u biển, c&ograve;n gọi l&agrave; cảng đ&iacute;ch.</p>\r\n\r\n<p>10. Điều khoản giao h&agrave;ng v&agrave; điều khoản thanh to&aacute;n: N&ecirc;n&nbsp;ghi r&otilde; điều khoản giao h&agrave;ng l&agrave; g&igrave;, theo Incoterms n&agrave;o. Điều&nbsp;khoản thanh to&aacute;n l&agrave; TT hay LC, DA/DP&nbsp;hay&nbsp;No Payment?</p>\r\n\r\n<p>11. Số lượng kiện (Packages):&nbsp;Ghi tổng số lượng kiện h&agrave;ng của l&ocirc; h&agrave;ng đ&oacute;, thường ghi k&egrave;m tổng trọng lượng cả b&igrave; (Gross Weight &ndash; kgs). Th&ocirc;ng tin n&agrave;y thuộc về phần đ&oacute;ng g&oacute;i (Packing), nếu chi tiết đ&oacute;ng g&oacute;i kh&ocirc;ng c&oacute; g&igrave; phức tạp th&igrave; c&oacute; thể kết hợp th&ocirc;ng tin của Packing List &ndash; phiếu đ&oacute;ng g&oacute;i v&agrave;o c&ugrave;ng với h&oacute;a đơn thương mại.</p>\r\n\r\n<p>12. M&ocirc; tả h&agrave;ng ho&aacute;: M&ocirc; tả chi tiết của h&agrave;ng h&oacute;a&nbsp;bao gồm c&aacute;c nội dung ch&iacute;nh sau:</p>\r\n\r\n<ul>\r\n	<li>M&atilde; h&agrave;ng (Code) c&oacute; thể c&oacute; hoặc kh&ocirc;ng, t&ugrave;y theo c&aacute;ch quản l&yacute; h&agrave;ng h&oacute;a của người b&aacute;n hoặc y&ecirc;u cầu của người mua để thể hiện th&ocirc;ng tin n&agrave;y tr&ecirc;n bảng m&ocirc; tả chi tiết h&agrave;ng h&oacute;a</li>\r\n	<li>M&ocirc; tả chi tiết t&ecirc;n h&agrave;ng (Descriptions of goods): thường m&ocirc; tả bằng một số từ kh&oacute;a, c&aacute;c chi tiết đi k&egrave;m, Model, Serial &ndash; nếu c&oacute;.</li>\r\n	<li>Số lượng (Quantity), Đơn gi&aacute; thường đi k&egrave;m đồng tiền thanh to&aacute;n (Unit Price), Đơn vị t&iacute;nh h&agrave;ng h&oacute;a (Unit), Th&agrave;nh tiền (Amount), Tổng trị gi&aacute; (Total Amount)</li>\r\n</ul>\r\n\r\n<p>13. Số tiền bằng chữ:&nbsp;Số tiền viết bằng chữ phải khớp với Tổng số tiền phải thanh to&aacute;n (n&ecirc;n c&oacute; phần n&agrave;y, tuy nhi&ecirc;n kh&ocirc;ng phải bắt buộc)</p>\r\n\r\n<p>14. Ghi ch&uacute; th&ecirc;m:&nbsp;C&oacute; thể th&ecirc;m&nbsp;th&ocirc;ng tin ghi ch&uacute;&nbsp;(nếu c&oacute;)</p>\r\n\r\n<p>15. K&yacute; t&ecirc;n: Kh&ocirc;ng bắt buộc Gi&aacute;m đốc c&ocirc;ng ty phải l&agrave; người k&yacute; t&ecirc;n tr&ecirc;n h&oacute;a đơn thương mại xuất khẩu. Chỉ bằng cụm từ &ldquo;On behalf of&rdquo; người đại diện của c&ocirc;ng ty c&oacute; thể k&yacute; ủy quyền c&oacute; nghĩa l&agrave;, người đến người xuất khẩu uỷ quyền k&yacute; h&oacute;a đơn thương mại thay mặt cho c&aacute;c nh&agrave; xuất khẩu.</p>\r\n\r\n<p>Mrs Jade.</p>','/63/c5/56/5a6a203872798.jpg',NULL,3141,0,'2018-01-26 00:00:00',NULL,'Hóa đơn thương mại hay còn gọi là Commercial Invoice là chứng từ không thể thiếu trong Xuất nhập khẩu. Thông tin trên hóa đơn thương mại gồm những nội dung chính gì? Tầm quan trọng của chúng như thế nào? Chúng ta sẽ cùng nhau tìm hiểu các tiêu chí chính t','hóa đơn thương mại, chứng từ trong xuất nhập khẩu, các tiêu chí trên hóa đơn thương mại, nội dung của hóa đơn thương mại','','',2,0,'vi','tieu-chi-khi-lap-hoa-don-thuong-mai-commercial-invoice',6,27,26,'2018-01-26 01:21:44','2018-03-03 20:27:27'),(246,'TỔNG HỢP CÁC LOẠI BILL OF LADING VÀ Ý NGHĨA CỦA CHÚNG','','Vận đơn đường biển (Bill of Lading) là một trong những chứng từ quan trọng nhất trong thương mại quốc tế. Nó là một chứng từ do người vận chuyển hoặc thuyền trưởng, hoặc người được ủy quyền của hãng vận chuyển ký và phát hành. Để hiểu ý nghĩa, vai trò, phân loại...các loại vận đơn thường gặp đòi hỏi bạn phải có kiến thức tổng quát về nó bởi vận đơn liên quan đến không chỉ một bên nào cả.','<p>Vận đơn đường biển (Bill of Lading) l&agrave; một trong những chứng từ quan trọng nhất trong thương mại quốc tế. N&oacute; l&agrave; một chứng từ do người vận chuyển hoặc thuyền trưởng, hoặc người được ủy quyền của h&atilde;ng vận chuyển k&yacute; v&agrave; ph&aacute;t h&agrave;nh.</p>\r\n\r\n<h2><strong>1.Vai tr&ograve; của vận đơn đường biển trong vận tải Quốc tế</strong></h2>\r\n\r\n<h3>Thứ nhất: Vận đơn l&agrave;&nbsp;bi&ecirc;n lai h&agrave;ng h&oacute;a</h3>\r\n\r\n<p>Vận đơn đường biển do thuyền trưởng hoặc người được ủy quyền của h&atilde;ng vận tải k&yacute;. Đ&acirc;y l&agrave; chức năng cơ bản của vận đơn. Trước đ&acirc;y thời xa xưa, c&aacute;c l&aacute;i bu&ocirc;n thường đi c&ugrave;ng với t&agrave;u chở h&agrave;ng v&agrave; trực tiếp nhận h&agrave;ng giao cho người mua n&ecirc;n kh&ocirc;ng cần vận đơn. Với sự ph&aacute;t triển của kinh tế thị trường, nền sản xuất h&agrave;ng h&oacute;a lớn, c&aacute;c l&aacute;i bu&ocirc;n giờ l&agrave; c&aacute;c thương nh&acirc;n- nh&agrave; xuất khẩu thường gửi h&agrave;ng th&ocirc;ng qua một đại l&yacute; vận chuyển hoặc gửi trực tiếp cho h&atilde;ng t&agrave;u. Khi h&agrave;ng đ&atilde; được xếp l&ecirc;n t&agrave;u th&igrave; người gửi h&agrave;ng cần một chứng cứ rằng m&igrave;nh đ&atilde; giao h&agrave;ng cho nh&agrave; vận chuyển v&agrave; gửi k&egrave;m với c&aacute;c chứng từ kh&aacute;c cho người nhận h&agrave;ng ở Cảng đ&iacute;ch.</p>\r\n\r\n<h3>Thứ hai: Vận đơn l&agrave; bằng chứng của Hợp đồng chuy&ecirc;n chở</h3>\r\n\r\n<p>Vận đơn được xem như&nbsp;hợp đồng chuy&ecirc;n chở giữa người vận chuyển (carrier) v&agrave; người gửi h&agrave;ng (shipper). Khi người gửi h&agrave;ng li&ecirc;n hệ với c&aacute;c c&ocirc;ng ty vận chuyển v&agrave; đ&atilde; thỏa thuận xong hết về gi&aacute; cước v&agrave; c&aacute;c điều kiện vận chuyển th&igrave; hai b&ecirc;n sẽ k&yacute; kết Hợp đồng vận tải h&agrave;ng h&oacute;a trước khi h&agrave;ng được giao l&ecirc;n phương tiện vận chuyển. Cho đến khi h&agrave;ng đ&atilde; được giao l&ecirc;n phương tiện vận tải th&igrave; l&uacute;c đ&oacute; vận đơn như một bằng chứng x&aacute;c nhận Hợp đồng chuy&ecirc;n chở đ&atilde; được thực thi.</p>\r\n\r\n<h3>Thứ ba: Vận đơn c&ograve;n c&oacute; chức năng l&agrave; chứng từ sở hữu h&agrave;ng h&oacute;a.</h3>\r\n\r\n<p>Hiện nay, đ&acirc;y l&agrave; vai tr&ograve; quan trọng nhất của vận đơn trong Thương mại quốc tế. Ai l&agrave; người sở hữu vận đơn gốc l&agrave; người c&oacute; quyền nhận h&agrave;ng v&agrave; sở hữu h&agrave;ng h&oacute;a. Đương nhi&ecirc;n l&uacute;c nhận h&agrave;ng c&ograve;n cần c&aacute;c tờ kh&aacute;c như giấy giới thiệu, ủy quyền v&agrave; phải được nh&agrave; vận tải ph&aacute;t lệnh giao h&agrave;ng. Vận đơn được quyền chuyển nhượng cho người kh&aacute;c th&ocirc;ng qua việc k&yacute; hậu vận đơn thường d&ugrave;ng khi hai b&ecirc;n thanh to&aacute;n bằng L/C.</p>\r\n\r\n<h2><strong>2.C&aacute;c loại vận đơn đường biển thường gặp</strong></h2>\r\n\r\n<p>C&oacute; thể ph&acirc;n loại vận đơn theo nhiều c&aacute;ch kh&aacute;c nhau nhưng trong chủ đề n&agrave;y m&igrave;nh muốn n&oacute;i đến c&aacute;c loại vận đơn hay gặp nhất.</p>\r\n\r\n<h3>*Vận đơn đ&iacute;ch danh hay c&ograve;n gọi l&agrave; Straight Bill of Lading:</h3>\r\n\r\n<p>Loại vận đơn n&agrave;y ở mục Consignee sẽ ghi r&otilde; c&aacute;c th&ocirc;ng tin của người nhận h&agrave;ng như t&ecirc;n, địa chỉ, số điện thoại, email, fax&hellip;v&agrave; chỉ c&oacute; người n&agrave;y mới c&oacute; quyền nhận h&agrave;ng. Tr&ecirc;n vận đơn sẽ kh&ocirc;ng&nbsp; c&oacute; mục To order of. Xem h&igrave;nh dưới đ&acirc;y:</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/v%C4%91%201.jpg\" style=\"height:271px; width:626px\" /></p>\r\n\r\n<h3>*Vận đơn theo lệnh (Order Bill of Lading):</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; loại vận đơn hay gặp nhất trong vận tải Quốc tế.&nbsp; Theo đ&oacute; người vận tải sẽ giao h&agrave;ng theo lệnh của người đứng ở mục To order of bằng c&aacute;ch người n&agrave;y k&yacute; hậu đằng sau vận đơn t&ecirc;n người nhận h&agrave;ng. Người đứng ở mục To order of c&oacute; thể l&agrave; một người đ&iacute;ch danh n&agrave;o đ&oacute;, một ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh L/C, hay ch&iacute;nh l&agrave; người gửi h&agrave;ng shipper.</p>\r\n\r\n<p>Một số c&aacute;ch ghi như sau:</p>\r\n\r\n<p>TO ORDER OF XYZ = TO THE ORDER OF XYZ = ORDER OF XYZ<br />\r\nTO ORDER = TO THE ORDER = ORDER</p>\r\n\r\n<p>Để bạn hiểu r&otilde; hơn về việc ai l&agrave; người được quyền ph&aacute;t lệnh giao h&agrave;ng dựa v&agrave;o mục Order of, h&atilde;y tham khảo bảng minh họa dưới đ&acirc;y</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:295px\">\r\n			<p><strong>C&aacute;ch thể hiện</strong></p>\r\n			</td>\r\n			<td style=\"width:291px\">\r\n			<p><strong>Người được k&yacute; hậu vận đơn</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:295px\">\r\n			<p><strong>To order of shipper</strong></p>\r\n			</td>\r\n			<td style=\"width:291px\">\r\n			<p>shipper</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:295px\">\r\n			<p><strong>To order of XYZ company</strong></p>\r\n			</td>\r\n			<td style=\"width:291px\">\r\n			<p>XYZ company</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:295px\">\r\n			<p><strong>To order of ACB Bank</strong></p>\r\n			</td>\r\n			<td style=\"width:291px\">\r\n			<p>ACB Bank</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:295px\">\r\n			<p><strong>To order</strong></p>\r\n			</td>\r\n			<td style=\"width:291px\">\r\n			<p>shipper</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/v%C4%91%202.jpg\" style=\"height:726px; width:502px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/v%C4%91%203.jpg\" /></p>\r\n\r\n<h3>*Vận đơn ho&agrave;n hảo (Clean on board):</h3>\r\n\r\n<p>L&agrave; vận đơn kh&ocirc;ng c&oacute; c&aacute;c ghi ch&uacute; đặc biệt về việc xếp h&agrave;ng l&ecirc;n t&agrave;u như h&agrave;ng bị thiếu, hư hỏng, sai kh&aacute;c số lượng, khiếm khuyết tr&ecirc;n bao b&igrave;&hellip;</p>\r\n\r\n<h3>*Vận đơn đ&atilde; xếp h&agrave;ng l&ecirc;n t&agrave;u (ship on board B/L):</h3>\r\n\r\n<p>L&agrave; vận đơn được cấp sau khi h&agrave;ng đ&atilde; giao l&ecirc;n t&agrave;u v&agrave;&nbsp; c&oacute; ng&agrave;y th&aacute;ng on board cụ thể</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/v%C4%91%204.jpg\" /></p>\r\n\r\n<h3>*Vận đơn bằng điện ( Telex Release Bill of Lading):</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; loại Bill được ph&aacute;t h&agrave;nh bằng điện, theo đ&oacute; người gửi h&agrave;ng (shipper) kh&ocirc;ng cần gửi Bill gốc cho nh&agrave; nhập khẩu để nhận h&agrave;ng.Với lại Bill n&agrave;y, để ph&aacute;t h&agrave;nh Bill Telex, h&atilde;ng t&agrave;u sẽ y&ecirc;u cầu thu hồi lại Bill gốc nếu trong trường hợp đ&atilde; ph&aacute;t h&agrave;nh. Thường d&ugrave;ng cho c&aacute;c tuyến vận tải gần, thời gian h&agrave;ng đến trước cả khi nh&agrave; nhập khẩu nhận được chứng từ gốc trong đ&oacute; c&oacute; Bill of Lading bản gốc. Tr&ecirc;n vận đơn Telex sẽ được đ&oacute;ng dấu Surrendered hoặc dấu Telex Release.</p>\r\n\r\n<h3><strong>Giới thiệu&nbsp;quy tr&igrave;nh để cấp ph&aacute;t Vận đơn bằng điện (Bill Telex)</strong></h3>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/v%C4%91%205.jpg\" style=\"height:771px; width:627px\" /></p>\r\n\r\n<h3>*Vận đơn chủ (Master Bill of Lading):</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; vận đơn rất hay gặp trong vận tải Quốc tế, do h&atilde;ng t&agrave;u/ h&atilde;ng h&agrave;ng kh&ocirc;ng ph&aacute;t h&agrave;nh trong đ&oacute;&nbsp; Shipper thường ghi t&ecirc;n c&ocirc;ng ty Forwarder c&ograve;n Consignee ( người nhận h&agrave;ng) sẽ ghi t&ecirc;n đại l&yacute; của Forwarder ở đầu nhập khẩu l&agrave; người nhận h&agrave;ng.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/v%C4%91%206.jpg\" style=\"height:413px; width:529px\" /></p>\r\n\r\n<p>H&igrave;nh tr&ecirc;n l&agrave; MBL trong đ&oacute; Shipper: cty Airland Logistics (l&agrave; đại l&yacute; của C&ocirc;ng ty Forwarder tại&nbsp; Australia). Người nhận h&agrave;ng ch&iacute;nh l&agrave; c&ocirc;ng ty Forwarder A&amp;E TRANSCO tại Việt Nam nơi m&agrave; kh&aacute;ch h&agrave;ng l&agrave; nh&agrave; nhập khẩu tại Việt Nam sử dụng dịch vụ.</p>\r\n\r\n<h3>*Vận đơn thứ cấp (House Bill of Lading):</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; loại vận đơn thường gặp nhất trong c&aacute;c loại vận đơn đường biển, n&oacute; được ph&aacute;t h&agrave;nh bởi c&ocirc;ng ty Forwarder trong đ&oacute; Shipper sẽ ghi t&ecirc;n chủ h&agrave;ng, Consignee l&agrave; t&ecirc;n nh&agrave; nhập khẩu.&nbsp; B&ecirc;n th&ocirc;ng b&aacute;o Notify Party c&oacute; thể ghi t&ecirc;n của c&ocirc;ng ty Forwarder m&agrave; nh&agrave; Nhập khẩu thu&ecirc; dịch vụ.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/uploads/ckfinder/images/v%C4%91%207.jpg\" style=\"height:424px; width:479px\" /></p>\r\n\r\n<p>H&igrave;nh tr&ecirc;n l&agrave; HBL, trong đ&oacute; người gửi h&agrave;ng l&agrave; t&ecirc;n nh&agrave; xuất khẩu, người nhận h&agrave;ng l&agrave; nh&agrave; nhập khẩu c&oacute; t&ecirc;n MY HAO DISTRIBUTION JSC tại Việt Nam</p>\r\n\r\n<h2><strong>Về quy tr&igrave;nh cấp MBL v&agrave; HBL như sau</strong></h2>\r\n\r\n<h3><span style=\"color:#B22222\">H&atilde;ng t&agrave;u&ndash;(MBL)&ndash;&gt; Forwarder agent&ndash;(HBL)&mdash;&gt; Nh&agrave; xuất khẩu&ndash;(HBL)&ndash;&gt;Nh&agrave; nhập khẩu</span></h3>\r\n\r\n<p>Để c&aacute;c em tiện theo d&otilde;i, ch&uacute;ng ta h&atilde;y nh&igrave;n v&agrave;o bảng minh họa dưới đ&acirc;y</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:214px\">\r\n			<p>Ph&acirc;n biệt</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>Master Bill of Lading</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>House Bill of Lading</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:214px\">\r\n			<p>Ph&aacute;t h&agrave;nh bởi</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>H&atilde;ng vận chuyển</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>C&ocirc;ng ty Forwarde</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:214px\">\r\n			<p>Người gửi h&agrave;ng</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>Đại l&yacute; của c&ocirc;ng ty Forwarder</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>Người gửi h&agrave;ng thực tế</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:214px\">\r\n			<p>Người nhận h&agrave;ng</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>C&ocirc;ng ty Forwarder tại nước nhập khẩu</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>Người nhận h&agrave;ng thực tế</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:214px\">\r\n			<p>T&ecirc;n của vận đơn</p>\r\n			</td>\r\n			<td colspan=\"2\" style=\"width:428px\">\r\n			<p>Bill of Lading, Seaway Bill of Lading, Ocean Bill of Lading, Airway Bill of Lading&hellip;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:214px\">\r\n			<p>Qu&aacute; tr&igrave;nh xử l&yacute;</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>Được ph&aacute;t trực tiếp cho Forwarder m&agrave; kh&ocirc;ng cần chuyển cho ai kh&aacute;c.</p>\r\n			</td>\r\n			<td style=\"width:214px\">\r\n			<p>C&ocirc;ng ty Forwarder chuyển th&ocirc;ng tin từ MBL sang HBL sau đ&oacute; chuyển tiếp cho Shipper.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h2><strong>3.Ai l&agrave; người k&yacute; tr&ecirc;n Vận đơn theo UCP 600</strong></h2>\r\n\r\n<p>C&aacute;c quy định về LC c&oacute; đưa ra những ch&uacute; &yacute; đặc biệt với c&aacute;c chứng từ vận tải.</p>\r\n\r\n<p>Nếu bạn c&oacute; t&igrave;m hiểu phi&ecirc;n bản mới nhất về c&aacute;c quy định li&ecirc;n quan đến LC như UCP 600, ta sẽ thấy rằng tất cả c&aacute;c chứng từ vận tải phải rất chi tiết, cụ thể.</p>\r\n\r\n<p>UCP 600 c&oacute; đề cập ai l&agrave; người k&yacute; tr&ecirc;n chứng từ vận tải để được chấp nhận thanh to&aacute;n bằng LC.</p>\r\n\r\n<p>Chi tiết trong UCP 600, c&oacute; đề cập về Bill of Lading, Một chứng từ vận tải d&ugrave;ng cho &iacute;t nhất hai phương thức vận tải kh&aacute;c nhau (chứng từ vận tải đa phương thức hoặc li&ecirc;n hợp) d&ugrave; được gọi như thế n&agrave;o, phải:</p>\r\n\r\n<p>Chỉ r&otilde; t&ecirc;n của người chuy&ecirc;n chở v&agrave; được k&iacute; bởi:</p>\r\n\r\n<p>* người chuy&ecirc;n chở hoặc đại l&yacute; được chỉ định cho hoặc thay mặt người chuy&ecirc;n chở, hoặc</p>\r\n\r\n<p>* thuyền trưởng hoặc đại l&yacute; được chỉ định hoặc thay mặt thuyền trưởng</p>\r\n\r\n<p>C&aacute;c chữ k&iacute; của người chuy&ecirc;n chở, thuyền trưởng hoặc đại l&yacute; phải x&aacute;c định được chữ k&iacute; n&agrave;o l&agrave; của người chuy&ecirc;n chở, chữ k&iacute; n&agrave;o l&agrave; của thuyền trưởng hoặc của đại l&yacute;.</p>\r\n\r\n<p>Chữ k&iacute; của đại l&yacute; phải chỉ r&otilde; l&agrave; đại l&yacute; đ&atilde; k&iacute; thay hoặc đại diện cho người chuy&ecirc;n chở hoặc đ&atilde; k&iacute; thay hoặc đại diện cho thuyền trưởng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/v%C4%91%208.jpg\" style=\"height:524px; width:405px\" /></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; H&igrave;nh minh họa: H&atilde;ng t&agrave;u với tư c&aacute;ch l&agrave; nh&agrave; chuy&ecirc;n chở k&yacute; tr&ecirc;n vận đơn</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/v%C4%91%209.jpg\" style=\"height:640px; width:452px\" /></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;H&igrave;nh minh họa:&nbsp;&nbsp;Vận đơn c&oacute; chữ k&yacute; của Đại l&yacute; h&atilde;ng vận chuyển</p>\r\n\r\n<p>Hy vọng với b&agrave;i viết n&agrave;y đ&atilde; gi&uacute;p c&aacute;c em nắm được c&aacute;c loại vận đơn đường biển thường gặp trong thực tế cũng như &yacute; nghĩa của ch&uacute;ng. Cảm ơn c&aacute;c em đ&atilde; theo d&otilde;i v&agrave; ủng hộ CLB Y&ecirc;u Xuất Nhập Khẩu. H&atilde;y để lại comment ph&iacute;a dưới nếu c&aacute;c em thấy b&agrave;i viết hay hoặc vẫn c&ograve;n bất cứ thắc mắc n&agrave;o.</p>\r\n\r\n<p>Mrs Jade.</p>','/b9/2a/ab/5a6a209094680.jpg',NULL,18132,0,'2018-01-26 00:00:00',NULL,'Vận đơn đường biển (Bill of Lading) là một trong những chứng từ quan trọng nhất trong thương mại quốc tế. Nó là một chứng từ do người vận chuyển hoặc thuyền trưởng, hoặc người được ủy quyền của hãng vận chuyển ký và phát hành. Để hiểu ý nghĩa, vai trò, ph','các loại vận đơn đường biển, vận đơn là gì, bill of lading, ý nghĩa của vận đơn, quy trình xuất nhập khẩu đường biển, phát hành vận đơn','','',2,0,'vi','tong-hop-cac-loai-bill-of-lading-va-y-nghia-cua-chung',6,27,26,'2018-01-26 01:23:12','2018-03-03 20:31:54'),(247,'THẾ NÀO LÀ XUẤT TRÌNH CHỨNG TỪ PHÙ HỢP TRONG LC','','Nếu chứng từ xuất trình phù hợp với các quy định trong hợp đồng cơ sở, nhưng không phù hợp với thư tín dụng LC thì Ngân hàng phát hành không thanh toán tiền cho người hưởng lợi. Nhưng Ngân hàng phát hành có thể:','<h2>Xuất tr&igrave;nh ph&ugrave; hợp l&agrave; khi xuất tr&igrave;nh bộ chứng từ phải đảm bảo c&aacute;c y&ecirc;u cầu sau:</h2>\r\n\r\n<p>+ Đ&uacute;ng loại chứng từ như L/C y&ecirc;u cầu.</p>\r\n\r\n<p>+ Đ&uacute;ng số lượng bản gốc v&agrave; bản sao</p>\r\n\r\n<p>+ Đ&uacute;ng nội dung m&agrave; L/C y&ecirc;u cầu.</p>\r\n\r\n<p>+ Ghi r&otilde; người ph&aacute;t h&agrave;nh chứng từ&hellip;</p>\r\n\r\n<h2>Nếu chứng từ xuất tr&igrave;nh ph&ugrave; hợp với c&aacute;c quy định trong hợp đồng cơ sở, nhưng kh&ocirc;ng ph&ugrave; hợp với L/C th&igrave; Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh kh&ocirc;ng thanh to&aacute;n tiền cho người hưởng lợi. Nhưng Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh c&oacute; thể:</h2>\r\n\r\n<p>+ Th&ocirc;ng b&aacute;o kh&ocirc;ng chậm trễ c&aacute;c sai biệt cho Người hưởng lợi biết.</p>\r\n\r\n<p>+ C&oacute; thể tranh thủ &yacute; kiến của Người y&ecirc;u cầu về sự sai biệt.</p>\r\n\r\n<p>+ C&oacute; thể được Người hưởng lợi ủy quyền thương thảo về sai biệt đ&oacute; với Người y&ecirc;u cầu.</p>\r\n\r\n<p>+ Trong trường hợp kh&ocirc;ng thể bỏ qua c&aacute;c sai biệt, Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh phải trả lại chứng từ cho Người xuất tr&igrave;nh chứng từ kh&ocirc;ng chậm trễ.</p>\r\n\r\n<p>Y&ecirc;u cầu về việc k&yacute; ph&aacute;t v&agrave; xuất tr&igrave;nh c&aacute;c loại chứng từ cần phải được n&ecirc;u r&otilde; r&agrave;ng, cụ thể v&agrave; chặt chẽ trong L/C. C&aacute;c y&ecirc;u cầu n&agrave;y xuất ph&aacute;t từ đặc điểm của h&agrave;ng h&oacute;a, của phương thức vận tải, của c&ocirc;ng t&aacute;c thanh to&aacute;n v&agrave; t&iacute;n dụng, của t&iacute;nh chất hợp đồng v&agrave; c&aacute;c nguồn ph&aacute;p l&yacute; c&oacute; li&ecirc;n quan đến việc thực hiện hoạt động đ&oacute;.</p>\r\n\r\n<p>Mrs Jade.</p>','/9e/82/29/5a6a20c6d7a8a.jpg',NULL,2402,0,'2018-01-26 00:00:00',NULL,'Yêu cầu về việc ký phát và xuất trình các loại chứng từ cần phải được nêu rõ ràng, cụ thể và chặt chẽ trong L/C. Các yêu cầu này xuất phát từ đặc điểm của hàng hóa, của phương thức vận tải, của công tác thanh toán và tín dụng, của tính chất hợp đồng và cá','bộ chứng từ để mở LC, cách mở LC, thư tín dụng','','',2,0,'vi','the-nao-la-xuat-trinh-chung-tu-phu-hop-trong-lc',7,27,26,'2018-01-26 01:24:06','2018-03-03 20:36:35'),(248,'TỔNG HỢP CÁC WEBSITE B2B TÌM KIẾM NHÀ CUNG CẤP, KHÁCH HÀNG LỚN NHẤT THẾ GIỚI','','B2B viết tắt của Business to Bussiness là loại hình giao dịch qua các phương tiện điện tử và mạng viễn thông đặc biệt là Internet giữa các doanh nghiệp với doanh nghiệp. Nhờ có các website này mà các doanh nghiệp có thể chào hàng, tìm kiếm khách hàng, nhà cung cấp một cách dễ dàng. Dưới đây là 50 website B2B lớn nhất thế giới:','<p>B2B viết tắt của Business to Bussiness l&agrave; loại h&igrave;nh giao dịch qua c&aacute;c phương tiện điện tử v&agrave; mạng viễn th&ocirc;ng đặc biệt l&agrave; Internet giữa c&aacute;c doanh nghiệp với doanh nghiệp. Nhờ c&oacute; c&aacute;c website n&agrave;y m&agrave; c&aacute;c doanh nghiệp c&oacute; thể ch&agrave;o h&agrave;ng, t&igrave;m kiếm kh&aacute;ch h&agrave;ng, nh&agrave; cung cấp một c&aacute;ch dễ d&agrave;ng. Dưới đ&acirc;y l&agrave; 50 website B2B lớn nhất thế giới:</p>\r\n\r\n<p>Nếu bạn l&agrave; một nh&agrave; xuất khẩu hoặc l&agrave; nh&agrave; nhập khẩu th&igrave; kh&ocirc;ng thể bỏ qua những website n&agrave;y. Đ&acirc;y l&agrave; t&agrave;i liệu v&ocirc; c&ugrave;ng hữu &iacute;ch m&agrave; do ch&iacute;nh CLB Xuất nhập khẩu bi&ecirc;n soạn.</p>\r\n\r\n<p><strong>1. Alibaba http://www.alibaba.com</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; cổng th&ocirc;ng tin, website B2B lớn nhất thế giới; bạn c&oacute; thể đặt 50 sản phẩm tr&ecirc;n đ&oacute;&nbsp;miễn ph&iacute;. Website n&agrave;y được th&agrave;nh lập tại Trung Quốc v&agrave; nhanh ch&oacute;ng đ&atilde; trở th&agrave;nh s&agrave;n giao dịch TMĐT h&agrave;ng đầu nhờ những t&iacute;nh năng cũng như số lượng kh&aacute;ch h&agrave;ng- nh&agrave; cung cấp online lu&ocirc;n ở mức cao. Nếu l&agrave; nh&agrave; nhập khẩu th&igrave; bạn c&oacute; thể d&ugrave;ng Free để t&igrave;m kiếm Nh&agrave; cung cấp c&ograve;n nếu l&agrave; Nh&agrave; xuất khẩu th&igrave; bạn cần c&oacute; t&agrave;i khoản Gold Supplier để được hỗ trợ tốt nhất hoặc &nbsp;&iacute;t nhất trả ph&iacute; để c&oacute; được th&ocirc;ng tin kh&aacute;ch đang &nbsp;c&oacute; nhu cầu mặt h&agrave;ng th&ocirc;ng qua Inquiry.</p>\r\n\r\n<p><strong>2. Ec21 http://www.ec21.com</strong></p>\r\n\r\n<p>Website B2B n&agrave;y l&agrave; của H&agrave;n quốc v&agrave; c&oacute; thị trường ch&iacute;nh ở Trung Quốc. Giao diện kh&aacute; dễ d&ugrave;ng v&agrave; tương tự như Alibaba.</p>\r\n\r\n<p><strong>3. TradeKey http://www.tradekey.com</strong></p>\r\n\r\n<p>Website B2B TradeKey&nbsp;c&oacute; gốc từ Ả Rập v&agrave; đ&atilde; nhanh ch&oacute;ng trở th&agrave;nh 1 trong những s&agrave;n giao dịch lớn nhất thế giới.</p>\r\n\r\n<p><strong>4. Indiamart http://www.indiamart.com</strong></p>\r\n\r\n<p>Tạo ra ở Ấn Độ, 70 % kh&aacute;ch h&agrave;ng đang ở Indiamart, đ&oacute; l&agrave; một nơi tốt để mua b&aacute;n tại thị trường Ấn Độ.</p>\r\n\r\n<p><strong>5. Manta http://www.manta.com</strong></p>\r\n\r\n<p>Được khởi tạo tại Mỹ, Manta kh&ocirc;ng phải l&agrave; một thị trường B2B thực sự. Thay v&agrave;o đ&oacute;, n&oacute; cung cấp hồ sơ c&ocirc;ng ty cho c&aacute;c doanh nghiệp ng&agrave;nh c&ocirc;ng nghiệp.</p>\r\n\r\n<p><strong>6. Made-in-china http://www.made-in-china.com</strong></p>\r\n\r\n<p>Danh mục c&aacute;c nh&agrave; sản xuất Trung Quốc v&agrave; danh mục c&aacute;c sản phẩm l&agrave; cầu nối giữa c&aacute;c nh&agrave; m&aacute;y, nh&agrave; sản xuất, nh&agrave; cung cấp v&agrave; người mua to&agrave;n cầu.</p>\r\n\r\n<p><strong>7. Dhgate http://www.dhgate.com</strong></p>\r\n\r\n<p>Khởi tạo tại Trung Quốc, như một cổng thanh to&aacute;n trung gian an to&agrave;n cho c&aacute;c nh&agrave; kinh doanh quốc tế.</p>\r\n\r\n<p><strong>8. China.cn http://en.china.cn</strong></p>\r\n\r\n<p>Được th&agrave;nh lập bởi Trung t&acirc;m th&ocirc;ng tin Internet, China.cn l&agrave; một&nbsp;website B2B&nbsp;được sự ủy quyền của ch&iacute;nh phủ c&oacute; trụ sở tại Bắc Kinh, thủ đ&ocirc; của Trung Quốc.</p>\r\n\r\n<p><strong>9. Ioffer http://www.ioffer.com</strong></p>\r\n\r\n<p>Khởi tạo tại Mỹ, thị trường trực tuyến để mua, b&aacute;n v&agrave; giao dịch nổi tiếng Tại Mỹ</p>\r\n\r\n<p><strong>10. Tradeindia http://www.tradeindia.com</strong></p>\r\n\r\n<p>Website B2B n&agrave;y l&agrave; thị trường trực tuyến lớn nhất ở Ấn Độ, cung cấp danh bạ kinh doanh trực tuyến v&agrave; c&aacute;c trang v&agrave;ng của c&aacute;c nh&agrave; sản xuất Ấn Độ v&agrave; nước ngo&agrave;i, c&aacute;c nh&agrave; xuất khẩu, nh&agrave; cung cấp, nh&agrave; nhập khẩu v&agrave; nh&agrave; cung cấp dịch vụ.</p>\r\n\r\n<p><strong>11. Globalsources http://www.globalsources.com</strong></p>\r\n\r\n<p>Website tại Trung Quốc, n&oacute; c&oacute; một hệ thống cơ sở dữ liệu rất lớn của Ch&acirc;u &Aacute;; n&oacute; l&agrave; một nơi tuyệt vời để t&igrave;m kiếm kh&aacute;ch h&agrave;ng hoặc đối t&aacute;c tiềm năng của bạn đến từ ch&acirc;u &Aacute;.</p>\r\n\r\n<p><strong>12. Diytrade http://www.diytrade.com</strong></p>\r\n\r\n<p>Ra mắt v&agrave;o năm 1999 với t&ecirc;n eBigChina tại Trung Quốc v&agrave; đổi t&ecirc;n v&agrave;o năm 2006, n&oacute; được coi l&agrave; một trong những nền tảng kinh doanh tốt nhất với một số lượng đ&aacute;ng kinh ngạc của hơn năm triệu sản phẩm.</p>\r\n\r\n<p><strong>13. Toocle http://www.toocle.com</strong></p>\r\n\r\n<p>Website đến từ Trung Quốc, n&oacute; l&agrave; một v&ograve;ng tr&ograve;n giao dịch m&agrave; người b&aacute;n h&agrave;ng kh&aacute;c nhau được kết nối xung quanh một người mua. Tất cả bạn cần l&agrave;m l&agrave; tạo ra v&ograve;ng tr&ograve;n kinh doanh hoặc Toocle v&agrave; sau khi bắt đầu dẫn mua h&agrave;ng h&atilde;y so s&aacute;nh n&oacute; với gi&aacute; của thương nh&acirc;n kh&aacute;c nhau. Hiện c&oacute; hơn 100.000 người mua to&agrave;n cầu đăng k&yacute; tại trang web với c&aacute;c nh&agrave; cung cấp từ c&aacute;c bộ phận kh&aacute;c nhau của thế giới tham gia.</p>\r\n\r\n<p><strong>14. Yandex http://www.yandex.com</strong></p>\r\n\r\n<p>C&ocirc;ng cụ t&igrave;m kiếm sử dụng nhiều nhất ở Nga. Nếu bạn muốn t&igrave;m sản phẩm, t&igrave;m hiểu thị trường lớn n&agrave;y th&igrave; c&oacute; thể sử dụng để hiển thị ra những&nbsp;kết quả ch&iacute;nh x&aacute;c v&agrave; ph&ugrave; hợp nhất.</p>\r\n\r\n<p><strong>15. Ecplaza http://www.ecplaza.net</strong></p>\r\n\r\n<p>Khởi tạo tại H&agrave;n Quốc, đ&acirc;y l&agrave; nơi c&oacute; nhiều cơ hội giao thương cho bạn ngo&agrave;i ra c&ograve;n l&agrave; nơi ch&agrave;o h&agrave;ng, cung cấp danh bạ c&ocirc;ng ty, danh mục sản phẩm cho c&aacute;c nh&agrave; nhập khẩu, nh&agrave; xuất khẩu, nh&agrave; sản xuất, nh&agrave; cung cấp, người mua, người b&aacute;n , b&aacute;n bu&ocirc;n , đại l&yacute;, nh&agrave; ph&acirc;n phối.</p>\r\n\r\n<p><strong>16. Hktdc http://www.hktdc.com</strong></p>\r\n\r\n<p>HKTDC cung cấp một loạt c&aacute;c dịch vụ kết nối h&agrave;ng ng&agrave;n doanh nghiệp quốc tế với nhau. C&aacute;c dịch vụ được cung cấp bởi HKTDC bao gồm triển l&atilde;m đẳng cấp thế giới v&agrave; c&aacute;c hội nghị, thị trường trực tuyến, dịch vụ kinh doanh ph&ugrave; hợp, tạp ch&iacute; sản phẩm, tin tức kinh doanh kịp thời, th&ocirc;ng tin thương mại , hội thảo v&agrave; hội thảo. Tất cả c&aacute;c dịch vụ được cung cấp c&oacute; thể được sử dụng để đ&aacute;p ứng hiệu quả c&aacute;c y&ecirc;u cầu của doanh nghiệp của bạn.</p>\r\n\r\n<p><strong>17. Thomasne thttp://www.thomasnet.com</strong></p>\r\n\r\n<p>Ra đời tại Mỹ, đ&acirc;y l&agrave; website B2B c&oacute; c&aacute;c nguồn lực to&agrave;n diện nhất cho việc t&igrave;m kiếm th&ocirc;ng tin về nh&agrave; cung cấp c&aacute;c sản phẩm c&ocirc;ng nghiệp v&agrave; dịch vụ ở Bắc Mỹ.</p>\r\n\r\n<p><strong>18. Kompass http://www.kompass.com</strong></p>\r\n\r\n<p>Website tại Ph&aacute;p, hiện c&oacute; hơn ba triệu c&ocirc;ng ty trong nước v&agrave; quốc tế được liệt k&ecirc; trong Kompass. Cơ sở dữ liệu to&agrave;n diện về Kompass kết nối người b&aacute;n v&agrave; người mua kh&aacute;c nhau từ khắp nơi tr&ecirc;n thế giới. Tất cả bạn cần l&agrave;m l&agrave; đưa ra y&ecirc;u cầu của bạn trong c&aacute;c lĩnh vực cụ thể v&agrave; t&igrave;m kiếm c&aacute;c nh&agrave; cung cấp th&iacute;ch hợp nhất của bạn&nbsp;để lựa chọn.</p>\r\n\r\n<p><strong>19. Ecvv http://www.ecvv.com</strong></p>\r\n\r\n<p>Website B2B to&agrave;n cầu cho c&aacute;c nh&agrave; sản xuất v&agrave; c&aacute;c c&ocirc;ng ty kinh doanh đặc biệt tại Trung Quốc, l&agrave; nơi cung cấp dịch vụ trực tuyến cho c&aacute;c nh&agrave; nhập khẩu v&agrave; xuất khẩu.</p>\r\n\r\n<p><strong>20. Exportersindia http://www.exportersindia.com</strong></p>\r\n\r\n<p>Khởi tạo tại Ấn Độ, Exportersindia l&agrave; nơi m&agrave; người ta c&oacute; thể t&igrave;m thấy rất nhiều c&aacute;c nh&agrave; cung cấp b&aacute;n bu&ocirc;n, nh&agrave; sản xuất, nh&agrave; cung cấp dịch vụ, xuất khẩu, nhập khẩu từ Ấn Độ. N&oacute; bắt đầu v&agrave;o năm 1997 v&agrave; cung cấp c&aacute;c dịch vụ kh&aacute;c nhau về ngoại thương.</p>\r\n\r\n<p><strong>21. Fibre2fashion http://www.fibre2fashion.com</strong></p>\r\n\r\n<p>Website B2B lớn về ng&agrave;nh may mặc tại Ấn Độ, B2B Portal v&agrave; B2B Marketplace cung cấp cho kh&aacute;ch h&agrave;ng th&ocirc;ng tin về Dệt may, Thời trang, Trang phục v&agrave; C&ocirc;ng nghiệp b&aacute;n lẻ cung cấp tin tức, c&aacute;c cuộc phỏng vấn, b&agrave;i viết, cơ hội kinh doanh, nhu cầu mua b&aacute;n, x&acirc;y dựng thương hiệu dịch vụ.</p>\r\n\r\n<p><strong>22. Meritline http://www.meritline.com</strong></p>\r\n\r\n<p>Một điểm dừng tr&ecirc;n to&agrave;n thế giới về giải ph&aacute;p B2B bắt nguồn từ Mỹ</p>\r\n\r\n<p><strong>23. Exportpages http://www.exportpages.com</strong></p>\r\n\r\n<p>Exportpages được th&agrave;nh lập v&agrave;o năm 2000 tại Đức v&agrave; kể từ đ&oacute; n&oacute; đ&atilde; ph&aacute;t triển như l&agrave; một trong những chương tr&igrave;nh xuất khẩu trực tuyến h&agrave;ng đầu thế giới. C&oacute; rất nhiều danh mục mặt h&agrave;ng th&ocirc;ng qua đ&oacute; bạn c&oacute; thể lựa chọn nh&agrave; cung cấp, sản phẩm ph&ugrave; hợp với nhu cầu của m&igrave;nh. Đối với c&aacute;c chuy&ecirc;n gia kinh doanh, c&aacute;c doanh nh&acirc;n, nh&agrave; quản l&yacute; v&agrave; Xuất khẩu- đ&acirc;y l&agrave; nơi tốt nhất để ph&aacute;t triển quan hệ đối t&aacute;c trong kinh doanh quốc tế.</p>\r\n\r\n<p><strong>24. Globalmarket http://www.globalmarket.com</strong></p>\r\n\r\n<p>Globmarket l&agrave; một trong những nh&agrave; cung cấp thương mại điện tử v&agrave; dịch vụ B2B h&agrave;ng đầu kết nối c&aacute;c nh&agrave; sản xuất chất lượng h&agrave;ng đầu kh&aacute;c nhau ở Trung Quốc cho người mua từ khắp nơi tr&ecirc;n thế giới. Hiện c&oacute; hơn 900.000 người mua đăng k&yacute; Globmarket với nhiều nguồn cung ứng c&aacute;c giải ph&aacute;p v&agrave; c&aacute;c dịch vụ sẵn s&agrave;ng cung cấp cho bạn c&aacute;c th&ocirc;ng tin để cải thiện hoạt động kinh doanh của m&igrave;nh.</p>\r\n\r\n<p><strong>25. Globalspec http://www.globalspec.com</strong></p>\r\n\r\n<p>Tại Mỹ, đ&acirc;y l&agrave; website t&igrave;m kiếm h&agrave;ng đầu trong ng&agrave;nh c&ocirc;ng nghiệp kỹ thuật, Global Spec l&agrave; một nguồn tuyệt vời cho bộ phận c&ocirc;ng nghiệp, kỹ thuật v&agrave; c&aacute;c dịch vụ li&ecirc;n quan.</p>\r\n\r\n<p><strong>26. Chemnet http://www.chemnet.com</strong></p>\r\n\r\n<p>Website B2B n&agrave;y của Trung Quốc, cung cấp cho kh&aacute;ch h&agrave;ng cơ sở dữ liệu về h&oacute;a chất v&agrave; sản xuất h&oacute;a chất; ph&acirc;n phối cung cấp hoặc nhu cầu cho ng&agrave;nh c&ocirc;ng nghiệp h&oacute;a học tr&ecirc;n to&agrave;n thế giới; t&igrave;m nh&agrave; cung cấp h&oacute;a chất v&agrave; c&aacute;c hướng dẫn kinh doanh li&ecirc;n quan đến c&ocirc;ng nghiệp h&oacute;a chất.</p>\r\n\r\n<p><strong>27. Tootoo http://www.tootoo.com</strong></p>\r\n\r\n<p>C&aacute;c c&ocirc;ng cụ t&igrave;m kiếm B2B của Trung Quốc, được thiết kế để gi&uacute;p tất cả c&aacute;c nh&agrave; cung cấp tr&ecirc;n thế giới t&igrave;m ra c&aacute;c sản phẩm, c&ocirc;ng ty, dịch vụ v&agrave; nguồn lực cần thiết để ra quyết định đ&uacute;ng đắn nhất.</p>\r\n\r\n<p><strong>28. wholesalecentral http://www.wholesalecentral.com</strong></p>\r\n\r\n<p>Ra đời tại Mỹ, Wholesale Directory cung cấp c&aacute;c nguồn cho người b&aacute;n bu&ocirc;n sản phẩm.</p>\r\n\r\n<p><strong>29. Made InChina http://www.madeinchina.com</strong></p>\r\n\r\n<p>Ra đời tại Trung Quốc, kh&aacute;ch h&agrave;ng sẽ được mua b&aacute;n với sản phẩm chất lượng cao phục vụ b&aacute;n bu&ocirc;n những mặt h&agrave;ng điện thoại di động, đồ trang sức, t&uacute;i x&aacute;ch, gi&agrave;y d&eacute;p, tr&ograve; chơi video, thiết bị kỹ thuật số v&agrave; c&aacute;c sản phẩm b&aacute;n bu&ocirc;n &hellip;được giao h&agrave;ng tr&ecirc;n to&agrave;n thế giới v&agrave; chương tr&igrave;nh bảo vệ quỹ tr&ecirc;n MadeInChina.com</p>\r\n\r\n<p><strong>30. Frbiz http://www.frbiz.com</strong></p>\r\n\r\n<p>Website B2B n&agrave;y gi&uacute;p doanh nghiệp Trung Quốc cung cấp danh mục sản phẩm, cung cấp th&ocirc;ng tin nh&agrave; m&aacute;y, nh&agrave; sản xuất, nh&agrave; cung cấp v&agrave; người mua to&agrave;n cầu.</p>\r\n\r\n<p><strong>31. Taiwantrade http://www.taiwantrade.com.tw</strong></p>\r\n\r\n<p>Khởi tạo tại Đ&agrave;i Loan, doanh nghiệp vừa v&agrave; nhỏ c&oacute; thể được tiếp x&uacute;c trực tiếp với c&aacute;c hoạt động thương mại điện tử th&ocirc;ng qua website B2B Taiwantrade.C&aacute;c t&iacute;nh năng của Taiwantrade n&acirc;ng cao hiệu quả t&igrave;m kiếm, dịch vụ t&ugrave;y biến, tiếp thị trực tuyến, gia tăng gi&aacute; trị cơ sở dữ liệu, cổng th&ocirc;ng tin doanh nghiệp v&agrave; hệ thống RFQ dễ sử dụng.</p>\r\n\r\n<p><strong>32. Toboc http://www.toboc.com</strong></p>\r\n\r\n<p>S&agrave;n giao dịch Quốc tế cho c&aacute;c nh&agrave; nhập khẩu v&agrave; xuất khẩu, nh&agrave; sản xuất, nh&agrave; cung cấp quốc tế của Canada.</p>\r\n\r\n<p><strong>33. net http://www.ttnet.net</strong></p>\r\n\r\n<p>&nbsp;Trang Web cung cấp cho kh&aacute;ch h&agrave;ng c&aacute;c nh&agrave; cung cấp, c&aacute;c sản phẩm, c&aacute;c nh&agrave; xuất khẩu, nh&agrave; sản xuất, nh&agrave; cung cấp dữ liệu từ Đ&agrave;i Loan, Trung Quốc v&agrave; ch&acirc;u &Aacute;.</p>\r\n\r\n<p><strong>34. 21food http://www.21food.com</strong></p>\r\n\r\n<p>Thực phẩm &amp; Đồ uống trực tuyến &ndash; S&agrave;n giao dịch thực phẩm chuy&ecirc;n về B2B cho c&aacute;c nh&agrave; sản xuất, nh&agrave; xuất khẩu, nh&agrave; cung cấp tại Trung Quốc.</p>\r\n\r\n<p><strong>35. Esources http://www.esources.co.uk</strong></p>\r\n\r\n<p>Website B2B&nbsp;Wholesale Directory lớn nhất của Vương quốc Anh t&igrave;m kiếm nh&agrave; b&aacute;n ph&acirc;n phối, nh&agrave; cung cấp v&agrave; c&aacute;c sản phẩm.</p>\r\n\r\n<p><strong>36. Bridgat http://www.bridgat.com</strong></p>\r\n\r\n<p>Từ khi th&agrave;nh lập v&agrave;o cuối năm 2007, Bridgat.com sớm chứng tỏ sự hiện diện của m&igrave;nh bằng c&aacute;c chức năng như &ldquo;latest buying leads&rdquo; độc quyền v&agrave; &ldquo;latest selling leads&rdquo; trong trang web của m&igrave;nh. Cả người mua v&agrave; người b&aacute;n c&oacute; thể dễ d&agrave;ng li&ecirc;n lạc th&ocirc;ng qua tin nhắn trực tuyến của m&igrave;nh.</p>\r\n\r\n<p><strong>37. Ec51 http://www.ec51.com</strong></p>\r\n\r\n<p>Trang thương mại điện tử B2B từ Hồng K&ocirc;ng.</p>\r\n\r\n<p><strong>38. Toptenwholesale http://www.toptenwholesale.com</strong></p>\r\n\r\n<p>Website B2B để kết nối người mua, đại l&yacute; b&aacute;n lẻ, t&igrave;m kiếm c&aacute;c chủ h&agrave;ng, c&aacute;c nh&agrave; sản xuất v&agrave; nh&agrave; cung cấp. Họ c&oacute; hơn 100 loại v&agrave; kết nối khoảng 3300+ nh&agrave; cung cấp. Họ cũng cung cấp c&aacute;c c&ocirc;ng cụ như chat&nbsp;trực tuyến, nơi người mua v&agrave; người b&aacute;n c&oacute; thể tạo ra c&aacute;c buổi tr&ograve; chuyện.</p>\r\n\r\n<p><strong>39. Buyer Zone http://www.buyerzone.com</strong></p>\r\n\r\n<p>Trang web B2B tại Anh, đ&acirc;y l&agrave; thị trường cho c&aacute;c doanh nghiệp nhỏ v&agrave; vừa;</p>\r\n\r\n<p><strong>40. Europages http://www.europages.com</strong></p>\r\n\r\n<p>Danh bạ doanh nghiệp B2B tại ch&acirc;u &Acirc;u v&agrave; trang v&agrave;ng Quốc Tế.</p>\r\n\r\n<p><strong>41. Sugoo http://www.sugoo.com</strong></p>\r\n\r\n<p>Tạo ra ở Trung Quốc, n&oacute; c&oacute; hệ thống dữ liệu được li&ecirc;n kết với c&aacute;c tổ chức, kh&aacute;ch h&agrave;ng c&oacute; thể nhận được th&ecirc;m nhiều giao dịch khi c&oacute; dữ liệu tr&ecirc;n trang web n&agrave;y.</p>\r\n\r\n<p><strong>42. Gobizkorea http://www.gobizkorea.com</strong></p>\r\n\r\n<p>Thị trường B2B to&agrave;n cầu được th&agrave;nh lập ở H&agrave;n Quốc. Bạn c&oacute; thể t&igrave;m thấy những nh&agrave; cung cấp c&oacute; uy t&iacute;n v&agrave; kh&aacute;ch h&agrave;ng từ khắp nơi tr&ecirc;n thế giới.</p>\r\n\r\n<p><strong>43. go4worldbusiness http://www.go4worldbusiness.com</strong></p>\r\n\r\n<p>Website của Ấn Độ gi&uacute;p t&igrave;m kiếm sản phẩm, nh&agrave; cung cấp v&agrave; c&aacute;c ch&agrave;o h&agrave;ng</p>\r\n\r\n<p><strong>44. Tradeholding http://www.tradeholding.com</strong></p>\r\n\r\n<p>Nhằm mục đ&iacute;ch x&acirc;y dựng một mạng lưới to&agrave;n cầu c&aacute;c trang web B2B, nơi c&aacute;c c&ocirc;ng ty c&oacute; thể t&igrave;m kiếm nh&agrave; cung cấp v&agrave; gửi ch&agrave;o h&agrave;ng</p>\r\n\r\n<p><strong>45. Kelly Search http://www.kellysearch.com</strong></p>\r\n\r\n<p>Một trong những website t&igrave;m kiếm h&agrave;ng đầu của c&aacute;c nh&agrave; sản xuất tại Hoa Kỳ, Kelly t&igrave;m kiếm dẫn trực tiếp tới website B2B&nbsp;ở Anh Quốc, Đức, H&agrave; Lan, v&agrave; Ấn Độ.</p>\r\n\r\n<p><strong>46. Eindiabusiness http://www.eindiabusiness.com</strong></p>\r\n\r\n<p>Website B2B cung cấp cho c&aacute;c doanh nghiệp xuất khẩu c&aacute;c th&ocirc;ng tin về c&aacute;c nh&agrave; sản xuất, nhập khẩu, c&aacute;c nh&agrave; cung cấp th&ocirc;ng tin, thương mại điện tử tại Ấn Độ.</p>\r\n\r\n<p><strong>47. Tradeford http://www.tradeford.com</strong></p>\r\n\r\n<p>TradeFord.com l&agrave; một website&nbsp;B2B nhằm kết nối cộng đồng kinh doanh quốc tế, nhằm giảm chi ph&iacute; kinh doanh tổng thể v&agrave; tăng lợi nhuận của c&aacute;c doanh nghiệp th&agrave;nh vi&ecirc;n của m&igrave;nh với sự gi&uacute;p đỡ của c&aacute;c giải ph&aacute;p dựa tr&ecirc;n nền tảng web ph&ugrave; hợp với nhu cầu hiện tại của cộng đồng trực tuyến. TradeFord.com kết nối kh&aacute;ch h&agrave;ng to&agrave;n cầu với c&aacute;c nh&agrave; cung cấp, c&aacute;c nh&agrave; xuất khẩu v&agrave; nh&agrave; sản xuất OEM, giữa người mua v&agrave; c&aacute;c đại l&yacute; mua h&agrave;ng đang t&igrave;m kiếm c&aacute;c đối t&aacute;c t&igrave;m nguồn cung ứng của họ từ khắp nơi tr&ecirc;n thế giới. Ngo&agrave;i TradeFord.com đ&atilde; t&iacute;ch hợp Diễn đ&agrave;n Thương mại v&agrave; hiển thị tr&ecirc;n 220 quốc gia cho cộng đồng thương mại quốc tế.</p>\r\n\r\n<p><strong>48. Asianne thttp://www.asiannet.com</strong></p>\r\n\r\n<p>C&aacute;c trang web B2B đến từ ch&acirc;u &Aacute;.</p>\r\n\r\n<p><strong>49. sg http://www.exporters.sg</strong></p>\r\n\r\n<p>Website B2B uy t&iacute;n v&agrave; lơn tại Singapore, với việc cung cấp danh bạ c&ocirc;ng ty, danh mục sản phẩm, thương mại, c&aacute;c nh&agrave; xuất khẩu, nh&agrave; nhập khẩu, nh&agrave; sản xuất, nh&agrave; cung cấp, b&aacute;n bu&ocirc;n trực tuyến.</p>\r\n\r\n<p>C&aacute;c bạn c&ograve;n biết những website B2B n&agrave;o hữu &iacute;ch th&igrave; đừng qu&ecirc;n comment ở dưới để chia sẻ cho mọi người nh&eacute;.</p>\r\n\r\n<p>Mrs Jade.</p>','/68/84/46/5a6a217fbc4ec.jpg',NULL,14226,0,'2018-01-26 00:00:00',NULL,'B2B viết tắt của Business to Bussiness là loại hình giao dịch qua các phương tiện điện tử và mạng viễn thông đặc biệt là Internet giữa các doanh nghiệp với doanh nghiệp. Nhờ có các website này mà các doanh nghiệp có thể chào hàng, tìm kiếm khách hàng, nhà','cách tìm nhà cung cấp nước ngoài, nhân viên mua hàng, các website B2B uy tín','','',2,0,'vi','tong-hop-cac-website-b2b-tim-kiem-nha-cung-cap-khach-hang-lon-nhat-the-gioi',6,27,26,'2018-01-26 01:27:11','2018-03-03 20:40:32'),(249,'ƯU, NHƯỢC ĐIỂM CỦA PHƯƠNG THỨC NHỜ THU KÈM CHỨNG TỪ VỚI NHÀ XUẤT KHẨU VÀ NHẬP KHẨU','','Trong thanh toán quốc tế, mỗi phương thức sẽ có những ưu, nhược điểm riêng. Tùy thuộc vào từng điều kiện nhất định mà chúng ta sẽ áp dụng từng  loại hình sao cho phù hợp. Hãy cùng tìm hiểu về ưu nhược điểm của phương thức nhờ thu kèm chứng từ dưới đây','<p>Trong thanh to&aacute;n quốc tế, mỗi phương thức sẽ c&oacute; những ưu, nhược điểm ri&ecirc;ng. T&ugrave;y thuộc v&agrave;o từng điều kiện nhất định m&agrave; ch&uacute;ng ta sẽ &aacute;p dụng từng &nbsp;loại h&igrave;nh sao cho ph&ugrave; hợp. Trong khu&ocirc;n khổ b&agrave;i viết n&agrave;y, ch&uacute;ng ta sẽ t&igrave;m hiểu Ưu, nhược điểm của phương thức Nhờ thu k&egrave;m chứng từ đối với</p>\r\n\r\n<h2><em>&ndash;&nbsp;</em>Người Xuất khẩu</h2>\r\n\r\n<h3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Ưu điểm:</h3>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ndash; Chắn chắn bộ chứng từ chỉ được trao cho Người NK sau khi đ&atilde; thanh to&aacute;n hay chấp nhận thanh to&aacute;n.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &ndash; C&oacute; quyền kiện Người NK ra t&ograve;a nếu kh&ocirc;ng trả tiền hối phiếu.</p>\r\n\r\n<p>&ndash; C&oacute; thể chỉ định người đại diện ở nước người NK thay mặt m&igrave;nh để giải quyết trường hợp kh&ocirc;ng thanh to&aacute;n hoặc kh&ocirc;ng chấp nhận thanh to&aacute;n.</p>\r\n\r\n<h3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Nhược điểm:</h3>\r\n\r\n<p>&ndash; NH thu hộ trao bộ chứng từ h&agrave;ng h&oacute;a cho Người NK trước khi người n&agrave;y thanh to&aacute;n hay chấp nhận thanh to&aacute;n.</p>\r\n\r\n<p>&ndash; Chữ k&yacute; chấp nhận thanh to&aacute;n c&oacute; thể bị giả mạo, hoặc người k&yacute; chấp nhận kh&ocirc;ng đủ thẩm quyền hay chưa được đăng k&yacute; mẫu chữ k&yacute;.</p>\r\n\r\n<p>&ndash; NH thu hộ sai x&oacute;t trong việc thực hiện lệnh nhờ thu, th&igrave; mọi hậu quả đều do Người XK phải tự g&aacute;nh chịu.</p>\r\n\r\n<p>&ndash; To&agrave;n bộ hay một phần chứng từ bị thất lạc, chậm trễ.</p>\r\n\r\n<p>&ndash; NH kh&ocirc;ng chịu bất cứ tr&aacute;ch nhiệm n&agrave;o về việc lưu kho, mua bảo hiểm, giao h&agrave;ng hay dỡ h&agrave;ng h&oacute;a&hellip;</p>\r\n\r\n<p>&ndash; NH thu hộ kh&ocirc;ng thể hoặc chậm trễ thanh to&aacute;n do c&aacute;c biện ph&aacute;p kiểm so&aacute;t ngoại hối cấm chuyển ngoại tệ ra ngo&agrave;i quốc gia.</p>\r\n\r\n<p>&ndash; NH gửi nhờ thu chậm trễ, mất khả năng thanh to&aacute;n.</p>\r\n\r\n<p>&ndash; Người NK khước từ thanh to&aacute;n hay chấp nhận thanh to&aacute;n trong khi h&agrave;ng h&oacute;a đ&atilde; gửi đi từ trước.</p>\r\n\r\n<p>&ndash; Rủi ro tỷ gi&aacute;.</p>\r\n\r\n<p>&ndash; Người NK từ chối thanh to&aacute;n chi ph&iacute; ph&aacute;t sinh n&agrave;o li&ecirc;n quan đến nhờ thu hay chi ph&iacute; l&atilde;i suất m&agrave; người NK phải chịu.</p>\r\n\r\n<h2>&ndash; Người NK:</h2>\r\n\r\n<h3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Ưu điểm:</h3>\r\n\r\n<p>&ndash; C&oacute; cơ hội kiểm tra chứng từ tại NH xuất tr&igrave;nh trước khi thanh to&aacute;n hay chấp nhận thanh to&aacute;n.</p>\r\n\r\n<h3>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + Nhược điểm:</h3>\r\n\r\n<p><strong>&ndash;&nbsp;</strong>H&agrave;ng h&oacute;a c&oacute; thể đ&atilde; kh&ocirc;ng được kiểm định, chưa được bảo hiểm đầy đủ, hay kh&ocirc;ng ph&ugrave; hợp với hợp đồng.</p>\r\n\r\n<p><strong>&ndash;</strong>&nbsp;Bộ chứng từ giả, c&oacute; sai x&oacute;t, hay cố t&igrave;nh gian lận thương mại.</p>\r\n\r\n<p><strong>&ndash;</strong>&nbsp;Sau khi chấp nhận thanh to&aacute;n hối phiếu kỳ hạn (hay ph&aacute;t h&agrave;nh kỳ phiếu) người NK c&oacute; thể bị người XK kiện ra t&ograve;a nếu kh&ocirc;ng thanh to&aacute;n đ&uacute;ng hạn.</p>\r\n\r\n<p><strong>&ndash;</strong>&nbsp;Rủi ro tỷ gi&aacute;.</p>\r\n\r\n<p>Mrs Jade.</p>','/4b/b3/34/5a6a21b1c77fd.jpg',NULL,7296,0,'2018-01-26 00:00:00',NULL,'Trong thanh toán quốc tế, mỗi phương thức sẽ có những ưu, nhược điểm riêng. Tùy thuộc vào từng điều kiện nhất định mà chúng ta sẽ áp dụng từng  loại hình sao cho phù hợp. Hãy cùng tìm hiểu về ưu nhược điểm của phương thức nhờ thu kèm chứng từ dưới đây','thanh toán quốc tế, nhờ thu kèm chứng từ, ưu nhược điểm của nhờ thu kèm chứng từ','','',2,0,'vi','uu-nhuoc-diem-cua-phuong-thuc-nho-thu-kem-chung-tu-voi-nha-xuat-khau-va-nhap-khau',7,27,26,'2018-01-26 01:28:01','2018-03-03 20:43:12'),(250,'TỪ VÀ CỤM TỪ THƯỜNG DÙNG TRONG HỢP ĐỒNG NGOẠI THƯƠNG','','Trong hợp đồng ngoại thương có một số từ và cụm từ xen vào các điều khoản nhằm làm  hợp  đồng chặt chẽ hơn. Để có thể soạn thảo được một hợp đồng ngoại thương hoàn chỉnh đòi hỏi bạn phải nắm chắc các thuật ngữ này.','<p>Trong&nbsp;<strong>hợp đồng ngoại thương</strong>&nbsp;c&oacute; một số từ v&agrave; cụm từ xen v&agrave;o c&aacute;c điều khoản nhằm l&agrave;m &nbsp;hợp &nbsp;đồng chặt chẽ hơn. Để c&oacute; thể soạn thảo được một hợp đồng ngoại thương ho&agrave;n chỉnh đ&ograve;i hỏi bạn phải nắm chắc c&aacute;c thuật ngữ n&agrave;y.&nbsp;H&atilde;y c&ugrave;ng&nbsp;<strong>CLB Y&ecirc;u Xuất Nhập Khẩu</strong>&nbsp;t&igrave;m hiểu nh&eacute;:</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Quantity: Số lượng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Trade term: điều kiện thương mại</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Unit price: đơn&nbsp; gi&aacute;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Description of Goods: m&ocirc; tả h&agrave;ng h&oacute;a</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Commercial Invoice: h&oacute;a đơn thương&nbsp; mại</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Packing list: phiếu đ&oacute;ng g&oacute;i</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Net weight: trọng lượng tịnh</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Gross weight: trọng lượng tổng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Cubic meter (CBM): m&eacute;t khối</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Bill of Lading: vận tải&nbsp; đơn</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Shipper/ consigner: người gửi h&agrave;ng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Receiver/ consignee: người nhận h&agrave;ng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Notify party: b&ecirc;n th&ocirc;ng b&aacute;o</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Voyage number: số hiệu chuyến đi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Seal no: Số kẹp ch&igrave;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Number of packages: số kiện</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Freight prepaid: cước ph&iacute; trả trước</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Freight collect: cước ph&iacute; trả sau</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Number of Original: số bản gốc</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Date of issue: ng&agrave;y ph&aacute;t h&agrave;nh</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Sample: h&agrave;ng mẫu</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Quality Standard: ti&ecirc;u chuẩn chất lượng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Specification: quy c&aacute;ch</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Techical document: t&agrave;i liệu kỹ thuật</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Quantity: số lượng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Commodity: h&agrave;ng h&oacute;a</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Delivery term/ Shipment: điều khoản giao h&agrave;ng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Payment term: điều&nbsp; khoản thanh to&aacute;n</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Packing and marking: bao b&igrave; v&agrave; k&yacute; hiệu</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Warranty: bảo h&agrave;nh</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Inspection: Kiểm tra</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Force majeure: Bất khả kh&aacute;ng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Claim: khiếu nại</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Arbitration: trọng t&agrave;i</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Delivery status: t&igrave;nh trạng giao&nbsp; h&agrave;ng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Measurement: k&iacute;ch thước</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Amendment/ modification: sửa đổi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Consider: coi l&agrave;, xem như</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>On behalf of: đại diện, thay mặt</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>According to: theo</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>In witness whereof: với sự chứng kiến của</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>During the period of: trong khoảng</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Country of origin: nước xuất xứ</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Export standard packing: bao g&oacute;i ti&ecirc;u chuẩn xuất khẩu</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Total value: tổng gi&aacute; trị</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>unless otherwise agreed: trừ khi c&oacute; thỏa thuận kh&aacute;c</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Unenforceable: kh&ocirc;ng thể thực thi</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>all prior agreements: mọi thỏa thuận trước</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>upon written notice: theo th&ocirc;ng b&aacute;o bằng văn bản</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>application of law: &aacute;p dụng ph&aacute;p luật</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>settlements of disputes: giải quyết tranh chấp</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Cancellation: sự hủy bỏ</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>Effectiveness: hiệu lực</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>any circumstance: bất&nbsp; kỳ ho&agrave;n cảnh</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>have the right to sth: c&oacute; quyền g&igrave;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:548px\">\r\n			<p>at place where sth: tại nơi m&agrave;&hellip;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mrs Jade.</p>','/41/1b/b4/5a6a21fee208b.jpg',NULL,6222,0,'2018-01-26 00:00:00',NULL,'Trong hợp đồng ngoại thương có một số từ và cụm từ xen vào các điều khoản nhằm làm  hợp  đồng chặt chẽ hơn. Để có thể soạn thảo được một hợp đồng ngoại thương hoàn chỉnh đòi hỏi bạn phải nắm chắc các thuật ngữ này. Dưới đây là một số cụm từ thường gặp','hợp đồng ngoại thương, các tiêu chí trên hợp đồng ngoại thương, soạn thảo hợp đồng ngoại thương, từ vựng thường gặp trong hợp đồng ngoại thương','','',2,0,'vi','tu-va-cum-tu-thuong-dung-trong-hop-dong-ngoai-thuong',6,27,26,'2018-01-26 01:29:18','2018-03-03 20:45:17'),(251,'VAI TRÒ VÀ TRÁCH NHIỆM CỦA CÁC BÊN ĐƯỢC THÔNG BÁO TRÊN VẬN ĐƠN','','Trong khi làm nghề Xuất nhập khẩu, chắc hẳn không ít lần các bạn lúng túng khi có bên Notify party (Bên thông báo trên vận đơn) và cả bên Consignee (Người nhận hàng) cùng xuất hiện trên Vận đơn. Ngay cả khi có thể có nhiều hơn một bên thông báo, vai trò chính và trách nhiệm của các bên thông báo trên vận đơn là như nhau (được nhận thông báo về lô hàng sẽ đến).','<p>Trong khi l&agrave;m nghề Xuất nhập khẩu, chắc hẳn kh&ocirc;ng &iacute;t lần c&aacute;c bạn l&uacute;ng t&uacute;ng khi c&oacute; b&ecirc;n Notify party (B&ecirc;n th&ocirc;ng b&aacute;o tr&ecirc;n vận đơn) v&agrave; cả b&ecirc;n Consignee (Người nhận h&agrave;ng) c&ugrave;ng xuất hiện tr&ecirc;n Vận đơn.</p>\r\n\r\n<p>Ngay cả khi c&oacute; thể c&oacute; nhiều hơn một b&ecirc;n th&ocirc;ng b&aacute;o, vai tr&ograve; ch&iacute;nh v&agrave; tr&aacute;ch nhiệm của c&aacute;c b&ecirc;n th&ocirc;ng b&aacute;o tr&ecirc;n vận đơn l&agrave; như nhau (được nhận th&ocirc;ng b&aacute;o về l&ocirc; h&agrave;ng sẽ đến). Một số chủ thể c&oacute; thể được show dưới tư c&aacute;ch l&agrave; B&ecirc;n th&ocirc;ng b&aacute;o tr&ecirc;n vận đơn như:</p>\r\n\r\n<ol>\r\n	<li>Người nhận h&agrave;ng thực tế của l&ocirc; h&agrave;ng</li>\r\n	<li>C&ocirc;ng ty Forwarder hoặc Đại l&yacute; khai b&aacute;o Hải quan của người mua h&agrave;ng/ nhận h&agrave;ng.</li>\r\n	<li>B&ecirc;n trung gian thương mại</li>\r\n	<li>Ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh L/C (Ng&acirc;n h&agrave;ng của người mua)</li>\r\n</ol>\r\n\r\n<p>Nếu c&oacute; nhiều hơn một b&ecirc;n th&ocirc;ng b&aacute;o tr&ecirc;n vận đơn th&igrave; c&aacute;ch tr&igrave;nh b&agrave;y c&oacute; thể l&agrave;:</p>\r\n\r\n<p><strong>V&iacute; dụ 1:</strong></p>\r\n\r\n<p>Consignee: To order of XYZ bank</p>\r\n\r\n<p>Notify 1: Người nhận h&agrave;ng thực tế</p>\r\n\r\n<p>Notify 2:&nbsp; Đại l&yacute; khai b&aacute;o</p>\r\n\r\n<p>Trong trường hợp n&agrave;y, ng&acirc;n h&agrave;ng muốn th&ocirc;ng b&aacute;o th&ocirc;ng tin l&ocirc; h&agrave;ng cho cả chủ l&ocirc; h&agrave;ng v&agrave; đại l&yacute; khai b&aacute;o về th&ocirc;ng tin h&agrave;ng đến từ đ&oacute; c&aacute;c b&ecirc;n c&oacute; thể chủ động chuẩn bị chứng từ, thủ tục nhận h&agrave;ng.</p>\r\n\r\n<p><strong>V&iacute; dụ 2:</strong></p>\r\n\r\n<p>Consignee: ABC company</p>\r\n\r\n<p>Notify 1: C&ocirc;ng ty Forwarder hoặc ABC company</p>\r\n\r\n<p>Notify 2: Đại l&yacute; nước ngo&agrave;i</p>\r\n\r\n<p>V&iacute; dụ 2, c&aacute;c bạn thấy rằng người nhận h&agrave;ng muốn c&ocirc;ng ty Forwarder v&agrave; đại l&yacute; nước ngo&agrave;i l&agrave; b&ecirc;n th&ocirc;ng b&aacute;o tr&ecirc;n vận đơn để C&ocirc;ng ty Forwarder c&oacute; thể sắp xếp giải ph&oacute;ng h&agrave;ng h&oacute;a v&agrave; l&agrave;m thủ tục nhận h&agrave;ng cho người mua c&ograve;n đại l&yacute; ở nước ngo&agrave;i được biết rằng h&agrave;ng h&oacute;a đ&atilde; được giao theo đ&uacute;ng kế hoạch.</p>\r\n\r\n<p>Ở một số c&aacute;c Quốc gia, b&ecirc;n th&ocirc;ng b&aacute;o tr&ecirc;n vận đơn kh&ocirc;ng c&oacute; nghĩa vụ ph&aacute;p l&yacute;&nbsp;hoặc r&agrave;ng buộc về l&ocirc; h&agrave;ng. Ch&iacute;nh v&igrave; vậy m&agrave; kh&ocirc;ng c&oacute; hạn chế cụ thể về việc số lượng b&ecirc;n th&ocirc;ng b&aacute;o tr&ecirc;n vận đơn.</p>\r\n\r\n<p>Cũng cần ch&uacute; &yacute;, ở một số nước th&igrave; việc ghi nhiều b&ecirc;n th&ocirc;ng b&aacute;o tr&ecirc;n vận đơn lại kh&ocirc;ng được cho ph&eacute;p. Tốt nhất, h&atilde;y tham khảo ch&iacute;nh c&aacute;c c&ocirc;ng ty dịch vụ giao vận Quốc tế để biết ch&iacute;nh x&aacute;c nhất. C&aacute;c em c&oacute; thể tham khảo những b&agrave;i viết kh&aacute;c ở b&ecirc;n dưới v&agrave; đừng qu&ecirc;n Comment để kết nối với HAN EXIM CLUB nh&eacute;.</p>','/2d/e5/52/5a6a2234e4f30.jpg',NULL,1727,0,'2018-01-26 00:00:00',NULL,'Các bạn có để ý mục Notify party (Bên thông báo trên vận đơn) và cả bên Consignee (Người nhận hàng) cùng xuất hiện trên Vận đơn. Ngay cả khi có thể có nhiều hơn một bên thông báo, vai trò chính và trách nhiệm của các bên thông báo trên vận đơn là như nhau','notify party là gì, vai trò của notify party, người được thông báo trên vận đơn là ai','','',2,0,'vi','vai-tro-va-trach-nhiem-cua-cac-ben-duoc-thong-bao-tren-van-don',6,27,26,'2018-01-26 01:30:12','2018-03-03 20:47:21'),(252,'SỰ KHÁC NHAU GIỮA ĐẠI LÝ TÀU BIỂN (VESSEL AGENT) VÀ ĐẠI LÝ CHUYÊN CHỞ (CARRIER AGENT)','','Nhắc đến 2 thuật ngữ là Vessel Agent và Carrier Agent nhiều người vẫn tưởng là một nhưng thực ra không hẳn vậy. Hãy cùng nhau xem 2 thuật ngữ này khác nhau ở đâu nhé\r\nĐại lý chuyên chở (Carrier Agent) là người trực tiếp hoặc gián tiếp thực hiện vận chuyển hàng hóa (đường biển/ hàng không/ đa phương thức) trên cơ sở của Hợp đồng vận chuyển với các hãng tàu. Về cơ bản, đây là bên trung gian, nhận vận chuyển hàng của chủ hàng, hoặc gom nhiều lô hàng nhỏ (consolidation) thành những lô hàng lớn hơn, sau đó lại thuê người vận tải (hãng tàu, hãng hàng không) vận chuyển từ điểm xuất phát tới địa điểm đích. Ngoài việc thu xếp việc vận chuyển, các công ty giao nhận còn cung cấp nhiều dịch vụ phụ trợ khác, giúp khách hàng tập trung vào việc sản xuất kinh doanh của mình.','<p style=\"text-align:justify\">Nhắc đến 2 thuật ngữ l&agrave; Vessel Agent v&agrave; Carrier Agent nhiều người vẫn tưởng l&agrave; một nhưng thực ra kh&ocirc;ng hẳn vậy. H&atilde;y c&ugrave;ng nhau xem 2 thuật ngữ n&agrave;y kh&aacute;c nhau ở đ&acirc;u nh&eacute;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Đại l&yacute; chuy&ecirc;n chở (Carrier Agent)</strong> l&agrave; người trực tiếp hoặc gi&aacute;n tiếp thực hiện vận chuyển h&agrave;n&nbsp;h&oacute;a (đường biển/ h&agrave;ng kh&ocirc;ng/ đa phương thức) tr&ecirc;n cơ sở của Hợp đồng vận chuyển với c&aacute;c h&atilde;ng t&agrave;u. Về cơ bản, đ&acirc;y l&agrave; b&ecirc;n trung gian, nhận vận chuyển h&agrave;ng của chủ h&agrave;ng, hoặc gom nhiều l&ocirc; h&agrave;ng nhỏ (consolidation) th&agrave;nh những l&ocirc; h&agrave;ng lớn hơn, sau đ&oacute; lại thu&ecirc; người vận tải (h&atilde;ng t&agrave;u, h&atilde;ng h&agrave;ng kh&ocirc;ng) vận chuyển từ điểm xuất ph&aacute;t tới địa điểm đ&iacute;ch. Ngo&agrave;i việc thu xếp việc vận chuyển, c&aacute;c c&ocirc;ng ty giao nhận c&ograve;n cung cấp nhiều dịch vụ phụ trợ kh&aacute;c, gi&uacute;p kh&aacute;ch h&agrave;ng tập trung v&agrave;o việc sản xuất kinh doanh của m&igrave;nh. Dưới đ&acirc;y l&agrave; một số dịch vụ phổ biến:</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Th&ocirc;ng quan h&agrave;ng h&oacute;a: Carrier Agent c&oacute; thể thay chủ h&agrave;ng ho&agrave;n tất hồ sơ th&ocirc;ng quan v&agrave; nộp thuế xuất nhập khẩu</li>\r\n	<li style=\"text-align:justify\">Những vấn đề li&ecirc;n quan đến chứng từ - chẳng hạn như&nbsp;vận đơn (B/L),&nbsp;giấy chứng nhận xuất xứ (C/O), giấy ph&eacute;p xuất nhập khẩu</li>\r\n	<li style=\"text-align:justify\">Quản l&yacute; h&agrave;ng tồn kho, logistics v&agrave; c&aacute;c hoạt động quản l&yacute; chuỗi cung ứng.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/ckfinder/images/bai%20%201.png\" style=\"height:315px; width:550px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Đại l&yacute; t&agrave;u biển (Vessel Agent)</strong> sẽ l&agrave;m nhiều c&ocirc;ng việc hơn l&agrave; đại l&yacute; chuy&ecirc;n chở. Đại l&yacute; t&agrave;u biển cần phải li&ecirc;n lạc với cơ quan Cảng vụ để đảm bảo rằng t&agrave;u đến đ&uacute;ng&nbsp; giờ, đảm bảo hoạt động một c&aacute;ch tr&ocirc;i chảy hoạt động vận chuyển h&agrave;ng h&oacute;a. Một số c&ocirc;ng việc của đại l&yacute; t&agrave;u biển như&nbsp; :</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Thu xếp v&agrave; điều đ&igrave;nh c&aacute;c c&ocirc;ng t&aacute;c thương vụ h&agrave;ng h&oacute;a như: + Xếp dỡ, giao nhận chuyển tải h&agrave;ng h&oacute;a + Thu gom, chia lẻ h&agrave;ng h&oacute;a + Kiểm tra, gi&aacute;m s&aacute;t c&acirc;n đo h&agrave;ng h&oacute;a</li>\r\n	<li style=\"text-align:justify\">Thu xếp việc gửi h&agrave;ng v&agrave;o kho</li>\r\n	<li style=\"text-align:justify\">Bảo quản h&agrave;ng h&oacute;a</li>\r\n	<li style=\"text-align:justify\">Thu xếp việc đ&oacute;ng g&oacute;i sửa chữa bao b&igrave; hư hỏng r&aacute;ch n&aacute;t</li>\r\n	<li style=\"text-align:justify\">Điều đ&igrave;nh việc bồi thường h&agrave;ng h&oacute;a bị hư hỏng, mất m&aacute;t nhầm lẫn</li>\r\n	<li style=\"text-align:justify\">K&yacute; kết c&aacute;c hợp đồng vận chuyển h&agrave;ng h&oacute;a , hợp đồng thu&ecirc; t&agrave;u, hợp đồng xếp dỡ h&agrave;ng h&oacute;a, l&agrave;m thủ tục giao nhận t&agrave;u, cho thu&ecirc;, l&agrave;m thủ tục gửi h&agrave;ng, lưu khoang t&agrave;u, nhận h&agrave;ng, mua bảo hiểm cho h&agrave;ng h&oacute;a Thực hiện việc thu hộ, trả hộ tiền cước, tiền bồi thường, thanh to&aacute;n tiền thưởng phạt xếp dỡ giải ph&oacute;ng t&agrave;u v&agrave; c&aacute;c khoản tiền kh&aacute;c - M&ocirc;i giới thu&ecirc; t&agrave;u, mua t&agrave;u v&agrave; b&aacute;n t&agrave;u - Thu xếp c&aacute;c hoạt động cung ứng cho t&agrave;u biển tại cảng:</li>\r\n	<li style=\"text-align:justify\">Thu xếp sửa chữa kh&aacute;m nghiệm t&agrave;u</li>\r\n	<li style=\"text-align:justify\">Kiểm nghiệm khoang t&agrave;u cho việc xếp h&agrave;ng</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">Nhờ c&oacute; người đại l&yacute; t&agrave;u biển m&agrave; hiệu quả kinh tế của hoạt động ngoại thương được n&acirc;ng cao hơn. Người đại l&yacute; t&agrave;u biển gi&uacute;p cho người chủ t&agrave;u tận dụng được khả năng khai th&aacute;c con t&agrave;u, gi&uacute;p cho qu&aacute; tr&igrave;nh ngoại thương diễn ra nhanh ch&oacute;ng hơn, tr&aacute;nh việc người chủ t&agrave;u phải tự m&igrave;nh l&agrave;m qu&aacute; nhiều việc vừa l&agrave;m cho qu&aacute; tr&igrave;nh chuy&ecirc;n chở h&agrave;ng h&oacute;a bằng t&agrave;u biển sẽ diễn ra chậm chạp, t&agrave;u h&agrave;ng bị &ugrave;n tắc vừa tạo ra sự k&eacute;m hiệu quả do một m&igrave;nh người chủ t&agrave;u kh&ocirc;ng thể th&ocirc;ng thạo hết tất cả c&aacute;c nghiệp vụ đi biển. Người đại l&yacute; t&agrave;u biển g&oacute;p phần đ&aacute;ng kể trong việc đẩy nhanh tốc độ lưu th&ocirc;ng h&agrave;ng h&oacute;a giữa c&aacute;c nước, c&aacute;c khu vực tr&ecirc;n thế giới, gi&uacute;p cho mậu dịch quốc tế diễn ra một c&aacute;ch thuận tiện v&agrave; dễ d&agrave;ng hơn, giảm bớt được những khiếu nại v&agrave; xung đột ph&aacute;p l&yacute; xảy ra giữa người chủ t&agrave;u với chủ h&agrave;ng, cơ quan cảng sở tại v&agrave; c&aacute;c tổ chức c&oacute; li&ecirc;n quan.</p>\r\n\r\n<p style=\"text-align:justify\">Ở những b&agrave;i tiếp theo ch&uacute;ng ta sẽ c&ugrave;ng xem bạn th&iacute;ch hợp l&agrave;m ở c&ocirc;ng ty n&agrave;o hơn nh&eacute;.</p>\r\n\r\n<p style=\"text-align:justify\">Mrs Jade.</p>','/6f/48/86/5a9188249b4e1.jpg',NULL,4837,0,'2018-02-24 00:00:00',NULL,'Đại lý chuyên chở (Carrier Agent) là người trực tiếp hoặc gián tiếp thực hiện vận chuyển hàn hóa (đường biển/ hàng không/ đa phương thức) trên cơ sở của Hợp đồng vận chuyển với các hãng tàu. Về cơ bản, đây là bên trung gian, nhận vận chuyển hàng của chủ h','đại lý tàu biển, vessel agent, đại lý chuyên chở, sự khác nhau giữa đại lý tàu biển và forwarder, carrier agent','','',2,0,'vi','su-khac-nhau-giua-dai-ly-tau-bien-vessel-agent-va-dai-ly-chuyen-cho-carrier-agent',8,26,26,'2018-01-30 10:24:11','2018-03-03 20:48:23'),(253,'QUY TRÌNH LẤY LỆNH GIAO HÀNG (DELIVERY ORDER)','','Lệnh giao hàng là một trong những chứng từ cơ bản và bắt buộc cần phải có để nhà nhập khẩu có thể lấy hàng ra khỏi Cảng (đối với cả hàng LCL và FCL).  Lấy lệnh giao hàng là một trong những mắt xích cuối cùng để hàng về được kho của Nhà nhập khẩu. Tuy nhiên, khi thực  tế đi làm nghề thì nhiều bạn vẫn còn bỡ ngỡ không biết xử lý ra sao, cái gì làm trước cái gì làm sau. Hãy  để CLB Yêu Xuất Nhập Khẩu Hà Nội hướng dẫn bạn nhé.','<p style=\"text-align:justify\"><span style=\"color:#006400\">Lệnh giao h&agrave;ng l&agrave; một trong những chứng từ cơ bản v&agrave; bắt buộc cần phải c&oacute; để nh&agrave; nhập khẩu c&oacute; thể lấy h&agrave;ng ra khỏi Cảng (đối với cả h&agrave;ng LCL v&agrave; FCL). &nbsp;Lấy lệnh giao h&agrave;ng l&agrave; một trong những mắt x&iacute;ch cuối c&ugrave;ng để h&agrave;ng về được kho của Nh&agrave; nhập khẩu. Tuy nhi&ecirc;n, khi thực&nbsp; tế đi l&agrave;m nghề th&igrave; nhiều bạn vẫn c&ograve;n bỡ ngỡ kh&ocirc;ng biết xử l&yacute; ra sao, c&aacute;i g&igrave; l&agrave;m trước c&aacute;i g&igrave; l&agrave;m sau. H&atilde;y&nbsp; để CLB Y&ecirc;u Xuất Nhập Khẩu H&agrave; Nội hướng dẫn bạn nh&eacute;.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">1. Khi n&agrave;o lấy lệnh giao h&agrave;ng Delivery Order?</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Lệnh giao h&agrave;ng được lấy sau khi t&agrave;u cập cảng, h&atilde;ng t&agrave;u hoặc Forwarder gửi giấy b&aacute;o h&agrave;ng đến Arrival Notice cho Người nhận h&agrave;ng. Về cơ bản, lấy lệnh giao h&agrave;ng c&oacute; thể diễn ra trước/ sau hoặc song song với việc l&agrave;m thủ tục Hải quan v&igrave; n&oacute; độc lập với Quy tr&igrave;nh l&agrave;m thủ tục Hải quan.</span></p>\r\n\r\n<hr />\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">2.&nbsp; Ph&acirc;n loại lệnh giao h&agrave;ng</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">C&oacute; 2 loại DO ch&iacute;nh được ph&acirc;n theo chủ thể ph&aacute;t h&agrave;nh DO</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">DO do h&atilde;ng t&agrave;u ph&aacute;t h&agrave;nh: Lệnh giao h&agrave;ng của h&atilde;ng t&agrave;u l&agrave; lệnh h&atilde;ng t&agrave;u ph&aacute;t h&agrave;nh để y&ecirc;u cầu người đang giữ h&agrave;ng giao h&agrave;ng cho người n&agrave;o đ&oacute;. Thường mối quan hệ sẽ l&agrave;: H&atilde;ng t&agrave;u y&ecirc;u cầu giao h&agrave;ng cho forwarder v&agrave; forwarder y&ecirc;u cầu giao h&agrave;ng cho consignee thực sự (doanh nghiệp nhập khẩu)</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">DO do Forwarder ph&aacute;t h&agrave;nh: Lệnh giao h&agrave;ng của đại l&yacute; vận chuyển c&oacute; thể hiểu l&agrave; đại l&yacute; vận chuyển ban h&agrave;nh lệnh n&agrave;y để y&ecirc;u cầu người nắm giữ h&agrave;ng phải đưa h&agrave;ng cho người nhận (doanh nghiệp nhập khẩu).</span></li>\r\n</ul>\r\n\r\n<hr />\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">3. Lấy lệnh giao h&agrave;ng cần mang những chứng từ g&igrave;?</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Với c&aacute;c trường hợp thong thường để đi lấy lệnh giao h&agrave;ng cần mang những chứng từ sau:</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Giấy giới thiệu (bản gốc)</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Th&ocirc;ng b&aacute;o h&agrave;ng đến (bản photo)</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Vận đơn (bản gốc)</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Chứng minh nh&acirc;n d&acirc;n người đi lấy lệnh</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Nếu l&ocirc; h&agrave;ng thanh to&aacute;n bằng LC th&igrave; ngo&agrave;i c&aacute;c chứng từ tr&ecirc;n cần mang theo vận đơn gốc nhưng l&agrave; vận đơn k&yacute; hậu c&oacute; đ&oacute;ng dấu ng&acirc;n h&agrave;ng ở mặt sau.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Đối với h&agrave;ng FCL (h&agrave;ng full container) th&igrave; tr&ecirc;n DO sẽ được&nbsp; đ&oacute;ng&nbsp; dấu &ldquo;h&agrave;ng giao thẳng&rdquo; c&ograve;n nếu nh&agrave; nhập khẩu hạ h&agrave;ng v&agrave; cắt ch&igrave; tại b&atilde;i th&igrave; tr&ecirc;n DO sẽ&nbsp; được đ&oacute;ng dấu l&agrave; &ldquo;h&agrave;ng r&uacute;t ruột&rdquo;</span></p>\r\n\r\n<hr />\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">4. C&oacute; cần mang theo tiền kh&ocirc;ng?</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Để lấy được Lệnh giao h&agrave;ng th&igrave; cần phải đ&oacute;ng đầy đủ c&aacute;c khoản ph&iacute; cho h&atilde;ng h&agrave;u/ forwarder như:</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Ph&iacute; DO</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Ph&iacute; vệ sinh container (Container Cleaning Fee)</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Ph&iacute; THC</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Handling</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Ph&iacute; CFS</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Muốn biết ph&iacute; cụ thể với từng l&ocirc; bạn c&oacute; thể xem tại Giấy b&aacute;o h&agrave;ng đến hoặc check lại với h&atilde;ng t&agrave;u.</span></p>\r\n\r\n<hr />\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">5. Một số ch&uacute; &yacute; khi đi lấy lệnh giao h&agrave;ng</span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">C&oacute; một số h&atilde;ng t&agrave;u sẽ c&oacute; th&ecirc;m y&ecirc;u cầu hơn so với b&igrave;nh thường v&iacute; dụ đ&ograve;i hỏi vận đơn đ&oacute;ng dấu của chủ h&agrave;ng (c&aacute;i n&agrave;y m&agrave; gặp phải l&agrave; mất th&ecirc;m thời gian đi lấy dấu sếp c&ocirc;ng ty nhập khẩu) hoặc y&ecirc;u cầu xuất tr&igrave;nh c&aacute;c giấy nộp tiền, bi&ecirc;n lai ng&acirc;n h&agrave;ng&hellip;Nếu l&agrave;m nghề l&acirc;u lăm th&igrave; t&ocirc;i khuy&ecirc;n bạn n&ecirc;n li&ecirc;n hệ trước với h&atilde;ng t&agrave;u/ forwarder với trường hợp bạn chưa l&agrave;m với họ bao&nbsp; giờ để tr&aacute;nh phiền phức.</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Ch&uacute; &yacute; tiếp theo l&agrave; d&ugrave; c&oacute; bận hay vội thế n&agrave;o đi chăng nữa th&igrave; bạn đừng qu&ecirc;n lấy c&aacute;c bi&ecirc;n lai thu ph&iacute; của h&atilde;ng t&agrave;u, cầm nhầm h&oacute;a đơn (nếu bạn l&agrave; Forwarder th&igrave; đủ mệt v&igrave; sẽ bị kh&aacute;ch h&agrave;ng đ&ograve;i lại hoặc hold thanh to&aacute;n).</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Hy vọng với b&agrave;i viết n&agrave;y l&agrave;m bạn c&oacute; thể trở th&agrave;nh Chuy&ecirc;n gia đi lấy lệnh hoặc tự tin hơn trong c&ocirc;ng việc sắp tới.</span></p>\r\n\r\n<p style=\"text-align:justify\">Mrs Jade.</p>','/ce/4a/ac/5a6fe98f6a4b6.png',NULL,24549,0,'2018-02-24 00:00:00',NULL,'Lệnh giao hàng là một trong những chứng từ cơ bản và bắt buộc cần phải có để nhà nhập khẩu có thể lấy hàng ra khỏi Cảng (đối với cả hàng LCL và FCL).  Lấy lệnh giao hàng là một trong những mắt xích cuối cùng để hàng về được kho của Nhà nhập khẩu. Tuy nhiê','lệnh giao hàng cần mang những gì, lệnh giao hàng là gì, delivery order, thủ tục lấy lệnh giao hàng, làm thế nào lấy lệnh giao hàng','','',2,0,'vi','quy-trinh-lay-lenh-giao-hang-delivery-order',8,26,26,'2018-01-30 10:42:07','2018-03-03 20:49:52'),(254,'HƯỚNG DẪN CÁCH MỞ THƯ TÍN DỤNG  (LETTER OF CREDIT) CHO NHÀ XUẤT KHẨU','','Thư tín dụng (Letter of credit – L/C) là một văn bản pháp lý được phát hành bởi một tổ chức tài chính (thông thường là ngân hàng), nhằm cung cấp một sự bảo đảm trả tiền cho một người thụ hưởng hoặc chấp nhận hối phiếu do người này kí phát trong phạm vi số tiền đó trên cơ sở người thụ hưởng phải đáp ứng các điều khoản trong thư tín dụng.','<p><span style=\"color:#006400\">Thư t&iacute;n dụng (Letter of credit &ndash; <em>L/C</em>) l&agrave; một văn bản ph&aacute;p l&yacute; được ph&aacute;t h&agrave;nh bởi một tổ chức t&agrave;i ch&iacute;nh (th&ocirc;ng thường l&agrave; ng&acirc;n h&agrave;ng), nhằm cung cấp một sự bảo đảm trả tiền cho một người thụ hưởng hoặc chấp nhận hối phiếu do người n&agrave;y k&iacute; ph&aacute;t trong phạm vi số tiền đ&oacute; tr&ecirc;n cơ sở người thụ hưởng phải đ&aacute;p ứng c&aacute;c điều khoản trong thư t&iacute;n dụng.</span></p>\r\n\r\n<p><span style=\"color:#006400\">V&agrave; nếu bạn l&agrave; Nh&agrave; nhập khẩu trong nước chắc hẳn bạn rất muốn t&igrave;m hiểu về c&aacute;ch mở thư t&iacute;n dụng cho nh&agrave; xuất khẩu nước ngo&agrave;i. B&agrave;i viết n&agrave;y t&ocirc;i sẽ hướng dẫn chi tiết c&aacute;c bước trong thực tế để bạn mở LC th&agrave;nh c&ocirc;ng ngay từ giao dịch với ng&acirc;n h&agrave;ng lần đầu ti&ecirc;n.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Thực chất, quy tr&igrave;nh mở L/C ho&agrave;n to&agrave;n rất đơn giản chỉ vỏn vẹn c&oacute; ba bước:</span></p>\r\n\r\n<ul style=\"list-style-type:circle\">\r\n	<li><span style=\"color:#006400\">Bước 1: xin mở L/C</span></li>\r\n	<li><span style=\"color:#006400\">Bước 2: ng&acirc;n h&agrave;ng ch&iacute;nh thụ l&yacute; hồ sơ v&agrave; tiến h&agrave;nh mở thư t&iacute;n dụng cho doanh nghiệp</span></li>\r\n	<li><span style=\"color:#006400\">Bước 3: hệ thống c&aacute;c ng&acirc;n h&agrave;ng hoạt động, chuyển giao thư t&iacute;n dụng đến đối t&aacute;c, người hưởng lợi.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#006400\">Tuy chỉ đơn giản c&oacute; ba bước nhưng để mở được một L/C kh&ocirc;ng ho&agrave;n to&agrave;n dễ d&agrave;ng ở cả vị thế doanh nghiệp lẫn ng&acirc;n h&agrave;ng. Tại v&igrave; sao vậy? Để hiểu được điều đ&oacute; ch&uacute;ng ta sẽ đi s&acirc;u hơn về c&aacute;c bước tr&ecirc;n.</span></p>\r\n\r\n<h2 style=\"margin-left:0.25in\"><span style=\"color:#006400\"><strong><em>Bước 1: xin mở t&iacute;n dụng thư (L/C)</em></strong></span></h2>\r\n\r\n<p><span style=\"color:#006400\">Căn cứ v&agrave;o hợp đồng bu&ocirc;n b&aacute;n ngọai thương (hoặc h&oacute;a đơn ch&agrave;o h&agrave;ng) tổ chức nhập khẩu chủ động viết đơn, l&agrave;m hồ sơ xin mở thư t&iacute;n dụng gửi đến Ng&acirc;n h&agrave;ng phục vụ m&igrave;nh. Hồ sơ thường bao gồm:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#006400\">Đơn y&ecirc;u cầu mở L/C</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng ngoại thương (bản gốc/sao)</span></li>\r\n	<li><span style=\"color:#006400\">Giấy ph&eacute;p nhập khẩu của Bộ Thương Mại (nếu mặt h&agrave;ng nhập khẩu thuộc Danh mục quản l&yacute; quy định tại Quyết định điều h&agrave;nh xuất nhập khẩu h&agrave;ng năm của Thủ tướng Ch&iacute;nh Phủ)</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng mua b&aacute;n ngoại tệ (nếu c&oacute;)</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:1.0in\"><span style=\"color:#006400\"><img alt=\"\" src=\"/uploads/ckfinder/images/%E1%BA%A3nh%201_bi%E1%BB%83u%20ph%C3%AD%20thanh%20to%C3%A1n%20LC%20_Eximbank%20ng%C3%A0y%20230118.png\" style=\"float:left; height:391px; width:550px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/%E1%BA%A3nh%202_bi%E1%BB%83u%20ph%C3%AD%20thanh%20to%C3%A1n%20LC%20Exim%20bank%20ng%C3%A0y%20230118.png\" style=\"float:left; height:373px; width:550px\" /></span></p>\r\n\r\n<h2 style=\"margin-left:0.25in\"><strong><span style=\"color:#006400\"><u>N&ecirc;n ch&uacute; &yacute; rằng:</u></span></strong></h2>\r\n\r\n<p><span style=\"color:#006400\">1/ Mỗi ng&acirc;n h&agrave;ng c&oacute; một mẫu đơn, hồ sơ mở t&iacute;n dụng ri&ecirc;ng, kh&aacute;c nhau. Doanh nghiệp c&oacute; mong muốn mở thư t&iacute;n dụng phải lựa chọn trước ng&acirc;n h&agrave;ng m&agrave; m&igrave;nh muốn mở L/C. Ng&acirc;n h&agrave;ng đ&oacute; c&oacute; thể l&agrave; ng&acirc;n h&agrave;ng đ&atilde; rất quen thuộc v&agrave; từng hoạt động giao dịch với doanh nghiệp nhiều lần hoặc cũng c&oacute; thể l&agrave; chưa một ng&acirc;n h&agrave;ng xa lạ. Khi đ&oacute;, thủ tục xin mở doanh nghiệp c&oacute; thể sẽ rất kh&aacute;c nhau. Do đ&oacute;, doanh nghiệp phải t&igrave;m hiểu kỹ mẫu đơn của ng&acirc;n h&agrave;ng đ&atilde; chọn, v&agrave; đọc kỹ thủ tục, quy định m&agrave; ng&acirc;n h&agrave;ng đ&atilde; quy định.</span></p>\r\n\r\n<p><span style=\"color:#006400\">2/ Đơn xin mở thư t&iacute;n dụng phải được viết tối thiểu 2 bản. Sau khi Ng&acirc;n h&agrave;ng đ&oacute;ng dấu gửi trả cho đơn vị một bản</span></p>\r\n\r\n<p><span style=\"color:#006400\">3/ Cần ch&uacute; &yacute; loại thư t&iacute;n dụng m&agrave; doanh nghiệp cần, muốn mở. Ng&acirc;n h&agrave;ng sẽ c&oacute; những c&aacute;ch cung cấp dịch vụ L/C kh&aacute;c nhau. Hầu hết c&aacute;c ng&acirc;n h&agrave;ng đều c&oacute; L/C trả chậm, L/C trả ngay. B&ecirc;n cạnh đ&oacute; cũng c&oacute; c&aacute;c loại L/C dự ph&ograve;ng, c&oacute; chuyển nhượng, gi&aacute;p lưng, tuần ho&agrave;n, đối ứng &hellip;. Do đ&oacute; doanh nghiệp cần lựa chọn kỹ v&agrave; ho&agrave;n tất đầy đủ, đ&uacute;ng theo c&aacute;c y&ecirc;u cầu, thủ tục, chứng từ của ng&acirc;n h&agrave;ng đề ra.</span></p>\r\n\r\n<p><span style=\"color:#006400\">4/ Cẩn trọng v&agrave; c&acirc;n nhắc kỹ lưỡng khi đưa ra những điều kiện r&agrave;ng buộc b&ecirc;n xuất khẩu .</span></p>\r\n\r\n<p><span style=\"color:#006400\">Tr&aacute;nh t&igrave;nh trạng m&acirc;u thuẫn với những điều khoản k&yacute; kết trong hợp đồng. &ldquo;V&igrave; ng&acirc;n h&agrave;ng mở L/C theo y&ecirc;u cầu của người nhập khẩu, do vậy Qu&yacute; kh&aacute;ch n&ecirc;n xem x&eacute;t kỹ nội dung hợp đồng để đảm bảo khi đưa v&agrave;o L/C kh&ocirc;ng bị m&acirc;u thuẫn.&rdquo;</span></p>\r\n\r\n<p><span style=\"color:#006400\">5/ Đơn xin t&iacute;n dụng ch&iacute;nh l&agrave; cơ sở ph&aacute;p l&yacute; giải quyết tranh chấp giữa người xin mở thư t&iacute;n dụng với Ng&acirc;n h&agrave;ng mở thư t&iacute;n dụng v&agrave; l&agrave; cơ sở để Ng&acirc;n h&agrave;ng mở thu t&iacute;n dụng gửi cho b&ecirc;n xuất khẩu</span></p>\r\n\r\n<p><span style=\"color:#006400\">6/ Ngo&agrave;i ra khi tiến h&agrave;nh đăng k&yacute; mở L/C c&aacute;c doanh nghiệp cần ch&uacute; &yacute; đến y&ecirc;u cầu của ng&acirc;n h&agrave;ng m&igrave;nh về việc k&yacute; quỹ, b&atilde;o l&atilde;nh, uỷ quyền.</span></p>\r\n\r\n<h2><span style=\"color:#006400\"><strong><em>Bước 2: ng&acirc;n h&agrave;ng ch&iacute;nh thụ l&yacute; hồ sơ v&agrave; tiến h&agrave;nh mở thư t&iacute;n dụng cho doanh nghiệp.</em></strong></span></h2>\r\n\r\n<p><span style=\"color:#006400\">Nếu đồng &yacute;, ng&acirc;n h&agrave;ng đ&oacute;ng dấu đơn xin mở thư t&iacute;n dụng v&agrave; gửi trả cho đơn vị một bản. Sau đ&oacute; ng&acirc;n h&agrave;ng tr&iacute;ch t&agrave;i khoản đơn vị mở t&agrave;i khoản t&iacute;n dụng (k&yacute; quỹ 100% gi&aacute; trị thư t&iacute;n dụng trong trường hợp thanh to&aacute;n ngay hay X% gi&aacute; trị thư t&iacute;n dung trong trường hợp thanh to&aacute;n c&oacute; kỳ hạn). Sau đ&oacute; Ng&acirc;n h&agrave;ng viết thư t&iacute;n dụng gửi cho tổ chức xuất khẩu th&ocirc;ng qua Ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o tại nước tại nước xuất khẩu.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Việc mở thư t&iacute;n dụng c&oacute; thể thực hiện bằng đường h&agrave;ng kh&ocirc;ng, bưu ch&iacute;nh hoặc bằng điện t&iacute;n, bằng hệ thống Swift.</span></p>\r\n\r\n<p><span style=\"color:#006400\"><u>Ch&uacute; &yacute;:</u></span></p>\r\n\r\n<p><span style=\"color:#006400\">Sau khi ng&acirc;n h&agrave;ng đ&atilde; chấp thuận, một điều m&agrave; doanh nghiệp cần ch&uacute; &yacute; đ&oacute; l&agrave; ph&iacute; giao dịch. Mỗi ng&acirc;n h&agrave;ng, mỗi loại giao dịch đều c&oacute; ph&iacute; kh&aacute;c nhau tuỳ theo quy định, t&iacute;nh chất. Do vậy doanh nghiệp phải c&acirc;n nhắc lựa chọn cho sao cho ph&ugrave; hợp nhất với khả năng đ&aacute;p ứng của ch&iacute;nh doanh nghiệp m&igrave;nh.</span></p>\r\n\r\n<h2><span style=\"color:#006400\"><strong><em>Bước 3: Hệ thống c&aacute;c ng&acirc;n h&agrave;ng hoạt động, chuyển giao thư t&iacute;n dụng đến đối t&aacute;c, người hưởng lợi.</em></strong></span></h2>\r\n\r\n<p><span style=\"color:#006400\">Ng&acirc;n h&agrave;ng mở L/C gửi thư t&iacute;n dụng đến ng&acirc;n h&agrave;ng đại diện nh&agrave; xuất khẩu, nh&agrave; xuất khẩu th&ocirc;ng qua hệ thống c&aacute;c ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o (2 ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o).</span></p>\r\n\r\n<p><span style=\"color:#006400\">Tất cả ng&acirc;n h&agrave;ng thuộc v&agrave;o vị tr&iacute; người trung chuyển đều phải c&oacute; tr&aacute;ch nhiệm&nbsp; phải kiểm tra t&iacute;nh x&aacute;c thực của L/C.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Tại Ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o khi nhận thư t&iacute;n dụng th&igrave; phải tiến h&agrave;nh kiểm tra, x&aacute;c b&aacute;o điện mở L/C&nbsp; rồi chuyển bản ch&iacute;nh cho nh&agrave; xuất khẩu dưới h&igrave;nh thức văn bản nguy&ecirc;n văn.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Bất kỳ L/C n&agrave;o cũng phải đều được kiểm tra chặt chẽ t&iacute;nh x&aacute;c thực, nội dung, chữ k&yacute;, m&atilde; test trước khi chuyển đến tay người hưởng lợi. Nếu c&oacute; sai s&oacute;t g&igrave; th&igrave; ng&acirc;n h&agrave;ng trung gian, th&ocirc;ng b&aacute;o c&oacute; quyền kh&ocirc;ng chuyển giao L/C v&agrave; phải li&ecirc;n hệ ngay với ng&acirc;n h&agrave;ng mở L/C, chuyển giao lại L/C c&agrave;ng nhanh c&agrave;ng tốt, kh&ocirc;ng được chậm trễ.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Đ&acirc;y ch&iacute;nh l&agrave; ba bước trong quy tr&igrave;nh mở L/C. Trong ba bước n&agrave;y th&igrave; bước một l&agrave; tiền đề quan trọng của c&aacute;c bước c&ograve;n lại. điều n&agrave;y đ&ograve;i hỏi sự ch&uacute; &yacute;, cẩn thận, c&ocirc;ng sức &hellip; của cả doanh nghiệp v&agrave; ng&acirc;n h&agrave;ng. Đ&acirc;y cũng l&agrave; l&yacute; do tại sao việc mở L/C tưởng chừng như đơn giản nhưng thực sự kh&ocirc;ng đơn giản t&iacute; n&agrave;o.</span></p>\r\n\r\n<p>Mrs Jade.</p>','/a1/f5/5a/5a6fec30c2fc2.jpg',NULL,4228,0,'2018-02-24 00:00:00',NULL,'Thư tín dụng (Letter of credit – L/C) là một văn bản pháp lý được phát hành bởi một tổ chức tài chính (thông thường là ngân hàng), nhằm cung cấp một sự bảo đảm trả tiền cho một người thụ hưởng hoặc chấp nhận hối phiếu do người này kí phát trong phạm vi số','cách mở thư tín dụng, quy trình mở thư tín dụng trong thực tế, thư tín dụng, Letter of Credit, LC','','',2,0,'vi','huong-dan-cach-mo-thu-tin-dung-letter-of-credit-cho-nha-xuat-khau',7,26,26,'2018-01-30 10:53:20','2018-03-03 20:54:48'),(255,'MỘT SỐ LƯU Ý BẮT BUỘC CẦN ĐỌC TRƯỚC KHI MỞ THƯ TÍN DỤNG LC','','Trong quá trình mở LC các bạn rất hay luống cuống bởi các chứng từ cần thiết nhưng không chỉ có vậy bởi vì để ngân hàng chấp nhận cho bạn mở LC họ còn thẩm định doanh nghiệp và uy tín tài chính của doanh nghiệp. Chúng ta hãy cùng rà soát lại những điều quan trọng cần lưu ý trước khi mở LC nhé:','<p><span style=\"color:#006400\">Trong qu&aacute; tr&igrave;nh mở LC c&aacute;c bạn rất hay luống cuống bởi c&aacute;c chứng từ cần thiết nhưng kh&ocirc;ng chỉ c&oacute; vậy bởi v&igrave; để ng&acirc;n h&agrave;ng chấp nhận cho bạn mở LC họ c&ograve;n thẩm định doanh nghiệp v&agrave; uy t&iacute;n t&agrave;i ch&iacute;nh của doanh nghiệp. Ch&uacute;ng ta h&atilde;y c&ugrave;ng r&agrave; so&aacute;t lại những điều quan trọng cần lưu &yacute; trước khi mở LC nh&eacute;:</span></p>\r\n\r\n<h2 style=\"margin-left:13.5pt\"><span style=\"color:#006400\"><strong>1. Hồ sơ đi k&egrave;m với Đơn đề nghị mở LC</strong></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"color:#006400\">Quyết định th&agrave;nh lập doanh nghiệp (đối với c&aacute;c doanh nghiệp giao dịch lần đầu)</span></li>\r\n	<li><span style=\"color:#006400\">Đăng k&yacute; kinh doanh (đối với doanh nghiệp giao dịch lần đầu)</span></li>\r\n	<li><span style=\"color:#006400\">Đăng k&yacute; m&atilde; số xuất nhập khẩu - nếu c&oacute; (đối với doanh nghiệp giao dịch lần đầu)</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng ngoại thương gốc (trường hợp k&yacute; hợp đồng qua FAX th&igrave; đơn vị phải k&yacute; v&agrave; đ&oacute;ng dấu tr&ecirc;n bản ph&ocirc;t&ocirc;).</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng nhập khẩu uỷ th&aacute;c (nếu c&oacute;)</span></li>\r\n	<li><span style=\"color:#006400\">Giấy ph&eacute;p nhập khẩu của Bộ Thương Mại (nếu mặt h&agrave;ng nhập khẩu thuộc Danh mục quản l&yacute; quy định tại Quyết định điều h&agrave;nh xuất nhập khẩu h&agrave;ng năm của Thủ tướng Ch&iacute;nh Phủ).</span></li>\r\n	<li><span style=\"color:#006400\">Cam kết Thanh to&aacute;n, Hợp đồng T&iacute;n dụng (trường hợp vay vốn), c&ocirc;ng văn ph&ecirc; duyệt cho mở L/C trả chậm của ng&acirc;n h&agrave;ng (trường hợp mở L/C trả chậm).</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng mua b&aacute;n ngoại tệ (nếu c&oacute;)</span></li>\r\n	<li><span style=\"color:#006400\">Bản tờ tr&igrave;nh mở L/C do ph&ograve;ng T&iacute;n dụng ng&acirc;n h&agrave;ng lập được Gi&aacute;m đốc chi nh&aacute;nh hoặc người được Gi&aacute;m đốc uỷ quyền ph&ecirc; duyệt (trường hợp k&yacute; quỹ dưới 100% trị gi&aacute; L/C).</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:.25in\"><span style=\"color:#006400\">Tất cả c&aacute;c chứng từ tr&ecirc;n đều phải xuất tr&igrave;nh bản gốc v&agrave; lưu lại ng&acirc;n h&agrave;ng bản ph&ocirc;t&ocirc; c&oacute; đ&oacute;ng dấu treo của doanh nghiệp. Ri&ecirc;ng c&aacute;c chứng từ sau sẽ phải lưu bản gốc:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#006400\">Cam kết thanh to&aacute;n- Hợp đồng vay vốn</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng mua b&aacute;n ngoại tệ</span></li>\r\n	<li><span style=\"color:#006400\">Đơn xin mở L/C của kh&aacute;ch h&agrave;ng</span></li>\r\n	<li><span style=\"color:#006400\">Bản giải tr&igrave;nh mở L/C</span></li>\r\n</ul>\r\n\r\n<h2 style=\"margin-left:0.25in\"><span style=\"color:#006400\"><strong>2. C&aacute;c vấn đề với b&ecirc;n li&ecirc;n quan như ng&acirc;n h&agrave;ng, nh&agrave; xuất khẩu</strong></span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"color:#006400\">Trước khi mở L/C, người nhập khẩu cần thoả thuận cụ thể với người xuất khẩu về c&aacute;c khoản thanh to&aacute;n, lịch giao h&agrave;ng, phương tiện giao h&agrave;ng v&agrave; c&aacute;c chứng từ cần xuất tr&igrave;nh.&nbsp;</span></li>\r\n	<li><span style=\"color:#006400\">Người nhập khẩu phải nhận thức rằng L/C kh&ocirc;ng phải l&agrave; h&igrave;nh thức thanh to&aacute;n an to&agrave;n tuyệt đối v&igrave; Ng&acirc;n h&agrave;ng chỉ giao dịch tr&ecirc;n chứng từ chứ kh&ocirc;ng biết đến h&agrave;ng ho&aacute;. Nếu chứng từ ph&ugrave; hợp với c&aacute;c điều kiện, điều khoản của L/C th&igrave; người nhập khẩu phải trả tiền mặc d&ugrave; h&agrave;ng ho&aacute; đ&atilde; giao kh&ocirc;ng đ&uacute;ng với hợp đồng.&nbsp;</span></li>\r\n	<li><span style=\"color:#006400\">Đảm bảo chắc chắn l&agrave; L/C ph&ugrave; hợp với hợp đồng.&nbsp;</span></li>\r\n	<li><span style=\"color:#006400\">C&aacute;c điều kiện của L/C phải đảm bảo ngắn gọn, dễ hiểu, kh&ocirc;ng n&ecirc;n đưa v&agrave;o L/C c&aacute;c nội dung qu&aacute; chi tiết v&agrave; những quy c&aacute;ch phẩm chất qu&aacute; phức tạp.&nbsp;</span></li>\r\n	<li><span style=\"color:#006400\">Người nhập khẩu cần nghi&ecirc;n cứu kỹ để tr&aacute;nh rủi ro do biến động tỷ gi&aacute; ngoại tệ.</span></li>\r\n	<li><span style=\"color:#006400\">Ph&ograve;ng trường hợp ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh L/C cho nh&agrave; nhập khẩu gặp rắc rối v&igrave; 1 số l&yacute; do n&agrave;o đ&oacute;, nh&agrave; nhập khẩu n&ecirc;n bảo đảm rằng L/C sẽ vẫn được chấp nhận thực hiện tại ng&acirc;n h&agrave;ng kh&aacute;c đủ điều kiện (thường l&agrave; ng&acirc;n h&agrave;ng th&ocirc;ng b&aacute;o).&nbsp; Như thế sẽ bảo đảm được uy t&iacute;n của m&igrave;nh.</span></li>\r\n</ul>\r\n\r\n<p>Mrs Jade.</p>','/2b/3c/c2/5a6fed511fde1.jpg',NULL,2438,0,'2018-02-24 00:00:00',NULL,'Trong quá trình mở LC các bạn rất hay luống cuống bởi các chứng từ cần thiết nhưng không chỉ có vậy bởi vì để ngân hàng chấp nhận cho bạn mở LC họ còn thẩm định doanh nghiệp và uy tín tài chính của doanh nghiệp. Chúng ta hãy cùng rà soát lại những điều qu','các điều kiện mở thư tín dụng, hồ sơ mở thư tín dụng','','',2,0,'vi','mot-so-luu-y-bat-buoc-can-doc-truoc-khi-mo-thu-tin-dung-lc',7,26,26,'2018-01-30 10:58:09','2018-03-03 20:55:54'),(256,'QUY TRÌNH THANH TOÁN QUỐC TẾ BẰNG PHƯƠNG THỨC CHUYỂN TIỀN BẰNG ĐIỆN (TT)','','Phương thức chuyển tiền là phương thức thanh toán quốc tế trong đó khách hàng (người trả tiền, người mua, người nhập khẩu…) yêu cầu ngân hàng phục vụ mình chuyển một số tiền nhất định cho người hưởng lợi (người xuất khẩu, người cung ứng dịch vụ…) ở một địa điểm nhất định. Ngân hàng chuyển tiền thường thông qua ngân hàng đại lí của mình ở nước người hưởng lợi để thực hiện nghiệp vụ chuyển tiền.','<p><span style=\"color:#006400\">Ch&agrave;o c&aacute;c bạn h&ocirc;m nay t&ocirc;i sẽ chia sẻ c&ugrave;ng c&aacute;c bạn v&agrave;i n&eacute;t về Quy tr&igrave;nh thanh to&aacute;n Quốc tế bằng phương thức chuyển tiền TT.</span></p>\r\n\r\n<h2><span style=\"color:#006400\"><strong>1. Về kh&aacute;i niệm:</strong></span></h2>\r\n\r\n<p><span style=\"color:#006400\">Phương thức chuyển tiền l&agrave; phương thức thanh to&aacute;n quốc tế trong đ&oacute; kh&aacute;ch h&agrave;ng (người trả tiền, người mua, người nhập khẩu&hellip;) y&ecirc;u cầu ng&acirc;n h&agrave;ng phục vụ m&igrave;nh chuyển một số tiền nhất định cho người hưởng lợi (người xuất khẩu, người cung ứng dịch vụ&hellip;) ở một địa điểm nhất định. Ng&acirc;n h&agrave;ng chuyển tiền thường th&ocirc;ng qua ng&acirc;n h&agrave;ng đại l&iacute; của m&igrave;nh ở nước người hưởng lợi để thực hiện nghiệp vụ chuyển tiền.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Trong phương thức chuyển tiền th&igrave; Chuyển tiền bằng điện (Telegraphic Transfer &ndash; T/T) c&oacute; tốc độ nhanh, nhưng chi ph&iacute; cao. Ng&agrave;y nay khi tham gia mạng SWITF th&igrave; hầu hết chuyển tiền được thực hiện tr&ecirc;n mạng SWITF.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">Phương thức chuyển tiền c&oacute; thể l&agrave; bộ phận của phương thức thanh to&aacute;n kh&aacute;c như phương thức nhờ thu, t&iacute;n dụng dự ph&ograve;ng, t&iacute;n dụng chứng từ&hellip; nhưng cũng c&oacute; thể l&agrave; một phương thức thanh to&aacute;n độc lập.</span></p>\r\n\r\n<h2><span style=\"color:#006400\"><strong>2. Ưu điểm của phương&nbsp; thức chuyển tiền</strong></span></h2>\r\n\r\n<p><span style=\"color:#006400\">+ Với kh&aacute;ch h&agrave;ng: Thủ tục chuyển tiền đơn giản, thuận lợi cho người chuyển tiền; thời gian chuyển tiền ngắn n&ecirc;n người thụ hưởng c&oacute; thể nhanh ch&oacute;ng nhận được tiền.</span></p>\r\n\r\n<p><span style=\"color:#006400\">+ Với ng&acirc;n h&agrave;ng: Ng&acirc;n h&agrave;ng chỉ tham gia với vai tr&ograve; l&agrave; trung gian thanh to&aacute;n thuần t&uacute;y để hưởng ph&iacute;, kh&ocirc;ng c&oacute; tr&aacute;ch nhiệm kiểm tra sự hợp l&yacute; của thời gian thanh to&aacute;n v&agrave; lượng tiền chuyển đi.</span></p>\r\n\r\n<h2><span style=\"color:#006400\"><strong>3. C&aacute;c b&ecirc;n tham gia phương thức chuyển tiền</strong></span></h2>\r\n\r\n<p><span style=\"color:#006400\">- Người chuyển tiền (remitter): người mua,người nhập khẩu, người mắc nợ.</span></p>\r\n\r\n<p><span style=\"color:#006400\">- Ng&acirc;n h&agrave;ng chuyển tiền (remitting bank): ng&acirc;n h&agrave;ng phục vụ cho người chuyển tiền.</span></p>\r\n\r\n<p><span style=\"color:#006400\">- Ng&acirc;n h&agrave;ng đại l&iacute; (agent bank): l&agrave; ng&acirc;n h&agrave;ng phục vụ cho người thụ hưởng v&agrave; c&oacute; quan hệ đại l&iacute; với ng&acirc;n h&agrave;ng chuyển tiền.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"color:#006400\">- Người thụ hưởng: người b&aacute;n, người xuất khẩu, chủ nợ.</span></p>\r\n\r\n<h2><span style=\"color:#006400\"><strong>4. Hồ sơ chuyển tiền:</strong></span></h2>\r\n\r\n<p><span style=\"color:#006400\">* Đối với chuyển tiền trả trước hồ sơ thường bao gồm:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#006400\">Lệnh chuyển tiền</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng ngoại thương</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng mua b&aacute;n ngoại tệ (nếu c&oacute;).</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#006400\">* Đối với chuyển tiền trả sau:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#006400\">Lệnh chuyển tiền</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng ngoại thương</span></li>\r\n	<li><span style=\"color:#006400\">Hợp đồng mua b&aacute;n ngoại tệ (nếu c&oacute;)</span></li>\r\n	<li><span style=\"color:#006400\">Tờ khai hải quan</span></li>\r\n	<li><span style=\"color:#006400\">H&oacute;a đơn thương mại</span></li>\r\n	<li><span style=\"color:#006400\">Vận đơn</span></li>\r\n</ul>\r\n\r\n<h2><span style=\"color:#006400\"><strong>5. Quy tr&igrave;nh chuyển tiền</strong></span></h2>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/quy%20trinh%20thanh%20toan%20tt\" style=\"height:181px; width:539px\" /></p>\r\n\r\n<p><span style=\"color:#006400\">Bước 1: Người xuất khẩu giao h&agrave;ng hoặc dịch vụ v&agrave; bộ chứng từ cho người nhập khẩu.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Bước 2: Người nhập khẩu viết lệnh chuyển tiền gửi đến y&ecirc;u cầu ng&acirc;n h&agrave;ng chuyển tiền trả cho người xuất khẩu.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Bước 3: Sau khi kiểm tra, nếu thấy hợp lệ v&agrave; đủ khả năng thanh to&aacute;n th&igrave; ng&acirc;n h&agrave;ng phục vụ người nhập khẩu sẽ tr&iacute;ch tiền để chuyển trả người hưởng lợi v&agrave; gửi gi&acirc;y b&aacute;o nợ (giấy b&aacute;o đ&atilde; thanh to&aacute;n cho người nhập khẩu).</span></p>\r\n\r\n<p><span style=\"color:#006400\">Bước 4: Ng&acirc;n h&agrave;ng đại l&yacute; chuyển tiền trả (ghi c&oacute; v&agrave; b&aacute;o c&oacute; cho người xuất khẩu).</span></p>\r\n\r\n<p><span style=\"color:#006400\">C&oacute; hai h&igrave;nh thức chuyển tiền được sử dụng l&agrave; chuyển tiền bằng điện (Telegraphic Transfer &ndash; T/T) v&agrave; chuyển tiền bằng thư (Mail Transfer &ndash; M/L). Nếu c&aacute;c b&ecirc;n &aacute;p dụng phương thức thanh to&aacute;n điện tử th&igrave; thực hiện chuyển tiền điện tử (E.Transfer).</span></p>\r\n\r\n<p><span style=\"color:#006400\">Phương thức chuyển tiền đơn giản dễ thực hiện nhưng chỉ n&ecirc;n thực hiện với c&aacute;c kh&aacute;ch h&agrave;ng c&oacute; mối quan hệ mua b&aacute;n l&acirc;u năm v&igrave; kh&ocirc;ng đảm bảo rằng người b&aacute;n sẽ thu được tiền h&agrave;ng trong trường hợp thanh to&aacute;n sau, v&agrave; kh&ocirc;ng đảm bảo cho người mua nhận được hangnhư y&ecirc;u cầu trong trường hợp thanh to&aacute;n trước.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Mrs Jade.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>','/52/51/15/5a6fee80e5787.jpg',NULL,8752,0,'2018-02-24 00:00:00',NULL,'','quy trình thanh toán điện chuyển tiền, TT, TT là gì','','',2,0,'vi','quy-trinh-thanh-toan-quoc-te-bang-phuong-thuc-chuyen-tien-bang-dien-tt',7,26,26,'2018-01-30 11:03:12','2018-02-27 22:26:08'),(257,'MỐI LIÊN HỆ GIỮA VẬN ĐƠN ĐƯỜNG BIỂN VÀ THANH TOÁN QUỐC TẾ BẰNG THƯ TÍN DỤNG LC','','Nếu L/C quy định \"Vận đơn của người giao nhận cũng chấp nhận\" hoặc sử dụng một nhóm từ tương tự thì người giao nhận có thể ký BL với tư cách của người giao nhận mà không cần phải thể hiện anh ta là người chuyên chở hay đại lý của người chuyên chở đích danh. Cũng không cần thiết phải nêu tên người chuyên chở.','<p><span style=\"color:#006400\">Ad đ&atilde; c&oacute; rất nhiều b&agrave;i viết về vận đơn đường biển v&agrave; thanh to&aacute;n quốc tế bằng thư t&iacute;n dụng&nbsp; tuy nhi&ecirc;n về mối li&ecirc;n hệ giữa vận đơn v&agrave; thanh to&aacute;n LC lại chưa đi s&acirc;u sắc. Ch&uacute;ng ta c&ugrave;ng b&agrave;n ở b&agrave;i viết n&agrave;y nh&eacute;</span></p>\r\n\r\n<p><span style=\"color:#006400\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/42.%20bill%20of%20lading%20and%20letter%20of%20credit/bill%20of%20lading%20and%20letter%20of%20credit.jpg\" style=\"height:375px; width:500px\" /></span></p>\r\n\r\n<h2><span style=\"color:#006400\">1. Bộ đầy đủ c&aacute;c vận đơn gốc như thế n&agrave;o?</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">Điều khoản 23 UCP về chứng từ vận tải phải quy định số lượng c&aacute;c bản gốc ph&aacute;t h&agrave;nh. C&aacute;c chứng từ vận tải c&oacute; ghi ch&uacute; &ldquo;bản gốc thứ nhất&rdquo; (Original), &ldquo;bản gốc thứ hai&rdquo; (Duplicate), &ldquo;bản gốc thứ ba&rdquo; (triplicate), &ldquo;bản gốc thứ nhất&rdquo;, &ldquo;bản gốc thứ hai như nhau&rdquo;, &ldquo;bản gốc thứ ba như nhau&rdquo; v.v&hellip;. hoặc c&aacute;c ghi ch&uacute; tương tự đều l&agrave; bản gốc. B/L kh&ocirc;ng nhất thiết l&agrave; phải c&oacute; chữ &ldquo;Original&rdquo; mới được chấp nhận như l&agrave; bản gốc.</span></p>\r\n\r\n<h2><span style=\"color:#006400\">2. K&yacute; vận đơn:</span><br />\r\n&nbsp;</h2>\r\n\r\n<p><span style=\"color:#006400\">Tr&ecirc;n bề mặt của vận đơn ghi r&otilde; t&ecirc;n của người chuy&ecirc;n chở v&agrave; đ&atilde; k&yacute; t&ecirc;n hoặc được chứng thực<br />\r\n* Bởi người chuy&ecirc;n chở hoặc người đại l&yacute; đ&iacute;ch danh hoặc đại diện của người chuy&ecirc;n chở.<br />\r\n* Bởi thuyền trưởng hoặc một người đại l&yacute; đ&iacute;ch danh hoặc đại diện của thuyền trưởng.<br />\r\nBất cứ chữ k&yacute; hay chứng thực n&agrave;o của người chuy&ecirc;n chở hoặc thuyền trưởng phải được nhận biết họ đ&iacute;ch thực l&agrave; người chuy&ecirc;n chở hoặc thuyền trưởng. Tuỳ từng trường hợp một người đại l&yacute; k&yacute; t&ecirc;n hoặc chứng thực cho người chuy&ecirc;n chở hoặc thuyền trưởng cũng phải ghi r&otilde; t&ecirc;n v&agrave; năng lực của họ, người chuy&ecirc;n chở hoặc thuyền trưởng m&agrave; người đại l&yacute; thay mặt để h&agrave;nh động.<br />\r\nNếu L/C quy định &ldquo;Vận đơn của người giao nhận cũng chấp nhận&rdquo; hoặc sử dụng một nh&oacute;m từ tương tự th&igrave; người giao nhận c&oacute; thể k&yacute; BL với tư c&aacute;ch của người giao nhận m&agrave; kh&ocirc;ng cần phải thể hiện anh ta l&agrave; người chuy&ecirc;n chở hay đại l&yacute; của người chuy&ecirc;n chở đ&iacute;ch danh. Cũng kh&ocirc;ng cần thiết phải n&ecirc;u t&ecirc;n người chuy&ecirc;n chở.</span></p>\r\n\r\n<h2><span style=\"color:#006400\">3. Ghi ch&uacute; đ&atilde; bốc h&agrave;ng:</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">Việc bốc h&agrave;ng l&ecirc;n t&agrave;u hoặc giao h&agrave;ng l&ecirc;n một con t&agrave;u đ&iacute;ch danh c&oacute; thể được chỉ ra bằng từ ngữ đ&atilde; được in từ trước tr&ecirc;n mặt vận tải đơn rằng h&agrave;ng đ&atilde; được bốc xong l&ecirc;n t&agrave;u hoặc giao tr&ecirc;n một con t&agrave;u đ&iacute;ch danh. Trường hợp n&agrave;y, ng&agrave;y ph&aacute;t h&agrave;nh vận tải đơn được coi l&agrave; ng&agrave;y bốc h&agrave;ng l&ecirc;n t&agrave;u v&agrave; l&agrave; ng&agrave;y giao h&agrave;ng.<br />\r\nTrong tất cả c&aacute;c trường hợp kh&aacute;c, bốc h&agrave;ng l&ecirc;n một con t&agrave;u đ&iacute;ch danh phải được chứng minh rằng sự ghi nhận tr&ecirc;n vận tải đơn v&agrave; ngaỳ ghi nhận bốc h&agrave;ng được coi l&agrave; ng&agrave;y giao h&agrave;ng.<br />\r\nNếu vận tải đơn c&oacute; ghi &ldquo;t&agrave;u dự kiến&rdquo; hoặc những từ tương tự c&oacute; li&ecirc;n quan đến người chuy&ecirc;n chở, việc bốc h&agrave;ng l&ecirc;n t&agrave;u tr&ecirc;n con t&agrave;u đ&iacute;ch danh phải được ghi ch&uacute; tr&ecirc;n vận tải đơn, ngo&agrave;i việc ghi r&otilde; ng&agrave;y m&agrave; h&agrave;ng ho&aacute; đ&atilde; được bốc l&ecirc;n t&agrave;u c&ograve;n phải ghi t&ecirc;n của con t&agrave;u đ&oacute;, thậm ch&iacute; cả ngay khi h&agrave;ng ho&aacute; đ&atilde; được bốc l&ecirc;n một con t&agrave;u gọi l&agrave; &ldquo;con t&agrave;u dự định&rdquo;.<br />\r\nNếu vận tải đơn nơi nhận h&agrave;ng hoặc nơi nhận h&agrave;ng để gửi kh&aacute;c với cảng bốc h&agrave;ng việc ghi ch&uacute; h&agrave;ng đ&atilde; bốc l&ecirc;n t&agrave;u cũng phải ghi r&otilde; cảng bốc qui định tr&ecirc;n T&iacute;n dụng v&agrave; t&ecirc;n t&agrave;u nhận h&agrave;ng, thậm ch&iacute; ngay cả khi h&agrave;ng ho&aacute; đ&atilde; được bốc xong tr&ecirc;n con t&agrave;u được ghi t&ecirc;n tr&ecirc;n vận tải đơn. Điều khoản n&agrave;y cũng được &aacute;p dụng ngay cả khi tr&ecirc;n vận tải đơn đ&atilde; in trước chữ h&agrave;ng đ&atilde; bốc xong l&ecirc;n t&agrave;u.</span></p>\r\n\r\n<h2><span style=\"color:#006400\">4. Cảng bốc h&agrave;ng v&agrave; dỡ h&agrave;ng:</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">* Một khi cảng bốc h&agrave;ng chỉ định theo y&ecirc;u cầu của L/C phải thể hiện ở &ocirc; cảng bốc h&agrave;ng tr&ecirc;n B/L, th&igrave; c&oacute; thể n&oacute; lại được ghi v&agrave;o &ocirc; &ldquo;Nơi nhận h&agrave;ng&rdquo; hoặc tương tự nếu biết r&otilde; r&agrave;ng l&agrave; h&agrave;ng h&oacute;a đ&atilde; được vận chuyển từ nơi nhận h&agrave;ng đ&oacute; bằng t&agrave;u biển v&agrave; với điều kiện l&agrave; c&oacute; ghi ch&uacute; chứng minh rằng h&agrave;ng h&oacute;a đ&atilde; được bốc l&ecirc;n t&agrave;u tại cảng quy định ở &ldquo;Nơi nhận h&agrave;ng&rdquo; hoặc tương tự.<br />\r\n* Một khi cảng dỡ h&agrave;ng chỉ định theo y&ecirc;u cầu của L/C phải thể hiện ở &ocirc; cảng bốc h&agrave;ng tr&ecirc;n B/L, th&igrave; c&oacute; thể n&oacute; lại được ghi v&agrave;o &ocirc; &ldquo;Nơi đến cuối c&ugrave;ng&rdquo; hoặc tương tự nếu biết r&otilde; r&agrave;ng l&agrave; h&agrave;ng h&oacute;a đ&atilde; được vận chuyển đến nơi đến cuối c&ugrave;ng bằng t&agrave;u biển v&agrave; với điều kiện l&agrave; c&oacute; ghi ch&uacute; chứng minh rằng cảng dỡ h&agrave;ng l&agrave; cảng quy định ở &ldquo;Nơi đến cuối c&ugrave;ng&rdquo; hoặc tương tự.<br />\r\n* Nếu CY, trạm giao nhận hoặc kho CFS được ghi l&agrave; nơi nhận h&agrave;ng v&agrave; nơi đ&oacute; tr&ugrave;ng với cảng b&ocirc;c h&agrave;ng chỉ định th&igrave; những nơi n&agrave;y được coi l&agrave; như nhau, v&agrave; do đ&oacute; việc quy định cảng bốc h&agrave;ng v&agrave; t&ecirc;u t&agrave;u ở trong ghi ch&uacute; về h&agrave;ng đ&atilde; bốc l&ecirc;n t&agrave;u l&agrave; kh&ocirc;ng cần thiết.<br />\r\n* Nếu L/C quy định 1 khu vực địa l&yacute; hoặc 1 loạt cảng bố v&agrave; cảng dỡ th&igrave; B/L phải ghi cảng bốc v&agrave; dỡ thực tế v&agrave; c&aacute;c cảng n&agrave;y phải nằm trong khu vực địa l&yacute; hoặc c&aacute;c cảng đ&atilde; n&ecirc;u ở tr&ecirc;n.</span></p>\r\n\r\n<h2><span style=\"color:#006400\">5. Người nhận h&agrave;ng, b&ecirc;n ra lệnh, người gửi h&agrave;ng v&agrave; k&yacute; hận, b&ecirc;n th&ocirc;ng b&aacute;o:</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">* Nếu 1 L/C y&ecirc;u cầu 1 B/L ghi h&agrave;ng h&oacute;a được giao cho 1 b&ecirc;n đ&iacute;ch danh m&agrave; kh&ocirc;ng phải &ldquo;theo lệnh (to order)&rdquo; hoặc &ldquo;theo lệnh của (to order of)&rdquo; th&igrave; B/L kh&ocirc;ng được ghi từ &rdquo; theo lệnh&rdquo; hoặc &rdquo; theo lệnh của&rdquo; truước t&ecirc;n b&ecirc;n đ&iacute;ch danh đ&oacute;. Tương tự như vậy nếu L/C y&ecirc;u cầu h&agrave;ng h&oacute;a giao &ldquo;theo lệnh&rdquo; hoặc &ldquo;theo lệnh của&rdquo; một b&ecirc;n đ&iacute;ch danh th&igrave; B/L kh&ocirc;ng được ghi h&agrave;ng h&oacute;a giao trực tiếp cho b&ecirc;n đ&iacute;ch danh.<br />\r\n* Nếu B/L được ph&aacute;t h&agrave;nh theo lệnh của người gửi h&agrave;ng th&igrave; n&oacute; phải được k&yacute; hậu bởi người gửi h&agrave;ng. Việc k&yacute; hậu thể hiện v&igrave; hay l&agrave; thay mặt người gửi h&agrave;ng c&oacute; thể chấp nhận.<br />\r\n* Nếu L/C kh&ocirc;ng quy định l&agrave; th&ocirc;ng b&aacute;o cho ai th&igrave; &ocirc; đ&oacute; tr&ecirc;n B/L c&oacute; thể để trống hoặc c&oacute; thể điền v&agrave;o bất cứ c&aacute;ch n&agrave;o.</span></p>\r\n\r\n<h2><span style=\"color:#006400\">6. Chuyển tải h&agrave;ng h&oacute;a:</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">Chuyển tải c&oacute; nghĩa l&agrave; dỡ h&agrave;ng xuống v&agrave; bốc dỡ h&agrave;ng từ con t&agrave;u n&agrave;y sang con t&agrave;u kh&aacute;c từ một h&agrave;nh tr&igrave;nh vận tải biển từ cảng bốc tới cảng dỡ h&agrave;ng quy định trong T&iacute;n dụng.<br />\r\nTrừ khi c&aacute;c điều kiện ghi trong T&iacute;n dụng cấm chuyển tải, c&aacute;c Ng&acirc;n h&agrave;ng sẽ chấp nhận c&aacute;c vận tải đơn c&oacute; ghi h&agrave;ng ho&aacute; sẽ được chuyển tải, miễn l&agrave; to&agrave;n bộ h&agrave;nh tr&igrave;nh chuy&ecirc;n chở đường biển chỉ d&ugrave;ng c&ugrave;ng một vận tải đơn.Ngay cả khi T&iacute;n dụng cấm chuyển tải, c&aacute;c Ng&acirc;n h&agrave;ng sẽ chấp nhận một vận tải đơn, tr&ecirc;n đ&oacute;:<br />\r\n* C&oacute; ghi l&agrave; sẽ chuyển tải chừng n&agrave;o m&agrave; những h&agrave;ng ho&aacute; li&ecirc;n quan được chuy&ecirc;n chở bằng container, c&aacute;c mo&oacute;c v&agrave;/hoặc c&aacute;c s&agrave; lan đ&atilde; ghi tr&ecirc;n vận tải đơn, miễn l&agrave; to&agrave;n bộ h&agrave;nh tr&igrave;nh đường biển chỉ d&ugrave;ng c&ugrave;ng một vận tải đơn m&agrave; th&ocirc;i.<br />\r\n&nbsp;V&agrave;/ hoặc<br />\r\n* C&oacute; ghi điều khoản người chuy&ecirc;n chở bảo lưu quyền chuyển tải.</span></p>\r\n\r\n<h2><span style=\"color:#006400\">7. Bill of Lading ho&agrave;n hảo như thế n&agrave;o?</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">C&aacute;c điều khoản ghi ch&uacute; tr&ecirc;n B/L tuy&ecirc;n bố một c&aacute;ch r&otilde; r&agrave;ng về t&igrave;nh trạng khuyết tật của h&agrave;ng h&oacute;a v&agrave;/hoặc của bao b&igrave; l&agrave; kh&ocirc;ng thể chấp nhận. C&aacute;c điều khoản hoặc ghi ch&uacute; tr&ecirc;n B/L kh&ocirc;ng tuy&ecirc;n bố r&otilde; r&agrave;ng về t&igrave;nh trạng khuyết tật của h&agrave;ng h&oacute;a v&agrave;/hoặc của bao b&igrave; th&igrave; kh&ocirc;ng coi l&agrave; c&oacute; sai biệt. Từ ho&agrave;n hảo kh&ocirc;ng nhất thiết phải thể hiện tr&ecirc;n B/L cho d&ugrave; L/C c&oacute; thể y&ecirc;u cầu . Nếu từ ho&agrave;n hảo xuất hiện tr&ecirc;n B/L v&agrave; được x&oacute;a đi th&igrave; B/L sẽ kh&ocirc;ng được coi l&agrave; kh&ocirc;ng ho&agrave;n hảo hay kh&ocirc;ng sạch trừ khi B/L c&oacute; điều khoản hoặc ghi ch&uacute; l&agrave; h&agrave;ng h&oacute;a hoặc bao b&igrave; c&oacute; khuyết điểm.</span></p>\r\n\r\n<h2><span style=\"color:#006400\">8. M&ocirc; tả h&agrave;ng h&oacute;a:</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">M&ocirc; tả h&agrave;ng h&oacute;a tr&ecirc;n bill c&oacute; thể thể hiện một c&aacute;ch chung chung kh&ocirc;ng m&acirc;u thuẫn với m&ocirc; tả trong L/C.</span></p>\r\n\r\n<h2><span style=\"color:#006400\">9. C&aacute;c sửa chữa v&agrave; thay đổi:</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">Những sửa chữa v&agrave; thay đổi tr&ecirc;n B/L phải được x&aacute;c nhận. Những x&aacute;c nhận như thế phải được thể hiện l&agrave; so người chuy&ecirc;n chở, thuyền trưởng hoặc bất kỳ người đại l&yacute; n&agrave;o của họ thực hiện<br />\r\nC&aacute;c bản sao lưu kh&ocirc;ng lưu th&ocirc;ng được của B/L kh&ocirc;ng cần phải c&oacute; chữ k&yacute; hoặc x&aacute;c nhận bất cứ những thay đổi hoặc sửa chữa n&agrave;o c&oacute; thể đ&atilde; dược thực hiện tr&ecirc;n bản gốc.</span></p>\r\n\r\n<h2><span style=\"color:#006400\">10. Cước ph&iacute; v&agrave; phụ ph&iacute;:</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">* Nếu L/C y&ecirc;u cầu B/L phải ghi r&otilde; cước ph&iacute; PP hay CC th&igrave; B/L phải ghi ch&uacute; cho ph&ugrave; hợp.<br />\r\n* Những người y&ecirc;u cầu v&agrave; c&aacute;c ng&acirc;n h&agrave;ng ph&aacute;t h&agrave;nh phải ghi r&otilde; r&agrave;ng c&aacute;c y&ecirc;u cầu của c&aacute;c chứng từ để thể hiện l&agrave; cước ph&iacute; trả trước hay trả sau.<br />\r\n* Nếu L/C quy định kh&ocirc;ng chấp nhận c&aacute;c phụ ph&iacute; th&igrave; B/L kh&ocirc;ng được thể hiện l&agrave; phụ ph&iacute; đ&atilde; hoặc sẽ c&oacute;.Việc thể hiện như vậy c&oacute; thể n&oacute;i một c&aacute;ch r&otilde; r&agrave;ng về phụ ph&iacute; hoặc c&oacute; thể sử dụng c&aacute;c thuật ngữ m&agrave; đề cập c&aacute;c chi ph&iacute; gắn liền với việc xếp dỡ h&agrave;ng h&oacute;a như: Miễn xếp(FI), miễn dỡ(FO), miễn xếp dỡ(FIO), miễn xếp dỡ v&agrave; sắp xếp(FIOS).</span></p>\r\n\r\n<h2><span style=\"color:#006400\">11. H&agrave;ng h&oacute;a được cấp nhiều B/L:</span></h2>\r\n\r\n<p><br />\r\n<span style=\"color:#006400\">Nếu B/L ghi l&agrave; trong một cont được vận chuyển theo B/L đ&oacute; cộng với một hoặc nhiều B/L kh&aacute;c hoặc c&aacute;c từ c&oacute; &yacute; nghĩa tương tự, điều n&agrave;y c&oacute; nghĩa l&agrave; to&agrave;n bộ cont sẽ phải được giao cho ng nhận h&agrave;ng v&agrave; do đ&oacute; tất cả B/L li&ecirc;n quan đến cont đ&oacute; phải được xuất tr&igrave;nh để được giải tỏa cont. Một B/L như thế kh&ocirc;ng dược chấp nhận, trừ khi tất cả đc xuất tr&igrave;nh theo c&ugrave;ng một L/C</span></p>\r\n\r\n<p><span style=\"color:#006400\">Vận đơn l&agrave; một trong những chứng từ quan trọng nhất của mua b&aacute;n quốc tế khi h&agrave;ng ho&aacute; được vận chuyển bằng đường biển. Tuy vậy, dần dần vận đơn đ&atilde; bộc lộ nhiều nhược điểm như:</span></p>\r\n\r\n<p><span style=\"color:#006400\">- Thứ nhất, nhiều khi h&agrave;ng ho&aacute; đ&atilde; đến cảng dỡ h&agrave;ng nhưng người nhận kh&ocirc;ng c&oacute; vận đơn (B/L) để nhận h&agrave;ng v&igrave; thời gian h&agrave;nh tr&igrave;nh của h&agrave;ng ho&aacute; tr&ecirc;n biển ngắn hơn thời gian gửi bill từ cảng xếp h&agrave;ng đến cảng dỡ h&agrave;ng.</span></p>\r\n\r\n<p><span style=\"color:#006400\">- Thứ hai, B/L kh&ocirc;ng th&iacute;ch hợp với việc &aacute;p dụng c&aacute;c phương tiện truyền số liệu hiện đại tự động (fax, teleax&hellip;) bởi việc sử dụng B/L trong thanh to&aacute;n, nhận h&agrave;ng&hellip;. đ&ograve;i hỏi phải c&oacute; chứng từ gốc.</span></p>\r\n\r\n<p><span style=\"color:#006400\">- Thứ ba, việc in ấn B/L đ&ograve;i hỏi nhiều c&ocirc;ng sức v&agrave; tốn k&eacute;m bởi chữ in mặt sau của B/L thường rất nhỏ, khoảng 0,3mm để chống l&agrave;m giả.</span></p>\r\n\r\n<p><span style=\"color:#006400\">- Thứ tư, việc sử dụng B/L c&oacute; thể gặp rủi ro trong việc giao nhận h&agrave;ng ho&aacute; (nếu đơn vị bị mất cắp) v&igrave; B/L l&agrave; chứng từ sở hữu h&agrave;ng ho&aacute;&hellip;.</span></p>\r\n\r\n<p>Mrs Jade.</p>','/ff/fe/ef/5a918b9a2e16a.jpg',NULL,4492,0,'2018-02-24 00:00:00',NULL,'Nếu L/C quy định \"Vận đơn của người giao nhận cũng chấp nhận\" hoặc sử dụng một nhóm từ tương tự thì người giao nhận có thể ký BL với tư cách của người giao nhận mà không cần phải thể hiện anh ta là người chuyên chở hay đại lý của người chuyên chở đích dan','vận đơn ký hậu, vận đơn trong LC, cách ký hậu vận đơn','','',2,0,'vi','moi-lien-he-giua-van-don-duong-bien-va-thanh-toan-quoc-te-bang-thu-tin-dung-lc',7,26,26,'2018-01-30 11:07:53','2018-03-03 20:57:43'),(258,'PHÂN BIỆT FCL, LCL, CY VÀ CFS','','Các khái niệm FCL, LCL, CY VÀ CFS chỉ xuất hiện trên vận đơn đường biển và dường như đã rất quen thuộc với chúng ta. Khi nào dùng CY/CFS, khi nào dùng CFS/CY? Bài viết dưới đây sẽ giúp chúng ta hiểu rõ hơn về các khái niệm trên và biết cách sử dụng phù hợp trong từng tình huống.','<p>FCL:&nbsp; <strong>F</strong>ull <strong>C</strong>ontainer <strong>L</strong>oad (h&agrave;ng được xếp đầy v&agrave;o container)<br />\r\nLCL:&nbsp;<strong>L</strong>ess than <strong>C</strong>ontainer <strong>L</strong>oad (h&agrave;ng rời, h&agrave;ng xếp chưa đầy container), loose container load: (&iacute;t d&ugrave;ng)</p>\r\n\r\n<p>CY:&nbsp;<strong>C</strong>ontainer <strong>Y</strong>ard (b&atilde;i tập kết container)<br />\r\nCFS:&nbsp;<strong>C</strong>ontainer <strong>F</strong>reight <strong>S</strong>tation (kho gửi/nhận h&agrave;ng lẻ)</p>\r\n\r\n<p>Container: dưới đ&acirc;y được viết tắt l&agrave; &quot;cont&quot;</p>\r\n\r\n<p><strong>FCL/FCL: from FCL to FCL</strong>, được ghi tr&ecirc;n vận đơn đường biển trong trường hợp người gửi h&agrave;ng gửi h&agrave;ng nguy&ecirc;n cont từ cảng đi, người nhận h&agrave;ng nhận nguy&ecirc;n cont tại cảng đến</p>\r\n\r\n<p><strong>LCL/LCL: from LCL to LCL</strong>, được ghi tr&ecirc;n vận đơn đường biển trong trường hợp người gửi h&agrave;ng gửi h&agrave;ng lẻ từ cảng đi, h&agrave;ng được đ&oacute;ng chung cont với nhiều l&ocirc; h&agrave;ng của c&aacute;c shippers kh&aacute;c c&oacute; c&ugrave;ng điểm đến. Người nhận h&agrave;ng nhận h&agrave;ng của m&igrave;nh tại kho CFS sau khi h&agrave;ng đ&atilde; được dỡ từ cont v&agrave; chuyển v&agrave;o kho.</p>\r\n\r\n<p><strong>FCL/LCL: from FCL to LCL, </strong>d&ugrave;ng trong trường hợp người gửi h&agrave;ng (shipper) gửi 2 hay nhiều l&ocirc; h&agrave;ng đến c&ugrave;ng một điểm đến. C&aacute;c l&ocirc; h&agrave;ng lẻ n&agrave;y được xếp đầy v&agrave;o chung một cont tại điểm đi (FCL) v&agrave; gửi đến cảng đ&iacute;ch cho nhiều người nhận h&agrave;ng (consignee) kh&aacute;c nhau tại điểm đ&iacute;ch đ&oacute;.</p>\r\n\r\n<p><strong>CY/CFS &ndash; from CY to CFS, </strong><em>tương tự như h&igrave;nh thức tr&ecirc;n, </em>nhưng&nbsp;trường hợp n&agrave;y đề cập cụ thể đến địa điểm nhận h&agrave;ng v&agrave; gửi h&agrave;ng.<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Về dịch vụ vận tải bằng container, c&oacute; c&aacute;c h&igrave;nh thức sau:&nbsp;</strong></p>\r\n\r\n<p><strong>CY/CY</strong> = <em>door-to-door </em>(hoặc) <em>House to House</em>, h&agrave;ng gửi nguy&ecirc;n cont từ người gửi h&agrave;ng đến người nhận h&agrave;ng</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/43.%20CFS%20CY%20LCL%20FCL/CY.gif\" style=\"height:110px; width:550px\" /></p>\r\n\r\n<p><strong>CY/CFS</strong> = <em>door-to-port </em>(hoặc)<em> House to Pier</em>, người gửi h&agrave;ng (shipper) gửi nguy&ecirc;n cont từ nơi đi CY đến điểm đ&iacute;ch l&agrave; kho CFS của người vận chuyển (carrier), người nhận h&agrave;ng (consignee) sắp xếp phương tiện để nhận v&agrave; vận chuyển h&agrave;ng rời (h&agrave;ng lẻ) tại địa điểm kho CFS hoặc địa điểm kh&aacute;c ngo&agrave;i kho CFS.</p>\r\n\r\n<p><strong>CFS/CY</strong>&nbsp;= <em>port-to-door</em> (hoặc) <em>pier to house</em>: bến t&agrave;u đến kho, c&aacute;c l&ocirc; h&agrave;ng rời c&oacute; c&ugrave;ng đ&iacute;ch đến được đ&oacute;ng ở kho CFS tại nơi đi v&agrave;o một cont v&agrave; giao đến đ&iacute;ch cho người nhận h&agrave;ng tại b&atilde;i CY</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/43.%20CFS%20CY%20LCL%20FCL/CFS.gif\" style=\"height:112px; width:550px\" /></p>\r\n\r\n<p><strong>CFS/CFS</strong> = <em>port-to-port</em> (hoặc) <em>pier to pier</em>: bến t&agrave;u đến bến t&agrave;u. C&aacute;c l&ocirc; h&agrave;ng rời được đ&oacute;ng v&agrave;o cont v&agrave; giao đến kho CFS ở nước nhập khẩu, người nhận h&agrave;ng sắp xếp phương tiện để nhận v&agrave; vận chuyển h&agrave;ng rời (h&agrave;ng lẻ) tại địa điểm kho CFS hoặc địa điểm kh&aacute;c ngo&agrave;i kho CFS</p>\r\n\r\n<p><strong>CFS CHARGE</strong> (<em>Container Freight Charge</em>): l&agrave; ph&iacute; dịch vụ xử l&yacute; h&agrave;ng lẻ do kho CFS thu tại cảng bốc hay cảng dỡ h&agrave;ng (cho c&aacute;c hoạt động như xếp h&agrave;ng v&agrave;o cont hay dỡ h&agrave;ng ra khỏi cont&hellip;)</p>\r\n\r\n<p><strong>CFS Receiving Service</strong>: ph&iacute; dịch vụ xử l&yacute; h&agrave;ng h&oacute;a tại cảng bốc h&agrave;ng khi nhận v&agrave; đ&oacute;ng g&oacute;i&nbsp; h&agrave;ng v&agrave;o cont từ kho CFS đến b&atilde;i CY hay đến t&agrave;u. &quot;<em>CFS Receiving Services</em>&quot; th&ocirc;ng thường được gọi chung cho c&aacute;c hoạt động dưới đ&acirc;y:</p>\r\n\r\n<p><em>- Chuyển cont rỗng từ CY đến CFS</em></p>\r\n\r\n<p><em>- Drayage charge: l&agrave; ph&iacute; vận chuyển h&agrave;ng h&oacute;a một đoạn đường ngắn, như vận chuyển cont h&agrave;ng từ kho CFS đến CY hoặc ngược lại.</em></p>\r\n\r\n<p><em>- Tallying: kiểm đếm h&agrave;ng h&oacute;a</em></p>\r\n\r\n<p><em>- Issuing dock receipt/shipping order: ph&aacute;t h&agrave;nh bi&ecirc;n nhận h&agrave;ng h&oacute;a/lệnh xếp h&agrave;ng xuống t&agrave;u </em></p>\r\n\r\n<p><em>- C&aacute;c hoạt động di chuyển h&agrave;ng v&agrave;o/ra v&agrave; trong khu vực kho CFS</em></p>\r\n\r\n<p><em>- Xếp h&agrave;ng, kẹp ch&igrave;/d&aacute;n ni&ecirc;m phong v&agrave; đ&aacute;nh dấu cont</em></p>\r\n\r\n<p><em>- Storage: lưu kho</em></p>\r\n\r\n<p><em>- Sắp xếp v&agrave; xếp chồng th&ocirc;ng thường</em></p>\r\n\r\n<p><em>- L&ecirc;n kế hoạch điều xe/vận tải nội bộ </em></p>\r\n\r\n<p>Ms Sarah.</p>','/a9/3d/da/5a9199796c93f.jpg',NULL,16200,0,'2018-02-24 00:00:00',NULL,'Các khái niệm FCL, LCL, CY VÀ CFS chỉ xuất hiện trên vận đơn đường biển và dường như đã rất quen thuộc với chúng ta. Khi nào dùng CY/CFS, khi nào dùng CFS/CY? Bài viết dưới đây sẽ giúp chúng ta hiểu rõ hơn về các khái niệm trên và biết cách sử dụng phù hợ','LCL và FCL là gì, CFS và CY là gì, hàng nguyên cont, hàng lẻ, hàng rời, kho lưu hàng rời, bãi tập kết container, các loại dịch vụ vận chuyển','','',2,0,'vi','phan-biet-fcl-lcl-cy-va-cfs',8,27,26,'2018-02-24 23:57:29','2018-03-03 21:00:44'),(259,'THƯ TRẢ LỜI SINH VIÊN NĂM NHẤT - NĂM HAI MUỐN HỌC XUẤT NHẬP KHẨU','','Lớp học của CLB Yêu Xuất Nhập Khẩu Hà Nội chỉ nhận học viên từ năm 3 trở đi để đào tạo thôi các em nhé. Sinh viên năm nhất, năm hai các em nên dành thời gian để làm các việc sau đây trước khi quyết định có dấn thân vào nghề này hay không nhé?','<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Th&ocirc;ng thường 1,5 năm đầu c&aacute;c em chủ yếu học c&aacute;c m&ocirc;n đại cương, ngoại trừ bạn n&agrave;o đặt mục ti&ecirc;u bằng xuất sắc th&igrave; cắm đầu cắm cổ học, c&ograve;n chỉ cần bằng kh&aacute; hoặc chỉ cần c&oacute; bằng, c&oacute; nghĩa l&agrave; kh&ocirc;ng cần d&agrave;nh 100% thời gian v&agrave;o cho việc học mọi thứ trong s&aacute;ch gi&aacute;o tr&igrave;nh, th&igrave; chị chia sẻ cho c&aacute;c em một số việc c&aacute;c em c&oacute; thể l&agrave;m để n&acirc;ng cấp bản th&acirc;n, tăng khả năng cạnh tranh, tăng &quot;gi&aacute;&quot; của m&igrave;nh trong c&aacute;c cuộc phỏng vấn&hellip; biết đ&acirc;u lại t&igrave;m ra c&ocirc;ng việc m&igrave;nh th&iacute;ch v&agrave; muốn theo đuổi, kh&ocirc;ng nhất thiết cứ l&agrave; học xuất nhập khẩu nh&eacute;!</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#000066\">1) Tham gia c&aacute;c hoạt động của trường, của cộng đồng: </span></span></p>\r\n\r\n<h2><span style=\"font-size:14px\"><span style=\"color:#000066\">V&iacute; dụ: t&igrave;nh nguyện, v&igrave; m&ocirc;i trường, chăm s&oacute;c bệnh nh&acirc;n t&acirc;m thần, hiến m&aacute;u, vv... C&aacute;c hoạt động n&agrave;y kh&ocirc;ng nhất thiết c&aacute;c em phải được cấp chứng nhận hay x&aacute;c nhận tham gia. Tham gia những hoạt động n&agrave;y, gi&uacute;p c&aacute;c em:</span></span></h2>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Tăng tương t&aacute;c với x&atilde; hội qua c&aacute;c trải nghiệm, cơ hội giao tiếp, gi&uacute;p tăng phản xạ giao tiếp th&ocirc;ng minh</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Đ&aacute;nh gi&aacute; đ&uacute;ng v&agrave; biết ơn những g&igrave; m&igrave;nh đang c&oacute; (so với những người thiệt th&ograve;i hơn m&igrave;nh),</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Tăng nhận thức về việc tại sao phải thay đổi những th&oacute;i quen nhỏ trong sinh hoạt để g&oacute;p phần bảo vệ m&ocirc;i trường (chứ kh&ocirc;ng chỉ tham gia khi c&oacute; mặt tại c&aacute;c chiến dịch lớn),</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) R&egrave;n cho bản th&acirc;n c&oacute; l&ograve;ng trắc ẩn, bao dung</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Tiếp x&uacute;c với m&ocirc;i trường v&agrave; những người t&iacute;ch cực, gi&uacute;p bản th&acirc;n r&egrave;n luyện tư duy t&iacute;ch cực</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/gia%20son%20le.jpg\" style=\"height:367px; width:550px\" /></span></span></p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"color:#000066\">2) T&igrave;m một c&ocirc;ng việc l&agrave;m th&ecirc;m</span></span></h2>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Việc l&agrave;m kh&ocirc;ng chỉ mang lại thu nhập, m&agrave; c&aacute;c bạn c&ograve;n c&oacute; cơ hội để:</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Thấm th&iacute;a việc kiếm tiền kh&oacute; khăn như thế n&agrave;o nếu như kh&ocirc;ng c&oacute; tri thức (m&agrave; chỉ l&agrave;m c&ocirc;ng việc ch&acirc;n tay)</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Biết gi&aacute; trị thực của tiền bạc</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Nh&igrave;n nhận thấu đ&aacute;o hơn về sự ch&ecirc;nh lệch thu nhập cũng như cơ hội việc l&agrave;m giữa người c&oacute; ngoại ngữ v&agrave; người chỉ n&oacute;i được tiếng Việt</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) R&egrave;n t&aacute;c phong l&agrave;m việc c&oacute; tr&aacute;ch nhiệm, biết quan t&acirc;m đến đội nh&oacute;m, l&agrave;m việc chuy&ecirc;n nghiệp</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) R&egrave;n khả năng l&agrave;m việc dưới &aacute;p lực cao, khả năng chịu đựng, ki&ecirc;n nhẫn, thậm ch&iacute; l&agrave; khả năng nhẫn nhục</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Biết qu&yacute; trọng quỹ thời gian m&igrave;nh c&oacute; v&agrave; sử dụng c&oacute; hiệu quả hơn, biết c&acirc;n đối thời gian hơn</span></span></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/44.%20sinh%20vien%20nam%201-2/1_241663.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"color:#000066\">3) Tham gia trải nghiệm đời sống sinh vi&ecirc;n: ở trọ kiểu sinh vi&ecirc;n, ở k&iacute; t&uacute;c x&aacute; sinh vi&ecirc;n&hellip;</span></span></h2>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Với c&aacute;c bạn c&oacute; nh&agrave; gần trường, cuộc sống của bạn thời sinh vi&ecirc;n kh&ocirc;ng kh&aacute;c g&igrave; thời cấp ba. Thậm ch&iacute; sung sướng hơn v&igrave; lịch học &iacute;t hơn, nhiều thời gian hơn, nhiều quyền lực trong nh&agrave; hơn. Nếu c&oacute; duy&ecirc;n, c&aacute;c bạn c&oacute; thể kết bạn được với &ldquo;d&acirc;n tỉnh lẻ&rdquo; l&ecirc;n th&agrave;nh phố học ĐH th&igrave; rất tốt. Phần lớn c&aacute;c bạn ấy sẽ phải tự thu xếp cuộc sống của m&igrave;nh dựa tr&ecirc;n một khoản trợ cấp cố định từ cha mẹ, tự lo mọi thứ từ việc đi thu&ecirc; ph&ograve;ng trọ ngay từ l&uacute;c mới lớ ngớ bước ch&acirc;n l&ecirc;n HN, cho đến mua sắm c&aacute;i b&aacute;t, c&aacute;i đĩa&hellip;. Khi th&acirc;n nhau rồi, c&aacute;c bạn c&oacute; thể thi thoảng xin bố mẹ tới ở c&ugrave;ng với bạn m&igrave;nh v&agrave;i ba ng&agrave;y (được ph&eacute;p của bạn m&igrave;nh v&agrave; bố mẹ, c&oacute; thể ở một v&agrave;i tuần) để học từ c&aacute;c bạn ấy v&agrave; từ việc sống chung với người &ldquo;lạ&rdquo; những điều sau đ&acirc;y:</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/44.%20sinh%20vien%20nam%201-2/troinong2_fjni.jpg\" style=\"height:413px; width:550px\" /></span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Sự dũng cảm v&agrave; khả năng tự lập, tự xoay xở mọi việc với khoản trợ cấp kh&ocirc;ng thay đổi nhiều trong suốt 4 năm học ĐH</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Tr&acirc;n trọng từng vật dụng nhỏ b&eacute; m&agrave; m&igrave;nh phải bỏ tiền t&uacute;i ra mua, từ đ&oacute; sẽ biết tr&acirc;n trọng đồ đạc của người kh&aacute;c</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Thấm th&iacute;a s&acirc;u sắc c&acirc;u &ldquo;Kh&ocirc;ng ai thương con bằng cha mẹ&rdquo; v&agrave; biết ơn những g&igrave; cha mẹ đ&atilde; d&agrave;nh cho m&igrave;nh lu&ocirc;n l&agrave; thứ tốt nhất m&agrave; cha mẹ c&oacute; thể</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Biết điều chỉnh bản th&acirc;n để th&iacute;ch nghi với m&ocirc;i trường, biết nhường nhịn v&agrave; giảm bớt c&aacute;i t&ocirc;i, sự &iacute;ch kỷ khi sống chung với người kh&aacute;c, tất nhi&ecirc;n l&agrave;, nếu ch&uacute;ng ta đều muốn sống trong h&ograve;a b&igrave;nh</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) T&ocirc;n trọng ri&ecirc;ng tư của người kh&aacute;c: điện thoại, m&aacute;y t&iacute;nh, nhật k&yacute;, tiền nong, c&aacute;c loại password</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Học được c&aacute;ch chi ti&ecirc;u, quản l&yacute; t&agrave;i ch&iacute;nh v&agrave; tiết kiệm khi sử dụng t&agrave;i nguy&ecirc;n điện, nước v&agrave; tiết kiệm chi ti&ecirc;u</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Học c&aacute;ch sống bản lĩnh, tự chủ, tăng khả năng tự quyết từ c&aacute;c việc nhỏ v&agrave; đơn giản như mua rau n&ecirc;n mua 1 mớ 3 ngh&igrave;n hay 2 mớ 5 ngh&igrave;n? Mua 1 mớ th&igrave; ăn được mấy bữa v&agrave; 2 mớ liệu ăn c&oacute; ch&aacute;n kh&ocirc;ng, c&oacute; bị hỏng v&agrave; vứt đi kh&ocirc;ng?</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Học c&aacute;ch chia sẻ v&agrave; y&ecirc;u thương bạn b&egrave;, người th&acirc;n b&ecirc;n cạnh m&igrave;nh nhất, ai sẽ ở b&ecirc;n m&igrave;nh trong ho&agrave;n cảnh kh&oacute; khăn nhất&hellip;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Nếu nhiệt t&igrave;nh hơn, em c&oacute; thể c&ugrave;ng bạn nếm trải cảm gi&aacute;c &ldquo;ĐI T&Igrave;M PH&Ograve;NG TRỌ&rdquo; n&oacute; khốn khổ như thế n&agrave;o, mệt mỏi v&agrave; ch&aacute;n nản như thế n&agrave;o th&igrave; mỗi khi đến thăm nơi ăn chốn ở của ai đ&oacute;, em đều biết c&aacute;ch cư xử chừng mực v&agrave; t&ocirc;n trọng họ nhiều hơn.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Qua những trải nghiệm với cuộc sống sinh vi&ecirc;n, c&aacute;c em cũng sẽ c&oacute; c&aacute;ch nh&igrave;n mới về cuộc sống của ch&iacute;nh m&igrave;nh, thấu hiểu người kh&aacute;c hơn khi đặt m&igrave;nh v&agrave;o vị tr&iacute; của họ, biết c&aacute;ch suy nghĩ cho người kh&aacute;c nhiều hơn, &hellip; th&igrave; r&otilde; r&agrave;ng ch&iacute;nh em cũng trở n&ecirc;n ch&iacute;n chắn v&agrave; kh&ocirc;n ngoan hơn.</span></span></p>\r\n\r\n<p><span style=\"font-size:18px\"><span style=\"color:#000066\">4) Đi xe bus</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Sinh vi&ecirc;n m&agrave; chưa một lần đi xe bus th&igrave; chưa phải l&agrave; một sinh vi&ecirc;n ch&iacute;nh hiệu J Tr&ecirc;n xe bus c&oacute; rất nhiều kiểu người kh&aacute;c nhau, như một phần của x&atilde; hội thu nhỏ. Việc đi bus cũng gi&uacute;p em học được nhiều thứ phết đấy nh&eacute;, đừng coi thường:</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Khả năng chủ động lựa chọn thay đổi t&igrave;nh thế: khi chờ xe cả tiếng đồng hồ v&agrave;o giờ cao điểm hoặc trời mưa, c&oacute; khả năng c&ograve;n bị bỏ bến, hoặc xe đi đường kh&aacute;c tr&aacute;nh tắc v&agrave; kh&ocirc;ng qua bến của m&igrave;nh. Nếu như mắc một v&agrave;i lần, ta r&uacute;t ra quy luật về thời gian v&agrave; th&oacute;i quen của xe đ&oacute; v&agrave;o khung giờ đ&oacute;, m&igrave;nh sẽ c&oacute; c&aacute;c lựa chọn kh&aacute;c như l&agrave;: gọi uber, gọi người quen đ&oacute;n, đi bộ ra bến tiếp theo xe đ&oacute; đi qua, đi l&ecirc;n 1 tuyến kh&aacute;c c&oacute; thể dẫn m&igrave;nh đến chặn đầu xe kia&hellip; vv&hellip;vv Vậy, thay v&igrave; đứng chửi rủa cho đến khi thất vọng tột đỉnh th&igrave; em c&oacute; thể lựa chọn c&aacute;c con đường kh&aacute;c nhau để vẫn về được nh&agrave;. Nếu kh&ocirc;ng vội, h&atilde;y lu&ocirc;n để một cuốn s&aacute;ch/truyện em th&iacute;ch trong ba l&ocirc; để lấy ra đọc bất cứ l&uacute;c n&agrave;o trong khi em chờ bus, em sẽ chẳng c&ograve;n thấy thời gian ngồi chờ l&agrave; v&ocirc; nghĩa nữa.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Sắp xếp thời gian của bản th&acirc;n: Thay v&igrave; đi đ&uacute;ng giờ hay bị tắc đường v&agrave; kh&ocirc;ng c&oacute; ghế ngồi th&igrave; h&atilde;y chịu kh&oacute; đi sớm hơn để thư thả hơn</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Cư xử lịch sự, văn minh: biết chia sẻ chỗ ngồi cho người phải đứng qu&aacute; l&acirc;u, cho người gi&agrave;, trẻ em, phụ nữ c&oacute; thai&hellip;; giữ trật tự tr&ecirc;n xe, giữ vệ sinh tr&ecirc;n xe, cảm ơn người phục vụ m&igrave;nh&hellip;</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Cẩn thận với đồ đạc: đồ đạc c&oacute; gi&aacute; trị kh&ocirc;ng n&ecirc;n để hớ h&ecirc;nh, đeo ba l&ocirc; ngược nếu cẩn thận hơn&hellip;</span></span></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/44.%20sinh%20vien%20nam%201-2/ha-noi-luong-khach-di-xe-buyt-giam-gan-10-trong-quy-i2016-1460884316.jpg\" style=\"height:385px; width:550px\" /></p>\r\n\r\n<h2><span style=\"font-size:18px\"><span style=\"color:#000066\">5) Học ngoại ngữ</span></span></h2>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Những lợi thế khi c&oacute; Tiếng Anh, chắc chắn c&aacute;c em đều nắm r&otilde;, chỉ c&oacute; điều l&agrave; c&aacute;c em thực sự chưa c&oacute; động lực để học. Động lực bắt nguồn từ động cơ. C&aacute;c em biết cần thiết nhưng do chưa c&oacute; mục ti&ecirc;u c&ocirc;ng việc/cuộc sống r&otilde; r&agrave;ng n&ecirc;n c&aacute;c em cảm thấy việc học ngoại ngữ thật sự &aacute;p lực v&agrave; bắt buộc. V&agrave; cuối c&ugrave;ng c&aacute;c em d&ugrave; c&oacute; đi học bao nhi&ecirc;u trung t&acirc;m đi chăng nữa th&igrave; cũng chỉ ph&iacute; tiền.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Đầu ti&ecirc;n, c&aacute;c em cần biết &ldquo;M&igrave;nh thực sự muốn g&igrave;?&rdquo; Trả lời ch&iacute;nh x&aacute;c điều m&igrave;nh muốn. Điều em muốn li&ecirc;n quan mật thiết như thế n&agrave;o đối với khả năng ngoại ngữ của m&igrave;nh? Tr&igrave;nh độ ngoại ngữ tối thiểu ở mức n&agrave;o? Ghi lại tất cả những l&yacute; do khiến em cần phải học tiếng Anh, từ l&yacute; do b&ecirc;n ngo&agrave;i đến ch&iacute;nh bản th&acirc;n m&igrave;nh. Khi em gắn mục ti&ecirc;u của m&igrave;nh với y&ecirc;u cầu tr&igrave;nh độ ngoại ngữ th&igrave; tự dưng em cảm thấy việc học ngoại ngữ bắt đầu từ động cơ &ldquo;tr&aacute;ch nhiệm&rdquo; của bản th&acirc;n cũng sẽ dễ d&agrave;ng hơn so với việc học m&agrave; kh&ocirc;ng c&oacute; một định hướng g&igrave;. Nếu em nản, h&atilde;y hỏi lại ch&iacute;nh bản th&acirc;n, &ldquo;Liệu m&igrave;nh c&oacute; thực sự muốn c&oacute; điều m&igrave;nh muốn hay kh&ocirc;ng?&rdquo; em sẽ lấy được lại động lực, cũng như điều chỉnh m&igrave;nh đi theo đ&uacute;ng con đường m&igrave;nh đ&atilde; vạch ra.</span></span></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/44.%20sinh%20vien%20nam%201-2/people%20with%20signs%20%5BConverted%5D.png\" style=\"height:230px; width:550px\" /></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Kinh nghiệm t&igrave;m trung t&acirc;m học ngoại ngữ: c&aacute;c trung t&acirc;m c&agrave;ng to c&agrave;ng đắt đỏ, kh&ocirc;ng phải l&agrave; lựa chọn ph&ugrave; hợp với sinh vi&ecirc;n khi c&aacute;c em chưa tự kiếm ra tiền v&agrave; cũng chưa c&oacute; mục ti&ecirc;u r&otilde; r&agrave;ng để theo đuổi một c&aacute;ch quyết liệt th&igrave; c&aacute;c em n&ecirc;n lựa chọn nơi học dựa tr&ecirc;n những yếu tốt sau:</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Trung t&acirc;m nhỏ, start-up c&agrave;ng tốt: v&igrave; phần lớn start-up c&aacute;c bạn l&agrave;m v&igrave; đam m&ecirc;, y&ecirc;u th&iacute;ch n&ecirc;n sẽ rất hết l&ograve;ng với học vi&ecirc;n, v&agrave; cũng lu&ocirc;n c&oacute; những &yacute; tưởng mới mẻ để gi&uacute;p học vi&ecirc;n học hiệu quả, chất lượng.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) Lớp học c&oacute; số lượng học vi&ecirc;n mỗi lớp từ 10 người trở xuống: để tăng thời lượng tương t&aacute;c của mỗi học vi&ecirc;n với gi&aacute;o vi&ecirc;n tốt hơn</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">+) D&ugrave; học ngoại ngữ hay bất cứ lớp học n&agrave;o b&ecirc;n ngo&agrave;i khu&ocirc;n khổ trường ĐH, nếu chưa c&oacute; review từ bạn b&egrave; m&igrave;nh, c&aacute;c em n&ecirc;n đến học thử để cảm nhận chất lượng trước khi đ&oacute;ng học ph&iacute;, tr&aacute;nh mất tiền lại th&ecirc;m phiền l&ograve;ng.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Th&ecirc;m một điều nữa, tiếng Anh l&agrave; thứ ng&ocirc;n ngữ bắt buộc sau tiếng mẹ đẻ, tất cả ch&uacute;ng ta đều biết rồi. Vậy lợi thế hơn nữa l&agrave; g&igrave;? Ngoại ngữ thứ 2. Sau khi em đ&atilde; th&agrave;nh thạo tiếng Anh, n&ecirc;n t&igrave;m đường học cho m&igrave;nh một ng&ocirc;n ngữ nữa để tăng lợi thế cạnh tranh cho m&igrave;nh khi đi xin việc. Ngo&agrave;i chuy&ecirc;n m&ocirc;n, tiếng Anh l&agrave; bắt buộc th&igrave; ng&ocirc;n ngữ tiếp theo sẽ l&agrave; g&igrave;? C&acirc;u trả lời d&agrave;nh cho ch&iacute;nh c&aacute;c em. Những ng&ocirc;n ngữ c&aacute;c em c&oacute; thể học ngo&agrave;i Trung, Nhật, H&agrave;n l&agrave; phổ biến, c&aacute;c em c&oacute; thể lựa chọn học c&aacute;c tiếng &ldquo;hiếm&rdquo; m&agrave; kh&ocirc;ng phải ai cũng biết để lựa chọn học: Đức, &Yacute;, Nga, Ph&aacute;p, T&acirc;y Ban Nha&hellip;</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Mỗi năm c&oacute; h&agrave;ng trăm ngh&igrave;n sinh vi&ecirc;n tốt nghiệp ra trường, để x&aacute;c định cho m&igrave;nh chỗ đứng ngay sau khi ra trường, từ năm nhất năm hai, khi đang l&agrave; &ocirc;ng vua b&agrave; nữ ho&agrave;ng về thời gian th&igrave; c&aacute;c em h&atilde;y t&iacute;ch cực lăn xả v&agrave;o cuộc sống thực tế b&ecirc;n ngo&agrave;i c&agrave;ng nhiều c&agrave;ng tốt. Điều n&agrave;y tr&aacute;i với mong muốn của cha mẹ, v&igrave; cha mẹ lu&ocirc;n sợ c&aacute;c em khổ, sợ vất vả, sợ bị lừa&hellip; cha mẹ muốn c&aacute;c em tập trung học h&agrave;nh đạt bằng giỏi, bằng xuất sắc để ra trường xin việc tốt, lương cao, ổn định. Nhưng cha mẹ kh&ocirc;ng hay rằng, điểm cao kh&ocirc;ng c&oacute; nghĩa l&agrave; kỹ năng sống cao. M&agrave; thực tế c&aacute;c c&ocirc;ng ty ng&agrave;y nay, khi tuyển nh&acirc;n vi&ecirc;n l&agrave; sinh vi&ecirc;n mới ra trường, họ kh&ocirc;ng xem xếp loại bằng m&agrave; họ test trực tiếp kỹ năng cứng &ndash; mềm của c&aacute;c em tại chỗ, l&agrave;m được hay kh&ocirc;ng biết ngay.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Mọi trải nghiệm d&ugrave; mất tiền hay kh&ocirc;ng mất tiền, d&ugrave; mất &iacute;t hay mất nhiều th&igrave; sau mỗi lần &ldquo;trải&rdquo;, c&aacute;c em sẽ c&oacute; nhiều điều để &ldquo;nghiệm&rdquo;. C&agrave;ng nghiệm ra nhiều điều t&iacute;ch cực th&igrave; c&aacute;c em c&agrave;ng nhanh trưởng th&agrave;nh. V&agrave; trải nghiệm c&agrave;ng nhiều, c&aacute;c em c&agrave;ng trưởng th&agrave;nh v&agrave; cứng c&aacute;p hơn so với c&aacute;c bạn c&ugrave;ng trang lứa. Đ&oacute; ch&iacute;nh l&agrave; c&aacute;i nh&agrave; tuyển dụng chọn em chứ kh&ocirc;ng phải v&igrave; chữ &ldquo;Good&rdquo; hay &ldquo;Excellent&rdquo; tr&ecirc;n Bằng tốt nghiệp.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Thế nh&eacute;, c&aacute;c em năm nhất, năm hai cứ dũng cảm l&ecirc;n, ngẩng cao đầu, mở to mắt để quan s&aacute;t v&agrave; dỏng tai l&ecirc;n để lắng nghe v&agrave; đứng thẳng dậy để bước ch&acirc;n v&agrave;o thế giới. Những g&igrave; em học được sẽ kh&ocirc;ng c&oacute; tiền bạc n&agrave;o mua nổi. Năm thứ 3-4 tập trung học chuy&ecirc;n ng&agrave;nh, kỳ II năm thứ tư l&agrave; nộp hồ sơ đi l&agrave;m part-time ở c&aacute;c c&ocirc;ng ty rồi, nếu l&agrave; tốt được người ta ưng thuận th&igrave; ra trường lấy bằng l&agrave; về đầu qu&acirc;n cho c&ocirc;ng ty lu&ocirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Nếu em n&agrave;o muốn vừa được học Xuất nhập khẩu lại vừa c&oacute; kỹ năng mềm th&igrave; đợi năm ba h&atilde;y đến HAN EXIM CLUB để tham gia Kh&oacute;a huấn luyện đặc biệt c&oacute; một kh&ocirc;ng hai n&agrave;y nh&eacute;.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Chi tiết về kh&oacute;a học tại:&nbsp;http://tuhocxuatnhapkhau.com/khoa-huan-luyen</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000066\">Ms Sarah.</span></span></p>','/59/05/55/5a919e6e622d6.jpg',NULL,948,0,'2018-02-25 00:00:00',NULL,'Lớp học của CLB Yêu Xuất Nhập Khẩu Hà Nội chỉ nhận học viên từ năm 3 trở đi để đào tạo thôi các em nhé. Sinh viên năm nhất, năm hai các em nên dành thời gian để làm các việc sau đây trước khi quyết định có dấn thân vào nghề này hay không nhé?','sinh viên năm nhất, việc làm thêm sinh viên, nhà trọ sinh viên, học xuất nhập khẩu, tuyển dụng sinh viên, nơi học ngoại ngữ giá sinh viên uy tín','','',2,0,'vi','thu-tra-loi-sinh-vien-nam-nhat-nam-hai-muon-hoc-xuat-nhap-khau',5,27,26,'2018-02-25 00:06:07','2018-03-03 21:15:41'),(260,'QUY TRÌNH THỦ TỤC HÀNG NHẬP KHẨU CHI TIẾT CHO NGƯỜI MỚI VÀO NGHỀ','','Bài viết hôm nay chị sẽ hướng dẫn các em chi tiết các bước trong quy trình xử lý hàng nhập khẩu từ khi nhận chứng từ cho đến khi giao hàng về tận kho cho khách hàng. Hãy ghi chép lại vào sổ tay của mình để theo dõi trong quá trình làm việc thực tế và có thể phản hồi dưới bài viết ở mục comment nếu như em thấy thực tế đúng như vậy hay có điểm nào là khác biệt nhé.','<p><span style=\"font-size:14px\"><strong>1<em>. </em>Xử l&yacute; chứng từ </strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bước 1. Th&ocirc;ng thường, chứng từ sẽ được PIC (person in charge: người phụ tr&aacute;ch) đầu xuất gửi trước khi h&agrave;ng về (pre-alert) để PIC đầu nhập chuẩn bị thủ tục nhập khẩu. Bạn cần kiểm tra kỹ c&aacute;c th&ocirc;ng tin tr&ecirc;n chứng từ đảm bảo đầy đủ về mặt số lượng, ch&iacute;nh x&aacute;c về mặt số liệu v&agrave; logic về c&aacute;c th&ocirc;ng tin kh&aacute;c giữa c&aacute;c chứng từ trong một bộ chứng từ. C&oacute; sai s&oacute;t g&igrave;, cần tập trung xử l&yacute; v&agrave; phản hồi ngay với đầu xuất để giải quyết kịp thời.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Bước 2: X&aacute;c định t&ecirc;n h&agrave;ng, chủng loại h&agrave;ng v&agrave; thủ tục nhập khẩu</span></p>\r\n\r\n<p><span style=\"font-size:14px\">+ Tra m&atilde; HS cho h&agrave;ng h&oacute;a. Xem <a href=\"http://tuhocxuatnhapkhau.com/news-detail/bi-quyet-tra-ma-hs-chua-ai-day-ban-phan-1\">hướng dẫn tra m&atilde; HS</a> tại đ&acirc;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>+</em> Dự t&iacute;nh thuế nhập khẩu phải nộp</span></p>\r\n\r\n<p><span style=\"font-size:14px\">+ Xuất xứ h&agrave;ng h&oacute;a, ch&ecirc;nh lệch thuế nếu được hưởng thuế suất nhập khẩu ưu đ&atilde;i đặc biệt?</span></p>\r\n\r\n<p><span style=\"font-size:14px\">+ Ch&iacute;nh s&aacute;ch nhập khẩu</span></p>\r\n\r\n<p><strong>2. Chuẩn bị bộ chứng từ nhập khẩu</strong></p>\r\n\r\n<p><span style=\"font-size:14px\">Theo khoản 2, điều 16 thuộc th&ocirc;ng tư 38/2015/TT-BTC bộ hồ sơ l&agrave;m thủ tục hải quan nhập khẩu cơ bản bao gồm:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">Tờ khai hải quan nhập khẩu: 02 bản ch&iacute;nh</span></li>\r\n	<li><span style=\"font-size:14px\">H&oacute;a đơn thương mại: 01 bản copy (trong trường hợp h&agrave;ng c&oacute; thanh to&aacute;n, h&agrave;ng phi mậu dịch thuộc diện phải l&agrave;m tờ khai, nộp h&oacute;a đơn phi thương mại non-commercial invoice 01 bản copy)</span></li>\r\n	<li><span style=\"font-size:14px\">Vận tải đơn (hoặc c&aacute;c chứng từ vận tải kh&aacute;c c&oacute; gi&aacute; trị tương đương): 01 bản copy</span></li>\r\n	<li><span style=\"font-size:14px\">Giấy giới thiệu hoặc giấy ủy quyền: 03 bản ch&iacute;nh. Mặc d&ugrave; th&ocirc;ng tư kh&ocirc;ng ghi điều n&agrave;y, nhưng trong thực tế c&oacute; y&ecirc;u cầu, bạn cứ chuẩn bị đầy đủ nh&eacute;!</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:4.5pt\"><span style=\"font-size:14px\">Trong một số trường hợp nhất định, t&ugrave;y theo y&ecirc;u cầu thực tế v&agrave; đặc th&ugrave; của từng mặt h&agrave;ng, từng trường hợp kh&aacute;c nhau m&agrave; c&aacute;c loại chứng từ được bổ sung cho ph&ugrave; hợp. Dưới đ&acirc;y l&agrave; danh s&aacute;ch một số chứng từ được liệt k&ecirc; c&oacute; thể c&oacute; để bạn tham khảo:</span></p>\r\n\r\n<p style=\"margin-left:22.5pt\"><span style=\"font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>+ </strong>Vận đơn thứ cấp/House Bill of Lading/House Airway Bill: 1 copy</span></p>\r\n\r\n<p style=\"margin-left:22.5pt\"><span style=\"font-size:14px\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + </strong>VAT invoice: 1 copy (h&oacute;a đơn VAT trong trường hợp XNK tại chỗ)</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ Hợp đồng ngoại thương/Sales contract: 1 copy (if required)</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ H&oacute;a đơn thương mại &amp; Phiếu đ&oacute;ng g&oacute;i/Commercial Invoice &amp; Packing List: 1 copy</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ Giấy ph&eacute;p nhập khẩu/Import license (if any): 1 original</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ Giấy ph&eacute;p nhập khẩu theo hạn ngạch thuế quan: 01 bản ch&iacute;nh nếu nhập khẩu một lần hoặc 01 bản chụp k&egrave;m theo Phiếu theo d&otilde;i trừ l&ugrave;i nếu nhập khẩu nhiều lần</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ Giấy giới thiệu/Ủy quyền (letter of introduction/authorization letter): 3 originals</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ Tờ khai hải quan nhập khẩu (Import customs declaration): 2 originals</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ H&oacute;a đơn cước vận chuyển/Freight Invoice (if any) d&ugrave;ng cho h&agrave;ng nhập khẩu nh&oacute;m E, F: 1 copy</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ Chứng nhận xuất xứ/Certificate of Origin (if any): 1 original</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ Catalogue/Drawing/MSDS&hellip; (if required): 1 copy</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ Ủy quyền chữ k&yacute;/Authorization of signature: 1 copy</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ C&aacute;c loại chứng từ kh&aacute;c/Other documents: c&aacute;c chứng từ do cơ quan chuy&ecirc;n ng&agrave;nh ph&aacute;t h&agrave;nh nhằm mục đ&iacute;ch th&ocirc;ng b&aacute;o kết quả gi&aacute;m định chất lượng, kiểm tra chất lượng h&agrave;ng h&oacute;a nhập khẩu, hay c&aacute;c thủ tục về mặt ph&aacute;p l&yacute; li&ecirc;n quan đến quy tr&igrave;nh nhập khẩu h&agrave;ng h&oacute;a&hellip; <strong>cần phải nộp bản ch&iacute;nh</strong>&nbsp;trong bộ chứng từ hải quan.</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">*** Ch&uacute; &yacute;:</span></p>\r\n\r\n<p style=\"margin-left:14.2pt\"><span style=\"font-size:14px\">+ Nếu c&aacute;c chứng từ tr&ecirc;n đ&atilde; được khai b&aacute;o v&agrave; nhận kết quả tại Cổng th&ocirc;ng tin một cửa quốc gia, bạn kh&ocirc;ng phải nộp lại chứng từ đ&oacute; khi l&agrave;m thủ tục hải quan nữa.</span></p>\r\n\r\n<h2 style=\"margin-left:14.2pt\"><strong>3. Xử l&yacute; th&ocirc;ng tin h&agrave;ng đến v&agrave; sắp xếp kế hoạch</strong></h2>\r\n\r\n<p><span style=\"font-size:14px\"><strong>3.1. Xử l&yacute; th&ocirc;ng tin h&agrave;ng đến</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nhận giấy b&aacute;o h&agrave;ng đến từ h&atilde;ng t&agrave;u, đại l&yacute; hoặc forwarder của c&ocirc;ng ty bạn. Kiểm tra ch&eacute;o th&ocirc;ng tin với chứng từ của bạn, phản hồi nếu c&oacute; sai s&oacute;t.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sắp xếp thời gian l&agrave;m thủ tục hợp l&yacute; để giảm tối đa c&aacute;c khoản ph&iacute;/phụ ph&iacute; ph&aacute;t sinh về lưu kho, lưu phương tiện.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">[<a href=\"http://tuhocxuatnhapkhau.com/kho-tai-lieu/3?cat=7\">Mẫu AN</a>]</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Dựa v&agrave;o th&ocirc;ng tin đ&atilde; chuẩn bị ở mục 1 c&ugrave;ng với bộ chứng từ v&agrave; giấy b&aacute;o h&agrave;ng đến, bạn tiến h&agrave;nh khai b&aacute;o hải quan tr&ecirc;n phần mềm <a href=\"http://tuhocxuatnhapkhau.com/kho-tai-lieu?cat=3\">ECUS5 VNACCS</a>.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>3.2. L&ecirc;n kế hoạch sắp xếp phương tiện vận tải nội địa v&agrave; phương tiện dỡ h&agrave;ng </strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nh&acirc;n vi&ecirc;n chứng từ/XNK dựa v&agrave;o khối lượng, thể t&iacute;ch, k&iacute;ch thước của h&agrave;ng h&oacute;a để t&iacute;nh to&aacute;n sắp xếp phương tiện ph&ugrave; hợp để vận chuyển, nh&acirc;n lực v&agrave; phương tiện dỡ h&agrave;ng khi tới kho.</span></p>\r\n\r\n<h2><strong>4. L&agrave;m thủ tục hải quan</strong></h2>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi khai b&aacute;o hải quan, t&ugrave;y trạng th&aacute;i ph&acirc;n luồng của tờ khai để l&agrave;m thủ tục tương ứng. (Xem th&ecirc;m Quy định về kiểm tra hải quan: hồ sơ v&agrave; h&agrave;ng h&oacute;a, tại khoản 3, điều 10, <a href=\"http://tuhocxuatnhapkhau.com/kho-tai-lieu?cat=2\">th&ocirc;ng tư 38/2015/TT-BTC</a>)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nộp c&aacute;c loại thuế v&agrave; ph&iacute; ph&aacute;t sinh tr&ecirc;n tờ khai nhập.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Sau khi ho&agrave;n th&agrave;nh nghĩa vụ thuế v&agrave; c&aacute;c nghĩa vụ li&ecirc;n quan đến thủ tục nhập khẩu, tờ khai được th&ocirc;ng quan. DN lấy <em>Danh s&aacute;ch h&agrave;ng h&oacute;a đủ điều kiện qua khu vực gi&aacute;m s&aacute;t hải quan &ndash; thường gọi l&agrave; tờ khai gi&aacute;m s&aacute;t (Website: </em><a href=\"https://www.customs.gov.vn/\"><em>https://www.customs.gov.vn/</em></a> <em>&egrave;</em> <a href=\"https://www.customs.gov.vn/SitePages/ContainerBarcodeReceiver.aspx\" target=\"_blank\">In bảng k&ecirc; m&atilde; vạch phương tiện chứa h&agrave;ng</a><em>). Hải quan gi&aacute;m s&aacute;t k&yacute;, đ&oacute;ng dấu l&ecirc;n tờ khai gi&aacute;m s&aacute;t th&igrave; h&agrave;ng h&oacute;a ho&agrave;n to&agrave;n đủ điều kiện để nhập khẩu v&agrave;o l&atilde;nh thổ Việt Nam.</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">[Mẫu <a href=\"http://tuhocxuatnhapkhau.com/kho-tai-lieu/4?cat=7\">Tờ khai gi&aacute;m s&aacute;t</a>]</span></p>\r\n\r\n<p><strong>5. Thủ tục nhận h&agrave;ng</strong></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>5.1. Lấy D/O</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trước khi lấy D/O cần đọc kỹ nội dung v&agrave; y&ecirc;u cầu ở A/N, kiểm tra điều kiện Incoterms l&agrave; g&igrave;, Cước trả trước hay trả sau, Chứng từ cần chuẩn bị, Ph&iacute; D/O v&agrave; c&aacute;c loại ph&iacute;/phụ ph&iacute; kh&aacute;c nếu c&oacute;, Địa điểm lấy D/O&hellip; để chủ động sắp xếp kế hoạch.</span></p>\r\n\r\n<p><strong>5.2. Nhận h&agrave;ng ở kho</strong></p>\r\n\r\n<p><span style=\"font-size:14px\">Để lấy được h&agrave;ng cần xuất tr&igrave;nh cho nh&acirc;n vi&ecirc;n kho của s&acirc;n bay/cảng vụ&nbsp;DO, giấy giới thiệu v&agrave; tờ khai hải quan đ&atilde; th&ocirc;ng quan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nộp ph&iacute; lưu kho, ph&iacute; bến b&atilde;i hoặc c&aacute;c ph&iacute;/phụ ph&iacute; ph&aacute;t sinh (c&oacute; h&oacute;a đơn)</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chụp ảnh t&igrave;nh trạng kiện h&agrave;ng khi nhận từ kho, giao l&ecirc;n xe v&agrave; trước khi dỡ h&agrave;ng xuống tại kho người nhập khẩu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Th&ocirc;ng b&aacute;o thời gian giao h&agrave;ng cụ thể cho nh&acirc;n vi&ecirc;n kho nhập khẩu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi giao h&agrave;ng, y&ecirc;u cầu 2 b&ecirc;n k&yacute; v&agrave;o POD x&aacute;c nhận t&igrave;nh trạng, số lượng h&agrave;ng đ&atilde; nhận, ghi ch&uacute; v&agrave; chụp ảnh c&aacute;c bất thường (nếu c&oacute;).</span></p>\r\n\r\n<p><span style=\"font-size:14px\">[Mẫu POD &ndash; Proof of delivery &ndash; Bi&ecirc;n bản giao nhận h&agrave;ng h&oacute;a]</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ms Sarah.</span></p>','/7c/27/77/5a930788691a9.jpg',NULL,16713,0,'2018-02-26 00:00:00',NULL,'Quy trình Xuất nhập khẩu có rất nhiều bước, quy trình là xương sống, là tiền đề quan trọng để bạn tự tin bước vào nghề xuất nhập khẩu. Đây là hướng dẫn quy trình Xuất nhập khẩu dành cho các bạn mới vào nghề hoặc đang  trên ghế nhà trường để tìm hiểu sơ bộ','thủ tục nhập khẩu, quy trình nhập khẩu chi tiết, các bước làm thủ tục nhập khẩu, giấy báo hàng đến, vận đơn thứ cấp, tờ khai nhập khẩu, lệnh giao hàng','','',2,0,'vi','quy-trinh-thu-tuc-hang-nhap-khau-chi-tiet-cho-nguoi-moi-vao-nghe',9,27,26,'2018-02-26 01:59:20','2018-03-03 21:19:32'),(261,'5 ĐIỀU QUAN TRỌNG CẦN BIẾT VỀ KIỂM TRA SAU THÔNG QUAN','','Thời điểm cuối năm, rất nhiều doanh nghiệp có Quyết định Kiểm tra Sau thông quan và đang rất hoang mang điều này. Vậy thực sự việc Kiểm tra Sau thông quan là như thế nào và có mang cho công ty quá nhiều điều lo sợ như vậy không? Hãy cùng HAN EXIM CLUB tìm hiểu điều này','<p style=\"text-align:justify\"><span style=\"color:#006400\">Thời điểm cuối năm, rất nhiều doanh nghiệp c&oacute; Quyết định Kiểm tra Sau th&ocirc;ng quan v&agrave; đang rất hoang mang điều n&agrave;y. Vậy thực sự việc Kiểm tra Sau th&ocirc;ng quan l&agrave; như thế n&agrave;o v&agrave; c&oacute; mang cho c&ocirc;ng ty qu&aacute; nhiều điều lo sợ như vậy kh&ocirc;ng? H&atilde;y c&ugrave;ng HAN EXIM CLUB t&igrave;m hiểu điều n&agrave;y</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">1. Kiểm tra sau th&ocirc;ng quan l&agrave; g&igrave;?</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Thực chất Kiểm tra sau th&ocirc;ng quan l&agrave; việc cơ quan Hải quan kiểm tra t&iacute;nh trung thực, ch&iacute;nh x&aacute;c, sựhợp l&yacute; của c&aacute;c th&ocirc;ng tin m&agrave; c&aacute;c doanh nghiệp xuất nhập khẩu đ&atilde; khai b&aacute;o với cơ quan Hải quan khi mở tờ khai. Th&ocirc;ng thường, qu&aacute; tr&igrave;nh n&agrave;y diễn ra sau khi h&agrave;ng h&oacute;a đ&atilde; được th&ocirc;ng quan n&ecirc;n gọi l&agrave; &ldquo;Kiểm tra sau th&ocirc;ng quan&rdquo;. N&oacute;i vậy để ph&acirc;n biệt với một số h&igrave;nh thức kiểm tra kh&aacute;c m&agrave; cơ quan Hải quan tiến h&agrave;nh nhưng c&oacute; thể ngay trong Doanh nghiệp đang l&agrave;m thủ tục Hải quan.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">2. Nội dung kiểm tra sau th&ocirc;ng quan</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Người khai hải quan c&oacute; nghĩa vụ cung cấp xuất tr&igrave;nh hợp đồng mua b&aacute;n h&agrave;ng h&oacute;a hoặc c&aacute;c h&igrave;nh thức c&oacute; gi&aacute; trị tương đương hợp đồng, h&oacute;a đơn thương mại, chứng từ vận tải, chứng từ bảo hiểm, chứng nhận xuất xứ h&agrave;ng ho&aacute;, chứng từ thanh to&aacute;n, hồ sơ, t&agrave;i liệu kỹ thuật của h&agrave;ng h&oacute;a xuất khẩu, nhập khẩu li&ecirc;n quan đến hồ sơ đang được kiểm tra v&agrave; c&oacute; tr&aacute;ch nhiệm giải tr&igrave;nh những nội dung li&ecirc;n quan; cử đại diện c&oacute; thẩm quyền đến l&agrave;m việc với cơ quan hải quan theo Quyết định kiểm tra.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Trong qu&aacute; tr&igrave;nh kiểm tra, nội dung kiểm tra được ghi nhận tại c&aacute;c bi&ecirc;n bản kiểm tra, k&egrave;m c&aacute;c hồ sơ, chứng từ người khai hải quan giải tr&igrave;nh, chứng minh.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">3. Đối tượng Kiểm tra Sau th&ocirc;ng quan</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Theo th&ocirc;ng tư 38- Bộ T&agrave;i Ch&iacute;nh th&igrave; đối tượng Kiểm tra Sau th&ocirc;ng quan kh&ocirc;ng chỉ dừng lại ở c&aacute;c C&ocirc;ng ty Xuất Nhập Khẩu (chủ h&agrave;ng) m&agrave; c&ograve;n c&oacute; thể l&agrave; c&aacute;c đối tượng sau:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- C&aacute;c đại l&yacute; khai thu&ecirc;/ m&ocirc;i giới hải quan&nbsp;: nắm giữ c&aacute;c chứng từ thương mại hải quan, c&aacute;c th&ocirc;ng tin kh&aacute;c về h&agrave;ng ho&aacute; v&agrave; trị gi&aacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- C&aacute;c doanh nghiệp nhập khẩu ủy th&aacute;c&nbsp;: nắm giữ c&aacute;c th&ocirc;ng tin giao dịch trước khi hợp đồng thương mại v&agrave; hợp đồng thương mại&hellip;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- C&aacute;c doanh nghiệp kho vận ngoại thương&nbsp;: nắm giữ c&aacute;c chứng vận chuyển h&agrave;ng ho&aacute;, số lượng bản chất chủng loại đơn gi&aacute;, tổng trị gi&aacute; h&agrave;ng ho&aacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- C&aacute;c h&atilde;ng vận tải h&agrave;ng ho&aacute; xuất nhập khẩu&nbsp;: ph&aacute;t h&agrave;nh v&agrave; lưu giữ chứng từ vận chuyển, h&agrave;nh tr&igrave;nh của con t&agrave;u, cước ph&iacute; vận tải (để x&aacute;c định trị gi&aacute; hải quan v&agrave; xuất xứ h&agrave;ng ho&aacute;), bi&ecirc;n bản hải sự, b&aacute;o c&aacute;o tổn hại h&agrave;ng ho&aacute; (để xem x&eacute;t trường hợp tổn thất trị gi&aacute; thương mại của h&agrave;ng ho&aacute; - một yếu tố ảnh hưởng trực tiếp tới giảm thuế thường bị lạm dụng).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- C&aacute;c h&atilde;ng bảo hiểm h&agrave;ng ho&aacute; xuất nhập khẩu&nbsp;: nắm giữ c&aacute;c chứng từ bảo hiểm, ph&iacute; bảo hiểm (một yếu tố cấu th&agrave;nh của trị gi&aacute; h&agrave;ng quan) v&agrave; c&aacute;c chứng từ kh&aacute;c (Trị gi&aacute; cần bảo hiểm &ndash; Insurable Value, Số tiền bảo hiểm &ndash; Amount Insued&hellip;) để x&aacute;c định trị gi&aacute;&nbsp; hải quan, mối quan hệ thương t&aacute;c về thời gian ghi tr&ecirc;n chứng từ bảo hiểm với thời gian ghi tr&ecirc;n c&aacute;c chứng từ kh&aacute;c c&oacute; &yacute; nghĩa rất quan trọng trong nghiệp vụ x&eacute;t đo&aacute;n chứng từ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- C&aacute;c ng&acirc;n h&agrave;ng thương mại&nbsp;: nắm giữ c&aacute;c chứng từ ghi nhận số tiền thực tế đ&atilde; chuyển trả cho người xuất khẩu, tiền b&aacute;n h&agrave;ng xuất khẩu tr&ecirc;n thị trường nội địa để từ đ&oacute; c&oacute; thể ph&acirc;n t&iacute;ch t&igrave;m ra trị gi&aacute; hải quan v&agrave; c&aacute;c th&ocirc;ng tin kh&aacute;c.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- Cơ quan thuế nội địa&nbsp;: nắm giữa c&aacute;c th&ocirc;ng tin về gi&aacute; b&aacute;n h&agrave;ng nhập khẩu tr&ecirc;n thị trường nội địa, thuế gi&aacute; trị gia tăng đầu v&agrave;o - đầu ra, c&aacute;c chi ph&iacute; hợp l&yacute; của doanh nghiệp gi&uacute;p cho việc ph&acirc;n t&iacute;ch x&aacute;c định trị gi&aacute; hải quan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- Người mua h&agrave;ng nhập khẩu tr&ecirc;n thị trường nội địa&nbsp;: gi&aacute; thực tế đ&atilde; mua h&agrave;ng nhập khẩu tr&ecirc;n thị trường nội địa từ đ&oacute; ph&acirc;n t&iacute;ch để t&igrave;m ra gi&aacute; nhập khẩu, chủng loại xuất xứ của h&agrave;ng ho&aacute; thực tế đ&atilde; mua để đối so&aacute;t với những th&ocirc;ng tin n&agrave;y tr&ecirc;n hồ sơ hải quan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- C&aacute;c cơ quan, tổ chức gi&aacute;m định h&agrave;ng h&oacute;a, v&agrave;&hellip;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Theo luật ph&aacute;p của nhiều nước th&igrave; c&aacute;c đối tượng c&oacute; li&ecirc;n quan trực tiếp đến thương mại quốc tế (người nhập khẩu/người ủy th&aacute;c) l&agrave; đối tượng trực tiếp của kiểm tra sau th&ocirc;ng qua, c&aacute;c đối tượng kh&aacute;c c&oacute; tr&aacute;ch nhiệm hợp t&aacute;c, gi&uacute;p đỡ v&agrave; cung cấp c&aacute;c th&ocirc;ng tin cần thiết khi cơ quan Hải quan y&ecirc;u cầu.</span></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">4. C&aacute;c h&igrave;nh thức Kiểm tra Sau Th&ocirc;ng Quan</span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">C&oacute; 2 h&igrave;nh thức Kiểm tra Sau th&ocirc;ng qun l&agrave; tại Trụ sở Cơ quan Hải quan v&agrave; trụ sở Doanh nghiệp. Việc quyết định h&igrave;nh thức Kiểm tra do Cơ quan Hải quan quyết định.</span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Chi cục trưởng Chi cục Hải quan c&oacute; thẩm quyền quyết định kiểm tra đối với c&aacute;c hồ sơ hải quan đ&atilde; th&ocirc;ng quan trong thời hạn 60 ng&agrave;y kể từ ng&agrave;y h&agrave;ng h&oacute;a được th&ocirc;ng quan đến ng&agrave;y k&yacute; ban h&agrave;nh Quyết định kiểm tra, thuộc diện phải kiểm tra</span></li>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Cục trưởng Cục Hải quan c&oacute; thẩm quyền quyết định kiểm tra đối với c&aacute;c hồ sơ hải quan thuộc diện phải kiểm tra theo quy định tại khoản 1, khoản 2 Điều 78 Luật Hải quan</span></li>\r\n</ul>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"color:#006400\">5. Kết quả kiểm tra sau th&ocirc;ng quan</span></h2>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><span style=\"color:#006400\">Trường hợp th&ocirc;ng tin, hồ sơ, chứng từ, t&agrave;i liệu, nội dung giải tr&igrave;nh của người khai hải quan chứng minh nội dung khai hải quan l&agrave; đ&uacute;ng quy định ph&aacute;p luật th&igrave; cơ quan hải quan chấp nhận nội dung khai b&aacute;o của người khai hải quan, th&ocirc;ng b&aacute;o kết quả kiểm tra</span></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:13.5pt; text-align:justify\"><span style=\"color:#006400\">- Trường hợp Hải quan c&oacute; quyền b&aacute;c bỏ Nội dung đ&atilde; khai b&aacute;o trước đ&acirc;y của Doanh nghiệp khi:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">+ Người khai hải quan kh&ocirc;ng cung cấp đủ th&ocirc;ng tin, hồ sơ, chứng từ, t&agrave;i liệu theo quy định hoặc kh&ocirc;ng giải tr&igrave;nh, kh&ocirc;ng chứng minh được nội dung khai hải quan l&agrave; đ&uacute;ng</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">+ Trường hợp người khai hải quan khai kh&ocirc;ng đ&uacute;ng, kh&ocirc;ng đầy đủ, kh&ocirc;ng ch&iacute;nh x&aacute;c: c&aacute;c ti&ecirc;u ch&iacute; tr&ecirc;n tờ khai hải quan; tờ khai trị gi&aacute;; c&aacute;c yếu tố li&ecirc;n quan đến việc x&aacute;c định số thuế phải nộp, ch&iacute;nh s&aacute;ch quản l&yacute; đối với h&agrave;ng h&oacute;a xuất nhập khẩu; c&aacute;c yếu tố c&oacute; li&ecirc;n quan ảnh hưởng đến nội dung, phương ph&aacute;p x&aacute;c định trị gi&aacute; như c&aacute;c khoản điều chỉnh, mối quan hệ đặc biệt, điều kiện, tr&igrave;nh tự c&aacute;c phương ph&aacute;p x&aacute;c định trị gi&aacute;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">+ Trường hợp hồ sơ, chứng từ, t&agrave;i liệu người khai hải quan cung cấp cho cơ quan hải quan kh&ocirc;ng hợp ph&aacute;p</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">+ Trường hợp c&oacute; sự m&acirc;u thu&acirc;̃n hoặc kh&ocirc;ng ph&ugrave; hợp về n&ocirc;̣i dung giữa các chứng từ, tài li&ecirc;̣u trong h&ocirc;̀ sơ hải quan; giữa hồ sơ hải quan với c&aacute;c hồ sơ, chứng từ, t&agrave;i liệu nộp, xuất tr&igrave;nh cho cơ quan hải quan theo quy định</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">- Trường hợp người khai hải quan kh&ocirc;ng đến l&agrave;m việc hoặc kh&ocirc;ng cung cấp hồ sơ, t&agrave;i liệu theo y&ecirc;u cầu của cơ quan hải quan th&igrave; cơ quan hải quan xử l&yacute; tr&ecirc;n cơ sở kết quả kiểm tra hồ sơ, dữ liệu của cơ quan hải quan hiện c&oacute;; đồng thời cập nhật th&ocirc;ng tin v&agrave;o Hệ thống cơ sở dữ liệu của Tổng cục Hải quan để &aacute;p dụng biện ph&aacute;p kiểm tra đối với hồ sơ hải quan, c&aacute;c l&ocirc; h&agrave;ng xuất khẩu, nhập khẩu tiếp theo của người khai hải quan.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#006400\">Bạn muốn t&igrave;m hiểu kỹ hơn h&atilde;y t&igrave;m đọc Quyết định 1410/QĐ-TCHQ năm 2015 về việc ban h&agrave;nh Quy tr&igrave;nh kiểm tra sau th&ocirc;ng quan. </span><span style=\"font-size:14px\"><strong><a href=\"https://drive.google.com/open?id=1PsTZHeG9Tiy4OZB7KGzcjS_X9WE8V_1V\"><span style=\"color:#0000CD\">Tải tại đ&acirc;y</span></a></strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Mrs Jade.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>','/a3/6b/ba/5a9554dc48e3b.jpg',NULL,8186,0,'2018-03-01 00:00:00',NULL,'Thực chất Kiểm tra sau thông quan là việc cơ quan Hải quan kiểm tra tính trung thực, chính xác, sự hợp lý của các thông tin mà các doanh nghiệp xuất nhập khẩu đã khai báo với cơ quan Hải quan khi mở tờ khai. Thông thường, quá trình này diễn ra sau khi hàn','kiểm tra sau thông quan, quy trình kiểm tra sau thông quan','','',2,0,'vi','5-dieu-quan-trong-can-biet-ve-kiem-tra-sau-thong-quan',9,26,27,'2018-02-27 19:53:48','2018-03-06 09:15:04'),(262,'MỘT SỐ CÂU HỎI & TRẢ LỜI GIỮA HẢI QUAN TP HỒ CHÍ MINH VÀ DOANH NGHIỆP LIÊN QUAN ĐẾN TRỊ GIÁ HẢI QUAN','','Lĩnh vực liên quan đến Trị giá Hải quan luôn là vấn đề được quan tâm mỗi khi doanh nghiệp có tờ khai rơi vào diện tham vấn giá hoặc kiểm tra sau thông quan. Trong hội nghị đối thoại giữa Hải quan và doanh nghiệp tại Thành phố Hồ Chí Minh năm 2017, đã có rất nhiều câu hỏi thiết thực được Doanh nghiệp đặt ra và đại diện cơ quan Hải quan TP Hồ Chí Minh đã có những trả lời chi tiết, dựa trên các câu trả lời này chúng ta có cái nhìn sâu hơn về lĩnh vực Kiểm tra, giám sát quản lý của cơ quan Hải quan.','<p style=\"margin-left:34pt; text-align:justify\">Lĩnh vực li&ecirc;n quan đến Trị gi&aacute; Hải quan lu&ocirc;n l&agrave; vấn đề được quan t&acirc;m mỗi khi doanh nghiệp c&oacute; tờ khai rơi v&agrave;o diện tham vấn gi&aacute; hoặc kiểm tra sau th&ocirc;ng quan. Trong hội nghị đối thoại giữa Hải quan v&agrave; doanh nghiệp tại Th&agrave;nh phố Hồ Ch&iacute; Minh năm 2017, đ&atilde; c&oacute; rất nhiều c&acirc;u hỏi thiết thực được Doanh nghiệp đặt ra v&agrave; đại diện cơ quan Hải quan TP Hồ Ch&iacute; Minh đ&atilde; c&oacute; những trả lời chi tiết, dựa tr&ecirc;n c&aacute;c c&acirc;u trả lời n&agrave;y ch&uacute;ng ta c&oacute; c&aacute;i nh&igrave;n s&acirc;u hơn về lĩnh vực Kiểm tra, gi&aacute;m s&aacute;t quản l&yacute; của cơ quan Hải quan.</p>\r\n\r\n<p style=\"margin-left:34pt; text-align:justify\"><span style=\"color:#0000FF\"><em>1. Cơ sở dữ Hải quan được cập nhật theo lịch tr&igrave;nh/ kế hoạch như thế n&agrave;o?</em></span></p>\r\n\r\n<p style=\"margin-left:34pt; text-align:justify\"><span style=\"color:#0000FF\"><em>2.Cơ quan Hải quan c&oacute; lấy gi&aacute; tr&ecirc;n Cơ sở dữ liệu Hải quan để ấn định gi&aacute; trị Hải quan kh&ocirc;ng?</em></span></p>\r\n\r\n<p style=\"margin-left:34pt; text-align:justify\"><span style=\"color:#0000FF\"><em>3 .C&aacute;c chi cục Hải quan thường &aacute;p dụng lấy 80% gi&aacute; tr&ecirc;n internet để x&aacute;c định lại gi&aacute; trị hải quan, &nbsp;liệu điều n&agrave;y c&oacute; đ&uacute;ng quy định của luật ph&aacute;p? V&agrave; nếu đ&uacute;ng , th&igrave; căn cứ v&agrave;o quy định n&agrave;o?</em></span></p>\r\n\r\n<p style=\"margin-left:34pt; text-align:justify\"><span style=\"color:#0000FF\"><em>4. Trường hợp c&ocirc;ng chức kiểm tra gi&aacute; của bộ phận tham vấn gi&aacute; hải quan kh&ocirc;ng c&oacute; gi&aacute; khai b&aacute;o giống hệt hoặc tương tự như gi&aacute; của doanh nghiệp khai b&aacute;o trong 3 th&aacute;ng v&agrave; 6 th&aacute;ng trước đ&oacute; trong dữ liệu của hải quan, nhưng c&ocirc;ng chức vẫn lấy gi&aacute; tr&ecirc;n internet để &aacute;p cho Doanh nghiệp, vậy c&oacute; đ&uacute;ng với c&aacute;c quy định của ph&aacute;p luật hiện h&agrave;nh hay kh&ocirc;ng? Nếu sai th&igrave; doanh nghiệp c&oacute; bị cưỡng chế hoặc bắt buộc đ&oacute;ng thuế tăng l&ecirc;n (nếu c&oacute;) hay kh&ocirc;ng?</em></span></p>\r\n\r\n<p style=\"margin-left:34pt; text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:34.0pt\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <u>Trả lời c&acirc;u hỏi 1 - 2 - 3 v&agrave; 4</u>:</strong></p>\r\n\r\n<p style=\"margin-left:34.0pt\">Căn cứ Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh:</p>\r\n\r\n<p style=\"margin-left:34.0pt\"><strong>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<u>Cơ sở dữ liệu về gi&aacute; t&iacute;nh thuế</u></strong> của hải quan được thu thập từ nhiều nguồn th&ocirc;ng tin kh&aacute;c nhau như:</p>\r\n\r\n<p style=\"margin-left:34.0pt\">&nbsp;&nbsp; <em>- Gi&aacute; khai b&aacute;o của cộng đồng doanh nghiệp l&agrave;m thủ tục hải quan trong phạm vi to&agrave;n quốc, được hải quan chấp nhận t&iacute;nh thuế theo trị gi&aacute; giao dịch.</em></p>\r\n\r\n<p style=\"margin-left:34.0pt\"><em>- G&iacute;a ch&agrave;o b&aacute;n của nh&agrave; sản xuất, nh&agrave; xuất khẩu, c&aacute;c nh&agrave; ph&acirc;n phối tại quốc xuất khẩu, tại c&aacute;c quốc gia ngo&agrave;i nước xuất khẩu hoặc tại Việt Nam.</em></p>\r\n\r\n<p style=\"margin-left:34.0pt\"><em>- Th&ocirc;ng tin do c&aacute;c Ph&ograve;ng Thương mại - L&atilde;nh sự qu&aacute;n của Việt Nam tại c&aacute;c quốc gia hoặc Ph&ograve;ng Thương mại - L&atilde;nh sự qu&aacute;n của c&aacute;c quốc gia tại Việt Nam;</em></p>\r\n\r\n<p style=\"margin-left:34.0pt\"><em>- Th&ocirc;ng tin do Cục Điều tra chống bu&ocirc;n lậu - TCHQ cung cấp;</em></p>\r\n\r\n<p style=\"margin-left:34.0pt\">Cơ sở dữ liệu gi&aacute; của Hải quan được TCHQ ban h&agrave;nh 1 năm / 2 lần. Trong năm 2017, TCHQ ban h&agrave;nh quyết định 96/QĐ-TCHQ ng&agrave;y 31/3/2017 quy định về danh mục quản l&yacute; rủi ro v&agrave; mức gi&aacute; tham chiếu.</p>\r\n\r\n<p style=\"margin-left:34.0pt\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:34.0pt\"><span style=\"color:#0000FF\"><em>5 .Thủ tục x&acirc;y dựng&nbsp;gi&aacute; trị Hải quan đối với h&agrave;ng h&oacute;a <strong>phi mậu dịch</strong> thực hiện như thế n&agrave;o? Lộ &nbsp;tr&igrave;nh về thời gian thực hiện như thế n&agrave;o?</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong><em>Trả lời:</em></strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><em>- Căn cứ Điều 17 Th&ocirc;ng tư 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh quy định h&agrave;ng qu&agrave; biếu (phi mậu dịch) <strong>sẽ được t&iacute;nh theo gi&aacute; khai b&aacute;o nếu hải quan kh&ocirc;ng c&oacute; nghi vấn.</strong></em></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><em>- Trường hợp hải quan nghi vấn g&iacute;a khai b&aacute;o của doanh nghiệp kh&ocirc;ng trung thực hoặc thấp hơn gi&aacute; h&agrave;ng giống hệt, tương tự được hải quan cập nhật v&agrave;o hệ thống của hải quan th&igrave; <strong>hải quan sẽ điều chỉnh gi&aacute; t&iacute;nh thuế c&aacute;c l&ocirc; h&agrave;ng phi mậu dịch (qu&agrave; biếu) theo c&aacute;c mức gi&aacute; t&iacute;nh thuế c&oacute; sẵn trong hệ thống dữ liệu nhưng kh&ocirc;ng phải l&agrave; cơ sở dữ liệu.</strong></em>&nbsp;</p>\r\n\r\n<p style=\"margin-left:.5in\"><span style=\"color:#0000FF\"><em>6.Nh&agrave; nhập khẩu c&oacute; thể mua h&agrave;ng trực tuyến với gi&aacute; khuyến mại v&agrave; gi&aacute; khyến mại c&oacute; thể l&agrave; gi&aacute; khai Hải quan được kh&ocirc;ng? Theo Th&ocirc;ng tư 39/2015/TT-BTC, gi&aacute; trị giao dịch l&agrave; gi&aacute; trị thanh to&aacute;n thực, do đ&oacute;, gi&aacute; trị khuyến mại l&agrave; gi&aacute; trị thanh to&aacute;n thực c&oacute; thể được coi l&agrave; gi&aacute; trị khai b&aacute;o kh&ocirc;ng?</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong><em>Trả lời:</em></strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><em>Mua h&agrave;ng trực tuyến, mua h&agrave;ng c&oacute; khuyến mại &hellip;. l&agrave; th&ocirc;ng lệ quốc tế v&agrave; l&agrave; hoạt động b&igrave;nh thường trong thương mại quốc tế. Hải quan lu&ocirc;n t&ocirc;n trọng việc khai b&aacute;o của doanh nghiệp. Tuy nhi&ecirc;n gi&aacute; khai b&aacute;o c&oacute; được hải quan chấp nhận hay kh&ocirc;ng chấp nhận t&ugrave;y thuộc v&agrave;o l&ocirc; h&agrave;ng đ&oacute; c&oacute; đủ điều kiện v&agrave; thỏa m&atilde;n c&aacute;c quy định hay kh&ocirc;ng.</em></p>\r\n\r\n<p style=\"margin-left:.5in\"><span style=\"color:#0000FF\"><em>7 .Đối với luồng v&agrave;ng của h&agrave;ng ho&aacute; xuất khẩu nhập khẩu qua dịch vụ CPN, C&aacute;n bộ Hải quan c&oacute; phải th&ocirc;ng b&aacute;o bằng văn bản về c&aacute;c phương ph&aacute;p/thời gian cụ thể khi x&acirc;y dựng lại trị Hải quan kh&ocirc;ng?</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong>Trả lời:</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">Tất cả c&aacute;c hoạt động nghiệp vụ của cơ quan hải quan c&oacute; li&ecirc;n quan v&agrave; ảnh hưởng đến doanh nghiệp hoặc kh&aacute;ch h&agrave;ng về ch&iacute;nh s&aacute;ch, về thuế XNK, <strong>cơ quan hải quan đều c&oacute; Th&ocirc;ng b&aacute;o cho kh&aacute;ch h&agrave;ng bằng văn bản, </strong>cụ thể l&agrave; <strong>Th&ocirc;ng b&aacute;o x&aacute;c định trị gi&aacute; hải quan.</strong> Đề nghị Doanh nghiệp nghi&ecirc;n cứu Th&ocirc;ng tư số 38/2015/TT-BTC v&agrave; Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh v&agrave; c&oacute; thể đề nghị Hải quan th&ocirc;ng b&aacute;o bằng văn bản về mức gi&aacute; x&aacute;c định v&agrave; mức thuế phải nộp.</p>\r\n\r\n<p style=\"margin-left:.5in\"><span style=\"color:#0000FF\"><em>8. Đối với h&agrave;ng ho&aacute; phi mậu dịch, người khai hải quan c&oacute; quyền khiếu nại sau khi đ&atilde; ho&agrave;n th&agrave;nh nghĩa vụ thuế nhưng kh&ocirc;ng h&agrave;i l&ograve;ng với việc x&aacute;c định lại gi&aacute; trị Hải quan kh&ocirc;ng?</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong>Trả lời:</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- Căn cứ Luật khiếu nại số 02/2011/QH13 ng&agrave;y 11 th&aacute;ng 11 năm 2011;</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Căn cứ Nghị định số 75/2012/NĐ-CP ng&agrave;y 03 th&aacute;ng 10 năm 2012 của Ch&iacute;nh phủ qui định chi tiết một số điều của Luật Khiếu nại;</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Căn cứ Th&ocirc;ng tư số 07/2013/TT-TTCP ng&agrave;y 31 th&aacute;ng 10 năm 2013 của Thanh tra Ch&iacute;nh phủ qui định qui tr&igrave;nh giải quyết khiếu nại h&agrave;nh ch&iacute;nh v&agrave; Th&ocirc;ng tư số 02/2016/TT-TTCP ng&agrave;y 20 th&aacute;ng 10 năm 2016 của Thanh tra Ch&iacute;nh phủ sửa đổi, bổ sung một số điều của Th&ocirc;ng tư số 07/2013/TT-TTCP ng&agrave;y 31 th&aacute;ng 10 năm 2013 của Thanh tra Ch&iacute;nh phủ quy định quy tr&igrave;nh giải quyết khiếu nại h&agrave;nh ch&iacute;nh;</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em>9. Đối với l&ocirc; h&agrave;ng chuyển ph&aacute;t nhanh phi mậu dịch, liệu c&oacute; thể th&ocirc;ng quan trước v&agrave; định gi&aacute; ch&iacute;nh x&aacute;c gi&aacute; trị hải quan sau khi th&ocirc;ng quan được kh&ocirc;ng? Nếu c&oacute;, ch&uacute;ng t&ocirc;i c&oacute; thể c&oacute; được chấp thuận bằng văn bản cho việc n&agrave;y được kh&ocirc;ng. Nếu được, ch&uacute;ng t&ocirc;i đề xuất &nbsp;l&agrave; việc chấp thuận n&agrave;y n&ecirc;n được bổ sung v&agrave;o Th&ocirc;ng tư 191 để &aacute;p dụng cho dịch vụ Chuyển ph&aacute;t nhanh.</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;<strong>Trả lời:</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">Căn cứ quy định tại Th&ocirc;ng tư số 38/2015/TT-BTC v&agrave; Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 v&agrave; Th&ocirc;ng tư số 191 của Bộ T&agrave;i Ch&iacute;nh th&igrave;:&nbsp;H&agrave;ng phi mậu dịch (qu&agrave; biếu): phải nộp thuế ngay trước khi th&ocirc;ng quan h&agrave;ng h&oacute;a</p>\r\n\r\n<p style=\"margin-left:.5in\"><em><span style=\"color:#0000CD\">&nbsp;10. Cơ sở dữ liệu của Hải quan l&agrave; th&ocirc;ng tin nội bộ m&agrave; doanh nghiệp kh&ocirc;ng được biết, việc n&agrave;y dẫn tới việc th&ocirc;ng tin x&acirc;y dựng gi&aacute; kh&ocirc;ng minh bạch, r&otilde; r&agrave;ng đối với doanh nghiệp, cơ quan Hải quan c&oacute; thể xem x&eacute;t về việc minh bạch h&oacute;a cơ sở dữ liệu n&agrave;y ? v&iacute; dụ Hải quan c&oacute; thể chia sẻ th&ocirc;ng tin với doanh nghiệp về cơ sở dữ liệu n&agrave;y?</span></em></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trả lời:</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Căn cứ Th&ocirc;ng tư số 12/2002/TT-BCA ng&agrave;y 13/9/2002 của Bộ C&ocirc;ng An v&agrave; Th&ocirc;ng tư số 161/2014/TT-BTC ng&agrave;y 13/9/2014 của Bộ T&agrave;i Ch&iacute;nh quy định c&ocirc;ng t&aacute;c bảo vệ b&iacute; mật Nh&agrave; nước của ng&agrave;nh t&agrave;i ch&iacute;nh, Cục Hải quan TP.HCM kh&ocirc;ng được ph&eacute;p cung cấp th&ocirc;ng tin dưới dạng t&agrave;i liệu &ldquo;mật&rdquo;.</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Việc x&aacute;c định trị gi&aacute; t&iacute;nh thuế Cục Hải quan TP.HCM lu&ocirc;n tu&acirc;n thủ c&aacute;c quy định của ph&aacute;p luật v&agrave; Hiệp định trị gi&aacute; GATT: <strong>th&ocirc;ng qua tham vấn v&agrave; kiểm tra sau th&ocirc;ng quan</strong> giữa cơ quan hải quan v&agrave; doanh nghiệp: <strong>hải quan &nbsp;x&aacute;c định trị gi&aacute; t&iacute;nh thuế theo trị gi&aacute; thực tế đ&atilde; hoặc sẽ thanh to&aacute;n trong điều kiện giao dịch b&igrave;nh thường, t&iacute;nh cạnh tranh kh&ocirc;ng bị hạn chế.</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em>11.&nbsp; Khi x&acirc;y dựng gi&aacute;, phương ph&aacute;p trị gi&aacute; giao dịch vẫn được &aacute;p dụng nếu người mua v&agrave; người b&aacute;n c&oacute; mối quan hệ đặc biệt v&agrave; mối quan hệ n&agrave;y phải được chứng minh l&agrave; kh&ocirc;ng ảnh hưởng đến trị gi&aacute; Hải quan. Vậy người khai Hải quan phải cung cấp c&aacute;c th&ocirc;ng tin, giấy tờ g&igrave; để chứng minh mối quan hệ đặc biệt kh&ocirc;ng ảnh hưởng đến trị gi&aacute; Hải quan?</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trả lời:</em></strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Căn cứ Điều 6 điểm 3 mục d &nbsp;Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh: nếu cơ quan hải quan nghi vấn gi&aacute; khai b&aacute;o thấp, c&oacute; thể kh&ocirc;ng đủ điều kiện để t&iacute;nh thuế theo trị gi&aacute; giao dịch do người mua v&agrave; người b&aacute;n c&oacute; mối quan hệ đặc biệt; doanh nghiệp lựa chọn tham vấn hoặc kiểm tra sau th&ocirc;ng quan tại trụ sở hải quan để giải tr&igrave;nh với cơ quan hải quan về mối quan hệ đặc biệt.</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Việc chứng minh mối quan hệ đặc biệt: cơ quan hải quan v&agrave; doanh nghiệp phải tu&acirc;n thủ theo quy định tại Điều 6 điểm 3 mục a, b v&agrave; c Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh.</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em>12.&nbsp; C&aacute;c ti&ecirc;u ch&iacute; chứng minh trị gi&aacute; Hải quan kh&ocirc;ng thực hiện được theo phương ph&aacute;p 1 ( Trị gi&aacute; giao dịch ) l&agrave; g&igrave;?&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trả lời:</em></strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Căn cứ Điều 6 Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh,</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Khi c&oacute; nghi vấn: th&ocirc;ng qua tham vấn (kiểm tra sau th&ocirc;ng quan) Doanh nghiệp <strong>phải chứng minh l&agrave; gi&aacute; mua b&aacute;n thực tế đ&atilde; thanh to&aacute;n hay sẽ phải thanh to&aacute;n (kể cả c&aacute;c khoản thanh to&aacute;n gi&aacute;n tiếp cho người b&aacute;n) v&agrave; c&aacute;c chi ph&iacute; li&ecirc;n quan đến h&agrave;ng h&oacute;a nhập khẩu. Thỏa m&atilde;n đủ 4 điều kiện:</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Điều kiện 1:</strong> quyền định của nh&agrave; nhập khẩu;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Điều kiện 2:</strong> Gi&aacute; khai b&aacute;o kh&ocirc;ng phụ thuộc v&agrave;o những điều kiện từ 2 b&ecirc;n mua v&agrave; b&aacute;n l&agrave;m ch&uacute;ng kh&ocirc;ng phản &aacute;nh đ&uacute;ng bản chất của h&agrave;ng h&oacute;a được mang đi b&aacute;n.</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Điều kiện 3:</strong> người mua h&agrave;ng kh&ocirc;ng phải trả th&ecirc;m bất kỳ khoản tiền n&agrave;o từ số tiền thu được do việc định đoạt h&agrave;ng ho&aacute; nhập khẩu mang lại cho người b&aacute;n hoặc đối tượng thứ 3 theo chỉ định của người b&aacute;n m&agrave; kh&ocirc;ng khai b&aacute;o hải quan;</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Điều kiện 4:</strong> Người mua v&agrave; người b&aacute;n kh&ocirc;ng c&oacute; mối quan hệ đặc biệt hoặc nếu c&oacute; th&igrave; mối quan hệ đặc biệt đ&oacute; kh&ocirc;ng ảnh hưởng đến trị gi&aacute; giao dịch.</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;<strong>Quy định tr&ecirc;n đ&acirc;y kh&ocirc;ng &aacute;p dụng đối với h&agrave;ng h&oacute;a l&agrave; qu&agrave; biếu, qu&agrave; tăng (phi mậu dịch).</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em>13.&nbsp; Hiện nay c&oacute; trường hợp 1 kh&aacute;ch h&agrave;ng nhập 1 mặt h&agrave;ng về Việt nam, Hải quan c&oacute; y&ecirc;u cầu x&acirc;y dựng gi&aacute; A. Sau đ&oacute; kh&aacute;ch h&agrave;ng cũng nhập mặt h&agrave;ng tương tự về Việt Nam, Hải quan kh&aacute;c c&oacute; y&ecirc;u cầu x&acirc;y dựng gi&aacute; B. Hải quan c&oacute; hệ thống n&agrave;o để theo d&otilde;i t&igrave;nh h&igrave;nh nhập khẩu của 1 doanh nghiệp để đảm bảo việc x&acirc;y dựng gi&aacute; l&agrave; đồng nhất cho mỗi lần nhập chứ kh&ocirc;ng phải lệ thuộc v&agrave;o từng c&aacute;n bộ Hải quan. Ngo&agrave;i ra, nếu doanh nghiệp nhập thường xuy&ecirc;n 1 mặt h&agrave;ng th&igrave; gi&aacute; hải quan x&acirc;y dựng c&oacute; gi&aacute; trị trong v&ograve;ng bao l&acirc;u (6 th&aacute;ng, 1 năm)?</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>&nbsp;Trả lời:</em></strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; T&igrave;nh huống Doanh nghiệp n&ecirc;u kh&ocirc;ng phải l&agrave; c&aacute; biệt v&agrave; cũng kh&ocirc;ng sai.</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Một mặt h&agrave;ng được x&aacute;c định thỏa m&atilde;n kh&aacute;i niệm h&agrave;ng giống hệt, tương tự (Điều 8 v&agrave; Điều 9 Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh) phải thỏa m&atilde;n 4 điều kiện tại Điều 6 Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh v&agrave; <strong>cơ quan hải quan sử dụng trong thời gian tối đa l&agrave; 90 ng&agrave;y kể từ ng&agrave;y h&agrave;ng h&oacute;a được xuất khẩu (kh&ocirc;ng phải l&agrave; 6 th&aacute;ng hay 1 năm như doanh nghiệp đặt vấn đề).</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&ugrave;ng một mặt h&agrave;ng giống hệt nhập khẩu trong c&ugrave;ng thời điềm <strong>(kh&ocirc;ng qu&aacute; 90 ng&agrave;y) </strong>chỉ c&oacute; một mức gi&aacute; t&iacute;nh thuế, trong nhiều trường hợp sẽ c&oacute; những mức gi&aacute; kh&aacute;c nhau kh&ocirc;ng đ&aacute;ng kể v&agrave; trong trường hợp n&agrave;y cơ quan hải quan sẽ chọn gi&aacute; thấp hơn để t&iacute;nh thuế nếu c&oacute; từ 2 mức gi&aacute; của h&agrave;ng giống hệt, tương tự (nghi&ecirc;n cứu Điều 8 v&agrave; Điều 9 Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh).</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em>14.&nbsp; Nhiều c&ocirc;ng ty cung cấp cho kh&aacute;ch h&agrave;ng của m&igrave;nh mức gi&aacute; thấp hơn đối với c&aacute;c đơn đặt h&agrave;ng số lượng lớn. R&otilde; r&agrave;ng c&oacute; thể thấy l&agrave; đơn đặt h&agrave;ng trị gi&aacute; 1 triệu đ&ocirc; la Mỹ sẽ kh&ocirc;ng bằng đơn đặt h&agrave;ng 10.000 đ&ocirc; la Mỹ. Tuy nhi&ecirc;n, c&aacute;c c&ocirc;ng ty thường xuy&ecirc;n gặp rắc rối với định gi&aacute; về những vấn đề n&agrave;y.</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trả lời:</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trường hợp Doanh nghiệp n&ecirc;u l&agrave; th&ocirc;ng lệ quốc v&agrave; l&agrave; điều b&igrave;nh thường trong thương mại quốc tế.</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Căn cứ Điều 15 Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh nếu khoản giảm gi&aacute; m&agrave; người b&aacute;n d&agrave;nh cho người mua thuộc c&aacute;c khoản giảm gi&aacute; về:</p>\r\n\r\n<ul>\r\n	<li><strong>cấp độ số lượng, </strong></li>\r\n	<li><strong>cấp độ thương mại &nbsp;</strong></li>\r\n	<li><strong>cấp độ thanh to&aacute;n </strong></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; v&agrave; thỏa m&atilde;n <strong>c&aacute;c điều kiện quy định tại điều n&agrave;y</strong> th&igrave; cơ quan hải quan sẽ t&iacute;nh thuế theo trị gi&aacute; thực tế đ&atilde; thanh to&aacute;n. <strong>Nếu khoản giảm gi&aacute; từ 5% trở l&ecirc;n sẽ do Tổng cục Hải quan xem x&eacute;t.</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em>&nbsp;15.&nbsp; C&aacute;c quy định hiện tại của WTO cho ph&eacute;p người nước ngo&agrave;i thực hiện c&aacute;c hoạt động ph&acirc;n phối để b&aacute;n trực tiếp cho kh&aacute;ch h&agrave;ng chứ kh&ocirc;ng phải th&ocirc;ng qua c&aacute;c nh&agrave; ph&acirc;n phối, gi&aacute; của họ đương nhi&ecirc;n sẽ thấp hơn v&igrave; họ b&aacute;n h&agrave;ng cho họ từ văn ph&ograve;ng tại nước sở tại hoặc mua trực tiếp từ một b&ecirc;n thứ ba ở nước ngo&agrave;i. Trong cả hai trường hợp, gi&aacute; sẽ thấp hơn khi doanh nghiệp ở nước ngo&agrave;i b&aacute;n cho b&ecirc;n thứ ba kh&ocirc;ng li&ecirc;n quan. Tuy nhi&ecirc;n, c&oacute; một số lo ngại rằng hải quan kh&ocirc;ng chấp nhận mức gi&aacute; thấp như vậy. Vui l&ograve;ng tư vấn cho c&aacute;c c&ocirc;ng ty c&aacute;ch xử l&yacute; t&igrave;nh huống như vậy.</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trả lời:</em></strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Việt Nam đang &aacute;p dụng đầy đủ Hiệp định thực hiện Điều VII Hiệp định chung về thuế quan v&agrave; thương mại (Hiệp định trị gi&aacute; GATT).</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>G&iacute;a b&aacute;n trực tiếp từ nh&agrave; sản xuất</strong><strong> (đại l&yacute; ch&iacute;nh h&atilde;ng)</strong><strong> đương nhi&ecirc;n sẽ thấp hơn gi&aacute; b&aacute;n cho c&aacute;c nh&agrave; ph&acirc;n phối, đại l&yacute;</strong><strong> cấp 2, cấp 3 tại Việt Nam: v&igrave; sẽ tăng chi ph&iacute; đầu v&agrave;o</strong><strong>.</strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Căn cứ Điều 6 Th&ocirc;ng tư số 39/2015/TT-BTC ng&agrave;y 25/3/2015 của Bộ T&agrave;i Ch&iacute;nh, Hải quan sẽ chấp nhận gi&aacute; khai b&aacute;o nếu gi&aacute; khai b&aacute;o của doanh nghiệp thỏa m&atilde;n đủ 4 điều kiện dưới đ&acirc;y:</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong>Điều kiện 1:</strong> quyền định của nh&agrave; nhập khẩu;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong>Điều kiện 2:</strong> Gi&aacute; khai b&aacute;o kh&ocirc;ng phụ thuộc v&agrave;o những điều kiện từ 2 b&ecirc;n mua v&agrave; b&aacute;n l&agrave;m ch&uacute;ng kh&ocirc;ng phản &aacute;nh đ&uacute;ng bản chất của h&agrave;ng h&oacute;a được mang đi b&aacute;n.</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong>Điều kiện 3:</strong> người mua h&agrave;ng kh&ocirc;ng phải trả th&ecirc;m bất kỳ khoản tiền n&agrave;o từ số tiền thu được do việc định đoạt h&agrave;ng ho&aacute; nhập khẩu mang lại cho người b&aacute;n hoặc đối tượng thứ 3 theo chỉ định của người b&aacute;n m&agrave; kh&ocirc;ng khai b&aacute;o hải quan;</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong>Điều kiện 4:</strong> Người mua v&agrave; người b&aacute;n kh&ocirc;ng c&oacute; mối quan hệ đặc biệt hoặc nếu c&oacute; th&igrave; mối quan hệ đặc biệt đ&oacute; kh&ocirc;ng ảnh hưởng đến trị gi&aacute; giao dịch.</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em>16. Giữa cơ sở dữ liệu gi&aacute; của cơ quan hải quan v&agrave; trị gi&aacute; giao dịch (thoả m&atilde;n Điều 6 Th&ocirc;ng tư số 39/2015/TT-BTC) th&igrave; cơ quan x&aacute;c định trị gi&aacute; t&iacute;nh thuế như thế n&agrave;o?</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em><u>V&iacute; dụ</u></em><em>: C&ocirc;ng ty nhập khẩu mặt h&agrave;ng A c&oacute; trị gi&aacute; giao dịch (trị gi&aacute; invoice) l&agrave; 10USD. Nhưng tr&ecirc;n cơ sở dữ liệu gi&aacute; cơ quan hải quan cho mặt h&agrave;ng A n&agrave;y l&agrave; 15USD. Vậy trị gi&aacute; t&iacute;nh thuế mặt h&agrave;ng A l&agrave; 10USD hay 15USD?</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trả lời:</em></strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">1. Cơ sở dữ liệu gi&aacute; l&agrave; c&aacute;c mức gi&aacute; chỉ d&ugrave;ng tham khảo;</p>\r\n\r\n<p style=\"margin-left:42.55pt\">2. Trị gi&aacute; t&iacute;nh thuế h&agrave;ng giống hệt, tương tự thỏa m&atilde;n đủ c&aacute;c điều kiện tại Điều 6 Th&ocirc;ng tư số 39/2015/TT-BTC, được hải quan chấp nhận trị gi&aacute; giao dịch, tồn tại&nbsp; trong hệ thống cơ sở dữ liệu của hải quan tối đa kh&ocirc;ng qu&aacute; 90 ng&agrave;y;</p>\r\n\r\n<p style=\"margin-left:42.55pt\">Nếu h&agrave;ng h&oacute;a nhập khẩu kh&ocirc;ng đủ điều kiện để t&iacute;nh thuế theo trị gi&aacute; giao dịch th&igrave; hải quan sẽ căn cứ mức gi&aacute; n&ecirc;u tại điểm 2 để điều chỉnh gi&aacute; t&iacute;nh thuế. Sản phẩm 10USD hay 15USD kh&ocirc;ng phải l&agrave; yếu tố để hải quan hoặc doanh nghiệp quan t&acirc;m.</p>\r\n\r\n<p style=\"margin-left:42.55pt\">Quan trọng nhất l&agrave; sản phẩm 10USD hoặc 15USD c&oacute; đủ c&aacute;c điều kiện quy định tại Điều 6 Th&ocirc;ng tư số 39/2015/TT-BTC hay kh&ocirc;ng?</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\">&nbsp;17. Danh mục rủi ro h&agrave;ng nhập khẩu chỉ để tham khảo hay &aacute;p dụng để &aacute;p gi&aacute; t&iacute;nh thuế? Trong qu&aacute; tr&igrave;nh tham vấn, DN đ&atilde; chứng minh gi&aacute; thực mua từ nh&agrave; cung cấp nhưng c&aacute;n bộ hải quan vẫn kh&ocirc;ng chấp nhận, l&yacute; do l&agrave; ch&ecirc;nh lệch nhiều so với gi&aacute; tr&ecirc;n danh mục? Tại sao? DN mua đ&atilde; v&agrave;i năm trở lại đ&acirc;y vẫn một mức gi&aacute; cố định, nếu c&oacute; thay đổi từ 3-5% theo biến động thị trường.</span></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&Yacute; 1:Thay v&igrave; HQ đặt ra&nbsp;&ldquo;nghi vấn&rdquo; cho gi&aacute; trị của h&agrave;ng h&oacute;a th&igrave; n&ecirc;n đưa ra 1 cơ sở r&otilde; r&agrave;ng để dẫn đến &ldquo;sự nghi vấn&rdquo; đ&oacute;?</p>\r\n\r\n<p style=\"margin-left:42.55pt\">&Yacute; 2: Một trong c&aacute;c cơ sở định gi&aacute;, đ&oacute; l&agrave; xem x&eacute;t tờ khai sau th&ocirc;ng quan của DN kh&aacute;c. Vậy HQ c&oacute; cung cấp được kh&ocirc;ng?</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trả lời:</em></strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp; - Cơ sở dữ liệu gi&aacute; t&iacute;nh thuế của Hải quan chỉ d&ugrave;ng để x&aacute;c định căn cứ nghi vấn ban đầu khi doanh nghiệp nập khẩu. Tổng cục Hải quan kh&ocirc;ng cho ph&eacute;p sử dụng để điều chỉnh gi&aacute; t&iacute;nh thuế.</p>\r\n\r\n<p style=\"margin-left:42.55pt\">- Doanh nghiệp được quyền y&ecirc;u cầu hải quan cung cấp mức gi&aacute; t&iacute;nh thuế theo c&aacute;c phương ph&aacute;p kh&aacute;c.</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em>24. Theo quy định tại TT 39 th&igrave; ph&iacute; ph&aacute;t sinh tại cửa khẩu VN trước khi th&ocirc;ng quan li&ecirc;n quan đến ph&iacute; local Doanh nghiệp thanh to&aacute;n cho đại l&yacute; h&atilde;ng t&agrave;u.</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\"><span style=\"color:#0000FF\"><em>&nbsp; Ph&iacute; THC =&gt; th&igrave; kh&ocirc;ng phải l&agrave; khoản ph&iacute; phải cộng v&agrave;o trị gi&aacute; t&iacute;nh thuế, nhưng ph&iacute; C.I.C (Ph&iacute; mất c&acirc;n bằng container) doanh nghiệp thanh to&aacute;n cho đại l&yacute; h&atilde;ng t&agrave;u th&igrave; phải cộng v&agrave;o trị gi&aacute; t&iacute;nh thuế. Vui l&ograve;ng giải th&iacute;ch về khoản phải cộng n&agrave;y&nbsp;? Hai khoản ph&iacute; n&agrave;y ph&aacute;t sinh c&ugrave;ng thời điểm v&agrave; doanh nghiệp thanh to&aacute;n c&ugrave;ng nhau. V&acirc;y doanh nghiệp c&oacute; tiếp tục cộng khoản ph&iacute; C.I.C n&agrave;y v&agrave;o trị gi&aacute; t&iacute;nh thuế. ( khoản ph&iacute; n&agrave;y c&oacute; h&atilde;ng t&agrave;u thu, c&oacute; h&atilde;ng t&agrave;u kh&ocirc;ng thu).</em></span></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong><em>&nbsp;Trả lời:</em></strong></p>\r\n\r\n<p style=\"margin-left:42.55pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&aacute;c chi ph&iacute; tr&ecirc;n chỉ cộng v&agrave;o trị gi&aacute; giao dịch khi người mua h&agrave;ng (người nhập khẩu) trực tiếp thanh to&aacute;n. Nếu người nhập khẩu mua h&agrave;ng với điều kiện CIF v&agrave; kh&ocirc;ng phải trả bất kỳ chi ph&iacute; n&agrave;o kh&aacute;c như&nbsp; ph&iacute; C.I.C hoặc T.H.C th&igrave; hải quan kh&ocirc;ng được cộng th&ecirc;m v&agrave;o gi&aacute; giao dịch.</p>\r\n\r\n<p style=\"margin-left:42.55pt\"><u><strong>K&egrave;m&nbsp;Th&ocirc;ng b&aacute;o số:&nbsp;&nbsp; 340/TB-HQHCM ng&agrave;y 07/9/2017.</strong></u></p>','/0c/1a/a0/5a9797d831eef.jpg',NULL,1140,0,'2018-03-01 00:00:00',NULL,'Lĩnh vực liên quan đến Trị giá Hải quan luôn là vấn đề được quan tâm mỗi khi doanh nghiệp có tờ khai rơi vào diện tham vấn giá hoặc kiểm tra sau thông quan. Trong hội nghị đối thoại giữa Hải quan và doanh nghiệp tại Thành phố Hồ Chí Minh năm 2017, đã có r','tham vấn giá, trị giá hải quan, kiểm tra sau thông quan','','',2,0,'vi','mot-so-cau-hoi-tra-loi-giua-hai-quan-tp-ho-chi-minh-va-doanh-nghiep-lien-quan-den-tri-gia-hai-quan',9,26,26,'2018-02-27 20:22:30','2018-03-03 21:22:05'),(263,'THỦ TỤC NHẬP KHẨU NHÔM THANH & HỢP KIM NHÔM ĐỊNH HÌNH','','Nhôm là một trong những nguyên liệu sản xuất ra rất nhiều sản phẩm trong cuộc sống trong đó các thành phẩm nhôm như nhôm than và hợp kim nhôm lại là một trong những mặt hàng nhập khẩu rất nhiều nhất là từ Trung Quốc. Việc nhập khẩu nhôm thanh và hợp kim nhôm định hình càng ngày càng bị kiểm tra, kiểm soát trước và sau thông quan nhiều hơn. Hãy cùng tìm hiểu về thủ tục nhập khẩu mặt hàng này nhé:','<p>Nh&ocirc;m l&agrave; một trong những nguy&ecirc;n liệu sản xuất ra rất nhiều sản phẩm trong cuộc sống trong đ&oacute; c&aacute;c th&agrave;nh phẩm nh&ocirc;m như nh&ocirc;m than v&agrave; hợp kim nh&ocirc;m lại l&agrave; một trong những mặt h&agrave;ng nhập khẩu rất nhiều nhất l&agrave; từ Trung Quốc. Việc nhập khẩu nh&ocirc;m thanh v&agrave; hợp kim nh&ocirc;m định h&igrave;nh c&agrave;ng ng&agrave;y c&agrave;ng bị kiểm tra, kiểm so&aacute;t trước v&agrave; sau th&ocirc;ng quan nhiều hơn. H&atilde;y c&ugrave;ng t&igrave;m hiểu về thủ tục nhập khẩu mặt h&agrave;ng n&agrave;y nh&eacute;:</p>\r\n\r\n<p>Về thủ tục nhập khẩu nh&ocirc;m thanh v&agrave; hợp kim nh&ocirc;m định h&igrave;nh, dựa tr&ecirc;n Quy chuẩn quốc gia<strong>&nbsp;QCVN 16:2014-BXD</strong>&nbsp;ban h&agrave;nh k&egrave;m theo th&ocirc;ng tư 15/2014/TT-BXD của Bộ X&acirc;y Dựng. C&aacute;c sản phẩm nh&ocirc;m v&agrave; hợp kim nh&ocirc;m định h&igrave;nh l&agrave; h&agrave;ng h&oacute;a thu&ocirc;c danh mục &ldquo;<strong>vật liệu x&acirc;y dựng&rdquo;&nbsp;</strong>bắt buộc phải&nbsp;<strong>chứng nhận hợp quy</strong>&nbsp;đảm bảo ph&ugrave; hợp với Quy chuẩn ban h&agrave;nh.</p>\r\n\r\n<p><strong>Bước 1: L&agrave;m đăng k&yacute; chứng nhận Hợp quy</strong></p>\r\n\r\n<p><strong>Hồ sơ đăng k&iacute; chứng nhận Hợp Quy đối với h&agrave;ng nh&ocirc;m thanh v&agrave; hợp kim nh&ocirc;m định h&igrave;nh:</strong></p>\r\n\r\n<p>+ Giấy đăng k&iacute; chứng nhận Hợp quy (2 bản gốc)</p>\r\n\r\n<p>+ C&ocirc;ng văn đem h&agrave;ng về bảo quản (2 bản gốc)</p>\r\n\r\n<p>+ C&ocirc;ng văn cam kết với đơn vị chứng nhận Hợp quy đem h&agrave;ng về kho bảo quản (2 bản gốc)</p>\r\n\r\n<p>+ Tờ khai sao y (1 bản)</p>\r\n\r\n<p>+ Bộ hồ sơ nhập khẩu (sao y, mỗi thứ 1 bản)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bước 2: Lấy kết quả nộp cho Hải quan</strong></p>\r\n\r\n<p>Sau khi ho&agrave;n tất thủ tục đăng k&iacute;, Doanh nghiệp đem&nbsp;<strong>giấy đăng k&iacute; đ&atilde; được x&aacute;c nhận</strong>&nbsp;của đơn vị chứng nhận v&agrave;&nbsp;<strong>c&ocirc;ng văn đem h&agrave;ng về kho</strong>&nbsp;của doanh nghiệp gửi cho Hải quan để giải ph&oacute;ng h&agrave;ng. Khoảng&nbsp;<strong>07-10 ng&agrave;y</strong>&nbsp;sau, đơn vị chứng nhận sẽ c&oacute;&nbsp;<strong>chứng thư tạm</strong>&nbsp;để bổ sung cho Hải quan th&ocirc;ng quan cho l&ocirc; h&agrave;ng.&nbsp;<strong>Chứng thư gốc</strong>&nbsp;sẽ được cấp trong v&ograve;ng&nbsp;<strong>30 ng&agrave;y</strong>&nbsp;kể từ ng&agrave;y đăng k&iacute;.<br />\r\nVề m&atilde; số h&agrave;ng h&oacute;a v&agrave; thuế nhập khẩu, theo c&ocirc;ng văn số 6393/TB-TCHQ,&nbsp;<em><strong>nh&ocirc;m định h&igrave;nh (aluminium profile), dạng h&igrave;nh, đ&atilde; được xử l&yacute; bề mặt bằng sơn tĩnh điện thuộc nh&oacute;m 7604 &ldquo;Nh&ocirc;m ở dạng thanh, que v&agrave; h&igrave;nh&rdquo;</strong></em>. Về ph&acirc;n nh&oacute;m th&igrave; t&ugrave;y v&agrave;o chất liệu của h&agrave;ng h&oacute;a ( nh&ocirc;m hay hợp kim nh&ocirc;m) sẽ được ph&acirc;n loại ri&ecirc;ng,&nbsp;<strong>thuế nhập khẩu dao động từ 5-10%.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>','/a5/34/4a/5a957914069da.jpg',NULL,3541,0,'2018-03-01 00:00:00',NULL,'Nhôm là một trong những nguyên liệu sản xuất ra rất nhiều sản phẩm trong cuộc sống trong đó các thành phẩm nhôm như nhôm than và hợp kim nhôm lại là một trong những mặt hàng nhập khẩu rất nhiều nhất là từ Trung Quốc. Việc nhập khẩu nhôm thanh và hợp kim n','thủ tục nhập khẩu nhôm, nhôm thanh và nhôm định hình, mã HS hàng nhôm','','',2,0,'vi','thu-tuc-nhap-khau-nhom-thanh-hop-kim-nhom-dinh-hinh',9,26,26,'2018-02-27 20:52:14','2018-03-03 21:22:54'),(264,'10 NGHỊ ĐỊNH VỀ BIỂU THUẾ NHẬP KHẨU ƯU ĐÃI ĐẶC BIỆT CÓ HIỆU LỰC TỪ 01/01/2018','','2018-2022 là giai đoạn mà nhiều biểu thuế nhập khẩu ưu đãi mới có hiệu lực. Vậy đó là những biểu thuế nào, hãy liệt kê dưới đây nhé','<p><span style=\"color:#008000\">Dưới đ&acirc;y l&agrave; 10 nghị định li&ecirc;n quan đến chiều chỉnh thuế suất nhập khẩu ưu đ&atilde;i đặc biệt c&oacute; hiệu lực từ 01/01/2018.</span></p>\r\n\r\n<p><span style=\"color:#006400\">1. Nghị định số 149/2017/NĐ-CP quy định cụ thể suất thuế NK ưu đ&atilde;i đặc biệt của Việt Nam để thực hiện Hiệp định Thương mại tự do Việt Nam - H&agrave;n Quốc giai đoạn 2018-2022 (thuế suất VKFTA) v&agrave; điều kiện được hưởng thuế suất thuế NK ưu đ&atilde;i đặc biệt theo Hiệp định n&agrave;y.</span></p>\r\n\r\n<p><span style=\"color:#006400\">Nghị định n&ecirc;u r&otilde; h&agrave;ng h&oacute;a NK được &aacute;p dụng thuế suất VKFTA phải đ&aacute;p ứng đủ c&aacute;c điều kiện: Thuộc Biểu thuế NK ưu đ&atilde;i đặc biệt; được NK từ H&agrave;n Quốc v&agrave;o Việt Nam; được vận chuyển trực tiếp từ H&agrave;n Quốc v&agrave;o Việt Nam do Bộ C&ocirc;ng Thương quy định; đ&aacute;p ứng c&aacute;c quy định về xuất xứ h&agrave;ng h&oacute;a trong Hiệp định Thương mại tự do Việt Nam - H&agrave;n Quốc, c&oacute; giấy chứng nhận xuất xứ h&agrave;ng h&oacute;a (C/O) mẫu KV theo quy định.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">2. Nghị định 150/2017/NĐ-CP về biểu thuế NK ưu đ&atilde;i đặc biệt của Việt Nam để thực hiện Hiệp định Thương mại tự do giữa Việt Nam v&agrave; Li&ecirc;n minh Kinh tế &Aacute; - &Acirc;u v&agrave; c&aacute;c nước th&agrave;nh vi&ecirc;n giai đoạn 2018-2022 (Hiệp định VN-EAEU FTA).</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">3. Nghị định 154/2017/NĐ-CP về biểu thuế NK ưu đ&atilde;i đặc biệt của Việt Nam để thực hiện Hiệp định thương mại tự do giữa Việt Nam v&agrave; Chi L&ecirc; giai đoạn 2018-2022 (thuế suất VCFTA).</span></p>\r\n\r\n<p><span style=\"color:#006400\">Theo Nghị định n&agrave;y, h&agrave;ng h&oacute;a NK được &aacute;p dụng thuế suất VCFTA theo Hiệp định phải đ&aacute;p ứng đầy đủ c&aacute;c điều kiện: Thuộc Biểu thuế NK ưu đ&atilde;i đặc biệt; được NK v&agrave; vận chuyển trực tiếp từ Chi L&ecirc; v&agrave;o Việt Nam; đ&aacute;p ứng c&aacute;c quy định về xuất xứ h&agrave;ng h&oacute;a trong Hiệp định, c&oacute; C/O mẫu VC theo quy định.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">4. Nghị định 155/2017/NĐ-CP về biểu thuế NK ưu đ&atilde;i đặc biệt của Việt Nam để thực hiện Hiệp định giữa Việt Nam v&agrave; Nhật Bản về Đối t&aacute;c kinh tế giai đoạn 2018-2023 (thuế suất VJEPA).</span></p>\r\n\r\n<p><span style=\"color:#006400\">Theo đ&oacute;, h&agrave;ng h&oacute;a được &aacute;p dụng thuế suất VJEPA ưu đ&atilde;i đặc biệt phải đ&aacute;p ứng c&aacute;c điều kiện như: Thuộc Biểu thuế NK ưu đ&atilde;i đặc biệt; được NK v&agrave; vận chuyển trực tiếp từ Nhật Bản v&agrave;o Việt Nam; đ&aacute;p ứng c&aacute;c quy định về xuất xứ h&agrave;ng h&oacute;a trong Hiệp định, c&oacute; C/O mẫu JV theo quy định.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">5. Nghị định 156/2017/NĐ-CP về biểu thuế NK ưu đ&atilde;i đặc biệt của Việt Nam để thực hiện Hiệp định thương mại H&agrave;ng h&oacute;a ASEAN giai đoạn&nbsp; 2018-2022 (thuế suất ATIGA).</span></p>\r\n\r\n<p><span style=\"color:#006400\">H&agrave;ng h&oacute;a NK được &aacute;p dụng thuế suất ATIGA theo Hiệp định phải đ&aacute;p ứng đầy đủ c&aacute;c điều kiện: Thuộc Biểu thuế NK ưu đ&atilde;i đặc biệt; được NK v&agrave;o Việt Nam từ c&aacute;c nước l&agrave; th&agrave;nh vi&ecirc;n của Hiệp định Thương mại H&agrave;ng h&oacute;a ASEAN; được vận chuyển trực tiếp từ nước XK tr&ecirc;n v&agrave;o Việt Nam do Bộ C&ocirc;ng Thương quy định; đ&aacute;p ứng c&aacute;c quy định về xuất xứ h&agrave;ng h&oacute;a trong Hiệp định, c&oacute; C/O mẫu D theo quy định.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">6. Nghị định 157/2017/NĐ-CP quy định thuế suất thuế NK ưu đ&atilde;i đặc biệt để thực hiện Hiệp định Thương mại h&agrave;ng h&oacute;a ASEAN - H&agrave;n Quốc thuộc Hiệp định Khung về Hợp t&aacute;c Kinh tế To&agrave;n diện ASEAN - H&agrave;n Quốc giai đoạn 2018-2022 v&agrave; điều kiện được hưởng thuế suất thuế NK ưu đ&atilde;i đặc biệt theo Hiệp định n&agrave;y (thuế suất AKFTA).</span></p>\r\n\r\n<p><span style=\"color:#006400\">H&agrave;ng h&oacute;a NK được &aacute;p dụng thuế suất AKFTA phải đ&aacute;p ứng đầy đủ c&aacute;c điều kiện: Thuộc Biểu thuế NK ưu đ&atilde;i đặc biệt; được NK v&agrave;o Việt Nam từ c&aacute;c nước l&agrave; th&agrave;nh vi&ecirc;n của Hiệp định Thương mại H&agrave;ng h&oacute;a ASEAN - H&agrave;n Quốc; được vận chuyển trực tiếp từ nước XK tr&ecirc;n v&agrave;o Việt Nam do Bộ C&ocirc;ng Thương quy định; đ&aacute;p ứng c&aacute;c quy định về xuất xứ h&agrave;ng h&oacute;a trong Hiệp định, c&oacute; C/O mẫu AK theo quy định.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">7. Nghị định 158/2017/NĐ-CP về biểu thuế NK ưu đ&atilde;i đặc biệt của Việt Nam để thực hiện Hiệp định th&agrave;nh lập Khu vực Thương mại tự do ASEAN &ndash; Australia &ndash; New Zealand giai đoạn 2018-2022 (thuế suất AANZFTA).</span></p>\r\n\r\n<p><span style=\"color:#006400\">H&agrave;ng h&oacute;a NK được &aacute;p dụng thuế suất ưu đ&atilde;i theo Hiệp định phải đ&aacute;p ứng đầy đủ c&aacute;c điều kiện: Thuộc Biểu thuế NK ưu đ&atilde;i đặc biệt; được NK v&agrave;o Việt Nam từ c&aacute;c nước l&agrave; th&agrave;nh vi&ecirc;n của Hiệp định; được vận chuyển trực tiếp từ nước XK tr&ecirc;n v&agrave;o Việt Nam do Bộ C&ocirc;ng Thương quy định; đ&aacute;p ứng c&aacute;c quy định về xuất xứ h&agrave;ng h&oacute;a trong Hiệp định, c&oacute; C/O mẫu AANZ theo quy định.</span></p>\r\n\r\n<p><span style=\"color:#006400\">8. Nghị định 159/2017/NĐ-CP về biểu thuế NK ưu đ&atilde;i đặc biệt của Việt Nam để thực hiện Hiệp định Thương mại h&agrave;ng h&oacute;a ASEAN - Ấn Độ giai đoạn 2018-2022 (thuế suất AIFTA).</span></p>\r\n\r\n<p><span style=\"color:#006400\">H&agrave;ng h&oacute;a NK được &aacute;p dụng thuế suất AIFTA ưu đ&atilde;i phải đ&aacute;p ứng đầy đủ c&aacute;c điều kiện: Thuộc Biểu thuế NK ưu đ&atilde;i đặc biệt; được NK v&agrave;o Việt Nam từ c&aacute;c nước l&agrave; th&agrave;nh vi&ecirc;n của Hiệp định; được vận chuyển trực tiếp từ nước XK tr&ecirc;n v&agrave;o Việt Nam do Bộ C&ocirc;ng Thương quy định; đ&aacute;p ứng c&aacute;c quy định về xuất xứ h&agrave;ng h&oacute;a trong Hiệp định, c&oacute; C/O mẫu AI theo quy định.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">9. Nghị định 160/2017/NĐ-CP về biểu thuế NK ưu đ&atilde;i đặc biệt của Việt Nam để thực hiện Hiệp định Đối t&aacute;c kinh tế to&agrave;n diện ASEAN - Nhật Bản giai đoạn 2018-2023 (thuế suất AJCEP).</span></p>\r\n\r\n<p><span style=\"color:#006400\">H&agrave;ng h&oacute;a NK được &aacute;p dụng thuế suất AJCEP ưu đ&atilde;i phải đ&aacute;p ứng đầy đủ c&aacute;c điều kiện: Thuộc Biểu thuế NK ưu đ&atilde;i đặc biệt; được NK v&agrave;o Việt Nam từ c&aacute;c nước l&agrave; th&agrave;nh vi&ecirc;n của Hiệp định; được vận chuyển trực tiếp từ nước XK tr&ecirc;n v&agrave;o Việt Nam do Bộ C&ocirc;ng Thương quy định; đ&aacute;p ứng c&aacute;c quy định về xuất xứ h&agrave;ng h&oacute;a trong Hiệp định, c&oacute; C/O mẫu AJ theo quy định.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"color:#006400\">10. Nghị định 153/2017/NĐ-CP về biểu thuế NK ưu đ&atilde;i đặc biệt của Việt Nam để thực hiện Hiệp định Thương mại h&agrave;ng h&oacute;a ASEAN - Trung Quốc giai đoạn 2018-2022.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>','/49/ab/b4/5a9569c9dd41d.jpg',NULL,1108,0,'2018-03-01 00:00:00',NULL,'Dưới đây là 10 nghị định liên quan đến chiều chỉnh thuế suất nhập khẩu ưu đãi đặc biệt có hiệu lực từ 01/01/2018.\r\n\r\n1. Nghị định số 149/2017/NĐ-CP quy định cụ thể suất thuế NK ưu đãi đặc biệt của Việt Nam để thực hiện Hiệp định Thương mại tự do Việt Nam ','thuế nhập khẩu ưu đãi, HS, biểu thuế','','',2,0,'vi','10-nghi-dinh-ve-bieu-thue-nhap-khau-uu-dai-dac-biet-co-hieu-luc-tu-01-01-2018',9,26,26,'2018-02-27 21:23:05','2018-03-03 21:24:17'),(265,'THỦ TỤC HẢI QUAN ĐỐI VỚI HÀNG HÓA XUẤT NHẬP TẠI CHỖ','','Hàng hoá xuất khẩu, nhập khẩu tại chỗ áp dụng đối với hàng hoá do thương nhân Việt Nam (bao gồm cả doanh nghiệp có vốn đầu tư nước ngoài; doanh nghiệp chế xuất) xuất khẩu cho thương nhân nước ngoài nhưng theo chỉ định của thương nhân nước ngoài hàng hoá đó được giao tại việt Nam cho thương nhân Việt Nam khác. Vì vậy, thủ tục Hải quan của loại hình này có khác gì so với các loại hình xuất nhập khẩu khác?','<h2>1.Xuất nhập tại chỗ l&agrave; g&igrave;?</h2>\r\n\r\n<p>H&agrave;ng ho&aacute;&nbsp;xuất khẩu,&nbsp;nhập khẩu tại chỗ&nbsp;&aacute;p dụng đối với h&agrave;ng ho&aacute; do thương nh&acirc;n Việt Nam (bao gồm cả doanh nghiệp c&oacute; vốn đầu tư nước ngo&agrave;i; doanh nghiệp chế&nbsp;xuất)&nbsp;xuất khẩu&nbsp;cho thương nh&acirc;n nước ngo&agrave;i nhưng theo chỉ định của thương nh&acirc;n nước ngo&agrave;i h&agrave;ng ho&aacute; đ&oacute; được giao&nbsp;tại&nbsp;việt Nam cho thương nh&acirc;n Việt Nam kh&aacute;c</p>\r\n\r\n<h2>2. H&agrave;ng h&oacute;a xuất nhập tại chỗ bao gồm:</h2>\r\n\r\n<p><em>a) Sản phẩm gia c&ocirc;ng; m&aacute;y m&oacute;c, thiết bị thu&ecirc; hoặc mượn; nguy&ecirc;n liệu, vật tư dư thừa; phế liệu, phế phẩm thuộc hợp đồng gia c&ocirc;ng theo quy định tại khoản 3 Điều 32 Nghị định số 187/2013/NĐ-CP;</em></p>\r\n\r\n<p><em>b) H&agrave;ng h&oacute;a mua b&aacute;n giữa doanh nghiệp nội địa với doanh nghiệp chế xuất, doanh nghiệp trong khu phi thuế quan;</em></p>\r\n\r\n<p><em>c) H&agrave;ng h&oacute;a mua b&aacute;n giữa doanh nghiệp Việt Nam với tổ chức, c&aacute; nh&acirc;n nước ngo&agrave;i kh&ocirc;ng c&oacute; hiện diện tại Việt Nam v&agrave; được thương nh&acirc;n nước ngo&agrave;i chỉ định giao, nhận h&agrave;ng h&oacute;a với doanh nghiệp kh&aacute;c tại Việt Nam.</em></p>\r\n\r\n<h2><em>3. Thủ tục Hải quan</em></h2>\r\n\r\n<p><em>3.1Thời gian cần l&agrave;m khai b&aacute;o Hải quan như sau:</em></p>\r\n\r\n<p><em>Đối với h&agrave;ng h&oacute;a xuất khẩu: Trong thời hạn 15 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y th&ocirc;ng quan.</em></p>\r\n\r\n<p><em>Đối với h&agrave;ng h&oacute;a nhập khẩu: sau khi ho&agrave;n th&agrave;nh việc giao nhận h&agrave;ng h&oacute;a</em></p>\r\n\r\n<p><em>3.2. Tr&igrave;nh tự thực hiện</em></p>\r\n\r\n<p>Đối với người xuất khẩu</p>\r\n\r\n<p>+ Bước 1: Khai th&ocirc;ng tin tờ khai h&agrave;ng h&oacute;a xuất khẩu v&agrave; khai vận chuyển kết hợp, trong đ&oacute; ghi r&otilde; v&agrave;o &ocirc; &ldquo;Điểm đ&iacute;ch cho vận chuyển bảo thuế&rdquo; l&agrave; m&atilde; địa điểm của Chi cục Hải quan l&agrave;m thủ tục hải quan nhập khẩu;</p>\r\n\r\n<p>+ Bước 2: Thực hiện thủ tục xuất khẩu h&agrave;ng h&oacute;a theo quy định;</p>\r\n\r\n<p>+ Bước 3: Giao h&agrave;ng h&oacute;a cho người nhập khẩu sau khi h&agrave;ng h&oacute;a xuất khẩu đ&atilde; được th&ocirc;ng quan.</p>\r\n\r\n<p>Đối với người nhập khẩu</p>\r\n\r\n<p>+ Bước 1: Khai th&ocirc;ng tin tờ khai h&agrave;ng h&oacute;a nhập khẩu theo đ&uacute;ng thời hạn quy định trong đ&oacute; ghi r&otilde; số tờ khai h&agrave;ng h&oacute;a xuất khẩu tại chỗ tương ứng tại &ocirc; &ldquo;Phần ghi ch&uacute;&rdquo; tr&ecirc;n tờ khai h&agrave;ng h&oacute;a nhập khẩu hoặc &ocirc; &ldquo;Ghi ch&eacute;p kh&aacute;c&rdquo; tr&ecirc;n tờ khai hải quan giấy;</p>\r\n\r\n<p>+ Bước 2: Thực hiện thủ tục nhập khẩu h&agrave;ng h&oacute;a theo quy định;</p>\r\n\r\n<p><em>3.3 Th&agrave;nh phần hồ sơ:</em></p>\r\n\r\n<p>* Đối với h&agrave;ng h&oacute;a nhập khẩu:</p>\r\n\r\n<p>+ Tờ khai h&agrave;ng h&oacute;a nhập khẩu theo c&aacute;c chỉ ti&ecirc;u th&ocirc;ng tin tại Phụ lục II ban h&agrave;nh k&egrave;m Th&ocirc;ng tư số 38/2015/TT-BTC.</p>\r\n\r\n<p>Trường hợp thực hiện tr&ecirc;n tờ khai hải quan giấy theo quy định tại khoản 2 Điều 25 Nghị định số 08/2015/NĐ-CP, người khai hải quan khai v&agrave; nộp 02 bản ch&iacute;nh tờ khai h&agrave;ng h&oacute;a nhập khẩu theo mẫu HQ/2015/NK Phụ lục IV ban h&agrave;nh k&egrave;m Th&ocirc;ng tư số 38/2015/TT-BTC;</p>\r\n\r\n<p>+ H&oacute;a đơn thương mại trong trường hợp người mua phải thanh to&aacute;n cho người b&aacute;n: 01 bản chụp;</p>\r\n\r\n<p>+ Vận tải đơn hoặc c&aacute;c chứng từ vận tải kh&aacute;c c&oacute; gi&aacute; trị tương đương đối với trường hợp h&agrave;ng h&oacute;a vận chuyển bằng đường biển, đường h&agrave;ng kh&ocirc;ng, đường sắt, vận tải đa phương thức theo quy định của ph&aacute;p luật (trừ h&agrave;ng ho&aacute; nhập khẩu qua cửa khẩu bi&ecirc;n giới đường bộ, h&agrave;ng ho&aacute; mua b&aacute;n giữa khu phi thuế quan v&agrave; nội địa, h&agrave;ng h&oacute;a nhập khẩu do người nhập cảnh mang theo đường h&agrave;nh l&yacute;): 01 bản chụp;</p>\r\n\r\n<p>+ Giấy ph&eacute;p nhập khẩu đối với h&agrave;ng h&oacute;a phải c&oacute; giấy ph&eacute;p nhập khẩu; Giấy ph&eacute;p nhập khẩu theo hạn ngạch thuế quan: 01 bản ch&iacute;nh nếu xuất khẩu một lần hoặc 01 bản chụp k&egrave;m theo Phiếu theo d&otilde;i trừ l&ugrave;i nếu xuất khẩu nhiều lần;</p>\r\n\r\n<p>+ Giấy th&ocirc;ng b&aacute;o miễn kiểm tra hoặc Giấy th&ocirc;ng b&aacute;o kết quả kiểm tra của cơ quan kiểm tra chuy&ecirc;n ng&agrave;nh theo quy định của ph&aacute;p luật: 01 bản ch&iacute;nh.</p>\r\n\r\n<p>Đối với chứng từ quy định tại điểm d, điểm đ khoản n&agrave;y, nếu &aacute;p dụng cơ chế một cửa quốc gia, cơ quan quản l&yacute; nh&agrave; nước chuy&ecirc;n ng&agrave;nh gửi giấy ph&eacute;p nhập khẩu, văn bản th&ocirc;ng b&aacute;o kết quả kiểm tra, miễn kiểm tra chuy&ecirc;n ng&agrave;nh dưới dạng điện tử th&ocirc;ng qua Cổng th&ocirc;ng tin một cửa quốc gia, người khai hải quan kh&ocirc;ng phải nộp khi l&agrave;m thủ tục hải quan;</p>\r\n\r\n<p>+ Tờ khai trị gi&aacute;: Người khai hải quan khai tờ khai trị gi&aacute; theo mẫu, gửi đến Hệ thống dưới dạng dữ liệu điện tử hoặc nộp cho cơ quan hải quan 02 bản ch&iacute;nh (đối với trường hợp khai tr&ecirc;n tờ khai hải quan giấy). C&aacute;c trường hợp phải khai tờ khai trị gi&aacute; v&agrave; mẫu tờ khai trị gi&aacute; thực hiện theo Th&ocirc;ng tư của Bộ T&agrave;i ch&iacute;nh quy định về việc x&aacute;c định trị gi&aacute; hải quan đối với h&agrave;ng h&oacute;a xuất khẩu, nhập khẩu;</p>\r\n\r\n<p>+ Chứng từ chứng nhận xuất xứ h&agrave;ng h&oacute;a (Giấy chứng nhận xuất xứ h&agrave;ng h&oacute;a hoặc Chứng từ tự chứng nhận xuất xứ): 01 bản ch&iacute;nh hoặc chứng từ dưới dạng dữ liệu điện tử.</p>\r\n\r\n<p>* Đối với h&agrave;ng h&oacute;a xuất khẩu:</p>\r\n\r\n<p>+ Tờ khai h&agrave;ng h&oacute;a xuất khẩu theo c&aacute;c chỉ ti&ecirc;u th&ocirc;ng tin tại Phụ lục II ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y.</p>\r\n\r\n<p>Trường hợp thực hiện tr&ecirc;n tờ khai hải quan giấy theo quy định tại khoản 2 Điều 25 Nghị định số 08/2015/NĐ-CP, người khai hải quan khai v&agrave; nộp 02 bản ch&iacute;nh tờ khai h&agrave;ng h&oacute;a xuất khẩu theo mẫu HQ/2015/XK Phụ lục IV ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y;</p>\r\n\r\n<p>+ Giấy ph&eacute;p xuất khẩu đối với h&agrave;ng h&oacute;a phải c&oacute; giấy ph&eacute;p xuất khẩu: 01 bản ch&iacute;nh nếu xuất khẩu một lần hoặc 01 bản chụp k&egrave;m theo Phiếu theo d&otilde;i trừ l&ugrave;i nếu xuất khẩu nhiều lần;</p>\r\n\r\n<p>+ Giấy th&ocirc;ng b&aacute;o miễn kiểm tra hoặc giấy th&ocirc;ng b&aacute;o kết quả kiểm tra của cơ quan kiểm tra chuy&ecirc;n ng&agrave;nh theo quy định của ph&aacute;p luật: 01 bản ch&iacute;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>','/74/22/27/5a95725b4b933.jpg',NULL,4799,0,'2018-03-01 00:00:00',NULL,'Hàng hoá xuất khẩu, nhập khẩu tại chỗ áp dụng đối với hàng hoá do thương nhân Việt Nam (bao gồm cả doanh nghiệp có vốn đầu tư nước ngoài; doanh nghiệp chế xuất) xuất khẩu cho thương nhân nước ngoài nhưng theo chỉ định của thương nhân nước ngoài hàng hoá đ','xuất nhập khẩu tại chỗ, thủ tục xuất nhập khẩu','','',2,0,'vi','thu-tuc-hai-quan-doi-voi-hang-hoa-xuat-nhap-tai-cho',9,26,26,'2018-02-27 21:59:39','2018-03-03 21:25:36'),(266,'TỔNG HỢP CHI TIẾT 20 MẪU CO - CHỨNG NHẬN XUẤT XỨ HÀNG HÓA','','C/O là từ viết tắt của Certificate of Origin. C/O được nhắc đến với vai trò chủ yếu là chứng minh xuất xứ hàng hóa và hưởng thuế suất ưu đãi cho hàng hóa có xuất xứ được ghi trên C/O theo các hiệp định thương mại được ký kết giữa các quốc gia/liên minh/vùng lãnh thổ. Không phải có C/O hợp lệ thì hàng hóa được hưởng ưu đãi thuế suất, có những mẫu C/O chỉ đơn thuần chứng minh xuất xứ. Hãy cùng nhau tìm hiểu kỹ 19 mẫu C/O dưới đây để hiểu rõ hơn về C/O.','<p><em><strong>C&oacute; hai loại CO ch&iacute;nh:</strong></em></p>\r\n\r\n<p><em>- C/O kh&ocirc;ng ưu đ&atilde;i:</em> C/O chỉ chứng minh xuất xứ h&agrave;ng h&oacute;a, kh&ocirc;ng c&oacute; gi&aacute; trị hưởng thuế suất ưu đ&atilde;i</p>\r\n\r\n<p><em>- C/O ưu đ&atilde;i:</em>&nbsp;C/O cho ph&eacute;p h&agrave;ng h&oacute;a&nbsp;được cắt giảm hoặc miễn thuế sang c&aacute;c nước mở rộng đặc quyền n&agrave;y. C/O vừa chứng minh xuất xứ, vừa c&oacute; gi&aacute; trị hưởng thuế suất ưu đ&atilde;i cho c&aacute;c h&agrave;ng h&oacute;a ghi tr&ecirc;n C/O.</p>\r\n\r\n<p><em><strong>C&aacute;c mẫu C/O:</strong></em></p>\r\n\r\n<p><em>1. CO form A:</em> h&agrave;ng xuất khẩu/nhập khẩu giữa Việt Nam v&agrave; c&aacute;c nước cho Việt Nam hưởng ưu đ&atilde;i thuế quan phổ cập GSP - <em>Generalized&nbsp;</em><em>Systems of Prefrences</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Theo danh s&aacute;ch của UNCTAD: Việt Nam kh&ocirc;ng c&oacute; trong danh s&aacute;ch c&aacute;c nước được hưởng ưu đ&atilde;i GSP của Australia, Estonia, v&agrave; United States</p>\r\n\r\n<p>- C/O ưu đ&atilde;i, hưởng thuế suất nhập khẩu ưu đ&atilde;i</p>\r\n\r\n<p>- C/O form A kh&ocirc;ng ưu đ&atilde;i cho h&agrave;ng gi&agrave;y d&eacute;p xuất đi EU do Ph&ograve;ng Quản l&yacute; XNK của Bộ C&ocirc;ng thương v&agrave; c&aacute;c Ban quản l&yacute; KCX-KCN được Bộ C&ocirc;ng thương ủy quyền cấp</p>\r\n\r\n<p>- C/O form A ưu đ&atilde;i: VCCI cấp</p>\r\n\r\n<p><em><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20A.jpg\" style=\"height:920px; width:650px\" /></em></p>\r\n\r\n<p><em>2. CO form B:</em> h&agrave;ng xuất khẩu sang tất cả c&aacute;c nước, cấp theo quy định xuất xứ <strong>kh&ocirc;ng ưu đ&atilde;i</strong></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/COFORM%20B.jpg\" style=\"height:920px; width:650px\" /></p>\r\n\r\n<p><em>3. CO form D:</em> h&agrave;ng xuất khẩu/nhập khẩu giữa c&aacute;c nước thuộc Hiệp định thương mại h&agrave;ng h&oacute;a ASEAN (<em>ATIGA - ASEAN Trade in Goods Agreement, </em>được k&yacute; v&agrave;o th&aacute;ng 02/2009 v&agrave; c&oacute; hiệu lực từ ng&agrave;y 17/05/2010), trước đ&acirc;y gọi khu vực mậu dịch tự do ASEAN (<em>AFTA - ASEAN Free Trade Area</em>) hay hiệp định thương mại h&agrave;ng h&oacute;a <em>CEPT - Common Effective Preferential Tariff</em>. Bao gồm c&aacute;c quốc gia th&agrave;nh vi&ecirc;n: Brunei, Indonesia, Malaysia, Philippines, Singapore, Thailand, Campuchia, Laos, Myanmar,Vietnam.</p>\r\n\r\n<p>- C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do Ph&ograve;ng Quản l&yacute; XNK của Bộ C&ocirc;ng thương v&agrave; c&aacute;c Ban quản l&yacute; KCX-KCN được Bộ C&ocirc;ng thương ủy quyền cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20D-th.jpg\" style=\"height:920px; width:650px\" /></p>\r\n\r\n<p><em>4. CO form AJ:</em> h&agrave;ng xuất khẩu/nhập khẩu giữa Nhật Bản v&agrave; c&aacute;c nước ASEAN thuộc diện hưởng ưu đ&atilde;i thuế quan theo hiệp định đối t&aacute;c kinh tế to&agrave;n diện ASEAN-Nhật Bản (<em>AJCEP - Association of Southeast Asian Nations, </em>được k&yacute; kết v&agrave;o th&aacute;ng 04/2008 v&agrave; Hiệp định n&agrave;y bắt đầu c&oacute; hiệu lực từ ng&agrave;y 01/12/2008)</p>\r\n\r\n<p>- C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do Ph&ograve;ng Quản l&yacute; XNK của Bộ C&ocirc;ng thương v&agrave; c&aacute;c Ban quản l&yacute; KCX-KCN được Bộ C&ocirc;ng thương ủy quyền cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20AJ.JPG\" style=\"height:933px; width:650px\" /></p>\r\n\r\n<p>5. <em>CO form VJ: </em>h&agrave;ng xuất khẩu/nhập khẩu giữa Nhật Bản v&agrave;<em> </em>Việt Nam thuộc diện hưởng ưu đ&atilde;i thuế quan theo hiệp định đối t&aacute;c kinh tế VJEPA <em>(Vietnam-Japan Economic Partnership Agreement, </em>được k&yacute; kết ng&agrave;y&nbsp;25/12/2008 v&agrave; c&oacute; hiệu lực từ ng&agrave;y 01/10/2009<em>)</em></p>\r\n\r\n<p><em>- </em><em>C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do VCCI cấp.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20VJ.jpg\" style=\"height:890px; width:650px\" /></em></p>\r\n\r\n<p><em>6. CO form AK:</em> h&agrave;ng xuất khẩu/nhập khẩu giữa H&agrave;n Quốc v&agrave; c&aacute;c nước ASEAN thuộc diện hưởng ưu đ&atilde;i thuế quan theo hiệp định thương mại h&agrave;ng h&oacute;a ASEAN-H&agrave;n Quốc AKFTA <em>(ASEAN-Korea Free Trade Agreement, </em>được k&yacute; kết ng&agrave;y&nbsp;13/05/2009 v&agrave; c&oacute; hiệu lực từ&nbsp;ng&agrave;y&nbsp;01/09/2009)</p>\r\n\r\n<p>- C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do Ph&ograve;ng Quản l&yacute; XNK của Bộ C&ocirc;ng thương v&agrave; c&aacute;c Ban quản l&yacute; KCX-KCN được Bộ C&ocirc;ng thương ủy quyền cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20AK.jpg\" style=\"height:893px; width:650px\" /></p>\r\n\r\n<p>7. <em>CO form VK: </em>h&agrave;ng xuất khẩu/nhập khẩu giữa H&agrave;n Quốc-Việt Nam nằm trong AKFTA v&agrave; thuộc Hiệp định Thương mại tự do Việt Nam-H&agrave;n Quốc<em> (VKFTA - VietNam - Korea Free Trade Area, </em>được k&yacute; kết ng&agrave;y&nbsp;5/5/2015 v&agrave; ch&iacute;nh thức c&oacute; hiệu lực từ ng&agrave;y 20/12/2015<em>)</em></p>\r\n\r\n<p><em>- </em><em>C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do VCCI cấp.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20VK.jpg\" style=\"height:807px; width:650px\" /></em></p>\r\n\r\n<p><em>8. CO form E:</em> h&agrave;ng xuất khẩu/nhập khẩu giữa Trung Quốc v&agrave; c&aacute;c nước ASEAN thuộc diện hưởng ưu đ&atilde;i thuế quan theo hiệp định thương mại h&agrave;ng h&oacute;a ASEAN - Trung Quốc (<em>ACFTA - The ASEAN&ndash;China Free Trade Area, </em>được k&yacute; kết ng&agrave;y 29/11/2004 v&agrave; bắt đầu c&oacute; hiệu lực từ&nbsp;ng&agrave;y&nbsp;01/01/2005)</p>\r\n\r\n<p>- C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do Ph&ograve;ng Quản l&yacute; XNK của Bộ C&ocirc;ng thương v&agrave; c&aacute;c Ban quản l&yacute; KCX-KCN được Bộ C&ocirc;ng thương ủy quyền cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20E.jpg\" style=\"height:894px; width:650px\" /></p>\r\n\r\n<p>9. <em>CO form AANZ</em>: h&agrave;ng xuất khẩu/nhập khẩu giữa c&aacute;c nước ASEAN &ndash; &Uacute;c &ndash; Newzealand thuộc diện ưu đ&atilde;i thuế quan theo hiệp định khu vực thương mại tự do giữa c&aacute;c nước ASEAN, Australia v&agrave; New Zealand (<em>AANZFTA: ASEAN-Australia-New Zealand Free Trade Agreement, </em>được k&yacute; kết ng&agrave;y&nbsp;27/02/2009, c&oacute; hiệu lực từ&nbsp;ng&agrave;y&nbsp;01/01/2010<em>)</em></p>\r\n\r\n<p><em>- </em><em>C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do VCCI cấp</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20AANZ.jpg\" style=\"height:913px; width:650px\" /></em></p>\r\n\r\n<p><em>10. CO form AI:</em> h&agrave;ng xuất khẩu/nhập khẩu giữa Ấn Độ v&agrave; c&aacute;c nước ASEAN thuộc diện hưởng ưu đ&atilde;i thuế quan theo hiệp định thương mại h&agrave;ng h&oacute;a ASEAN-Ấn Độ <em>(AIFTA: ASEAN &ndash; India Free Trade Area, </em>được k&yacute; kết&nbsp;v&agrave;o&nbsp;ng&agrave;y&nbsp;13/08/2009 v&agrave; c&oacute; hiệu lực từ&nbsp;ng&agrave;y&nbsp;01/01/2010<em>)</em></p>\r\n\r\n<p><em>- </em><em>C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do VCCI cấp.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20AI.JPG\" style=\"height:925px; width:650px\" /></em></p>\r\n\r\n<p><em>11. CO form VC: </em>h&agrave;ng xuất khẩu/nhập khẩu giữa Việt Nam-Chi L&ecirc; thuộc diện ưu đ&atilde;i thuế quan theo hiệp định thương mại h&agrave;ng h&oacute;a <em>VCFTA (Vietnam-Chile Free Trade Agreement, </em>được k&yacute; kết ng&agrave;y&nbsp;11/11/2011 v&agrave; c&oacute; hiệu lực từng&agrave;y&nbsp;1/1/2014<em>)</em></p>\r\n\r\n<p><em>- </em><em>C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do VCCI cấp.</em></p>\r\n\r\n<p><em><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20VC.jpg\" style=\"height:1025px; width:650px\" /></em></p>\r\n\r\n<p><em>12. CO form EAV: </em>h&agrave;ng xuất khẩu/nhập khẩu giữa Việt Nam v&agrave; c&aacute;c nước th&agrave;nh vi&ecirc;n Li&ecirc;n minh Kinh tế &Aacute; &Acirc;u theo Hiệp định thương mại tự do Việt Nam &ndash; Li&ecirc;n minh Kinh tế &Aacute; &Acirc;u. EAEU - bao gồm c&aacute;c nước Li&ecirc;n bang Nga, Cộng h&ograve;a Belarus, Cộng h&ograve;a Kazakhstan, Cộng h&ograve;a Armenia v&agrave; Cộng h&ograve;a Kyrgyzstan. (VNEAEUFTA -<em>The Vietnam-</em><em>Eurasian Economic Union Free Trade Agreement</em>, được k&yacute; kết ng&agrave;y 29/5/2015 v&agrave;&nbsp;c&oacute; hiệu lực từ&nbsp;ng&agrave;y&nbsp;05/10/2016)</p>\r\n\r\n<p>- C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do Ph&ograve;ng Quản l&yacute; XNK của Bộ C&ocirc;ng thương v&agrave; c&aacute;c Ban quản l&yacute; KCX-KCN được Bộ C&ocirc;ng thương ủy quyền cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20EAV.jpg\" style=\"height:940px; width:650px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Mẫu&nbsp;Giấy chứng nhận xuất xứ Mẫu EAV - C/O form EAV</em></p>\r\n\r\n<p style=\"text-align:center\"><em><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20EAV%20ADD.jpg\" style=\"height:936px; width:650px\" /></em></p>\r\n\r\n<p style=\"text-align:center\"><em>Mẫu Tờ khai bổ sung C/O mẫu EAV&nbsp;</em><br />\r\n&nbsp;</p>\r\n\r\n<p><em>13. CO form S:</em> h&agrave;ng xuất khẩu/nhập khẩu giữa Việt Nam - L&agrave;o thuộc diện hưởng ưu đ&atilde;i thuế quan theo hiệp định thương mại Việt Nam - L&agrave;o được k&yacute; kết ng&agrave;y 03/03/2015 v&agrave; c&oacute; hiệu lực kể từ ng&agrave;y 03/10/2015.</p>\r\n\r\n<p>- C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i đặc biệt, do Ph&ograve;ng Quản l&yacute; XNK của Bộ C&ocirc;ng thương v&agrave; c&aacute;c Ban quản l&yacute; KCX-KCN được Bộ C&ocirc;ng thương ủy quyền cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20S.jpg\" style=\"height:922px; width:650px\" /></p>\r\n\r\n<p>14.<em> CO form GSTP:</em> h&agrave;ng xuất khẩu sang c&aacute;c nước tham gia hệ thống ưu đ&atilde;i thương mại to&agrave;n cầu (GSTP - <em>Global System of Trade Preperences</em>) cho Việt Nam hưởng ưu đ&atilde;i GSTP</p>\r\n\r\n<p>- C/O ưu đ&atilde;i, hưởng thuế suất ưu đ&atilde;i, VCCI cấp</p>\r\n\r\n<p>15.<em> CO form ICO:</em> cấp cho sản phẩm từ c&agrave; ph&ecirc; trồng v&agrave; thu hoạch tại Việt Nam xuất khẩu sang tất cả c&aacute;c nước theo quy định của Tổ chức c&agrave; ph&ecirc; thế giới (ICO - <em>International Coffee Organization</em>)</p>\r\n\r\n<p>- C/O kh&ocirc;ng ưu đ&atilde;i, VCCI cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20ICO.jpg\" style=\"height:920px; width:650px\" /></p>\r\n\r\n<p><em>16. CO form Textile (gọi tắt l&agrave; form T)</em> cấp cho h&agrave;ng dệt may thuộc diện quản l&yacute; hạn ngạch theo Hiệp định dệt may Việt Nam - EU</p>\r\n\r\n<p>- C/O kh&ocirc;ng ưu đ&atilde;i, VCCI cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20TEXTILE.jpg\" style=\"height:920px; width:650px\" /></p>\r\n\r\n<p>17.<em> CO form Mexico:</em> (thường gọi l&agrave; anexo III) cấp cho h&agrave;ng dệt may, gi&agrave;y d&eacute;p xuất khẩu sang Mexico theo quy định của Mexico</p>\r\n\r\n<p>- C/O kh&ocirc;ng ưu đ&atilde;i, VCCI cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20M%20MEXICO.jpg\" style=\"height:1070px; width:650px\" /></p>\r\n\r\n<p>18.<em> CO form Venezuela: </em>cấp cho h&agrave;ng xuất khẩu sang Venezuela theo quy định của Venezuela</p>\r\n\r\n<p>- C/O kh&ocirc;ng ưu đ&atilde;i, VCCI cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20VENEZUELA.jpg\" style=\"height:997px; width:650px\" /></p>\r\n\r\n<p>19.<em> CO form Peru:</em> cấp cho h&agrave;ng gi&agrave;y d&eacute;p xuất khẩu sang Peru theo quy định của Peru</p>\r\n\r\n<p>- C/O kh&ocirc;ng ưu đ&atilde;i, VCCI cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20PERU.jpg\" style=\"height:919px; width:650px\" /></p>\r\n\r\n<p>20.&nbsp;<em>CO Form DA59:</em> (Mẫu C/O cho một số sản phẩm xuất khẩu sang Nam Phi)</p>\r\n\r\n<p>- C/O kh&ocirc;ng ưu đ&atilde;i, VCCI cấp</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20DA59.jpg\" style=\"height:841px; width:650px\" /></p>\r\n\r\n<p>Ở b&agrave;i sau, ch&uacute;ng ta sẽ t&igrave;m hiểu về bộ chứng từ xin cấp C/O v&agrave; quy tr&igrave;nh xin C/O nh&eacute;.</p>\r\n\r\n<p>Ms Sarah.</p>','/53/9d/d5/5a963136ac023.jpg',NULL,28452,0,'2018-03-01 00:00:00',NULL,'C/O là từ viết tắt của Certificate of Origin. C/O được nhắc đến với vai trò chủ yếu là chứng minh xuất xứ hàng hóa và hưởng thuế suất ưu đãi cho hàng hóa có xuất xứ được ghi trên C/O theo các hiệp định thương mại được ký kết giữa các quốc gia/liên minh/vù','các mẫu CO và nơi cấp, các loại giấy chứng nhận xuất xứ, CO không ưu đãi, CO ưu đãi, CO form A, CO form B, form D, form AJ, form VJ, form AK, form VK, form E, form AANZ, form AI, form VC, form EAV, Form DA59, form S, form GSTP, form IC','','',2,0,'vi','tong-hop-chi-tiet-tat-ca-cac-form-co',9,27,26,'2018-02-28 11:17:38','2018-03-03 21:26:44'),(267,'THỦ TỤC XIN CẤP MỚI C/O VÀ THỜI HẠN CẤP C/O','','Sau khi xác định được doanh nghiệp mình sẽ xin C/O mẫu nào. Tiếp tục dưới đây là các bước làm thủ tục xin cấp C/O và bộ chứng từ cần chuẩn bị.','<h2><strong>1. THỦ TỤC ĐĂNG K&Yacute; T&Agrave;I KHOẢN KHAI B&Aacute;O C/O</strong></h2>\r\n\r\n<p>Đối với doanh nghiệp (DN) lần đầu xin C/O, trước khi chuẩn bị c&aacute;c chứng từ C/O, phải điền đầy đủ Bộ Hồ sơ Thương nh&acirc;n c&ugrave;ng với 1 bản sao của Giấy ph&eacute;p Đăng k&yacute; kinh doanh nộp cho&nbsp;VCCI (Ph&ograve;ng Thương Mại C&ocirc;ng nghiệp Việt Nam). <strong>Bộ <a href=\"https://drive.google.com/open?id=1sX-JLM-anPfc0aEVe7AX1DRQ_gPRCqfj\">hồ sơ thương nh&acirc;n</a></strong>&nbsp; (3 trang) bao gồm:&nbsp;</p>\r\n\r\n<p>* Hồ sơ doanh nghiệp đề nghị cấp C/O (P1-HSTN)</p>\r\n\r\n<p>* Đăng k&yacute; mẫu chữ k&yacute;, mẫu dấu của Người được ủy quyền k&yacute; (P2-HSTN)</p>\r\n\r\n<p>* Danh mục c&aacute;c cơ sở sản xuất của thương nh&acirc;n (P3-HSTN)</p>\r\n\r\n<p>* Giấy chứng nhận đăng&nbsp; k&yacute;&nbsp; kinh doanh của thương nh&acirc;n (1 bản sao)</p>\r\n\r\n<p>*&nbsp;Giấy Đăng k&yacute; M&atilde; số thuế của DN (1 bản sao)</p>\r\n\r\n<p><em><strong>Ch&uacute; &yacute;: </strong>Mọi&nbsp; sự&nbsp; thay&nbsp; đổi&nbsp; trong&nbsp; hồ&nbsp; sơ&nbsp; thương&nbsp; nh&acirc;n&nbsp; phải được th&ocirc;ng b&aacute;o cho Tổ chức cấp C/O nơi đ&atilde; đăng k&yacute; trước khi đề nghị cấp C/O. Hồ sơ thương nh&acirc;n vẫn phải được cập nhật hai (02) năm một lần.</em></p>\r\n\r\n<h2><strong>2. BỘ HỒ SƠ XIN CẤP C/O:</strong></h2>\r\n\r\n<p>1.&nbsp;<strong>Đơn xin cấp C/O</strong>: Điền đầy đủ c&aacute;c &ocirc; tr&ecirc;n đơn v&agrave; c&oacute; dấu, chữ k&yacute; của người c&oacute; thẩm quyền của DN<em>&nbsp;<a href=\"https://drive.google.com/open?id=1I4i0TrRwV_Rdi8YMMTm4KyMZlihgFFyl\">(tải mẫu tại đ&acirc;y)</a></em></p>\r\n\r\n<p>2.<strong>&nbsp;Mẫu C/O (A, B, T, Mexico, Venezuela,&hellip;):&nbsp;</strong>Người xuất khẩu chỉ được đề nghị cấp một loại Mẫu C/O cho mỗi l&ocirc; h&agrave;ng xuất khẩu, trừ Mẫu C/O c&agrave; ph&ecirc; c&oacute; thể đề nghị cấp th&ecirc;m Mẫu A hoặc Mẫu B (T&ugrave;y loại mặt h&agrave;ng v&agrave; nước xuất khẩu, c&aacute;n bộ C/O sẽ tư vấn cho DN mua mẫu C/O n&agrave;o).</p>\r\n\r\n<p>- C/O đ&atilde; được khai gồm c&oacute; 1 bản gốc v&agrave; &iacute;t nhất 2 bản sao C/O để Tổ chức cấp C/O v&agrave; Người xuất khẩu mỗi b&ecirc;n lưu một bản.</p>\r\n\r\n<p><em>Lưu &yacute;:</em>&nbsp;DN phải đ&aacute;nh m&aacute;y đầy đủ c&aacute;c &ocirc; tr&ecirc;n Form bằng tiếng Anh, bản ch&iacute;nh v&agrave; bản sao C/O phải c&oacute; dấu đỏ v&agrave; chữ k&yacute; người c&oacute; thẩm quyền k&yacute; của DN (trừ tr&ecirc;n C/O Form T kh&ocirc;ng cần dấu v&agrave; chữ k&yacute; của DN).</p>\r\n\r\n<p>Xem chi tiết c&aacute;c mẫu C/O <a href=\"http://tuhocxuatnhapkhau.com/news-detail/tong-hop-20-form-co\">tại đ&acirc;y</a>.</p>\r\n\r\n<p>3.&nbsp;<strong>Commercial Invoice</strong>&nbsp;(H&oacute;a đơn thương mại): 1 bản gốc do DN ph&aacute;t h&agrave;nh.</p>\r\n\r\n<p>4.&nbsp;<strong>Tờ khai Hải quan h&agrave;ng xuất khẩu</strong>: đ&atilde; ho&agrave;n th&agrave;nh thủ tục hải quan (1 bản sao c&oacute; dấu đỏ, chữ k&yacute; người c&oacute; thẩm quyền k&yacute; của DN, v&agrave; dấu &ldquo;Sao y bản ch&iacute;nh&rdquo;), trừ c&aacute;c trường hợp h&agrave;ng xuất khẩu kh&ocirc;ng phải khai b&aacute;o tờ khai hải quan xuất khẩu theo quy định của ph&aacute;p luật. Trong trường hợp c&oacute; l&yacute; do ch&iacute;nh đ&aacute;ng, Người xuất khẩu c&oacute; thể nộp sau chứng từ n&agrave;y.</p>\r\n\r\n<p>Nếu x&eacute;t thấy cần thiết, Tổ chức cấp C/O c&oacute; thể y&ecirc;u cầu Người xuất khẩu cung cấp th&ecirc;m c&aacute;c chứng từ li&ecirc;n quan đến sản phẩm xuất khẩu như:</p>\r\n\r\n<p>5.&nbsp;<strong>Packing List</strong>: 1 bản gốc của DN</p>\r\n\r\n<p>6.&nbsp;<strong>Bill of Lading</strong>&nbsp;(Vận đơn): 1 bản sao c&oacute; dấu đỏ, chữ k&yacute; người c&oacute; thẩm quyền k&yacute; của DN v&agrave; dấu &ldquo;Sao y bản ch&iacute;nh&rdquo;</p>\r\n\r\n<p>7<strong>&nbsp;Tờ khai Hải quan h&agrave;ng nhập</strong>&nbsp;(1 bản sao): nếu DN nhập c&aacute;c nguy&ecirc;n, phụ liệu từ nước ngo&agrave;i;</p>\r\n\r\n<p><em>hoặc</em>&nbsp;<strong>Ho&aacute; đơn g&iacute;a trị gia tăng mua b&aacute;n nguy&ecirc;n phụ liệu trong nước</strong>: nếu DN mua c&aacute;c nguy&ecirc;n vật liệu trong nước</p>\r\n\r\n<p>8.&nbsp;<strong>Bảng giải tr&igrave;nh Quy tr&igrave;nh sản xuất</strong>: Đối với DN lần đầu xin C/O hay mặt h&agrave;ng lần đầu xin C/O phải được DN giải tr&igrave;nh c&aacute;c bước sản xuất th&agrave;nh sản phẩm cuối c&ugrave;ng.</p>\r\n\r\n<p>B&ecirc;n cạnh&nbsp;đ&oacute;, tuỳ từng mặt h&agrave;ng v&agrave; nước xuất khẩu, c&aacute;n bộ C/O sẽ hướng dẫn DN giải tr&igrave;nh theo như c&aacute;c mẫu (xem phần&nbsp;<em>&quot;Hướng dẫn giải tr&igrave;nh sản phẩm&nbsp;để&nbsp;đ&aacute;p&nbsp;ứng ti&ecirc;u chuẩn xuất xứ&quot;&nbsp;</em>v&agrave; tư vấn c&aacute;c bước giải tr&igrave;nh tiếp theo.</p>\r\n\r\n<p>9.&nbsp;<strong>C&aacute;c giấy tờ kh&aacute;c:&nbsp;</strong>như Giấy ph&eacute;p xuất khẩu; Hợp đồng mua b&aacute;n; Mẫu nguy&ecirc;n, phụ liệu hoặc sản phẩm xuất khẩu; hoặc c&aacute;c chứng từ kh&aacute;c để chứng minh xuất xứ của sản phẩm.Tuỳ từng mặt h&agrave;ng v&agrave; nước xuất khẩu, c&aacute;n bộ C/O sẽ tư vấn c&aacute;c bước giải tr&igrave;nh tiếp theo.</p>\r\n\r\n<h2><strong>3. THỜI HẠN CẤP C/O</strong></h2>\r\n\r\n<p>*** <em><strong>Đối với C/O kh&ocirc;ng ưu đ&atilde;i</strong></em>,&nbsp;được quy định tại Th&ocirc;ng tư số&nbsp;<a href=\"https://drive.google.com/open?id=1dDZZSbjIWRBY1MW7Uz7bVYfCSnffxhVc\">07/2006/TT-BTM</a> ng&agrave;y 17/04/2006: (form A, C/O h&agrave;ng dệt may, C/O h&agrave;ng dệt thủ c&ocirc;ng xuất sang EU, form ICO, form B, C/O cho h&agrave;ng xuất khẩu theo mẫu của nước nhập khẩu y&ecirc;u cầu...)</p>\r\n\r\n<p>- Theo điểm a, khoản 4, mục 2 C/O được cấp trong v&ograve;ng&nbsp;ba&nbsp; (03)&nbsp; ng&agrave;y&nbsp; l&agrave;m việc kể từ thời điểm Người đề nghị cấp C/O nộp Bộ hồ sơ đầy đủ v&agrave; hợp lệ</p>\r\n\r\n<p>*** <em><strong>Đối với C/O ưu đ&atilde;i</strong></em>, được quy định tại Th&ocirc;ng tư số&nbsp;<a href=\"https://drive.google.com/open?id=1y_ugZEUy2v2FCUMqkDiU6GPaMJbOyKpa\">06/2011/TT-BCT</a> ng&agrave;y 21/03/2011:</p>\r\n\r\n<p>- Theo Khoản 1,&nbsp;Điều 13,&nbsp;C/O được cấp trong thời hạn sau:</p>\r\n\r\n<p>- Kh&ocirc;ng qu&aacute; 4 giờ l&agrave;m việc kể từ thời điểm người đề nghị cấp C/O nộp hồ sơ đầy đủ v&agrave; hợp lệ đối với trường hợp xuất khẩu qua đường h&agrave;ng kh&ocirc;ng;</p>\r\n\r\n<p>- Kh&ocirc;ng qu&aacute; 8 giờ l&agrave;m việc kể từ thời điểm người đề nghị cấp C/O nộp hồ sơ đầy đủ v&agrave; hợp lệ đối với trường hợp xuất khẩu bằng c&aacute;c phương tiện kh&aacute;c;</p>\r\n\r\n<p>- Đối với trường hợp thương nh&acirc;n nộp hồ sơ qua bưu điện, thời gian cấp C/O l&agrave; 1 ng&agrave;y l&agrave;m việc kể từ ng&agrave;y nhận ghi tr&ecirc;n b&igrave; thư.</p>\r\n\r\n<p>***<strong> <em>Lệ ph&iacute; cấp C/O</em></strong>: Miễn ph&iacute;&nbsp;</p>\r\n\r\n<h2>4. THỜI HẠN HIỆU LỰC CỦA C/O</h2>\r\n\r\n<p>Theo quy định tại Khoản 1, Điều 11 Phụ lục VII Th&ocirc;ng tư số 22/2016/TT-BCT về Thời hạn hiệu lực của C/O:</p>\r\n\r\n<p>&nbsp;C/O c&oacute; hiệu lực trong trong v&ograve;ng 12 th&aacute;ng kể từ ng&agrave;y cấp, v&agrave; phải được&nbsp;nộp cho cơ quan Hải quan Nước th&agrave;nh vi&ecirc;n nhập khẩu trong thời hạn đ&oacute;.</p>\r\n\r\n<p><em>Trường hợp C/O được nộp cho cơ quan Hải quan của nước nhập khẩu&nbsp;<strong>sau thời hạn</strong> quy định tại khoản 1, C/O vẫn được chấp nhận nếu việc kh&ocirc;ng tu&acirc;n&nbsp;thủ thời hạn n&ecirc;u tr&ecirc;n l&agrave; do <strong>bất khả kh&aacute;ng</strong> hoặc &nbsp;do những <strong>nguy&ecirc;n nh&acirc;n ch&iacute;nh&nbsp;đ&aacute;ng</strong> kh&aacute;c nằm ngo&agrave;i kiểm so&aacute;t của Người xuất khẩu.</em></p>\r\n\r\n<p>Ms Sarah.</p>','/b0/93/3b/5a979b8e0b23c.jpg',NULL,3444,0,'2018-03-01 00:00:00',NULL,'Đối với doanh nghiệp (DN) lần đầu xin C/O, trước khi chuẩn bị các chứng từ C/O, phải điền đầy đủ Bộ Hồ sơ Thương nhân cùng với 1 bản sao của Giấy phép Đăng ký kinh doanh nộp cho VCCI (Phòng Thương Mại Công nghiệp Việt Nam). Bộ hồ sơ thương nhân  (3 trang)','thời hạn cấp c/o ưu đãi, thủ tục xin co, xin giấy chứng nhận xuất xứ, xuất xứ hàng hóa, hồ sơ thương nhân, đơn đề nghị cấp co','','',2,0,'vi','thu-tuc-xin-cap-moi-c-o',9,27,26,'2018-02-28 12:24:51','2018-03-03 21:27:36'),(268,'PHÂN BIỆT C/O BACK TO BACK VÀ MOVEMENT CERTIFICATE','','Đây là 2 tên gọi khác nhau nhưng cùng một bản chất của Giấy chứng nhận xuất xứ hàng hóa có tên gọi \"C/O giáp lưng\" – C/O được cấp bởi Bên thành viên xuất khẩu trung gian dựa trên thông tin của C/O được cấp bởi Bên thành viên xuất khẩu đầu tiên','<p style=\"text-align:justify\">Đ&acirc;y l&agrave; 2 t&ecirc;n gọi kh&aacute;c nhau nhưng c&ugrave;ng một bản chất của Giấy chứng nhận xuất xứ h&agrave;ng h&oacute;a c&oacute; t&ecirc;n gọi &ldquo;C/O gi&aacute;p lưng&rdquo; &ndash; C/O được cấp bởi B&ecirc;n th&agrave;nh vi&ecirc;n xuất khẩu trung gian dựa tr&ecirc;n th&ocirc;ng tin của C/O được cấp bởi B&ecirc;n th&agrave;nh vi&ecirc;n xuất khẩu đầu ti&ecirc;n&nbsp;&nbsp;v&agrave; do vậy <strong>phải c&oacute; giao dịch của &iacute;t nhất 3 B&ecirc;n th&agrave;nh vi&ecirc;n</strong> trong&nbsp;Hiệp định mới c&oacute; thể cấp C/O gi&aacute;p lưng.</p>\r\n\r\n<p style=\"text-align:justify\">Một số FTA <em>(Free Trade Area: Khu vực mậu dịch tự do)</em>&nbsp;y&ecirc;u cầu nh&agrave; xuất khẩu&nbsp;tr&ecirc;n C/O gi&aacute;p lưng v&agrave; nh&agrave; nhập khẩu tr&ecirc;n C/O gốc phải l&agrave; một, c&oacute; nghĩa l&agrave; ai&nbsp;nhập khẩu &nbsp;(tr&ecirc;n C/O gốc) th&igrave; người đ&oacute; phải đồng thời phải l&agrave; người xuất khẩu&nbsp;(tr&ecirc;n &nbsp;C/O &nbsp;gi&aacute;p &nbsp;lưng) &nbsp;v&agrave; &nbsp;kh&ocirc;ng được &nbsp;ph&eacute;p &nbsp;b&aacute;n &nbsp;h&agrave;ng cho &nbsp;một &nbsp;ai &nbsp;kh&aacute;c &nbsp;tại &nbsp;B&ecirc;n&nbsp;th&agrave;nh vi&ecirc;n &nbsp;trung gian &nbsp;(AKFTA, &nbsp;ACFTA, &nbsp;AIFTA). &nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y &nbsp;l&agrave; &nbsp;quy &nbsp;định &nbsp;chặt chẽ,&nbsp;phần n&agrave;o giảm sự thuận lợi trong quan hệ thương mại c&oacute; nhiều hơn 2 B&ecirc;n tham&nbsp;gia. Một số FTA chặt hơn, thậm ch&iacute; c&ograve;n y&ecirc;u cầu sản phẩm ho&agrave;n to&agrave;n nằm trong&nbsp;sự kiểm so&aacute;t của cơ quan hải quan (AIFTA, ACFTA).</p>\r\n\r\n<p style=\"text-align:justify\">Một số FTA kh&ocirc;ng y&ecirc;u&nbsp;cầu nh&agrave; xuất khẩu tr&ecirc;n C/O gi&aacute;p lưng phải tr&ugrave;ng với nh&agrave; nhập khẩu tr&ecirc;n C/O&nbsp;gốc. Điều đ&oacute; c&oacute; nghĩa nh&agrave; nhập khẩu &nbsp;(tr&ecirc;n C/O gốc) c&oacute; thể b&aacute;n h&agrave;ng cho một&nbsp;thương nh&acirc;n kh&aacute;c v&agrave; thương nh&acirc;n n&agrave;y sẽ đứng t&ecirc;n tại &Ocirc; số 1 (nh&agrave; xuất khẩu)&nbsp;tr&ecirc;n C/O gi&aacute;p lưng (ATIGA, AANZFTA, AJCEP).&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/46.%20CO%20GIAP%20LUNG/CO%20GIAP%20LUNG.JPG\" style=\"height:439px; width:550px\" /></p>\r\n\r\n<p style=\"text-align:justify\">Nguồn: Internet</p>','/6e/f4/46/5a98a79a499da.jpg',NULL,3800,0,'2018-03-02 00:00:00',NULL,'Đây là 2 tên gọi khác nhau nhưng cùng một bản chất của Giấy chứng nhận xuất xứ hàng hóa có tên gọi \"C/O giáp lưng\" – C/O được cấp bởi Bên thành viên xuất khẩu trung gian dựa trên thông tin của C/O được cấp bởi Bên thành viên xuất khẩu đầu tiên  và do vậy ','co back to back, movement certificate, co giáp lưng','','',2,0,'vi','phan-biet-c-o-back-to-back-va-movement-certificate',9,27,26,'2018-03-02 00:34:47','2018-03-03 21:28:38'),(269,'HƯỚNG DẪN KÊ KHAI C/O FORM D','','Theo hướng dẫn ở Phụ lục IX Thông tư số 22/2016/TT-BCT ngày 03/10/2016. C/O phải được khai bằng tiếng Anh và đánh máy. Ô số 1: tên giao dịch của người xuất khẩu, địa chỉ, tên quốc gia xuất khẩu (Việt Nam). Ô số 2: Tên người nhận hàng, địa chỉ, tên nước.','<p>C/O phải được khai bằng tiếng Anh v&agrave; đ&aacute;nh m&aacute;y (trừ trường hợp hướng dẫn ở khoản 15 dưới đ&acirc;y). Nội dung khai phải ph&ugrave; hợp với c&aacute;c chứng từ quy định tại Điều 2 của Th&ocirc;ng tư n&agrave;y. Nội dung k&ecirc; khai C/O cụ thể như sau:</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/45.%2020%20mau%20CO/CO%20FORM%20D-th.jpg\" style=\"height:849px; width:600px\" /></p>\r\n\r\n<p>1. &Ocirc; số 1: t&ecirc;n giao dịch của người xuất khẩu, địa chỉ, t&ecirc;n quốc gia xuất khẩu (Việt Nam).</p>\r\n\r\n<p>2. &Ocirc; số 2: T&ecirc;n người nhận h&agrave;ng, địa chỉ, t&ecirc;n nước.</p>\r\n\r\n<p>3. &Ocirc; tr&ecirc;n c&ugrave;ng b&ecirc;n phải về việc ghi số tham chiếu (do Tổ chức cấp C/O ghi). Số tham chiếu gồm 13 k&yacute; tự, chia l&agrave;m 5 nh&oacute;m, với c&aacute;ch ghi cụ thể như sau:</p>\r\n\r\n<p>a) Nh&oacute;m 1: t&ecirc;n nước th&agrave;nh vi&ecirc;n xuất khẩu l&agrave; Việt Nam, gồm 02 k&yacute; tự&nbsp; l&agrave;&nbsp; &ldquo;VN&rdquo;;&nbsp;</p>\r\n\r\n<p>b) Nh&oacute;m 2: t&ecirc;n nước th&agrave;nh vi&ecirc;n nhập khẩu l&agrave; c&aacute;c nước th&agrave;nh vi&ecirc;n thuộc khối ASEAN, gồm 02 k&yacute; tự như sau:</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:30px; width:300px\">\r\n			<p>BN:&nbsp;&nbsp; Bru-n&acirc;y</p>\r\n			</td>\r\n			<td style=\"height:30px; width:300px\">\r\n			<p>MM:&nbsp; Mi-an-ma</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:300px\">\r\n			<p>KH:&nbsp;&nbsp; Cam-pu-chia&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n			</td>\r\n			<td style=\"width:300px\">\r\n			<p>PH:&nbsp;&nbsp;&nbsp; Phi-lip-pin</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:300px\">\r\n			<p>ID:&nbsp;&nbsp;&nbsp;&nbsp; In-đ&ocirc;-n&ecirc;-xi-a</p>\r\n			</td>\r\n			<td style=\"width:300px\">\r\n			<p>SG:&nbsp;&nbsp;&nbsp; Xinh-ga-po</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:300px\">\r\n			<p>LA:&nbsp;&nbsp;&nbsp; L&agrave;o</p>\r\n			</td>\r\n			<td style=\"width:300px\">\r\n			<p>TH:&nbsp;&nbsp; Th&aacute;i Lan</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:300px\">\r\n			<p>MY:&nbsp; Ma-lai-xi-a</p>\r\n			</td>\r\n			<td style=\"width:300px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; c) Nh&oacute;m 3: năm cấp C/O, gồm 02 k&yacute; tự. V&iacute; dụ: cấp năm 2009 sẽ ghi l&agrave; &ldquo;09&rdquo;;</p>\r\n\r\n<p>d) Nh&oacute;m 4: t&ecirc;n Tổ chức cấp C/O, gồm 02 k&yacute; tự. Danh s&aacute;ch c&aacute;c Tổ chức cấp C/O được quy định cụ thể tại Phụ lục XIII. Danh s&aacute;ch n&agrave;y được Bộ C&ocirc;ng Thương cập nhật thường xuy&ecirc;n khi c&oacute; sự thay đổi về c&aacute;c Tổ chức cấp C/O;&nbsp; &nbsp;</p>\r\n\r\n<p>đ) Nh&oacute;m 5: số thứ tự của C/O, gồm 05 k&yacute; tự;</p>\r\n\r\n<p>e) Giữa nh&oacute;m 1 v&agrave; nh&oacute;m 2 c&oacute; gạch ngang &ldquo;-&rdquo;. Giữa nh&oacute;m 3, nh&oacute;m 4 v&agrave; nh&oacute;m 5 c&oacute; dấu gạch ch&eacute;o &ldquo;/&rdquo;.</p>\r\n\r\n<p>V&iacute; dụ: Ph&ograve;ng Quản l&yacute; Xuất nhập khẩu khu vực th&agrave;nh phố Hồ Ch&iacute; Minh cấp C/O mang số thứ 6 cho một l&ocirc; h&agrave;ng xuất khẩu sang Th&aacute;i Lan trong năm 2009 th&igrave; c&aacute;ch ghi số tham chiếu của C/O n&agrave;y sẽ l&agrave;: VN-TH 09/02/00006.</p>\r\n\r\n<p>4. &Ocirc; số 3: ng&agrave;y khởi h&agrave;nh, t&ecirc;n phương tiện vận tải (nếu gửi bằng m&aacute;y bay th&igrave; đ&aacute;nh &ldquo;By air&rdquo;, nếu gửi bằng đường biển th&igrave; đ&aacute;nh t&ecirc;n t&agrave;u v&agrave; t&ecirc;n cảng &nbsp;dỡ h&agrave;ng).</p>\r\n\r\n<p>5. &Ocirc; số 4: cơ quan Hải quan tại cảng hoặc địa điểm nhập khẩu sẽ đ&aacute;nh dấu &radic; v&agrave;o &ocirc; th&iacute;ch hợp.</p>\r\n\r\n<p>6. &Ocirc; số 5: số thứ tự c&aacute;c mặt h&agrave;ng (nhiều mặt h&agrave;ng ghi tr&ecirc;n 1 C/O, mỗi mặt h&agrave;ng c&oacute; một số thứ tự ri&ecirc;ng).</p>\r\n\r\n<p>7. &Ocirc; số 6: k&yacute; hiệu v&agrave; số hiệu của kiện h&agrave;ng.</p>\r\n\r\n<p>8. &Ocirc; số 7: số kiện h&agrave;ng, loại kiện h&agrave;ng, m&ocirc; tả h&agrave;ng ho&aacute; (bao gồm số lượng v&agrave; m&atilde; HS của nước nhập khẩu).</p>\r\n\r\n<p>9.&nbsp; &Ocirc; số 8: ghi ti&ecirc;u ch&iacute; xuất xứ của h&agrave;ng h&oacute;a:</p>\r\n\r\n<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:624px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:336px\">\r\n			<p><strong>H&agrave;ng h&oacute;a được sản xuất tại nước ghi đầu ti&ecirc;n ở &ocirc; số 11 của C/O:</strong></p>\r\n			</td>\r\n			<td style=\"width:288px\">\r\n			<p><strong>Điền v&agrave;o &ocirc; số 8:</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:336px\">\r\n			<p>a) H&agrave;ng ho&aacute; c&oacute; xuất xứ thuần t&uacute;y hoặc được sản xuất to&agrave;n bộ tại nước xuất khẩu theo Điều 3 của Phụ lục I&nbsp;&nbsp;&nbsp;</p>\r\n			</td>\r\n			<td style=\"width:288px\">\r\n			<p>&ldquo;WO&rdquo;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:336px\">\r\n			<p>b) H&agrave;ng ho&aacute; c&oacute; xuất xứ kh&ocirc;ng thuần t&uacute;y theo Điều 4 của Phụ lục I</p>\r\n			</td>\r\n			<td style=\"width:288px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:336px\">\r\n			<p>- H&agrave;m lượng gi&aacute; trị khu vực</p>\r\n			</td>\r\n			<td style=\"width:288px\">\r\n			<p>Ghi h&agrave;m lượng thực tế, v&iacute; dụ &ldquo;40%&rdquo;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:336px\">\r\n			<p>- Thay đổi m&atilde; số h&agrave;ng h&oacute;a</p>\r\n			</td>\r\n			<td style=\"width:288px\">\r\n			<p>Ghi ti&ecirc;u ch&iacute; cụ thể, v&iacute; dụ &ldquo;CC&rdquo; hoặc &ldquo;CTH&rdquo; hoặc &ldquo;CTSH&rdquo;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:336px\">\r\n			<p>- C&ocirc;ng đoạn gia c&ocirc;ng chế biến cụ thể</p>\r\n			</td>\r\n			<td style=\"width:288px\">\r\n			<p>&ldquo;SP&rdquo;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:336px\">\r\n			<p>- Ti&ecirc;u ch&iacute; kết hợp</p>\r\n			</td>\r\n			<td style=\"width:288px\">\r\n			<p>Ghi ti&ecirc;u ch&iacute; kết hợp cụ thể, v&iacute; dụ: &ldquo;CTSH + 35%&rdquo;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:336px\">\r\n			<p>c) H&agrave;ng h&oacute;a đ&aacute;p ứng khoản 2 Điều 6 của Phụ lục I (cộng gộp từng phần)</p>\r\n			</td>\r\n			<td style=\"width:288px\">\r\n			<p>&ldquo;PC x%&rdquo; trong đ&oacute; &ldquo;x&rdquo; l&agrave; tỉ lệ phần trăm của h&agrave;m lượng gi&aacute; trị khu vực lớn hơn 20% nhưng nhỏ hơn 40%, v&iacute; dụ &ldquo;PC 25%&rdquo;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>10. &Ocirc; số 9: trọng lượng cả b&igrave; của h&agrave;ng ho&aacute; (hoặc số lượng kh&aacute;c) v&agrave; trị gi&aacute; FOB trong trường hợp sử dụng ti&ecirc;u ch&iacute; RVC để x&aacute;c định xuất xứ h&agrave;ng ho&aacute;.</p>\r\n\r\n<p>11. &Ocirc; số 10: số v&agrave; ng&agrave;y của ho&aacute; đơn thương mại.</p>\r\n\r\n<p>12. &Ocirc; số 11:&nbsp;</p>\r\n\r\n<p>- D&ograve;ng thứ nhất ghi chữ &ldquo;VIET NAM&rdquo;.</p>\r\n\r\n<p>- D&ograve;ng thứ hai ghi đầy đủ t&ecirc;n nước nhập khẩu bằng chữ in hoa.</p>\r\n\r\n<p>- D&ograve;ng thứ ba ghi địa điểm, ng&agrave;y th&aacute;ng năm đề nghị cấp C/O, chữ k&yacute; của người đề nghị cấp C/O.</p>\r\n\r\n<p>13. &Ocirc; số 12: d&agrave;nh cho c&aacute;n bộ Tổ chức cấp C/O ghi: ng&agrave;y th&aacute;ng năm cấp C/O, chữ k&yacute; của c&aacute;n bộ cấp C/O, con dấu của Tổ chức cấp C/O.</p>\r\n\r\n<p>14. &Ocirc; số 13:</p>\r\n\r\n<p>- Đ&aacute;nh dấu &radic; v&agrave;o &ocirc; &ldquo;Third Country Invoicing&rdquo; trong trường hợp h&oacute;a đơn thương mại được ph&aacute;t h&agrave;nh bởi một c&ocirc;ng ty c&oacute; trụ sở tại một nước thứ ba kh&ocirc;ng phải l&agrave; nước th&agrave;nh vi&ecirc;n, hoặc bởi một c&ocirc;ng ty c&oacute; trụ sở tại một nước ASEAN đối với l&ocirc; h&agrave;ng của c&ocirc;ng ty được chỉ định giao h&agrave;ng. C&aacute;c th&ocirc;ng tin như t&ecirc;n v&agrave; nước của c&ocirc;ng ty ph&aacute;t h&agrave;nh h&oacute;a đơn n&ecirc;u tr&ecirc;n cần ghi v&agrave;o &ocirc; số 7. &nbsp;</p>\r\n\r\n<p>- Đ&aacute;nh dấu &radic; v&agrave;o &ocirc; &ldquo;Back-to-Back CO&rdquo; trong trường hợp tổ chức cấp C/O của nước trung gian cấp C/O gi&aacute;p lưng theo Điều 11 của Phụ lục VII.</p>\r\n\r\n<p>- Đ&aacute;nh dấu &radic; v&agrave;o &ocirc; &ldquo;Exhibitions&rdquo; trong trường hợp h&agrave;ng h&oacute;a gửi từ nước th&agrave;nh vi&ecirc;n xuất khẩu để tham gia triển l&atilde;m tại một nước kh&aacute;c v&agrave; được b&aacute;n trong qu&aacute; tr&igrave;nh hoặc sau triển l&atilde;m để nhập khẩu v&agrave;o một nước th&agrave;nh vi&ecirc;n theo Điều 22 của Phụ lục VII, đồng thời ghi t&ecirc;n v&agrave; địa chỉ của nơi triển l&atilde;m v&agrave;o &ocirc; số 2.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Đ&aacute;nh dấu &radic; v&agrave;o &ocirc; &ldquo;Issued Retroactively&rdquo; trong trường hợp cấp C/O&nbsp; được cấp sau do sai s&oacute;t hoặc v&igrave; l&yacute; do ch&iacute;nh đ&aacute;ng kh&aacute;c theo khoản 2 Điều 10 của Phụ lục VII.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Đ&aacute;nh dấu &radic; v&agrave;o &ocirc; &ldquo;Accumulation&rdquo; trong trường hợp h&agrave;ng ho&aacute; c&oacute; xuất xứ của một nước th&agrave;nh vi&ecirc;n được sử dụng l&agrave;m nguy&ecirc;n liệu tại l&atilde;nh thổ của một nước th&agrave;nh vi&ecirc;n kh&aacute;c để sản xuất ra một sản phẩm ho&agrave;n chỉnh.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Đ&aacute;nh dấu &radic; v&agrave;o &ocirc; &ldquo;Partial Accumulation&rdquo; trong trường hợp h&agrave;m lượng gi&aacute; trị khu vực của nguy&ecirc;n liệu nhỏ hơn 40% nhưng lớn hơn 20% v&agrave; C/O được cấp nhằm mục đ&iacute;ch cộng gộp theo khoản 2 Điều 6 của Phụ lục I.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Đ&aacute;nh dấu &radic; v&agrave;o &ocirc; &ldquo;De Minimis&rdquo; nếu h&agrave;ng h&oacute;a kh&ocirc;ng thoả m&atilde;n ti&ecirc;u ch&iacute; chuyển đổi m&atilde; số h&agrave;ng h&oacute;a v&igrave; l&yacute; do c&oacute; một số nguy&ecirc;n liệu c&oacute; m&atilde; số HS tr&ugrave;ng với m&atilde; số HS của sản phẩm nhưng tỉ lệ tr&ugrave;ng n&agrave;y kh&ocirc;ng vượt qu&aacute; 10% gi&aacute; trị FOB của sản phẩm theo như quy định tại Điều 9 của Phụ lục I.</p>\r\n\r\n<p>15. C&aacute;c hướng dẫn kh&aacute;c:</p>\r\n\r\n<p>- Trong trường hợp c&oacute; nhiều mặt h&agrave;ng khai tr&ecirc;n c&ugrave;ng một C/O, nếu mặt h&agrave;ng n&agrave;o kh&ocirc;ng được hưởng ưu đ&atilde;i thuế quan, cơ quan Hải quan đ&aacute;nh dấu th&iacute;ch hợp v&agrave;o &ocirc; số 4 v&agrave; mặt h&agrave;ng đ&oacute; cần được khoanh tr&ograve;n hoặc đ&aacute;nh dấu th&iacute;ch hợp tại &ocirc; số 5.</p>\r\n\r\n<p>- &Ocirc; số 13 c&oacute; thể được đ&aacute;nh dấu &radic; bằng tay hoặc in bằng m&aacute;y vi t&iacute;nh./.</p>\r\n\r\n<p>Nguồn:&nbsp;<em>Phụ lục IX Th&ocirc;ng tư số 22/2016/TT-BCT ng&agrave;y 03/10/2016.</em></p>','/d2/25/5d/5a98b3cc54f07.jpg',NULL,11714,0,'2018-03-02 00:00:00',NULL,'C/O phải được khai bằng tiếng Anh và đánh máy (trừ trường hợp hướng dẫn ở khoản 15 dưới đây). Nội dung khai phải phù hợp với các chứng từ quy định tại Điều 2 của Thông tư này. Nội dung kê khai C/O cụ thể như sau:','hướng dẫn khai CO form D chi tiết, cách khai các ô trên CO form D, Phụ lục IX Thông tư số 22/2016/TT-BCT ngày 03/10/2016.','','',2,0,'vi','huong-dan-ke-khai-c-o-form-d',9,27,26,'2018-03-02 09:15:40','2018-03-03 21:29:36'),(270,'C/O FORM GSTP LÀ GÌ?','','Quy định xuất xứ GSTP (hệ thống ưu đãi thương mại toàn cầu) : áp dụng cho hàng xuất khẩu tới các nước thuộc nhóm G77 (sử dụng form GSTP)','<p><strong>Quy định xuất xứ GSTP</strong>&nbsp;(Global System of Trade Preferences: hệ thống ưu đ&atilde;i thương mại to&agrave;n cầu) : &aacute;p dụng cho h&agrave;ng xuất khẩu tới&nbsp;<a href=\"file:///H:/WEB_VNEIC/Vneic_co/GSTP-%20Countries.htm\">c&aacute;c nước thuộc nh&oacute;m G77</a>&nbsp;(sử dụng form GSTP). &ETH;iều kiện:<br />\r\n-&nbsp;Phải thuộc danh mục h&agrave;ng được hưởng GSTP của nước nhập.<br />\r\n-&nbsp;Phải đ&aacute;p ứng quy định xuất xứ GSTP.<br />\r\n-&nbsp;Phải đ&aacute;p ứng quy định về vận chuyển.<br />\r\nH&agrave;ng xuất khẩu được coi l&agrave; đ&aacute;p ứng quy định xuất xứ GSTP nếu l&agrave;:&nbsp;<br />\r\n1. Sản phẩm thuần t&uacute;y Việt Nam; hoặc l&agrave;<br />\r\n2. Sản phẩm kh&ocirc;ng thuần t&uacute;y Việt Nam, nhưng&nbsp;<br />\r\n2.1. &ETH;ược sản xuất tại Việt Nam với tổng trị gi&aacute; nguy&ecirc;n liệu kh&ocirc;ng c&oacute; xuất xứ Việt Nam chiếm kh&ocirc;ng qu&aacute; 50% trị gi&aacute; FOB sản phẩm; hoặc&nbsp;<br />\r\n2.2. &ETH;ược sản xuất tại Việt Nam với tổng trị gi&aacute; h&agrave;m lượng gốc Việt Nam v&agrave; c&aacute;c nước GSTP kh&aacute;c chiếm kh&ocirc;ng &iacute;t hơn 60% trị gi&aacute; FOB sản phẩm.<br />\r\n(&ETH;ối với c&aacute;c nước chậm ph&aacute;t triển tỷ lệ % quy định tại 2.1 v&agrave; 2.2 tương ứng l&agrave; 60% v&agrave; 50%).</p>\r\n\r\n<p>Nguồn: Internet</p>','/e5/e7/7e/5a98cdc3ac7f3.jpg',NULL,1161,0,'2018-03-02 00:00:00',NULL,'Quy định xuất xứ GSTP (Global System of Trade Preferences: hệ thống ưu đãi thương mại toàn cầu) : áp dụng cho hàng xuất khẩu tới các nước thuộc nhóm G77 (sử dụng form GSTP). Ðiều kiện:\r\n- Phải thuộc danh mục hàng được hưởng GSTP của nước nhập.\r\n- Phải đáp','C/O form GSTP, GSTP là gì, Global System of Trade Preferences','','',2,0,'vi','c-o-form-gstp-la-gi',9,27,26,'2018-03-02 11:06:27','2018-03-03 21:30:20'),(271,'CÁC BƯỚC KIỂM TRA THÔNG TIN TRÊN C/O','','Người nhập khẩu cần hết sức chú ý trong quá trình kiểm tra thông tin C/O ngay khi nhận được bản nháp. Thông thường, người xuất khẩu trước khi phát hành C/O gốc sẽ gửi bản nháp cho người nhập khẩu kiểm tra thông tin, dưới đây là các bước kiểm tra và các thông tin người nhập khẩu cần lưu ý.','<p>Người nhập khẩu cần hết sức ch&uacute; &yacute; trong qu&aacute; tr&igrave;nh kiểm tra th&ocirc;ng tin C/O ngay khi nhận được bản nh&aacute;p. Th&ocirc;ng thường, người xuất khẩu trước khi ph&aacute;t h&agrave;nh C/O gốc sẽ gửi bản nh&aacute;p cho người nhập khẩu kiểm tra th&ocirc;ng tin, dưới đ&acirc;y l&agrave; c&aacute;c bước kiểm tra v&agrave; c&aacute;c th&ocirc;ng tin người nhập khẩu cần lưu &yacute;.</p>\r\n\r\n<p><strong>Gồm 3 h&igrave;nh thức kiểm tra ch&iacute;nh:<br />\r\n- Kiểm tra theo h&igrave;nh thức b&ecirc;n ngo&agrave;i của C/O.<br />\r\n- Kiểm tra theo nội dung của C/O.<br />\r\n- Kiểm tra theo loại v&agrave; thời gian cấp C/O.</strong><br />\r\n<br />\r\n<strong>1.&nbsp;KIỂM TRA THEO H&Igrave;NH THỨC B&Ecirc;N NGO&Agrave;I CỦA C/O:</strong><br />\r\n<br />\r\n- Tr&ecirc;n C/O phải thể hiện d&ograve;ng chữ FORM D/ FORM E/ FORM S/ FORM AK/ FORM AJ, ...<br />\r\n<br />\r\n- Số tham chiếu: mỗi C/O c&oacute; một số tham chiếu ri&ecirc;ng.<br />\r\n<br />\r\n- C&aacute;c ti&ecirc;u ch&iacute; tr&ecirc;n C/O phải được điền đầy đủ theo quy định.<br />\r\n<br />\r\n- K&iacute;ch thước, m&agrave;u sắc, ng&ocirc;n ngữ v&agrave; mặt sau của C/O phải theo đ&uacute;ng quy định của c&aacute;c Hiệp định v&agrave; c&aacute;c văn bản ph&aacute;p luật c&oacute; li&ecirc;n quan.<br />\r\n<br />\r\n<strong>2.&nbsp;KIỂM TRA THEO NỘI DUNG CỦA C/O:&nbsp;</strong><br />\r\n<br />\r\n<strong>- Kiểm tra đối chiếu dấu v&agrave;/hoặc chữ k&yacute; tr&ecirc;n C/O</strong>&nbsp;với mẫu dấu, v&agrave;/hoặc chữ k&yacute; của người, v&agrave; cơ quan, tổ chức c&oacute; thẩm quyền cấp C/O đ&atilde; được Tổng cục Hải quan th&ocirc;ng b&aacute;o cho Cục Hải quan tỉnh, th&agrave;nh phố:<br />\r\n+ Thời hạn c&oacute; hiệu lực của chữ k&yacute; của người c&oacute; thẩm quyền cấp C/O.<br />\r\n+ Chữ k&yacute; của người cấp C/O phải thuộc danh s&aacute;ch chữ k&yacute; của Ph&ograve;ng cấp C/O đ&atilde; được Tổng cục Hải quan th&ocirc;ng b&aacute;o (kh&ocirc;ng chấp nhận trường hợp người k&yacute; tr&ecirc;n C/O thuộc Ph&ograve;ng cấp n&agrave;y nhưng con dấu của Ph&ograve;ng cấp kh&aacute;c).<br />\r\n<br />\r\n<strong>- Kiểm tra thời hạn hiệu lực của C/O.&nbsp;</strong><br />\r\n<br />\r\n<strong>- Kiểm tra sự ph&ugrave; hợp, thống nhất giữa c&aacute;c th&ocirc;ng tin tr&ecirc;n C/O</strong>&nbsp;với nhau v&agrave; giữa nội dung tr&ecirc;n C/O với c&aacute;c chứng từ thuộc hồ sơ hải quan (tờ khai hải quan, h&oacute;a đơn thương mại, chứng từ vận tải, Th&ocirc;ng b&aacute;o kết quả x&aacute;c định trước xuất xứ (nếu c&oacute;)). Trong đ&oacute; lưu &yacute;:<br />\r\n+ Về t&ecirc;n người nhập khẩu: C/O phải thể hiện t&ecirc;n người nhập khẩu ph&ugrave; hợp với t&ecirc;n người nhập khẩu tr&ecirc;n tờ khai hải quan.<br />\r\n+ T&ecirc;n h&agrave;ng: h&agrave;ng h&oacute;a m&ocirc; tả tr&ecirc;n C/O ph&ugrave; hợp với h&agrave;ng h&oacute;a khai b&aacute;o tr&ecirc;n tờ khai hải quan v&agrave; chứng từ kh&aacute;c trong bộ hồ sơ hải quan.<br />\r\n+ Lượng h&agrave;ng: trường hợp lượng h&agrave;ng nhập khẩu thực tế lớn hơn hoặc nhỏ hơn lượng h&agrave;ng tr&ecirc;n C/O th&igrave; thực hiện theo hướng dẫn tại điểm d v&agrave; đ khoản 2 Điều 26 Th&ocirc;ng tư số 38/2015/TT-BTC.<br />\r\n<br />\r\n<em>&quot;d) Trường hợp người khai hải quan nộp chứng từ chứng nhận xuất xứ cấp cho cả l&ocirc; h&agrave;ng nhưng chỉ nhập khẩu một phần của l&ocirc; h&agrave;ng th&igrave; cơ quan hải quan chấp nhận chứng từ chứng nhận xuất xứ đ&oacute; đối với phần h&agrave;ng ho&aacute; thực nhập;<br />\r\nđ) Trường hợp số lượng hoặc trọng lượng thực tế h&agrave;ng nhập khẩu vượt qu&aacute; số lượng hoặc trọng lượng h&agrave;ng ghi tr&ecirc;n chứng từ chứng nhận xuất xứ th&igrave; lượng h&agrave;ng h&oacute;a vượt qu&aacute; kh&ocirc;ng được hưởng ưu đ&atilde;i theo c&aacute;c Điều ước quốc tế m&agrave; Việt Nam l&agrave; th&agrave;nh vi&ecirc;n;&quot;</em><br />\r\n<br />\r\n<em>Lưu &yacute;:</em>&nbsp;Cơ quan hải quan xem x&eacute;t chấp nhận C/O trong trường hợp số kiện h&agrave;ng/ loại kiện h&agrave;ng/số lượng/trọng lượng h&agrave;ng h&oacute;a thể hiện tại &ocirc; kh&aacute;c với &ocirc; quy định tr&ecirc;n C/O (v&iacute; dụ số kiện h&agrave;ng v&agrave; loại kiện h&agrave;ng được ghi tại &ocirc; số 9 thay v&igrave; phải ghi tại &ocirc; số 7) v&agrave; ph&ugrave; hợp với c&aacute;c th&ocirc;ng tin tr&ecirc;n chứng từ kh&aacute;c thuộc bộ hồ sơ hải quan; hoặc trường hợp C/O kh&ocirc;ng thể hiện một trong những th&ocirc;ng tin về số kiện h&agrave;ng/số lượng/trọng lượng h&agrave;ng nhưng cơ quan hải quan x&aacute;c định được số lượng hoặc trọng lượng h&agrave;ng h&oacute;a được hưởng mức thuế suất ưu đ&atilde;i đặc biệt qua việc kiểm tra, đối chiếu C/O với c&aacute;c chứng từ kh&aacute;c trong bộ hồ sơ hải quan.<br />\r\n<br />\r\n<strong>- M&atilde; HS tr&ecirc;n C/O:</strong>&nbsp;trường hợp c&oacute; sự kh&aacute;c biệt về ph&acirc;n loại m&atilde; số HS giữa nước th&agrave;nh vi&ecirc;n xuất khẩu v&agrave; nước th&agrave;nh vi&ecirc;n nhập khẩu nhưng&nbsp;kh&ocirc;ng l&agrave;m thay đổi bản chất xuất xứ&nbsp;h&agrave;ng h&oacute;a th&igrave; xem x&eacute;t chấp nhận.<br />\r\n<br />\r\n<strong>- Trị gi&aacute; tr&ecirc;n C/O:</strong>&nbsp;sự kh&aacute;c biệt về trị gi&aacute; khai b&aacute;o tr&ecirc;n C/O với trị gi&aacute; khai b&aacute;o tr&ecirc;n c&aacute;c chứng từ kh&aacute;c (h&oacute;a đơn thương mại, ...) kh&ocirc;ng ảnh hưởng đến t&iacute;nh hợp lệ của C/O. Trường hợp c&oacute; nghi ngờ về trị gi&aacute; hải quan, c&ocirc;ng chức hải quan đề xuất Chi cục trưởng Chi cục Hải quan tiến h&agrave;nh tham vấn gi&aacute;.<br />\r\n<br />\r\n<strong>- H&oacute;a đơn thương mại:&nbsp;</strong>đối với C/O c&oacute; h&oacute;a đơn thương mại do b&ecirc;n/nước thứ ba ph&aacute;t h&agrave;nh, c&ocirc;ng chức hải quan kiểm tra c&aacute;c th&ocirc;ng tin về b&ecirc;n/nước thứ ba tr&ecirc;n C/O theo quy định tại Hiệp định v&agrave; văn bản ph&aacute;p luật li&ecirc;n quan.<br />\r\n<br />\r\n<strong>- Kiểm tra ti&ecirc;u ch&iacute; xuất xứ tr&ecirc;n C/O:</strong>&nbsp;Kiểm tra c&aacute;ch ghi ti&ecirc;u ch&iacute; xuất xứ của h&agrave;ng h&oacute;a tr&ecirc;n C/O (theo ti&ecirc;u ch&iacute; h&agrave;m lượng gi&aacute; trị gia tăng, hoặc ti&ecirc;u ch&iacute; chuyển đổi m&atilde; số h&agrave;ng h&oacute;a, hoặc ti&ecirc;u ch&iacute; c&ocirc;ng đoạn gia c&ocirc;ng chế biến h&agrave;ng h&oacute;a, ti&ecirc;u ch&iacute; xuất xứ thuần t&uacute;y,...) được quy định tại c&aacute;c Quy tắc xuất xứ thực hiện Hiệp định thương mại tự do do Bộ C&ocirc;ng Thương ban h&agrave;nh v&agrave; phần hướng dẫn ở mặt sau C/O.<br />\r\n<br />\r\n<strong>- Kiểm tra ti&ecirc;u ch&iacute; xuất xứ:</strong>&nbsp;X&aacute;c định quy tắc xuất xứ &aacute;p dụng cho h&agrave;ng h&oacute;a theo m&atilde; HS quy định tại Hiệp định thương mại tự do c&oacute; li&ecirc;n quan hoặc Nghị định số 19/2006/NĐ-CP. - Trường hợp khai b&aacute;o h&agrave;ng h&oacute;a c&oacute; xuất xứ thuần t&uacute;y (WO): c&ocirc;ng chức hải quan kiểm tra khả năng đ&aacute;p ứng ti&ecirc;u ch&iacute; WO của h&agrave;ng h&oacute;a căn cứ v&agrave;o th&ocirc;ng tin thị trường, địa l&yacute;, quy tr&igrave;nh sản xuất h&agrave;ng h&oacute;a,&hellip;, đối chiếu với quy định tại Th&ocirc;ng tư/Quyết định của Bộ C&ocirc;ng Thương về xuất xứ thuần t&uacute;y để x&aacute;c định xuất xứ khai b&aacute;o c&oacute; ph&ugrave; hợp với quy định hay kh&ocirc;ng v&agrave; kh&ocirc;ng phải xem x&eacute;t đến c&aacute;c ti&ecirc;u ch&iacute; xuất xứ quy định tại PSR hay ti&ecirc;u ch&iacute; chung (nếu c&oacute;).<br />\r\n<br />\r\n<em>V&iacute; dụ 1:</em>&nbsp;Mặt h&agrave;ng xăng dầu nhập khẩu từ Singapore c&oacute; C/O mẫu D thể hiện xuất xứ Singapore với ti&ecirc;u ch&iacute; xuất xứ &ldquo;WO&rdquo; do cơ quan c&oacute; thẩm quyền của Singapore cấp: trường hợp n&agrave;y c&oacute; căn cứ để nghi ngờ về ti&ecirc;u ch&iacute; xuất xứ do Singapore kh&ocirc;ng phải l&agrave; nước khai th&aacute;c được dầu mỏ, do đ&oacute; kh&ocirc;ng đ&aacute;p ứng quy định về xuất xứ thuần t&uacute;y quy định tại Điều 3 Phụ lục I Th&ocirc;ng tư số 21/2010/TT-BCT ng&agrave;y 17/5/2010.<br />\r\n<br />\r\n<em>V&iacute; dụ 2:</em>&nbsp;Mặt h&agrave;ng tivi nhập khẩu từ Th&aacute;i Lan c&oacute; C/O mẫu D thể hiện xuất xứ Th&aacute;i Lan với ti&ecirc;u ch&iacute; xuất xứ &ldquo;WO&rdquo; do cơ quan c&oacute; thẩm quyền của Th&aacute;i Lan cấp: trường hợp n&agrave;y c&oacute; căn cứ để nghi ngờ về ti&ecirc;u ch&iacute; xuất xứ do c&aacute;c mặt h&agrave;ng điện tử, điện m&aacute;y thường được lắp r&aacute;p từ c&aacute;c linh kiện nhập khẩu từ nhiều nước kh&aacute;c nhau, do đ&oacute; kh&ocirc;ng đ&aacute;p ứng quy định về xuất xứ thuần t&uacute;y quy định tại Điều 3 Phụ lục I Th&ocirc;ng tư số 21/2010/TT-BCT ng&agrave;y 17/5/2010.<br />\r\n<br />\r\n- Trường hợp khai b&aacute;o h&agrave;ng h&oacute;a được sản xuất tại l&atilde;nh thổ của một nước th&agrave;nh vi&ecirc;n từ c&aacute;c nguy&ecirc;n liệu c&oacute; xuất xứ của một hay nhiều nước th&agrave;nh vi&ecirc;n kh&aacute;c (PE): c&ocirc;ng chức hải quan kh&ocirc;ng phải xem x&eacute;t đến c&aacute;c ti&ecirc;u ch&iacute; xuất xứ quy định tại PSR hay ti&ecirc;u ch&iacute; chung (nếu c&oacute;).<br />\r\n<br />\r\n- H&agrave;ng h&oacute;a c&oacute; xuất xứ kh&ocirc;ng thuần t&uacute;y th&igrave; x&aacute;c định ti&ecirc;u ch&iacute; xuất xứ theo tr&igrave;nh tự:<br />\r\n+ H&agrave;ng h&oacute;a thuộc danh mục PSR th&igrave; x&aacute;c định ti&ecirc;u ch&iacute; xuất xứ theo quy định tại Danh mục n&agrave;y.<br />\r\n+ H&agrave;ng h&oacute;a kh&ocirc;ng thuộc danh mục PSR hoặc Hiệp định kh&ocirc;ng quy định danh mục PSR th&igrave; x&aacute;c định ti&ecirc;u ch&iacute; xuất xứ theo ti&ecirc;u ch&iacute; chung. Kiểm tra th&ocirc;ng tin về h&agrave;nh tr&igrave;nh của l&ocirc; h&agrave;ng ghi tr&ecirc;n C/O để đảm bảo h&agrave;ng h&oacute;a nhập khẩu đ&aacute;p ứng quy tắc vận chuyển trực tiếp theo quy định tại c&aacute;c Th&ocirc;ng tư/Quyết định của Bộ C&ocirc;ng Thương v&agrave; Th&ocirc;ng tư của Bộ T&agrave;i ch&iacute;nh để thực hiện c&aacute;c Hiệp định thương mại tự do. Trường hợp h&agrave;ng h&oacute;a qu&aacute; cảnh qua một nước/l&atilde;nh thổ thứ ba kh&ocirc;ng phải l&agrave; th&agrave;nh vi&ecirc;n Hiệp định (ri&ecirc;ng đối với C/O mẫu AK kể cả trường hợp qu&aacute; cảnh qua một nước l&agrave; th&agrave;nh vi&ecirc;n), c&ocirc;ng chức hải quan y&ecirc;u cầu người khai hải quan nộp một trong c&aacute;c chứng từ chứng minh h&agrave;ng h&oacute;a được giữ nguy&ecirc;n trạng theo quy định của Hiệp định dưới đ&acirc;y:<br />\r\n<br />\r\n- Chứng từ do cơ quan hải quan nước qu&aacute; cảnh x&aacute;c nhận về việc h&agrave;ng h&oacute;a nằm dưới sự gi&aacute;m s&aacute;t của cơ quan hải quan nước đ&oacute; v&agrave; chưa được l&agrave;m thủ tục để th&ocirc;ng quan.<br />\r\n<br />\r\n- Giấy x&aacute;c nhận của ch&iacute;nh cơ quan, tổ chức ph&aacute;t h&agrave;nh vận đơn cho l&ocirc; h&agrave;ng xuất nhập khẩu chứng minh h&agrave;ng h&oacute;a qu&aacute; cảnh l&agrave;&nbsp;<em>cần thiết v&igrave; l&yacute; do địa l&iacute; hoặc do c&aacute;c y&ecirc;u cầu c&oacute; li&ecirc;n quan trực tiếp đến vận tải; H&agrave;ng h&oacute;a kh&ocirc;ng tham gia v&agrave;o giao dịch thương mại hoặc ti&ecirc;u thụ tại nước qu&aacute; cảnh đ&oacute;; H&agrave;ng h&oacute;a kh&ocirc;ng trải qua bất kỳ c&ocirc;ng đoạn n&agrave;o kh&aacute;c ngo&agrave;i việc dỡ h&agrave;ng v&agrave; bốc lại h&agrave;ng hoặc những c&ocirc;ng việc cần thiết để bảo quản h&agrave;ng h&oacute;a trong điều kiện tốt</em>. Đối với giấy x&aacute;c nhận ph&aacute;t h&agrave;nh bởi c&ocirc;ng ty con, chi nh&aacute;nh hay đại l&yacute; của c&ocirc;ng ty vận tải, tổ chức ph&aacute;t h&agrave;nh vận đơn cho l&ocirc; h&agrave;ng tại Việt Nam th&igrave; cần th&ecirc;m giấy ủy quyền của ch&iacute;nh h&atilde;ng vận tải.<br />\r\n<br />\r\n- Trong trường hợp h&agrave;ng h&oacute;a được vận chuyển nguy&ecirc;n container, số container, số ch&igrave; kh&ocirc;ng thay đổi từ khi xếp h&agrave;ng l&ecirc;n phương tiện vận tải tại cảng xếp h&agrave;ng của nước th&agrave;nh vi&ecirc;n xuất khẩu đến khi nhập khẩu v&agrave;o Việt Nam th&igrave; xem x&eacute;t chấp nhận chứng từ chứng minh vận tải trực tiếp l&agrave; vận đơn v&agrave; lệnh giao h&agrave;ng của h&atilde;ng t&agrave;u, trong đ&oacute; thể hiện số container, số ch&igrave; kh&ocirc;ng thay đổi.<br />\r\n<br />\r\n<strong>3.&nbsp;KIỂM TRA THEO LOẠI V&Agrave; THỜI GIAN CẤP C/O<br />\r\n<br />\r\n- Đối với C/O cấp sau:</strong><br />\r\n+ Kiểm tra việc ghi d&ograve;ng chữ &ldquo;ISSUED RETROACTIVELY/ ISSUED RETROSPECTIVELY&rdquo; tr&ecirc;n C/O hoặc việc đ&aacute;nh dấu v&agrave;o &ocirc; th&iacute;ch hợp.<br />\r\n+ Đối chiếu ng&agrave;y xuất khẩu tr&ecirc;n vận đơn/c&aacute;c chứng từ vận tải kh&aacute;c với ng&agrave;y cấp C/O để đảm bảo ph&ugrave; hợp với quy định về C/O cấp sau.<br />\r\n<br />\r\nĐối với h&agrave;ng h&oacute;a được vận chuyển theo đường bộ, đường s&ocirc;ng qua bi&ecirc;n giới đất liền giữa Việt Nam với nước th&agrave;nh vi&ecirc;n Hiệp định như Trung Quốc, L&agrave;o, Campuchia, ng&agrave;y xuất khẩu l&agrave; ng&agrave;y giao h&agrave;ng tại cửa khẩu xuất của nước xuất khẩu hoặc ng&agrave;y ph&aacute;t h&agrave;nh chứng từ vận tải (nếu c&oacute;). Trường hợp kh&ocirc;ng c&oacute; căn cứ để x&aacute;c định th&ocirc;ng tin về ng&agrave;y xuất khẩu như tr&ecirc;n th&igrave; ng&agrave;y h&agrave;ng đến cửa khẩu nhập đầu ti&ecirc;n của Việt Nam được coi l&agrave; ng&agrave;y xuất khẩu.<br />\r\n<br />\r\n<strong>- Đối với C/O gi&aacute;p lưng:&nbsp;</strong><br />\r\n+ Kiểm tra, đối chiếu về h&igrave;nh thức v&agrave; nội dung C/O như hướng dẫn n&ecirc;u tr&ecirc;n.<br />\r\n+ Kiểm tra th&ocirc;ng tin C/O gốc được thể hiện tr&ecirc;n C/O gi&aacute;p lưng, thời hạn hiệu lực của CO gi&aacute;p lưng.<br />\r\n<br />\r\n<strong>- Đối với C/O cấp thay thế:</strong><br />\r\nKiểm tra th&ocirc;ng tin x&aacute;c nhận của cơ quan cấp (tr&ecirc;n C/O hoặc theo văn bản th&ocirc;ng b&aacute;o của Tổng cục) về việc C/O được cấp thay thế theo quy định của Hiệp định.<br />\r\n<br />\r\n<strong>- Đối với chứng từ tự chứng nhận xuất xứ:</strong><br />\r\n<br />\r\n+ Kiểm tra h&igrave;nh thức chứng từ tự chứng nhận xuất xứ:<br />\r\nKhai b&aacute;o xuất xứ phải được thể hiện tr&ecirc;n h&oacute;a đơn thương mại hoặc tr&ecirc;n c&aacute;c chứng từ thương mại kh&aacute;c. Đối với chứng từ tự chứng nhận xuất xứ được quy định tại Th&ocirc;ng tư số 28/2015/TT-BCT ng&agrave;y 20/8/2015 của Bộ C&ocirc;ng Thương (Dự &aacute;n th&iacute; điểm số 2 của ASEAN) th&igrave; khai b&aacute;o xuất xứ phải được thể hiện tr&ecirc;n h&oacute;a đơn thương mại. Mẫu khai b&aacute;o quy định tại Điều 7 Th&ocirc;ng tư số 28/2015/TT-BCT.<br />\r\nKiểm tra t&iacute;nh đầy đủ của c&aacute;c th&ocirc;ng tin bắt buộc tr&ecirc;n chứng từ tự chứng nhận xuất xứ theo quy định tại c&aacute;c Hiệp định. Trường hợp tự chứng nhận xuất xứ theo Th&ocirc;ng tư số 28/2015/TT-BCT th&igrave; kiểm tra c&aacute;c th&ocirc;ng tin bắt buộc sau: m&atilde; số tự chứng nhận (m&atilde; số của doanh nghiệp được cấp ph&eacute;p), m&atilde; số HS, nước xuất xứ, ti&ecirc;u ch&iacute; xuất xứ, chữ k&yacute; của người c&oacute; thẩm quyền được tự khai b&aacute;o xuất xứ.<br />\r\n<br />\r\n+ Kiểm tra nội dung chứng từ tự chứng nhận xuất xứ:<br />\r\nKiểm tra, đối chiếu t&ecirc;n thương mại, địa chỉ v&agrave; m&atilde; số tự chứng nhận của doanh nghiệp, t&ecirc;n h&agrave;ng, m&atilde; HS của h&agrave;ng h&oacute;a, chữ k&yacute; v&agrave; thời hạn hiệu lực của giấy ph&eacute;p tự chứng nhận, tr&ecirc;n chứng từ tự chứng nhận xuất xứ với danh s&aacute;ch doanh nghiệp đ&atilde; được Tổng cục Hải quan th&ocirc;ng b&aacute;o.<br />\r\nKiểm tra ti&ecirc;u ch&iacute; xuất xứ v&agrave; c&aacute;c th&ocirc;ng tin kh&aacute;c: c&aacute;ch thức kiểm tra tương tự hướng dẫn tr&ecirc;n.</p>\r\n\r\n<p>Nguồn:&nbsp;vietxnk.com</p>','/a7/33/3a/5a98d7dbc22cf.jpg',NULL,6489,0,'2018-03-02 00:00:00',NULL,'Người nhập khẩu cần hết sức chú ý trong quá trình kiểm tra thông tin C/O ngay khi nhận được bản nháp. Thông thường, người xuất khẩu trước khi phát hành C/O gốc sẽ gửi bản nháp cho người nhập khẩu kiểm tra thông tin, dưới đây là các bước kiểm tra','kiểm tra C/O, Mã HS trên C/O, Trị giá trên C/O, sự phù hợp, thống nhất giữa các thông tin trên C/O','','',2,0,'vi','cac-buoc-kiem-tra-thong-tin-tren-c-o',9,27,26,'2018-03-02 11:49:31','2018-03-03 21:31:27'),(272,'10 CÂU HỎI PHỎNG VẤN HOT DÀNH RIÊNG CHO DÂN XUẤT NHẬP KHẨU- LOGISTCS','','Chắc hẳn không ít lần các bạn hồi hộp khi đi phỏng vấn vị trí Xuất Nhập Khẩu- Logistics? Bạn hồi hộp lo sợ một phần vì bạn thiếu tự tin, không biết bị hỏi những gì, không biết mình đã có đủ những kỹ năng cần thiết hay chưa?\r\n\r\nHãy để Ad giúp các bạn bằng việc hé lộ 10 câu hỏi thực tế thường gặp. Hãy đến với HAN EXIM CLUB để được đào tạo và Huấn luyện thêm về kiến thức và kỹ năng cần thiết trong nghề Xuất nhập khẩu- Logistics nhé.','<h3><span style=\"color:#006400\">Chắc hẳn kh&ocirc;ng &iacute;t lần c&aacute;c bạn hồi hộp khi đi phỏng vấn vị tr&iacute; Xuất Nhập Khẩu- Logistics? Bạn hồi hộp lo sợ một phần v&igrave; bạn thiếu tự tin, kh&ocirc;ng biết bị hỏi những g&igrave;, kh&ocirc;ng biết m&igrave;nh đ&atilde; c&oacute; đủ những kỹ năng cần thiết hay chưa?</span></h3>\r\n\r\n<h3><span style=\"color:#006400\">H&atilde;y để Ad gi&uacute;p c&aacute;c bạn bằng việc h&eacute; lộ 10 c&acirc;u hỏi thực tế thường gặp. H&atilde;y đến với HAN EXIM CLUB để được đ&agrave;o tạo v&agrave; Huấn luyện th&ecirc;m về kiến thức v&agrave; kỹ năng cần thiết trong nghề Xuất nhập khẩu- Logistics nh&eacute;.</span></h3>\r\n\r\n<h2><span style=\"color:#006400\">10 c&acirc;u hỏi phỏng vấn HOT d&agrave;nh ri&ecirc;ng cho d&acirc;n Xuất nhập khẩu- Logistics</span></h2>\r\n\r\n<ol>\r\n	<li>\r\n	<h3><span style=\"color:#006400\">Em h&atilde;y giới thiệu qua một ch&uacute;t về bản th&acirc;n em. Đ&acirc;u l&agrave; điểm mạnh của em? Em thấy n&oacute; c&oacute; gi&uacute;p g&igrave; cho c&ocirc;ng việc xuất nhập khẩu?</span></h3>\r\n	</li>\r\n	<li>\r\n	<h3><span style=\"color:#006400\">T&ocirc;i c&oacute; một l&ocirc; h&agrave;ng l&agrave; sản phẩm X. Em h&atilde;y n&oacute;i quy tr&igrave;nh để nhập khẩu l&ocirc; h&agrave;ng n&agrave;y từ người b&aacute;n đến nước người mua.</span></h3>\r\n	</li>\r\n	<li>\r\n	<h3><span style=\"color:#006400\">Theo em, trong quy tr&igrave;nh Xuất nhập khẩu th&igrave; bước n&agrave;o l&agrave;&nbsp; quan trọng nhất?</span></h3>\r\n	</li>\r\n	<li>\r\n	<h3><span style=\"color:#006400\">Bộ chứng từ Xuất/ nhập khẩu cần những giấy tờ g&igrave;? Bạn đ&atilde; được tiếp cận thực tế với ch&uacute;ng chưa?</span></h3>\r\n	</li>\r\n	<li>\r\n	<h3><span style=\"color:#006400\">Theo em để l&agrave;m được c&ocirc;ng việc n&agrave;y em cần c&oacute; những kiến thức v&agrave; kỹ năng như thế n&agrave;o?</span></h3>\r\n	</li>\r\n	<li>\r\n	<h3><span style=\"color:#006400\">Kỹ năng tin học văn ph&ograve;ng của em ra sao? H&atilde;y thiết kế một số chứng từ như Commercial Invoice v&agrave; Packing List theo mẫu sau</span></h3>\r\n	</li>\r\n	<li>\r\n	<h3><span style=\"color:#006400\">Doanh nghiệp của em rất th&agrave;nh c&ocirc;ng trong việc ti&ecirc;u thụ c&agrave; ph&ecirc; tại thị trường Việt Nam. Trong chiến lược kinh doanh C&ocirc;ng ty muốn xuất khẩu c&agrave; ph&ecirc; ra thị trường nước ngo&agrave;i như ch&acirc;u &Acirc;u, Trung Đ&ocirc;ng&hellip;Theo em, c&ocirc;ng ty phải l&agrave;m những g&igrave; để xuất khẩu th&agrave;nh c&ocirc;ng sản phẩm n&agrave;y? M&ocirc; tả chi tiết c&aacute;c bước nếu em l&agrave; nh&acirc;n vi&ecirc;n ph&ograve;ng Ngoại thương.</span></h3>\r\n	</li>\r\n	<li>\r\n	<h3><span style=\"color:#006400\">Nếu khi h&agrave;ng về kho, em ph&aacute;t hiện ra l&ocirc; h&agrave;ng bị thiếu số lượng l&uacute;c đ&oacute; em sẽ l&agrave;m g&igrave;?</span></h3>\r\n	</li>\r\n	<li>\r\n	<h3><span style=\"color:#006400\">Với một mặt h&agrave;ng lần đầu em gặp, l&agrave;m thế n&agrave;o để em tra cứu được ch&iacute;nh s&aacute;ch mặt h&agrave;ng v&agrave; thuế suất xuất nhập khẩu của mặt h&agrave;ng đ&oacute;?</span></h3>\r\n	</li>\r\n</ol>\r\n\r\n<h3 style=\"margin-left:0.25in\"><span style=\"color:#006400\">10. Bạn h&atilde;y cho biết c&aacute;c lỗi thường gặp khi Khai hải quan điện tử v&agrave; c&aacute;ch khắc phục</span></h3>\r\n\r\n<h3 style=\"margin-left:0.25in\"><span style=\"color:#006400\">C&ograve;n nữa&hellip;.</span></h3>','/d5/ce/ed/5a98e7a3f099f.jpg',NULL,8051,0,'2018-03-03 21:33:14',NULL,'Chắc hẳn không ít lần các bạn hồi hộp khi đi phỏng vấn vị trí Xuất Nhập Khẩu- Logistics? Bạn hồi hộp lo sợ một phần vì bạn thiếu tự tin, không biết bị hỏi những gì, không biết mình đã có đủ những kỹ năng cần thiết hay chưa? Mình sẽ hé lộ 10 câu hỏi thực t','câu hỏi phỏng vấn xuất nhập khẩu, nghề xuất nhập khẩu, câu hỏi tuyển dụng xuất nhập khẩu- logistics','','',2,0,'vi','10-cau-hoi-phong-van-hot-danh-rieng-cho-dan-xuat-nhap-khau-logistics',5,27,26,'2018-03-02 12:56:52','2018-03-03 21:33:14'),(273,'PHÂN BIỆT CO GIÁP LƯNG VÀ CO BA BÊN','','Khái niệm về C/O giáp lưng và C/O ba bên không còn xa lạ gì với những chuyên gia C/O lâu năm. Nhưng nó vẫn gây nhầm lẫn cho những người mới vào nghề hay không chuyên. Bài viết này chỉ rõ sự khác nhau giữ C/O giáp lưng và C/O ba bên, các bạn nghiên cứu và chia sẻ thêm nhé.','<h2><strong>1. ĐỊNH NGHĨA C/O GI&Aacute;P LƯNG: </strong></h2>\r\n\r\n<p style=\"margin-left:.25in\">Theo định nghĩa tại khoản 3, điều 1 Phụ lục VII Th&ocirc;ng tư số 22/2016/TT-BCT, &ldquo;C/O gi&aacute;p lưng&rdquo; l&agrave; C/O được cấp bởi Nước th&agrave;nh vi&ecirc;n xuất khẩu trung gian dựa tr&ecirc;n C/O của Nước th&agrave;nh vi&ecirc;n xuất khẩu đầu ti&ecirc;n.</p>\r\n\r\n<h2><strong>2. ĐIỀU KIỆN ĐỂ ĐƯỢC CẤP C/O GI&Aacute;P LƯNG</strong></h2>\r\n\r\n<p style=\"margin-left:.25in\">Theo quy định tại Điều 11 Phụ lục VII Th&ocirc;ng tư số 22/2016/TT-BCT về điều kiện để được cấp C/O gi&aacute;p lưng như sau:</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp; Tổ chức cấp C/O của Nước th&agrave;nh vi&ecirc;n trung gian c&oacute; thể cấp C/O gi&aacute;p lưng nếu c&oacute; <strong>đơn đề nghị cấp C/O gi&aacute;p lưng</strong> của Người xuất khẩu, với điều kiện:</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp; 1. Người đề nghị cấp C/O gi&aacute;p lưng xuất tr&igrave;nh <strong>bản gốc của C/O ban đầu <em>c&ograve;n hiệu lực</em></strong>. Trong trường hợp kh&ocirc;ng xuất tr&igrave;nh được bản gốc C/O, Người đề nghị cấp C/O gi&aacute;p lưng phải xuất tr&igrave;nh <strong>bản sao chứng thực</strong> của C/O đ&oacute;.</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp; 2. C/O gi&aacute;p lưng được cấp phải bao gồm <strong>một số th&ocirc;ng tin như tr&ecirc;n C/O gốc</strong>.&nbsp; C&aacute;c &ocirc; trong C/O gi&aacute;p&nbsp; lưng&nbsp; phải&nbsp; được&nbsp; điền&nbsp; đầy&nbsp; đủ.&nbsp; <strong>Gi&aacute; FOB của Nước th&agrave;nh vi&ecirc;n trung gian</strong> tại <strong>&ocirc; số 9 phải được ghi trong C/O gi&aacute;p lưng</strong>.</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp; 3. Đối với c&aacute;c l&ocirc; h&agrave;ng xuất khẩu từng phần, trị gi&aacute; của từng phần xuất khẩu đ&oacute; sẽ được ghi thay cho trị gi&aacute; của cả l&ocirc; h&agrave;ng tr&ecirc;n C/O ban đầu. Khi cấp C/O gi&aacute;p lưng cho Người xuất khẩu, Nước th&agrave;nh vi&ecirc;n trung gian phải đảm bảo <strong>tổng số lượng t&aacute;i xuất</strong> khẩu của c&aacute;c l&ocirc; h&agrave;ng xuất khẩu từng phần <strong>kh&ocirc;ng vượt qu&aacute; số lượng ghi tr&ecirc;n C/O ban đầu</strong> nhập khẩu từ Nước th&agrave;nh vi&ecirc;n đầu ti&ecirc;n.</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp; 4. Trong trường hợp kh&ocirc;ng đầy đủ th&ocirc;ng tin v&agrave;/hoặc nghi ngờ c&oacute; vi phạm, cơ quan Hải quan của Nước th&agrave;nh vi&ecirc;n nhập khẩu cuối c&ugrave;ng c&oacute; thể y&ecirc;u cầu xuất tr&igrave;nh C/O ban đầu.</p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:.25in\"><em>Quy định về C/O gi&aacute;p lưng trong ATIGA c&oacute; nhiều điểm tương đồng với quy định về C/O gi&aacute;p lưng trong AANZFTA v&agrave; AJCEP, đ&oacute; l&agrave; kh&ocirc;ng bắt buộc nh&agrave; xuất khẩu tr&ecirc;n C/O gi&aacute;p lưng v&agrave; nh&agrave; nhập khẩu tr&ecirc;n C/O gốc phải l&agrave; một.</em></p>\r\n\r\n<p style=\"margin-left:.25in\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:.25in\"><strong>V&iacute; dụ về C/O gi&aacute;p lưng:</strong></p>\r\n\r\n<p style=\"margin-left:.25in\">C&ocirc;ng ty nhập khẩu ở Việt Nam k&iacute; hợp đồng mua h&agrave;ng với c&ocirc;ng ty Singapore chi tiết như sau: H&agrave;ng h&oacute;a được sản xuất tại Trung Quốc vận chuyển qua Singapore, h&agrave;ng được vận chuyển từ Cảng Singapore về Việt Nam;</p>\r\n\r\n<ul>\r\n	<li><em>Trường hợp n&agrave;y DN cung cấp C/O <strong>mẫu E</strong> gi&aacute;p lưng hợp lệ do cơ quan c&oacute; thẩm quyền của Singapore ph&aacute;t h&agrave;nh th&igrave; được hưởng thuế suất ưu đ&atilde;i đặc biệt. Tại &ocirc; số 13 tr&ecirc;n C/O, đ&aacute;nh dấu v&agrave;o &ldquo;Back to Back&rdquo; hoặc &ldquo;Movement Certificate&rdquo;</em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:.25in\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/47.%20CO%20GIAP%20LUNG%20VA%20CO%203%20BEN/CO%20BACK%20TO%20BACK%20.jpg\" style=\"height:337px; width:600px\" /></p>\r\n\r\n<h2 style=\"margin-left:0.25in\"><strong>3. ĐIỀU KIỆN ĐỂ ĐƯỢC CẤP C/O CHO H&Oacute;A ĐƠN B&Ecirc;N THỨ BA (C/O BA B&Ecirc;N)</strong></h2>\r\n\r\n<p style=\"margin-left:.25in\">- Giao dịch ph&aacute;t sinh giữa ba b&ecirc;n tham gia đặt trụ sở tại 3 nước</p>\r\n\r\n<p style=\"margin-left:.25in\">- H&oacute;a đơn thương mại được ph&aacute;t h&agrave;nh bởi một c&ocirc;ng ty c&oacute; trụ sở tại một nước thứ ba</p>\r\n\r\n<p style=\"margin-left:.25in\">- Nh&agrave; xuất khẩu v&agrave; người nhận h&agrave;ng phải c&oacute; trụ sở đặt tại c&aacute;c b&ecirc;n tham gia c&ugrave;ng một Hiệp định</p>\r\n\r\n<p style=\"margin-left:.25in\">- Tại &ocirc; số 13 tr&ecirc;n C/O, đ&aacute;nh dấu v&agrave;o &ldquo;Third party invoicing&rdquo; hoặc &ldquo;Third country invoicing&rdquo;</p>\r\n\r\n<p style=\"margin-left:.25in\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/47.%20CO%20GIAP%20LUNG%20VA%20CO%203%20BEN/CO%203%20BEN.jpg\" style=\"height:309px; width:550px\" /></p>\r\n\r\n<p style=\"margin-left:.25in\">V&iacute; dụ:</p>\r\n\r\n<p style=\"margin-left:.25in\">C&ocirc;ng ty nhập khẩu ở Việt Nam k&iacute; hợp đồng mua h&agrave;ng với c&ocirc;ng ty Singapore chi tiết như sau: H&agrave;ng h&oacute;a được sản xuất tại Australia vận chuyển thẳng từ Australia về Việt Nam, h&oacute;a đơn thương mại do c&ocirc;ng ty Singapore ph&aacute;t h&agrave;nh;</p>\r\n\r\n<ul>\r\n	<li><em>Trường hợp n&agrave;y DN cung cấp C/O <strong>mẫu AANZ</strong> hợp lệ do cơ quan c&oacute; thẩm quyền của Singapore ph&aacute;t h&agrave;nh, tại &ocirc; số 13 tr&ecirc;n C/O, đ&aacute;nh dấu v&agrave;o &ldquo;Third party invoicing&rdquo; hoặc &ldquo;Third country invoicing&rdquo;.</em></li>\r\n	<li><em>&Ocirc; số 10 ghi số h&oacute;a đơn thương mại của c&ocirc;ng ty Singapore ph&aacute;t h&agrave;nh</em></li>\r\n</ul>\r\n\r\n<p><em><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/47.%20CO%20GIAP%20LUNG%20VA%20CO%203%20BEN/CO%20FORM%20D-3RD.jpg\" style=\"height:827px; width:600px\" /></em></p>\r\n\r\n<p><em>Mẫu C/O form E ba b&ecirc;n do c&ocirc;ng ty XK ở VN cấp cho người nhập khẩu ở Indonesia, ph&aacute;t h&agrave;nh h&oacute;a đơn cho c&ocirc;ng ty trung gian ở Singapore.</em></p>\r\n\r\n<p>Như vậy, ch&uacute;ng ta c&oacute; thể hiểu đơn giản l&agrave; C/O gi&aacute;p lưng v&agrave; C/O ba b&ecirc;n xuất hiện khi c&oacute; giao dịch giữa &iacute;t nhất ba&nbsp;b&ecirc;n tham gia ở ba quốc gia kh&aacute;c nhau; nếu h&agrave;ng h&oacute;a được giao thẳng từ nước xuất khẩu (sản xuất) tới nước nhập khẩu cuối c&ugrave;ng th&igrave; được cấp&nbsp;C/O ba b&ecirc;n, nếu h&agrave;ng h&oacute;a được vận chuyển sang nước trung gian, sau đ&oacute; mới chuyển về nước nhập khẩu cuối c&ugrave;ng th&igrave; được cấp C/O gi&aacute;p lưng.</p>\r\n\r\n<p>---</p>\r\n\r\n<p>Ms Sarah.</p>','/7a/02/27/5a98ec54d73e4.jpg',NULL,13112,0,'2018-03-02 00:00:00',NULL,'Khái niệm về C/O giáp lưng và C/O ba bên không còn xa lạ gì với những chuyên gia C/O lâu năm. Nhưng nó vẫn gây nhầm lẫn cho những người mới vào nghề hay không chuyên. Bài viết này chỉ rõ sự khác nhau giữ C/O giáp lưng và C/O ba bên, các bạn nghiên cứu và','C/O giáp lưng và C/O ba bên, \"Back to Back\" hoặc \"Movement Certificate\", vào \"Third party invoicing\" hoặc \"Third country invoicing\", hóa đơn bên thứ ba','','',2,0,'vi','phan-biet-co-giap-lung-va-co-ba-ben',9,27,27,'2018-03-02 13:11:45','2018-03-06 09:12:17'),(278,'THỦ TỤC NHẬP KHẨU ĐÁ MUỐI HALIT (ĐÁ MUỐI HIMALAYA)','','Muối Himalaya là tên thương mại của đá muối được sản xuất ở Pakistan, loại muối đã được nhiều công ty ở châu Âu, Bắc Mỹ và Úc kinh doanh vào đầu thế kỷ 21. Bạn có biết doanh số bán đá muối đang tăng nhanh. Nếu giữa năm 2017, trung bình mỗi doanh nghiệp  chỉ nhập p khoảng 20-30 tấn/tháng, thì nay đã nhập tới cả trăm tấn/tháng mới đáp ứng đủ nhu cầu','<p>Quy tr&igrave;nh nhập khẩu đ&aacute; muối Himalaya</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Muối Himalaya&nbsp;l&agrave; t&ecirc;n thương mại của đ&aacute; muối&nbsp;được sản xuất ở Pakistan, loại muối đ&atilde; được nhiều c&ocirc;ng ty ở ch&acirc;u &Acirc;u, Bắc Mỹ v&agrave; &Uacute;c kinh doanh v&agrave;o đầu thế kỷ 21.&nbsp;Bạn c&oacute; biết doanh số b&aacute;n đ&aacute; muối đang tăng nhanh. Nếu giữa năm 2017, trung b&igrave;nh mỗi doanh nghiệp&nbsp; chỉ nhập&nbsp;p khoảng 20-30 tấn/th&aacute;ng, th&igrave; nay đ&atilde; nhập tới cả trăm tấn/th&aacute;ng mới đ&aacute;p ứng đủ nhu cầu.</p>\r\n\r\n<p>Trước kia đ&aacute; muối Himalaya&nbsp;chỉ cung cấp cho c&aacute;c spa l&agrave; ch&iacute;nh, nay c&aacute;c gia đ&igrave;nh mua rất nhiều. C&ocirc;ng dụng của đ&aacute; muối l&agrave;: l&agrave;m đẹp, chăm s&oacute;c sức khỏe, ẩm thực (d&ugrave;ng như muối ăn, tấm đ&aacute; muối để nướng...), trang tr&iacute;, x&acirc;y dựng...</p>\r\n\r\n<p>Thủ tục để nhập khẩu đ&aacute; muối cũng c&oacute; &ldquo;phức tạp&rdquo; hơn so với một số mặt h&agrave;ng kh&aacute;c.</p>\r\n\r\n<p>1. M&atilde; HS: 25010090 hoặc 25010020 (t&ugrave;y loại) c&ograve;n gạch l&agrave;m bằng đ&aacute; muối c&oacute; m&atilde; HS l&agrave; : 68159900</p>\r\n\r\n<p>2. Căn cứ ph&aacute;p l&yacute;</p>\r\n\r\n<p style=\"margin-left:.5in\">+ NĐ 187/2013</p>\r\n\r\n<p style=\"margin-left:.5in\">+ TT 04/2014/BCT</p>\r\n\r\n<p style=\"margin-left:.5in\">+ TT 34/2014/BNNPTNT</p>\r\n\r\n<p style=\"margin-left:.5in\">+ TT 03/2017/BCT</p>\r\n\r\n<p>- L&agrave; mặt h&agrave;ng nhập khẩu chịu hạn ngạch thuế quan (nhập tối đa 102000 tấn/năm). Hạn ngạch thuế quan nhập khẩu muối được ph&acirc;n giao cho thương nh&acirc;n trực tiếp sử dụng l&agrave;m nguy&ecirc;n liệu sản xuất h&oacute;a chất v&agrave; l&agrave;m nguy&ecirc;n liệu sản xuất thuốc, sản phẩm y tế.</p>\r\n\r\n<p>- Chịu sự quản l&yacute; của hai Bộ C&ocirc;ng thương v&agrave; N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n.</p>\r\n\r\n<p>- Nếu nhập từ Pakistan sẽ chịu mức thuế suất tối huệ quốc MFN</p>\r\n\r\n<p>3. Thủ tục nhập khẩu:</p>\r\n\r\n<p style=\"margin-left:.5in\">Bước 1: Xin cấp giấy ph&eacute;p nhập khẩu theo hạn ngạch thuế quan</p>\r\n\r\n<ul>\r\n	<li>Tham khảo thủ tục theo Khoản 5, Điều 5, TT04/2014/BCT</li>\r\n	<li>Địa điểm xin giấy ph&eacute;p hạn ngạch: <em>Bộ C&ocirc;ng thương</em> &ndash; <em>Cục Xuất nhập khẩu</em></li>\r\n</ul>\r\n\r\n<p style=\"margin-left:.5in\">+ Địa chỉ: số 54, Hai B&agrave; Trưng, Ho&agrave;n Kiếm, H&agrave; Nội</p>\r\n\r\n<p style=\"margin-left:.5in\">Bước 2: Đăng k&yacute; kiểm tra chất lượng</p>\r\n\r\n<ul>\r\n	<li>Thủ tục đăng k&iacute; kiểm tra chất lượng tham khảo Điều 8, TT34/2014/BNNPTNT</li>\r\n	<li>Địa điểm nộp hồ sơ đăng k&iacute; v&agrave; tổ chức việc kiểm tra muối nhập khẩu: <em>Chi cục Ph&aacute;t triển N&ocirc;ng th&ocirc;n H&agrave; Nội</em> (Theo Điều 11, TT34/2014/BNNPTNT)</li>\r\n</ul>\r\n\r\n<p style=\"margin-left:.5in\">+ Địa chỉ: số 73, L&ecirc; Hồng Phong, NguyễnTr&atilde;i, H&agrave; Đ&ocirc;ng, H&agrave; Nội</p>\r\n\r\n<p style=\"margin-left:.5in\">Bước 3: Truyền tờ khai Hải quan qua VNACCS</p>\r\n\r\n<p style=\"margin-left:.5in\">Bước 4: Bộ hồ sơ nộp Hải quan gồm c&oacute;:</p>\r\n\r\n<ul>\r\n	<li>H&oacute;a đơn thương mại (Commercial Invoice)</li>\r\n	<li>Phiếu đ&oacute;ng&nbsp; g&oacute;i (Packing List)</li>\r\n	<li>Bill of Lading</li>\r\n	<li>C/O</li>\r\n	<li>Giấy ph&eacute;p nhập khẩu theo hạn ngạch thuế quan</li>\r\n	<li>Giấy th&ocirc;ng b&aacute;o kết quả kiểm tra chuy&ecirc;n ng&agrave;nh</li>\r\n</ul>\r\n\r\n<p>Nguồn cung cấp th&ocirc;ng tin: <a href=\"https://www.facebook.com/tranglovechang.leshim\">Facebook Trang L&ecirc;</a></p>\r\n\r\n<p style=\"margin-left:.5in\">&nbsp;</p>\r\n\r\n<p style=\"margin-left:.5in\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style=\"margin-left:.5in\">&nbsp;</p>','/24/3d/d2/5baa40e62d596.jpg',NULL,0,0,NULL,NULL,'','','','',-1,0,'vi','thu-tuc-nhap-khau-da-muoi-halit-da-muoi-himalaya',9,26,1,'2018-03-07 11:46:00','2018-09-25 21:06:30'),(276,'18 CÁCH QUẢN LÝ THỜI GIAN VÀ LÀM VIỆC THÔNG MINH','','\"Quản lý thời gian\" thực tế chỉ là cụm từ có trong từ điển. Tại sao vậy? Thực tế, mỗi người đều có 24h như nhau một ngày. Thời gian không thể thay đổi được. Vậy điều gì mới thực sự cần thay đổi? Đó là thay đổi chính chúng ta, thay đổi cách chúng ta đang sử dụng thời gian.','<p>Nhiều người trong x&atilde; hội ch&uacute;ng ta đang cố gắng để l&agrave;m việc hiệu quả hơn.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, nhiều người lại cố tỏ ta bận rộn bằng việc lu&ocirc;n chạy theo c&ocirc;ng việc, lu&ocirc;n kiểm tra email, gọi điện thoại, hay l&agrave;m những việc lặt vặt g&igrave; đ&oacute;&hellip; v&igrave; họ cho rằng &ldquo;bận rộn&rdquo; đồng nghĩa với việc bạn đang l&agrave;m việc chăm chỉ v&agrave; sẽ th&agrave;nh c&ocirc;ng hơn.</p>\r\n\r\n<p>Mặc d&ugrave; niềm tin n&agrave;y c&oacute; thể đ&uacute;ng ở một g&oacute;c độ n&agrave;o đ&oacute;, nhưng n&oacute; thường g&acirc;y hiểu sai về &ldquo;năng suất l&agrave;m việc&rdquo; &ndash; l&agrave;m việc li&ecirc;n tục gần như kh&ocirc;ng ngơi nghỉ v&agrave; c&oacute; xu hướng l&atilde;ng ph&iacute; thời gian v&agrave;o c&aacute;c c&ocirc;ng việc v&ocirc; &iacute;ch.</p>\r\n\r\n<p>Thay v&igrave; l&agrave;m việc theo c&aacute;ch n&agrave;y, h&atilde;y chọn c&aacute;ch h&agrave;nh động kh&aacute;c đi.</p>\r\n\r\n<h2>L&Agrave;M VIỆC TH&Ocirc;NG MINH HƠN THAY V&Igrave; LAO LỰC HƠN</h2>\r\n\r\n<p>Thay v&igrave; tiếp nhận nhiệm vụ v&agrave; lao đầu v&agrave;o l&agrave;m như một c&aacute;i m&aacute;y, bạn d&agrave;nh thời gian để &ldquo;m&agrave;i r&igrave;u&rdquo;, h&atilde;y quan s&aacute;t v&agrave; đặt c&acirc;u hỏi xem &ldquo;Liệu c&oacute; c&aacute;ch n&agrave;o gi&uacute;p việc n&agrave;y trở n&ecirc;n hiệu quả hơn so với c&aacute;ch cũ?&rdquo;, &ldquo;C&aacute;i g&igrave; c&oacute; thể loại bỏ để tiết kiệm thời gian, c&ocirc;ng sức hơn?&rdquo;</p>\r\n\r\n<p>Quản l&yacute; thời gian kh&ocirc;ng phải l&agrave; l&agrave;m được c&agrave;ng nhiều việc trong một ng&agrave;y c&agrave;ng tốt. M&agrave; đ&oacute; l&agrave; t&igrave;m c&aacute;ch đơn giản h&oacute;a quy tr&igrave;nh, c&aacute;ch thức để l&agrave;m việc nhanh hơn v&agrave; giảm căng thẳng.</p>\r\n\r\n<p>Quản l&yacute; thời gian gi&uacute;p bạn c&oacute; th&ecirc;m thời gian cho gia đ&igrave;nh, vui chơi, nghỉ ngơi thoải m&aacute;i hơn trong cuộc sống. Bạn sẽ nhận ra, bạn c&oacute; thể mất ch&uacute;t thời gian để sắp xếp lại c&ocirc;ng việc, để t&igrave;m chiến lược xử l&yacute; một nhiệm vụ mới, nhưng chắc chắn bạn sẽ tiết kiệm được rất nhiều thời gian c&ograve;n lại v&agrave; thực sự thỏa m&atilde;n khi ch&iacute;nh bạn l&agrave; người kiểm so&aacute;t c&ocirc;ng việc chứ kh&ocirc;ng để c&ocirc;ng việc kiểm so&aacute;t cuộc sống của bạn.</p>\r\n\r\n<p>Sau đ&acirc;y, t&ocirc;i sẽ giới thiệu với bạn 18 c&aacute;ch để quản l&yacute; thời gian, hy vọng n&oacute; sẽ hữu &iacute;ch cho bạn. Tất nhi&ecirc;n, c&oacute; h&agrave;ng trăm c&aacute;ch kh&aacute;c nhau từ kinh nghiệm của h&agrave;ng vạn người, t&ocirc;i chỉ giới thiệu cho bạn những c&aacute;ch t&ocirc;i đ&atilde; từng v&agrave; tiếp tục &aacute;p dụng th&agrave;nh c&ocirc;ng. Bạn kh&ocirc;ng nhất thiết phải &aacute;p dụng hết, chỉ cần chọn lọc c&aacute;ch ph&ugrave; hợp với m&igrave;nh để thực hiện v&agrave; thay đổi t&ugrave;y th&iacute;ch.</p>\r\n\r\n<h3>1. Ho&agrave;n th&agrave;nh những việc quan trọng nhất trước</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; nguy&ecirc;n tắc v&agrave;ng để quản l&yacute; thời gian. Mỗi ng&agrave;y, bạn h&atilde;y chọn 3 việc l&agrave; quan trọng nhất để ho&agrave;n th&agrave;nh v&agrave; thực hiện ch&uacute;ng đầu ti&ecirc;n.</p>\r\n\r\n<p>Một khi bạn đ&atilde; ho&agrave;n th&agrave;nh 3 việc đ&oacute;, một ng&agrave;y của bạn coi như đ&atilde; th&agrave;nh c&ocirc;ng. Sau đ&oacute; bạn c&oacute; thể chuyển sang l&agrave;m những việc kh&aacute;c, hoặc chuyển việc sang ng&agrave;y h&ocirc;m sau nếu đ&atilde; hết thời gian.</p>\r\n\r\n<h3>2. Học c&aacute;ch n&oacute;i &ldquo;KH&Ocirc;NG&rdquo;</h3>\r\n\r\n<p>Khi bạn nhận lời l&agrave;m một việc g&igrave; đ&oacute;, c&oacute; nghĩa l&agrave; bạn đ&atilde; cam kết d&agrave;nh thời gian v&agrave; mối quan t&acirc;m cho việc đ&oacute;. Cam kết (hứa hẹn) nhiều sẽ dẫn đến việc bạn trở n&ecirc;n qu&aacute; tải v&agrave; kh&ocirc;ng thể tập trung tốt cho c&aacute;c c&ocirc;ng việc quan trọng của bạn.</p>\r\n\r\n<p>Vậy, h&atilde;y chỉ nhận lời những việc bạn thực sự quan t&acirc;m v&agrave; khi bạn c&oacute; thời gian, n&oacute;i KH&Ocirc;NG với những việc &iacute;t quan trọng kh&aacute;c.</p>\r\n\r\n<h3>3. Ngủ đủ 7-8 giờ mỗi ng&agrave;y</h3>\r\n\r\n<p>Một số người nghĩ rằng, hy sinh một v&agrave;i giờ ngủ trong ng&agrave;y để tăng th&ecirc;m quỹ thời gian l&agrave;m việc l&agrave; một c&aacute;ch hay. Tuy nhi&ecirc;n, theo nghi&ecirc;n cứu của c&aacute;c nh&agrave; khoa học, hầu hết mỗi người cần ngủ đủ 7-8 giờ mỗi ng&agrave;y để đảm bảo cơ thể được nghỉ ngơi v&agrave; tr&iacute; &oacute;c được l&agrave;m việc tối ưu. Chỉ c&oacute; bạn mới hiểu cơ thể m&igrave;nh. Nếu bạn c&oacute; thể ngủ &iacute;t hơn m&agrave; vẫn l&agrave;m việc hiệu quả, năng lượng v&agrave; khỏe mạnh th&igrave; điều đ&oacute; thật sự tuyệt vời. Nhưng nếu bạn cố gắng cắt giảm giờ ngủ để trở n&ecirc;n vật vờ ở c&ocirc;ng sở th&igrave; h&atilde;y n&ecirc;n xem lại.</p>\r\n\r\n<h3>4. Tập trung to&agrave;n lực cho nhiệm vụ hiện tại</h3>\r\n\r\n<p>Tắt hết c&aacute;c ứng dụng giải tr&iacute;, ngắt kết nối internet đến c&aacute;c thiết bị giải tr&iacute; v&agrave; c&aacute;c thiết bị khiến bạn mất tập trung. Kh&ocirc;ng h&oacute;ng chuyện, t&ograve; m&ograve; chuyện đồng nghiệp, chỉ trừ khi người ta mời m&igrave;nh v&agrave;o cuộc n&oacute;i chuyện. Nếu bạn l&agrave;m c&ocirc;ng việc tự do, cần sự s&aacute;ng tạo, bạn c&oacute; thể tự tạo cho m&igrave;nh kh&ocirc;ng gian y&ecirc;n tĩnh v&agrave; thư th&aacute;i với một ch&uacute;t &acirc;m nhạc để c&oacute; thể thoải m&aacute;i s&aacute;ng tạo.</p>\r\n\r\n<p>Tập trung v&agrave;o nhiệm vụ hiện tại, mọi vướng bận kh&aacute;c đều n&ecirc;n được bỏ qua, h&atilde;y đắm ch&igrave;m v&agrave;o n&oacute;!</p>\r\n\r\n<h3>5. Bắt đầu ngay!</h3>\r\n\r\n<p>Hầu như tất cả ch&uacute;ng ta đều bị cản trở bởi sự tr&igrave; ho&atilde;n. Ch&uacute;ng ta c&oacute; thể đảm bảo được rằng c&ocirc;ng việc sẽ được ho&agrave;n th&agrave;nh đ&uacute;ng hạn. Vậy n&ecirc;n thường c&oacute; tư tưởng tr&igrave; ho&atilde;n cho đến khi cảm thấy &ldquo;gấp r&uacute;t&rdquo; th&igrave; mới bắt đầu l&agrave;m. Điều đ&oacute; sẽ g&acirc;y th&ecirc;m căng thẳng trong qu&aacute; tr&igrave;nh xử l&yacute; c&ocirc;ng việc. Tại sao ch&uacute;ng ta kh&ocirc;ng bắt đầu ngay để ho&agrave;n th&agrave;nh c&ocirc;ng việc trước hạn, c&oacute; thời gian để kiểm tra lại v&agrave; cảm thấy m&igrave;nh đ&atilde; th&agrave;nh c&ocirc;ng hơn dự kiến so với việc ho&agrave;n th&agrave;nh c&ocirc;ng việc chạm deadline?</p>\r\n\r\n<h3>6. Kh&ocirc;ng cho ph&eacute;p những điều vụn vặt ảnh hưởng đến tiến độ</h3>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh thực thi sẽ c&oacute; những việc nhỏ ph&aacute;t sinh, h&atilde;y nhanh ch&oacute;ng x&aacute;c định chi tiết nhỏ đ&oacute; ảnh hưởng như thế n&agrave;o đến bức tranh lớn? Nếu n&oacute; c&oacute; thể được tạm thời bỏ qua v&agrave; giải quyết sau khi đ&atilde; ho&agrave;n th&agrave;nh việc ch&iacute;nh, th&igrave; h&atilde;y dứt kho&aacute;t bỏ qua v&agrave; tập trung xử l&yacute; việc ch&iacute;nh yếu.</p>\r\n\r\n<h3>7. Biến việc chọn 3 nhiệm vụ ch&iacute;nh mỗi ng&agrave;y th&agrave;nh th&oacute;i quen</h3>\r\n\r\n<p>Mỗi ng&agrave;y trước khi bắt đầu c&ocirc;ng việc bạn đều điểm qua c&aacute;c việc quan trọng m&igrave;nh sẽ l&agrave;m trước trong h&ocirc;m nay, sau đ&oacute; đến c&aacute;c việc ch&iacute;nh yếu. Ghi ra trong một cuốn sổ v&agrave; check (v) v&agrave;o c&aacute;c việc đ&atilde; ho&agrave;n th&agrave;nh. Cuối ng&agrave;y nh&igrave;n một danh s&aacute;ch đ&atilde; được đ&aacute;nh dấu gần hết, chắc chắn bạn sẽ cảm thấy rất hứng khởi. Trước khi rời khỏi văn ph&ograve;ng, h&atilde;y đ&aacute;nh dấu 3 việc quan trọng phải l&agrave;m trong ng&agrave;y h&ocirc;m sau. Nếu bạn cứ l&agrave;m việc đ&oacute; trong &iacute;t nhất 21 ng&agrave;y li&ecirc;n tục, bạn sẽ bắt đầu tại được th&oacute;i quen mới v&agrave; dần dần bạn sẽ c&agrave;ng trở n&ecirc;n nhanh nhẹn hơn, th&ocirc;ng minh v&agrave; linh hoạt hơn trong quản l&yacute; thời gian v&agrave; c&ocirc;ng việc.</p>\r\n\r\n<h3>8. Quan t&acirc;m hơn tới thời gian d&agrave;nh cho TV v&agrave; mạng x&atilde; hội (hay c&aacute;c thiết bị c&ocirc;ng nghệ giải tr&iacute;)</h3>\r\n\r\n<p>Nếu mỗi ng&agrave;y bạn d&agrave;nh qu&aacute; 1 giờ để xem TV (phim ảnh), chơi game v&agrave; ti&ecirc;u tốn v&agrave;o mạng x&atilde; hội th&igrave; bạn thật sự n&ecirc;n xem lại v&agrave; điều chỉnh. Nếu bạn đang bị cuốn theo những d&ograve;ng tin kh&ocirc;ng c&oacute; ảnh hưởng t&iacute;ch cực đến cuộc sống của bạn, hay cảm thấy được &ldquo;l&agrave; ai đ&oacute;&rdquo; tr&ecirc;n mạng XH th&igrave; r&otilde; r&agrave;ng bạn đang để những thứ đ&oacute; điều khiển cuộc sống của bạn. Nếu kh&ocirc;ng thể rời xa, h&atilde;y cai nghiện bằng c&aacute;ch đọc v&agrave; xem những thứ c&oacute; gi&aacute; trị, bổ &iacute;ch hơn, tham gia những hội nh&oacute;m t&iacute;ch cực, nơi những người giỏi giang hay chia sẻ để ch&uacute;ng ta c&oacute; thể học tập được từ họ.</p>\r\n\r\n<h3>9. X&aacute;c định r&otilde; thời gian mục ti&ecirc;u để ho&agrave;n th&agrave;nh mỗi nhiệm vụ</h3>\r\n\r\n<p>Thay v&igrave; ngồi xuống v&agrave; nghĩ &ldquo;m&igrave;nh sẽ l&agrave;m cho đến l&uacute;c xong&rdquo; th&igrave; h&atilde;y thay đổi c&aacute;ch nghĩ &ldquo;m&igrave;nh sẽ l&agrave;m việc n&agrave;y trong 30 ph&uacute;t, 1 giờ, 3 giờ&hellip;&rdquo; H&atilde;y cho bộ n&atilde;o của bạn một deadline để n&oacute; sẽ th&uacute;c đẩy bạn l&agrave;m việc hiệu quả, tập trung v&agrave; th&ocirc;ng minh hơn.</p>\r\n\r\n<h3>10. H&atilde;y giải lao</h3>\r\n\r\n<p>Sau khi ho&agrave;n th&agrave;nh mỗi nhiệm vụ, bạn h&atilde;y d&agrave;nh 5-7 ph&uacute;t nghỉ ngơi để thưởng cho m&igrave;nh. Điều n&agrave;y vừa gi&uacute;p cơ thể bạn thay đổi trạng th&aacute;i, vừa gi&uacute;p bộ n&atilde;o bạn được refresh lại, sạc th&ecirc;m năng lượng bằng c&aacute;ch h&iacute;t thở, uống một t&aacute;ch tr&agrave;, đi lại&hellip; để tiếp tục tỉnh t&aacute;o v&agrave; minh mẫn với c&aacute;c nhiệm vụ tiếp theo.</p>\r\n\r\n<h3>11. Đừng lo lắng qu&aacute; về to&agrave;n bộ danh s&aacute;ch c&ocirc;ng việc v&ocirc; tận của bạn.</h3>\r\n\r\n<p>Sau mỗi nhiệm vụ, đ&aacute;nh dấu check ho&agrave;n th&agrave;nh, v&agrave; đọc lại một lượt để xem tại thời điểm n&agrave;y, việc n&agrave;o sẽ được xếp quan trọng tiếp theo? V&agrave; khi đ&atilde; chọn một việc để l&agrave;m, n&oacute; sẽ l&agrave; nhiệm vụ duy nhất. Nếu đang l&agrave;m việc n&agrave;y m&agrave; đầu &oacute;c c&ograve;n nghĩ đến danh s&aacute;ch c&aacute;c việc kh&aacute;c th&igrave; chỉ l&agrave;m cho nhiệm vụ hiện tại trở n&ecirc;n rối ren v&agrave; giảm hiệu quả hơn th&ocirc;i.</p>\r\n\r\n<h3>12. Tập thể dục v&agrave; ăn uống l&agrave;nh mạnh</h3>\r\n\r\n<p>Nhiều nghi&ecirc;n cứu đ&atilde; cho thấy mối li&ecirc;n quan chặt chẽ giữa một lối sống l&agrave;nh mạnh với năng suất l&agrave;m việc. Tương tự như ngủ đủ giấc, tập thể dục v&agrave; ăn uống l&agrave;nh mạnh, thư gi&atilde;n đầu &oacute;c&hellip; sẽ cho ph&eacute;p bạn tập trung tốt hơn v&agrave;o c&ocirc;ng việc.</p>\r\n\r\n<h3>13. L&agrave;m việc &iacute;t hơn nhưng tạo nhiều gi&aacute; trị hơn</h3>\r\n\r\n<p>H&atilde;y quan s&aacute;t v&agrave; đ&aacute;nh gi&aacute; xem những việc m&igrave;nh l&agrave;m. Bạn c&oacute; đang l&atilde;ng ph&iacute; thời gian v&agrave;o c&aacute;c c&ocirc;ng việc v&ocirc; &iacute;ch? H&atilde;y tập trung những việc tạo ra nhiều gi&aacute; trị hơn, giảm bớt những việc kh&ocirc;ng mang lại gi&aacute; trị.</p>\r\n\r\n<h3>14. Chỉ l&agrave;m việc một ch&uacute;t v&agrave;o cuối tuần</h3>\r\n\r\n<p>Bạn c&oacute; thể d&agrave;nh thời gian một ch&uacute;t v&agrave;o cuối tuần khoảng 2-4 giờ để giải quyết nốt c&aacute;c c&ocirc;ng việc c&ograve;n trong tuần. Sau đ&oacute;, bạn c&oacute; thể d&agrave;nh thời gian ho&agrave;n to&agrave;n cho gia đ&igrave;nh v&agrave; c&aacute;c hoạt động kh&aacute;c.</p>\r\n\r\n<h3>15. Sắp xếp hồ sơ, t&agrave;i liệu</h3>\r\n\r\n<p>Tạo cho m&igrave;nh một quy luật quản l&yacute; hồ sơ, chứng từ, đặc biệt l&agrave; lưu c&aacute;c file mềm trong m&aacute;y. Để việc t&igrave;m kiếm t&agrave;i liệu trở n&ecirc;n dễ d&agrave;ng v&agrave; tiết kiệm thời gian. Tạo quy tắc ph&acirc;n loại email v&agrave;o c&aacute;c thư mục một c&aacute;ch tự động, hủy đăng k&yacute; nhận mail từ c&aacute;c địa chỉ bạn kh&ocirc;ng muốn.</p>\r\n\r\n<h3>16. L&agrave;m việc g&igrave; đ&oacute; trong thời gian chờ đợi</h3>\r\n\r\n<p>Thực ra ch&uacute;ng ta c&oacute; rất nhiều thời gian rảnh trong một ng&agrave;y cho việc chờ đợi, như chờ tại bến xe bus, tại s&acirc;n bay, tr&ecirc;n t&agrave;u điện, trong l&uacute;c tắc đường, xếp h&agrave;ng&hellip; H&atilde;y tận dụng thời gian n&agrave;y để l&agrave;m những việc đơn giản v&agrave; dễ l&agrave;m như đọc s&aacute;ch b&aacute;o, xem c&aacute;c video, nghe audio&hellip;để bạn vẫn c&oacute; thể học ngay cả khi chờ đợi. Ngo&agrave;i ra, việc l&agrave;m n&agrave;y cũng gi&uacute;p bạn ki&ecirc;n nhẫn hơn v&agrave; kh&ocirc;ng trở n&ecirc;n căng thẳng, bực bội trong khi chờ đợi.</p>\r\n\r\n<h3>17. Tự c&aacute;ch ly bản th&acirc;n với b&ecirc;n ngo&agrave;i</h3>\r\n\r\n<p>Đ&ocirc;i khi, bạn c&oacute; thể c&aacute;ch ly bản th&acirc;n ra khỏi c&aacute;c t&aacute;c nh&acirc;n g&acirc;y phiền nhiễu, kh&oacute;a cửa ở trong ph&ograve;ng một m&igrave;nh, kh&ocirc;ng ai ra v&agrave;o, kh&ocirc;ng tiếp kh&aacute;ch. Nếu bạn l&agrave; kiểu người th&iacute;ch sự tập trung cao độ trong kh&ocirc;ng gian ho&agrave;n to&agrave;n y&ecirc;n tĩnh th&igrave; h&atilde;y l&agrave;m như vậy. Nếu kh&ocirc;ng muốn cho mọi người cảm thấy bị bạn phớt lờ, h&atilde;y th&ocirc;ng b&aacute;o cho những người quan trọng để họ biết v&agrave; li&ecirc;n hệ với bạn sau.</p>\r\n\r\n<p>Ngo&agrave;i ra, việc kh&aacute;m ph&aacute; sự tĩnh lặng trong c&ocirc;ng việc cũng khiến cho đầu &oacute;c trở n&ecirc;n s&aacute;ng suốt, minh mẫn hơn, bạn sẽ cảm thấy c&ocirc;ng việc kh&ocirc;ng phải l&agrave; một cuộc đua.</p>\r\n\r\n<h3>18. Chọn l&agrave;m c&aacute;c nh&oacute;m nhiệm vụ c&oacute; li&ecirc;n quan đến nhau</h3>\r\n\r\n<p>Thay v&igrave; ch&uacute;ng ta thực hiện nh&oacute;m ba nhiệm vụ ri&ecirc;ng biệt, h&atilde;y gom c&aacute;c nhiệm vụ c&oacute; mối li&ecirc;n kết với nhau về mặt th&ocirc;ng tin, dữ liệu hay c&aacute;ch thức thực hiện. Để trong qu&aacute; tr&igrave;nh l&agrave;m việc n&agrave;y, ch&uacute;ng ta c&oacute; thể xử l&yacute; lu&ocirc;n c&aacute;c việc li&ecirc;n quan. Thay v&igrave; phải mất thời gian v&agrave; c&ocirc;ng sức để khởi động lại từ đầu.</p>\r\n\r\n<p>Hy vọng những c&aacute;ch tr&ecirc;n đ&acirc;y sẽ gi&uacute;p bạn thay đổi c&aacute;ch quản l&yacute; v&agrave; xử l&yacute; c&ocirc;ng việc của bạn, gi&uacute;p bạn tận hưởng th&agrave;nh quả c&ocirc;ng việc thay v&igrave; cố để l&agrave;m cho xong c&aacute;c đầu việc.</p>\r\n\r\n<p>Bạn thường c&oacute; những c&aacute;ch n&agrave;o để quản l&yacute; c&ocirc;ng việc v&agrave; thời gian hiệu quả? Comment dưới đ&acirc;y để chia sẻ kinh nghiệm của bản th&acirc;n nh&eacute;.</p>\r\n\r\n<p>Ms Sarah.</p>','/71/ee/e7/5a9be203c3c9e.jpg',NULL,1931,0,'2018-03-04 00:00:00',NULL,'\"Quản lý thời gian\" thực tế chỉ là cụm từ có trong từ điển. Tại sao vậy? Thực tế, mỗi người đều có 24h như nhau một ngày. Thời gian không thể thay đổi được. Vậy điều gì mới thực sự cần thay đổi? Đó là thay đổi chính chúng ta, thay đổi cách chúng ta đang s','','','',2,0,'vi','18-cach-quan-ly-thoi-gian-va-lam-viec-thong-minh',5,27,27,'2018-03-04 19:09:39','2018-03-09 22:34:47'),(275,'KỸ NĂNG ĐÀM PHÁN WIN-WIN HAI BÊN CÙNG CÓ LỢI','','Đàm phán trong Xuất Nhập Khẩu là một trong những kỹ năng quan trọng trong tất cả các thương vụ, bạn muốn có một mức giá tốt khi nhập khẩu nhưng nhà Xuất khẩu cũng không hề bị lỗ và chấp nhận bán cho bạn? Bạn muốn có một khách hàng sử dụng dịch vụ logistics của mình mặc dù giá của bạn không phải là tốt nhất?  Bí quyết đó là kỹ năng đàm phán WIN-WIN.','<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Đ&agrave;m ph&aacute;n trong Xuất Nhập Khẩu l&agrave; một trong những kỹ năng quan trọng trong tất cả c&aacute;c thương vụ, bạn muốn c&oacute; một mức gi&aacute; tốt khi nhập khẩu nhưng nh&agrave; Xuất khẩu cũng kh&ocirc;ng hề bị lỗ v&agrave; chấp nhận b&aacute;n cho bạn? Bạn muốn c&oacute; một kh&aacute;ch h&agrave;ng sử dụng dịch vụ logistics của m&igrave;nh mặc d&ugrave; gi&aacute; của bạn kh&ocirc;ng phải l&agrave; tốt nhất? &nbsp;B&iacute; quyết đ&oacute; l&agrave; kỹ năng đ&agrave;m ph&aacute;n WIN-WIN.</span></span></h3>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Ch&uacute;ng ta đang sống trong một xu thế to&agrave;n cầu h&oacute;a m&agrave; th&ocirc;ng tin l&agrave; một điều hết sức cần thiết v&agrave; cần một lưu lượng giao tiếp rất lớn h&agrave;ng ng&agrave;y. Một trong c&aacute;c vấn đề cốt l&otilde;i của c&aacute;c cuộc giao tiếp l&agrave; sự đ&agrave;m ph&aacute;n v&agrave; thương lượng, c&oacute; người n&oacute;i đ&oacute; l&agrave; linh hồn của giao tiếp. Bạn h&atilde;y đặt m&igrave;nh v&agrave;o vị tr&iacute; người đi đ&agrave;m ph&aacute;n v&agrave; thương lượng, bạn lu&ocirc;n lu&ocirc;n phải nghĩ tạo điều kiện cho đối t&aacute;c (hay b&ecirc;n kia) cũng đạt được thắng lợi như bạn chứ kh&ocirc;ng phải l&agrave; đ&agrave;m ph&aacute;n chỉ mang lại thắng lợi cho bạn m&agrave; th&ocirc;i, tức l&agrave; cả hai c&ugrave;ng c&oacute; lợi. </span></span></h3>\r\n\r\n<h2 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><strong><span style=\"font-size:14px\">Tại sao lại đ&agrave;m ph&aacute;n WIN-WIN cả hai b&ecirc;n c&ugrave;ng thắng?</span></strong></span></h2>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:14px\"><span style=\"color:#008000\">Bởi v&igrave; trong thương trường v&agrave; cuộc sống rất &iacute;t c&aacute;c cuộc đ&agrave;m ph&aacute;n v&agrave; thương lượng chỉ xảy ra một lần giữa hai b&ecirc;n l&agrave; xong. Th&ocirc;ng thường ch&uacute;ng ta cần l&agrave;m ăn l&acirc;u d&agrave;i với nhau. Cao hơn tất cả l&agrave; uy t&iacute;n v&agrave; tiếng tăm tốt. Bạn c&oacute; thể d&ugrave;ng c&aacute;c biện ph&aacute;p để thắng đối t&aacute;c một lần với những lợi nhuận to lớn. Nhưng nếu bạn kh&ocirc;ng tạo cho đối t&aacute;c một điều g&igrave; c&oacute; lợi cho họ, d&ugrave; l&agrave; một sự an ủi, th&igrave; bạn sẽ mất rất nhiều, nếu kh&ocirc;ng n&oacute;i l&agrave; sụp đổ sự nghiệp trong tương lai. Trong đ&agrave;m ph&aacute;n, một trong những điều cốt l&otilde;i nhất l&agrave; phải đạt được thỏa thuận về lợi &iacute;ch &ndash; lợi &iacute;ch của cả hai b&ecirc;n. Theo Herb Cohen &ndash;t&aacute;c giả của cuốn s&aacute;ch &ldquo;Bạn c&oacute; thể đ&agrave;m ph&aacute;n bất cứ điều g&igrave;&rdquo; cho rằng, trong cuộc đ&agrave;m ph&aacute;n, c&ugrave;ng với việc thỏa m&atilde;n nhu cầu của hai b&ecirc;n, nghệ thuật win &ndash; win cũng được sử dụng v&agrave;o ba hoạt động: X&acirc;y dựng l&ograve;ng tin, đạt được cam kết v&agrave; giải quyết xung đột. Win-Win (nguy&ecirc;n tắc thắng-thắng) l&agrave; một trong những nguy&ecirc;n tắc cơ bản của </span><a href=\"http://vi.wikipedia.org/w/index.php?title=Ngh%E1%BB%87_thu%E1%BA%ADt_%C4%91%C3%A0m_ph%C3%A1n&amp;action=edit&amp;redlink=1\" title=\"Nghệ thuật đàm phán (trang chưa được viết)\"><span style=\"color:#008000\">nghệ thuật đ&agrave;m ph&aacute;n</span></a><span style=\"color:#008000\">, kinh doanh hiện đại. Theo nguy&ecirc;n tắc n&agrave;y, những người tham gia đ&agrave;m ph&aacute;n, kinh doanh, hợp t&aacute;c với nhau sẽ t&ocirc;n trọng v&agrave; chấp nhận nguy&ecirc;n tắc &quot;đ&ocirc;i b&ecirc;n c&ugrave;ng c&oacute; lợi&quot; (win-win). Nguy&ecirc;n tắc n&agrave;y đảm bảo cho kết quả hợp t&aacute;c bền vững hơn. Nguy&ecirc;n tắc win &ndash; win đưa ra dựa tr&ecirc;n cơ sở l&agrave;m cho đối t&aacute;c hiểu những quyền lợi của họ dưới g&oacute;c nh&igrave;n của m&igrave;nh cũng như thế c&acirc;n bằng trong hợp t&aacute;c giữa hai b&ecirc;n. Điều n&agrave;y đem lại sự hợp t&aacute;c bền l&acirc;u. Cần l&agrave;m cho đối t&aacute;c hiểu những quyền lợi của họ dưới g&oacute;c nh&igrave;n của m&igrave;nh cũng như thế c&acirc;n bằng trong hợp t&aacute;c giữa hai b&ecirc;n.</span></span></h3>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><u><span style=\"font-size:14px\">C&oacute; một case study trong cuốn s&aacute;ch Marketing dựa tr&ecirc;n gi&aacute; trị - Peter Doyle như sau:</span></u></span></h3>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">C&oacute; một c&ocirc;ng ty chuy&ecirc;n sản xuất chỉ may c&ocirc;ng nghiệp. Họ đang đứng trước th&aacute;ch thức với sự cạnh tranh của c&aacute;c đối thủ mới v&agrave;o nghề - những đơn vị n&agrave;y ch&agrave;o gi&aacute; thấp. Họ nhận được y&ecirc;u cầu từ một c&ocirc;ng ty kh&aacute;ch h&agrave;ng lớn l&agrave; phải cắt giảm bớt 20% gi&aacute;. Kh&aacute;ch h&agrave;ng đ&oacute; l&agrave; một nh&agrave; thầu gia c&ocirc;ng c&oacute; trụ sở tại Trung Quốc &ndash; may gia c&ocirc;ng cho hai h&atilde;ng giầy thể thao h&agrave;ng đầu tại Mỹ.</span></span></h3>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Thay v&igrave; phải giảm gi&aacute;, hay chịu mất đi hợp đồng, c&ocirc;ng ty n&agrave;y cử một nh&oacute;m chuy&ecirc;n gia đến ph&acirc;n t&iacute;ch năng suất qu&aacute; tr&igrave;nh hoạt động của kh&aacute;ch h&agrave;ng đ&oacute;. Họ ph&aacute;t hiện ra rằng: chỉ kh&acirc;u chỉ chiếm 2% chi ph&iacute; vận h&agrave;nh nhưng c&aacute;c hoạt động kh&acirc;u chiếm tới &iacute;t nhất 50% tổng chi ph&iacute;. Tức l&agrave; hầu hết nh&acirc;n c&ocirc;ng v&agrave; m&aacute;y m&oacute;c bị h&uacute;t v&agrave;o qu&aacute; tr&igrave;nh kh&acirc;u kh&ocirc;ng hiệu quả. Thu nhập từ kh&aacute;ch h&agrave;ng cho những sản phẩm đầy lỗi n&agrave;y cũng lu&ocirc;n kh&aacute;c nhau, v&igrave; việc thực hiện c&ocirc;ng đoạn kh&acirc;u qu&aacute; k&eacute;m. B&aacute;o c&aacute;o kinh doanh kh&aacute;ch h&agrave;ng được tr&igrave;nh b&agrave;y dưới đ&acirc;y. Nh&oacute;m chuy&ecirc;n gia ph&aacute;t hiện ra rằng vấn đề năng suất lớn l&agrave; những đoạn đứt trong cuộn chỉ trong qu&aacute; tr&igrave;nh kh&acirc;u. Những đoạn đứt đ&oacute; đ&atilde; khiến sản lượng bị mất đi, lao động v&agrave; nguy&ecirc;n liệu bị l&atilde;ng ph&iacute; v&agrave; thời gian m&aacute;y chạy kh&ocirc;ng cao. Nh&oacute;m n&agrave;y cũng ph&aacute;t hiện ra những chỗ đứt trong cuộn chỉ c&oacute; thể được giảm đi đ&aacute;ng kể nhờ bảo dưỡng m&aacute;y tốt hơn, huấn luyện việc vận h&agrave;nh m&aacute;y được cải thiện hơn v&agrave; họ giới thiệu c&aacute;c kiểu kh&acirc;u kh&aacute;c.</span></span></h3>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\">&nbsp;</h3>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\"><u><span style=\"color:#008000\"><span style=\"font-size:14px\">Chi ph&iacute; sản xuất một triệu đ&ocirc;i gi&agrave;y</span></span></u></h3>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" style=\"width:100.0%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Chi ph&iacute;</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">100 (%)</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">10,000 ($000)</span></span></h3>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Nguy&ecirc;n liệu th&ocirc;</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">35</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">3,500</span></span></h3>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Chỉ</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">2</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">200</span></span></h3>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Nh&acirc;n c&ocirc;ng</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">30</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">3,000</span></span></h3>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Thu nhập</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">10</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">1,000</span></span></h3>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Lạm ph&aacute;t</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">7</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">700</span></span></h3>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">C&aacute;c chi ph&iacute; gi&aacute;n tiếp</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">16</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">1,600</span></span></h3>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Lợi nhuận</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">15</span></span></h3>\r\n			</td>\r\n			<td style=\"width:205px\">\r\n			<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">1,500</span></span></h3>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Lợi nhuận kinh doanh của kh&aacute;ch h&agrave;ng l&agrave; 15%. C&ocirc;ng ty cho kh&aacute;ch h&agrave;ng của họ thấy rằng, việc cắt giảm 20% gi&aacute; chỉ may sẽ chỉ giảm bớt 0.4% tổng chi ph&iacute;. Nhưng tổng hợp lại qu&aacute; tr&igrave;nh kh&acirc;u sẽ giảm chi ph&iacute; đến 7%, tức l&agrave; tỉ lệ cải thiện lợi nhuận kinh doanh của c&ocirc;ng ty l&agrave; hơn 40%. Kh&aacute;ch h&agrave;ng ấn tượng với c&aacute;c kiến thức về quy tr&igrave;nh kh&acirc;u của nh&agrave; cung cấp đến mức thương vụ đ&oacute; được đ&agrave;m ph&aacute;n lại. C&ocirc;ng ty đ&oacute; kh&ocirc;ng những kh&ocirc;ng bị &eacute;p hạ gi&aacute; sản phẩm m&agrave; c&ograve;n được chỉ định l&agrave;m &ldquo;đối t&aacute;c tư vấn&rdquo; v&agrave; được trả ph&iacute; v&igrave; đ&atilde; cải thiện được qu&aacute; tr&igrave;nh kh&acirc;u. Thay v&igrave; được trả tiền cho những cuộn chỉ, c&ocirc;ng ty n&agrave;y đ&atilde; được trả tiền cho kiến thức trong việc cải thiện năng suất kh&aacute;ch h&agrave;ng. Đ&oacute; l&agrave; một t&igrave;nh huống c&oacute; lợi cho đ&ocirc;i b&ecirc;n win-win: kh&aacute;ch h&agrave;ng c&oacute; được những lợi &iacute;ch đ&aacute;ng kể trong tăng năng suất. Nh&agrave; cung cấp duy tr&igrave; được kh&aacute;ch h&agrave;ng v&agrave; c&oacute; một mức ti&ecirc;u thụ sản phẩm cao hơn.</span></span></h3>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">Casestudy tr&ecirc;n hy vọng sẽ l&agrave; minh họa đắt gi&aacute; cho những ảnh hưởng t&iacute;ch cực m&agrave; nguy&ecirc;n tắc đ&agrave;m ph&aacute;n WIN- WIN mang lại.</span></span></h3>\r\n\r\n<h3 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">H&atilde;y follow web để cập nhật th&ecirc;m nhiều b&agrave;i viết v&agrave; kỹ năng hay nữa bạn nh&eacute;.</span></span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"color:#008000\"><span style=\"font-size:14px\">&nbsp;</span></span></h3>','/08/56/60/5a98ed52d8f8e.jpg',NULL,3537,0,'2018-03-03 21:34:14',NULL,'Đàm phán trong Xuất Nhập Khẩu là một trong những kỹ năng quan trọng trong tất cả các thương vụ, bạn muốn có một mức giá tốt khi nhập khẩu nhưng nhà Xuất khẩu cũng không hề bị lỗ và chấp nhận bán cho bạn? Bạn muốn có một khách hàng sử dụng dịch vụ logistic','đàm phán win win, nguyên tắc đàm phán có lợi','','',2,0,'vi','ky-nang-dam-phan-win-win-hai-ben-cung-co-loi-wmm8q',5,26,26,'2018-03-02 13:21:06','2018-03-03 21:34:14'),(279,'KỸ NĂNG SALES LOGISTICS CHO LẦN ĐẦU GẶP KHÁCH HÀNG','','Bạn có thường xuyên cảm thấy mất tự tin khi đi gặp 1 khách hàng nhất là các bạn đang làm trong lĩnh vực Sales Logistics. Nếu \"Có\" thì đây là bài viết dành  cho bạn.Nhân viên Sales Logistics là một công việc mà bạn có thể học hỏi được rất nhiều tuy nhiên cũng đầy thách thức.','<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">Bạn c&oacute; thường xuy&ecirc;n cảm thấy mất tự tin khi đi gặp 1 kh&aacute;ch h&agrave;ng nhất l&agrave; c&aacute;c bạn đang l&agrave;m trong lĩnh vực Sales Logistics. Nếu &ldquo;C&oacute;&rdquo; th&igrave; đ&acirc;y l&agrave; b&agrave;i viết d&agrave;nh&nbsp; cho bạn.Nh&acirc;n vi&ecirc;n Sales Logistics l&agrave; một c&ocirc;ng việc m&agrave; bạn c&oacute; thể học hỏi được rất nhiều tuy nhi&ecirc;n cũng đầy th&aacute;ch thức.</p>\r\n\r\n<p style=\"text-align: justify;\">Trước ti&ecirc;n, để v&agrave;o nghề bạn n&ecirc;n t&igrave;m cho m&igrave;nh một c&ocirc;ng ty cung cấp dịch vụ logistics tốt, uy t&iacute;n để từ đ&oacute; bạn dễ d&agrave;ng hơn trong việc sales sau n&agrave;y.</p>\r\n\r\n<p style=\"text-align: justify;\">Về kiến thức v&agrave; kỹ năng: để th&agrave;nh c&ocirc;ng trong lĩnh vực n&agrave;y ngo&agrave;i c&aacute;c kỹ năng của bất kỳ một nh&acirc;n vi&ecirc;n sales n&agrave;o như kỹ năng gọi điện ch&agrave;o h&agrave;ng, b&aacute;n h&agrave;ng, marketing, thuyết tr&igrave;nh trước đ&aacute;m đ&ocirc;n đ&agrave;m ph&aacute;n&hellip; Bạn cần trang bị th&ecirc;m c&aacute;c kiến thức chuy&ecirc;n m&ocirc;n về Giao nhận vận tải, nghiệp vụ ngoại thương, quy tr&igrave;nh xuất nhập khẩu h&agrave;ng h&oacute;a, incoterms... B&ecirc;n cạnh đ&oacute;, lu&ocirc;n cập nhật những th&ocirc;ng tin n&oacute;ng li&ecirc;n quan đến kh&aacute;ch h&agrave;ng, cũng như những th&ocirc;ng tin kh&aacute;ch h&agrave;ng quan t&acirc;m để những cuộc tr&ograve; chuyện kh&ocirc;ng bị nh&agrave;m ch&aacute;n v&agrave; mang lại nhiều th&ocirc;ng tin hữu &iacute;ch d&agrave;nh cho họ.</p>\r\n\r\n<p style=\"text-align: justify;\">Để set up được một cuộc hẹn với kh&aacute;ch h&agrave;ng đ&atilde; kh&oacute; nhưng để c&oacute; thể biến cuộc hẹn gặp đ&oacute; th&agrave;nh cơ hội kinh doanh l&agrave; điều c&ograve;n kh&oacute; hơn.</p>\r\n\r\n<p style=\"text-align: justify;\">Những điều lưu &yacute; trước khi hẹn gặp kh&aacute;ch h&agrave;ng?</p>\r\n\r\n<p style=\"text-align: justify;\">- Gọi điện cho kh&aacute;ch h&agrave;ng:</p>\r\n\r\n<p style=\"text-align: justify;\">Trước khi đến n&ecirc;n gọi điện lại cho kh&aacute;ch h&agrave;ng một lần nữa? V&igrave; sao ư? Kh&aacute;ch h&agrave;ng của bạn cũng đang đi l&agrave;m v&agrave; bộn bề với rất nhiều c&ocirc;ng việc, họ c&oacute; thể qu&ecirc;n lịch hẹn với những&nbsp; cuộc gặp kh&ocirc;ng phải qu&aacute; &ldquo;VIP&rdquo;. Việc gọi điện trước khi đến vừa như để nhắc nhở kh&aacute;ch h&agrave;ng, vừa để confirm v&agrave; chuẩn bị tinh thần cho bạn.</p>\r\n\r\n<p style=\"text-align: justify;\">- Lịch sự v&agrave; chuy&ecirc;n nghiệp trong lần đầu gặp mặt:</p>\r\n\r\n<p style=\"text-align: justify;\">Bao gồm việc ăn mặc lịch sự, cử chỉ đ&agrave;ng ho&agrave;ng v&agrave; c&oacute; th&aacute;i độ b&igrave;nh tĩnh, vững v&agrave;ng, tắt điện thoại hoặc để chế độ im lặng, mang theo sổ tay v&agrave; ghi tốc k&yacute; những g&igrave; trao đổi với kh&aacute;ch h&agrave;ng&hellip;</p>\r\n\r\n<p style=\"text-align: justify;\">- Kh&ocirc;ng n&ecirc;n tr&igrave;nh b&agrave;y d&agrave;i d&ograve;ng về c&ocirc;ng ty m&igrave;nh hoặc những g&igrave; kh&aacute;ch h&agrave;ng đ&atilde; biết:</p>\r\n\r\n<p style=\"text-align: justify;\">Thay v&agrave;o đ&oacute; bạn n&ecirc;n chỉ ra điểm mạnh v&agrave; lợi thế của m&igrave;nh, những g&igrave; kh&aacute;c biệt so với đối thủ cạnh tranh kh&aacute;c tr&ecirc;n thị trường.</p>\r\n\r\n<p style=\"text-align: justify;\">V&iacute; dụ: Dịch vụ automatic b&aacute;o gi&aacute; h&agrave;ng th&aacute;ng cho những tuyến vận tải m&agrave; kh&aacute;ch h&agrave;ng quan t&acirc;m ngay cả khi kh&aacute;ch h&agrave;ng kh&ocirc;ng y&ecirc;u cầu, tư vấn miễn ph&iacute; cho kh&aacute;ch h&agrave;ng 24/07, cho kh&aacute;ch h&agrave;ng c&ocirc;ng nợ d&agrave;i ng&agrave;y&hellip;</p>\r\n\r\n<p style=\"text-align: justify;\">- Lu&ocirc;n chủ động trong c&acirc;u chuyện v&agrave; chuẩn bị trước những g&igrave; sẽ định n&oacute;i.</p>\r\n\r\n<p style=\"text-align: justify;\">H&atilde;y chủ động hỏi kh&aacute;ch h&agrave;ng th&ocirc;ng tin hơn l&agrave; giới thiệu về m&igrave;nh v&agrave; lu&ocirc;n cần chuẩn bị trước &ldquo;c&acirc;u trả lời&rdquo; cho những g&igrave; m&agrave; kh&aacute;ch h&agrave;ng sẽ thắc mắc.</p>\r\n\r\n<p style=\"text-align: justify;\">V&iacute; dụ: Khi kh&aacute;ch h&agrave;ng h&eacute; lộ với bạn họ đang nhập về mặt h&agrave;ng XYZ th&igrave; sau đ&oacute; bạn n&ecirc;n giới thiệu với kh&aacute;ch h&agrave;ng rằng C&ocirc;ng ty m&igrave;nh cũng đang c&oacute; kh&aacute;ch h&agrave;ng nhập mặt h&agrave;ng tương tự v&agrave; l&agrave;m rất OK. Đương nhi&ecirc;n, bạn chỉ c&oacute; thể n&oacute;i được điều n&agrave;y&nbsp; khi bạn đ&atilde; t&igrave;m hiểu trước về kh&aacute;ch h&agrave;ng v&agrave; quay lại xem c&ocirc;ng ty m&igrave;nh đ&atilde; từng l&agrave;m mặt h&agrave;ng đ&oacute; hay chưa? N&ecirc;n nhớ, kh&ocirc;ng kh&aacute;ch h&agrave;ng n&agrave;o muốn l&agrave; &ldquo;chuột bạch&rdquo; cho c&aacute;c c&ocirc;ng ty Logistics/ forwarder n&ecirc;n việc n&ecirc;u t&ecirc;n một số kh&aacute;ch h&agrave;ng lớn bạn đang l&agrave;m việc sẽ l&agrave;m kh&aacute;ch h&agrave;ng y&ecirc;n t&acirc;m hơn.</p>\r\n\r\n<p style=\"text-align: justify;\">- Th&aacute;i độ ch&acirc;n th&agrave;nh, biết lắng nghe v&agrave; t&igrave;m giải ph&aacute;p cho kh&aacute;ch h&agrave;ng</p>\r\n\r\n<p style=\"text-align: justify;\">Th&aacute;i độ ch&acirc;n th&agrave;nh cần thiết trong mọi việc, th&ocirc;ng qua th&aacute;i độ ch&acirc;n th&agrave;nh kh&aacute;ch h&agrave;ng sẽ &ldquo;cảm nhận&rdquo; được bạn v&agrave; tạo l&ograve;ng tin trong mắt kh&aacute;ch h&agrave;ng. Bạn phải thực sự &ldquo;mong muốn&rdquo; l&agrave; người &ldquo;hiến kế&rdquo; hoặc &ldquo;t&igrave;m giải ph&aacute;p&rdquo; cho kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p style=\"text-align: justify;\">V&iacute; dụ: Bạn đang tư vấn dịch vụ Logistics nhưng t&igrave;nh cờ biết kh&aacute;ch h&agrave;ng cần tuyển nh&acirc;n vi&ecirc;n kinh doanh sản phẩm=&gt; Bạn c&oacute; thể b&agrave;y tỏ mong muốn gi&uacute;p đỡ cho kh&aacute;ch h&agrave;ng bằng việc c&oacute; thể giới thiệu v&agrave;i người bạn của m&igrave;nh hiện đang cần t&igrave;m việc.</p>\r\n\r\n<p style=\"text-align: justify;\">- Chuẩn bị sẵn &ldquo;t&acirc;m l&yacute; bị từ chối&rdquo;:</p>\r\n\r\n<p style=\"text-align: justify;\">Bạn h&atilde;y thực sự thoải m&aacute;i khi đi gặp kh&aacute;ch h&agrave;ng, đừng đưa ra mục ti&ecirc;u &ldquo;chốt kh&aacute;ch&rdquo; ngay lần đầu ti&ecirc;n, h&atilde;y coi cuộc gặp mặt n&agrave;y cũng như việc &ldquo;một chuyến kh&aacute;m ph&aacute;&rdquo; của bạn. Nếu l&agrave;m được vậy d&ugrave; bạn l&agrave; một nh&acirc;n vi&ecirc;n b&igrave;nh thường nhưng ngay cả khi gặp mặt kh&aacute;ch h&agrave;ng l&agrave; gi&aacute;m đốc, trưởng ph&ograve;ng&hellip; bạn cũng kh&ocirc;ng hề &ldquo;lo sợ&rdquo; bởi chỉ l&agrave; kh&aacute;m ph&aacute; th&ocirc;i m&agrave;.</p>\r\n\r\n<p style=\"text-align: justify;\">- Kh&ocirc;ng ngừng n&acirc;ng cao kỹ năng v&agrave; kiến thức cho bản than</p>\r\n\r\n<p style=\"text-align: justify;\">Cuối c&ugrave;ng, d&ugrave; trong c&ocirc;ng việc n&agrave;o cũng vậy, bạn n&ecirc;n cố gắng để c&oacute; thể trở th&agrave;nh &ldquo;chuy&ecirc;n gia&rdquo; trong lĩnh vực của m&igrave;nh. Mỗi kh&aacute;ch h&agrave;ng bạn được tiếp x&uacute;c l&agrave; một b&agrave;i học cho bạn, nếu b&acirc;y giờ họ chưa k&yacute; hợp đồng với bạn th&igrave; c&oacute; thể l&agrave; một thời điểm kh&aacute;c trong tương lai.</p>\r\n\r\n<p style=\"text-align: justify;\">Mrs Jade.</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>','/a8/0c/ca/5acd9527029d0.png',NULL,5289,0,'2018-04-11 00:00:00',NULL,'Bạn có thường xuyên cảm thấy mất tự tin khi đi gặp 1 khách hàng nhất là các bạn đang làm trong lĩnh vực Sales Logistics. Nếu \"Có\" thì đây là bài viết dành  cho bạn.Nhân viên Sales Logistics là một công việc mà bạn có thể học hỏi được rất nhiều tuy nhiên c','kỹ năng sales logistics, nhân viên sales logistics làm gì, sales forwarder','','',2,0,'vi','ky-nang-sales-logistics-cho-lan-dau-gap-khach-hang',5,26,26,'2018-04-11 11:55:03','2018-04-11 11:56:29'),(277,'7 CÁCH SỬ DỤNG PHẦN MỀM PDF XCHANGE VIEWER ĐỂ XỬ LÝ CHỨNG TỪ','','x','<p>PDF Xchange Viewer l&agrave; một phần mềm hỗ trợ chỉnh sửa chứng từ dưới định dạng ảnh v&agrave; định dạng pdf rất tối ưu v&agrave; dễ d&agrave;ng sử dụng, thao t&aacute;c đơn giản, giao diện dễ tiếp cận.</p>\r\n\r\n<p>Đầu ti&ecirc;n c&aacute;c bạn cần tải v&agrave; c&agrave;i đặt phần mềm, phần n&agrave;y đơn giản c&aacute;c bạn tự thao t&aacute;c nh&eacute;. Link tải b&ecirc;n dưới. Khi c&agrave;i phần mềm th&igrave; chọn bản miễn ph&iacute; nh&eacute;.</p>\r\n\r\n<p>Để hỗ trợ tốt nhất cho PDF Xchange Viewer, m&aacute;y c&aacute;c bạn cần c&agrave;i một phần mềm chuyển đổi file sang định dạng pdf, gọi tắt l&agrave; in pdf. C&oacute; rất nhiều phần mềm kh&aacute;c nhau như Do pfd, Cute pdf, Phantom, Nitro&hellip;nhưng c&aacute;c PM đ&oacute; đều bắt mua bản quyền, n&ecirc;n t&ocirc;i&nbsp;chọn Foxit Reader&nbsp;n&agrave;y, link tải b&ecirc;n dưới, trong qu&aacute; tr&igrave;nh c&agrave;i đặt, c&aacute;c bạn để &yacute; chọn v&agrave;o chức năng &ldquo;Chọn foxit l&agrave;m m&aacute;y in ảo mặc định&ldquo; nh&eacute;.</p>\r\n\r\n<p>Để thực h&agrave;nh phần mềm n&agrave;y v&agrave; nh&igrave;n thấy hiệu quả, c&aacute;c bạn bắt buộc phải c&oacute; file định dạng pdf để thực h&agrave;nh.</p>\r\n\r\n<p>C&aacute;ch 1: Lấy 1 file word, excel, power point, file văn bản dưới dạng ảnh để tự chuyển sang pdf</p>\r\n\r\n<p>C&aacute;ch 2: Tải file mẫu dưới đ&acirc;y</p>\r\n\r\n<p>Đối với c&aacute;ch l&agrave;m thứ nhất. Ch&uacute;ng ta l&agrave;m như sau</p>\r\n\r\n<p>Mở 1 file word, ấn F12 hoặc chọn lệnh Save As, tại &ocirc; Save as type, c&aacute;c bạn chọn lưu với đu&ocirc;i pdf. Thế l&agrave; ch&uacute;ng ta đ&atilde; c&oacute; 1 file pdf nh&eacute;.</p>\r\n\r\n<p>Nếu bạn n&agrave;o d&ugrave;ng office thấp hơn v&agrave; kh&ocirc;ng c&oacute; chức năng đ&oacute;, th&igrave; l&agrave;m như sau: Ấn tổ hợp ph&iacute;m tắt Ctrl P, chọn m&aacute;y in ở đ&acirc;y: Chọn m&aacute;y in Foxit reader PDF Printer, th&igrave; ch&uacute;ng ta cũng c&oacute; được file pdf như mong muốn.</p>\r\n\r\n<p>Nếu bạn n&agrave;o c&oacute; file ảnh, mở file ảnh ra như b&igrave;nh thường. Sau đ&oacute; cũng d&ugrave;ng lệnh in Ctrl P để in n&oacute; dưới dạng pdf tương tự.</p>\r\n\r\n<p>Vậy l&agrave; mỗi ch&uacute;ng ta đều đ&atilde; c&oacute; c&aacute;c file pdf rồi. Giờ mở giao diện pdf ra như thế n&agrave;y nha. Để hiển thị hết c&aacute;c chức năng cần thiết, c&aacute;c bạn đặt chuột ở thanh c&ocirc;ng cụ, click chuột phải, v&agrave; chọn lần lượt hết c&aacute;c mục hiển thị ở đ&acirc;y nh&aacute;.</p>\r\n\r\n<p>1) X&oacute;a một đoạn nội dung tr&ecirc;n file</p>\r\n\r\n<p>Giả sử tr&ecirc;n hiển thị n&agrave;y ch&uacute;ng ta cần phải x&oacute;a khu vực c&acirc;y sơ đồ tư duy n&agrave;y nh&eacute;. C&aacute;ch l&agrave;m đ&oacute; l&agrave; lấy 1 khoảng trắng bằng với v&ugrave;ng m&igrave;nh muốn x&oacute;a (hay n&oacute;i c&aacute;ch kh&aacute;c l&agrave; che đi v&ugrave;ng đ&oacute;). M&igrave;nh l&agrave;m như sau: C&aacute;c bạn d&ugrave;ng c&ocirc;ng cụ chiếc m&aacute;y ảnh n&agrave;y, ấn giữ chuột tr&aacute;i v&agrave; k&eacute;o để chọn v&ugrave;ng cần x&oacute;a. Sau đ&oacute; nhả chuột, tiếp tục giữ chuột tr&aacute;i để di chuyển v&ugrave;ng chọn đến khu vực nền trắng. Ấn Ctrl C hoặc Click chuột phải, chọn Copy page selection, sau đ&oacute; ấn Ctrl V, c&aacute;c bạn sẽ nh&igrave;n thấy v&ugrave;ng trắng c&oacute; k&iacute;ch thước vừa bằng v&ugrave;ng chọn x&oacute;a tr&ecirc;n, chuyển sang c&ocirc;ng cụ Hand tool h&igrave;nh b&agrave;n tay để di chuyển v&ugrave;ng trắng đ&egrave; l&ecirc;n v&ugrave;ng ch&uacute;ng ta định che đi. Sau đ&oacute; Ctrl S để lưu lại.</p>\r\n\r\n<p>C&aacute;c bạn lưu &yacute;, mọi chỉnh sửa tr&ecirc;n pdf đều dưới dạng sửa&nbsp; tr&ecirc;n bề mặt, c&oacute; nghĩa l&agrave; nếu chỉnh sửa xong m&igrave;nh chỉ lưu lại bằng thao t&aacute;c Save Ctrl S th&igrave; khi chạm chuột v&agrave;o c&aacute;c v&ugrave;ng sửa, vẫn ph&aacute;t hiện ra l&agrave; chứng từ bị sửa. N&ecirc;n sau khi thực hiện chỉnh sửa, c&aacute;c bạn d&ugrave;ng lệnh in pdf để in lại 1 lần nữa ch&iacute;nh file đ&oacute; một lần nữa v&agrave; lưu file mới th&igrave; mới cố định được c&aacute;c phần sửa nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>2) Xem font chữ của đoạn text v&agrave; của to&agrave;n văn bản: đ&acirc;y l&agrave; chức năng gi&uacute;p c&aacute;c bạn khi muốn sửa nội dung của đoạn văn bản, trước hết cần biết font chữ v&agrave; size chữ của đoạn văn bản đ&oacute;. Để kiểm tra chi tiết của trang pdf, đặt chuột ở chế độ Hand tool &ndash; h&igrave;nh b&agrave;n tay tại 1 vị tr&iacute; bất k&igrave; tr&ecirc;n trang, click chuột phải, chọn Document Properties hoặc tổ hợp ph&iacute;m tắt Ctrl D, chọn Fonts, ở đ&acirc;y hiển thị tất cả c&aacute;c font chữ được d&ugrave;ng trong văn bản n&agrave;y, nếu kh&ocirc;ng hiển thị g&igrave; c&oacute; nghĩa l&agrave; file n&agrave;y được scan từ file cứng hoặc pdf 2 lần. Với c&aacute;c file dạng n&agrave;y cũng kh&ocirc;ng cho ph&eacute;p chọn hay b&ocirc;i đen đoạn văn bản, n&oacute; tương tự như h&igrave;nh ảnh.</p>\r\n\r\n<p>Nếu chỉ xem font của 1 đoạn văn bản nhất định, chọn c&ocirc;ng cụ Select tool, b&ocirc;i đen một đoạn văn bản như thế n&agrave;y, click chuột phải, chọn text properties, chọn formatting, thả dấu + n&agrave;y xuống sẽ nh&igrave;n thấy font chữ v&agrave; size chữ của đoạn text vừa rồi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiếp theo, sau khi đ&atilde; biết kiểu chữ v&agrave; cỡ chữ rồi, cho ph&eacute;p ta sửa một đoạn nội dung tr&ecirc;n file ban đầu bằng c&aacute;ch sau:</p>\r\n\r\n<p>3) Sửa một đoạn nội dung tr&ecirc;n file:</p>\r\n\r\n<p>Giả sử, ta cần sửa đoạn văn bản n&agrave;y th&agrave;nh &ldquo;B&aacute;o gi&aacute; tr&ecirc;n chưa bao gồm VAT10%&rdquo;</p>\r\n\r\n<p>Đầu ti&ecirc;n chọn chức năng Typewriter, ấn giữ ph&iacute;m Ctrl v&agrave; lăn bi chuột để ph&oacute;ng to, thu nhỏ để chỉnh sửa chuẩn x&aacute;c hơn, sau đ&oacute; trỏ chuột v&agrave;o vị tr&iacute; cần sửa, g&otilde; đoạn text v&agrave;o. Sau đ&oacute; chỉnh font chữ, size chữ cho khớp với bản gốc. Tuy nhi&ecirc;n c&aacute;c bạn để &yacute;, do file gốc được pdf nhiều lần n&ecirc;n chất lượng giảm, m&agrave;u nhạt đi. Vậy n&ecirc;n ch&uacute;ng ta cần l&agrave;m giảm bớt m&agrave;u chữ cho khớp m&agrave;u với bản gốc bằng c&aacute;ch chọn m&agrave;u chữ ở đ&acirc;y. Ngo&agrave;i ta, hiện tại background mặc định l&agrave; trong suốt n&ecirc;n ta vẫn nh&igrave;n thấy phần text cũ. Click v&agrave;o đ&acirc;y v&agrave; chọn m&agrave;u nền ph&ugrave; hợp với m&agrave;u nền trang giấy v&agrave; che hết to&agrave;n bộ phần đằng sau. Vậy l&agrave; ch&uacute;ng ta vừa ho&agrave;n th&agrave;nh thao t&aacute;c sửa nội dung chứng từ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>4) Th&ecirc;m một h&igrave;nh ảnh từ file kh&aacute;c v&agrave;o</p>\r\n\r\n<p>Trong trường hợp cần th&ecirc;m một số h&igrave;nh ảnh v&agrave;o khoảng trống sẵn c&oacute; tr&ecirc;n file pdf, ta c&oacute; thể lấy h&igrave;nh ảnh từ file pdf kh&aacute;c m&agrave; kh&ocirc;ng insert được từ nguồn ngo&agrave;i như đối với excel, word, powerpoint. C&aacute;ch l&agrave;m như sau:</p>\r\n\r\n<p>Mở cả 2 file pdf, giả sử cần ch&egrave;n logo n&agrave;y v&agrave;o file n&agrave;y. D&ugrave;ng c&ocirc;ng cụ snap shot để chụp h&igrave;nh ảnh ở file n&agrave;y, ấn tổ hợp ph&iacute;m Ctrl C để copy, ấn tiếp tổ hợp ph&iacute;m Ctrl tab để chuyển giữa c&aacute;c tab pdf đang mở. Ấn tổ hợp ph&iacute;m Ctrl V để d&aacute;n h&igrave;nh ảnh vừa copy b&ecirc;n file vừa rồi sang file n&agrave;y, v&agrave; di chuyển n&oacute; đến vị tr&iacute; mong muốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong một số trường hợp y&ecirc;u cầu ch&uacute;ng ta phải</p>\r\n\r\n<p>5) Ch&egrave;n một trang nội dung hoặc trang trắng v&agrave;o file hiện tại, Sarah chỉ cho c&aacute;c bạn c&aacute;ch l&agrave;m như sau:</p>\r\n\r\n<p>Giả sử ta c&oacute; file MSDS n&agrave;y c&oacute; 4 trang v&agrave; m&igrave;nh sẽ ch&egrave;n file 10 cont plywood v&agrave;o giữa trang thứ 3 v&agrave; 4.</p>\r\n\r\n<p>V&agrave;o document, Insert pages hoặc tổ hợp ph&iacute;m tắt Ctrl Shift I sẽ hiện ra 1 cửa sổ. Ta c&oacute; thể ch&egrave;n từ file n&agrave;o đ&oacute; trong 1 folder hoặc ch&egrave;n trực tiếp từ file đang mở sẵn, chỉ cần chọn đ&uacute;ng t&ecirc;n file đang mở, sau đ&oacute; chọn số trang cần ch&egrave;n: chọn tất cả c&aacute;c trang, chọn một số trang kh&ocirc;ng theo thứ tự, hoặc chỉ trang chẵn hoặc chỉ trang lẻ.</p>\r\n\r\n<p>Sau đ&oacute; chọn vị tr&iacute; ch&egrave;n v&agrave;o: ch&egrave;n v&agrave;o đằng trước hay đằng sau của trang đầu ti&ecirc;n, trang cuối c&ugrave;ng hay một trang cụ thể n&agrave;o đ&oacute;.</p>\r\n\r\n<p>Hoặc ch&egrave;n trang trắng bằng c&aacute;ch: V&agrave;o document, Insert empty pages, hiện ra cửa sổ để m&igrave;nh chọn cỡ trang giấy, trang ngang hay trang dọc, số lượng trang trống ch&egrave;n v&agrave;o, ch&egrave;n trước hay sau của trang đầu ti&ecirc;n, trang cuối hay 1 trang cụ thể n&agrave;o đ&oacute;</p>\r\n\r\n<p>Chức năng ch&egrave;n trang cũng cho ph&eacute;p m&igrave;nh nối c&aacute;c file pdf kh&aacute;c nhau v&agrave;o c&ugrave;ng một file bằng c&aacute;ch ch&egrave;n v&agrave;o ph&iacute;a sau của trang cuối c&ugrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, ở đ&acirc;y m&igrave;nh c&ograve;n c&oacute; chức năng</p>\r\n\r\n<p>6) T&aacute;ch trang ra khỏi file ban đầu trong trường hợp m&igrave;nh chỉ cần lấy 1 số trang trong số rất nhiều trang của file pdf hiện tại. V&agrave;o document, chọn Extract pages, chọn đ&uacute;ng trang m&igrave;nh muốn t&aacute;ch, v&igrave; bản miễn ph&iacute; n&ecirc;n m&igrave;nh kh&ocirc;ng sử dụng được 3 options b&ecirc;n dưới. Ấn ok, th&igrave; c&aacute;c trang ta vừa chọn được t&aacute;ch ra th&agrave;nh 1 file mới. Tuy nhi&ecirc;n ch&uacute; &yacute;, kh&ocirc;ng ấn lưu ngay, m&agrave; phải lưu lại bằng thao t&aacute;c in pdf cho file n&agrave;y th&igrave; tr&aacute;nh được dấu bản quyền.</p>\r\n\r\n<p>3 chức năng ở phần options d&agrave;nh cho phi&ecirc;n bản c&oacute; bản quyền</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>7) X&oacute;a một trang ra khỏi file ban đầu</p>\r\n\r\n<p>Để x&oacute;a một hoặc 1 số trang ra khỏi file gốc, ta l&agrave;m tương tự: V&agrave;o document, chọn Delete Pages hoặc ấn tổ hợp ph&iacute;m Ctrl Shift D, sau đ&oacute; chọn trang cần x&oacute;a, chọn trang chẵn, trang lẻ, hoặc x&oacute;a tất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i c&aacute;c thao t&aacute;c cơ bản tr&ecirc;n, th&ecirc;m 1 số c&aacute;ch để tối ưu hiệu suất c&ocirc;ng việc:</p>\r\n\r\n<p>8) Chuyển file từ pdf sang dạng ảnh: trong trường hợp m&igrave;nh cần chuyển trang pdf hiện tại sang dạng h&igrave;nh ảnh, đặt chuột tr&ecirc;n trang, click chuột phải chọn export, export to image, chọn số trang cần chuyển, loại định dạng ảnh, nơi lưu, đặt t&ecirc;n file ảnh ở đ&acirc;y, ta c&oacute; thể thay đổi m&agrave;u nền của file từ m&agrave;u trắng sang c&aacute;c m&agrave;u kh&aacute;c t&ugrave;y chọn ở đ&acirc;y, sau đ&oacute; ấn export.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>9) Xem nguồn lưu file</p>\r\n\r\n<p>C&aacute;ch xem nguồn lưu file khi ta kh&ocirc;ng biết file n&agrave;y được lưu trong folder n&agrave;o. Click chuột phải v&agrave;o t&ecirc;n của file tr&ecirc;n mỗi tab, chọn open containing folder sẽ hiện ra folder m&igrave;nh lưu file đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>10) In nhiều file pdf một lệnh in</p>\r\n\r\n<p>Tiếp theo l&agrave; c&aacute;ch in nhiều file pdf chỉ trong một thao t&aacute;c: V&agrave;o folder chứa tất cả c&aacute;c file đ&oacute;, ấn Ctrl A để chọn to&agrave;n bộ, giữ Ctrl click loại bỏ c&aacute;c file kh&aacute;c định dạng, sau đ&oacute; click chuột phải, chọn print th&igrave; sẽ in được tất cả c&aacute;c file 1 l&uacute;c</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>11) Tạo bookmark</p>\r\n\r\n<p>Nếu như bạn l&agrave; người hay đọc s&aacute;ch ebook m&agrave; muốn tạo mục lục tự động hoặc đ&aacute;nh dấu trang bạn đọc dở, hay đ&aacute;nh dấy nội dung hay m&agrave; m&igrave;nh muốn lưu tập trung lại, ta sử dụng c&ocirc;ng cụ book mark.</p>\r\n\r\n<p>V&iacute; dụ chị muốn book mark th&agrave;nh mục lục c&aacute;c phần, Click chuột phải v&agrave;o trang đ&oacute;, chọn Add book mark, hoặc cụm ph&iacute;m tắt Ctrl Shift B. Sau đ&oacute; điền t&ecirc;n book mark v&agrave;o nếu kh&ocirc;ng chọn text được. Nếu l&agrave; dạng chọn text được th&igrave; m&igrave;nh chọn text trước, sau đ&oacute; Ctrl Shift B l&agrave; c&oacute; ngay book mark nh&eacute;.</p>\r\n\r\n<p>Ch&uacute;c c&aacute;c em sức khỏe, b&igrave;nh an, lu&ocirc;n vui vẻ v&agrave; c&ocirc;ng t&aacute;c tốt!</p>',NULL,NULL,0,0,NULL,NULL,'','','','',0,0,'vi','7-cach-su-dung-phan-mem-pdf-xchange-viewer-de-xu-ly-chung-tu',5,27,27,'2018-03-05 08:36:31','2018-03-05 08:36:31'),(280,'Video 15. Giới thiệu các công cụ chỉnh sửa chứng từ','','Video 15. Giới thiệu các công cụ chỉnh sửa chứng từ','<p>Ch&agrave;o c&aacute;c bạn, video h&ocirc;m nay t&ocirc;i sẽ hướng dẫn c&aacute;c bạn c&aacute;ch sử dụng phần mềm pdf xchange viewer để chỉnh sửa chứng từ.</p>\r\n\r\n<p>Trong XNK v&agrave; logistics, chứng từ file mềm dưới định dạng pdf được sử dụng rất phổ biến. V&agrave; như c&aacute;c bạn đ&atilde; biết, chứng từ ch&uacute;ng ta tiếp x&uacute;c c&agrave;ng nhiều th&igrave; c&agrave;ng ph&aacute;t hiện nhiều sai s&oacute;t, v&agrave; tất nhi&ecirc;n l&agrave; c&oacute; những sai s&oacute;t ch&uacute;ng ta vẫn c&oacute; thể chủ động sửa chữa được.</p>\r\n\r\n<p>PDF Xchange Viewer l&agrave; một phần mềm hỗ trợ chỉnh sửa chứng từ dưới định dạng ảnh v&agrave; định dạng pdf rất tối ưu v&agrave; dễ d&agrave;ng sử dụng, thao t&aacute;c đơn giản.</p>\r\n\r\n<p>Đầu ti&ecirc;n c&aacute;c bạn cần tải v&agrave; c&agrave;i đặt phần mềm, phần n&agrave;y đơn giản c&aacute;c bạn tự thao t&aacute;c nh&eacute;. Khi c&agrave;i phần mềm th&igrave; chỉ cần chọn bản miễn ph&iacute; th&ocirc;i. B&acirc;y giờ t&ocirc;i sẽ đi v&agrave;o c&aacute;c hướng dẫn chi tiết:</p>\r\n\r\n<p>Để hỗ trợ tốt nhất cho PDF Xchange Viewer, m&aacute;y c&aacute;c bạn cần c&agrave;i một phần mềm chuyển đổi c&aacute;c loại &nbsp;file sang định dạng pdf, gọi tắt l&agrave; in pdf. C&oacute; rất nhiều phần mềm kh&aacute;c nhau như Do pfd, Phantom, Nitro, cute pdf&hellip; nhưng t&ocirc;i chọn Foxit Reader bản m&agrave;u cam như thế n&agrave;y, link tải c&oacute; trong website tuhocxuatnhapkhau.com, trong qu&aacute; tr&igrave;nh c&agrave;i đặt, c&aacute;c bạn để &yacute; chọn v&agrave;o chức năng &ldquo;Chọn foxit l&agrave;m m&aacute;y in ảo mặc định&ldquo; nh&eacute;.</p>\r\n\r\n<p>Để thực h&agrave;nh phần mềm n&agrave;y, c&aacute;c bạn bắt buộc phải c&oacute; file định dạng pdf để thực h&agrave;nh.</p>\r\n\r\n<p>C&Aacute;C BẠN C&Oacute; THỂ Lấy 1 file word, excel, power point, file văn bản để tự chuyển sang pdf,</p>\r\n\r\n<p>Ch&uacute;ng ta l&agrave;m như sau</p>\r\n\r\n<p>Mở 1 file word, ấn F12 hoặc chọn lệnh Save As, tại &ocirc; Save as type, c&aacute;c bạn chọn lưu với đu&ocirc;i pdf. Thế l&agrave; ch&uacute;ng ta đ&atilde; c&oacute; 1 file pdf nh&eacute;.</p>\r\n\r\n<p>Nếu bạn n&agrave;o d&ugrave;ng office thấp hơn v&agrave; kh&ocirc;ng c&oacute; chức năng đ&oacute;, th&igrave; l&agrave;m như sau: Ấn tổ hợp ph&iacute;m tắt Ctrl P, chọn m&aacute;y in ở đ&acirc;y: Chọn m&aacute;y in Foxit reader PDF Printer, th&igrave; ch&uacute;ng ta cũng c&oacute; được file pdf như mong muốn.</p>\r\n\r\n<p>Nếu bạn n&agrave;o c&oacute; file ảnh, mở file ảnh ra như b&igrave;nh thường. Sau đ&oacute; cũng d&ugrave;ng lệnh in Ctrl P để in n&oacute; dưới dạng pdf tương tự.</p>\r\n\r\n<p>HOẶC NẾU Đ&Atilde; C&Oacute; SẴN, C&Aacute;C BẠN CHỈ CẦN Thao t&aacute;c lu&ocirc;n tr&ecirc;n file pdf của m&igrave;nh.</p>\r\n\r\n<p>Vậy l&agrave; mỗi ch&uacute;ng ta đều đ&atilde; c&oacute; c&aacute;c file pdf rồi. Giờ mở giao diện pdf ra như thế n&agrave;y nha. Để hiển thị hết c&aacute;c chức năng cần thiết, c&aacute;c bạn đặt chuột ở thanh c&ocirc;ng cụ, click chuột phải, v&agrave; chọn lần lượt hết c&aacute;c mục hiển thị ở đ&acirc;y nh&aacute;.</p>\r\n\r\n<p>B&acirc;y giờ, Ch&uacute;ng ta bắt đầu thao t&aacute;c sửa file:</p>\r\n\r\n<p>1) X&oacute;a một đoạn nội dung tr&ecirc;n file</p>\r\n\r\n<p>Giả sử tr&ecirc;n ch&uacute;ng ta cần phải x&oacute;a chữ m&atilde; số thuế n&agrave;y nh&eacute;. C&aacute;ch l&agrave;m đ&oacute; l&agrave; lấy 1 khoảng trắng bằng với v&ugrave;ng m&igrave;nh muốn x&oacute;a để che đi phần m&igrave;nh định x&oacute;a. M&igrave;nh l&agrave;m như sau: C&aacute;c bạn d&ugrave;ng c&ocirc;ng cụ chiếc m&aacute;y ảnh n&agrave;y, ấn giữ chuột tr&aacute;i v&agrave; k&eacute;o để chọn v&ugrave;ng cần x&oacute;a. Sau đ&oacute; nhả chuột, tiếp tục giữ chuột tr&aacute;i để di chuyển v&ugrave;ng chọn đến khu vực nền trắng. Ấn Ctrl C hoặc Click chuột phải, chọn Copy page selection, sau đ&oacute; ấn Ctrl V, c&aacute;c bạn sẽ nh&igrave;n thấy v&ugrave;ng trắng c&oacute; k&iacute;ch thước vừa bằng v&ugrave;ng chọn x&oacute;a tr&ecirc;n, chuyển sang c&ocirc;ng cụ Hand tool h&igrave;nh b&agrave;n tay để di chuyển v&ugrave;ng trắng đ&egrave; l&ecirc;n v&ugrave;ng ch&uacute;ng ta định che đi. Sau đ&oacute; Ctrl S để lưu lại.</p>\r\n\r\n<p>C&aacute;c bạn lưu &yacute;, mọi chỉnh sửa tr&ecirc;n pdf đều dưới dạng sửa&nbsp; tr&ecirc;n bề mặt, c&oacute; nghĩa l&agrave; nếu chỉnh sửa xong m&igrave;nh chỉ lưu lại bằng thao t&aacute;c Save Ctrl S th&igrave; khi chạm chuột v&agrave;o c&aacute;c v&ugrave;ng sửa, vẫn ph&aacute;t hiện ra l&agrave; chứng từ bị sửa. N&ecirc;n sau khi thực hiện chỉnh sửa, c&aacute;c bạn d&ugrave;ng lệnh in pdf để in lại 1 lần nữa ch&iacute;nh file đ&oacute; một lần nữa v&agrave; lưu file mới th&igrave; mới cố định được c&aacute;c phần sửa nh&eacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>2) Xem font chữ của đoạn text v&agrave; của to&agrave;n văn bản: đ&acirc;y l&agrave; chức năng gi&uacute;p c&aacute;c bạn khi muốn sửa nội dung của đoạn văn bản, trước hết cần biết font chữ v&agrave; size chữ của đoạn văn bản đ&oacute;. Để kiểm tra chi tiết của trang pdf, đặt chuột ở chế độ Hand tool &ndash; h&igrave;nh b&agrave;n tay tại 1 vị tr&iacute; bất k&igrave; tr&ecirc;n trang, click chuột phải, chọn Document Properties hoặc tổ hợp ph&iacute;m tắt Ctrl D, chọn Fonts, ở đ&acirc;y hiển thị tất cả c&aacute;c font chữ được d&ugrave;ng trong văn bản n&agrave;y, nếu kh&ocirc;ng hiển thị g&igrave; c&oacute; nghĩa l&agrave; file n&agrave;y được scan từ file cứng hoặc pdf 2 lần. Với c&aacute;c file dạng n&agrave;y cũng kh&ocirc;ng cho ph&eacute;p chọn hay b&ocirc;i đen đoạn văn bản, n&oacute; tương tự như h&igrave;nh ảnh.</p>\r\n\r\n<p>Nếu chỉ xem font của 1 đoạn văn bản nhất định, chọn c&ocirc;ng cụ Select tool, b&ocirc;i đen một đoạn văn bản như thế n&agrave;y, click chuột phải, chọn text properties, chọn formatting, thả dấu + n&agrave;y xuống sẽ nh&igrave;n thấy font chữ v&agrave; size chữ của đoạn text vừa rồi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiếp theo, sau khi đ&atilde; biết kiểu chữ v&agrave; cỡ chữ rồi, cho ph&eacute;p ta sửa một đoạn nội dung tr&ecirc;n file ban đầu bằng c&aacute;ch sau:</p>\r\n\r\n<p>3) Sửa một đoạn nội dung tr&ecirc;n file:</p>\r\n\r\n<p>Giả sử, ta cần sửa đoạn văn bản n&agrave;y th&agrave;nh &ldquo;B&aacute;o gi&aacute; tr&ecirc;n chưa bao gồm VAT10%&rdquo;</p>\r\n\r\n<p>Đầu ti&ecirc;n chọn chức năng Typewriter, ấn giữ ph&iacute;m Ctrl v&agrave; lăn bi chuột để ph&oacute;ng to, thu nhỏ để chỉnh sửa chuẩn x&aacute;c hơn, sau đ&oacute; trỏ chuột v&agrave;o vị tr&iacute; cần sửa, g&otilde; đoạn text v&agrave;o. Sau đ&oacute; chỉnh font chữ, size chữ cho khớp với bản gốc. Tuy nhi&ecirc;n c&aacute;c bạn để &yacute;, do file gốc được pdf nhiều lần n&ecirc;n chất lượng giảm, m&agrave;u nhạt đi. Vậy n&ecirc;n ch&uacute;ng ta cần l&agrave;m giảm bớt m&agrave;u chữ cho khớp m&agrave;u với bản gốc bằng c&aacute;ch chọn m&agrave;u chữ ở đ&acirc;y. Ngo&agrave;i ta, hiện tại background mặc định l&agrave; trong suốt n&ecirc;n ta vẫn nh&igrave;n thấy phần text cũ. Click v&agrave;o đ&acirc;y v&agrave; chọn m&agrave;u nền ph&ugrave; hợp với m&agrave;u nền trang giấy v&agrave; che hết to&agrave;n bộ phần đằng sau. Vậy l&agrave; ch&uacute;ng ta vừa ho&agrave;n th&agrave;nh thao t&aacute;c sửa nội dung chứng từ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>4) Th&ecirc;m một h&igrave;nh ảnh từ file kh&aacute;c v&agrave;o</p>\r\n\r\n<p>Trong trường hợp cần th&ecirc;m một số h&igrave;nh ảnh v&agrave;o khoảng trống sẵn c&oacute; tr&ecirc;n file pdf, ta c&oacute; thể lấy h&igrave;nh ảnh từ file pdf kh&aacute;c m&agrave; kh&ocirc;ng c&oacute; lệnh insert được từ nguồn ngo&agrave;i như đối với excel, word, powerpoint. C&aacute;ch l&agrave;m như sau:</p>\r\n\r\n<p>Mở cả 2 file pdf, giả sử cần ch&egrave;n logo n&agrave;y v&agrave;o file n&agrave;y. D&ugrave;ng c&ocirc;ng cụ snap shot để chụp h&igrave;nh ảnh ở file n&agrave;y, ấn tổ hợp ph&iacute;m Ctrl C để copy, ấn tiếp tổ hợp ph&iacute;m Ctrl tab để chuyển giữa c&aacute;c tab pdf đang mở. Ấn tổ hợp ph&iacute;m Ctrl V để d&aacute;n h&igrave;nh ảnh vừa copy b&ecirc;n file vừa rồi sang file n&agrave;y, v&agrave; di chuyển n&oacute; đến vị tr&iacute; mong muốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong một số trường hợp y&ecirc;u cầu ch&uacute;ng ta phải chuyển từ h&igrave;nh ảnh sang file pdf, hoặc từ ảnh v&agrave;o file word/excel rồi copy sang pdf cũng được</p>\r\n\r\n<p>5) Ch&egrave;n một trang nội dung hoặc trang trắng v&agrave;o file hiện tại, t&ocirc;i chỉ cho c&aacute;c bạn c&aacute;ch l&agrave;m như sau:</p>\r\n\r\n<p>Giả sử ta c&oacute; file n&agrave;y c&oacute; .. trang v&agrave; m&igrave;nh sẽ ch&egrave;n file &hellip; v&agrave;o giữa trang thứ &hellip;</p>\r\n\r\n<p>V&agrave;o document, Insert pages hoặc tổ hợp ph&iacute;m tắt Ctrl Shift I sẽ hiện ra 1 cửa sổ. Ta c&oacute; thể ch&egrave;n từ file n&agrave;o đ&oacute; trong 1 folder hoặc ch&egrave;n trực tiếp từ file đang mở sẵn, chỉ cần chọn đ&uacute;ng t&ecirc;n file đang mở, sau đ&oacute; chọn số trang cần ch&egrave;n: chọn tất cả c&aacute;c trang, chọn một số trang kh&ocirc;ng theo thứ tự, hoặc chỉ trang chẵn hoặc chỉ trang lẻ.</p>\r\n\r\n<p>Sau đ&oacute; chọn vị tr&iacute; ch&egrave;n v&agrave;o: ch&egrave;n v&agrave;o đằng trước hay đằng sau của trang đầu ti&ecirc;n, trang cuối c&ugrave;ng hay một trang cụ thể n&agrave;o đ&oacute;.</p>\r\n\r\n<p>Hoặc ch&egrave;n trang trắng bằng c&aacute;ch: V&agrave;o document, Insert empty pages, hiện ra cửa sổ để m&igrave;nh chọn cỡ trang giấy, trang ngang hay trang dọc, số lượng trang trống ch&egrave;n v&agrave;o, ch&egrave;n trước hay sau của trang đầu ti&ecirc;n, trang cuối hay 1 trang cụ thể n&agrave;o đ&oacute;</p>\r\n\r\n<p>Chức năng ch&egrave;n trang cũng cho ph&eacute;p m&igrave;nh nối c&aacute;c file pdf kh&aacute;c nhau v&agrave;o c&ugrave;ng một file bằng c&aacute;ch ch&egrave;n v&agrave;o ph&iacute;a sau của trang cuối c&ugrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, ở đ&acirc;y m&igrave;nh c&ograve;n c&oacute; chức năng</p>\r\n\r\n<p>6) T&aacute;ch trang ra khỏi file ban đầu trong trường hợp m&igrave;nh chỉ cần lấy 1 số trang trong số rất nhiều trang của file pdf hiện tại. V&agrave;o document, chọn Extract pages, chọn đ&uacute;ng trang m&igrave;nh muốn t&aacute;ch, v&igrave; bản miễn ph&iacute; n&ecirc;n m&igrave;nh kh&ocirc;ng sử dụng được 3 options b&ecirc;n dưới. Ấn ok, th&igrave; c&aacute;c trang ta vừa chọn được t&aacute;ch ra th&agrave;nh 1 file mới. Tuy nhi&ecirc;n ch&uacute; &yacute;, kh&ocirc;ng ấn lưu ngay, m&agrave; phải lưu lại bằng thao t&aacute;c in pdf cho file n&agrave;y th&igrave; tr&aacute;nh được dấu bản quyền.</p>\r\n\r\n<p>3 chức năng ở phần options d&agrave;nh cho phi&ecirc;n bản c&oacute; bản quyền</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>7) X&oacute;a một trang ra khỏi file ban đầu</p>\r\n\r\n<p>Để x&oacute;a một hoặc 1 số trang ra khỏi file gốc, ta l&agrave;m tương tự: V&agrave;o document, chọn Delete Pages hoặc ấn tổ hợp ph&iacute;m Ctrl Shift D, sau đ&oacute; chọn trang cần x&oacute;a, chọn trang chẵn, trang lẻ, hoặc x&oacute;a tất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i c&aacute;c thao t&aacute;c cơ bản tr&ecirc;n, Sarah hướng dẫn c&aacute;c bạn th&ecirc;m 1 số c&aacute;ch để tối ưu hiệu suất c&ocirc;ng việc nh&eacute;:</p>\r\n\r\n<p>Chức năng thứ 8 l&agrave;</p>\r\n\r\n<p>8) Chuyển file từ pdf sang dạng ảnh: trong trường hợp m&igrave;nh cần chuyển trang pdf hiện tại sang dạng h&igrave;nh ảnh, đặt chuột tr&ecirc;n trang, click chuột phải chọn export, export to image, chọn số trang cần chuyển, loại định dạng ảnh, nơi lưu, đặt t&ecirc;n file ảnh ở đ&acirc;y, ta c&oacute; thể thay đổi m&agrave;u nền của file từ m&agrave;u trắng sang c&aacute;c m&agrave;u kh&aacute;c t&ugrave;y chọn ở đ&acirc;y, sau đ&oacute; ấn export.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>9) Xem nguồn lưu file</p>\r\n\r\n<p>C&aacute;ch xem nguồn lưu file khi ta kh&ocirc;ng biết file n&agrave;y được lưu trong folder n&agrave;o. Click chuột phải v&agrave;o t&ecirc;n của file tr&ecirc;n mỗi tab, chọn open containing folder sẽ hiện ra folder m&igrave;nh lưu file đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>10) In nhiều file pdf một lệnh in</p>\r\n\r\n<p>Tiếp theo l&agrave; c&aacute;ch in nhiều file pdf chỉ trong một thao t&aacute;c: V&agrave;o folder chứa tất cả c&aacute;c file đ&oacute;, ấn Ctrl A để chọn to&agrave;n bộ, giữ Ctrl click loại bỏ c&aacute;c file kh&aacute;c định dạng, sau đ&oacute; click chuột phải, chọn print th&igrave; sẽ in được tất cả c&aacute;c file 1 l&uacute;c</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>11) Tạo bookmark</p>\r\n\r\n<p>Nếu như bạn l&agrave; người hay đọc s&aacute;ch ebook, hoặc đọc c&aacute;c th&ocirc;ng tư, văn bản m&agrave; muốn tạo mục lục tự động hoặc đ&aacute;nh dấu trang bạn đọc dở, đ&aacute;nh dấu c&aacute;c nội dung quan trọng, hay đ&aacute;nh dấu nội dung hay m&agrave; m&igrave;nh muốn lưu tập trung lại, ta sử dụng c&ocirc;ng cụ book mark.</p>\r\n\r\n<p>V&iacute; dụ t&ocirc;i muốn book mark th&agrave;nh mục lục c&aacute;c phần, Click chuột phải v&agrave;o trang đ&oacute;, chọn Add book mark, hoặc cụm ph&iacute;m tắt Ctrl Shift B. Sau đ&oacute; điền t&ecirc;n book mark v&agrave;o nếu kh&ocirc;ng chọn text được. Nếu l&agrave; dạng chọn text được th&igrave; m&igrave;nh chọn text trước, sau đ&oacute; Ctrl Shift B l&agrave; c&oacute; ngay book mark nh&eacute;.</p>\r\n\r\n<p>Vậy l&agrave; h&ocirc;m nay Sarah đ&atilde; hướng dẫn xong cho c&aacute;c bạn 11 ứng dụng từ pdf xchange viewer rất hữu &iacute;ch cho c&ocirc;ng việc của nh&acirc;n vi&ecirc;n chứng từ XNK n&oacute;i ri&ecirc;ng v&agrave; c&aacute;c vị tr&iacute; xử l&yacute; chứng từ kh&aacute;c nhau n&oacute;i chung.</p>',NULL,NULL,0,0,NULL,NULL,'','','','',-1,0,'vi','video-15-gioi-thieu-cac-cong-cu-chinh-sua-chung-tu',5,27,27,'2018-08-25 23:24:08','2018-08-25 23:24:08'),(281,'PHÂN BIỆT ORIGINAL BILL - SURRENDERED BILL - TELEX RELEASE','','ORIGINAL BILL - SURRENDERED BILL - TELEX RELEASE là 3 loại vận đơn cực kỳ phổ biến trong hoạt động Xuất nhập khẩu- logistics nhưng đôi khi vẫn có những bạn chưa phân biệt được sự khác nhau của chúng. Bài viết với hình ảnh và hướng dẫn đầy đủ sẽ làm các bạn hiểu rõ hơn về 3 loại vận đơn đường biển này.','<h1><strong><span style=\"color:#2F4F4F\">Ph&acirc;n biệt ORIGINAL BILL - SURRENDERED BILL - TELEX RELEASE</span></strong></h1>\r\n\r\n<p><span style=\"color:#2F4F4F\">Đ&acirc;y c&oacute; lẽ l&agrave; kh&aacute;i niệm m&agrave; nhiều người hoạt động trong lĩnh vực Xuất nhập khẩu&nbsp;&amp; giao nhận rất hay gặp phải.&nbsp;&nbsp;Tất nhi&ecirc;n khi search tr&ecirc;n mạng, c&aacute;c bạn c&oacute; thể thấy nhiều b&agrave;i viết về điều n&agrave;y, nhưng để gi&uacute;p c&aacute;c bạn dễ h&igrave;nh dung nhất Ad sẽ giải th&iacute;ch qua ảnh minh họa v&agrave; ng&ocirc;n ngữ đơn giản dễ hiểu nhất.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"color:#2F4F4F\">1. ORIGINAL BILL (VẬN ĐƠN GỐC).</span></h2>\r\n\r\n<p><span style=\"color:#2F4F4F\">Để l&agrave;m r&otilde; vấn đề n&agrave;y Ad sẽ lấy v&iacute; dụ trong trường hợp đơn giản nhất theo h&igrave;nh minh họa, SHIPPER (người gửi h&agrave;ng) chuyển h&agrave;ng cho CONSIGNEE (người nhận h&agrave;ng), người vận chuyển l&agrave; h&atilde;ng t&agrave;u OOCL, trong trường hợp n&agrave;y SHIPPER book t&agrave;u v&agrave; l&agrave;m việc trực tiếp với h&atilde;ng t&agrave;u WANHAI, kh&ocirc;ng book qua FORWARDER.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Vận đơn do h&atilde;ng t&agrave;u OOCL ph&aacute;t h&agrave;nh. &Yacute; nghĩa của n&oacute; thực ra l&agrave; chỉ việc c&oacute; phải sử dụng &lsquo;&rsquo;vận đơn gốc&rsquo;&rsquo; trong qu&aacute; tr&igrave;nh giao nhận hay kh&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Th&ocirc;ng thường trong giao nhận vận tải quốc tế, sau khi t&agrave;u chạy SHIPPER sẽ y&ecirc;u cầu h&atilde;ng t&agrave;u hoặc Forwarder ph&aacute;t h&agrave;nh vận đơn gốc (03 bản) . Sau đ&oacute; đưa lại 03 bản đ&oacute; cho SHIPPER.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Trường hợp n&agrave;y h&atilde;ng t&agrave;u OOCL&nbsp;sẽ ph&aacute;t h&agrave;nh 3 bản vận đơn gốc, trao cho SHIPPER. Sau khi nhận vận đơn gốc , SHIPPER chuyển cả 03 bản cho người nhận CONSIGNEE (bằng đường AIR). Khi h&agrave;ng về đến cảng của người nhận, họ buộc phải mang 03 bản gốc ấy đến h&atilde;ng t&agrave;u OOCL&nbsp;v&agrave; đ&oacute;ng c&aacute;c ph&iacute; Local charge. Khi đ&oacute; h&atilde;ng t&agrave;u OOCL&nbsp;mới chấp nhận giao D/O tức cho ph&eacute;p người nhận h&agrave;ng được nhận l&ocirc; h&agrave;ng tr&ecirc;n.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Như vậy 1 khi đ&atilde; ph&aacute;t h&agrave;nh vận đơn gốc, th&igrave; muốn nhận h&agrave;ng, CONSIGNEE bắt buộc phải c&oacute; được vận đơn gốc đ&oacute; th&igrave; mới nhận được h&agrave;ng.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Điều n&agrave;y thể hiện sự chặt chẽ trong qu&aacute; tr&igrave;nh giao nhận. V&iacute; dụ thế n&agrave;y, mặc d&ugrave; SHIPPER đ&atilde; chấp nhận gửi h&agrave;ng cho CONSIGNEE, nhưng ngay cả khi h&agrave;ng về đển cảng, CONSIGNEE vẫn chưa trả tiền h&agrave;ng cho SHIPPER l&ocirc; h&agrave;ng đ&oacute;, v&igrave; thế n&ecirc;n SHIPPER kh&ocirc;ng gửi vận đơn gốc cho CONSIGNEE. M&agrave; kh&ocirc;ng c&oacute; vận đơn gốc trong tay th&igrave; CONSIGNEE kh&ocirc;ng thể lấy được h&agrave;ng. Vận đơn gốc l&uacute;c n&agrave;y sẽ đ&oacute;ng vai tr&ograve; r&agrave;ng buộc việc giao nhận giữa c&aacute;c chủ thể với nhau.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Trong trường hợp CONSIGNEE chưa c&oacute; vận đơn gốc, những h&atilde;ng t&agrave;u OOCL&nbsp;vẫn cố t&igrave;nh giao h&agrave;ng, như vậy điều n&agrave;y l&agrave; sai luật, SHIPPER ho&agrave;n to&agrave;n c&oacute; thể kiện h&atilde;ng t&agrave;u n&agrave;y v&agrave; bắt bồi thường tổn thất ph&aacute;t sinh. Nhưng TH n&agrave;y gần như kh&ocirc;ng c&oacute;, v&igrave; h&atilde;ng t&agrave;u l&agrave; 1 hệ thống v&ocirc; c&ugrave;ng lớn, l&agrave;m việc v&ocirc; c&ugrave;ng nguy&ecirc;n tắc.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Tr&aacute;i lại trong trường hợp SHIPPER kh&ocirc;ng book qua h&atilde;ng t&agrave;u m&agrave; book qua FWD, th&igrave; đại l&yacute; của FWD b&ecirc;n kia phải nhận được vận đơn gốc mới giao h&agrave;ng, c&aacute;i n&agrave;y t&ugrave;y thuộc v&agrave;o đạo đức của b&ecirc;n FWD nữa, đ&atilde; c&oacute; nhiều trường hợp FWD tự &yacute; giao h&agrave;ng khi chưa c&oacute; vận đơn gốc rồi, n&ecirc;n đối với những l&ocirc; h&agrave;ng x&aacute;c định l&agrave;m vận đơn gốc, người b&aacute;n rất ngại l&agrave;m việc qua c&aacute;c Forwarder l&agrave; v&igrave; thế.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\"><img alt=\"\" src=\"/uploads/ckfinder/images/bl%20original.png\" style=\"height:502px; width:579px\" /></span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Lưu &yacute;: Một số trường hợp B/L Original được ph&aacute;t h&agrave;nh r&ugrave;i nhưng sau đ&oacute; CONSIGNEE y&ecirc;u cầu nhận h&agrave;ng sớm hơn khi chờ B/L Original th&igrave; được sự chấp nhận của Shipper sẽ SURRENDERED&nbsp;B/L tr&ecirc;n B/L gốc v&agrave; đ&oacute;ng dấu ng&agrave;y Sur l&ecirc;n đ&oacute; (Trừ B/L Original c&oacute; k&yacute; hậu ng&acirc;n h&agrave;ng)</span></p>\r\n\r\n<h2><span style=\"color:#2F4F4F\">2. SURRENDERED BILL (VẬN ĐƠN SURRENDERED)</span></h2>\r\n\r\n<p><span style=\"color:#2F4F4F\">Việc phải ph&aacute;t h&agrave;nh &lsquo;&rsquo;vận đơn gốc&rsquo;&rsquo; trong qu&aacute; tr&igrave;nh giao nhận.n&oacute; chặt chẽ nhưng cũng ph&aacute;t sinh 1 v&agrave;i vấn đề rắc rối, như thế n&agrave;y:</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Khi người b&aacute;n v&agrave; người mua đ&atilde; thanh to&aacute;n hết tiền, hoặc c&oacute; quan hệ l&agrave;m ăn gần gũi với nhau , chẳng c&oacute; l&yacute; do g&igrave; m&agrave; người b&aacute;n lại phải giữ h&agrave;ng của người mua th&ocirc;ng qua vận đơn gốc.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Việc gửi vận đơn gốc từ SHIPPER sang CONSIGNEE để họ cầm đi nhận h&agrave;ng vừa mất thời gian, vừa tốn tiền, thủ tục lại lằng nhằng, phiền h&agrave;.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Kh&ocirc;ng may l&agrave;m mất c&aacute;i vận đơn gốc, l&uacute;c n&agrave;y h&atilde;ng t&agrave;u sẽ kh&ocirc;ng cấp lại, muốn nhận h&agrave;ng CONSIGNEE phải cam kết đ&oacute;ng tiền thế chấp , thường l&agrave; 110% gi&aacute; trị của h&agrave;ng v&agrave; giữ trong v&ograve;ng 2 năm. Để đề ph&ograve;ng sau n&agrave;y c&oacute; ai đ&oacute; mang vận đơn gốc đến đ&ograve;i th&igrave; h&atilde;ng t&agrave;u lấy g&igrave; ra m&agrave; đưa nữa.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Thế n&ecirc;n mới xuất hiện thuật ngữ VẬN ĐƠN SURRENDERED.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">SURRENDERED BILL (VẬN ĐƠN SURRENDERED) l&agrave; 1 vận đơn c&oacute; nội dung hầu như giống với vận đơn gốc v&agrave; được đ&oacute;ng dấu SURRENDEREDED tr&ecirc;n đ&oacute;.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Khi SHIPPER y&ecirc;u cầu h&atilde;ng t&agrave;u (nếu book trực tiếp qua h&atilde;ng t&agrave;u) hoặc FWD (nếu book qua FWD) l&agrave;m SURRENDERED Bill nghĩa l&agrave; họ đ&atilde; y&ecirc;u cầu h&atilde;ng t&agrave;u hoặc FWD giao h&agrave;ng cho người nhận CONSIGNEE tại cảng đến m&agrave; kh&ocirc;ng bắt họ phải xuất tr&igrave;nh &lsquo;&rsquo;vận đơn gốc&rsquo;&rsquo;.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Như vậy l&agrave;m Bill SURRENDERED sẽ tiết kiệm được thời gian v&agrave; thủ tục trong việc giao nhận. Trong trường hợp n&agrave;y khi vận đơn đ&atilde; được SURRENDERED , tại cảng đến CONSIGNEE chỉ cần đ&oacute;ng c&aacute;c ph&iacute; cần thiết cho h&atilde;ng t&agrave;u WANHAI l&agrave; h&atilde;ng t&agrave;u n&agrave;y buộc phải giao D/O , tức chấp nhận giao h&agrave;ng cho CONSIGNEE.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Để l&agrave;m SURRENDERED B/L, thực tế SHIPPER chỉ cần y&ecirc;u cầu h&atilde;ng t&agrave;u hoặc FWD l&agrave;m lu&ocirc;n SURRENDERED B/L, kh&ocirc;ng y&ecirc;u cầu l&agrave;m vận đơn gốc, rất nhanh v&agrave; thuận tiện, những l&ocirc; h&agrave;ng gi&aacute; trị kh&ocirc;ng qu&aacute; lớn, thường l&agrave;m SURRENDERED Bill lu&ocirc;n cho gọn.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Nhưng khi h&atilde;ng t&agrave;u hoặc FWD đ&atilde; ph&aacute;t h&agrave;nh vận đơn gốc v&agrave; trao cho SHIPPER, nếu SHIPPER y&ecirc;u cầu SURRENDERED Bill bắt buộc họ phải thu lại vận đơn gốc, c&aacute;i n&agrave;y dễ hiểu , v&igrave; lỡ sau n&agrave;y c&oacute; thằng n&agrave;o tự nhi&ecirc;n cầm vận đơn gốc đến đ&ograve;i h&agrave;ng, th&igrave; h&atilde;ng t&agrave;u c&ograve;n h&agrave;ng đ&acirc;u m&agrave; giao nữa.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\"><img alt=\"\" src=\"/uploads/ckfinder/images/BL%20surrendered.png\" style=\"height:893px; width:632px\" /></span></p>\r\n\r\n<h2><span style=\"color:#2F4F4F\">3. TELEX RELEASE (ĐIỆN GIAO H&Agrave;NG)</span></h2>\r\n\r\n<p><span style=\"color:#2F4F4F\">Điện giao h&agrave;ng ở đ&acirc;y được hiểu n&ocirc;m na l&agrave; 1 cuộc điện thoại, Fax hoặc Email ...của h&atilde;ng t&agrave;u hoặc FWD y&ecirc;u cầu đại l&yacute; hoặc văn ph&ograve;ng của họ tại cảng đến giao h&agrave;ng cho CONSIGNEE m&agrave; kh&ocirc;ng cần vận đơn gốc</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Hay n&oacute;i c&aacute;ch kh&aacute;c, khi SHIPPER y&ecirc;u cầu SURRENDERED Bill th&igrave; h&atilde;ng t&agrave;u hoặc FWD sẽ ph&aacute;t &lsquo;&rsquo;điện giao h&agrave;ng&rsquo;&rsquo; y&ecirc;u cầu văn ph&ograve;ng hoặc đại l&yacute; b&ecirc;n kia phải giao h&agrave;ng cho CONSIGNEE m&agrave; kh&ocirc;ng cần thu vận đơn gốc. Dễ hiểu vậy th&ocirc;i, Telex Release ch&iacute;nh l&agrave; cơ sở h&igrave;nh th&agrave;nh n&ecirc;n SURRENDERED Bill.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\"><img alt=\"\" src=\"/uploads/ckfinder/images/BL%20telex.png\" style=\"height:927px; width:655px\" /></span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Thực tế kể cả khi kh&ocirc;ng ph&aacute;t h&agrave;nh vận đơn gốc, khi h&agrave;ng đến cảng đến, SHIPPER vẫn c&oacute; thể y&ecirc;u cầu h&atilde;ng t&agrave;u hoặc FWD kh&ocirc;ng được giao h&agrave;ng cho CONSIGNEE bằng c&aacute;ch y&ecirc;u cầu h&atilde;ng t&agrave;u hoặc FWD chưa được l&agrave;m Telex Release, khi đ&oacute; vận đơn vẫn chưa được SURRENDERED , v&igrave; thế CONSIGNEE vẫn chưa thể nhận h&agrave;ng, trường hợp n&agrave;y ph&aacute;t sinh cũng kh&aacute; phổ biến.</span></p>\r\n\r\n<p><span style=\"color:#2F4F4F\">Đ&ocirc;i khi c&aacute;c b&ecirc;n đ&oacute;ng dấu Telex Release l&ecirc;n B/L thay cho chữ SURRENDERED, v&agrave; n&oacute; được hiểu theo nghĩa tương tự.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>','/9f/de/e9/5bade7f5d11c2.png',NULL,2657,100,'2018-09-24 00:00:00',NULL,'ORIGINAL BILL - SURRENDERED BILL - TELEX RELEASE là 3 loại vận đơn cực kỳ phổ biến trong hoạt động Xuất nhập khẩu- logistics nhưng đôi khi vẫn có những bạn chưa phân biệt được sự khác nhau của chúng. Bài viết với hình ảnh và hướng dẫn đầy đủ sẽ làm các bạ','vận đơn đường biển, vận đơn điện, điện giao hàng, vận đơn surrendered, vận đơn telex release, sự khách nhau giữa Bill Surrendered và Bill Telex Release.','','',2,0,'vi','phan-biet-original-bill-surrendered-bill-telex-release',8,26,26,'2018-09-22 11:25:00','2018-09-28 15:36:05'),(282,'3 LÝ DO NÊN ĐI HỌC NGHIỆP VỤ CHUYÊN MÔN TỪ NĂM THỨ BA ĐẠI HỌC TRỞ ĐI','','Ngày nay việc đi học nghiệp vụ nói chung và nghiệp vụ xuất nhập khẩu nói riêng đã không còn xa lạ với các bạn sinh viên do muốn có được những trải nghiệm về công việc và nắm chắc kiến thức chuyên môn. Có nhiều bạn tự hỏi liệu năm thứ ba mình đã nên đi học nghiệp vụ chưa?\r\nBạn hãy tham khảo một số lời khuyên dưới đây của HAN EXIM CLUB và tự đưa ra quyết định nhé.','<p>Ng&agrave;y nay việc đi học nghiệp vụ n&oacute;i chung v&agrave; nghiệp vụ xuất nhập khẩu n&oacute;i ri&ecirc;ng&nbsp;đ&atilde; kh&ocirc;ng c&ograve;n xa lạ với c&aacute;c bạn sinh vi&ecirc;n do muốn c&oacute; được những trải nghiệm về c&ocirc;ng việc v&agrave; nắm chắc kiến thức chuy&ecirc;n m&ocirc;n. C&oacute; nhiều bạn tự hỏi liệu năm thứ ba m&igrave;nh đ&atilde; n&ecirc;n đi học nghiệp vụ chưa?<br />\r\nBạn h&atilde;y tham khảo một số lời khuy&ecirc;n dưới đ&acirc;y của HAN EXIM CLUB v&agrave; tự đưa ra quyết định nh&eacute;.</p>\r\n\r\n<p>❌&nbsp;Thứ nhất: Từ năm 3 ở trường cũng bắt đầu học một số m&ocirc;n chuy&ecirc;n ng&agrave;nh, nếu em học nghiệp vụ ở ngo&agrave;i nữa th&igrave; sẽ c&oacute; c&aacute;i nh&igrave;n tổng quan về nghiệp vụ, tự tin hơn với c&aacute;c b&agrave;i thảo luận, thuyết tr&igrave;nh hay chuy&ecirc;n đề, b&agrave;i kiểm tra tr&ecirc;n lớp như : nghiệp vụ ngoại thương, vận tải quốc tế, bảo hiểm, thanh to&aacute;n quốc tế&hellip;<br />\r\n❌&nbsp;Thứ hai: C&aacute;c em học xong c&oacute; thể sớm xin đi thực tập từ năm 3 (c&aacute;i n&agrave;y kh&ocirc;ng nhiều nhưng vẫn c&oacute; nơi nhận). Ngo&agrave;i ra, v&igrave; tại lớp học của HAN EXIM CLUB&nbsp;được học cả kỹ năng n&ecirc;n em vẫn c&oacute; nhiều thời gian để trau dồi v&agrave; bồi dưỡng trước khi ra trường. Đừng đợi đến khi qu&aacute; muộn rồi lại mất th&ecirc;m 3-6 th&aacute;ng ở nh&agrave; luyện tin học văn ph&ograve;ng hay tập viết CV nh&eacute;.&nbsp;<br />\r\n=&gt;Trong khi c&aacute;c bạn c&ugrave;ng lứa vẫn c&ograve;n mơ hồ th&igrave; m&igrave;nh đ&atilde; c&oacute; hướng đi. Học sớm đ&oacute;n ch&agrave;o cơ hội sớm<br />\r\n❌&nbsp;Thứ ba: Một số em sợ học sớm đến khi tốt nghiệp lại qu&ecirc;n<br />\r\n=&gt; Chắc bạn chưa biết kh&oacute;a học Nghiệp vụ Xuất nhập khẩu-logistics&nbsp;&ldquo; được bảo h&agrave;nh trọn đời&rdquo;, học ph&iacute; đ&oacute;ng 1 lần nhưng được học lại miễn ph&iacute; 1 hay nhiều lần, 1 học phần hoặc cả kh&oacute;a, bất kể những g&igrave; em muốn. Học để update kiến thức, để được&nbsp;tr&ograve; chuyện hay đơn giản l&agrave; &ldquo;ngắm&rdquo; c&ocirc; gi&aacute;o. Hihi</p>\r\n\r\n<p>☕&nbsp;☕&nbsp;☕&nbsp;Như vậy học từ năm 3 l&agrave; c&aacute;c em đang &ldquo; tiết kiệm chi ph&iacute;&rdquo; cả thời gian lẫn tiền bạc đ&oacute; chứ?</p>\r\n\r\n<p>Ch&uacute;c c&aacute;c bạn sớm đưa ra quyết định. Nếu bạn muốn c&oacute; th&ecirc;m th&ocirc;ng tin về kh&oacute;a học c&oacute; thể truy cập <strong><a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/khoa-xnk-va-logistics-toan-dien\">tại đ&acirc;y</a></strong></p>','/10/3a/a1/5c7e14c998b6b.jpg',NULL,313,100,'2019-03-05 13:18:49',NULL,'Ngày nay việc đi học nghiệp vụ nói chung và nghiệp vụ xuất nhập khẩu nói riêng đã không còn xa lạ với các bạn sinh viên do muốn có được những trải nghiệm về công việc và nắm chắc kiến thức chuyên môn. Có nhiều bạn tự hỏi liệu năm thứ ba mình đã nên đi học','học nghiệp vụ xuất nhập khẩu, có nên học xuất nhập khẩu','','',2,0,'vi','3-ly-do-nen-di-hoc-nghiep-vu-chuyen-mon-tu-nam-thu-ba-dai-hoc-tro-di',5,26,26,'2019-03-05 13:18:49','2019-03-05 13:18:49'),(283,'Câu hỏi thường gặp của các bạn trước khi tham gia khóa học','','Có được thông tin đầy đủ trước khi đưa ra quyết định theo học tại nơi nào đó là nhu cầu chính đáng của mỗi bạn. Với những câu hỏi FAQ dưới đây mà chúng tôi nhận được, nếu không nằm trong câu hỏi của bạn thì bạn có thể liên hệ Hotline 0906246584 để được tư vấn kỹ hơn. Hy vọng bạn có thể đưa ra quyết định dễ dàng hơn với những câu hỏi FAQ này','<p>C&oacute; được th&ocirc;ng tin đầy đủ trước khi đưa ra quyết định theo học tại nơi n&agrave;o đ&oacute; l&agrave; nhu cầu ch&iacute;nh đ&aacute;ng của mỗi bạn. Với những c&acirc;u hỏi FAQ dưới đ&acirc;y m&agrave; ch&uacute;ng t&ocirc;i nhận được, nếu kh&ocirc;ng nằm trong c&acirc;u hỏi của bạn th&igrave; bạn c&oacute; thể li&ecirc;n hệ Hotline 0906246584 để được tư vấn kỹ hơn.</p>\r\n\r\n<p>1. B&ecirc;n trung t&acirc;m c&oacute; cấp chứng chỉ sau kh&oacute;a học kh&ocirc;ng?</p>\r\n\r\n<p>Trả lời: B&ecirc;n m&igrave;nh c&oacute; cấp chứng nhận ho&agrave;n th&agrave;nh kh&oacute;a học x&aacute;c nhận bởi HAN EXIM CLUB. Chứng nhận n&agrave;y bạn c&oacute; thể photo nộp c&ugrave;ng hồ sơ xin việc. Chỉ những nơi thuộc Sở gi&aacute;o dục đ&agrave;o tạo mới cấp Chứng chỉ bạn nh&eacute;.</p>\r\n\r\n<p>2. B&ecirc;n trung t&acirc;m c&oacute; hỗ trợ giới thiệu việc l&agrave;m, thực tập sau khi học kh&ocirc;ng?</p>\r\n\r\n<p>Trả lời: Hiện HAN EXIM CLUB đang kết nối với nhiều c&ocirc;ng ty XUẤT NHẬP KHẨU/logistics, khi bạn cần ch&uacute;ng t&ocirc;i c&oacute; thể hỗ trợ giới thiệu việc l&agrave;m để bạn ứng tuyển cũng như gi&uacute;p bạn ho&agrave;n thiện hồ sơ, CV xin việc, phỏng vấn giả định. Đ&acirc;y&nbsp; cũng l&agrave; 1 trong những buổi học tại kh&oacute;a học n&agrave;y. Ngo&agrave;i ra, c&aacute;c bạn học xong đều được đi thực tế tại c&aacute;c cty XUẤT NHẬP KHẨU hoặc thực tập đi&nbsp; nhận h&agrave;ng tại Chi cục Hải quan, xin C/O, xin giấy ph&eacute;p kiểm tra chuy&ecirc;n ng&agrave;nh&hellip; Chủ đề thực tế sẽ phụ thuộc v&agrave;o l&ocirc; h&agrave;ng.</p>\r\n\r\n<p>3. M&igrave;nh k&eacute;m tiếng Anh liệu c&oacute; thể theo học được kh&ocirc;ng?</p>\r\n\r\n<p>Trả lời:</p>\r\n\r\n<p>Thứ nhất, tiếng Anh hay bất cứ Ngoại ngữ n&agrave;o l&agrave; c&ocirc;ng cụ cần thiết cho c&ocirc;ng việc nhất l&agrave; c&ocirc;ng việc Xuất nhập khẩu (v&igrave; c&oacute; li&ecirc;n quan đến yếu tố nước ngo&agrave;i). Tuy nhi&ecirc;n, mức độ sử dụng hay y&ecirc;u cầu về ngoại ngữ lại kh&aacute;c nhau t&ugrave;y thuộc v&agrave;o từng vị tr&iacute; bạn ứng tuyển. V&iacute; dụ: đối với vị tr&iacute; l&agrave;m sales quốc tế hoặc mua h&agrave;ng đều y&ecirc;u cầu th&agrave;nh thạo cả 4 kỹ năng nghe-n&oacute;i-đọc-viết (cần l&agrave;m hợp đồng, tiếp kh&aacute;ch trực tiếp, đi hội chợ, dẫn kh&aacute;ch xuống Nh&agrave; m&aacute;y- xưởng sản xuất, phi&ecirc;n dịch cho sếp, dịch t&agrave;i liệu kỹ thuật của sản phẩm&hellip;). Nhưng vị tr&iacute; l&agrave;m chứng từ (tại c&ocirc;ng ty XUẤT NHẬP KHẨU hay logistics) th&igrave; mức độ sử dụng tiếng Anh thường l&agrave; đọc-viết th&agrave;nh thạo để &iacute;t nhất xử l&yacute; tr&ecirc;n c&aacute;c chứng từ (đọc, soạn chứng từ như hợp đồng, invoice, packing list, CO, Bill of Lading&hellip;.).</p>\r\n\r\n<p>Thứ hai, tiếng Anh trong XUẤT NHẬP KHẨU lại hẹp hơn tiếng Anh tổng qu&aacute;t. Từ vựng tầm 1000 từ (so với 3000 từ trong tiếng Anh giao tiếp). Khi đến với HAN EXIM CLUB, c&aacute;c bạn kh&ocirc;ng chỉ được học kiến thức, kỹ năng về Xuất nhập khẩu m&agrave; c&ograve;n được ch&uacute;ng t&ocirc;i chia sẻ, hướng dẫn tiếng Anh Xuất nhập khẩu ở tr&ecirc;n lớp th&ocirc;ng qua c&aacute;c b&agrave;i học. Ngo&agrave;i ra, HAN EXIM CLUB c&ograve;n c&oacute; cả một CLB tự học tiếng Anh Xuất nhập khẩu để đồng h&agrave;nh c&ugrave;ng c&aacute;c bạn. Đặc biệt l&agrave; ho&agrave;n to&agrave;n FREE với học vi&ecirc;n của ch&uacute;ng t&ocirc;i. Link fanpage&hellip;.</p>\r\n\r\n<p>3.M&igrave;nh đến từ chuy&ecirc;n ng&agrave;nh kh&aacute;c kh&ocirc;ng li&ecirc;n quan đến kinh tế, chưa bao giờ được học về những kiến thức kinh doanh n&oacute;i chung v&agrave; Xuất nhập khẩu n&oacute;i ri&ecirc;ng th&igrave; liệu c&oacute; theo học được kh&ocirc;ng?</p>\r\n\r\n<p>Trả lời: Kh&oacute;a học n&agrave;y tập trung v&agrave;o truyền đạt kiến thức v&agrave; nghiệp vụ thực tế, gần như kh&ocirc;ng dựa v&agrave;o kiến thức trường lớp c&aacute;c bạn từng học. V&igrave; vậy, d&ugrave; xuất ph&aacute;t điểm của bạn ở đ&acirc;u th&igrave; c&aacute;c bạn đều được hướng dẫn lại từ những bước cơ bản đầu ti&ecirc;n nhất. Ch&uacute;ng t&ocirc;i đ&atilde; c&oacute; rất nhiều học vi&ecirc;n đến từ c&aacute;c chuy&ecirc;n ng&agrave;nh kh&aacute;c nhau: kế to&aacute;n, t&agrave;i ch&iacute;nh, ng&acirc;n h&agrave;ng, nh&acirc;n sự, sư phạm, kỹ thuật, giao th&ocirc;ng, ... đ&atilde; đến đ&acirc;y tham gia kh&oacute;a học v&agrave; chuyển đổi nghề th&agrave;nh c&ocirc;ng. N&ecirc;n bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m nh&eacute;! Chỉ cần bạn tin tưởng, cam kết r&egrave;n luyện c&ugrave;ng ch&uacute;ng t&ocirc;i th&igrave; bạn sẽ vượt qua được mọi trở ngại của m&igrave;nh.</p>\r\n\r\n<p>4. M&igrave;nh đăng k&yacute; theo nh&oacute;m th&igrave; c&oacute; được ưu đ&atilde;i hơn kh&ocirc;ng?</p>\r\n\r\n<p>Trả lời: Bạn đăng k&yacute; nh&oacute;m sẽ nhận được học ph&iacute; ưu đ&atilde;i khi nộp học ph&iacute; trước buổi khai giảng, d&agrave;nh cho nh&oacute;m 2 người: giảm 200k/bạn, nh&oacute;m 3-4 người: giảm 400k/người, nh&oacute;m 5 người trở l&ecirc;n: giảm 600k/người</p>\r\n\r\n<p>5. M&igrave;nh c&oacute; phải nộp học ph&iacute; trước khi khai giảng kh&ocirc;ng? C&aacute;ch nộp học ph&iacute; như thế n&agrave;o?</p>\r\n\r\n<p>Trả lời: Bạn c&oacute; thể chọn nộp học ph&iacute; trước khi khai giảng hoặc cuối buổi khai giảng. Tuy nhi&ecirc;n, nếu bạn muốn được giảm học ph&iacute; theo ch&iacute;nh s&aacute;ch ưu đ&atilde;i th&igrave; c&oacute; thể ho&agrave;n th&agrave;nh học ph&iacute; trước khai giảng.</p>\r\n\r\n<p>Bạn c&oacute; thể đến trực tiếp lớp học để đ&oacute;ng học ph&iacute; (hẹn trước khi đến) hoặc bạn đ&oacute;ng học ph&iacute; qua chuyển khoản theo th&ocirc;ng tin b&ecirc;n m&igrave;nh sẽ gửi qua email.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>6. T&ocirc;i học xong muốn về mở c&ocirc;ng ty th&igrave; kh&oacute;a học n&agrave;y c&oacute; đ&aacute;p ứng kh&ocirc;ng?</p>\r\n\r\n<p>Trả lời: Kh&oacute;a học n&agrave;y kh&ocirc;ng phải dạy phương ph&aacute;p quản l&yacute; m&agrave; l&agrave; dạy chi tiết về quy tr&igrave;nh v&agrave; nghiệp vụ Xuất nhập khẩu.&nbsp; Bởi thực tế, nếu nh&agrave; quản l&yacute; nắm bắt quy tr&igrave;nh hay c&aacute;c nghiệp vụ m&agrave; nh&acirc;n vi&ecirc;n/c&ocirc;ng ty m&igrave;nh cần thực hiện th&igrave; từ đ&oacute; c&oacute; thể quản l&yacute;, kiểm so&aacute;t c&ocirc;ng việc, tối ưu về chi ph&iacute; v&agrave; n&acirc;ng cao năng suất lao động &hellip;.. Anh/chị cũng c&oacute; thể được tư vấn (nếu cần) c&aacute;c phương &aacute;n về bố tr&iacute; nh&acirc;n lực hay cấu tr&uacute;c nh&acirc;n sự trong c&ocirc;ng ty như thế n&agrave;o để ph&ugrave; hợp với nhu cầu kinh doanh của anh chị.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>7. H&atilde;y gi&uacute;p t&ocirc;i ph&acirc;n biệt logistics v&agrave; Xuất nhập khẩu. V&igrave; sao kh&oacute;a học n&agrave;y l&agrave; to&agrave;n diện m&agrave; kh&ocirc;ng chia cụ thể ra?</p>\r\n\r\n<p>Trả lời: Về cơ bản, Xuất nhập khẩu v&agrave; Logistics tạo th&agrave;nh 1 chuỗi cung ứng (gọi l&agrave; supply chain). C&oacute; xuất khẩu, c&oacute; nhập khẩu th&igrave; sẽ ph&aacute;t sinh sử dụng c&aacute;c dịch vụ từ ph&iacute;a c&aacute;c c&ocirc;ng ty Logistics: sử dụng phương tiện vận tải, dịch vụ kho b&atilde;i, c&aacute;c loại thủ tục h&agrave;nh ch&iacute;nh, quản l&yacute;, kế to&aacute;n, giao nhận... Hai mảng n&agrave;y li&ecirc;n quan mật thiết, kh&ocirc;ng thể t&aacute;ch rời được. Kh&oacute;a học được thiết kế để bạn c&oacute; c&aacute;i nh&igrave;n to&agrave;n diện nhất về chuỗi cung ứng n&oacute;i chung v&agrave; c&aacute;c quy tr&igrave;nh nghiệp vụ xử l&yacute; tại mỗi bước trong quy tr&igrave;nh Xuất nhập khẩu tổng qu&aacute;t.</p>\r\n\r\n<p style=\"margin-left:.5in\">&nbsp;</p>\r\n\r\n<p>8.T&ocirc;i muốn học online c&oacute;&nbsp; được kh&ocirc;ng?</p>\r\n\r\n<p>Trả lời: Hiện tại b&ecirc;n CLB chưa c&oacute; kh&oacute;a online bạn nh&eacute;. Bạn h&atilde;y chờ th&ecirc;m hoặc follow fanpage của ch&uacute;ng t&ocirc;i để cập nhật th&ocirc;ng tin mới nhất.</p>\r\n\r\n<p>9. Em l&agrave; sinh vi&ecirc;n năm 3 đ&atilde; c&oacute; thể&nbsp; học kh&oacute;a n&agrave;y được chưa? Nếu học th&igrave; c&oacute; sợ qu&ecirc;n kh&ocirc;ng ạ?</p>\r\n\r\n<p>Trả lời: Sinh vi&ecirc;n năm 3 em học xong th&igrave; sẽ c&oacute; thời gian để xin ứng tuyển đi l&agrave;m thực tập sinh (&iacute;t nhất l&agrave; n&ecirc;n đi v&agrave;i th&aacute;ng) để trải nghiệm v&agrave; &aacute;p dụng những g&igrave; được học. Từ đ&oacute; sẽ l&agrave; nền tảng kinh nghiệm tương đối tốt cho em khi ra trường bắt đầu ứng tuyển l&agrave;m việc ch&iacute;nh thức. Hoặc trong qu&aacute; tr&igrave;nh đ&oacute;, em c&oacute; thể xin l&agrave;m parttime - thực tập kh&ocirc;ng lương li&ecirc;n tục trong nhiều th&aacute;ng, vừa để trải nghiệm m&ocirc;i trường, vừa học hỏi th&ecirc;m kinh nghiệm. Sẽ c&oacute; 2 trường hợp xảy ra: một l&agrave; khi c&ocirc;ng ty cần người m&agrave; em đ&aacute;p ứng được, em sẽ được giữ lại lu&ocirc;n, hai l&agrave; với số thời gian kinh nghiệm như thế, chắc chắn sẽ l&agrave; một điểm cộng rất gi&aacute; trị cho CV của em.</p>\r\n\r\n<p>Ngo&agrave;i ra, từ năm 3 ở trường cũng bắt đầu học một số m&ocirc;n chuy&ecirc;n ng&agrave;nh, nếu em học nghiệp vụ ở ngo&agrave;i nữa th&igrave; sẽ c&oacute; c&aacute;i nh&igrave;n tổng quan về nghiệp vụ, tự tin hơn với c&aacute;c b&agrave;i thảo luận, thuyết tr&igrave;nh hay chuy&ecirc;n đề, b&agrave;i kiểm tra tr&ecirc;n lớp như : nghiệp vụ ngoại thương, vận tải quốc tế, bảo hiểm, thanh to&aacute;n quốc tế&hellip;</p>\r\n\r\n<p>Nếu bạn sợ rằng học sớm sẽ qu&ecirc;n th&igrave; tin vui cho bạn đ&acirc;y. Học với ch&uacute;ng t&ocirc;i l&agrave; c&aacute;c bạn đ&atilde; được &ldquo; bảo h&agrave;nh trọn đời&rdquo;, học ph&iacute; đ&oacute;ng 1 lần nhưng được học lại miễn ph&iacute; 1 hay nhiều lần, 1 học phần hoặc cả kh&oacute;a, bất kể những g&igrave; bạn muốn. Học để update kiến thức, để được tr&ograve; chuyện hay đơn giản l&agrave; &ldquo;ngắm&rdquo; c&ocirc; gi&aacute;o.&nbsp;</p>\r\n\r\n<p>Kết luận: Như vậy học từ năm 3 l&agrave; c&aacute;c em đang &ldquo; tiết kiệm chi ph&iacute;&rdquo; cả thời gian lẫn tiền bạc đ&oacute; chứ?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>10.Nếu sau khi đ&atilde; theo học m&agrave; t&ocirc;i kh&ocirc;ng h&agrave;i học th&igrave; c&oacute; được ho&agrave;n lại học ph&iacute; kh&ocirc;ng?</p>\r\n\r\n<p>Trả lời: Bạn sẽ được ho&agrave;n lại 100% học ph&iacute; nếu sau khi ho&agrave;n th&agrave;nh 100% thời lượng học m&agrave; bạn kh&ocirc;ng thấy h&agrave;i l&ograve;ng. Nếu kh&ocirc;ng như cam kết, vui l&ograve;ng li&ecirc;n hệ hotline: 0906246584 để được claim sớm nhất.</p>','/99/5e/e9/5c7e1d46e647f.jpg',NULL,321,0,'2019-03-05 13:55:02',NULL,'Có được thông tin đầy đủ trước khi đưa ra quyết định theo học tại nơi nào đó là nhu cầu chính đáng của mỗi bạn. Với những câu hỏi FAQ dưới đây mà chúng tôi nhận được, nếu không nằm trong câu hỏi của bạn thì bạn có thể liên hệ Hotline 0906246584 để được tư','có nên học xuất nhập khẩu, học xuất nhập khẩu từ','','',2,0,'vi','cau-hoi-thuong-gap-cua-cac-ban-truoc-khi-tham-gia-khoa-hoc',5,26,26,'2019-03-05 13:55:02','2019-03-05 13:55:02'),(284,'NHỮNG SAI LẦM DẪN ĐẾN KHÔNG ĐƯỢC BỒI THƯỜNG BẢO HIỂM HÀNG HÓA','','Bảo hiểm trong đời sống nói chung và bảo hiểm hàng hóa trong Xuất nhập khẩu nói riêng đóng vai trò vô cùng quan trọng. Tuy nhiên, rất nhiều doanh nghiệp chưa có kiến thức/hiểu biết khi mua bảo hiểm hàng hóa xuất nhập khẩu. Có công ty đã mua rồi nhưng vì lý do nào đó mà không được bồi thường nên họ nghĩ bảo hiểm chỉ mua nhưng rất khó nhận được tiền.','<h3><span style=\"color:#2F4F4F\">Bảo hiểm trong đời sống n&oacute;i chung v&agrave; bảo hiểm h&agrave;ng h&oacute;a trong Xuất nhập khẩu n&oacute;i ri&ecirc;ng đ&oacute;ng vai tr&ograve; v&ocirc; c&ugrave;ng quan trọng. Tuy nhi&ecirc;n, rất nhiều doanh nghiệp chưa c&oacute; kiến thức/hiểu biết khi mua bảo hiểm h&agrave;ng h&oacute;a xuất nhập khẩu. C&oacute; c&ocirc;ng ty đ&atilde; mua rồi nhưng v&igrave; l&yacute; do n&agrave;o đ&oacute; m&agrave; kh&ocirc;ng được bồi thường n&ecirc;n họ nghĩ bảo hiểm chỉ mua nhưng rất kh&oacute; nhận được tiền. Điều&nbsp;n&agrave;y c&oacute; phải vậy kh&ocirc;ng?</span></h3>\r\n\r\n<h3><span style=\"color:#2F4F4F\">H&atilde;y c&ugrave;ng lắng nghe một số lời khuy&ecirc;n từ chị Đo&agrave;n Vinh-&nbsp;chuy&ecirc;n gia trong lĩnh vực bảo hiểm h&agrave;ng h&oacute;a Xuất nhập khẩu từ h&atilde;ng MIC- Bảo Hiểm Qu&acirc;n Đội.</span></h3>\r\n\r\n<h3><span style=\"color:#2F4F4F\">Theo lời chị, chị đ&atilde;&nbsp;tiếp nhận một v&agrave;i vụ bồi thường tổn thất h&agrave;ng h&oacute;a, c&oacute; những sự vụ từ chối hoặc chế t&agrave;i bảo hiểm do kh&aacute;ch h&agrave;ng sơ suất trong qu&aacute; tr&igrave;nh mua bảo hiểm h&agrave;ng h&oacute;a hoặc do c&aacute;n bộ kinh doanh tư vấn kh&ocirc;ng r&otilde;. Với kinh nghiệm trong nghề bảo hiểm, chị&nbsp;chia sẻ&nbsp;lại một v&agrave;i điểm quan trọng cần lưu &yacute; để tr&aacute;nh kh&ocirc;ng được b&ograve;i thường bảo hiểm như sau: </span></h3>\r\n\r\n<h2><strong><span style=\"color:#2F4F4F\">Thứ nhất: Chứng từ đầy đủ</span></strong></h2>\r\n\r\n<h3><span style=\"color:#2F4F4F\">Trong giấy y&ecirc;u cầu mua bảo hiểm (Điền đầy đủ th&ocirc;ng tin theo FORM của nh&agrave; bảo hiểm, ch&uacute; &yacute; một số điểm: - Chuyển tải: Cho ph&eacute;p hay kh&ocirc;ng cho ph&eacute;p ( N&ecirc;n mua cho ph&eacute;p chuyển tải) - Phương thức đ&oacute;ng g&oacute;i: Đường biển th&igrave; ghi r&otilde; đ&oacute;ng nguy&ecirc;n cont hay gh&eacute;p cont hay rời. Khi sai phương thức c&oacute; thể sẽ chế t&agrave;i trong kh&acirc;u bồi thường </span></h3>\r\n\r\n<h2><strong><span style=\"color:#2F4F4F\">Thứ hai: Lu&ocirc;n biết trước th&ocirc;ng tin về hợp đồng</span></strong></h2>\r\n\r\n<h3><span style=\"color:#2F4F4F\">Y&ecirc;u cầu doanh nghiệp Bảo hiểm gửi những chứng từ sau: Scan giấy chứng nhận v&agrave; h&oacute;a đơn qua mail trước (mail trả lời l&agrave; mail c&aacute;n bộ c&ocirc;ng nh&acirc;n vi&ecirc;n của bảo hiểm, v&iacute; dụ như </span><a href=\"mailto:vinhdtt.ht@mic.vn\"><span style=\"color:#2F4F4F\">vinhdtt.ht@mic.vn</span></a><span style=\"color:#2F4F4F\"> tr&aacute;nh d&ugrave;ng gmail), sau đ&oacute; gửi bản gốc sau. </span></h3>\r\n\r\n<h2><strong><span style=\"color:#2F4F4F\">Thứ ba: Kịp thời bổ sung th&ocirc;ng tin</span></strong></h2>\r\n\r\n<h3><span style=\"color:#2F4F4F\">Khi c&oacute; bất kỳ sự thay đổi n&agrave;o so với th&ocirc;ng tin y&ecirc;u cầu bảo hiểm, th&igrave; l&agrave;m ngay sửa đổi bổ sung ghi nhận sự thay đổi; đặc biệt l&agrave; thay đổi h&agrave;nh tr&igrave;nh bảo hiểm, đối tượng bảo hiểm&hellip; </span></h3>\r\n\r\n<h2><strong><span style=\"color:#2F4F4F\">Thứ tư: Thanh to&aacute;n ph&iacute; bảo hiểm</span></strong></h2>\r\n\r\n<h3><span style=\"color:#2F4F4F\">C&aacute;c đơn bảo hiểm lu&ocirc;n c&oacute; một phần l&agrave; thời hạn thanh to&aacute;n ph&iacute;, nếu thanh to&aacute;n chậm so với thời hạn th&igrave; sẽ bị từ chối nếu ph&aacute;t sinh bồi thường. Trường hợp thanh to&aacute;n muộn, sau khi thanh to&aacute;n phải y&ecirc;u cầu kh&ocirc;i phục hiệu lực bảo hiểm. </span></h3>\r\n\r\n<h3><span style=\"color:#2F4F4F\">Đ&ocirc;i điều trao đổi trong nghề, nếu c&oacute; trao đổi th&ecirc;mhay muốn&nbsp; li&ecirc;n hệ tư vấn (miễn ph&iacute;) c&aacute;c bạn li&ecirc;n hệ qua số điện thoại: 0945.109.005 hoặc email: </span><a href=\"mailto:vinhdtt.ht@mic.vn\"><span style=\"color:#2F4F4F\">vinhdtt.ht@mic.vn</span></a><span style=\"color:#2F4F4F\"> </span></h3>\r\n\r\n<h3><span style=\"color:#2F4F4F\">Tr&acirc;n trọng ./.</span></h3>','/34/45/53/5c7fa54c567d0.jpg',NULL,386,25,'2019-03-06 17:47:40',NULL,'Bảo hiểm trong đời sống nói chung và bảo hiểm hàng hóa trong Xuất nhập khẩu nói riêng đóng vai trò vô cùng quan trọng. Tuy nhiên, rất nhiều doanh nghiệp chưa có kiến thức/hiểu biết khi mua bảo hiểm hàng hóa xuất nhập khẩu. Có công ty đã mua rồi nhưng vì l','bảo hiểm, bảo hiểm hàng hóa, bảo hiểm xuất nhập khẩu','','',2,0,'vi','nhung-sai-lam-dan-den-khong-duoc-boi-thuong-bao-hiem-hang-hoa',6,26,26,'2019-03-06 17:47:40','2019-03-06 17:47:40'),(285,'04 WEBSITE HỖ TRỢ TRA MÃ HS CHO DÂN XNK-LOGISTICS','','Chính vì không hiểu rõ và nắm bắt được các quy tắc tra mã HS và những nguyên tắc chung khi sắp xếp một tên hàng phù hợp để dễ tra cứu nên nhiều bạn đã gặp rất nhiều rắc rối khi không biết cách tìm được mã HS phù hợp. Vì thế, các website sau đây ra đời nhằm hỗ trợ những bạn mới đi làm có thể yên tâm hơn về HS của mình và kể cả những anh chị lâu năm trong nghề cũng có thể tham khảo thêm khi cần.','<p>Ph&acirc;n loại h&agrave;ng h&oacute;a hay c&ograve;n gọi l&agrave;&nbsp;<a href=\"https://www.facebook.com/hashtag/tram%C3%A3hs?source=feed_text&amp;epa=HASHTAG\">#tram&atilde;HS</a>&nbsp;l&agrave; một trong số những nghiệp vụ CỰC KỲ QUAN TRỌNG đối với những bạn l&agrave;m việc trong nghề&nbsp;<a href=\"https://www.facebook.com/hashtag/xnklogistics?source=feed_text&amp;epa=HASHTAG\">#XNKLogistics</a>&nbsp;ở c&aacute;c mảng li&ecirc;n quan trực tiếp đến&nbsp;<a href=\"https://www.facebook.com/hashtag/th%E1%BB%A7t%E1%BB%A5ch%E1%BA%A3iquan?source=feed_text&amp;epa=HASHTAG\">#thủtụchảiquan</a>, thuế, ch&iacute;nh s&aacute;ch mặt h&agrave;ng.</p>\r\n\r\n<p>C&oacute; hai đối tượng ch&iacute;nh thường xuy&ecirc;n xảy ra &quot;tranh chấp&quot; với nhau về m&atilde; HS đ&oacute; l&agrave; Hải quan v&agrave; chủ h&agrave;ng (doanh nghiệp) do t&iacute;nh nhạy cảm về thuế v&agrave; mang t&iacute;nh chủ quan của người ph&acirc;n loại. Do đ&oacute;, những người mới v&agrave;o nghề thường rất &quot;sợ&quot; phải l&agrave;m phần việc n&agrave;y. V&igrave; cơ bản, mọi người thường được đồng nghiệp đi trước dạy loa qua về c&aacute;ch đọc biểu thuế v&agrave; t&igrave;m một t&ecirc;n h&agrave;ng giống thế c&oacute; trong biểu thuế (...) thấy hợp l&yacute; l&agrave; &aacute;p v&agrave;o th&ocirc;i. Ch&iacute;nh v&igrave; kh&ocirc;ng hiểu r&otilde; v&agrave; nắm bắt được c&aacute;c quy tắc tra m&atilde; HS v&agrave; những nguy&ecirc;n tắc chung khi sắp xếp một t&ecirc;n h&agrave;ng ph&ugrave; hợp để dễ tra cứu n&ecirc;n nhiều bạn đ&atilde; gặp rất nhiều rắc rối khi kh&ocirc;ng biết c&aacute;ch t&igrave;m được m&atilde; HS ph&ugrave; hợp. V&igrave; thế, c&aacute;c website sau đ&acirc;y ra đời nhằm hỗ trợ những bạn mới đi l&agrave;m c&oacute; thể y&ecirc;n t&acirc;m hơn về HS của m&igrave;nh v&agrave; kể cả những anh chị l&acirc;u năm trong nghề cũng c&oacute; thể tham khảo th&ecirc;m khi cần.</p>\r\n\r\n<p>1.&nbsp;<a href=\"https://l.facebook.com/l.php?u=http%3A%2F%2Fhssearch.net%2F%3Ffbclid%3DIwAR0fNsN3Cu2jfpj_CBD4wLC14gw1KpYP7e_O76Sqv8kxA1VIKSDX3nvjBVs&amp;h=AT1H4omofob7KmYyRrr0v7m7SKRma21YMYu5fT4JLax4GF4S_X95kKRbt0xZKbrnkBTJOdhcCjJxgetKKI63d-G8Q05Wzy8q51ZaJF-mFg3qEc_ApCKp3kE0pSjoIY1xKIU-NdNYKvIvi79BT67zUaEQ1XeU76ucxRdEAC35hGtyPU5ZhA\" rel=\"noopener nofollow\" target=\"_blank\">http://hssearch.net</a>&nbsp;Website n&agrave;y phải đăng k&yacute; th&ocirc;ng tin c&aacute; nh&acirc;n kh&aacute; loằng ngoằng v&agrave; chi tiết. Thực sự c&aacute; nh&acirc;n t&ocirc;i th&igrave; kh&ocirc;ng th&iacute;ch việc khai b&aacute;o qu&aacute; nhiều (tr&ecirc;n 3) trường th&ocirc;ng tin l&ecirc;n c&aacute;c website/diễn đ&agrave;n. Tuy nhi&ecirc;n, c&aacute;c bạn c&oacute; thể r&egrave;n luyện t&iacute;nh ki&ecirc;n nhẫn th&ocirc;ng qua việc đăng k&yacute; v&agrave; sau đ&oacute; c&aacute;c bạn c&oacute; 7 ng&agrave;y d&ugrave;ng thử miễn ph&iacute;. Sau đ&oacute; nếu c&aacute;c bạn thấy hữu &iacute;ch th&igrave; đăng k&yacute; th&agrave;nh vi&ecirc;n trả tiền, 1 th&aacute;ng =200.000đ, 6 th&aacute;ng =300.000đ v&agrave; 1 năm =400.000đ</p>\r\n\r\n<p>2.&nbsp;<a href=\"https://l.facebook.com/l.php?u=http%3A%2F%2Fbieuthue.net%2F%3Ffbclid%3DIwAR2QbQVIVRMHwWdlV5rI1-SerL3U-ODkdaCSLyFxU7ob2OJReDiyo7dwiKk&amp;h=AT3ifQJaXHjAjWwPQUF1tQJYSup9nusgenViqNgbOIzQUi6VWtvMGO56VjVAKvVlek5Yi0-obwpG04T8zsq3lleaB0svdpMee6vy6TOpcDKhwrV8asEjTFvlCkSV1GKpzWjt0n9g8aOooGf5M2acLYELeBvWBB2WqaWoajlz_3fdpTMXtQ\" rel=\"noopener nofollow\" target=\"_blank\">http://bieuthue.net/</a>&nbsp;Website n&agrave;y c&oacute; phải đăng k&yacute; nhưng cảm nhận l&agrave; th&acirc;n thiện v&agrave; dễ chịu hơn. Nhưng kể cả bạn kh&ocirc;ng đăng k&yacute; g&igrave; th&igrave; bạn vẫn sử dụng được ho&agrave;n to&agrave;n miễn ph&iacute;. Ngo&agrave;i việc tra cứu HS từ kho dữ liệu của họ với t&ecirc;n h&agrave;ng đầy đủ, chi tiết, gi&uacute;p c&aacute;c bạn hiểu c&aacute;ch để diễn giải một t&ecirc;n h&agrave;ng thế n&agrave;o cho hợp l&yacute; th&igrave; website n&agrave;y c&ograve;n li&ecirc;n kết c&aacute;c kết quả ph&acirc;n t&iacute;ch ph&acirc;n loại từ c&aacute;c DN kh&aacute;c nhau đ&atilde; từng thực hiện PTPL cho h&agrave;ng h&oacute;a của DN họ. Đ&acirc;y cũng l&agrave; một trong những căn cứ &quot;mạnh&quot; c&oacute; t&iacute;nh ph&aacute;p l&yacute; trong ph&acirc;n loại h&agrave;ng h&oacute;a. Lưu &yacute; c&aacute;c bạn, kết quả PTPL chỉ c&oacute; gi&aacute; trị &aacute;p dụng trong v&ograve;ng 03 năm th&ocirc;i nh&eacute;!</p>\r\n\r\n<p>3.&nbsp;<a href=\"https://l.facebook.com/l.php?u=https%3A%2F%2Ftinsaigon247.blogspot.com%2F%3Ffbclid%3DIwAR2RuaKyMOsnKiO1r9w0PkY0vGf3oIN1rAW53Gt7a_0ECeeVjYIo53Dr5ts&amp;h=AT1AgVaNRIc9vYeVc-w_SDK_W4kTDgo3YW3aem4sG67UBWZZlP3HMWzPnfGbhd3QeI8wNXkH2g_xdweSJAWK04OGY8FegdHP5BUNSPdBBdT0sXcwTZYND6G4KhMSDmQon6znkfG03qMvz6cYOEjvRBxN9_vzLSerONgH6Ag8Hh0yVCC3dw\" rel=\"noopener nofollow\" target=\"_blank\">http://tinsaigon247.blogspot.com</a>&nbsp;Đ&acirc;y l&agrave; một blog đơn giản với lượng dữ liệu khổng lồ bao gồm cả t&ecirc;n h&agrave;ng Tiếng Anh v&agrave; Tiếng Việt, gi&uacute;p c&aacute;c bạn c&oacute; thể tham chiếu th&ecirc;m từ cả hai nguồn. Do t&iacute;nh năng lọc của blog kh&ocirc;ng th&ocirc;ng minh bằng website n&ecirc;n c&aacute;c bạn sẽ phải th&ecirc;m động t&aacute;c &quot;Ctrl F&quot; nếu c&aacute;c bạn muốn t&igrave;m ch&iacute;nh x&aacute;c cụm từ của m&igrave;nh nằm ở đ&acirc;u trong danh s&aacute;ch kết quả hiển thị.</p>\r\n\r\n<p>4.&nbsp;<a href=\"https://l.facebook.com/l.php?u=https%3A%2F%2Fmy.40ftcloud.com%2F%3Ffbclid%3DIwAR3Nn5QJnnb2iN2HqRZ1fDC-ENGmzz7Cerl2CuiZvsU8tmXfBt9hod7MYo8&amp;h=AT3olxBzWyIY9vaoS6iQ8zMbzJ173GrF-sXNatM_6TX-ukcwv0Xz4wO2wk4MhuL98UhMmu_9drc6oWAJ6RDLWj4RqAD06eVzruXtRQB6M5vUMVOPwUQfFjWWhU-X7hBFFe5BbKox3Y_VKKMMIXKvcas9L3gvVCI8cWj-kidlLtTad0M11A\" rel=\"noopener nofollow\" target=\"_blank\">https://my.40ftcloud.com/</a>&nbsp;Website n&agrave;y thực sự l&agrave; một sự t&iacute;ch hợp độc đ&aacute;o t&iacute;nh năng từ tất cả c&aacute;c website hỗ trợ nghiệp vụ logistics từ trước tới giờ, trong đ&oacute; c&oacute; tra m&atilde; HS. Điều đặc biệt l&agrave; c&aacute;c bạn được sử dụng to&agrave;n bộ t&agrave;i nguy&ecirc;n v&agrave; chức năng tr&ecirc;n website n&agrave;y MIỄN PH&Iacute; HO&Agrave;N TO&Agrave;N, giao diện th&acirc;n thiện, dễ d&ugrave;ng. Để người d&ugrave;ng c&oacute; thể nắm bắt được hết tất cả c&aacute;c t&iacute;nh năng, n&ecirc;n web c&oacute; phần hướng dẫn rất kỹ lưỡng cho c&aacute;c bạn ngay từ đầu (c&aacute;c bạn h&atilde;y đăng nhập v&agrave; trải nghiệm). Bật m&iacute;! Website n&agrave;y do một thầy gi&aacute;o của Han Exim c&ugrave;ng cộng sự của m&igrave;nh x&acirc;y dựng n&ecirc;n, nhằm hỗ trợ c&aacute;c doanh nghiệp, c&aacute;c anh chị đang c&ocirc;ng t&aacute;c trong nghề v&agrave; đặc biệt c&aacute;c bạn mới v&agrave;o nghề th&igrave; đ&acirc;y thực sự l&agrave; một c&ocirc;ng cụ kh&ocirc;ng thể tuyệt vời hơn!</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, nếu bạn muốn nắm vững v&agrave; chắc về quy tắc, phương ph&aacute;p cũng như c&aacute;c c&ocirc;ng cụ, nguồn lực m&igrave;nh c&oacute; thể sử dụng trong qu&aacute; tr&igrave;nh tra m&atilde; HS (ph&acirc;n loại h&agrave;ng h&oacute;a), bạn h&atilde;y c&acirc;n nhắc việc tham gia kh&oacute;a học XNK-logistics to&agrave;n diện của Han Exim nh&eacute;!</p>\r\n\r\n<p>By Sarah.</p>','/38/25/53/5cc8ed33ae8cf.jpg',NULL,311,0,'2019-05-01 07:52:14',NULL,'Thống kê 04 website hỗ trợ tra mã HS được đánh giá uy tín nhất và tiện lợi nhất cho người sử dụng.','tra mã HS, HS code, phân loại hàng hóa, website tra mã HS uy tín','','',2,0,'vi','04-website-ho-tro-tra-ma-hs-cho-dan-xnk-logistics',9,27,27,'2019-05-01 07:49:55','2019-05-01 07:52:14'),(286,'KIỂM TRA SAU THÔNG QUAN CẦN CHUẨN BỊ NHỮNG GÌ','','Doanh nghiệp phải chuẩn bị những gì? Giấy tờ gì để trình cho cán bộ hải quan của đội kiểm tra sau thông quan? Tờ khai xuất nhập khẩu, các tài khoản kế toán, bảng tổng hợp nguyên phụ liệu? Bảng cân đối định mức?','<p><span style=\"color:#000080\"><span style=\"font-size:14px\">Ch&agrave;o c&aacute;c bạn, c&aacute;c anh chị em đang l&agrave;m trong lĩnh vực xuất nhập khẩu. Sau một thời gian kh&aacute; d&agrave;i chuy&ecirc;n t&acirc;m v&agrave;o việc giảng dạy về l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n, nay đội ngũ giảng dạy của HAN EXIM CLUB quyết định thực hiện loạt b&agrave;i viết về <strong>b&aacute;o c&aacute;o quyết to&aacute;n </strong>v&agrave; việc <strong>kiểm tra sau th&ocirc;ng quan</strong> tại c&aacute;c doanh nghiệp chế xuất, doanh nghiệp sản xuất xuất khẩu v&agrave; doanh nghiệp gia c&ocirc;ng. Nhằm mục đ&iacute;ch chia sẻ kiến thức cho những bạn/anh/chị kh&ocirc;ng c&oacute; điều kiện tham gia <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\"><strong><em>học l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n hải quan</em></strong></a> tại Trung t&acirc;m đ&agrave;o tạo nghiệp vụ XNK &amp; Logistics HAN EXIM CLUB của ch&uacute;ng t&ocirc;i.</span></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#000080\">Kiến thức l&agrave; v&ocirc; bi&ecirc;n, kinh nghiệm cũng c&oacute; giới hạn, n&ecirc;n b&agrave;i viết của ch&uacute;ng t&ocirc;i lu&ocirc;n mong muốn được ho&agrave;n thiện hơn mỗi ng&agrave;y v&agrave; c&agrave;ng mong được bạn đọc bổ sung th&ecirc;m th&ocirc;ng qua c&aacute;c trải nghiệm của c&aacute;c bạn. Ch&uacute;ng t&ocirc;i cũng muốn nhấn mạnh rằng tất cả những kiến thức n&agrave;y đều c&oacute; trong kh&oacute;a học &ldquo;Hướng dẫn thực h&agrave;nh l&agrave;m <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\"><strong><em>b&aacute;o c&aacute;o quyết to&aacute;n</em></strong></a> theo th&ocirc;ng tư 39&rdquo; khai giảng h&agrave;ng th&aacute;ng của trung t&acirc;m. C&aacute;c bạn c&oacute; thể tham khảo th&ocirc;ng tin chi tiết về kh&oacute;a học tại </span><a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\"><span style=\"color:#000080\">Đ&Acirc;Y</span></a><span style=\"color:#000080\">. Hoặc đăng k&yacute; học trực tiếp tại </span><a href=\"https://docs.google.com/forms/d/e/1FAIpQLSdK7vPbgEEvrgZY6KBs0npd0igmlaIeMQwIg4r0hAsN34HqQQ/viewform\"><span style=\"color:#000080\">Đ&Acirc;Y</span></a><span style=\"color:#000080\">. Xin cảm ơn.</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">Nếu một ng&agrave;y đẹp trời, doanh nghiệp của bạn nhận được c&ocirc;ng văn về việc &ldquo;Kiểm tra sau th&ocirc;ng quan&rdquo;, mới nghe tới cụm từ kiểm tra sau th&ocirc;ng quan th&ocirc;i bạn đ&atilde; to&aacute;t mồ h&ocirc;i rồi, chứ nhận được c&ocirc;ng văn giấy trắng mực đen như thế n&agrave;y th&igrave; th&ocirc;i... ch&acirc;n tay rụng rời, tim th&igrave; muốn nhảy ra khỏi lồng ngực lu&ocirc;n. Mất ăn mất ngủ từ sếp cho tới nh&acirc;n vi&ecirc;n. Nhưng việc g&igrave; đến th&igrave; phải đến th&ocirc;i, đằng n&agrave;o th&igrave; cũng phải l&agrave;m, kh&ocirc;ng nay th&igrave; mai. Vậy bạn v&agrave; doanh nghiệp phải chuẩn bị những g&igrave;? Giấy tờ g&igrave; để tr&igrave;nh cho c&aacute;n bộ hải quan của đội kiểm tra sau th&ocirc;ng quan???</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">Dưới đ&acirc;y l&agrave; thứ tự những giấy tờ m&agrave; bạn, doanh nghiệp của bạn phải chuẩn bị:</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">1/ Họ v&agrave; t&ecirc;n của Gi&aacute;m đốc, kế to&aacute;n trưởng, ph&ograve;ng XNK, kế to&aacute;n kho (NL, TP), thủ kho (Năm sinh, quốc tịch, chứng minh nh&acirc;n d&acirc;n (hoặc hộ chiếu);</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">2/ Giấy chứng nhận đăng k&yacute; đầu tư;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">3/ Số lượng m&aacute;y m&oacute;c, thiết bị;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">4/ Số lượng nh&acirc;n vi&ecirc;n (quản l&yacute;, c&ocirc;ng nh&acirc;n trực tiếp);</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">5/ Bảng k&ecirc; tờ khai nhập khẩu NPL (k&egrave;m hồ sơ tờ khai)</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">6/ Bảng k&ecirc; tờ khai&nbsp;xuất khẩu th&agrave;nh phẩm&nbsp;(k&egrave;m hồ sơ tờ khai);</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">7/ Sổ theo d&otilde;i phế liệu, phế phẩm;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">8/ Hồ sơ x&acirc;y dựng định mức sản phẩm;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">9/ B&aacute;o c&aacute;o t&agrave;i ch&iacute;nh, đ&atilde; kiểm to&aacute;n 2015, 2016 ,2017, 2018&hellip; h&igrave;nh thức kế to&aacute;n, ni&ecirc;n độ kế to&aacute;n, đồng tiền hạch to&aacute;n;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">10/ Phương ph&aacute;p hạch to&aacute;n h&agrave;ng tồn kho;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">11/ Sổ chi tiết nguy&ecirc;n liệu;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">12/ Sổ tổng hợp nguy&ecirc;n vật liệu;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">13/ Sổ chi tiết th&agrave;nh phẩm;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">14/ Sổ tổng hợp th&agrave;nh phẩm;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">15/ Thẻ kho (Sổ kho) - nguy&ecirc;n liệu - th&agrave;nh phẩm;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">16/ Phiếu nhập kho nguy&ecirc;n liệu;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">17/ Phiếu xuất kho nguy&ecirc;n liệu;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">18/ Phiếu nhập kho th&agrave;nh phẩm;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">19/ Phiếu xuất kho th&agrave;nh phẩm;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">20/ Bi&ecirc;n bản kiểm k&ecirc; kho (nguy&ecirc;n liệu - th&agrave;nh phẩm);</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">21/ Bi&ecirc;n bản đối chiếu c&ocirc;ng nợ đến;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">22/ H&oacute;a đơn thuế gi&aacute; trị gia tăng hoặc invoice (xuất b&aacute;n th&agrave;nh phẩm cho nước ngo&agrave;i);</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">23/ H&oacute;a đơn thuế VAT mua nguy&ecirc;n liệu trong nước để cung ứng sản xuất xuất khẩu (nếu c&oacute;);</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">24/ Số c&aacute;i t&agrave;i khoản 111, 112, 131, 138, 152, 154, 155, 157, 331, 338, 511, 611, 621, 622, 627, 631, 632, 711;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">25/ Sổ nhật k&yacute; chung;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">26/ Bảng c&acirc;n đối ph&aacute;t sinh;</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">27/ Bảng t&iacute;nh lượng nguy&ecirc;n liệu xuất (sử dụng);</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">28/ Giải tr&igrave;nh nguy&ecirc;n liệu, th&agrave;nh tồn kho, b&aacute;n th&agrave;nh phẩm, ho&agrave;n thiện, giữa BC t&agrave;i ch&iacute;nh v&agrave; B&aacute;o c&aacute;o quyết to&aacute;n (Nếu c&oacute; ch&ecirc;nh lệch);</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">Tất cả 28 mục th&ocirc;i, nhưng đủ l&agrave;m bạn đau đầu rồi đấy, cố gắng l&ecirc;n nh&eacute;. Hẹn gặp c&aacute;c bạn ở những b&agrave;i viết tiếp theo.</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">Mọi b&agrave;i copy vui l&ograve;ng ghi nguồn của ch&uacute;ng t&ocirc;i: Trung t&acirc;m đ&agrave;o tạo nghiệp vụ XNK - Logistics HAN EXIM CLUB.</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">Địa chỉ: Số 4 - ng&aacute;ch 23 - ng&otilde; 82 - Phố Ch&ugrave;a L&aacute;ng - Quận Đống Đa -&nbsp;H&agrave; Nội</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">H&atilde;y l&agrave; người văn minh c&aacute;c bạn nh&eacute;.</span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">Hotline về lịch khai giảng, học ph&iacute; lớp học b&aacute;o c&aacute;o quyết to&aacute;n vui l&ograve;ng li&ecirc;n hệ: 0974 155 033 Mr Long. </span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:14px\">Tr&acirc;n trọng.</span></span></p>','/f0/e4/4f/5cdd835fd96bd.jpg',NULL,346,0,'2019-05-16 00:00:00',NULL,'','báo cáo quyết toán, kiểm tra sau thông quan, học báo cáo quyết toán hải quan, lớp học báo cáo quyết toán','','',2,1,'vi','kiem-tra-sau-thong-quan-can-chuan-bi-nhung-gi',10,27,27,'2019-05-16 22:19:16','2019-05-19 22:58:30'),(287,'BÁO CÁO QUYẾT TOÁN THEO THÔNG TƯ 39','','Trong khuôn khổ bài viết này, chúng tôi xin đề cập cụ thể thông tư, nghị định nào đang áp dụng cho việc làm báo cáo quyết toán hải quan hàng năm này của các doanh nghiệp sản xuất xuất khẩu (sxxk), gia công và chế xuất.','<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ch&agrave;o c&aacute;c bạn, c&oacute; lẽ bạn n&agrave;o đ&atilde; <strong>l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n</strong> đều đ&atilde; nắm được hết những th&ocirc;ng tư, nghị định li&ecirc;n quan đến việc l&agrave;m b&aacute;o c&aacute;o n&agrave;y, tuy nhi&ecirc;n với những bạn đang c&oacute; &yacute; định thử sức với n&oacute; th&igrave; chưa chắc đ&atilde; biết, th&ecirc;m nữa ph&aacute;p luật hải quan lại nhiều, chồng ch&eacute;o l&ecirc;n nhau. N&ecirc;n trong khu&ocirc;n khổ b&agrave;i viết n&agrave;y, ch&uacute;ng t&ocirc;i xin đề cập cụ thể th&ocirc;ng tư, nghị định n&agrave;o đang &aacute;p dụng cho việc <strong>l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n hải quan</strong> h&agrave;ng năm n&agrave;y của c&aacute;c doanh nghiệp sản xuất xuất khẩu (sxxk), gia c&ocirc;ng v&agrave; chế xuất.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">B&aacute;o c&aacute;o quyết to&aacute;n t&igrave;nh h&igrave;nh sử dụng nguy&ecirc;n phụ liệu, vật tư nhập khẩu, h&agrave;ng h&oacute;a xuất khẩu được quy định cụ thể tại điều 60 của th&ocirc;ng tư 39 năm 2018. Cụ thể:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">1. Tổ chức, c&aacute; nh&acirc;n c&oacute; hoạt động gia c&ocirc;ng, sản xuất xuất khẩu (bao gồm DNCX) thực hiện cung cấp th&ocirc;ng tin li&ecirc;n quan đến hoạt động nhập kho nguy&ecirc;n liệu, vật tư nhập khẩu, xuất kho nguy&ecirc;n liệu, vật tư; nhập kho th&agrave;nh phẩm, xuất kho th&agrave;nh phẩm v&agrave; c&aacute;c chỉ ti&ecirc;u th&ocirc;ng tin quy định tại mẫu số 30 Phụ lục II ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y từ hệ thống quản trị sản xuất của tổ chức, c&aacute; nh&acirc;n với Chi cục Hải quan nơi đ&atilde; th&ocirc;ng b&aacute;o cơ sở sản xuất th&ocirc;ng qua Hệ thống. Tổ chức, c&aacute; nh&acirc;n c&oacute; tr&aacute;ch nhiệm cung cấp th&ocirc;ng tin ngay sau khi ph&aacute;t sinh nghiệp vụ li&ecirc;n quan đến c&aacute;c chỉ ti&ecirc;u th&ocirc;ng tin quy định tại mẫu số 30 Phụ lục II ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trước khi thực hiện việc trao đổi th&ocirc;ng tin lần đầu khi kết nối với Hệ thống của cơ quan hải quan, tổ chức, c&aacute; nh&acirc;n thực hiện chốt tồn đầu kỳ nguy&ecirc;n liệu, vật tư, sản phẩm với Chi cục Hải quan nơi đ&atilde; th&ocirc;ng b&aacute;o cơ sở sản xuất.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Cơ quan hải quan c&oacute; tr&aacute;ch nhiệm c&ocirc;ng bố chuẩn dữ liệu để thực hiện việc trao đổi th&ocirc;ng tin giữa Hệ thống của tổ chức, c&aacute; nh&acirc;n với Hệ thống xử l&yacute; dữ liệu điện tử của hải quan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tr&ecirc;n cơ sở th&ocirc;ng tin cung cấp, cơ quan hải quan thực hiện ph&acirc;n t&iacute;ch, đ&aacute;nh gi&aacute; sự ph&ugrave; hợp giữa c&aacute;c dữ liệu do tổ chức, c&aacute; nh&acirc;n gửi qua hệ thống với dữ liệu tr&ecirc;n hệ thống của cơ quan hải quan. Trường hợp x&aacute;c định phải kiểm tra theo quy định tại khoản 1, khoản 2 Điều 59 Th&ocirc;ng tư n&agrave;y th&igrave; thực hiện việc kiểm tra t&igrave;nh h&igrave;nh sử dụng, tồn kho nguy&ecirc;n liệu, vật tư, m&aacute;y m&oacute;c thiết bị v&agrave; h&agrave;ng h&oacute;a xuất khẩu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">2. Trường hợp tổ chức, c&aacute; nh&acirc;n chưa thực hiện cung cấp th&ocirc;ng tin theo quy định tại khoản 1 Điều n&agrave;y với cơ quan hải quan th&igrave; định kỳ b&aacute;o c&aacute;o quyết to&aacute;n t&igrave;nh h&igrave;nh sử dụng nguy&ecirc;n liệu, vật tư nhập khẩu, h&agrave;ng h&oacute;a xuất khẩu với cơ quan hải quan theo năm t&agrave;i ch&iacute;nh. Tổ chức, c&aacute; nh&acirc;n nộp b&aacute;o c&aacute;o quyết to&aacute;n chậm nhất l&agrave; ng&agrave;y thứ 90 kể từ ng&agrave;y kết th&uacute;c năm t&agrave;i ch&iacute;nh hoặc trước khi thực hiện việc hợp nhất, s&aacute;p nhập, chia t&aacute;ch, giải thể, chuyển nơi l&agrave;m thủ tục nhập khẩu nguy&ecirc;n liệu, vật tư cho Chi cục Hải quan nơi đ&atilde; th&ocirc;ng b&aacute;o cơ sở sản xuất theo quy định tại Điều 56 Th&ocirc;ng tư n&agrave;y th&ocirc;ng qua Hệ thống.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">a) Nguy&ecirc;n tắc lập sổ chi tiết kế to&aacute;n v&agrave; b&aacute;o c&aacute;o quyết to&aacute;n t&igrave;nh h&igrave;nh sử dụng nguy&ecirc;n liệu, vật tư nhập khẩu, h&agrave;ng h&oacute;a xuất khẩu:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tổ chức, c&aacute; nh&acirc;n c&oacute; hoạt động gia c&ocirc;ng, sản xuất h&agrave;ng h&oacute;a xuất khẩu c&oacute; tr&aacute;ch nhiệm quản l&yacute; v&agrave; theo d&otilde;i nguy&ecirc;n liệu, vật tư nhập khẩu, sản phẩm xuất khẩu từ khi nhập khẩu, trong qu&aacute; tr&igrave;nh sản xuất ra sản phẩm cho đến khi sản phẩm được xuất khẩu hoặc thay đổi mục đ&iacute;ch sử dụng, xử l&yacute; phế liệu, phế phẩm, nguy&ecirc;n liệu, vật tư dư thừa, m&aacute;y m&oacute;c, thiết bị, sử dụng nguy&ecirc;n liệu, vật tư nhập khẩu để gia c&ocirc;ng tiết kiệm được trong qu&aacute; tr&igrave;nh sản xuất theo quy định ph&aacute;p luật tr&ecirc;n hệ thống sổ kế to&aacute;n theo c&aacute;c quy định về chế độ kế to&aacute;n của Bộ T&agrave;i ch&iacute;nh v&agrave; theo nguồn nhập kho (nhập khẩu hoặc mua trong nước). Nguy&ecirc;n liệu, vật tư c&oacute; nguồn gốc nhập khẩu được theo d&otilde;i chi tiết theo từng loại h&igrave;nh trong kỳ (nhập gia c&ocirc;ng, nhập sản xuất xuất khẩu, nhập kinh doanh, nhập lại nguy&ecirc;n vật liệu sau sản xuất...) đ&atilde; khai tr&ecirc;n tờ khai hải quan v&agrave; chứng từ nhập kho trong kỳ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tổ chức, c&aacute; nh&acirc;n c&oacute; tr&aacute;ch nhiệm lập v&agrave; lưu trữ sổ chi tiết đối với nguy&ecirc;n liệu, vật tư nhập khẩu theo chứng từ h&agrave;ng h&oacute;a nhập khẩu; lập v&agrave; lưu trữ sổ chi tiết sản phẩm xuất kho để xuất khẩu theo hợp đồng, đơn h&agrave;ng. Trường hợp tổ chức, c&aacute; nh&acirc;n sử dụng nguy&ecirc;n liệu, vật tư nhập khẩu kh&ocirc;ng t&aacute;ch biệt được nguồn theo nguy&ecirc;n tắc n&agrave;y th&igrave; kiểm tra, x&aacute;c định số lượng nguy&ecirc;n liệu, vật tư nhập khẩu sử dụng đ&uacute;ng mục đ&iacute;ch theo nguy&ecirc;n tắc tỷ lệ số lượng sản phẩm đầu ra được xuất khẩu đ&uacute;ng loại h&igrave;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tổ chức, c&aacute; nh&acirc;n lập b&aacute;o c&aacute;o quyết to&aacute;n t&igrave;nh h&igrave;nh sử dụng nguy&ecirc;n liệu, vật tư nhập khẩu, h&agrave;ng h&oacute;a xuất khẩu theo h&igrave;nh thức nhập - xuất - tồn kho nguy&ecirc;n liệu, kho th&agrave;nh phẩm theo từng m&atilde; nguy&ecirc;n liệu, vật tư, m&atilde; sản phẩm đang theo d&otilde;i trong quản trị sản xuất v&agrave; đ&atilde; khai tr&ecirc;n tờ khai hải quan khi nhập khẩu nguy&ecirc;n liệu, vật tư, xuất khẩu sản phẩm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trường hợp quản trị sản xuất của tổ chức, c&aacute; nh&acirc;n c&oacute; sử dụng m&atilde; nguy&ecirc;n liệu, vật tư, m&atilde; sản phẩm kh&aacute;c với m&atilde; đ&atilde; khai b&aacute;o tr&ecirc;n tờ khai hải quan khi nhập khẩu nguy&ecirc;n liệu, vật tư, xuất khẩu sản phẩm, tổ chức, c&aacute; nh&acirc;n phải x&acirc;y dựng, lưu giữ bảng quy đổi tương đương giữa c&aacute;c m&atilde; n&agrave;y v&agrave; xuất tr&igrave;nh khi cơ quan hải quan kiểm tra hoặc c&oacute; y&ecirc;u cầu giải tr&igrave;nh;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">b) Tổ chức, c&aacute; nh&acirc;n lập v&agrave; nộp b&aacute;o c&aacute;o quyết to&aacute;n về t&igrave;nh h&igrave;nh xuất - nhập - tồn kho nguy&ecirc;n liệu, vật tư nhập khẩu để gia c&ocirc;ng, sản xuất h&agrave;ng h&oacute;a xuất khẩu (bao gồm cả nhập khẩu của DNCX) cho Chi cục Hải quan nơi đ&atilde; th&ocirc;ng b&aacute;o cơ sở sản xuất theo c&aacute;c chỉ ti&ecirc;u th&ocirc;ng tin quy định tại mẫu số 25 Phụ lục II ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y qua Hệ thống hoặc theo mẫu số 15/BCQT-NVL/GSQL Phụ lục số V ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y; b&aacute;o c&aacute;o quyết to&aacute;n về t&igrave;nh h&igrave;nh nhập - xuất - tồn kho th&agrave;nh phẩm được sản xuất từ nguy&ecirc;n liệu, vật tư nhập khẩu để sản xuất h&agrave;ng h&oacute;a xuất khẩu theo c&aacute;c chỉ ti&ecirc;u th&ocirc;ng tin quy định tại mẫu số 26 Phụ lục II ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y qua Hệ thống hoặc theo mẫu số 15a/BCQTSP-GSQL Phụ lục số V ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y v&agrave; định mức thực tế sản xuất sản phẩm xuất khẩu theo c&aacute;c chỉ ti&ecirc;u th&ocirc;ng tin quy định tại mẫu số 27 Phụ lục II ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y qua Hệ thống hoặc theo mẫu số 16/ĐMTT-GSQL Phụ lục số V ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">c) Sửa đổi, bổ sung b&aacute;o c&aacute;o quyết to&aacute;n:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trong thời hạn 60 ng&agrave;y kể từ ng&agrave;y nộp b&aacute;o c&aacute;o quyết to&aacute;n nhưng trước thời điểm cơ quan hải quan ban h&agrave;nh quyết định kiểm tra b&aacute;o c&aacute;o quyết to&aacute;n, kiểm tra sau th&ocirc;ng quan, thanh tra, tổ chức, c&aacute; nh&acirc;n ph&aacute;t hiện sai s&oacute;t trong việc lập b&aacute;o c&aacute;o quyết to&aacute;n th&igrave; được sửa đổi, bổ sung b&aacute;o c&aacute;o quyết to&aacute;n v&agrave; nộp lại cho cơ quan hải quan. Hết thời hạn 60 ng&agrave;y kể từ ng&agrave;y nộp b&aacute;o c&aacute;o quyết to&aacute;n hoặc sau khi cơ quan hải quan quyết định kiểm tra b&aacute;o c&aacute;o quyết to&aacute;n, kiểm tra sau th&ocirc;ng quan, thanh tra, tổ chức, c&aacute; nh&acirc;n mới ph&aacute;t hiện sai s&oacute;t trong việc lập b&aacute;o c&aacute;o quyết to&aacute;n th&igrave; thực hiện việc sửa đổi, bổ sung b&aacute;o c&aacute;o quyết to&aacute;n với cơ quan hải quan v&agrave; bị xử l&yacute; theo quy định ph&aacute;p luật về thuế, ph&aacute;p luật về xử l&yacute; vi phạm h&agrave;nh ch&iacute;nh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">3. Kiểm tra b&aacute;o c&aacute;o quyết to&aacute;n t&igrave;nh h&igrave;nh sử dụng nguy&ecirc;n liệu, vật tư nhập khẩu, h&agrave;ng h&oacute;a xuất khẩu</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">a) C&aacute;c trường hợp kiểm tra b&aacute;o c&aacute;o quyết to&aacute;n: Cơ quan hải quan kiểm tra b&aacute;o c&aacute;o quyết to&aacute;n tr&ecirc;n cơ sở &aacute;p dụng quản l&yacute; rủi ro, đ&aacute;nh gi&aacute; qu&aacute; tr&igrave;nh tu&acirc;n thủ ph&aacute;p luật của tổ chức, c&aacute; nh&acirc;n. Ri&ecirc;ng đối với doanh nghiệp ưu ti&ecirc;n, việc kiểm tra b&aacute;o c&aacute;o quyết to&aacute;n thực hiện theo quy định của Bộ T&agrave;i ch&iacute;nh về &aacute;p dụng chế độ ưu ti&ecirc;n trong việc thực hiện thủ tục hải quan, kiểm tra, gi&aacute;m s&aacute;t hải quan đối với h&agrave;ng h&oacute;a xuất khẩu, nhập khẩu của doanh nghiệp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">b) Thẩm quyền kiểm tra: Cục trưởng Cục Hải quan ban h&agrave;nh quyết định kiểm tra theo mẫu số 17/QĐ-KTBCQT/GSQL Phụ lục V ban h&agrave;nh k&egrave;m theo Th&ocirc;ng tư n&agrave;y v&agrave; tổ chức thực hiện việc kiểm tra, Chi cục trưởng Chi cục Hải quan nơi quản l&yacute; thực hiện việc kiểm tra. Trường hợp kiểm tra b&aacute;o c&aacute;o quyết to&aacute;n tr&ugrave;ng với kế hoạch kiểm tra sau th&ocirc;ng quan th&igrave; thực hiện kiểm tra sau th&ocirc;ng quan theo kế hoạch;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">c) Nội dung kiểm tra: Kiểm tra hồ sơ hải quan nhập khẩu nguy&ecirc;n liệu, vật tư, hồ sơ hải quan xuất khẩu sản phẩm, chứng từ kế to&aacute;n, sổ kế to&aacute;n, chứng từ theo d&otilde;i nguy&ecirc;n liệu, vật tư, m&aacute;y m&oacute;c, thiết bị nhập kho, xuất kho v&agrave; c&aacute;c chứng từ kh&aacute;c ph&aacute;t sinh trong kỳ b&aacute;o c&aacute;o người khai hải quan phải lưu theo quy định tại Điều 16a Th&ocirc;ng tư n&agrave;y. Trường hợp kiểm tra c&aacute;c nội dung quy định tr&ecirc;n m&agrave; cơ quan hải quan ph&aacute;t hiện c&oacute; dấu hiệu vi phạm nhưng chưa đủ cơ sở để kết luận th&igrave; thực hiện kiểm tra t&igrave;nh h&igrave;nh sử dụng, quản l&yacute; nguy&ecirc;n liệu, vật tư nhập khẩu để sản xuất, gia c&ocirc;ng h&agrave;ng h&oacute;a xuất khẩu từ khi nhập khẩu, trong qu&aacute; tr&igrave;nh sản xuất ra sản phẩm cho đến khi sản phẩm được xuất khẩu hoặc thay đổi mục đ&iacute;ch sử dụng, xử l&yacute; phế liệu, phế phẩm, nguy&ecirc;n liệu, vật tư dư thừa, m&aacute;y m&oacute;c, thiết bị, sử dụng nguy&ecirc;n liệu, vật tư nhập khẩu để gia c&ocirc;ng tiết kiệm được trong qu&aacute; tr&igrave;nh sản xuất theo quy định ph&aacute;p luật;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">d) Thời gian, tr&igrave;nh tự, thủ tục v&agrave; xử l&yacute; kết quả kiểm tra thực hiện theo thẩm quyền, tr&igrave;nh tự, thủ tục kiểm tra t&igrave;nh h&igrave;nh sử dụng nguy&ecirc;n liệu, vật tư, m&aacute;y m&oacute;c, thiết bị tại trụ sở người khai hải quan quy định tại khoản 2, khoản 3, khoản 4 v&agrave; khoản 5 Điều 59 Th&ocirc;ng tư n&agrave;y. Ri&ecirc;ng Bi&ecirc;n bản kiểm tra b&aacute;o c&aacute;o quyết to&aacute;n v&agrave; kết luận kiểm tra b&aacute;o c&aacute;o quyết to&aacute;n thực hiện theo mẫu số 17a/BBKT-BCQT/GSQL v&agrave; mẫu số 17b/KLKT-BCQT/GSQL Phụ lục V ban h&agrave;nh k&egrave;m Th&ocirc;ng tư n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">4. Xử l&yacute; qu&aacute; hạn nộp b&aacute;o c&aacute;o quyết to&aacute;n t&igrave;nh h&igrave;nh sử dụng nguy&ecirc;n liệu, vật tư, m&aacute;y m&oacute;c, thiết bị v&agrave; h&agrave;ng h&oacute;a xuất khẩu</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">a) Khi hết hạn nộp b&aacute;o c&aacute;o quyết to&aacute;n, Chi cục Hải quan nơi l&agrave;m thủ tục quyết to&aacute;n thực hiện c&aacute;c c&ocirc;ng việc sau:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">a.1) C&oacute; văn bản mời tổ chức, c&aacute; nh&acirc;n đến cơ quan hải quan lập bi&ecirc;n bản vi phạm để xử l&yacute; theo quy định;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">a.2) Trong thời hạn 10 ng&agrave;y kể từ ng&agrave;y gửi văn bản, tổ chức, c&aacute; nh&acirc;n kh&ocirc;ng đến l&agrave;m việc th&igrave; cơ quan hải quan thực hiện điều tra x&aacute;c minh tại địa chỉ đăng k&yacute; kinh doanh;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">a.3) Thực hiện kiểm tra hồ sơ hoặc kiểm tra hồ sơ v&agrave; kiểm tra thực tế h&agrave;ng h&oacute;a đối với c&aacute;c l&ocirc; h&agrave;ng xuất khẩu, nhập khẩu tiếp theo của tổ chức, c&aacute; nh&acirc;n;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">a.4) Phối hợp với cơ quan chức năng để điều tra, x&aacute;c minh, truy t&igrave;m đối với tổ chức, c&aacute; nh&acirc;n c&oacute; dấu hiệu bỏ trốn khỏi địa chỉ đăng k&yacute; kinh doanh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">b) Biện ph&aacute;p xử l&yacute; sau khi đ&atilde; thực hiện đ&ocirc;n đốc, điều tra, x&aacute;c minh, truy t&igrave;m:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">b.1) Đối với tổ chức, c&aacute; nh&acirc;n kh&ocirc;ng b&aacute;o c&aacute;o quyết to&aacute;n t&igrave;nh h&igrave;nh sử dụng nguy&ecirc;n liệu, vật tư, m&aacute;y m&oacute;c, thiết bị nhưng vẫn c&ograve;n hoạt động, cơ quan hải quan lập bi&ecirc;n bản vi phạm để xử l&yacute; theo quy định v&agrave; chuyển th&ocirc;ng tin để thực hiện kiểm tra sau th&ocirc;ng quan, thanh tra chuy&ecirc;n ng&agrave;nh;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">b.2) Đối với tổ chức, c&aacute; nh&acirc;n bỏ trốn, mất t&iacute;ch m&agrave; cơ quan hải quan kh&ocirc;ng c&oacute; định mức thực tế để x&aacute;c định số tiền thuế th&igrave; sử dụng định mức thực tế đối với h&agrave;ng h&oacute;a tương tự của tổ chức, c&aacute; nh&acirc;n kh&aacute;c. Sau khi x&aacute;c định được số tiền thuế th&igrave; ho&agrave;n thiện hồ sơ v&agrave; chuyển to&agrave;n bộ hồ sơ cho cơ quan c&oacute; thẩm quyền điều tra về tội bu&ocirc;n lậu, trốn thuế theo quy định của Bộ Luật h&igrave;nh sự.&rdquo;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trước th&ocirc;ng tư 39 l&agrave; th&ocirc;ng tư 38, th&ocirc;ng tư 39 ra năm 2018 l&agrave; để sửa đổi bổ sung những quy định tại th&ocirc;ng tư 38. Thời điểm &aacute;p dụng th&ocirc;ng tư 38 th&igrave; việc b&aacute;o c&aacute;o quyết to&aacute;n nguy&ecirc;n liệu, vật tư, sản phẩm kh&aacute; l&agrave;&hellip;.dễ d&agrave;ng, n&ecirc;n nhiều doanh nghiệp chủ quan kh&ocirc;ng coi trọng. Tuy nhi&ecirc;n với 39 th&igrave; đ&atilde; chặt chẽ hơn rất nhiều. Mong l&agrave; c&aacute;c bạn l&agrave;m v&agrave; thực hiện đ&uacute;ng theo th&ocirc;ng tư để doanh nghiệp m&igrave;nh kh&ocirc;ng bị phạt truy thu thuế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Mọi đ&oacute;ng g&oacute;p cho b&agrave;i viết vui l&ograve;ng li&ecirc;n hệ với ch&uacute;ng t&ocirc;i. Xin cảm ơn</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Th&ocirc;ng tin về <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\"><strong><em>kh&oacute;a học b&aacute;o c&aacute;o quyết to&aacute;n</em></strong></a> vui l&ograve;ng xem tại <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\">Đ&Acirc;Y</a>.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Đăng k&yacute; <a href=\"https://docs.google.com/forms/d/e/1FAIpQLSdK7vPbgEEvrgZY6KBs0npd0igmlaIeMQwIg4r0hAsN34HqQQ/viewform\"><strong><em>học l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n</em></strong></a> vui l&ograve;ng xem&nbsp;tại <a href=\"https://docs.google.com/forms/d/e/1FAIpQLSdK7vPbgEEvrgZY6KBs0npd0igmlaIeMQwIg4r0hAsN34HqQQ/viewform\">Đ&Acirc;Y</a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Mọi b&agrave;i copy vui l&ograve;ng ghi nguồn của ch&uacute;ng t&ocirc;i: </span></p>\r\n\r\n<p><span style=\"font-size:14px\">Trung t&acirc;m đ&agrave;o tạo nghiệp vụ XNK - Logistics HAN EXIM CLUB.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Địa chỉ: Số 4 - ng&aacute;ch 23 - ng&otilde; 82 - Phố Ch&ugrave;a L&aacute;ng - Quận Đống Đa -&nbsp;H&agrave; Nội</span></p>\r\n\r\n<p><span style=\"font-size:14px\">H&atilde;y l&agrave; người văn minh c&aacute;c bạn nh&eacute;.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hotline về lịch khai giảng, học ph&iacute; lớp học b&aacute;o c&aacute;o quyết to&aacute;n vui l&ograve;ng li&ecirc;n hệ: 0974 155 033 Mr Long.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tr&acirc;n trọng.</span></p>','/30/ef/f3/5ce17ee2e8682.jpg',NULL,186,1,'2019-05-19 23:05:54',NULL,'báo cáo quyết toán, dạy báo cáo quyết toán, cách làm báo cáo quyết toán, hướng dẫn làm báo cáo quyết toán','báo cáo quyết toán, dạy báo cáo quyết toán, cách làm báo cáo quyết toán, hướng dẫn làm báo cáo quyết toán','','',2,1,'vi','bao-cao-quyet-toan-theo-thong-tu-39',10,27,27,'2019-05-19 22:51:47','2019-05-19 23:05:54'),(288,'NHỮNG MẪU BÁO CÁO QUYẾT TOÁN DOANH NGHIỆP PHẢI LÀM','','Theo thông tư 39 quy định, thì doanh nghiệp phải làm báo cáo quyết toán là những doanh nghiệp sản xuất xuất khẩu, doanh nghiệp gia công và doanh nghiệp chế xuất. Vậy báo cáo quyết toán gồm những gì? Chúng ta cùng tìm hiểu trong bài viết này nhé.','<p style=\"text-align:justify\"><span style=\"font-size:14px\">Theo th&ocirc;ng tư 39 quy định, th&igrave; doanh nghiệp phải l&agrave;m <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\"><strong>b&aacute;o c&aacute;o quyết to&aacute;n</strong></a> l&agrave; những doanh nghiệp sản xuất xuất khẩu, doanh nghiệp gia c&ocirc;ng v&agrave; doanh nghiệp chế xuất. L&yacute; do th&igrave; chắc ai cũng biết, đ&oacute; l&agrave; nhằm tr&aacute;nh thất thu thuế cho đất nước, bởi v&igrave; những doanh nghiệp n&agrave;y khi nhập khẩu nguy&ecirc;n phụ liệu đầu v&agrave;o để sản xuất hay gia c&ocirc;ng đ&atilde; được miễn thuế nhập khẩu, v&igrave; vậy việc sử dụng&nbsp; nguy&ecirc;n phụ liệu phải được theo d&otilde;i rất chặt chẽ v&agrave; s&aacute;t sao. Vậy <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\"><strong>b&aacute;o c&aacute;o quyết to&aacute;n gồm những g&igrave;</strong></a>? Ch&uacute;ng ta c&ugrave;ng t&igrave;m hiểu trong b&agrave;i viết n&agrave;y nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Đầu ti&ecirc;n l&agrave; mẫu 15 &ndash; B&aacute;o c&aacute;o quyết to&aacute;n xuất &ndash; nhập &ndash; tồn nguy&ecirc;n phụ liệu, vật tư nhập khẩu</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/Mau%2015%20bao%20cao%20quyet%20toan.jpg\" style=\"height:375px; width:680px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tiếp theo l&agrave; mẫu 15a &ndash; B&aacute;o c&aacute;o quyết to&aacute;n nhập &ndash; xuất &ndash; tồn kho sản phẩm xuất khẩu</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/M15a%20bao%20cao%20quyet%20toan.jpg\" style=\"height:373px; width:680px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">V&agrave; cuối c&ugrave;ng l&agrave; mẫu 16 &ndash; Định mức thực tế sản phẩm xuất&nbsp;khẩu</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/M16%20bao%20cao%20quyet%20toan.jpg\" style=\"height:473px; width:680px\" /></span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Sau khi thực hiện việc ho&agrave;n th&agrave;nh 3 mẫu b&aacute;o c&aacute;o tr&ecirc;n, doanh nghiệp tiến h&agrave;nh khai b&aacute;o tới hải quan qua phần mềm ECUS5/VNACCS. Thoạt nh&igrave;n th&igrave; c&oacute; vẻ đơn giản, nhưng nếu một doanh nghiệp nhẩu khẩu nhiều m&atilde; nguy&ecirc;n phụ liệu th&igrave; n&oacute; c&oacute; thể d&agrave;i tới mấy trang A4 đấy c&aacute;c bạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Việc tải nội dung 3 mẫu b&aacute;o c&aacute;o quyết to&aacute;n n&agrave;y về m&aacute;y t&iacute;nh của m&igrave;nh, c&aacute;c bạn c&oacute; thể t&igrave;m tr&ecirc;n mạng, hoặc inbox ngay cho ch&uacute;ng t&ocirc;i, ch&uacute;ng t&ocirc;i sẽ gửi ngay cho bạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Hoặc c&aacute;c bạn c&oacute; thể join v&agrave;o group <a href=\"https://www.facebook.com/groups/634655570308200/\">Hỗ trợ l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n &ndash; Kiểm tra sau th&ocirc;ng quan</a> tr&ecirc;n facebook.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Ch&uacute;ng t&ocirc;i tải l&ecirc;n đ&oacute; 3 mẫu b&aacute;o c&aacute;o n&agrave;y v&agrave; rất nhiều t&agrave;i liệu c&oacute; &iacute;ch kh&aacute;c cho c&aacute;c bạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Cảm ơn c&aacute;c bạn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">***</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Mọi b&agrave;i copy vui l&ograve;ng ghi nguồn của ch&uacute;ng t&ocirc;i:</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trung t&acirc;m đ&agrave;o tạo nghiệp vụ XNK - Logistics HAN EXIM CLUB.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Địa chỉ: Số 4 - ng&aacute;ch 23 - ng&otilde; 82 - Phố Ch&ugrave;a L&aacute;ng - Quận Đống Đa -&nbsp;H&agrave; Nội</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">H&atilde;y l&agrave; người văn minh c&aacute;c bạn nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Hotline về lịch khai giảng, học ph&iacute; lớp học b&aacute;o c&aacute;o quyết to&aacute;n vui l&ograve;ng li&ecirc;n hệ: 0974 155 033 Mr Long.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tr&acirc;n trọng.</span></p>','/bf/67/7b/5ce181d5101de.jpg',NULL,207,1,'2019-05-19 00:00:00',NULL,'mẫu làm báo cáo quyết toán, hướng dẫn làm báo cáo quyết toán, dạy làm báo cáo quyết toán, chia sẻ cách làm báo cáo quyết toán','mẫu làm báo cáo quyết toán, hướng dẫn làm báo cáo quyết toán, dạy làm báo cáo quyết toán, chia sẻ cách làm báo cáo quyết toán','','',2,1,'vi','nhung-mau-bao-cao-quyet-toan-doanh-nghiep-phai-lam',10,27,27,'2019-05-19 23:18:29','2019-05-19 23:19:50');
insert  into `ad_article`(`id`,`title`,`alttitle`,`header`,`body`,`image_path`,`attributes`,`hit_count`,`priority`,`published_time`,`expiredate_time`,`meta`,`keywords`,`author`,`other_link`,`is_active`,`is_hot`,`lang`,`slug`,`category_id`,`created_by`,`updated_by`,`created_at`,`updated_at`) values (289,'Công văn 3304/TCHQ-GSQL ngày 27/05/2019','','Công văn 3304/TCHQ-GSQL ngày 27/05/2019 quy định về Nguyên vật liệu, vật tư tiêu hao, công cụ dụng cụ tiếp tục giải quyết những vấn đề doanh nghiệp vẫn còn đang thắc mắc','<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B&agrave;i viết cập nhật ng&agrave;y 28/5/2019.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Ch&agrave;o c&aacute;c bạn, sau khoảng v&agrave;i th&aacute;ng tranh c&atilde;i nhau về việc nguy&ecirc;n vật liệu, vật tư ti&ecirc;u hao, c&ocirc;ng cụ dụng cụ khai m&atilde; loại h&igrave;nh nhập khẩu n&agrave;o, hay c&oacute; phải cho v&agrave;o <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\"><strong>b&aacute;o c&aacute;o quyết to&aacute;n</strong></a> hay kh&ocirc;ng. Đ&atilde; khiến nhiều anh chị em đang l&agrave;m việc cho c&aacute;c doanh nghiệp sản xuất xuất khẩu, gia c&ocirc;ng hay chế xuất phải đau đầu, khi m&agrave; th&ocirc;ng tư đưa ra th&igrave; mập mờ, kh&ocirc;ng r&otilde; r&agrave;ng. Hay việc Chi cục hải quan n&agrave;y n&oacute;i cho v&agrave;o b&aacute;o c&aacute;o quyết to&aacute;n, Chi cục hải quan kia th&igrave; bảo kh&ocirc;ng. Tạo n&ecirc;n một sự kh&ocirc;ng đồng nhất ngay giữa c&aacute;c Chi cục hải quan, giữa c&aacute;c bộ phận xuất nhập khẩu của c&aacute;c doanh nghiệp.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">H&ocirc;m qua, Tổng cục hải quan đ&atilde; đưa ra C&ocirc;ng văn số 3304 để tiếp tục giải quyết những vấn đề doanh nghiệp vẫn c&ograve;n đang thắc mắc. To&agrave;n bộ những c&ocirc;ng văn trước đ&oacute; gồm:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">C&ocirc;ng văn 293/GSQL-GQ2 ng&agrave;y 14/02/2017.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">C&ocirc;ng văn 226/GSQL-GQ2 ng&agrave;y 22/01/2019.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">C&ocirc;ng văn 858/GSQL-GQ2 ng&agrave;y 28/03/2019.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Quy định chi tiết v&agrave; cụ thể thế n&agrave;o l&agrave; nguy&ecirc;n liệu ti&ecirc;u hao, vật tư ti&ecirc;u hao, hay c&ocirc;ng cụ dụng cụ</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/C%C3%B4ng%20v%C4%83n%203304%201.jpg\" style=\"height:983px; width:680px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/C%C3%B4ng%20v%C4%83n%203304%202.jpg\" style=\"height:972px; width:680px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/C%C3%B4ng%20v%C4%83n%203304%203.jpg\" style=\"height:931px; width:680px\" /></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Chi tiết của c&ocirc;ng văn 3304 /TCHQ-GSQL ng&agrave;y 27/05/2019. Theo đ&oacute;:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">- Nhập khẩu c&ocirc;ng cụ dụng cụ theo loại h&igrave;nh A12, kh&ocirc;ng phải l&agrave;m <strong><a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\">b&aacute;o c&aacute;o quyết to&aacute;</a></strong><a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\"><strong>n</strong></a>, đối với doanh nghiệp chế xuất th&igrave; khi thanh l&yacute; phải c&oacute; th&ocirc;ng b&aacute;o với cơ quan hải quan.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">- Nhập khẩu nguy&ecirc;n vật liệu, vật tư ti&ecirc;u hao của doanh nghiệp chế xuất từ nước ngo&agrave;i theo loại h&igrave;nh E11, nhập khẩu nội địa theo loại h&igrave;nh E15 (đ&atilde; c&oacute; quy định theo c&ocirc;ng văn 858 trước đ&oacute;).</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Đối với doanh nghiệp chế xuất: Loại h&igrave;nh E15 nhập nội địa kh&ocirc;ng phải l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n (c&ocirc;ng văn 858). Loại h&igrave;nh E11 nhập từ nước ngo&agrave;i khi l&agrave;m <strong><a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\">b&aacute;o c&aacute;o quyết to&aacute;n</a></strong> m&agrave; kh&ocirc;ng x&acirc;y dựng định mức th&igrave; ghi ch&uacute; &ldquo;KXDĐM&rdquo; v&agrave; giải tr&igrave;nh với hải quan khi kiểm tra sau th&ocirc;ng quan.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Cho đến h&ocirc;m nay th&igrave; doanh nghiệp l&agrave;m theo c&ocirc;ng văn n&agrave;y, tuy nhi&ecirc;n kh&ocirc;ng biết đến khi n&agrave;o n&oacute; thay đổi, nếu c&oacute; ch&uacute;ng t&ocirc;i sẽ cập nhật ngay tới c&aacute;c bạn.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Việc tải nội dung c&ocirc;ng văn, c&aacute;c bạn c&oacute; thể join v&agrave;o nh&oacute;m <a href=\"https://www.facebook.com/bcqthaneximclub/\">Hỗ trợ l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n &ndash; Kiểm tra sau th&ocirc;ng quan</a> của ch&uacute;ng t&ocirc;i tr&ecirc;n facebook.</span></p>\r\n\r\n<p style=\"text-align: justify;\">---</p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Mọi b&agrave;i copy vui l&ograve;ng ghi nguồn của ch&uacute;ng t&ocirc;i:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><a href=\"http://tuhocxuatnhapkhau.com\">Trung t&acirc;m đ&agrave;o tạo nghiệp vụ XNK - Logistics</a> <a href=\"https://www.facebook.com/lophocxuatnhapkhau\">HAN EXIM CLUB</a>.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Địa chỉ: Số 4 - ng&aacute;ch 23 - ng&otilde; 82 - Phố Ch&ugrave;a L&aacute;ng - Quận Đống Đa - H&agrave; Nội</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">H&atilde;y l&agrave; người văn minh c&aacute;c bạn nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Hotline về lịch khai giảng, học ph&iacute; <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\">lớp học b&aacute;o c&aacute;o quyết to&aacute;n</a> vui l&ograve;ng li&ecirc;n hệ: 0974 155 033 Mr Long.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Tr&acirc;n trọng.</span></p>','/e9/6f/fe/5cf5ce6e1e246.jpg',NULL,191,0,'2019-06-04 08:50:38',NULL,'Công văn 3304, báo cáo quyết toán, hướng dẫn làm báo cáo quyết toán, chia sẻ kinh nghiệm làm báo cáo quyết toán, nguyên phụ liệu, vật tư sản xuất xuất khẩu','Công văn 3304, báo cáo quyết toán, hướng dẫn làm báo cáo quyết toán, chia sẻ kinh nghiệm làm báo cáo quyết toán, nguyên phụ liệu, vật tư sản xuất xuất khẩu','','',2,1,'vi','cong-van-3304-tchq-gsql-ngay-27-05-2019',10,27,27,'2019-06-04 08:50:38','2019-06-04 08:50:38'),(290,'Định nghĩa Doanh nghiệp chế xuất và các quy định liên quan','','Doanh nghiệp chế xuất là doanh nghiệp được thành lập và hoạt động trong khu chế xuất hoặc doanh nghiệp chuyên sản xuất sản phẩm để xuất khẩu hoạt động trong khu công nghiệp, khu kinh tế','<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Theo quy định mới nhất VỀ QUẢN L&Yacute; KHU C&Ocirc;NG NGHIỆP V&Agrave; KHU KINH TẾ: NĐ 82/2018/NĐ-CP <em>ng&agrave;y 22 th&aacute;ng 5 năm 2018 hiệu lực ng&agrave;y 10/07/2018 </em>th&igrave;:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><strong>Doanh nghiệp chế xuất</strong> l&agrave; doanh nghiệp được th&agrave;nh lập v&agrave; hoạt động trong khu chế xuất hoặc doanh nghiệp chuy&ecirc;n sản xuất sản phẩm để xuất khẩu hoạt động trong khu c&ocirc;ng nghiệp, khu kinh tế.</span></p>\r\n\r\n<p style=\"margin-left: 0.5in; text-align: justify;\"><span style=\"font-size:14px\">Doanh nghiệp chế xuất kh&ocirc;ng nằm trong khu chế xuất được ngăn c&aacute;ch với khu vực b&ecirc;n ngo&agrave;i theo c&aacute;c quy định &aacute;p dụng đối với khu phi thuế quan tại ph&aacute;p luật về thuế xuất khẩu, thuế nhập khẩu. (K10 &ndash; Đ2 nghị định 82/2018/NĐ-CP <em>ng&agrave;y 22 th&aacute;ng 5 năm 2018)</em></span></p>\r\n\r\n<ul>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:14px\">Doanh nghiệp chế xuất được quy định trong Giấy chứng nhận đăng k&yacute; đầu tư hoặc trong văn bản của cơ quan đăng k&yacute; đầu tư c&oacute; thẩm quyền (K1.Đ30 nghị định 82/2018/NĐ-CP)</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:14px\">Doanh nghiệp chế xuất được ngăn c&aacute;ch với l&atilde;nh thổ b&ecirc;n ngo&agrave;i bằng hệ thống tường r&agrave;o, c&oacute; cảng v&agrave; cửa ra, v&agrave;o, bảo đảm điều kiện cho sự kiểm tra, gi&aacute;m s&aacute;t, kiểm so&aacute;t của cơ quan hải quan v&agrave; c&aacute;c cơ quan chức năng c&oacute; li&ecirc;n quan theo quy định &aacute;p dụng đối với khu phi thuế quan, quy định tại ph&aacute;p luật về thuế&nbsp;xuất khẩu, thuế&nbsp;nhập khẩu. (K2.Đ30, nghị định 82/2018/NĐ-CP)</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:14px\">Quan hệ trao đổi h&agrave;ng h&oacute;a giữa c&aacute;c khu chế xuất, doanh nghiệp chế xuất với c&aacute;c khu vực kh&aacute;c tr&ecirc;n l&atilde;nh thổ Việt Nam, kh&ocirc;ng phải khu phi thuế quan, l&agrave; quan hệ xuất khẩu, nhập khẩu (K5.Đ30 nghị định 82/2018/NĐ-CP)</span></li>\r\n	<li style=\"text-align: justify;\"><span style=\"font-size:14px\">Doanh nghiệp chế xuất khi được ph&eacute;p kinh doanh hoạt động mua b&aacute;n h&agrave;ng h&oacute;a v&agrave; c&aacute;c hoạt động li&ecirc;n quan trực tiếp đến mua b&aacute;n h&agrave;ng h&oacute;a tại Việt Nam phải mở sổ kế to&aacute;n hạch to&aacute;n ri&ecirc;ng doanh thu, chi ph&iacute; li&ecirc;n quan đến hoạt động mua b&aacute;n h&agrave;ng h&oacute;a tại Việt Nam (K7.Đ30 nghị định 82/2018/NĐ-CP)</span></li>\r\n</ul>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">1. H&agrave;ng h&oacute;a do DNCX thu&ecirc; doanh nghiệp nội địa gia c&ocirc;ng:</span></p>\r\n\r\n<p style=\"margin-left: 0.5in; text-align: justify;\"><span style=\"font-size:14px\">Doanh nghiệp nội địa l&agrave;m thủ tục hải quan theo quy định về gia c&ocirc;ng h&agrave;ng h&oacute;a cho thương nh&acirc;n nước ngo&agrave;i quy định tại mục 1 v&agrave; mục 2 Chương III TT39. Ri&ecirc;ng về địa điểm l&agrave;m thủ tục hải quan, doanh nghiệp nội địa được lựa chọn thực hiện tại Chi cục Hải quan quản l&yacute; DNCX. Khi khai chỉ ti&ecirc;u th&ocirc;ng tin &ldquo;số quản l&yacute; nội bộ doanh nghiệp&rdquo; tr&ecirc;n tờ khai hải quan, doanh nghiệp nội địa phải khai như sau: #&amp;GCPTQ;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">DNCX kh&ocirc;ng phải l&agrave;m thủ tục hải quan khi đưa h&agrave;ng h&oacute;a v&agrave;o nội địa để gia c&ocirc;ng v&agrave; nhận lại sản phẩm gia c&ocirc;ng từ nội địa.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Trường hợp đưa h&agrave;ng h&oacute;a từ DNCX v&agrave;o thị trường nội địa để gia c&ocirc;ng, bảo h&agrave;nh, sửa chữa nhưng kh&ocirc;ng nhận lại h&agrave;ng h&oacute;a th&igrave; b&ecirc;n nhận gia c&ocirc;ng (doanh nghiệp nội địa) phải đăng k&yacute; tờ khai mới để thay đổi mục đ&iacute;ch sử dụng theo quy định tại Chương II TT 39.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">2. H&agrave;ng h&oacute;a do DNCX nhận gia c&ocirc;ng cho doanh nghiệp nội địa:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">a) Doanh nghiệp nội địa l&agrave;m thủ tục hải quan theo quy định về đặt gia c&ocirc;ng h&agrave;ng h&oacute;a ở nước ngo&agrave;i tại mục 1 v&agrave; mục 3 Chương III TT 39. Ri&ecirc;ng về địa điểm l&agrave;m thủ tục hải quan, doanh nghiệp nội địa được lựa chọn thực hiện tại Chi cục Hải quan quản l&yacute; DNCX. Khi khai chỉ ti&ecirc;u th&ocirc;ng tin &ldquo;số quản l&yacute; nội bộ doanh nghiệp&rdquo; tr&ecirc;n tờ khai hải quan, doanh nghiệp nội địa phải khai như sau: #&amp;GCPTQ;</span></p>\r\n\r\n<p style=\"margin-left: 0.5in; text-align: justify;\"><span style=\"font-size:14px\">b) DNCX kh&ocirc;ng phải l&agrave;m tục hải quan khi nhận h&agrave;ng h&oacute;a từ nội địa để gia c&ocirc;ng v&agrave; trả lại sản phẩm gia c&ocirc;ng v&agrave;o nội địa.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">3. Đối với h&agrave;ng h&oacute;a do DNCX thu&ecirc; DNCX kh&aacute;c gia c&ocirc;ng: DNCX thu&ecirc; gia c&ocirc;ng v&agrave; DNCX nhận gia c&ocirc;ng kh&ocirc;ng phải thực hiện thủ tục hải quan khi giao, nhận h&agrave;ng h&oacute;a phục vụ hợp đồng gia c&ocirc;ng, sản phẩm gia c&ocirc;ng.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">4. H&agrave;ng h&oacute;a DNCX thu&ecirc; nước ngo&agrave;i gia c&ocirc;ng th&igrave; thực hiện thủ tục hải quan theo quy định về h&agrave;ng h&oacute;a đặt gia c&ocirc;ng ở nước ngo&agrave;i quy định tại Mục 3 Th&ocirc;ng tư n&agrave;y.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">5. C&aacute;c trường hợp kh&ocirc;ng l&agrave;m thủ tục hải quan tại điều n&agrave;y, DNCX c&oacute; tr&aacute;ch nhiệm lưu giữ v&agrave; xuất tr&igrave;nh c&aacute;c chứng từ t&agrave;i liệu li&ecirc;n quan đến hoạt động gia c&ocirc;ng, sản xuất h&agrave;ng xuất khẩu theo quy định tại Điều 60 Luật Hải quan, Điều 37 Nghị định số 08/2015/NĐ-CP (trừ việc th&ocirc;ng b&aacute;o cơ sở sản xuất).&rdquo;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20BAI%20VIET/doanh%20nghiep%20che%20xuat.jpg\" style=\"height:511px; width:680px\" /></span></p>\r\n\r\n<p style=\"margin-left: 0.25in; text-align: justify;\"><span style=\"font-size:14px\">Tr&ecirc;n đ&acirc;y l&agrave; to&agrave;n bộ kh&aacute;i niệm về doanh nghiệp chế xuất, cũng như c&aacute;c quy định li&ecirc;n quan, nếu bạn đọc c&ograve;n thắc mắc n&agrave;o c&oacute; thể join v&agrave;o nh&oacute;m <a href=\"https://www.facebook.com/bcqthaneximclub/\">Hỗ trợ l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n &ndash; Kiểm tra sau th&ocirc;ng quan</a>&nbsp;của ch&uacute;ng t&ocirc;i để trao đổi th&ecirc;m nh&eacute;.</span></p>\r\n\r\n<p style=\"margin-left: 0.25in; text-align: justify;\"><span style=\"font-size:14px\">---</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Mọi b&agrave;i copy vui l&ograve;ng ghi nguồn của ch&uacute;ng t&ocirc;i:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><a href=\"http://tuhocxuatnhapkhau.com\">Trung t&acirc;m đ&agrave;o tạo nghiệp vụ XNK - Logistics</a> <a href=\"https://www.facebook.com/lophocxuatnhapkhau\">HAN EXIM CLUB</a>.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Địa chỉ: Số 4 - ng&aacute;ch 23 - ng&otilde; 82 - Phố Ch&ugrave;a L&aacute;ng - Quận Đống Đa - H&agrave; Nội</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">H&atilde;y l&agrave; người văn minh c&aacute;c bạn nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Hotline về lịch khai giảng, học ph&iacute; <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\">lớp học b&aacute;o c&aacute;o quyết to&aacute;n</a> vui l&ograve;ng li&ecirc;n hệ: 0974 155 033 Mr Long.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Tr&acirc;n trọng.</span></p>','/65/f9/96/5cf5cff4ae61d.jpg',NULL,137,0,'2019-06-04 08:57:08',NULL,'doanh nghiệp chế xuất là gì, định nghĩa doanh nghiệp chế xuất, khu chế xuất, khu phi thuế quan, báo cáo quyết toán','doanh nghiệp chế xuất là gì, định nghĩa doanh nghiệp chế xuất, khu chế xuất, khu phi thuế quan, báo cáo quyết toán','','',2,1,'vi','dinh-nghia-doanh-nghiep-che-xuat-va-cac-quy-dinh-lien-quan',10,27,27,'2019-06-04 08:57:08','2019-06-04 08:57:08'),(291,'Các loại hình xuất nhập khẩu phải làm báo cáo quyết toán của doanh nghiệp chế xuất','','Trong bài viết này chúng ta cùng đi tìm hiểu những loại hình xuất nhập khẩu chính của doanh nghiệp chế xuất, và trong đó, những loại hình nào phải đưa vào báo cáo quyết toán.','<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Ch&agrave;o c&aacute;c bạn, tiếp tục với chủ đề doanh nghiệp chế xuất, trong b&agrave;i viết n&agrave;y ch&uacute;ng ta c&ugrave;ng đi t&igrave;m hiểu những loại h&igrave;nh xuất nhập khẩu ch&iacute;nh của <a href=\"http://tuhocxuatnhapkhau.com/news-detail/dinh-nghia-doanh-nghiep-che-xuat-va-cac-quy-dinh-lien-quan\">doanh nghiệp chế xuất</a>, v&agrave; trong đ&oacute;, những loại h&igrave;nh n&agrave;o phải đưa v&agrave;o <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\"><strong>b&aacute;o c&aacute;o quyết to&aacute;n</strong></a>.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Lưu &yacute; với c&aacute;c bạn l&agrave; doanh nghiệp chế xuất th&igrave; c&oacute; 2 dạng: 1 l&agrave; chế xuất gia c&ocirc;ng, 2 l&agrave; chế xuất sản xuất xuất khẩu. <a href=\"http://tuhocxuatnhapkhau.com/news-detail/dinh-nghia-doanh-nghiep-che-xuat-va-cac-quy-dinh-lien-quan\">Doanh nghiệp chế xuất</a> ở đ&acirc;y được hiểu l&agrave; doanh nghiệp được &aacute;p dụng những chế t&agrave;i ri&ecirc;ng, đặc th&ugrave;. V&agrave; n&oacute; kh&aacute;c ho&agrave;n to&agrave;n với doanh nghiệp sản xuất xuất khẩu v&agrave; doanh nghiệp gia c&ocirc;ng th&ocirc;ng thường. Khi thực hiện việc khai b&aacute;o c&aacute;o quyết to&aacute;n, th&igrave; tr&ecirc;n phần mềm cũng chỉ c&oacute; 2 mục, đ&oacute; l&agrave; gia c&ocirc;ng v&agrave; sản xuất xuất khẩu, chứ kh&ocirc;ng c&oacute; mục n&agrave;o l&agrave; chế xuất cả.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Danh mục những loại h&igrave;nh sản xuất xuất khẩu của doanh nghiệp chế xuất:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">E11: Nhập nguy&ecirc;n liệu của DNCX từ nước ngo&agrave;i. Sử dụng trong trường hợp nhập nguy&ecirc;n liệu, vật tư từ nước ngo&agrave;i để sản xuất, gia c&ocirc;ng h&agrave;ng xuất khẩu của DNCX.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">E13: Nhập tạo t&agrave;i sản cố định của DNCX. Sử dụng trong trường hợp nhập khẩu tạo t&agrave;i sản cố định của DNCX (bao gồm cả nhập từ nước ngo&agrave;i, từ nội địa hoặc từ DNCX kh&aacute;c).</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">E15: Nhập nguy&ecirc;n liệu của DNCX từ nội địa. Sử dụng trong trường hợp DNCX nhập khẩu nguy&ecirc;n liệu, vật tư để sản xuất, gia c&ocirc;ng h&agrave;ng xuất khẩu từ nội địa.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">E42: Xuất khẩu sản phẩm của DNCX. Sử dụng trong trường hợp xuất khẩu sản phẩm của DNCX bao gồm cả trường hợp xuất ra nước ngo&agrave;i v&agrave; xuất v&agrave;o nội địa.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A31: Nhập khẩu h&agrave;ng xuất khẩu bị trả lại. Sử dụng trong trường hợp</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">- H&agrave;ng xuất khẩu bị trả lại gồm trả lại để sửa chữa, t&aacute;i chế, ti&ecirc;u thụ nội địa, ti&ecirc;u hủy hoặc t&aacute;i xuất sang nước thứ ba của loại h&igrave;nh xuất sản phẩm của DNCX.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">A12: Nhập kinh doanh sản xuất (h&agrave;ng h&oacute;a l&agrave;m thủ tục tại Chi cục Hải quan kh&aacute;c Chi cục Hải quan cửa khẩu). Sử dụng trong trường hợp doanh nghiệp nhập khẩu h&agrave;ng h&oacute;a để ti&ecirc;u d&ugrave;ng, h&agrave;ng kinh doanh thương mại đơn thuần; nhập kinh doanh nguy&ecirc;n liệu đầu v&agrave;o cho hoạt động sản xuất (trừ GC, SXXK, DNCX v&agrave; doanh nghiệp trong khu phi thuế quan); h&agrave;ng nhập khẩu đầu tư miễn thuế, đầu tư nộp thuế l&agrave;m thủ tục tại Chi cục Hải quan kh&aacute;c Chi cục Hải quan cửa khẩu nhập; doanh nghiệp nội địa nhập kinh doanh h&agrave;ng h&oacute;a từ khu phi thuế quan, DNCX hoặc nhập kinh doanh tại chỗ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B11: Xuất kinh doanh. Sử&nbsp; dụng trong trường hợp doanh nghiệp xuất khẩu h&agrave;ng h&oacute;a kinh doanh thương mại đơn thuần ra nước ngo&agrave;i hoặc xuất khẩu v&agrave;o khu phi thuế quan, DNCX theo hợp đồng mua b&aacute;n v&agrave; trường hợp thực hiện quyền kinh doanh xuất khẩu của doanh nghiệp đầu tư nước ngo&agrave;i (bao gồm cả quyền kinh doanh của DNCX).</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">H11: H&agrave;ng nhập khẩu kh&aacute;c. Bao gồm h&agrave;ng h&oacute;a l&agrave; qu&agrave; biếu, qu&agrave; tặng của tổ chức, c&aacute; nh&acirc;n ở Việt Nam gửi cho tổ chức, c&aacute; nh&acirc;n ở nước ngo&agrave;i; h&agrave;ng h&oacute;a của cơ quan đại diện ngoại giao, tổ chức quốc tế tại Việt Nam v&agrave; những người l&agrave;m việc tại c&aacute;c cơ quan, tổ chức n&agrave;y; h&agrave;ng h&oacute;a viện trợ nh&acirc;n đạo, viện trợ kh&ocirc;ng ho&agrave;n lại; h&agrave;ng h&oacute;a l&agrave; h&agrave;ng mẫu kh&ocirc;ng thanh to&aacute;n; h&agrave;ng h&oacute;a l&agrave; h&agrave;nh l&yacute; c&aacute; nh&acirc;n của người nhập cảnh gửi theo vận đơn, h&agrave;ng h&oacute;a mang theo người nhập cảnh vượt ti&ecirc;u chuẩn miễn thuế (bao gồm h&agrave;ng h&oacute;a mua b&aacute;n, trao đổi của cư d&acirc;n bi&ecirc;n giới vượt định mức miễn thuế).</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">H21: Xuất khẩu h&agrave;ng kh&aacute;c. H&agrave;ng h&oacute;a l&agrave; qu&agrave; biếu, qu&agrave; tặng của tổ chức, c&aacute; nh&acirc;n ở Việt Nam gửi cho tổ chức, c&aacute; nh&acirc;n ở nước ngo&agrave;i; h&agrave;ng h&oacute;a của cơ quan đại diện ngoại giao, tổ chức quốc tế tại Việt Nam v&agrave; những người l&agrave;m việc tại c&aacute;c cơ quan, tổ chức n&agrave;y; h&agrave;ng h&oacute;a viện trợ nh&acirc;n đạo, viện trợ kh&ocirc;ng ho&agrave;n lại; h&agrave;ng h&oacute;a l&agrave; h&agrave;ng mẫu kh&ocirc;ng thanh to&aacute;n; h&agrave;ng h&oacute;a l&agrave; h&agrave;nh l&yacute; c&aacute; nh&acirc;n của người xuất cảnh gửi theo vận đơn, h&agrave;ng h&oacute;a mang theo người xuất cảnh vượt ti&ecirc;u chuẩn miễn thuế; h&agrave;ng h&oacute;a mua b&aacute;n, trao đổi của cư d&acirc;n bi&ecirc;n giới vượt định mức miễn thuế.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">B13: Xuất khẩu trả h&agrave;ng đ&atilde; nhập khẩu. Sử dụng trong trường hợp:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">- H&agrave;ng nhập khẩu của c&aacute;c loại h&igrave;nh phải trả lại (gồm t&aacute;i xuất để trả lại cho kh&aacute;ch h&agrave;ng nước ngo&agrave;i; t&aacute;i xuất sang nước thứ ba hoặc xuất v&agrave;o khu phi thuế quan);</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">- H&agrave;ng h&oacute;a l&agrave; nguy&ecirc;n phụ liệu dư thừa của hợp đồng gia c&ocirc;ng xuất trả b&ecirc;n đặt gia c&ocirc;ng ở nước ngo&agrave;i;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">- H&agrave;ng h&oacute;a l&agrave; m&aacute;y m&oacute;c, thiết bị của doanh nghiệp chế xuất, m&aacute;y m&oacute;c, thiết bị được miễn thuế thanh l&yacute; theo h&igrave;nh thức b&aacute;n ra nước ngo&agrave;i.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">G51: T&aacute;i nhập h&agrave;ng đ&atilde; tạm xuất. Sử dụng trong trường hợp nhập khẩu h&agrave;ng h&oacute;a đ&atilde; tạm xuất (xuất theo chế độ tạm) bao gồm trường hợp h&agrave;ng h&oacute;a đ&atilde; tạm xuất của những c&aacute; nh&acirc;n được nh&agrave; nước Việt Nam cho miễn thuế; trường hợp h&agrave;ng h&oacute;a đ&atilde; tạm xuất l&agrave; dụng cụ, nghề nghiệp, phương tiện l&agrave;m việc tạm xuất c&oacute; thời hạn của cơ quan, tổ chức, của người xuất cảnh; trường hợp h&agrave;ng h&oacute;a đ&atilde; tạm xuất phương tiện chứa h&agrave;ng h&oacute;a theo phương thức quay v&ograve;ng kh&aacute;c (kệ, gi&aacute;, th&ugrave;ng, lọ&hellip;)</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">G61: Tạm xuất h&agrave;ng h&oacute;a. Sử dụng trong trường hợp tạm xuất h&agrave;ng h&oacute;a ra nước ngo&agrave;i, v&agrave;o khu PTQ, từ khu PTQ ra nước ngo&agrave;i theo chế độ tạm. Bao gồm cả c&aacute;c trường hợp h&agrave;ng h&oacute;a tạm xuất của những c&aacute; nh&acirc;n được nh&agrave; nước Việt Nam cho miễn thuế; trường hợp h&agrave;ng h&oacute;a đ&atilde; tạm xuất l&agrave; dụng cụ, nghề nghiệp, phương tiện l&agrave;m việc tạm xuất c&oacute; thời hạn của cơ quan, tổ chức, của người xuất cảnh; trường hợp h&agrave;ng h&oacute;a đ&atilde; tạm xuất phương tiện chứa h&agrave;ng h&oacute;a theo phương thức quay v&ograve;ng kh&aacute;c (kệ, gi&aacute;, th&ugrave;ng, lọ&hellip;).</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Những loại h&igrave;nh xuất nhập khẩu n&agrave;o của DNCX phải đưa v&agrave;o b&aacute;o c&aacute;o quyết to&aacute;n: E15, E11, E42. B13, B11, A12, A31, H11, H21.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Trong đ&oacute; loại h&igrave;nh B11 xảy ra khi DNCX thực hiện thủ tục b&aacute;n thanh l&yacute; phế liệu phế phẩm. doanh nghiệp thu mua phế phẩm nhập khẩu loại h&igrave;nh A12.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Tại sao loại h&igrave;nh nhập H11 phải đưa v&agrave;o b&aacute;o c&aacute;o: trường hợp n&agrave;y xảy ra khi doanh nghiệp thực hiện việc nhập h&agrave;ng mẫu, hoặc nguy&ecirc;n phụ liệu mẫu về. Nhưng v&ocirc; t&igrave;nh, hay cố &yacute; đem lu&ocirc;n NPL đ&oacute; v&agrave;o sản xuất, dẫn tới ch&ecirc;nh lệch số liệu b&aacute;o c&aacute;o quyết to&aacute;n. Kh&ocirc;ng c&oacute; đầu v&agrave;o NPL nhưng lại c&oacute; đầu ra sản phẩm. Hầu hết dncx kh&ocirc;ng để &yacute; đến việc n&agrave;y n&ecirc;n c&aacute;c bạn lưu &yacute; nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">C&aacute;c bạn c&oacute; thể join v&agrave;o nh&oacute;m <a href=\"https://www.facebook.com/bcqthaneximclub/\">Hỗ trợ l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n &ndash; Kiểm tra sau th&ocirc;ng quan</a> của ch&uacute;ng t&ocirc;i tr&ecirc;n facebook.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">---</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Mọi b&agrave;i copy vui l&ograve;ng ghi nguồn của ch&uacute;ng t&ocirc;i:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\"><a href=\"http://tuhocxuatnhapkhau.com\">Trung t&acirc;m đ&agrave;o tạo nghiệp vụ XNK - Logistics</a> <a href=\"https://www.facebook.com/lophocxuatnhapkhau\">HAN EXIM CLUB</a>.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Địa chỉ: Số 4 - ng&aacute;ch 23 - ng&otilde; 82 - Phố Ch&ugrave;a L&aacute;ng - Quận Đống Đa - H&agrave; Nội</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">H&atilde;y l&agrave; người văn minh c&aacute;c bạn nh&eacute;.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Hotline về lịch khai giảng, học ph&iacute; <a href=\"http://tuhocxuatnhapkhau.com/khoa-huan-luyen/huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018\">lớp học b&aacute;o c&aacute;o quyết to&aacute;n</a> vui l&ograve;ng li&ecirc;n hệ: 0974 155 033 Mr Long.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:14px\">Tr&acirc;n trọng.</span></p>','/2a/af/f2/5cf5d14b4ac48.jpg',NULL,146,0,'2019-06-04 09:02:51',NULL,'doanh nghiệp chế xuất, khu chế xuất, khu phi thuế quan, loại hình xuất nhập khẩu, báo cáo quyết toán','doanh nghiệp chế xuất, khu chế xuất, khu phi thuế quan, loại hình xuất nhập khẩu, báo cáo quyết toán','','',2,1,'vi','cac-loai-hinh-xuat-nhap-khau-phai-lam-bao-cao-quyet-toan-cua-doanh-nghiep-che-xuat',10,27,27,'2019-06-04 09:02:51','2019-06-04 09:02:51');

