/*Table structure for table `ad_document` */

DROP TABLE IF EXISTS `ad_document`;

CREATE TABLE `ad_document` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT 'Ho ten nguoi gop y',
  `description` text,
  `file_path` varchar(255) NOT NULL,
  `extension` varchar(25) DEFAULT NULL,
  `document_number` varchar(150) DEFAULT NULL,
  `document_date` datetime DEFAULT NULL,
  `priority` int(4) DEFAULT NULL,
  `category_id` int(8) DEFAULT NULL,
  `created_by` bigint(20) DEFAULT NULL,
  `updated_by` bigint(20) DEFAULT NULL,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `is_home` tinyint(4) DEFAULT '0',
  `is_active` tinyint(4) DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `category_id_idx` (`category_id`),
  KEY `created_by_idx` (`created_by`),
  KEY `updated_by_idx` (`updated_by`)
) ENGINE=MyISAM AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;

/*Data for the table `ad_document` */

insert  into `ad_document`(`id`,`name`,`description`,`file_path`,`extension`,`document_number`,`document_date`,`priority`,`category_id`,`created_by`,`updated_by`,`created_at`,`updated_at`,`is_home`,`is_active`) values (8,'Luật Báo chí','Luật Báo chí','/3d/74/43/55f290ca8451c.docx','','1','2015-09-11 00:00:00',1,2,NULL,1,'2015-09-11 15:28:58','2015-09-14 21:18:40',0,1),(9,'Test văn bản thử','3333','/f0/a4/4f/55f29bf0aad27.doc','','2222','2015-09-11 00:00:00',1,3,NULL,1,'2015-09-11 16:16:32','2015-09-17 05:46:26',0,1),(11,'Test văn bản pháp luật','','/5c/49/95/55f9f134c82ba.doc','','11','2015-09-17 00:00:00',NULL,4,1,1,'2015-09-17 05:46:12','2015-09-17 05:46:12',0,1);

/*Table structure for table `ad_document_category` */

DROP TABLE IF EXISTS `ad_document_category`;

CREATE TABLE `ad_document_category` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT 'Ho ten nguoi gop y',
  `image_path` varchar(255) DEFAULT NULL,
  `description` text,
  `priority` int(5) DEFAULT NULL,
  `created_by` bigint(20) DEFAULT NULL,
  `updated_by` bigint(20) DEFAULT NULL,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `is_active` tinyint(4) DEFAULT '0',
  `is_home` tinyint(1) DEFAULT '0',
  `keywords` text,
  PRIMARY KEY (`id`),
  KEY `created_by_idx` (`created_by`),
  KEY `updated_by_idx` (`updated_by`)
) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

/*Data for the table `ad_document_category` */

insert  into `ad_document_category`(`id`,`name`,`image_path`,`description`,`priority`,`created_by`,`updated_by`,`created_at`,`updated_at`,`is_active`,`is_home`,`keywords`) values (2,'Luật- Nghị Định- Thông tư','/1e/cb/b1/5a70836815ebe.jpg','Tổng hợp các văn bản Luật, Nghị định, Thông tư, Công văn hướng dẫn của các bộ ban ngành... từ các nguồn tài liệu chính thống, thuận tiện cho doanh nghiệp và người khai hải quan sử dụng.',3,1,27,'2015-05-31 19:17:32','2018-03-01 09:58:17',1,1,'Luật hải quan, thông tư 38/2015, biểu thuế xuất nhập khẩu song ngữ 2018 mới nhất, CV 1786/TCHQ, danh mục hàng hóa có điều kiện, QD38-2017-QĐ-TTg'),(3,'Phần mềm chuyên dụng','/30/85/53/556b423c6d243.png','Tổng hợp các phần mềm chuyên dụng trong XNK, hỗ trợ tối đa hóa hiệu suất xử lý công việc.',23,1,27,'2015-05-31 19:17:48','2018-03-01 10:21:37',1,1,'phần mềm khai báo hải quan ecus5vnaccs, phần mềm trình ký, teamviewer 13, ultraviewer, pdf xchange viewer,'),(4,'Ebook','/6e/21/16/556b424620f8e.png','Kho ebook khổng lồ cho những ai đam mê nghiên cứu sâu sắc về lĩnh vực XNK, các bài viết được chia sẻ lại dưới dạng ebook để bạn dễ dàng lưu về máy và chủ động sử dụng bất kỳ lúc nào.',2,1,27,'2015-05-31 19:17:58','2018-03-01 10:23:33',1,1,'ebook xuất nhập khẩu, tải tài liệu xuất nhập khẩu, sách về xuất nhập khẩu'),(6,'Tin học Xuất nhập khẩu','/6e/21/16/556b424620f8e.png','Hướng dẫn các kỹ năng tin học thiết yếu sử dụng trong xuất nhập khẩu, giúp bạn thành thạo các kỹ năng \"ghi điểm\" trong mắt nhà tuyển dụng và đồng nghiệp',4,1,27,'2015-05-31 19:17:58','2018-03-01 10:35:21',1,1,'tin học xuất nhập khẩu, excel xuất nhập khẩu'),(7,'Chứng từ mẫu',NULL,'Hàng chục mẫu chứng từ các loại phát sinh trong nhiều nghiệp vụ khác nhau của quy trình supply chain, hỗ trợ trực quan cho học viên và người mới vào nghề tiếp cận chứng từ đầy đủ, chủ động',0,26,27,'2018-02-23 13:26:09','2018-03-01 10:42:17',1,0,'chứng từ xuất nhập khẩu, tờ khai hải quan, vận đơn đường biển, vận đơn gốc, chứng nhận xuất xứ, hóa đơn thương mại, công văn xin gia hạn...');

/*Table structure for table `ad_document_download` */

DROP TABLE IF EXISTS `ad_document_download`;

CREATE TABLE `ad_document_download` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT 'Tên video',
  `description` text NOT NULL COMMENT 'mô tả',
  `body` longtext COMMENT 'Nội dung bài viết trên web',
  `link` varchar(255) NOT NULL COMMENT 'link google driver',
  `image` varchar(255) NOT NULL COMMENT 'ảnh đại diện',
  `priority` bigint(20) NOT NULL COMMENT 'Độ ưu tiên hiển thị',
  `is_active` tinyint(1) DEFAULT '0' COMMENT 'Trạng thái',
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `category_id` bigint(22) DEFAULT NULL COMMENT 'cat chuyen muc',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=121 DEFAULT CHARSET=utf8;

/*Data for the table `ad_document_download` */

insert  into `ad_document_download`(`id`,`name`,`description`,`body`,`link`,`image`,`priority`,`is_active`,`created_at`,`updated_at`,`category_id`) values (3,'ABBYY FineReader Pro 12','Phần mềm chuyển đổi ảnh chụp sang dạng chữ','<p><br />\r\n<a href=\"https://drive.google.com/open?id=1AYi6OJuJgIew7BTJqGzmtVmxgsbnIu32\"><strong>ABBYY FineReader Pro 12</strong></a>&nbsp;l&agrave; phần mềm hỗ trợ chuyển đổi gần như tất cả c&aacute;c loại file h&igrave;nh, văn bản.... sang&nbsp;PDF&nbsp;hoặc chuyển từ file PDF, h&igrave;nh ảnh&nbsp;sang c&aacute;c định dạng text, chữ như&nbsp;word... Bạn c&oacute; thể chuyển (convert) những file pdf, h&igrave;nh ảnh sang word,&nbsp;excel...<br />\r\n<br />\r\nChuyển đổi PDF sang</p>\r\n\r\n<ul>\r\n	<li>Microsoft Word DOC, DOCX)</li>\r\n	<li>Microsoft Excel XLS, XLSX)</li>\r\n	<li>Rich Text Format (RTF)</li>\r\n	<li>HTML</li>\r\n	<li>TXT</li>\r\n	<li>PDF/A</li>\r\n</ul>\r\n\r\n<p>Điểm đặc biệt nhất của phần mềm n&agrave;y m&agrave; m&igrave;nh kh&aacute; ấn tượng l&agrave; c&oacute; khả năng chuyển đổi từ h&igrave;nh&nbsp;<strong>ảnh chụp sang word</strong>.<br />\r\nBạn c&oacute; thể chuyển đổi chữ từ s&aacute;ch, t&agrave;i liệu ra th&agrave;nh văn bản word đơn giản với&nbsp;<a href=\"https://drive.google.com/open?id=1AYi6OJuJgIew7BTJqGzmtVmxgsbnIu32\">ABBYY FineReader Pro 12</a>. Chỉ cần chụp ảnh v&agrave; bỏ v&agrave;o phần mềm, n&oacute; sẽ chuyển sang dạng file word cực k&igrave; tốt m&agrave; kh&ocirc;ng bị lỗi font chữ hay mất chữ đối với file gốc bằng Tiếng Anh, với file Tiếng Việt th&igrave; c&oacute; lỗi nhưng kh&ocirc;ng nhiều.</p>\r\n\r\n<p><a href=\"https://drive.google.com/open?id=1AYi6OJuJgIew7BTJqGzmtVmxgsbnIu32\"><img alt=\"\" src=\"/uploads/ckfinder/images/download.gif\" style=\"height:41px; width:160px\" /></a></p>','https://drive.google.com/open?id=1AYi6OJuJgIew7BTJqGzmtVmxgsbnIu32','/21/98/82/5a6a98b9c280f.jpg',0,0,'2018-01-26 09:54:00','2018-03-02 08:42:44',3),(4,'BB.FlashBack.Pro.5.7.0.3607','Phần mềm quay màn hình ','<p><a href=\"https://drive.google.com/open?id=1NlzVszD7UYv-5lw5JY-uCrhGzqMPuYOa\">BB FlashBack Pro</a> l&agrave; một trong những phần mềm quay m&agrave;n h&igrave;nh chuy&ecirc;n nghiệp chất lượng cao. Với dung lượng nhỏ gọn khả năng ghi lại thao t&aacute;c m&agrave;n h&igrave;nh với chất lượng cao. Đặc biệt phần mềm hỗ trợ xuất ra nhiều định dạng kh&aacute;c nhau l&agrave; ưu điểm của BB FlashBack Pro.</p>\r\n\r\n<p><strong>BB FlashBack Pro </strong>hỗ trợ 3 kiểu quay th&ocirc;ng dụng:</p>\r\n\r\n<ol>\r\n	<li>Full Screen: Quay to&agrave;n bộ m&agrave;n h&igrave;nh Desktop</li>\r\n	<li>Windows: Quay cửa sổ hiện tại</li>\r\n	<li>Region: Quay những vị tr&iacute; bạn chọn</li>\r\n</ol>\r\n\r\n<p>C&aacute;c t&iacute;nh năng ch&iacute;nh :</p>\r\n\r\n<ol>\r\n	<li>Ghi lại c&aacute;c thao t&aacute;c tr&ecirc;n m&agrave;n h&igrave;nh m&aacute;y t&iacute;nh</li>\r\n	<li>Ghi &acirc;m từ loa m&aacute;y t&iacute;nh, Microphone</li>\r\n	<li>Tr&iacute;ch xuất ra c&aacute;c định dạng video th&ocirc;ng d&ugrave;ng.</li>\r\n	<li>Chỉnh sửa File Video sau khi quay một c&aacute;ch chuy&ecirc;n nghiệp</li>\r\n	<li>T&ugrave;y chọn thay đổi c&aacute;c th&ocirc;ng số đầu ra</li>\r\n	<li>Hoạt động ẩn tr&ecirc;n khay hệ thống</li>\r\n</ol>\r\n\r\n<p><a href=\"https://drive.google.com/open?id=1NlzVszD7UYv-5lw5JY-uCrhGzqMPuYOa\"><img alt=\"\" src=\"/uploads/ckfinder/images/download.gif\" style=\"height:41px; width:160px\" /></a></p>','https://drive.google.com/open?id=1NlzVszD7UYv-5lw5JY-uCrhGzqMPuYOa','/2a/04/42/5a6a9a21b7f4f.jpg',50,1,'2018-01-26 10:01:53','2018-04-25 22:30:39',3),(5,'Camtasia studio 8','Phần mềm quay màn hình đa năng','<p><a href=\"https://drive.google.com/open?id=1WbJMSsmpxJg9IfarjmTXeeNBZGO22HYk\">Camtasia Studio</a> l&agrave; một trong số những phần mềm đa năng nhất hiện nay trong,&nbsp;Camtasia Studio&nbsp;c&oacute; thể d&ugrave;ng để quay phim m&agrave;n h&igrave;nh, chụp ảnh desktop cũng như chỉnh sửa, cắt gh&eacute;p video, ch&egrave;n bản quyền. Camtasia Studio c&oacute; giao diện trực quan, th&iacute;ch hợp với những ai ghi video l&agrave;m thủ thuật, hướng dẫn m&aacute;y t&iacute;nh, chơi game...</p>\r\n\r\n<p><a href=\"https://drive.google.com/open?id=1WbJMSsmpxJg9IfarjmTXeeNBZGO22HYk\"><strong><img alt=\"\" src=\"/uploads/ckfinder/images/download.gif\" style=\"height:41px; width:160px\" /></strong></a></p>','https://drive.google.com/open?id=1WbJMSsmpxJg9IfarjmTXeeNBZGO22HYk','/2c/c9/92/5a6a9b9c9f9a7.jpg',40,1,'2018-01-26 10:05:17','2018-04-25 22:30:52',3),(6,'Kho lưu trữ tài liệu Dropbox','Lưu trữ tài liệu trực tuyến bằng Dropbox','<p><a href=\"https://drive.google.com/open?id=1cHj99ELwVyxsowUYmZPVTdn23HZd-3xD\">Dropbox</a>&nbsp;l&agrave; một trong những dịch vụ lưu trữ dữ liệu trực tuyến miễn ph&iacute;, cho ph&eacute;p người d&ugrave;ng mang theo tất cả h&igrave;nh ảnh, t&agrave;i liệu v&agrave; video tới bất cứ nơi đ&acirc;u v&agrave; xem tr&ecirc;n bất kỳ thiết bị kết nối n&agrave;o.&nbsp;Điều n&agrave;y c&oacute; nghĩa l&agrave; tập tin bạn đ&atilde; lưu v&agrave;o Dropbox sẽ tự động lưu v&agrave;o m&aacute;y t&iacute;nh, điện thoại của bạn v&agrave; cả tr&ecirc;n&nbsp;Dropbox Web.</p>\r\n\r\n<p><a href=\"https://drive.google.com/open?id=1cHj99ELwVyxsowUYmZPVTdn23HZd-3xD\"><strong><img alt=\"\" src=\"/uploads/ckfinder/images/download.gif\" style=\"height:41px; width:160px\" /></strong></a></p>','https://drive.google.com/open?id=1cHj99ELwVyxsowUYmZPVTdn23HZd-3xD','/f3/a1/1f/5a6a9b8c4bac9.jpg',1,1,'2018-01-26 10:07:56','2018-02-04 20:24:28',6),(7,'Phần mềm duyệt ký Ecussign','Phần mềm trình ký và duyệt trình ký Ecussign Basic.','<p>Phần mềm tr&igrave;nh k&yacute; v&agrave; duyệt tr&igrave;nh k&yacute; <a href=\"https://drive.google.com/open?id=1Iajo01zzNgA6QXGuP-Q8DM_TbZFby7Ok\">Ecussign Basic</a>.</p>\r\n\r\n<p><a href=\"https://drive.google.com/open?id=1Iajo01zzNgA6QXGuP-Q8DM_TbZFby7Ok\"><img alt=\"\" src=\"/uploads/ckfinder/images/download.gif\" style=\"height:41px; width:160px\" /></a></p>','https://drive.google.com/open?id=1Iajo01zzNgA6QXGuP-Q8DM_TbZFby7Ok','/47/00/04/5a6a9cd909d30.jpg',2,1,'2018-01-26 10:12:52','2018-02-04 20:24:53',3),(8,'Foxit Reader','Foxit Reader phần mềm chuyển đổi pdf miễn phí.','<p>Ngo&agrave;i chức năng đọc file pdf, Foxit Reader c&ograve;n cho ph&eacute;p bạn chỉnh sửa nội dung của file đ&oacute; nữa. Với phần mềm n&agrave;y bạn c&oacute; thể th&ecirc;m m&ocirc; tả, th&ecirc;m ghi ch&uacute; hay ch&uacute; th&iacute;ch cũng như đ&aacute;nh dấu c&aacute;c đoạn, c&aacute;c c&acirc;u quan trọng trong đoạn đ&oacute;.<br />\r\nNgo&agrave;i ra phần mềm c&ograve;n hỗ trợ <strong>in ấn file PDF</strong> chuy&ecirc;n nghiệp đặc biệt ph&ugrave; hợp với những l&agrave;m văn ph&ograve;ng v&agrave; học sinh, sinh vi&ecirc;n, c&aacute;n bộ&hellip;</p>\r\n\r\n<p><a href=\"https://drive.google.com/open?id=1wS_yc_XW1248dE4C_SlqBny1Ad5rE_Jk\"><img alt=\"\" src=\"/uploads/ckfinder/images/download.gif\" style=\"height:41px; width:160px\" /></a></p>','https://drive.google.com/open?id=1wS_yc_XW1248dE4C_SlqBny1Ad5rE_Jk','/18/59/91/5a6a9ed4a4d79.jpg',45,1,'2018-01-26 10:21:56','2018-04-25 22:31:05',3),(9,'Microsoft Office 2013 Full Crack','Microsoft Office 2013 Full Crack','<p>Tải về bộ c&ocirc;ng cụ tin học văn ph&ograve;ng&nbsp;<a href=\"https://drive.google.com/open?id=1vYTmWCgEXsjcpUZT4Gr-rCDCfVFOl3Hh\">Microsoft Office 2013</a>&nbsp;tại đ&acirc;y.</p>\r\n\r\n<p><a href=\"https://drive.google.com/open?id=1vYTmWCgEXsjcpUZT4Gr-rCDCfVFOl3Hh\"><img alt=\"\" src=\"/uploads/ckfinder/images/download.gif\" style=\"height:41px; width:160px\" /></a></p>','https://drive.google.com/open?id=1vYTmWCgEXsjcpUZT4Gr-rCDCfVFOl3Hh','/fd/84/4f/5a6a9f4919a38.jpg',4,1,'2018-01-26 10:23:53','2018-02-04 20:26:08',3),(10,'iMindMap 9.0.0','Phần mềm sơ đồ tư duy iMindMap 9.0.0','<p><a href=\"https://drive.google.com/open?id=1VSNo0vzvBQRIJ7W7dRZ_ASX_HR0fTUc-\">iMindMap 9.0.0</a> một c&ocirc;ng cụ tuyệt vời gi&uacute;p bạn ph&aacute;c họa &yacute; tưởng của bạn ra sản phẩm thực sự, n&oacute; gi&uacute;p bạn nhanh ch&oacute;ng thực hiện &yacute; tưởng của m&igrave;nh.</p>\r\n\r\n<p>Từ &yacute; tưởng đến hiện thực với imidmap chỉ cần 3 bước đơn giản l&agrave; bạn c&oacute; thể thực hiện n&oacute;.</p>\r\n\r\n<p><a href=\"https://drive.google.com/open?id=1VSNo0vzvBQRIJ7W7dRZ_ASX_HR0fTUc-\"><img alt=\"\" src=\"/uploads/ckfinder/images/download.gif\" style=\"height:41px; width:160px\" /></a></p>','https://drive.google.com/open?id=1VSNo0vzvBQRIJ7W7dRZ_ASX_HR0fTUc-','/c3/78/8c/5a6a9fbc15e26.jpg',5,1,'2018-01-26 10:25:48','2018-02-04 20:26:26',3),(11,'PDF XChange Viewer ','PDF XChange Viewer: phần mềm chỉnh sửa chứng từ chuyên nghiệp cho dân văn phòng','<p>Với PDF-XChange Viewer bạn cũng c&oacute; thể y&ecirc;n t&acirc;m chuyển đổi cho bất k&igrave; t&agrave;i liệu PDF n&agrave;o m&igrave;nh đang sở hữu bao gồm cả c&aacute;c t&agrave;i liệu được bảo vệ bằng mật khẩu hoặc bị hạn chế c&aacute;c quyền sao ch&eacute;p, in ấn, lưu văn bản&hellip; T&iacute;nh năng n&agrave;y g&oacute;p phần bảo đảm cho qu&aacute; tr&igrave;nh chuyển đổi diễn ra hiệu quả khi kh&ocirc;ng bỏ qua bất k&igrave; văn bản PDF n&agrave;o.<br />\r\n<br />\r\nNgo&agrave;i ra, chương tr&igrave;nh PDF-XChange Viewer c&ograve;n hỗ trợ người d&ugrave;ng một số c&ocirc;ng cụ để đọc hay lấy dữ liệu từ file PDF như: xuất bản nội dung file PDF như trang web HTML, chụp t&agrave;i liệu PDF, t&igrave;m kiếm đoạn văn, tr&iacute;ch xuất file PDF&hellip; Những c&ocirc;ng cụ n&agrave;y rất cần thiết để người d&ugrave;ng thực hiện chia sẻ dữ liệu của tệp tin PDF được tiện lợi.<br />\r\n<br />\r\n<strong>Những t&iacute;nh năng ch&iacute;nh của PDF-XChange Viewer:</strong></p>\r\n\r\n<p>- Hỗ trợ c&aacute;c định dạng đầu ra cho việc chuyển đổi file PDF: DOC, RTF, JPG, GIF, PNG, BMP, TIF, PCX, TGA, HTML, TXT.<br />\r\n- Khả năng chuyển đổi cho bất k&igrave; file PDF bao gồm file c&oacute; mật khẩu hoặc bị thiết lập c&aacute;c hạn chế.<br />\r\n- Cung cấp một số c&ocirc;ng cụ cho ph&eacute;p lấy dữ liệu dễ d&agrave;