/*Table structure for table `ad_giang_vien` */

DROP TABLE IF EXISTS `ad_giang_vien`;

CREATE TABLE `ad_giang_vien` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT 'Tên giảng viên',
  `description` text NOT NULL COMMENT 'mô tả',
  `image` varchar(255) NOT NULL COMMENT 'ảnh đại diện',
  `priority` bigint(20) NOT NULL COMMENT 'Độ ưu tiên hiển thị',
  `is_active` tinyint(1) DEFAULT '0' COMMENT 'Trạng thái',
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `body` longtext,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

/*Data for the table `ad_giang_vien` */

insert  into `ad_giang_vien`(`id`,`name`,`description`,`image`,`priority`,`is_active`,`created_at`,`updated_at`,`body`) values (1,'Dương Trang (Ms Sarah)','Ms Trang là chuyên viên Logistics nhưng học viên biết đến Ms Trang như một chuyên gia đào tạo cả về kỹ năng mềm. Ms Trang sẽ đồng hành cùng các bạn để các bạn thực sự tỏa sáng, luôn là người tích cực trong cuộc sống và công việc.','/4a/f4/44/5b34c6b11db61.jpg',2,1,'2018-01-12 16:28:07','2018-06-28 18:29:53',''),(2,'Đoàn Thúy (Mrs Jade)','Mrs Thúy là chuyên viên Xuất nhập khẩu với 8 năm kinh nghiệm tại các Công ty Xuất nhập khẩu. Mrs Thúy sẽ là người truyền cảm hứng  giúp các bạn học viên tự tin hơn trong công việc và dẫn dắt bạn đến gần hơn với nghề Xuất nhập khẩu','/f7/c8/8f/5b6c449591856.png',5,1,'2018-01-13 19:56:47','2018-08-09 20:41:41','');

/*Table structure for table `ad_hoc_vien` */

DROP TABLE IF EXISTS `ad_hoc_vien`;

CREATE TABLE `ad_hoc_vien` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT 'Tên học viên',
  `description` text NOT NULL COMMENT 'mô tả',
  `image` varchar(255) NOT NULL COMMENT 'ảnh đại diện',
  `priority` bigint(20) NOT NULL COMMENT 'Độ ưu tiên hiển thị',
  `is_active` tinyint(1) DEFAULT '0' COMMENT 'Trạng thái',
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `body` longtext,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=82 DEFAULT CHARSET=utf8;

/*Data for the table `ad_hoc_vien` */

insert  into `ad_hoc_vien`(`id`,`name`,`description`,`image`,`priority`,`is_active`,`created_at`,`updated_at`,`body`) values (9,'Bùi Ngọc Anh (1995)- Đại học Ngoại Thương','','/c5/f1/1c/5a6859a5e9cbd.jpg',1,1,'2018-01-24 13:51:43','2019-06-02 19:00:42','<p>C&ocirc; b&eacute; rất hiền l&agrave;nh, d&ugrave; trong lớp&nbsp;&iacute;t ph&aacute;t biểu hay&nbsp;đưa ra c&acirc;u hỏi nhưng bạn lại thể hiện qu&aacute; &rdquo;xuất sắc&rdquo; với Nh&agrave; tuyển dụng th&ocirc;ng qua buổi phỏng vấn dựa v&agrave;o những kinh nghiệm phỏng vấn&nbsp;được học tại CLB Y&ecirc;u Xuất Nhập Khẩu. Giờ bạn&nbsp;đang l&agrave;m vị tr&iacute; &nbsp;mua h&agrave;ng tại C&ocirc;ng ty nhập khẩu gỗ. C&ugrave;ng lắng nghe những chia sẻ của Ngọc Anh từ ng&agrave;y bắt đầu phỏng vấn đến&nbsp;ng&agrave;y&nbsp;đầu sau khi tr&uacute;ng tuyển v&agrave; qu&aacute; tr&igrave;nh thăng tiến&nbsp;của bạn nh&eacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NA1-3.jpg\" style=\"height:345px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NA4-6.jpg\" style=\"height:345px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NA7-9.jpg\" style=\"height:345px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NA4.jpg\" style=\"height:661px; width:500px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NA6.jpg\" style=\"height:889px; width:500px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/NA1-3.jpg\" style=\"height:345px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/NA4-6.jpg\" style=\"height:345px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/NA7-9.jpg\" style=\"height:345px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/NA4.jpg\" style=\"height:960px; width:726px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"bui ngoc anh\" /><img alt=\"\" src=\"/uploads/ckfinder/images/NA6.jpg\" style=\"height:960px; width:540px\" /></p>'),(10,'Bùi Thị Miền (1995)- Đại học Thương Mại','','/fb/65/5f/5a6976bb84471.jpg',2,1,'2018-01-24 14:08:28','2019-06-02 19:01:08','<p>Bạn t&igrave;m đến Lớp học HAN EXIM CLUB qua việc tự t&igrave;m kiếm tr&ecirc;n mạng về c&aacute;ch Soạn chứng từ Xuất nhập khẩu, l&agrave; một sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh Thương Mại Quốc Tế n&ecirc;n bạn rất cẩn thận trước khi chọn nơi học cho m&igrave;nh. Qua nhiều nguồn th&ocirc;ng tin v&agrave; so s&aacute;nh những nơi kh&aacute;c, bạn đ&atilde; quyết định chọn học với hai c&ocirc; gi&aacute;o. Đến giữa kh&oacute;a học bạn c&oacute; đợt tập huấn tr&ecirc;n trường để đi l&agrave;m tại C&ocirc;ng ty XNK v&agrave; được đ&aacute;nh gi&aacute; cao hơn so với những bạn kh&aacute;c. Ch&uacute;ng ta sẽ update bạn sau một thời gian nữa nh&eacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/MIEN.jpg\" style=\"height:337px; width:559px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/bui%20mien2.jpg\" style=\"height:932px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/bui%20mien3.jpg\" style=\"height:337px; width:559px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/bui%20mien2.jpg\" style=\"height:932px; width:540px\" /></p>'),(11,'Đào Sỹ Tiến (1994)- ĐH Kinh tế Quốc Dân','','/3e/07/73/5a68589c144e1.jpg',3,1,'2018-01-24 16:57:48','2019-06-02 19:01:35','<p>Ch&agrave;ng trai rất &ldquo;man&rdquo;, lu&ocirc;n gi&uacute;p&nbsp;đỡ c&aacute;c bạn c&ugrave;ng lớp. Sau khi ho&agrave;n th&agrave;nh kh&oacute;a học, hiện bạn&nbsp;đang l&agrave;m tại C&ocirc;ng ty XNK về Dầu v&agrave; h&oacute;a chất. C&ugrave;ng xem những chia sẻ ng&agrave;y&nbsp;đầu l&agrave;m việc của bạn nh&eacute;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/sy%20tien.jpg\" style=\"height:356px; width:388px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/sy%20tien%201.jpg\" style=\"height:356px; width:388px\" /></p>\r\n\r\n<p>Bạn đi l&agrave;m được khoảng 1 th&aacute;ng th&igrave; được C&ocirc;ng ty cử đi tiếp đối t&aacute;c Singapore tại S&agrave;i G&ograve;n. C&ocirc;ng việc được &ldquo;đi đ&oacute; đi đ&acirc;y&rdquo; l&agrave; ước mơ của rất nhiều bạn khi bước v&agrave;o nghề Xuất Nhập Khẩu, cố gắng trau dồi ngoại ngữ v&agrave; tư duy, th&aacute;i độ tốt th&igrave; sớm muộn c&aacute;c em sẽ được nh&igrave;n nhận.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/sy%20tien%202.jpg\" style=\"height:435px; width:490px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/sy%20tien%202.jpg\" style=\"height:435px; width:490px\" /></p>\r\n\r\n<p>V&agrave; thỉnh thoảng viết mail hỏi thăm 2 c&ocirc; gi&aacute;o</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/sy%20tien%203.jpg\" style=\"height:960px; width:540px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/sy%20tien%203.jpg\" style=\"height:960px; width:540px\" /></p>\r\n\r\n<p>&nbsp;</p>'),(12,'Đào Thị Hạ Giang (1994)- ĐH Tài Nguyên Môi Trường','','/cd/a7/7c/5a68595ebfa97.jpg',6,1,'2018-01-24 17:01:02','2019-06-02 19:02:22','<p>L&agrave; một sinh vi&ecirc;n mới tốt nghiệp ĐH T&agrave;i Nguy&ecirc;n M&ocirc;i Trường, bạn đến với xuất nhập khẩu để phụ gi&uacute;p c&ocirc;ng việc kinh doanh của gia đ&igrave;nh. C&ugrave;ng l&uacute;c học th&ecirc;m cả Xuất Nhập Khẩu tại HAN EXIM CLUB v&agrave; học nghiệp vụ kế to&aacute;n tại một nơi kh&aacute;c nhưng bạn đ&atilde; &ldquo;m&ecirc;&rdquo; Xuất nhập khẩu v&agrave; đ&atilde; c&oacute; những th&agrave;nh quả đầu ti&ecirc;n khi l&agrave;m cho ch&iacute;nh gia đ&igrave;nh m&igrave;nh. Nếu bạn thực sự đam m&ecirc; th&igrave; kh&ocirc;ng g&igrave; l&agrave; kh&ocirc;ng thể phải kh&ocirc;ng?</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/GIANG%20DAO%201.jpg\" style=\"height:345px; width:540px\" /></p>'),(13,'Đào Thị Thùy Dương (1996)- Đại học Kinh tế Quốc Dân','','/1b/15/51/5a685ee37d246.jpg',3,1,'2018-01-24 17:24:35','2019-06-02 19:02:35','<p>C&ocirc; g&aacute;i trẻ của ch&uacute;ng ta đang c&oacute; một thế mạnh đ&oacute; l&agrave; ham học hỏi v&agrave; th&aacute;i độ t&iacute;ch cực, v&agrave;o lớp học của hai chị sau đ&oacute; bạn đ&atilde; c&oacute; sự bứt ph&aacute; ngoạn mục. Bạn gần như l&agrave; một ứng vi&ecirc;n m&agrave; bất cứ nh&agrave; tuyển dụng n&agrave;o cũng muốn c&oacute;, pass phỏng vấn v&agrave; nhận việc ch&iacute;nh thức lu&ocirc;n, kh&ocirc;ng bị &aacute;p lương thử việc. C&ugrave;ng xem những g&igrave; bạn ấy n&oacute;i về cuộc phỏng vấn v&agrave; c&ocirc;ng việc sau một thời gian đi l&agrave;m nh&eacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"thuy-duong\" /><img alt=\"\" src=\"/uploads/ckfinder/images/thuy%20duong.jpg\" style=\"height:345px; width:1036px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/thuy%20duong%203.jpg\" style=\"height:345px; width:586px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thuy%20duong%202.jpg\" style=\"height:345px; width:1036px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thuy%20duong%203.jpg\" style=\"height:345px; width:586px\" /></p>'),(14,'Đỗ Thị Hải (1993)- Học viện Nông nghiệp Việt Nam','','/7f/91/17/5a68600f9c37c.jpg',6,1,'2018-01-24 17:29:35','2019-06-02 19:02:58','<p>C&acirc;u chuyện của Hải l&agrave; 1 tấm gương về việc &ldquo;C&oacute; c&ocirc;ng m&agrave;i sắt c&oacute; ng&agrave;y n&ecirc;n kim&rdquo;, xuất ph&aacute;t điểm kh&ocirc;ng c&oacute; kiến thức về kinh tế n&oacute;i chung v&agrave; Xuất nhập khẩu n&oacute;i ri&ecirc;ng. Sau khi c&ocirc;ng t&aacute;c tại một c&ocirc;ng ty về bảo hộ lao động tại H&agrave; Nam, bạn đăng k&yacute; lớp Chủ nhật (học cả ng&agrave;y) v&agrave; chưa hề nghỉ buổi học n&agrave;o. Đối với bạn mỗi buổi đi học thực sự qu&yacute; gi&aacute; v&igrave; bạn phải đi cả chặng đường d&agrave;i. Kết quả l&agrave; hiện bạn đang l&agrave;m vị tr&iacute; Mua h&agrave;ng v&agrave; Xuất nhập khẩu. C&aacute;c bạn c&ugrave;ng xem phản hồi bạn ấy về kết quả buổi phỏng vấn b&ecirc;n dưới.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/do%20hai%202.jpg\" style=\"height:345px; width:582px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/do%20hai%202.jpg\" style=\"height:345px; width:582px\" /></p>'),(15,'Đoàn Thị Quỳnh (1995)- Đại học Kinh tế Quốc Dân','','/3a/83/33/5a68620ca5e16.jpg',3,1,'2018-01-24 17:38:04','2019-06-02 19:03:15','<p>Học vi&ecirc;n đi l&agrave;m rồi bận qu&aacute; to&agrave;n qu&ecirc;n &ldquo;b&aacute;o c&aacute;o&rdquo; sau khi học th&ocirc;i. Bạn Quỳnh đ&atilde; đi l&agrave;m vị tr&iacute; Nh&acirc;n vi&ecirc;n mua h&agrave;ng&nbsp; trước khi kết th&uacute;c kh&oacute;a học 2 tuần. Nhờ những kiến thức đ&atilde; được đ&agrave;o tạo m&agrave; bạn cũng &iacute;t phải hỏi lại gi&aacute;o vi&ecirc;n sau &nbsp;khi học bởi lẽ tr&ecirc;n lớp bạn ghi ch&eacute;p v&agrave; học h&agrave;nh rất chăm chỉ, lại c&ograve;n chủ động học tập v&agrave; t&igrave;m t&ograve;i nữa. Ch&uacute;ng ta c&ugrave;ng ch&uacute;c mừng cho Quỳnh nh&eacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"DOAN-QUYNH\" /><img alt=\"\" src=\"/uploads/ckfinder/images/DOAN%20QUYNH.jpg\" style=\"height:345px; width:388px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/DOAN%20QUYNH.jpg\" style=\"height:345px; width:388px\" /></p>'),(16,'Lê Đức Trung (1995)- Đại học Mỏ Địa Chất','','/ee/a5/5e/5a68631ca7791.jpg',6,1,'2018-01-24 17:42:36','2019-06-02 19:03:28','<p>Xuất ph&aacute;t điểm từ một chuy&ecirc;n ng&agrave;nh kh&aacute;c nhưng một khi đ&atilde; đến với HAN EXIM CLUB l&agrave; bạn ấy đ&atilde; gửi gắm tất cả hy vọng v&agrave; niềm tin để c&oacute; một nghề mới. Học vi&ecirc;n lớp Chủ Nhật cứ h&agrave;ng tuần đều đặn từ sang đến chiều tối, kh&ocirc;ng nghỉ buổi n&agrave;o. Trước khi đi phỏng vấn c&ograve;n định lập team &ocirc;n b&agrave;i vậy m&agrave; chưa kịp lập team đ&atilde; pass phỏng vấn v&agrave; đi l&agrave;m rồi.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/LE%20DUC%20TRUNG.jpg\" style=\"height:345px; width:388px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/LE%20DUC%20TRUNG.jpg\" style=\"height:345px; width:388px\" /></p>'),(17,'Hoàng Mạnh Thắng (1995)- Khoa Hải quan, ĐH Kinh tế Quốc dân','','/bb/2d/db/5a68645d6ac38.jpg',3,1,'2018-01-24 17:47:57','2019-06-02 19:03:38','<p>Một dấu ấn kh&ocirc;ng thể lẫn v&agrave;o đ&acirc;u được của bạn ấy: với vẻ ngo&agrave;i v&agrave; phong th&aacute;i nghi&ecirc;m chỉnh như c&aacute;n bộ Hải quan, học khoa Hải quan trường KTQD, c&oacute; biệt danh ở trường l&agrave; &ldquo;Gi&aacute;o sư&rdquo; v&igrave; kiến thức nền của bạn qu&aacute; chắc n&ecirc;n những ng&agrave;y đầu bạn ấy c&ograve;n lạnh l&ugrave;ng v&agrave; &iacute;t n&oacute;i. Tuy nhi&ecirc;n, bạn ấy c&oacute; th&aacute;i độ học tập nghi&ecirc;m t&uacute;c v&agrave; tư duy rất mở với kiến thức mới. N&ecirc;n dần dần bạn ấy cũng &ldquo;ngấm&rdquo; được niềm đam m&ecirc; nghề nghiệp m&agrave; c&aacute;c chị truyền cho.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/thang%20hoang.jpg\" style=\"height:539px; width:825px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/thang%20hoang%202.jpg\" style=\"height:932px; width:540px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thang%20hoang.jpg\" style=\"height:539px; width:825px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thang%20hoang%202.jpg\" style=\"height:932px; width:540px\" /></p>'),(18,'Hoàng Thị Minh Nguyệt (1995)- Đại học Giao thông vận tải','','/00/70/00/5a68661c0bfa2.jpg',6,1,'2018-01-24 17:55:24','2019-06-02 19:03:52','<p>Bạn ấy đến với lớp học Xuất nhập khẩu từ con số 0 nhưng v&ocirc; c&ugrave;ng chăm chỉ, lu&ocirc;n ngồi b&agrave;n đầu, ghi ch&eacute;p đầy đủ, kh&ocirc;ng &iacute;t lần được C&ocirc; gi&aacute;o tuy&ecirc;n dương. V&agrave; kết quả kh&ocirc;ng ngo&agrave;i mong đợi, giờ bạn đ&atilde; v&agrave; đang l&agrave;m vị tr&iacute; Chứng từ+ Khai b&aacute;o Hải quan, v&agrave; được &aacute;p dụng c&aacute;c kiến thức đ&atilde; học. Dưới đ&acirc;y l&agrave; chia sẻ của bạn ấy.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/MINH%20NGUYET.jpg\" style=\"height:345px; width:388px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/MINH%20NGUYET.jpg\" style=\"height:345px; width:388px\" /></p>\r\n\r\n<p>&nbsp;</p>'),(19,'Nguyễn Thị Hương Danh (1995)- Đại học Kinh tế Quốc dân','','/cb/59/9c/5a6867aee7e68.jpg',3,1,'2018-01-24 18:02:06','2019-06-02 19:04:12','<p>C&ocirc; g&aacute;i nhỏ nhắn, xinh xắn với nụ cười tỏa s&aacute;ng tuy nhi&ecirc;n vẫn thiếu tự tin mặc d&ugrave; xuất th&acirc;n từ đ&uacute;ng chuy&ecirc;n ng&agrave;nh được đ&agrave;o tạo. Bạn Danh l&agrave; 1 học vi&ecirc;n rất chăm chỉ, t&iacute;ch cực trong học tập, trải qua kh&oacute;a học bạn đ&atilde; trưởng th&agrave;nh l&ecirc;n rất nhiều, tự tin hơn. Ch&uacute;c mừng bạn đ&atilde; trở th&agrave;nh nh&acirc;n vi&ecirc;n chứng từ h&agrave;ng xuất tại c&ocirc;ng ty Xuất khẩu.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/HUONG%20DANH.jpg\" style=\"height:345px; width:388px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/HUONG%20DANH.jpg\" style=\"height:345px; width:388px\" /></p>'),(20,'Lê Gia Sơn (1996)- Đại học Ngoại Thương','','/0a/c8/80/5a6887e609938.jpg',1,1,'2018-01-24 20:19:34','2019-06-02 19:04:28','<p>Bạn đến với kh&oacute;a học khi mới l&agrave; sinh vi&ecirc;n năm ba Ngoại Thương, với định hướng sớm như vậy v&agrave; kết hợp với việc học đ&uacute;ng chuy&ecirc;n ng&agrave;nh th&igrave; ngay từ năm ba bạn đ&atilde; được tiếp x&uacute;c với c&aacute;c c&ocirc;ng việc thực tế tại Doanh nghiệp, c&oacute; điều kiện để &aacute;p dụng những kiến thức đ&atilde; học v&agrave; c&ograve;n gi&uacute;p việc học tr&ecirc;n lớp của bạn tốt hơn. Vậy c&aacute;c bạn năm ba c&ugrave;ng lấy bạn Sơn l&agrave;m tấm gương noi theo nh&eacute;.<img alt=\"\" src=\"/uploads/ckfinder/images/gia%20son.jpg\" style=\"height:345px; width:388px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/gia%20son.jpg\" style=\"height:345px; width:388px\" /></p>'),(21,'Lê Thị Thanh Lan (1991)- Nhân viên kế toán','','/8f/bd/d8/5a6895348b922.jpg',7,1,'2018-01-24 21:16:20','2019-06-02 19:04:40','<p>Xuất ph&aacute;t điểm từ một nh&acirc;n vi&ecirc;n kế to&aacute;n với background cũng kh&ocirc;ng li&ecirc;n quan g&igrave; đến nghề XNK. Nhưng với t&iacute;nh chủ động học hỏi v&agrave; tư duy nhanh nhạy, bạn ấy đ&atilde; vượt qua tất cả mọi kh&oacute; khăn cản trở của việc &ldquo;l&agrave;m tr&aacute;i nghề&rdquo; để vượt l&ecirc;n th&agrave;nh một nh&acirc;n vi&ecirc;n Logistics sắc sảo v&agrave; cứng cỏi chỉ sau 6 th&aacute;ng đi l&agrave;m.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/THANH%20lan%20email.jpg\" style=\"height:960px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thanh%20lan%202.jpg\" style=\"height:482px; width:604px\" /></p>\r\n\r\n<p>Học vi&ecirc;n của CLB sau khi tốt nghiệp đi l&agrave;m, c&ograve;n quay lại CLB chia sẻ kinh nghiệm đi l&agrave;m để gi&uacute;p đỡ c&aacute;c bạn kh&oacute;a sau c&oacute; th&ecirc;m sự chuẩn bị tốt nhất cho c&ocirc;ng việc. Rất cảm ơn c&aacute;c em về những t&igrave;nh cảm đặc biệt đ&oacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/THANH%20LAN%20LETTER.jpg\" style=\"height:273px; width:750px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/thanh%20lan%202.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>'),(22,'Lê Thị Hợp (1992)- Đại học Thủy Lợi (Hà Nội)','','/6f/62/26/5a6898ee22ec0.jpg',6,1,'2018-01-24 21:32:14','2019-06-02 19:04:52','<p>Đ&acirc;y cũng l&agrave; 1 bạn học vi&ecirc;n như vậy, đặc biệt l&agrave; kh&ocirc;ng c&oacute; li&ecirc;n quan g&igrave; đến Kinh tế cả, bạn học khoa M&ocirc;i trường của Đại học Thủy Lợi. C&aacute;i duy&ecirc;n đưa bạn đến lớp học giữa bao nhi&ecirc;u băn khoăn kh&ocirc;ng biết c&oacute; l&agrave;m được nghề n&agrave;y kh&ocirc;ng, sau buổi khai giảng được tư vấn từ gi&aacute;o vi&ecirc;n bạn đ&atilde; quyết t&acirc;m theo học. Trong qu&aacute; tr&igrave;nh học bạn biết m&igrave;nh biết ta, cố gắng gấp đ&ocirc;i, gấp ba những người kh&aacute;c v&agrave; kết quả ngo&agrave;i mong đợi khi sau kh&oacute;a học 1 th&aacute;ng l&agrave; bạn đ&atilde; c&oacute; c&ocirc;ng việc. Bạn c&oacute; t&acirc;m sự về việc Nh&agrave; tuyển dụng kh&aacute; bất ngờ v&igrave; những kiến thức bạn c&oacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/em%20hop%202.jpg\" style=\"height:538px; width:649px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/em%20hop%204.jpg\" style=\"height:430px; width:890px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/em%20hop%202.jpg\" style=\"height:538px; width:649px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/em%20hop%204.jpg\" style=\"height:430px; width:890px\" /></p>'),(23,'Lương Thị Thu Hà (1990)- Nhân viên kế toán','','/b3/ab/bb/5a689c5452e3d.jpg',7,1,'2018-01-24 21:46:44','2019-06-02 19:05:04','<p>Học vi&ecirc;n comment về trải nghiệm sau khi đi l&agrave;m, trước đ&oacute; bạn ấy kh&ocirc;ng c&oacute; ch&uacute;t kiến thức g&igrave; về Xuất nhập khẩu v&igrave; xuất th&acirc;n từ kế to&aacute;n. Giờ th&igrave; bạn ấy đang được l&agrave;m c&ocirc;ng việc m&igrave;nh th&iacute;ch. Đọc kỹ chia sẻ của bạn ấy v&igrave; những kiến thức trong kh&oacute;a học đ&atilde; gi&uacute;p bạn ấy vượt qua buổi phỏng vấn thế n&agrave;o để được nhận l&agrave;m xuất nhập khẩu nh&eacute;.<img alt=\"\" src=\"/uploads/ckfinder/images/luong%20ha%201.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/luong%20thu%20ha%202.jpg\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/luong%20ha%201.jpg\" style=\"height:345px; width:740px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/luong%20ha%202.jpg\" style=\"height:345px; width:740px\" /></p>'),(24,'Hoàng Thị Mai Hương (1996)- Đại học Ngoại Thương','','/63/3c/c6/5a689de404aa6.jpg',1,1,'2018-01-24 21:53:24','2019-06-02 19:05:27','<p>Một bạn học vi&ecirc;n FTU mới chỉ năm cuối đ&atilde; được nhận v&agrave;o c&ocirc;ng ty lớn. C&aacute;c em h&atilde;y nhớ rằng &quot;th&ocirc;ng minh kh&ocirc;ng bằng chăm chỉ&quot;, &quot;th&aacute;i độ hơn tr&igrave;nh độ&quot; c&ograve;n c&oacute; được cả 2 điều n&agrave;y bằng Perfect. Sau kh&oacute;a học với hai c&ocirc; gi&aacute;o, Mai Hương đ&atilde; trở n&ecirc;n rất tự tin, c&oacute; th&aacute;i độ tốt, t&iacute;ch cực v&agrave; đi đến đ&acirc;u cũng l&agrave; t&acirc;m điểm ch&uacute; &yacute;.<img alt=\"\" src=\"/uploads/ckfinder/images/mai%20huong%20bee.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/mai%20huong%20bee.jpg\" style=\"height:345px; width:388px\" /></p>\r\n\r\n<p>&nbsp;</p>'),(25,'Mai Thị Khuyên (1995)- ĐH Kinh tế Quốc dân','','/0e/5f/f0/5a697e630d51a.jpg',3,1,'2018-01-24 22:02:39','2019-06-02 19:18:58','<p>Một c&ocirc; g&aacute;i vui vẻ, th&acirc;n thiện với nụ cười lu&ocirc;n thường trực tr&ecirc;n khu&ocirc;n mặt v&agrave;&nbsp;th&aacute;i độ học tập &ndash; l&agrave;m việc nghi&ecirc;m t&uacute;c c&ugrave;ng tinh thần cầu tiến đ&atilde; gi&uacute;p em vượt qua rất nhiều ứng vi&ecirc;n &ldquo;con &ocirc;ng ch&aacute;u cha&rdquo; để c&oacute; một khởi đầu tốt hơn cả mong đợi! Đ&acirc;y cũng l&agrave; điều m&agrave; C&ocirc; gi&aacute;o lu&ocirc;n nhấn mạnh với c&aacute;c em, TH&Aacute;I ĐỘ HƠN TR&Igrave;NH ĐỘ!</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/mai%20khuyen.jpg\" style=\"height:710px; width:922px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/mai%20khuyen%202.jpg\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/mai%20khuyen.jpg\" style=\"height:710px; width:922px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/mai%20khuyen%202.jpg\" style=\"height:345px; width:393px\" /></p>'),(26,'Phạm Thị Minh Thanh (1995)- Học viện chính sách phát triển','','/91/6e/e9/5a68a116bfbee.jpg',8,1,'2018-01-24 22:07:02','2019-06-02 19:17:18','<p>Từ một c&ocirc; g&aacute;i nh&uacute;t nh&aacute;t, e d&egrave; phải một thời gian d&agrave;i khi đ&atilde; thấy rất nhiều bạn b&egrave; c&ugrave;ng trường th&agrave;nh c&ocirc;ng, c&oacute; việc l&agrave;m ngay sau kh&oacute;a học b&ecirc;n HAN EXIM CLUB th&igrave; bạn ấy mới đăng k&yacute; học. Bạn học tiến bộ từng ng&agrave;y, giờ bạn cũng đ&atilde; c&oacute; c&ocirc;ng việc m&igrave;nh mong muốn l&agrave; Nh&acirc;n vi&ecirc;n chứng từ h&agrave;ng xuất. Kh&ocirc;ng c&oacute; g&igrave; l&agrave; qu&aacute; muộn nếu em thực hiện n&oacute; ngay h&ocirc;m nay phải kh&ocirc;ng n&agrave;o?<img alt=\"\" src=\"/uploads/ckfinder/images/minh%20thanh%202.jpg\" style=\"height:924px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/minh%20thanh%202.jpg\" style=\"height:924px; width:540px\" /></p>'),(27,'Mr Nguyễn Ngọc Anh (1986)- NV xuất nhập khẩu','','/29/2c/c2/5a68a29862d8a.jpg',7,1,'2018-01-24 22:13:28','2019-06-02 19:06:15','<p>Mr Ngọc Anh- xuất th&acirc;n từ ĐH Ngoại Thương nhưng anh đ&atilde; dường như &ldquo;qu&ecirc;n&rdquo; gần hết những g&igrave; đ&atilde; được học ở trường sau 1 thời gian d&agrave;i kh&ocirc;ng l&agrave;m g&igrave; li&ecirc;n quan đến kinh tế hay xuất nhập khẩu. Anh l&agrave; người anh C&ocirc; gi&aacute;o v&agrave; c&aacute;c học vi&ecirc;n lu&ocirc;n y&ecirc;u mến bởi sự nhiệt t&igrave;nh của anh. Giờ anh đ&atilde; c&oacute; rất nhiều kinh nghiệm về xuất nhập khẩu rồi v&agrave; lu&ocirc;n d&otilde;i theo sự th&agrave;nh c&ocirc;ng của c&aacute;c học vi&ecirc;n kh&aacute;c.<img alt=\"\" src=\"/uploads/ckfinder/images/mr%20ngoc%20anh%202.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/mr%20ngoc%20anh%202.jpg\" style=\"height:345px; width:630px\" /></p>'),(28,'Ngô Thị Dung (1995)- ĐH Giao thông vận tải','','/0d/e6/60/5a68a40429113.jpg',6,1,'2018-01-24 22:19:32','2019-06-02 19:06:53','<p>Vui mừng kh&ocirc;n xiết khi kheo c&ocirc; gi&aacute;o v&igrave; đ&atilde; tr&uacute;ng tuyển nhưng trước đ&oacute; khi mới bước v&agrave;o kh&oacute;a học, bạn c&ograve;n kh&aacute; nh&uacute;t nh&aacute;t v&agrave; lo lắng v&igrave; chưa c&oacute; kiến thức về Xuất nhập khẩu- Logistics nhưng sau khi ho&agrave;n th&agrave;nh kh&oacute;a học, hiện bạn&nbsp;đang l&agrave;m Cus cho c&ocirc;ng ty về Logistics. H&atilde;y tiếp tục học học v&agrave; n&acirc;ng cao kỹ năng nghiệp vụ trở n&ecirc;n xuất sắc Dung nh&eacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/dung%20ngo%202.jpg\" style=\"height:345px; width:398px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/dung%20ngo%202.jpg\" style=\"height:345px; width:398px\" /></p>'),(29,'Nguyễn Đỗ Thái Hưng – (1994) –  Đại học Ngoại Thương','','/61/d8/86/5a68a52310b9a.jpg',1,1,'2018-01-24 22:24:19','2019-06-02 19:07:00','<p>L&agrave; một trong những học vi&ecirc;n kh&oacute;a đầu ti&ecirc;n đ&atilde; c&oacute; nhiều trải nghiệm đ&aacute;ng kể trong qu&aacute; tr&igrave;nh r&egrave;n luyện tại HAN EXIM CLUB v&agrave; trong thời gian đi l&agrave;m. Hiện nay bạn ấy c&ograve;n trở lại CLB hỗ trợ lớp học với tư c&aacute;ch &ldquo;người đi trước&rdquo;, chia sẻ cho c&aacute;c học vi&ecirc;n kh&oacute;a sau những kiến thức thực tế m&agrave; bạn ấy t&iacute;ch g&oacute;p được thời gian vừa qua.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/hung%20thai%202.jpg\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/hung%20thai%202.jpg\" style=\"height:788px; width:940px\" /></p>'),(30,'Nguyễn Phương Lan (1991)- NV kế hoạch vật tư','','/48/0b/b4/5a6931b0cd4ea.jpg',7,1,'2018-01-25 08:24:00','2019-06-02 19:07:13','<p>Đang từ một nh&acirc;n vi&ecirc;n kế hoạch, c&ocirc;ng việc ổn định nhưng quyết t&acirc;m đổi nghề bằng việc tham gia kh&oacute;a học Nghiệp vụ Xuất nhập khẩu chỗ C&ocirc; gi&aacute;o. V&agrave; sau khi học xong, bạn đ&atilde; th&agrave;nh c&ocirc;ng với việc trở th&agrave;nh một nh&acirc;n vi&ecirc;n Chứng từ ki&ecirc;m Dịch vụ kh&aacute;ch hang (Documentation and Customer service)</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/phuong%20lan%202.jpg\" style=\"height:932px; width:540px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/phuong%20lan%202.jpg\" style=\"height:932px; width:540px\" /></p>'),(31,'Nguyễn Quang Lượng (1995) – Đại học Kinh Tế Quốc Dân','','/6d/8e/e6/5a6933fae6885.jpg',3,1,'2018-01-25 08:33:46','2019-06-02 19:07:19','<p>Lượng xuất ph&aacute;t l&agrave; một sinh vi&ecirc;n Khoa Kinh tế Quốc Tế, ngay sau kh&oacute;a học bạn đ&atilde; được thực tập tại C&ocirc;ng ty Xuất Nhập Khẩu, tại đ&acirc;y bạn đ&atilde; c&oacute; điều kiện học hỏi rất nhiều điều từ người đi trước. Sauk hi ra trường bạn c&oacute; ngay c&ocirc;ng việc trong Nội đ&ocirc; về Chứng từ v&agrave; Khai b&aacute;o Hải quan. Bạn cũng đ&atilde; c&oacute; &ldquo;c&aacute;i t&acirc;m&rdquo; của người l&agrave;m nghề khi mất ăn mất ngủ lo cho l&ocirc; h&agrave;ng của c&ocirc;ng ty. Nh&igrave;n thấy học tr&ograve; của m&igrave;nh mỗi ng&agrave;y một gi&agrave; dặn hơn, kinh nghiệm hơn v&agrave; ch&iacute;n chắn hơn, đ&oacute; l&agrave; niềm vui kh&ocirc;ng thể n&agrave;o diễn tả nổi bằng lời của những người dẫn đường.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/quang%20luong.jpg\" style=\"height:345px; width:600px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/quang%20luong.jpg\" style=\"height:345px; width:600px\" /></p>'),(32,'Nguyễn Thị Hạnh (1995) – Học viện chính sách phát triển','','/95/6e/e9/5a6934cc25853.jpg',8,1,'2018-01-25 08:37:16','2019-06-02 19:17:23','<p>Suốt 21 buổi học em rất chăm chỉ, lặng lẽ &ldquo;nuốt&rdquo; từng lời từ b&agrave;i giảng của c&aacute;c chị. Chịu kh&oacute; thực h&agrave;nh nhiều, khả năng tự học tốt. Em c&oacute; bề ngo&agrave;i lạnh l&ugrave;ng nhưng khi t&acirc;m sự với c&aacute;c chị, thực ra b&ecirc;n trong em l&agrave; cả một tr&aacute;i tim đầy nhiệt huyết v&agrave; sự ki&ecirc;n nhẫn hiếm c&oacute;, em c&oacute; t&igrave;nh y&ecirc;u s&acirc;u sắc với nghề gi&aacute;o v&agrave; niềm đam m&ecirc; với nghề Xuất nhập khẩu. Em l&agrave; một c&ocirc; b&eacute; &ldquo;gan l&igrave;&rdquo;, kh&ocirc;ng chọn con đường bằng phẳng v&agrave; &ecirc;m &aacute;i để đi m&agrave; ngược lại, em quyết định cho bản th&acirc;n m&igrave;nh được t&ocirc;i luyện bằng việc chọn khởi đầu với ch&ocirc;ng gai v&agrave; thử th&aacute;ch.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/hanh.jpg\" style=\"height:960px; width:540px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NGUYEN%20THI%20HANH.jpg\" style=\"height:960px; width:540px\" /></p>'),(33,'Nguyễn Thị Hoàng Mai (1995)-  Học viện Chính sách phát triển','','/ce/2b/bc/5a69361d8f062.jpg',8,1,'2018-01-25 08:42:53','2019-06-02 19:17:29','<p>C&ocirc; b&eacute; chăm chỉ, cần mẫn, hay ph&aacute;t biểu v&agrave; lu&ocirc;n biết lắng nghe. Hiện tại bạn&nbsp;đang l&agrave;m Chứng từ tại C&ocirc;ng ty về Logistics. Khi&nbsp;đi phỏng vấn, bạn&nbsp;ấy d&aacute;m bỏ những c&ocirc;ng ty trả lương cao hơn nhưng kh&ocirc;ng&nbsp;được vận dụng những g&igrave;&nbsp;đ&atilde; học&nbsp;để theo&nbsp;đuổi mục ti&ecirc;u về nghề XNK-logistics.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/hoang%20mai.jpg\" style=\"height:345px; width:540px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/hoang%20mai.jpg\" style=\"height:345px; width:540px\" /></p>'),(34,'Nguyễn Thị Ngọc (1995)- Đại học Kinh tế- ĐH Quốc Gia Hà Nội','','/3c/2a/a3/5a697d30b2eb6.jpg',6,1,'2018-01-25 08:51:52','2019-06-02 19:07:51','<p>Đến với lớp học như một c&aacute;i duy&ecirc;n, bạn đi ngược lại với đ&aacute;m đ&ocirc;ng bạn b&egrave; rủ học ở những nơi quảng c&aacute;o rầm rộ, ho&agrave;nh tr&aacute;ng. V&agrave; thực sự bạn đ&atilde; đ&uacute;ng, học vi&ecirc;n đến với ch&uacute;ng t&ocirc;i bằng tấm gương từ những bạn b&egrave; đi trước, theo d&otilde;i d&agrave;i hạn tr&ecirc;n facebook. Học vi&ecirc;n chất qu&aacute; đ&uacute;ng kh&ocirc;ng c&aacute;c bạn?</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/ngoc%20nemo2.jpg\" style=\"height:345px; width:388px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/ngoc%20nemo2.jpg\" style=\"height:345px; width:388px\" /></p>'),(35,'Nguyễn Thị Ngợi (1995)- ĐH Kinh tế Quốc dân','','/4f/63/34/5a693af8b3439.jpg',3,1,'2018-01-25 09:03:36','2019-06-02 19:08:20','<p>Bạn Ngợi đ&atilde; tr&uacute;ng tuyển v&agrave;o Honda Việt Nam. H&atilde;y c&ugrave;ng xem những chia sẻ của bạn ấy v&agrave; h&atilde;y tin những g&igrave; ch&uacute;ng t&ocirc;i day cho c&aacute;c bạn đều c&oacute; l&yacute; do của n&oacute;, đ&atilde; rất nhiều bạn tr&uacute;ng tuyển cả v&agrave;o những tập đo&agrave;n lớn như Samsung, LC, Honda, KMG&hellip;nhờ v&agrave;o việc l&agrave;m đủ v&agrave; đ&uacute;ng những y&ecirc;u cầu tr&ecirc;n lớp. Ch&uacute;ng t&ocirc;i lu&ocirc;n cố gắng để c&aacute;c bạn được thực h&agrave;nh nhiều nhất những g&igrave; trong thực tế sẽ gặp để đi phỏng vấn trở th&agrave;nh &ldquo;cuộc dạo chơi&rdquo; chứ kh&ocirc;ng phải &aacute;p lực.<img alt=\"\" src=\"/uploads/ckfinder/images/ngoi%202.jpg\" style=\"height:345px; width:776px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/ngoi3.jpg\" style=\"height:932px; width:540px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/ngoi%202.jpg\" style=\"height:345px; width:776px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/ngoi3.jpg\" style=\"height:932px; width:540px\" /></p>'),(36,'Nguyễn Thị Thảo (1994)- Sinh viên ĐH ngoại ngữ – ĐH Quốc gia Hà Nội','','/12/a8/81/5a6941fb82e02.jpg',6,1,'2018-01-25 09:33:31','2019-06-02 19:08:29','<p>Một c&ocirc; g&aacute;i s&ocirc;i nổi v&agrave; t&iacute;ch cực. Biết m&igrave;nh xuất ph&aacute;t điểm tr&aacute;i ng&agrave;nh so với c&aacute;c bạn Ngoại thương c&ugrave;ng lớp, Thảo đ&atilde; rất cố gắng học tập v&agrave; r&egrave;n luyện rất chăm chỉ những g&igrave; được học. Ngay sau kh&oacute;a học, Thảo đ&atilde; được nhận v&agrave;o một c&ocirc;ng ty xuất khẩu l&agrave;m việc với mức lương khởi điểm rất xứng đ&aacute;ng. Dần dần với th&aacute;i độ l&agrave;m việc tốt c&ugrave;ng những thể hiện của m&igrave;nh, Thảo được c&ocirc;ng ty tin tưởng giao ph&oacute; cho c&aacute;c nhiệm vụ quan trọng, kể cả đi c&ocirc;ng t&aacute;c nước ngo&agrave;i v&agrave; tham gia b&agrave;n đ&agrave;m ph&aacute;n với kh&aacute;ch h&agrave;ng đến từ Ch&acirc;u &Acirc;u.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/thao%202.jpg\" style=\"height:788px; width:940px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/thao%201.jpg\" style=\"height:363px; width:262px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thao%202.jpg\" style=\"height:461px; width:550px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thao%201.jpg\" style=\"height:363px; width:262px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thao%206.jpg\" style=\"height:241px; width:550px\" /></p>\r\n\r\n<p>Đ&acirc;y l&agrave; thư em Thảo viết bằng word gửi email tới c&aacute;c chị để chia sẻ suy nghĩ của m&igrave;nh về thời gian b&ecirc;n cạnh c&aacute;c chị v&agrave; lớp học của em. Xin ph&eacute;p Thảo nh&eacute; ^^</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thao%204.jpg\" style=\"height:712px; width:550px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thao%205.jpg\" style=\"height:712px; width:550px\" /></p>'),(37,'Trần Thị Chinh (1994) – Học viện Nông nghiệp Việt Nam','','/09/3e/e0/5a69471493f32.jpg',6,1,'2018-01-25 09:55:16','2019-06-02 19:08:37','<p>Ng&agrave;y đầu tới lớp với đ&ocirc;i mắt ngơ ng&aacute;c kh&ocirc;ng r&otilde; định hướng, lại rất rụt r&egrave; v&agrave; &iacute;t n&oacute;i. Tiếp x&uacute;c với c&ocirc; b&eacute; mới biết em c&oacute; rất nhiều nỗi sợ h&atilde;i, lu&ocirc;n thiếu tự tin về bản th&acirc;n. Hiện tại, em Chinh đang l&agrave;m việc rất tự tin v&agrave; cứng c&aacute;p với vai tr&ograve; l&agrave; nh&acirc;n vi&ecirc;n chứng từ &ndash; khai b&aacute;o hải quan cho c&ocirc;ng ty logistics.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/chinh%202.jpg\" style=\"height:788px; width:940px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/chinh%202.jpg\" /></p>'),(38,'Nguyễn Thu Hà (1991)- NV kế toán','','/1e/a7/71/5a694b2a4a6e3.jpg',7,1,'2018-01-25 10:12:42','2019-06-02 19:08:47','<p>Cũng giống như c&aacute;c bạn học vi&ecirc;n chuyển đổi nghề nghiệp kh&aacute;c, khi đến với kh&oacute;a học ban đầu bạn cũng c&ograve;n băn khoăn hay với mục ti&ecirc;u đơn giản l&agrave; học để tăng th&ecirc;m hiểu biết. Nhưng nhờ kiến thức trong kh&oacute;a học bạn đ&atilde; thể hiện được tại c&ocirc;ng ty cũ để c&ocirc;ng ty giao cho bạn ki&ecirc;m nhiệm th&ecirc;m về XNK. Kết quả l&agrave; mức lương của bạn đ&atilde; tăng so với trước, đ&uacute;ng l&agrave; &ldquo;đầu tư cho gi&aacute;o dục l&agrave; sự đầu tư kh&ocirc;n ngoan nhất&rdquo; phải kh&ocirc;ng c&aacute;c bạn?</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/thu%20ha%202.jpg\" style=\"height:345px; width:582px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thu%20ha%202.jpg\" /></p>'),(39,'Nguyễn Thúy Quỳnh (1995)- ĐH Kinh tế- Đại học Quốc Gia Hà Nội','','/ec/87/7e/5a694e360db67.jpg',6,1,'2018-01-25 10:25:42','2019-06-02 19:08:59','<p>C&ograve;n tận 2 tuần nữa mới ho&agrave;n th&agrave;nh kh&oacute;a học m&agrave; bạn đ&atilde; &ldquo;trốn&rdquo; C&ocirc; gi&aacute;o đi phỏng vấn v&agrave; tr&uacute;ng tuyển ngay v&agrave;o vị tr&iacute; Mua h&agrave;ng. Hiện tại bạn đang l&agrave;m rất tốt qua chia sẻ dưới đ&acirc;y nh&eacute;:</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/THUY%20QUYNH%201.jpg\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/THUY%20QUYNH%201.jpg\" style=\"height:450px; width:810px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/THUY%20QUYNH%202.jpg\" style=\"height:345px; width:388px\" /></p>'),(40,'Phạm Thị Dịu (1995) – Học viện chính sách phát triển','','/45/33/34/5a69529932561.jpg',8,1,'2018-01-25 10:44:25','2019-06-02 19:17:33','<p>Đ&acirc;y l&agrave; một trong những học vi&ecirc;n đặc biệt nhất. Hồi em mới tham gia CLB, em l&agrave; một c&ocirc; b&eacute; cực kỳ nh&uacute;t nh&aacute;t v&agrave; &iacute;t n&oacute;i. Giờ&nbsp;kh&ocirc;ng chỉ l&agrave; một nh&acirc;n vi&ecirc;n chứng từ-khai b&aacute;o hải quan, em c&ograve;n ki&ecirc;m cả việc thay c&ocirc;ng ty tuyển dụng nh&acirc;n sự cho c&aacute;c vị tr&iacute; kh&aacute;c nhau, v&agrave; nguồn em ấy lựa chọn tin cậy nhất ch&iacute;nh l&agrave; c&aacute;c học vi&ecirc;n kh&oacute;a sau của CLB. Sự thay đổi ngoạn mục của em cũng ch&iacute;nh l&agrave; đ&ograve;n bẩy tinh thần, tạo sức ảnh hưởng rất lớn đến c&aacute;c bạn c&ugrave;ng lớp của m&igrave;nh, c&ugrave;ng d&igrave;u dắt nhau bước v&agrave;o một ng&agrave;nh nghề mới: nghề xuất nhập khẩu- logistics.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/9.jpg\" style=\"height:788px; width:940px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/diu%20pham.jpg\" style=\"height:345px; width:582px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/DIU%20PHAM%201.jpg\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/diu%20pham.jpg\" style=\"height:345px; width:582px\" /></p>'),(41,'Phạm Thị Hải Yến (1995)- Đại học Ngoại thương','','/5e/2c/c5/5a69563f867e9.jpg',1,1,'2018-01-25 10:59:59','2019-06-02 19:09:13','<p>Ngay từ buổi đầu kh&oacute;a học bạn đ&atilde; rất h&agrave;o hứng v&agrave; rất chăm chỉ, kh&ocirc;ng nghỉ bất cứ buổi học n&agrave;o. Mặc d&ugrave; đến từ trường Ngoại thương nhưng bạn rất khi&ecirc;m tốn, biết m&igrave;nh thiếu những kỹ năng g&igrave; v&agrave; lu&ocirc;n ham học hỏi. Ch&iacute;nh sự khi&ecirc;m tốn đ&oacute; đ&atilde; l&agrave;m bạn tiếp thu kiến thức một c&aacute;ch rất trọng vẹn.</p>\r\n\r\n<p>Khi đi phỏng vấn hầu hết lần n&agrave;o bạn cũng pass do đ&atilde; được huấn luyện kỹ c&agrave;ng v&agrave; c&oacute; cẩm nang trả lời phỏng vấn của lớp HAN EXIM</p>\r\n\r\n<p>Cuối c&ugrave;ng bạn đ&atilde; l&agrave;m c&ocirc;ng việc m&igrave;nh y&ecirc;u th&iacute;ch l&agrave; trở th&agrave;nh nh&acirc;n vi&ecirc;n Sales Xuất Khẩu, v&agrave; hơn nữa l&agrave; bạn đ&atilde; xuất được container h&agrave;ng đầu ti&ecirc;n khi c&ograve;n đang thử việc.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/yen%20pham%202.jpg\" style=\"height:345px; width:584px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/yen%20pham%201.jpg\" style=\"height:265px; width:556px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/yen%20pham%202.jpg\" style=\"height:345px; width:584px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/yen%20pham%203.jpg\" style=\"height:345px; width:398px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/yen%20pham%201.jpg\" style=\"height:265px; width:556px\" /></p>'),(42,'Phạm Trung Kiên (1996) – Đại học Ngoại Thương','','/43/79/94/5a695d01c5fb9.jpg',1,1,'2018-01-25 11:28:49','2019-06-02 19:09:23','<p>Bạn tham gia lớp học khi mới năm ba đại học. Bạn l&agrave; một học vi&ecirc;n rất chăm chỉ, chịu kh&oacute; ngo&agrave;i ra Ki&ecirc;n c&oacute; tố chất của người dẫn đầu với sự nhiệt t&igrave;nh, chu đ&aacute;o v&agrave; cầu tiến, em sẽ lu&ocirc;n l&agrave; một c&aacute; nh&acirc;n xuất sắc. H&atilde;y giữ g&igrave;n v&agrave; trau dồi hơn nữa để biến những đức t&iacute;nh đ&oacute; trở th&agrave;nh con người của m&igrave;nh Ki&ecirc;n nh&eacute;. Ch&uacute;ng t&ocirc;i sẽ lu&ocirc;n d&otilde;i theo từng bước ph&aacute;t triển của c&aacute;c học vi&ecirc;n.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/kien%202.jpg\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/kien%202.jpg\" style=\"height:450px; width:810px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/kien%203.jpg\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/kien%203.jpg\" style=\"height:345px; width:388px\" /></p>'),(43,'Phan Thị Thục Hiền (1994)- ĐH Kinh tế Quốc Dân','','/55/90/05/5a695ece11301.jpg',3,1,'2018-01-25 11:36:30','2019-06-02 19:11:33','<p>Xuất ph&aacute;t điểm cũng l&agrave; một nh&acirc;n vi&ecirc;n văn ph&ograve;ng &ldquo;chưa biết g&igrave; về XNK&rdquo;. Ngồi học trong lớp rất trầm, chỉ n&oacute;i khi được gọi t&ecirc;n. Nhưng đổi lại, bạn ấy rất chịu kh&oacute; thực h&agrave;nh v&agrave; ghi ch&eacute;p, tự học th&ecirc;m. V&agrave; kết quả như dưới đ&acirc;y&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/THUC%20HIEN.jpg\" style=\"height:450px; width:810px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/THUC%20HIEN.jpg\" style=\"height:450px; width:810px\" /></p>'),(44,'Vũ Thị Thúy – (1992) –  Nhân viên Logistics','','/83/4f/f8/5a69613137fe4.jpg',7,1,'2018-01-25 11:46:41','2019-06-02 19:11:44','<p>Một c&ocirc; g&aacute;i c&oacute; ho&agrave;n cảnh đặc biệt nhưng với sức mạnh ki&ecirc;n cường v&agrave; &yacute; ch&iacute; kh&ocirc;ng khuất phục, em đ&atilde; vượt mọi kh&oacute; khăn trong cuộc sống để vươn đến điều m&igrave;nh mong muốn. Vẻ ngo&agrave;i d&ugrave; c&oacute; rắn rỏi&nbsp;đến đ&acirc;u nhưng những cử chỉ quan t&acirc;m nhỏ nhặt n&agrave;y của em cũng đủ thể hiện l&agrave; một c&ocirc; b&eacute; t&igrave;nh cảm, s&acirc;u sắc. Cảm ơn em v&agrave; ch&uacute;c những điều tốt đẹp nhất sẽ đến với em!</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/vu%20thuy%202.jpg\" style=\"height:788px; width:940px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/vu%20thuy.jpg\" style=\"height:960px; width:540px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/vu%20thuy%202.jpg\" style=\"height:788px; width:940px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/vu%20thuy.jpg\" style=\"height:960px; width:540px\" /></p>'),(45,'Phùng Phương Thảo (1994) – ĐH Kinh tế quốc dân','','/21/24/42/5a69633bb640c.jpg',3,1,'2018-01-25 11:55:23','2019-06-02 19:11:56','<p>Những tin mừng nối tiếp tin mừng. Trong qu&aacute; tr&igrave;nh huấn luyện, ch&uacute;ng t&ocirc;i lu&ocirc;n hướng cho học vi&ecirc;n của m&igrave;nh r&egrave;n luyện cả kiến thức, kỹ năng lẫn tư duy. V&agrave; một trong những tư duy quan trọng l&agrave; CHỌN VIỆC thay v&igrave; XIN VIỆC. Khi nắm được tinh thần n&agrave;y, vị thế của c&aacute;c em cũng kh&aacute;c hẳn trong những buổi phỏng vấn tuyển dụng, v&agrave; kết quả r&otilde; r&agrave;ng dưới đ&acirc;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/phung%20thao2.jpg\" style=\"height:345px; width:388px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/phung%20thao2.jpg\" style=\"height:345px; width:388px\" /></p>'),(46,'Nguyễn Thị Thu Hương (1994)- Đại học Hà Nội','','/63/4a/a6/5a69674249200.jpg',6,1,'2018-01-25 12:12:34','2019-06-02 19:12:03','<p>Xuất ph&aacute;t l&agrave; sinh vi&ecirc;n khoa tiếng &Yacute;- Đại học H&agrave; Nội bạn t&igrave;m đến với HAN EXIM CLUB khi trong long c&ograve;n nhiều vướng mắc. Bạn chưa được tiếp x&uacute;c về khối ng&agrave;nh kinh tế v&agrave; xuất nhập khẩu th&igrave; c&agrave;ng kh&ocirc;ng, sau khi được tư vấn bạn đ&atilde; quyết định theo học. 20/11/2017 bạn b&aacute;o tin vui v&agrave;o ng&agrave;y đầy &yacute; nghĩa n&agrave;y, bạn đ&atilde; thực hiện ước mơ khi l&agrave;m Nh&acirc;n vi&ecirc;n mua h&agrave;ng (Purchasing) tại c&ocirc;ng ty Xuất nhập khẩu. Với c&ocirc;ng việc n&agrave;y bạn đ&atilde; c&oacute; thu nhập ổn định v&agrave; được sử dụng Ngoại ngữ kh&aacute; nhiều để phục vụ c&ocirc;ng việc. C&ugrave;ng xem chia sẻ của bạn g&aacute;i ấy về việc Những kiến thức được truyền đạt tr&ecirc;n lớp, kỹ năng, c&aacute;ch trả lời phỏng vấn được training bạn đ&atilde; &aacute;p dụng ra sao nh&eacute;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/thu%20huong%202.jpg\" style=\"height:345px; width:582px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thu%20huong%202.jpg\" style=\"height:345px; width:582px\" /></p>'),(47,'Trần Diệu Ly (1995)- Đại học Ngoại ngữ Hà Nội khoa tiếng Nga','','/f0/39/9f/5a6968b327ea9.jpg',6,1,'2018-01-25 12:18:43','2019-06-03 10:08:19','<p>C&ocirc; gi&aacute;o vẫn c&ograve;n nhớ h&igrave;nh ảnh của bạn khi đến buổi học đầu ti&ecirc;n bị c&ocirc; gi&aacute;o mắng v&igrave; kh&ocirc;ng c&oacute; &yacute; ch&iacute; th&igrave; sẽ kh&ocirc;ng l&agrave;m được g&igrave; nhưng chỉ sau buổi học đ&oacute; bạn đ&atilde; &rdquo;thay đổi&rdquo; rất rất nhiều. Ngo&agrave;i việc t&iacute;ch cực đi học, chăm chỉ học tập v&agrave; ghi ch&eacute;p bạn c&ograve;n được c&ocirc; gi&aacute;o chỉ ra c&aacute;c kỹ năng c&ograve;n thiếu. V&agrave;&hellip; tin vui đến ngay sau khi kết th&uacute;c kh&oacute;a học, bạn đ&atilde; thực hiện được ước mơ l&agrave;m Nh&acirc;n vi&ecirc;n xuất khẩu n&ocirc;ng sản, được sử dụng kh&aacute; nhiều ngoại ngữ l&agrave; tiếng Nga v&agrave; tiếng Anh trong c&ocirc;ng việc. C&ocirc; gi&aacute;o đ&atilde; thực hiện được &ldquo;lời hứa&rdquo; với bạn l&agrave; &ldquo;nếu em cố gắng em sẽ c&oacute; việc ngay sau khi học&rdquo;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/dieu%20ly%202.jpg\" style=\"height:345px; width:540px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/dieu%20ly%202.jpg\" style=\"height:345px; width:540px\" /></p>'),(48,'Trần Hữu Tiến (1995)- Đại học Kinh tế Quốc Dân','','/34/40/03/5a6969bccf690.jpg',3,1,'2018-01-25 12:23:08','2019-06-02 19:12:16','<p>Tiến l&agrave; học vi&ecirc;n nam rất t&iacute;ch cực trong mỗi giờ học tr&ecirc;n lớp, kỹ năng giao tiếp. Sau khi học xong, bạn rất khi&ecirc;m tốn, cứ muốn th&agrave;nh c&ocirc;ng rồi mới khoe C&ocirc; gi&aacute;o. C&oacute; được một c&ocirc;ng việc li&ecirc;n quan đến những g&igrave; được học l&agrave; th&agrave;nh c&ocirc;ng rồi phải kh&ocirc;ng c&aacute;c bạn? Ch&uacute;c cho Tiến sẽ m&atilde;i vững bước v&agrave; tự tin tr&ecirc;n con đường m&agrave; m&igrave;nh đ&atilde; chọn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/huu%20tien%202.jpg\" style=\"height:345px; width:578px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/huu%20tien%202.jpg\" style=\"height:345px; width:578px\" /></p>'),(49,'Trần Thị Hồng Nhung (1993)- Đại học Ngoại Thương','','/6a/1a/a6/5a696b2a8a5c1.jpg',1,1,'2018-01-25 12:29:14','2019-06-02 19:12:25','<p>Đến từ khoa Tiếng Ph&aacute;p của Ngoại Thương, kh&ocirc;ng c&oacute; nhiều kiến thức nền về XNK nhưng sau khi được đ&agrave;o tạo, học vi&ecirc;n của C&ocirc; gi&aacute;o to&agrave;n được &rdquo;chọn việc&rdquo; thế n&agrave;y đ&acirc;y. Khi đang học tại lớp Chủ Nhật&nbsp;Hồ T&ugrave;ng Mậu, kh&oacute;a c&ograve;n chưa kết th&uacute;c&nbsp;bạn ấy đi phỏng vấn đ&atilde; được 3/4 c&ocirc;ng ty gọi đi l&agrave;m.<br />\r\nBạn n&agrave;o c&ograve;n t&ograve; m&ograve; về C&ocirc; gi&aacute;o cứ chat với học vi&ecirc;n của C&ocirc; gi&aacute;o hỏi thoải m&aacute;i xem C&ocirc; gi&aacute;o &ldquo;training&rdquo; học vi&ecirc;n thế n&agrave;o m&agrave; được vậy nh&eacute;, input &rdquo;trước học&rdquo; ra sao v&agrave; output &rdquo;sau khi học&rdquo; như thế n&agrave;o.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/nhung%20tran%202.jpg\" style=\"height:345px; width:582px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/nhung%20tran%202.jpg\" style=\"height:345px; width:582px\" /></p>'),(50,'Trần Thị Huệ (1995)- Học viện chính sách phát triển','','/09/9d/d0/5a696bdd74b42.jpg',8,1,'2018-01-25 12:32:13','2019-06-02 19:17:38','<p>Từ một c&ocirc; g&aacute;i nh&uacute;t nh&aacute;t, e d&egrave; mặc d&ugrave; học đ&uacute;ng chuy&ecirc;n ng&agrave;nh nhưng bạn thiếu tự tin v&agrave;o c&aacute;c kiến thức tr&ecirc;n trường, kh&ocirc;ng biết sẽ &aacute;p dụng ra sao ngo&agrave;i thực tế n&ecirc;n đến với kh&oacute;a học bạn đ&atilde; cố gắng rất nhiều. Phần thưởng của bạn ch&iacute;nh l&agrave; c&ocirc;ng việc bạn đang l&agrave;m đ&atilde; được &aacute;p dụng những g&igrave; đ&atilde; học. Ch&uacute;c em sẽ gặt h&aacute;i được nhiều th&agrave;nh c&ocirc;ng tr&ecirc;n chặng đường d&agrave;i sau n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/tran%20hue.jpg\" style=\"height:345px; width:388px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/tran%20hue.jpg\" /></p>'),(51,'Trần Thị Hường – (1990) –  Nhân viên xuất nhập khẩu','','/a3/1e/ea/5a696d4d700bd.jpg',7,1,'2018-01-25 12:38:21','2019-06-02 19:12:40','<p>Cũng l&agrave; một trong số những bạn đi học để thay đổi c&ocirc;ng việc. Một trong những học vi&ecirc;n xuất sắc, t&igrave;nh cảm v&agrave; kh&aacute; k&iacute;n tiếng. Bạn ấy c&oacute; mức lương khởi điểm ngang bằng với nhiều bạn c&oacute; kinh nghiệm 2-3 năm, nhưng y&ecirc;u cầu c&aacute;c chị kh&ocirc;ng được tiết lộ h&igrave;nh ảnh. Ch&uacute;c mừng em Hường nh&eacute;! Em cũng l&agrave; một niềm tự h&agrave;o lớn cho c&aacute;c chị v&agrave; l&agrave; điển h&igrave;nh cho c&aacute;c bạn chuyển việc c&ograve;n đang cảm thấy thiếu tự tin c&oacute; thể học tập em đấy!</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/huong%20tran%202.jpg\" style=\"height:495px; width:496px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/huong%20tran%202.jpg\" style=\"height:495px; width:496px\" /></p>'),(52,'Trần Thị Lan Phương - ĐH ngoại ngữ – ĐH Quốc gia Hà Nội','','/87/07/78/5a696e4a5426d.jpg',6,1,'2018-01-25 12:42:34','2019-06-02 19:12:58','<p>Tốt nghiệp kh&oacute;a học ngay khi c&ograve;n l&agrave; sinh vi&ecirc;n năm thứ 3. C&ugrave;ng với nền tảng sinh vi&ecirc;n ngoại ngữ, ngo&agrave;i tiếng Anh, Phương c&ograve;n c&oacute; Tiếng Trung &ndash; được gọi l&agrave; &ldquo;language of business&rdquo;, em đ&atilde; vượt qua rất nhiều ứng vi&ecirc;n kinh nghiệm kh&aacute;c để c&oacute; được vị tr&iacute; nh&acirc;n vi&ecirc;n mua h&agrave;ng (purchasing) tại một c&ocirc;ng ty nhập khẩu với đối t&aacute;c ch&iacute;nh đến từ Trung Quốc.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/lan%20phuong.jpg\" style=\"height:725px; width:940px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/lan%20phuong.jpg\" /></p>'),(53,'Trần Thu Thủy (1993)- Đại học Khoa học xã hội và nhân văn','','/65/25/56/5a69705306b6d.jpg',6,1,'2018-01-25 12:51:15','2019-06-02 19:10:11','<p>Bạn Thủy cũng l&agrave; d&acirc;n ngoại đạo, học trường về Nh&acirc;n văn kh&ocirc;ng hề li&ecirc;n quan nhưng sau buổi học đầu ti&ecirc;n bạn đ&atilde; thật sự &ldquo;m&ecirc;&quot;&nbsp;gi&aacute;o vi&ecirc;n v&agrave; c&aacute;ch dạy của c&aacute;c c&ocirc; gi&aacute;o. Bạn rất thấm những g&igrave; được đ&agrave;o tạo cả kiến thức v&agrave; kỹ năng, những c&acirc;u chuyện trong nghề v&agrave; giờ bạn đ&atilde; thực hiện được ước mơ sang lĩnh vực mới.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/thu%20thuy%202.jpg\" style=\"height:345px; width:776px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/thu%20thuy%201.jpg\" style=\"height:379px; width:494px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thu%20thuy%201.jpg\" style=\"height:379px; width:494px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thu%20thuy%202.jpg\" style=\"height:345px; width:776px\" /></p>'),(54,'Vũ Lan Phương (1996)- Học viện ngân hàng','','/9b/5b/b9/5a6971a44527c.jpg',4,1,'2018-01-25 12:56:52','2019-06-02 19:10:24','<p>Bạn Lan Phương đến với kh&oacute;a học khi đang học năm 3 tại HVNH, với sinh vi&ecirc;n năm 3 th&igrave; mong muốn của c&aacute;c bạn đ&oacute; l&agrave; được đi thực tập/học việc sớm v&agrave; bạn Lan Phương đ&atilde; l&agrave;m được điều đ&oacute;. Việc được nhận thực tập tại C&ocirc;ng ty Xuất nhập khẩu kh&ocirc;ng phải dễ đ&acirc;u c&aacute;c bạn nh&eacute;. Ch&uacute;ng ta h&atilde;y c&ugrave;ng chờ xem những bước ph&aacute;t triển của bạn ấy sau một thời gian nữa nh&eacute;<img alt=\"\" src=\"/uploads/ckfinder/images/lan%20phuong(1).jpg\" style=\"height:960px; width:697px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/lan%20phuong(1).jpg\" /></p>'),(55,'Vũ Thị Huế (1995) – Học viện chính sách phát triển','','/58/70/05/5a6973372f183.jpg',8,1,'2018-01-25 13:03:35','2019-06-02 19:17:43','<p>L&agrave; một c&ocirc; g&aacute;i t&igrave;nh cảm, nhẹ nh&agrave;ng c&oacute; thi&ecirc;n hướng nghệ thuật (bạn &yacute; vẽ đẹp lắm ^^) nhưng lại th&iacute;ch một c&ocirc;ng việc nhiều thử th&aacute;ch. C&ocirc; b&eacute; đ&atilde; chọn CLB Y&ecirc;u xuất nhập khẩu H&agrave; Nội để khởi đầu cho con đường sự nghiệp của m&igrave;nh&nbsp;v&agrave; ngay sau khi ho&agrave;n th&agrave;nh kh&oacute;a học, bạn ấy đ&atilde; được nhận đi l&agrave;m ngay với mức lương rất xứng đ&aacute;ng.<img alt=\"\" src=\"/uploads/ckfinder/images/hue%20vu.jpg\" style=\"height:345px; width:776px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/hue%20vu.jpg\" style=\"height:345px; width:776px\" /></p>'),(57,'Kiều Mỹ Linh (1994) – ĐH Thương mại','','/71/b7/77/5a697c9eed0db.jpg',2,1,'2018-01-25 13:43:42','2019-06-02 19:10:49','<p>Em c&oacute; nhiều s&oacute;ng gi&oacute; trong cuộc sống. Với tham vọng của em l&agrave; trở th&agrave;nh chuy&ecirc;n gia trong ng&agrave;nh XNK, em đ&atilde; cố gắng rất nhiều để đạt được mục ti&ecirc;u đ&oacute;. Hiện nay, em đang l&agrave; chuy&ecirc;n vi&ecirc;n l&agrave;m chứng từ-thủ tục hải quan cho một c&ocirc;ng ty logistics, chuy&ecirc;n s&acirc;u mảng dịch vụ giấy ph&eacute;p-kiểm tra chất lượng h&agrave;ng xuất khẩu &ndash; nhập khẩu.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/my%20linh%202.jpg\" style=\"height:788px; width:940px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/my%20linh%202.jpg\" style=\"height:345px; width:540px\" /></p>'),(58,'Đoàn Thị Thu Trang (1996) - Học viện ngân hàng','','/b9/3a/ab/5a9a070590d3e.jpg',4,1,'2018-03-03 09:23:01','2019-06-02 19:10:58','<p>Trang c&oacute; xuất ph&aacute;t điểm l&agrave; sinh vi&ecirc;n Ng&acirc;n h&agrave;ng, được bạn b&egrave; giới thiệu biết đến HAN EXIM CLUB, rất may mắn v&igrave; sau&nbsp;buổi học đầu ti&ecirc;n được sự tư vấn về c&aacute;ch trả lời phỏng vấn&nbsp;bạn đ&atilde; tr&uacute;ng tuyển vị tr&iacute; thực tập sinh tại&nbsp; C&ocirc;ng ty Xuất Nhập Khẩu. Vui mừng hơn nữa, sau khi kết th&uacute;c kh&oacute;a học bạn ấy b&aacute;o về với c&aacute;c c&ocirc; gi&aacute;o l&agrave; C&ocirc;ng ty bạn ấy thực tập quyết định đợi bạn ấy ra trường sẽ nhận l&agrave;m ch&iacute;nh thức. Sự cố gắng, nỗ lực của em đ&atilde; được đền đ&aacute;p xứng đ&aacute;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/TRANG%20DOAN%201.jpg\" style=\"height:825px; width:550px\" /></p>'),(59,'Lê Thuỳ Linh (1996) - Đại học Kinh tế quốc dân','','/c1/59/9c/5a9a07684296e.jpg',3,1,'2018-03-03 09:24:40','2019-06-02 19:11:08','<p>Bạn L&ecirc; Th&ugrave;y Linh được c&ocirc; gi&aacute;o ph&acirc;n c&ocirc;ng l&agrave;m lớp trưởng tr&ecirc;n lớp Xuất nhập khẩu ngay từ buổi đầu ti&ecirc;n bởi sự nhiệt t&igrave;nh v&agrave; hăng h&aacute;i, t&iacute;nh cẩn thận của bạn ấy. Linh t&igrave;m đến kh&oacute;a học do được người chị&nbsp;c&ugrave;ng ph&ograve;ng đ&atilde; theo học tại HAN EXIM CLUB v&agrave; sau khi học đ&atilde; xuất sắc tr&uacute;ng tuyển vị tr&iacute; Nh&acirc;n vi&ecirc;n Xuất nhập khẩu của HONDA Vệt Nam.&nbsp;(Bạn Nguyễn Thị Ngợi c&oacute; ở phần Cảm nhận học vi&ecirc;n).</p>\r\n\r\n<p>Kh&ocirc;ng thua k&eacute;m bạn b&egrave;, sau khi học xong kh&oacute;a Nghiệp vụ Xuất nhập khẩu- Logistics tại HAN EXIM CLUB mới đầu năm 4 nhưng đ&atilde; tr&uacute;ng tuyển thực tập. C&ugrave;ng xem những cảm nhận của bạn ấy để biết kh&oacute;a học đ&atilde; gi&uacute;p bạn ấy thế n&agrave;o khi&nbsp;&quot;lần đầu&quot; l&agrave;m việc thực tế.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/LE%20THUY%20LINH%201.jpg\" style=\"height:825px; width:550px\" /></p>'),(60,'Bùi Thị My (1996) - Đại học Thương mại','','/7b/8e/e7/5ab9280533d30.jpg',2,1,'2018-03-27 00:04:05','2019-06-02 19:11:16','<p>Vậy l&agrave; c&ocirc; b&eacute; của ch&uacute;ng ta đ&atilde; ch&iacute;nh thức trở th&agrave;nh Purchasing Staff của Canon Việt Nam rồi. Ngay từ những buổi đầu kh&oacute;a học t&ocirc;i c&ograve;n nhớ đ&ocirc;i mắt long lanh, đầy x&uacute;c động của em khi được học những thứ &ldquo;lần đầu ti&ecirc;n&rdquo; được biết v&agrave; mỗi buổi học tr&ocirc;i qua em trưởng th&agrave;nh hơn từng ng&agrave;y. T&ocirc;i rất vui mừng khi đ&atilde; chắp c&aacute;nh ước mơ cho rất nhiều bạn trở th&agrave;nh &nbsp;Purchasing, vị tr&iacute; đ&ograve;i hỏi nhiều kiến thức, kỹ năng v&agrave; lu&ocirc;n cần học hỏi kh&ocirc;ng ngừng. &nbsp;C&aacute;c bạn trong lớp c&ugrave;ng ch&uacute;c mừng cho My nh&eacute;. &nbsp;Em lại l&agrave; động lực cho c&aacute;c bạn học vi&ecirc;n kh&aacute;c cố gắng, minh chứng cho những g&igrave; ch&uacute;ng t&ocirc;i đ&atilde; v&agrave; đang l&agrave;m cho c&aacute;c học vi&ecirc;n của m&igrave;nh.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/Bui%20Thi%20My%20Hun2.jpg\" style=\"height:310px; width:550px\" /><br />\r\n<img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/Bui%20Thi%20My%20Hun1.jpg\" style=\"height:309px; width:550px\" /></p>'),(61,'Trần Văn Minh (1996) - Đại học Ngoại thương','','/97/55/59/5ac8e1a67a4a9.jpg',1,1,'2018-04-07 22:20:06','2019-06-02 19:11:21','<p>Ch&agrave;ng trai n&agrave;y nổi bật kh&ocirc;ng phải v&igrave; l&agrave; &ldquo;nam vương trường Ngoại thương&rdquo; m&agrave; v&igrave; th&aacute;i độ ứng xử v&agrave; c&aacute;ch đối nh&acirc;n xử thế. Những điều n&agrave;y thực sự chẳng trường lớp n&agrave;o c&oacute; thể dạy nổi c&aacute;c bạn. Bạn ấy c&oacute; sự nh&atilde; nhặn, lịch thiệp, trưởng th&agrave;nh như một qu&yacute; &ocirc;ng xen lẫn nhiệt huyết v&agrave; ch&acirc;n th&agrave;nh của một thanh ni&ecirc;n trẻ tuổi, đ&oacute; l&agrave; những yếu tố bổ trợ gi&uacute;p bạn ấy lu&ocirc;n gặp nhiều thuận lợi trong c&ocirc;ng việc, cuộc sống của m&igrave;nh. Trong lớp, bạn cũng l&agrave; người rất chu đ&aacute;o v&agrave; tử tế, th&acirc;n thiện v&agrave; được bạn b&egrave;, c&ocirc; gi&aacute;o y&ecirc;u qu&yacute;. Khởi sự một c&ocirc;ng việc mới nhiều thử th&aacute;ch, chị lu&ocirc;n tin em c&oacute; thể l&agrave;m tốt v&agrave; tốt hơn nữa!&nbsp;<br />\r\nMinh &agrave;, em l&agrave; một trong những học sinh xuất sắc kh&ocirc;ng phải ri&ecirc;ng trong lớp m&agrave; lọt &ldquo;TOP HAN EXIM&rdquo; rồi đấy nh&eacute;, những chia sẻ n&agrave;y của em ch&iacute;nh l&agrave; nguồn cảm hứng cho c&aacute;c bạn kh&aacute;c tiếp tục &ldquo;y&ecirc;u những g&igrave; m&igrave;nh l&agrave;m v&agrave; l&agrave;m những g&igrave; m&igrave;nh y&ecirc;u&rdquo;. C&aacute;c chị tự h&agrave;o về em!&nbsp;<br />\r\n<img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/Tr%E1%BA%A7n%20V%C4%83n%20Minh.jpg\" style=\"height:285px; width:600px\" /></p>'),(62,'1','1','/86/6d/d8/5baa3f2e6fdb2.png',0,0,'2018-09-25 13:59:10','2018-09-25 13:59:10','<p>1</p>'),(64,'Bùi Cẩm Ngọc (1996) - Đại học Ngoại thương','','/5d/67/75/5cdc2e5aa3801.jpg',100,1,'2019-05-15 22:20:58','2019-06-04 16:04:01','<p>C&ocirc; g&aacute;i nhỏ nhắn năm t&igrave;m đến lớp học Xuất nhập khẩu qua sự giới thiệu của người chị họ- cũng l&agrave; một học vi&ecirc;n xuất sắc của HAN EXIM CLUB. Ng&agrave;y ấy em c&ograve;n kh&aacute; nh&uacute;t nh&aacute;t, học xong m&agrave; em tr&uacute;ng tuyển qu&aacute; nhanh khiến bản th&acirc;n em cũng bất ngờ. Ch&iacute;nh em cũng thừa nhận m&igrave;nh đ&atilde; trở th&agrave;nh một con người kh&aacute;c sau kh&oacute;a học nghiệp vụ n&agrave;y. Giờ đ&acirc;y em đ&atilde; l&agrave; c&ocirc; nh&acirc;n vi&ecirc;n sales Xuất khẩu, c&oacute; trong tay mạng lưới kh&aacute;ch h&agrave;ng ch&acirc;u &Aacute;, Trung Đ&ocirc;ng, ch&acirc;u Phi... v&agrave; va chạm với c&aacute;c mặt h&agrave;ng kh&aacute;c nhau. Ch&uacute;c em vững bước tr&ecirc;n con đường m&igrave;nh đ&atilde; chọn!</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/BUI%20CAM%20NGOC%201.jpg\" style=\"height:313px; width:680px\" /></p>'),(65,'Bùi Thị Nụ (1996) - Đại học Thương mại','','/75/ca/a7/5cdc2f8f30d68.jpg',2,1,'2019-05-15 22:26:07','2019-06-04 16:09:24','<p>C&ocirc; g&aacute;i n&agrave;y đến với ch&uacute;ng t&ocirc;i từ một chuy&ecirc;n ng&agrave;nh chẳng li&ecirc;n quan g&igrave; đến Xuất nhập khẩu của Đại học Thương Mại. Khi ấy em cũng đ&atilde; c&oacute; một c&ocirc;ng việc b&aacute;n thời gian ở TOPICA, kh&ocirc;ng bằng l&ograve;ng với ch&iacute;nh m&igrave;nh em đ&atilde; t&igrave;m đến ch&uacute;ng t&ocirc;i sau qu&aacute; tr&igrave;nh t&igrave;m hiểu rất kỹ lưỡng từ bạn b&egrave;. Đến buổi học thứ 5 em đ&atilde; đi ứng tuyển v&agrave;o một c&ocirc;ng ty logistics qua lời giới thiệu của gi&aacute;o vi&ecirc;n. Vị tr&iacute; em ứng tuyển l&agrave; sales logistics- c&ocirc;ng việc đ&ograve;i hỏi sự ki&ecirc;n nhẫn, nghiệp vụ v&agrave; kiến thức tốt để tư vấn cho c&aacute;c c&ocirc;ng ty Xuất nhập khẩu. Đ&acirc;y l&agrave; vị tr&iacute; thường bị c&aacute;c bạn &quot;bỏ qua&quot; v&igrave; &quot;sợ h&atilde;i&quot; &aacute;p lực kh&aacute;ch h&agrave;ng. Vậy m&agrave; sau 3 th&aacute;ng thử việc em đ&atilde; được nhận lu&ocirc;n l&ecirc;n ch&iacute;nh thức bởi th&agrave;nh t&iacute;ch xuất sắc của m&igrave;nh- sales được 10 container h&agrave;ng đường biển. Bạn n&agrave;o c&ograve;n sợ vị tr&iacute; n&agrave;y th&igrave; h&atilde;y gặp Nụ nh&eacute;- chắc chắn bạn ấy sẽ chia sẻ nhiều b&iacute; quyết cho c&aacute;c bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/BUI%20THI%20NU%201.jpg\" style=\"height:381px; width:680px\" /></p>'),(66,'Đinh Diễm Hương (1997) - Đại học Ngoại thương','','/fd/60/0f/5cdc31c7d0ffb.jpg',1,1,'2019-05-15 22:35:35','2019-06-04 16:12:59','<p>C&ocirc; g&aacute;i tiếp theo của HAN EXIM CLUB đến từ ĐH Ngoại Thương, học nghiệp vụ mới từ năm thứ 3 đại học. Ngay từ l&uacute;c học em đ&atilde; rất hăng h&aacute;i v&agrave; t&iacute;ch cực, gần như kh&ocirc;ng một buổi n&agrave;o em nghỉ tr&ecirc;n lớp. Đi phỏng vấn ngay sau kh&oacute;a học v&agrave; tr&uacute;ng tuyển v&agrave;o vị tr&iacute; rất &quot;pro&quot; l&agrave;m fulltime v&agrave; được nhận ch&iacute;nh thức.&nbsp;Em c&oacute; t&acirc;m sự về sự tự tin của m&igrave;nh nhờ những buổi học, những kiến thức, nghiệp vụ tr&ecirc;n lớp. Nếu bạn n&agrave;o năm 3 học nghiệp vụ th&igrave; rất c&oacute; thể sẽ đạt được th&agrave;nh t&iacute;ch sớm như bạn ấy!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/DINH%20DIEM%20HUONG%201.jpg\" style=\"height:429px; width:680px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/DINH%20DIEM%20HUONG%202.jpg\" style=\"height:396px; width:680px\" /></p>'),(67,'Nguyễn Thị Xuyến (1997) - Học viện Tài chính','','/55/c1/15/5cdc324543325.jpg',5,1,'2019-05-15 22:37:41','2019-06-04 16:18:58','<p>T&ocirc;i vẫn nhớ như in h&igrave;nh ảnh của một c&ocirc; b&eacute; gầy g&ograve;, khu&ocirc;n mặt dễ mến ở lớp. Em c&oacute; t&acirc;m sự rằng em rất th&iacute;ch l&agrave;m Xuất nhập khẩu nhưng sợ sức khỏe kh&ocirc;ng cho ph&eacute;p. Vậy l&agrave; kh&ocirc;ng chỉ đ&agrave;o tạo nghiệp vụ, ch&uacute;ng t&ocirc;i c&ograve;n động vi&ecirc;n, kh&iacute;ch lệ tinh thần để em c&oacute; thể vượt qua nỗi sợ h&atilde;i đ&oacute;. Tin vui đ&atilde; đến khi sau kh&oacute;a học em đ&atilde; tr&uacute;ng tuyển đi l&agrave;m chứng từ- c&ocirc;ng việc kh&ocirc;ng đ&ograve;i hỏi đi lại nhiều. V&agrave; quan trọng hơn, vị l&atilde;nh đạo c&ocirc;ng ty đ&atilde; c&oacute; những lời khen ngợi d&agrave;nh cho em, giao cho quản l&yacute; cả đội sales khi c&ograve;n non trẻ.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/HA%20THI%20XUYEN%201.jpg\" style=\"height:413px; width:680px\" /></p>'),(68,'Trương Tuấn Đức (1996) - Đại học Ngoại thương','','/68/68/86/5cdcf27335cca.jpg',1,1,'2019-05-16 12:17:39','2019-06-04 16:22:45','<p>Ch&agrave;ng trai Ngoại Thương đa t&agrave;i, nồng hậu. Sau khi học xong kh&oacute;a học chưa b&aacute;o c&aacute;o ngay m&agrave; phải đợi khi đạt th&agrave;nh t&iacute;ch mới b&aacute;o c&aacute;o, đ&atilde; thế c&ograve;n đề nghị kh&ocirc;ng được tag t&ecirc;n tr&ecirc;n Facebook v&igrave; xấu hổ. Ấy vậy m&agrave; bạn ấy kh&ocirc;ng nh&uacute;t nh&aacute;t đ&acirc;u nh&eacute;, l&agrave;m ở vị tr&iacute; Sales Xuất&nbsp; khẩu hẳn hoi. Những lời cảm ơn từ đ&aacute;y l&ograve;ng của bạn ấy kh&ocirc;ng d&agrave;i d&ograve;ng, kh&ocirc;ng thắm thiết nhưng n&oacute; đ&uacute;ng với một ch&agrave;ng trai như bạn ấy &quot;N&oacute;i &iacute;t- l&agrave;m nhiều&quot;. Ch&uacute;c cho Đức vững bước tr&ecirc;n con đường m&agrave; em đ&atilde; chọn</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/TUAN%20DUC1.jpg\" style=\"height:630px; width:680px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/TUAN%20DUC2.jpg\" style=\"height:540px; width:680px\" /></p>'),(69,'Nguyễn Thị Ly (1992) - Nhân viên lễ tân Nhà hàng','','/a5/58/8a/5cdcf2f5d6429.jpg',7,1,'2019-05-16 12:19:49','2019-06-04 16:26:38','<p>Đến với nghề khi kh&ocirc;ng c&ograve;n &quot;qu&aacute; trẻ&quot; lại đang trong thời gian chăm con mọn dưới 6 th&aacute;ng. Những tưởng người phụ nữ đ&oacute; sẽ đầu b&ugrave; t&oacute;c rối thế nhưng c&ocirc; g&aacute;i n&agrave;y lại kh&aacute;c. T&igrave;m đến lớp học cũng qua lời giới thiệu của người họ h&agrave;ng đ&atilde; học tại HAN EXIM, động lực n&agrave;o cho một c&ocirc; g&aacute;i l&agrave;m du lịch 5 năm lại chuyển qua học nghề kh&aacute;c? Chỉ c&oacute; thể v&igrave; TIN V&Agrave; TIN. Kh&ocirc;ng ngo&agrave;i sự mong đợi đ&oacute;, c&ocirc; ấy đ&atilde; tr&uacute;ng tuyển đi l&agrave;m v&agrave; vượt qua v&ograve;ng phỏng vấn với&nbsp;rất nhiều c&acirc;u hỏi nghiệp vụ. Vậy l&agrave; lời hứa của ch&uacute;ng t&ocirc;i với em đ&atilde; th&agrave;nh sự thật. Th&ecirc;m một lần nữa, HAN EXIM lại gi&uacute;p cho c&aacute;c bạn trẻ l&agrave;m nghề Xuất nhập khẩu từ con số 0.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NGUYEN%20THI%20LY%201.jpg\" style=\"height:308px; width:680px\" /></p>'),(70,'Phú Lê Nhật Linh (1996) - Đại học Ngoại thương','','/1e/39/91/5cdcf356d5452.jpg',1,1,'2019-05-16 12:21:26','2019-06-04 16:29:16','<p>Linh học ở lớp Chủ Nhật với cường độ học t&ugrave; 8h s&aacute;ng đến 17h chiều. Cảm thấy như m&ugrave;a h&egrave; n&oacute;ng c&ograve;n chảy mỡ m&agrave; bạn ấy chưa nghỉ buổi học n&agrave;o. Kh&ocirc;ng ngo&agrave;i sự mong đợi,&nbsp; kh&ocirc;ng những đỗ 1 c&ocirc;ng ty m&agrave; &quot;một v&agrave;i c&ocirc;ng ty&quot; trong lĩnh vực Xuất nhập khẩu- logistics. Bạn ấy đ&atilde; th&ecirc;m v&agrave;o danh s&aacute;ch những ứng vi&ecirc;n KH&Ocirc;NG ĐI XIN VIỆC m&agrave; ĐI CHỌN VIỆC.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/PHU%20LE%20NHAT%20LINH%201.jpg\" style=\"height:294px; width:680px\" /></p>'),(71,'Trần Thùy Linh (1995) - Học viện Tài chính','','/ee/2e/ee/5cdcf3b41ded0.jpg',5,1,'2019-05-16 12:23:00','2019-06-04 16:33:15','<p>Đến từ học viện t&agrave;i ch&iacute;nh- c&aacute;ch xa lớp học đến hơn 10km nhưng bạn ấy kh&ocirc;ng những tham gia lớp học đều đặn&nbsp;m&agrave; c&ograve;n đảm nhận vai tr&ograve; lớp trưởng. Kh&ocirc;ng qu&aacute; nổi trội, kh&ocirc;ng qu&aacute; s&ocirc;i động nhưng lại l&agrave; c&ocirc; g&aacute;i &quot;tẩm ngầm chết voi&quot; khi b&aacute;o tin vui tr&uacute;ng tuyển hản v&agrave;o vị tr&iacute; mua h&agrave;ng- một vị tr&iacute; được gọi l&agrave; &quot;kh&oacute; nhằn&quot; trong lĩnh vực n&agrave;y. Hy vọng với h&agrave;nh trang m&agrave; HAN EXIM đ&atilde; trang bị cho bạn ấy, Linh&nbsp; sẽ vững bước tr&ecirc;n con đường bạn ấy đ&atilde; chọn,</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/TRAN%20THUY%20LINH%201.jpg\" style=\"height:611px; width:680px\" /></p>'),(72,'Nguyễn Minh Thư (1995) - ĐH Kinh tế - ĐH Quốc gia HN','','/2c/2d/d2/5cdcf42fcaf00.jpg',6,1,'2019-05-16 12:25:03','2019-06-04 16:38:12','<p>Feedback h&agrave;ng ng&agrave;y về kh&oacute;a họchay những d&ograve;ng tin nhắn b&aacute;o tr&uacute;ng tuyển kh&ocirc;ng phải l&agrave; &iacute;t nhưng để một học vi&ecirc;n viết l&aacute; thư t&acirc;m sự d&agrave;i như thế n&agrave;y khiến đội ngũ gi&aacute;o vi&ecirc;n tại HAN EXIM v&ocirc; c&ugrave;ng cảm động. Bạn Thư với đ&ocirc;i mắt biết n&oacute;i, sống t&igrave;nh cảm lu&ocirc;n được bạn b&egrave; qu&yacute; mến. Tr&ecirc;n lớp em cũng kh&ocirc;ng hay b&agrave;y tỏ cảm x&uacute;c nhưng đọc những d&ograve;ng thư của em khiến ch&uacute;ng t&ocirc;i cảm nhận được em v&ocirc; c&ugrave;ng y&ecirc;u qu&yacute; lớp học từ gi&aacute;o vi&ecirc;n đến c&aacute;c bạn học c&ugrave;ng. V&agrave; cũng chẳng phải đợi tr&uacute;ng tuyển, em kh&ocirc;ng k&igrave;m n&eacute;n được sự x&uacute;c động sau buổi bế giảng v&agrave; viết thư cảm ơn đến gi&aacute;o vi&ecirc;n. Ch&uacute;ng t&ocirc;i cũng cảm ơn em v&igrave; đ&atilde; lu&ocirc;n nhớ đến ch&uacute;ng t&ocirc;i. Tin rằng một người như em sẽ lu&ocirc;n được người kh&aacute;c tr&acirc;n trọng như c&aacute;ch em đ&atilde; l&agrave;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NGUYEN%20MINH%20THU%201.jpg\" style=\"height:337px; width:680px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NGUYEN%20MINH%20THU%202.jpg\" style=\"height:287px; width:680px\" /></p>'),(73,'Nguyễn Minh Ngọc (1997) - ĐH Kinh tế quốc dân','','/09/1f/f0/5cdcf62b279d1.jpg',500,1,'2019-05-16 12:33:31','2019-06-06 14:21:29','<p>C&ocirc; g&aacute;i học Xuất nhập khẩu gần đ&acirc;y nhất 2019. Khi đi học tại lớp Xuất nhập khẩu khiến c&ocirc; gi&aacute;o tưởng em học đ&uacute;ng chuy&ecirc;n ng&agrave;nh bởi sự tự tin, kiến thức vững v&agrave;ng của em. Đến cuối mới biết em học Kinh tế Vĩ M&ocirc; chứ kh&ocirc;ng li&ecirc;n quan đến Xuất nhập khẩu. V&agrave; c&aacute;i g&igrave; đến cũng đến, sự chăm chỉ của em đ&atilde; được đền đ&aacute;p. Khi chưa tốt nghiệp em đ&atilde; tr&uacute;ng tuyển vị tr&iacute; mua h&agrave;ng. Đi phỏng vấn nơi n&agrave;o tr&uacute;ng nơi ấy, một lần nữa học vi&ecirc;n HAN EXIM CLUB được ở thế &quot;CHỌN VIỆC&quot;.&nbsp;&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/MINH%20NGOC%201.jpg\" style=\"height:315px; width:680px\" /></p>'),(74,'Nguyễn Thị Hà Phương (1996) - Học viện chính sách-phát triển','','/38/b9/93/5cdcf74de926f.jpg',8,1,'2019-05-16 12:38:21','2019-06-04 16:44:30','<p>Nhớ đến c&ocirc; g&aacute;i n&agrave;y l&agrave; những lần đi học bị trễ giờ do h&ocirc;m trước em l&agrave;m th&ecirc;m về muộn. Nếu ở những nơi kh&aacute;c chắc em đ&atilde; bị gi&aacute;o vi&ecirc;n cho v&agrave;o Black List nhưng ri&ecirc;ng HAN EXIM th&igrave; kh&aacute;c. Ch&uacute;ng t&ocirc;i lu&ocirc;n b&ecirc;n cạnh gi&uacute;p đỡ, s&aacute;t c&aacute;nh, thấu hiểu học vi&ecirc;n. Em kh&ocirc;ng bị phạt m&agrave; c&ograve;n được tạo điều kiện học b&ugrave; với c&aacute;c lớp kh&aacute;c. Tuy c&oacute; lo lắng em kh&ocirc;ng bắt nhịp được nhưng t&ocirc;i thật sự bị vỡ &ograve;a khi nhận được tin b&aacute;o tr&uacute;ng tuyển của em khi em c&ograve;n chưa nhờ đến sự gi&uacute;p đỡ của m&igrave;nh khi đi phỏng vấn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NGUYEN%20THI%20HA%20PHUONG%201.jpg\" style=\"height:584px; width:680px\" /></p>'),(75,'Nguyễn Thu Giang (1997) - Học viện Ngân Hàng','','/47/6e/e4/5cdcfa6505114.jpg',4,1,'2019-05-16 12:51:33','2019-06-04 17:12:18','<p>C&ocirc; bạn đến từ Học viện Ng&acirc;n h&agrave;ng, tuy c&ograve;n khi&ecirc;m tốn, &iacute;t n&oacute;i nhưng rất t&igrave;nh cảm. Giữa bao nhi&ecirc;u c&ocirc;ng ty xa gần em cũng t&igrave;m được c&ocirc;ng ty kh&ocirc;ng qu&aacute; xa so với ti&ecirc;u chuẩn. Đấy, học vi&ecirc;n của t&ocirc;i cũng hơi &quot;chảnh&quot; nhưng quan trọng l&agrave; &quot;chất&quot; đ&uacute;ng kh&ocirc;ng c&aacute;c bạn.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NGUYEN%20THU%20GIANG%201.jpg\" style=\"height:928px; width:540px\" /></p>'),(76,'Nguyễn Thị Lan Anh (1997) - Học viện Ngân hàng','','/51/db/b5/5cdcfbda24b5e.jpg',4,1,'2019-05-16 12:57:46','2019-06-04 17:16:08','<p>Bạn Lan Anh sau khi tốt nghiệp kh&oacute;a học tại HAN EXIM đ&atilde; khẩn trương nộp CV v&agrave; li&ecirc;n hệ hỏi kinh nghiệm phỏng vấn từ c&ocirc; gi&aacute;o. C&ograve;n g&igrave; tuyệt vời hơn khi đi phỏng vấn m&agrave; được tr&uacute;ng tủ với trả lời hết c&aacute;c c&acirc;u hỏi. Thực ra ch&uacute;ng t&ocirc;i đều support tất cả c&aacute;c học vi&ecirc;n bằng những b&iacute; k&iacute;p ri&ecirc;ng nhưng mỗi học vi&ecirc;n lại c&oacute; c&aacute;ch &aacute;p dụng kh&aacute;c khau. Nếu cẩn thận bạn đừng qu&ecirc;n hỏi thăm c&ocirc; gi&aacute;o trước khi đi phỏng vấn như bạn Lan Anh để đạt được kết quả tốt nhất nh&eacute;. Vị tr&iacute; n&agrave;y rất ph&ugrave; hợp với em v&agrave; chị tin chắc rằng em sẽ l&agrave;m tốt. Ch&uacute;c em vững bước tr&ecirc;n con đường m&igrave;nh đ&atilde; chọn!</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/LAN%20ANH1.jpg\" style=\"height:293px; width:680px\" /></p>'),(77,'Hoàng Thị Nguyệt Hà (1996) - Học viện Ngân hàng','','/ab/b6/6a/5cdcfcf59ba09.jpg',4,1,'2019-05-16 13:02:29','2019-06-04 17:18:52','<p>Biết tin H&agrave; tr&uacute;ng tuyển từ trước khi em b&aacute;o c&aacute;o do rất t&igrave;nh cờ l&agrave; Gi&aacute;m đốc C&ocirc;ng ty nơi em l&agrave;m lại contact từ trước với c&ocirc; gi&aacute;o. Với feedback n&agrave;y hiếm hoi c&aacute;c bạn được đọc &quot;k&eacute;&quot; phản hồi về buổi phỏng vấn từ ch&iacute;nh Gi&aacute;m đốc doanh nghiệp. Hy vọng c&acirc;u chuyện của bạn H&agrave; v&agrave; vị Gi&aacute;m đốc sẽ khiến bạn suy nghĩ t&iacute;ch cực v&agrave; lu&ocirc;n c&oacute; th&aacute;i độ tốt trong cuộc sống. Ch&uacute;c mừng H&agrave;- c&ocirc; g&aacute;i cừ kh&ocirc;i lu&ocirc;n hăng&nbsp; h&aacute;i ph&aacute;t biểu trong lớp, đ&oacute; l&agrave; h&igrave;nh ảnh t&ocirc;i nhớ về em!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/HOANG%20THI%20HA%201.jpg\" style=\"height:406px; width:680px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/HOANG%20THI%20HA%202.jpg\" style=\"height:397px; width:680px\" /></p>'),(78,'Nguyễn Quỳnh Trang (1997) - Đại học Ngoại thương','','/aa/f6/6a/5cdcfecfad103.jpg',1,1,'2019-05-16 13:10:23','2019-06-04 17:20:53','<p>C&ocirc;ng việc của đội ngũ gi&aacute;o vi&ecirc;n HAN EXIM rất bận rộn v&igrave; ngo&agrave;i giờ dạy buổi tối th&igrave; ban ng&agrave;y họ l&agrave; những c&aacute;n bộ chuy&ecirc;n vi&ecirc;n phụ tr&aacute;ch Xuất nhập khẩu- logistics. Thế nhưng những tin nhắn ngắn ngủi chỉ cần b&aacute;o &quot;em đ&atilde; tr&uacute;ng tuyển&quot; cũng l&agrave;m một ng&agrave;y d&agrave;i tr&ocirc;i qua nhẹ nh&agrave;ng hơn, hạnh ph&uacute;c hơn. Vậy n&ecirc;n, c&aacute;c bạn muốn c&ocirc; gi&aacute;o hạnh ph&uacute;c th&igrave; nhớ đừng tiếc đ&ocirc;i ph&uacute;t b&aacute;o tin vui nha.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/TRANGNGUYEN%201.jpg\" style=\"height:932px; width:540px\" /></p>'),(79,'Nguyễn Thị Kim Ngân (1997) - ĐH Luật Hà Nội','','/87/ff/f8/5cf3c8f978183.jpg',6,1,'2019-06-02 20:02:49','2019-06-04 17:24:28','<p>Bức thư d&agrave;i, đầy cảm x&uacute;c v&agrave; t&acirc;m trạng của c&ocirc; g&aacute;i n&agrave;y được gửi ngay sau khi biết tin tr&uacute;ng tuyển v&agrave;o vị tr&iacute; chứng từ ở C&ocirc;ng ty Xuất khẩu. Tại sao bạn ấy lại vui thế, lại mừng thế...? C&acirc;u chuyện của bạn ấy t&ocirc;i kh&ocirc;ng qu&ecirc;n khi từ buổi học đầu ti&ecirc;n bạn ấy n&oacute;i đến từ ĐH Luật- ng&ocirc;i trường m&agrave; HAN EXIM chưa từng c&oacute; học vi&ecirc;n bao giờ. G&aacute;c lại những lo &acirc;u, băn khoăn của bạn ấy- ch&uacute;ng t&ocirc;i đồng h&agrave;nh v&agrave; tiếp th&ecirc;m động lực cho bạn Ng&acirc;n sau từng buổi học để bạn ấy tự tin hơn, y&ecirc;u nghề hơn. Sau khi học xong, bạn ấy rất chủ động từ việc hỏi han c&ocirc; gi&aacute;o về kinh nghiệm phỏng vấn hay l&agrave;m CV cẩn thận theo hướng dẫn của gi&aacute;o vi&ecirc;n. Quả ngọt đ&atilde; đến! Vậy l&agrave; t&ocirc;i c&oacute; thể tự h&agrave;o khi giờ đ&acirc;y HAN EXIM đ&atilde; chinh phục được cả nghề Luật đi l&agrave;m Xuất nhập khẩu từ con số 0.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NGUYEN%20THI%20KIM%20NGAN%201(1).JPG\" style=\"height:305px; width:680px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/NGUYEN%20THI%20KIM%20NGAN%202.jpg\" style=\"height:181px; width:680px\" /></p>'),(80,'Phạm Thị Bích (1997) - Học viện Tài chính','','/0a/82/20/5cf3c93eac21d.jpg',6,1,'2019-06-02 20:03:58','2019-06-04 17:27:35','<p>Đến với kh&oacute;a học khi đang đi thực tập ở c&ocirc;ng ty Xuất khẩu v&igrave; bạn ấy muốn tự tin hơn, review lại kiến thức v&agrave; được biết đến đa vị tr&iacute; hơn so với chỉ l&agrave;m ở một c&ocirc;ng ty. D&ugrave; kh&ocirc;ng phải thực sự&nbsp; xuất sắc ở điều g&igrave; nhưng việc t&igrave;m đ&uacute;ng người định hướng, đ&uacute;ng nơi học tập đ&atilde; mang lại th&agrave;nh t&iacute;ch đ&aacute;ng ngờ cho bạn ấy. Ch&uacute;c B&iacute;ch sẽ lu&ocirc;n ho&agrave;n th&agrave;nh c&ocirc;ng việc v&agrave; lu&ocirc;n nhớ đến những g&igrave; ch&uacute;ng t&ocirc;i đ&atilde; dặn d&ograve; để &quot;chinh phục sếp&quot;. :)</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/PHAM%20THI%20BICH%201.jpg\" style=\"height:292px; width:680px\" /></p>'),(81,'Trần Thị Ngọc Ánh (1997) - Học viện Ngân hàng','','/dd/b2/2d/5cf3c98daeb75.jpg',4,1,'2019-06-02 20:05:17','2019-06-04 17:30:04','<p>Lại một lần nữa, những c&ocirc;ng ty c&oacute; t&ecirc;n tuổi trong lĩnh vực Logistics gọi t&ecirc;n học vi&ecirc;n của HAN EXIM v&agrave;o l&agrave;m việc. Quan trọng hơn l&agrave; việc bạn ấy đ&atilde; tự tin hơn rất nhiều sau kh&oacute;a học tại HAN EXIM mặc d&ugrave; đ&atilde; học đ&uacute;ng chuy&ecirc;n ng&agrave;nh. H&ocirc;m tổng kết kh&oacute;a học t&ocirc;i vẫn nhớ đ&ocirc;i mắt em c&ograve;n rưng rưng. Y&ecirc;u lắm, nhớ lắm nhưng đừng lo v&igrave; ch&uacute;ng t&ocirc;i lu&ocirc;n đồng h&agrave;nh c&ugrave;ng c&aacute;c bạn. H&atilde;y đọc hết feedback để thấu hiểu những g&igrave; bạn ấy cảm nhận tại HAN EXIM nh&eacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/TRAN%20THI%20NGOC%20ANH%201.jpg\" style=\"height:347px; width:680px\" /><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/TRAN%20THI%20NGOC%20ANH%202.jpg\" style=\"height:324px; width:680px\" /></p>');

/*Table structure for table `ad_html` */

DROP TABLE IF EXISTS `ad_html`;

CREATE TABLE `ad_html` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT 'Tên bài viết',
  `image_path` varchar(255) DEFAULT NULL COMMENT 'Đường dẫn ảnh đại diện',
  `content` longtext COMMENT 'Nội dung bài viết',
  `is_active` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Trạng thái duyệt bài viết',
  `lang` varchar(10) NOT NULL COMMENT 'Đa ngôn ngữ',
  `slug` varchar(255) NOT NULL COMMENT 'chuyển đổi url',
  `prefix_path` varchar(255) NOT NULL COMMENT 'Đường dẫn trang hiển thị vd: /gioi-thieu/:slug',
  `created_by` bigint(20) DEFAULT NULL,
  `updated_by` bigint(20) DEFAULT NULL,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `slug` (`slug`),
  KEY `created_by_idx` (`created_by`),
  KEY `updated_by_idx` (`updated_by`)
) ENGINE=MyISAM AUTO_INCREMENT=59 DEFAULT CHARSET=utf8;

/*Data for the table `ad_html` */

insert  into `ad_html`(`id`,`name`,`image_path`,`content`,`is_active`,`lang`,`slug`,`prefix_path`,`created_by`,`updated_by`,`created_at`,`updated_at`) values (55,'KHÓA XNK VÀ LOGISTICS TOÀN DIỆN',NULL,'<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:950px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<div style=\"margin-left: 40px; text-align: justify;\"><span style=\"font-size:20px\"><span style=\"color:#006400\">Bạn đang tự hỏi:</span></span></div>\r\n\r\n			<ol>\r\n				<li style=\"text-align:justify\">\r\n				<p style=\"margin-left:40px\"><span style=\"color:#006400\"><span style=\"font-size:16px\">L&agrave;m thế n&agrave;o để tự tin gia nhập v&agrave;o ng&agrave;nh xuất nhập khẩu &ndash; ngoại thương với doanh nghiệp trẻ v&agrave; đội ngũ nh&acirc;n sự trẻ, chưa c&oacute; kinh nghiệm?</span></span></p>\r\n				</li>\r\n				<li style=\"text-align:justify\">\r\n				<p style=\"margin-left:40px\"><span style=\"color:#006400\"><span style=\"font-size:16px\">L&agrave;m thế n&agrave;o để tự tin ứng tuyển v&agrave;o c&aacute;c c&ocirc;ng ty xuất nhập khẩu, chinh phục v&agrave; l&agrave;m &ldquo;say m&ecirc;&rdquo; bất cứ nh&agrave; tuyển dụng n&agrave;o?</span></span></p>\r\n				</li>\r\n				<li style=\"text-align:justify\">\r\n				<p style=\"margin-left:40px\"><span style=\"color:#006400\"><span style=\"font-size:16px\">L&agrave;m thế n&agrave;o để bản lĩnh loại bỏ h&agrave;ng ngh&igrave;n đối thủ c&oacute; c&ugrave;ng tr&igrave;nh độ như m&igrave;nh để gi&agrave;nh được vị tr&iacute; c&ocirc;ng việc mơ ước?</span></span></p>\r\n				</li>\r\n				<li style=\"text-align:justify\">\r\n				<p style=\"margin-left:40px\"><span style=\"color:#006400\"><span style=\"font-size:16px\">L&agrave;m thế n&agrave;o để nắm bắt v&agrave; theo d&otilde;i s&aacute;t sao quy tr&igrave;nh vận chuyển h&agrave;ng h&oacute;a xuất nhập khẩu để giảm thiểu những rủi ro c&oacute; thể lường trước?</span></span></p>\r\n				</li>\r\n				<li style=\"text-align:justify\">\r\n				<p style=\"margin-left:40px\"><span style=\"color:#006400\"><span style=\"font-size:16px\">L&agrave;m thế n&agrave;o để quản l&yacute;, t&iacute;nh to&aacute;n v&agrave; đo lường c&aacute;c chi ph&iacute; ph&aacute;t sinh trong qu&aacute; tr&igrave;nh giao nhận h&agrave;ng h&oacute;a, tiết kiệm chi ph&iacute; tối đa?</span></span></p>\r\n				</li>\r\n				<li style=\"text-align:justify\">\r\n				<p style=\"margin-left:40px\"><span style=\"color:#006400\"><span style=\"font-size:16px\">L&agrave;m thế n&agrave;o để t&igrave;m kiếm kh&aacute;ch h&agrave;ng/đối t&aacute;c mới v&agrave; đ&aacute;nh gi&aacute; đ&uacute;ng năng lực cũng như độ tin cậy của họ tr&ecirc;n thị trường?</span></span></p>\r\n				</li>\r\n			</ol>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">Bạn th&acirc;n mến,</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">Theo thống k&ecirc; của Bộ Lao động - Thương binh v&agrave; X&atilde; hội khoảng 60% sinh vi&ecirc;n ra trường l&agrave;m tr&aacute;i ng&agrave;nh v&agrave; t&iacute;nh đến đầu năm 2017 c&oacute; hơn 200.000 cử nh&acirc;n thất nghiệp. V&igrave; sao 200.000 cử nh&acirc;n kia lại thất nghiệp? V&igrave; sao 60% sinh vi&ecirc;n kia vẫn c&oacute; c&ocirc;ng việc l&agrave;m mặc d&ugrave; tr&aacute;i chuy&ecirc;n ng&agrave;nh?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">Nếu bạn đang hay sắp sửa l&agrave;m một việc tr&aacute;i với chuy&ecirc;n ng&agrave;nh ở trường, bạn sẽ hiểu điều t&ocirc;i sắp n&oacute;i sau đ&acirc;y.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Những g&igrave; c&aacute;c bạn trải qua trong suốt qu&atilde;ng thời gian học đại học l&agrave; ở sự thay đổi trong nhận thức, tư duy v&agrave; th&aacute;i độ của bạn &ndash; đ&oacute; l&agrave; những bước đầu ti&ecirc;n cho bạn v&agrave;o đời</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- C&aacute;i x&atilde; hội thu nhỏ trong cuộc đời của một sinh vi&ecirc;n cũng đủ dạy cho bạn rằng: cuộc sống kh&ocirc;ng phải m&agrave;u hồng như phim H&agrave;n, bạn phải tự lập th&igrave; bạn mới tự chủ, tự do được</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bằng ĐH đối với đa số nh&agrave; tuyển dụng b&acirc;y giờ đơn giản chỉ l&agrave; tờ giấy chứng nhận &ldquo;Bạn được học h&agrave;nh&rdquo;. Với họ, thực dụng v&agrave; phũ ph&agrave;ng hơn bạn nghĩ rất nhiều: Bạn học g&igrave; kh&ocirc;ng quan trọng bằng bạn c&oacute; khả năng l&agrave;m những g&igrave;, bạn cống hiến được g&igrave; cho tổ chức của họ.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">Nếu một trong số những điều sau đ&acirc;y khiến bạn đang trăn trở:</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn c&oacute; kh&aacute;t khao xuất khẩu h&agrave;ng h&oacute;a của Việt Nam ra thị trường thế giới nhưng kh&ocirc;ng biết bắt đầu từ đ&acirc;u?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn muốn tự nhập khẩu h&agrave;ng h&oacute;a từ nước ngo&agrave;i về Việt Nam để kinh doanh nhưng kh&ocirc;ng biết phải l&agrave;m như thế n&agrave;o?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn muốn trở th&agrave;nh nh&acirc;n sự trong nghề xuất nhập khẩu nhưng kh&ocirc;ng hề c&oacute; kiến thức nền tảng chuy&ecirc;n m&ocirc;n, kiến thức + kinh nghiệm = 0?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn muốn trở th&agrave;nh nh&acirc;n sự trong nghề xuất nhập khẩu, bạn đ&atilde; được đ&agrave;o tạo chuy&ecirc;n m&ocirc;n ở trường nhưng kh&ocirc;ng hề c&oacute; kiến thức thực tế?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn đang ở một ng&agrave;nh nghề kh&aacute;c v&agrave; cảm thấy thiếu tự tin khi gia nhập v&agrave;o đội ngũ nh&acirc;n sự của nghề xuất nhập khẩu?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn rất e ngại khi kh&ocirc;ng c&oacute; ngoại ngữ nhưng bạn th&iacute;ch l&agrave;m việc trong nghề xuất nhập khẩu, trong m&ocirc;i trường quốc tế?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn cảm thấy xấu hổ v&agrave; tự ti khi hỏi những c&acirc;u hỏi bị cho l&agrave; ngớ ngẩn, khi dấn th&acirc;n v&agrave;o một c&ocirc;ng việc mới m&agrave; kh&ocirc;ng hề c&oacute; sự chuẩn bị g&igrave;?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">T&ocirc;i ho&agrave;n to&agrave;n hiểu!</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">Đ&oacute; đều l&agrave; những c&acirc;u hỏi ch&uacute;ng t&ocirc;i gặp nhiều nhất khi lắng nghe c&aacute;c bạn chia sẻ. H&atilde;y y&ecirc;n t&acirc;m, đ&acirc;y l&agrave; những g&igrave; ch&uacute;ng t&ocirc;i c&oacute; thể l&agrave;m để gi&uacute;p bạn tr&ecirc;n h&agrave;nh tr&igrave;nh n&agrave;y:</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">Kh&oacute;a huấn luyện nghiệp vụ xuất nhập khẩu thực tế tuyệt vời nhất m&agrave; bạn từng được học trong suốt những năm th&aacute;ng qua!</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\">Trước khi đọc tiếp, h&atilde;y <a href=\"http://tuhocxuatnhapkhau.com/cam-nhan-hoc-vien\">click</a> để xem học vi&ecirc;n n&oacute;i về ch&uacute;ng t&ocirc;i như thế n&agrave;o? Họ l&agrave; ai, c&oacute; xuất ph&aacute;t điểm ra sao v&agrave; hiện giờ họ đ&atilde; c&oacute; th&agrave;nh tựu g&igrave;? <a href=\"http://tuhocxuatnhapkhau.com/cam-nhan-hoc-vien\">Xem ngay</a> nh&eacute;!</span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\">Bạn muốn tham gia lớp học của ch&uacute;ng t&ocirc;i ngay b&acirc;y giờ?</span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:center\"><a href=\"https://docs.google.com/forms/d/1NoHiLv1GUu0pxRr3OjZA-gbOTVzD7LwMtRGlBPY9D6M/viewform?edit_requested=true\" target=\"_blank\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:95px; width:250px\" /></a></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">Bạn c&oacute; thể ra ngo&agrave;i kia tự m&igrave;nh mua v&agrave; đọc h&agrave;ng trăm cuốn s&aacute;ch, nghi&ecirc;n cứu TẤT CẢ những gi&aacute;o tr&igrave;nh s&acirc;u sắc nhất về xuất nhập khẩu &ndash; ngoại thương, d&agrave;nh NHIỀU GIỜ tham gia v&agrave;o tất cả c&aacute;c nh&oacute;m/diễn đ&agrave;n về xuất nhập khẩu để tự học tập kiến thức v&agrave; kinh nghiệm, v&agrave; bạn cũng c&oacute; thể d&agrave;nh rất NHIỀU NĂM để trải nghiệm, chiến đấu đến cả &ldquo;trầy vi tr&oacute;c vảy&rdquo; để c&oacute; được những b&agrave;i học qu&yacute; gi&aacute;, những kinh nghiệm kh&ocirc;ng phải ai cũng c&oacute; được trong nhiều năm l&agrave;m nghề&hellip;</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">Hoặc,</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">Bạn c&oacute; thể thực hiện tất cả những g&igrave; ch&uacute;ng t&ocirc;i chia sẻ trong kh&oacute;a học v&agrave; chỉ cần d&agrave;nh mỗi ng&agrave;y hơn 1 giờ đồng hồ luyện tập đều đặn, ch&uacute;ng t&ocirc;i đảm bảo bạn sẽ ho&agrave;n to&agrave;n tự tin để bắt đầu một c&ocirc;ng việc mới ở một m&ocirc;i trường mới.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:20px\">H&eacute; lộ MỘT PHẦN NHỎ những kiến thức v&agrave;&nbsp;nghiệp vụ&nbsp;bạn được học trong kh&oacute;a học&nbsp;xuất nhập khẩu tại HAN EXIM CLUB:</span></span></p>\r\n\r\n			<p style=\"margin-left:40px\"><span style=\"color:#006400\"><span style=\"font-size:16px\">-&nbsp;C&aacute;ch viết thư giao dịch, đ&agrave;m ph&aacute;n, thăm hỏi, th&ocirc;ng b&aacute;o... (Inquiry, Reply, Quotation...)</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">-&nbsp;Hướng dẫn chi tiết c&aacute;ch soạn thảo Hợp đồng v&agrave; c&aacute;c chứng từ từ đơn giản đến phức tạp&nbsp;tr&ecirc;n Word/ Excel</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Quy tr&igrave;nh xuất nhập khẩu chi tiết đường biển v&agrave; đường h&agrave;ng kh&ocirc;ng&nbsp;</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Li&ecirc;n hệ forwarder, hỏi b&aacute;o gi&aacute; v&agrave; so s&aacute;nh c&aacute;c b&aacute;o gi&aacute; vận tải</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">-&nbsp;C&aacute;ch kiểm so&aacute;t chi ph&iacute; v&agrave; l&agrave;m việc với b&ecirc;n Forwarder hoặc h&atilde;ng t&agrave;u</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">-&nbsp;C&aacute;ch mua bảo hiểm, đ&ograve;i bồi thường bảo hiểm</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">-&nbsp;Quy tr&igrave;nh, hồ sơ, tr&igrave;nh tự thực hiện từng phương thức&nbsp;thanh to&aacute;n Quốc tế thường gặp (LC, TT, DA/DP)</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- T&iacute;nh gi&aacute; vốn h&agrave;ng h&oacute;a nhập khẩu, hướng dẫn thiết lập c&ocirc;ng thức ph&acirc;n bổ c&aacute;c loại chi ph&iacute; ph&aacute;t sinh trước v&agrave; sau nhập khẩu</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">-&nbsp;Hướng dẫn chi tiết c&aacute;ch &aacute;p dụng 6 quy tắc tra m&atilde; HS, thực h&agrave;nh trực tiếp</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">-&nbsp;Quy tắc m&ocirc; tả t&ecirc;n h&agrave;ng h&oacute;a theo đ&uacute;ng ti&ecirc;u chuẩn hải quan, giảm sai s&oacute;t kh&ocirc;ng đ&aacute;ng c&oacute;</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:16px\"><span style=\"color:#006400\">-&nbsp;Quy tr&igrave;nh xin C/O h&agrave;ng xuất khẩu v&agrave;&nbsp;Thực hiện khai b&aacute;o C/O điện tử</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:16px\"><span style=\"color:#006400\">-&nbsp;Thực h&agrave;nh chuẩn bị bộ chứng từ v&agrave; khai b&aacute;o hải quan cho l&ocirc; h&agrave;ng xuất khẩu, nhập khẩu c&aacute;c loại h&igrave;nh cơ bản (&iacute;t nhất 8 loại h&igrave;nh)</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\">&nbsp;</p>\r\n\r\n			<p style=\"margin-left:40px; text-align:center\"><span style=\"color:#006400\"><span style=\"font-size:20px\"><a href=\"https://docs.google.com/forms/d/1NoHiLv1GUu0pxRr3OjZA-gbOTVzD7LwMtRGlBPY9D6M/viewform?edit_requested=true\" target=\"_blank\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:95px; width:250px\" /></a></span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">Ch&uacute;ng t&ocirc;i kh&ocirc;ng thể để lộ qu&aacute; nhiều về c&ocirc;ng thức của m&oacute;n ăn hay nguy&ecirc;n liệu, v&igrave; ch&uacute;ng t&ocirc;i muốn để bạn thực sự trải nghiệm v&agrave; cảm nhận thay v&igrave; tất cả những lời hoa mỹ ch&uacute;ng t&ocirc;i c&oacute; thể viết.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">H&atilde;y suy nghĩ đến điều n&agrave;y:</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn sẽ c&oacute; được kiến thức v&agrave; kinh nghiệm từ cả 2 lĩnh vực Xuất nhập khẩu v&agrave; Logistics của ch&uacute;ng t&ocirc;i chỉ th&ocirc;ng qua kh&oacute;a học n&agrave;y</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn sẽ được trải nghiệm phương ph&aacute;p học tập chủ động, linh hoạt, tăng tương t&aacute;c v&agrave; khả năng phản biện, ph&aacute;t triển tinh thần l&agrave;m việc&nbsp;đội nh&oacute;m của mỗi&nbsp; học vi&ecirc;n</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:center\"><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20HOAT%20DONG/HD69.jpg\" style=\"height:256px; width:400px\" /></span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn sẽ dễ d&agrave;ng nắm thế chủ động với đối t&aacute;c hay kh&aacute;ch h&agrave;ng của m&igrave;nh khi biết r&otilde; họ l&agrave; ai, họ như thế n&agrave;o</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn sẽ tự tin vượt qua những nh&agrave; tuyển dụng kh&oacute; t&iacute;nh kh&ocirc;ng chỉ bằng hiểu biết v&agrave; kỹ năng cứng vượt trội m&agrave; c&ograve;n l&agrave; những kỹ năng mềm để tạo ảnh hưởng, khiến nh&agrave; tuyển dụng chỉ muốn gi&agrave;nh lấy bạn ngay lập tức</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Bạn l&agrave;m việc với phong c&aacute;ch chuy&ecirc;n nghiệp ngay từ ng&agrave;y đầu ti&ecirc;n, khiến cấp tr&ecirc;n h&agrave;i l&ograve;ng v&agrave; đồng nghiệp y&ecirc;u mến, t&ocirc;n trọng v&agrave; họ sẽ hết sức bất ngờ khi biết bạn chẳng phải xuất th&acirc;n từ d&acirc;n chuy&ecirc;n ng&agrave;nh</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">C&oacute; thể bạn sẽ quan t&acirc;m điều n&agrave;y:</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Ch&uacute;ng t&ocirc;i đ&atilde; mất rất nhiều năm để thử v&agrave; sai, tự m&igrave;nh vượt qua rất nhiều loại &aacute;p lực v&agrave; s&oacute;ng gi&oacute; trong nghề. Ch&uacute;ng t&ocirc;i kh&ocirc;ng muốn bạn tiếp tục l&atilde;ng ph&iacute; thời gian như thế</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Nhiều doanh nghiệp nhỏ v&agrave; si&ecirc;u nhỏ mới gia nhập ng&agrave;nh, đ&atilde; v&ocirc; t&igrave;nh mất rất nhiều tiền v&agrave; mất cả uy t&iacute;n với đối t&aacute;c chỉ v&igrave; những sai lầm v&ocirc; c&ugrave;ng nhỏ nhặt</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- C&oacute; thể bạn chỉ &aacute;p dụng một phần nhỏ trong số những nội dung ch&uacute;ng t&ocirc;i chia sẻ v&agrave;o c&ocirc;ng việc bạn sắp sửa đảm nhận, nhưng việc bạn biết nhiều hơn những thứ bạn cần sẽ gi&uacute;p bạn dễ d&agrave;ng tương t&aacute;c với đội nh&oacute;m của m&igrave;nh, hoặc ngay cả việc bạn sẽ c&oacute; cơ hội lựa chọn việc l&agrave;m hay thăng tiến nhiều hơn nh&oacute;m kh&aacute;c</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">Khoan đ&atilde;, bạn c&ograve;n chưa biết những g&igrave; về kh&oacute;a học n&agrave;y?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Ch&uacute;ng t&ocirc;i l&agrave; sự kết hợp ho&agrave;n hảo của quy tr&igrave;nh xuất nhập khẩu - logistics, gi&uacute;p bạn vừa c&oacute; được những kiến thức chuy&ecirc;n m&ocirc;n s&acirc;u sắc từ c&aacute;c vị tr&iacute; xuất nhập khẩu, vừa được huấn luyện c&aacute;c kỹ năng nghiệp vụ sắc b&eacute;n từ c&aacute;c vị tr&iacute; chủ chốt trong c&ocirc;ng ty logistics</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- L&agrave; những người cầu to&agrave;n trong c&ocirc;ng việc v&agrave; cuộc sống, ch&uacute;ng t&ocirc;i rất khắt khe đối với sinh vi&ecirc;n của m&igrave;nh. Nếu bạn mong muốn một lớp học thoải m&aacute;i như ở trường Đại học, ch&uacute;ng t&ocirc;i kh&ocirc;ng phải l&agrave; lựa chọn của bạn.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Nếu bạn đ&atilde; tự tin về kiến thức của m&igrave;nh, kh&oacute;a học cũng kh&ocirc;ng phải l&agrave; nơi d&agrave;nh cho bạn. V&igrave; ở đ&acirc;y, ch&uacute;ng t&ocirc;i coi c&aacute;c bạn khởi điểm bằng <strong>&quot;kh&ocirc;ng&quot;</strong> v&agrave; hướng dẫn cho bạn bắt đầu từ những kiến thức cơ bản nhất</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">B&acirc;y giờ, bạn quan t&acirc;m đến học ph&iacute; đ&uacute;ng kh&ocirc;ng?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Tặng bạn một buổi học trải nghiệm bất kỳ: miễn ph&iacute;, để bạn tự đ&aacute;nh gi&aacute; v&agrave; cảm nhận</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Học ph&iacute;: chi tiết trong link đăng k&yacute; (ấn v&agrave;o n&uacute;t đăng k&yacute; để xem th&ocirc;ng tin cụ thể)</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:16px\">- C&oacute; ưu đ&atilde;i học ph&iacute; cho c&aacute;c bạn đăng k&yacute; nh&oacute;m</span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Kh&ocirc;ng bắt buộc đ&oacute;ng học ph&iacute; trước. Tuy nhi&ecirc;n, nếu bạn muốn giữ chỗ chắc chắn, bạn c&oacute; thể đ&oacute;ng học ph&iacute; trước qua h&igrave;nh thức chuyển khoản. Sau khi học thử, bạn cảm thấy lớp học kh&ocirc;ng ph&ugrave; hợp, bạn được <strong>ho&agrave;n lại 100% học ph&iacute;</strong>.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Tặng s&aacute;ch b&agrave;i tập, s&aacute;ch chứng từ&nbsp;được thiết kế cẩn thận, tăng khả năng tương t&aacute;c v&agrave; theo s&aacute;t chương tr&igrave;nh học.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:center\"><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/S48.JPG\" style=\"height:268px; width:450px\" /></span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:center\"><a href=\"https://docs.google.com/forms/d/1NoHiLv1GUu0pxRr3OjZA-gbOTVzD7LwMtRGlBPY9D6M/viewform?edit_requested=true\" target=\"_blank\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:95px; width:250px\" /></a></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"font-size:20px\"><span style=\"color:#006400\">Bạn đang cảm thấy như thế n&agrave;o? Bạn quan t&acirc;m một ch&uacute;t đến lộ tr&igrave;nh học chứ?</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">Ch&uacute;ng ta sẽ c&oacute; 20 buổi học x 2.5giờ/buổi, tổng số giờ&nbsp;huấn luyện&nbsp;l&agrave;: 50&nbsp;giờ.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">Gi&aacute; trị cộng th&ecirc;m cho bạn nếu bạn đặt niềm tin v&agrave;o ch&uacute;ng t&ocirc;i,</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Đi thực tế ở Cảng (cảng biển hoặc cảng h&agrave;ng kh&ocirc;ng), chi cục Hải quan, c&aacute;c cơ quan chuy&ecirc;n ng&agrave;nh (VCCI, Cục H&oacute;a Chất, Cục Viễn Th&ocirc;ng...)</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:center\"><img alt=\"\" src=\"/uploads/ckfinder/images/ANH%20HOAT%20DONG/HD9.jpg\" style=\"height:256px; width:400px\" /></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Hướng dẫn c&aacute;c kỹ thuật tạo ảnh hưởng, g&acirc;y thiện cảm với nh&agrave; tuyển dụng</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Phỏng vấn bất chợt v&agrave; tư vấn kỹ năng trả lời phỏng vấn tự tin</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Hướng dẫn chi tiết c&aacute;ch l&agrave;m CV khiến NTD dừng lại ngay trước CV của bạn</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Chia sẻ c&aacute;ch để bạn trở th&agrave;nh người c&oacute; sức ảnh hưởng tới tổ chức thay v&igrave; một nh&acirc;n vi&ecirc;n tầm thường</span></span></p>\r\n\r\n			<h1 style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Đặc biệt, hiểu được nỗi l&ograve;ng của c&aacute;c bạn khi đến với nghề Xuất nhập khẩu l&agrave; băn khoăn về tr&igrave;nh độ tiếng Anh của m&igrave;nh. HAN EXIM CLUB c&oacute; tổ chức c&aacute;c kh&oacute;a <strong>tiếng Anh sinh hoạt offline v&agrave; online</strong> hỗ trợ c&aacute;c bạn học vi&ecirc;n về tiếng Anh (cả về ph&aacute;t &acirc;m v&agrave; giao tiếp, kinh doanh, xuất nhập khẩu...)&nbsp;</span><span style=\"font-size:18px\"><strong>free 100%</strong></span><span style=\"font-size:16px\">. Link tham khảo&nbsp;<strong><a href=\"https://www.facebook.com/tuhocxuatnhapkhau/\">tại đ&acirc;y</a></strong></span></span></h1>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Hỗ trợ học vi&ecirc;n &quot;trọn đời&quot;, học ph&iacute; đ&oacute;ng 1 lần nhưng được học lại &quot;nhiều lần&quot; bất cứ khi n&agrave;o bạn muốn, học 1 phần hoặc to&agrave;n bộ kh&oacute;a học t&ugrave;y bạn.&nbsp;</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- Giới thiệu cơ hội thực tập, việc l&agrave;m tại c&aacute;c C&ocirc;ng ty Xuất nhập khẩu- logistics.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">- C&oacute; ch&iacute;nh s&aacute;ch ưu đ&atilde;i, miễn giảm học ph&iacute; cho học vi&ecirc;n c&oacute; ho&agrave;n cảnh kh&oacute; khăn, gia đ&igrave;nh ch&iacute;nh s&aacute;ch, người khuyết tật. Li&ecirc;n hệ với ch&uacute;ng t&ocirc;i để biết th&ecirc;m chi tiết.</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:justify\"><span style=\"color:#006400\"><span style=\"font-size:16px\">H&atilde;y đăng k&yacute; v&agrave; chọn thời gian học th&iacute;ch hợp nh&eacute;!&nbsp;</span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:center\"><span style=\"color:#006400\"><span style=\"font-size:16px\"><a href=\"https://docs.google.com/forms/d/1NoHiLv1GUu0pxRr3OjZA-gbOTVzD7LwMtRGlBPY9D6M/viewform?edit_requested=true\" target=\"_blank\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:95px; width:250px\" /></a></span></span></p>\r\n\r\n			<p style=\"margin-left:40px; text-align:center\"><font color=\"#006400\"><span style=\"font-size:16px\">Bạn c&oacute; c&acirc;u hỏi, vui l&ograve;ng xem <a href=\"http://tuhocxuatnhapkhau.com/news-detail/cau-hoi-thuong-gap-cua-cac-ban-truoc-khi-tham-gia-khoa-hoc\">tại đ&acirc;y</a></span></font></p>\r\n			</td>\r\n			<td>\r\n			<p style=\"margin-left:40px\"><span style=\"color:#006400\"><span style=\"font-size:16px\">H&atilde;y nghe những người đi trước n&oacute;i về ch&uacute;ng t&ocirc;i:</span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/1%20hai%20anh.jpg\" style=\"height:350px; width:320px\" /></span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/2%20huyen%20cham.jpg\" style=\"height:364px; width:320px\" /></span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/3%20ha%20tran.jpg\" style=\"height:352px; width:320px\" /></span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/4thu%20thuy.jpg\" style=\"height:332px; width:320px\" /></span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/5hong%20ky.jpg\" style=\"height:460px; width:320px\" /></span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/6huong%20bee.jpg\" style=\"height:378px; width:320px\" /></span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/3nguyen%20thanh.jpg\" style=\"height:645px; width:320px\" /></span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/khoa%20nguyen.jpg\" style=\"height:348px; width:320px\" /></span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thanh%20mai.jpg\" style=\"height:394px; width:320px\" /></span></span></p>\r\n\r\n			<p><span style=\"color:#006400\"><span style=\"font-size:16px\"><img alt=\"\" src=\"/uploads/ckfinder/images/CAM%20NHAN%20HOC%20VIEN/thu%20hien.jpg\" style=\"height:330px; width:320px\" /></span></span></p>\r\n\r\n			<p style=\"margin-left:40px\"><span style=\"color:#006400\"><span style=\"font-size:16px\">V&agrave; c&ograve;n rất nhiều nữa...</span></span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>',1,'vi','khoa-xnk-va-logistics-toan-dien','introduction',1,26,'2018-01-19 22:37:34','2019-03-05 13:56:31'),(57,'KHÓA INTERNATIONAL FREIGHT FORWARDING (GIAO NHẬN HÀNG HÓA QUỐC TẾ)',NULL,'<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Trước khi bạn định t&igrave;m hiểu kh&oacute;a học INTERNATIONAL FREIGHT FORWARDING (GIAO NHẬN H&Agrave;NG H&Oacute;A QUỐC TẾ), h&atilde;y xem bạn l&agrave; ai trong số những nh&oacute;m người dưới đ&acirc;y?</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">1/ C&oacute; nền tảng về c&aacute;c ng&agrave;nh kinh tế, ngoại thương, thương mại n&oacute;i chung</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">2/ C&oacute; khả năng d&ugrave;ng tiếng Anh giao tiếp hoặc đọc viết cơ bản</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">3/ Mới bắt đầu c&ocirc;ng việc tại c&aacute;c c&ocirc;ng ty Freight Forwarding</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">4/ Muốn chuyển đổi c&ocirc;ng việc sang mảng Freight Forwarding/Logistics</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">5/ Đ&atilde; c&ocirc;ng t&aacute;c nhiều năm nhưng vẫn kh&ocirc;ng thể thăng tiến, muốn c&oacute; những bước tiến mới trong c&ocirc;ng việc</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Nếu bạn l&agrave; một trong năm đối tượng tr&ecirc;n, bạn c&oacute; thể y&ecirc;n t&acirc;m đọc tiếp c&aacute;c nội dung chắc chắn bạn sẽ rất quan t&acirc;m dưới đ&acirc;y. Nếu kh&ocirc;ng phải, bạn cũng kh&ocirc;ng cần hỏi g&igrave; th&ecirc;m. Kh&oacute;a học n&agrave;y l&agrave; HUẤN LUYỆN CHUY&Ecirc;N S&Acirc;U n&ecirc;n ch&uacute;ng t&ocirc;i tuyển đầu v&agrave;o đều để chất lượng đầu ra tốt nhất.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Bạn quan t&acirc;m? Vậy bạn biết g&igrave; về bộ phận Dịch vụ kh&aacute;ch h&agrave;ng (Customer service) v&agrave; Chứng từ (Documentation) tại c&aacute;c c&ocirc;ng ty giao nhận quốc tế (International Freight forwarders) v&agrave; nhiệm vụ của họ?</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><img alt=\"\" src=\"/uploads/ckfinder/images/1_%20Hanexim%20-%20Book%20cover%202.jpg\" style=\"height:496px; width:400px\" /></span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Cho những fresh fishes: Kinh nghiệm cho thấy bạn c&oacute; thể mất &iacute;t nhất 2 th&aacute;ng để sử dụng th&agrave;nh thạo tất c&aacute;c loại m&aacute;y văn ph&ograve;ng, 4 th&aacute;ng để l&agrave;m việc được tr&ecirc;n hệ thống phần mềm của c&ocirc;ng ty, 6 th&aacute;ng để tạm nắm được m&ocirc; tả c&ocirc;ng việc v&agrave; l&agrave;m việc dưới sự gi&aacute;m s&aacute;t, 8 th&aacute;ng để c&oacute; thể l&agrave;m việc độc lập v&agrave; khoảng 1 năm để hiểu thực sự bạn đang l&agrave;m g&igrave;.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Bạn <strong>thật may mắn</strong> v&igrave; ch&uacute;ng t&ocirc;i c&oacute; c&ocirc;ng cụ gi&uacute;p bạn <strong>r&uacute;t ngắn</strong> khung thời gian tr&ecirc;n xuống c&ograve;n 10 buổi&nbsp;huấn luyện li&ecirc;n tục với cường độ cao.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Kh&oacute;a học n&agrave;y cho bạn cơ hội <strong>tiếp cận c&ocirc;ng việc thực tế</strong> ở hai bộ phận Dịch vụ kh&aacute;ch h&agrave;ng (Customer service) v&agrave; Chứng từ (Documentation) tại c&aacute;c c&ocirc;ng ty giao nhận quốc tế. Đ&acirc;y l&agrave; hai bộ phận cực kỳ quan trọng mang lại nhiều <strong>cơ hội việc l&agrave;m v&agrave; thăng tiến</strong> cho c&aacute;c bạn muốn l&agrave;m việc trong lĩnh vực logistics. Vậy bạn được học g&igrave; đ&acirc;y?</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Được học về c&aacute;c <strong>quy tr&igrave;nh l&agrave;m h&agrave;ng chi tiết</strong> ở từng phương thức giao nhận (Air, Ocean), c&aacute;ch thức giao dịch với kh&aacute;ch h&agrave;ng, h&atilde;ng vận tải, c&aacute;ch l&agrave;m việc với c&aacute;c bộ phận li&ecirc;n quan trong nội bộ c&ocirc;ng ty. Nếu bạn muốn l&agrave;m chủ, kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o kh&aacute;c ngo&agrave;i việc nắm chắc quy tr&igrave;nh v&agrave; đứng tr&ecirc;n hệ thống.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Được học <strong>c&aacute;ch thức xử l&yacute; chứng từ, giải quyết c&aacute;c ph&aacute;t sinh</strong> trong qu&aacute; tr&igrave;nh giao nhận h&agrave;ng h&oacute;a, chứng từ. Bạn c&oacute; thể mất &iacute;t nhất 2 năm để được coi l&agrave; &ldquo;c&oacute; kinh nghiệm&rdquo; trong nghề, bạn đổ rất nhiều c&ocirc;ng sức, thời gian, tuổi thanh xu&acirc;n, tiền bạc để &ldquo;mua&rdquo; lại những b&agrave;i học xương m&aacute;u, những kinh nghiệm m&agrave; người đi trước cũng từng trải qua, v&agrave; tất nhi&ecirc;n l&agrave; bạn sẽ kh&ocirc;ng được dạy điều đ&oacute; trong c&ocirc;ng ty. V&igrave; vậy, ch&uacute;ng t&ocirc;i sẽ gi&uacute;p bạn c&aacute;ch l&agrave;m thế n&agrave;o để <strong>tăng sự linh hoạt</strong> trong giải quyết v&agrave; xử l&yacute; c&aacute;c ph&aacute;t sinh trong qu&aacute; tr&igrave;nh l&agrave;m việc, gi&uacute;p bạn nhanh bắt nhịp với c&ocirc;ng việc mới.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><a href=\"https://docs.google.com/forms/d/1jLQrZaXxCjJvx2lhoYn9Ld0xaDpkWZRqzL5kjWI2-Nc/edit\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:95px; width:250px\" /></a></span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Được học <strong>c&aacute;ch quản l&yacute; thanh to&aacute;n, quản l&yacute; c&ocirc;ng nợ</strong> giữa c&ocirc;ng ty với h&atilde;ng vận tải, nh&agrave; thầu phụ hay b&ecirc;n gửi h&agrave;ng. Bạn c&oacute; thể phải d&agrave;nh cả th&aacute;ng lương, thậm ch&iacute; bị đuổi việc chỉ v&igrave; kh&ocirc;ng thể bồi thường nổi những g&igrave; bạn đ&atilde; v&ocirc; t&igrave;nh l&agrave;m sai do &ldquo;kh&ocirc;ng ai bảo&rdquo;. May mắn cho bạn l&agrave; ch&uacute;ng t&ocirc;i c&oacute; dạy bạn rất kỹ c&agrave;ng để bạn c&oacute; thể vững v&agrave;ng đảm nhận phần việc n&agrave;y như một nh&acirc;n vi&ecirc;n c&oacute; kinh nghiệm.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Được học <strong>c&aacute;ch quản l&yacute; đơn h&agrave;ng chặt chẽ, tr&aacute;nh sai s&oacute;t</strong>. Nếu bạn may mắn sẽ được dạy c&aacute;ch hoạt động của quy tr&igrave;nh trong c&ocirc;ng ty, c&aacute;ch l&agrave;m đ&uacute;ng. Nhưng vận h&agrave;nh như thế n&agrave;o để đạt hiệu quả cao nhất, tr&aacute;nh c&aacute;c sai s&oacute;t v&agrave; xử l&yacute; c&aacute;c sai s&oacute;t, th&igrave; hầu như kh&ocirc;ng bao giờ c&oacute; trong chương tr&igrave;nh huấn luyện của c&ocirc;ng ty. Nếu bạn nắm được c&aacute;c vấn đề n&agrave;y, bạn sẽ &ldquo;kh&aacute;c biệt v&agrave; nổi trội&rdquo;.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Được học <strong>c&aacute;ch quản l&yacute; c&ocirc;ng việc của bản th&acirc;n v&agrave; dẫn dắt đội, nh&oacute;m</strong>. Bao l&acirc;u rồi bạn vẫn l&agrave; &ldquo;staff&rdquo;? Bạn muốn bao l&acirc;u nữa sẽ được promote l&ecirc;n vị tr&iacute; cao hơn? Bạn nghĩ người l&atilde;nh đạo v&agrave; dẫn dắt cần tố chất? Tất cả đều c&oacute; thể học được bạn nh&eacute;! Nếu bạn định l&agrave;m staff trọn đời, tốt th&ocirc;i, nhưng phải l&agrave; &ldquo;super staff&rdquo;, phải &ldquo;outstanding staff&rdquo; chứ nhất định kh&ocirc;ng được ph&eacute;p &ldquo;tầm tầm v&agrave; thường thường&rdquo;.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Được học để c&oacute; <strong>t&aacute;c phong chuy&ecirc;n nghiệp, lịch l&atilde;m, văn minh</strong> m&agrave; KH&Ocirc;NG AI DẠY BẠN trong cuộc sống. Bạn nghĩ sao khi c&oacute; những nh&acirc;n vi&ecirc;n, những vị sếp m&agrave; ai cũng th&iacute;ch n&oacute;i chuyện, ai cũng y&ecirc;u qu&yacute;, trong khi bạn&hellip;lại &ldquo;tầm tầm v&agrave; thường thường&rdquo;, đi kh&ocirc;ng ai biết, về kh&ocirc;ng ai hay. Bạn c&oacute; bao giờ đặt c&acirc;u hỏi, Tại sao đứa n&agrave;y n&oacute; ra trường bằng giỏi như m&igrave;nh m&agrave; giờ n&oacute; đ&atilde; l&agrave; &ocirc;ng nọ b&agrave; kia, m&igrave;nh vẫn staff kh&ocirc;ng hơn? Tại sao người n&agrave;y v&agrave;o c&ugrave;ng l&uacute;c với m&igrave;nh, kinh nghiệm thua m&igrave;nh m&agrave; giờ lại l&ecirc;n lương, thăng chức v&agrave; được đi c&ocirc;ng t&aacute;c nước ngo&agrave;i li&ecirc;n tục? Điều đ&oacute; c&oacute; nghĩa l&agrave;, người ta biết điều g&igrave; đ&oacute; m&agrave; bạn chưa biết đấy th&ocirc;i! V&igrave; thế, ch&uacute;ng t&ocirc;i ở đ&acirc;y để h&eacute; lộ cho bạn biết điều m&agrave; bạn kh&ocirc;ng biết l&agrave; m&igrave;nh kh&ocirc;ng biết ;)</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><a href=\"https://docs.google.com/forms/d/1jLQrZaXxCjJvx2lhoYn9Ld0xaDpkWZRqzL5kjWI2-Nc/edit\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:95px; width:250px\" /></a></span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- <strong>Được truyền cảm hứng t&iacute;ch cực v&agrave; l&ograve;ng y&ecirc;u nghề</strong> say m&ecirc; từ người dẫn đầu. Xung quanh bạn, c&oacute; những người cũng ch&aacute;n nản giống bạn, mỗi ng&agrave;y đi l&agrave;m l&agrave; một cực h&igrave;nh, uể oải v&agrave; mệt mỏi, &aacute;p lực v&agrave; căng thẳng. Nhưng sao c&oacute; những người cũng bận rộn nhưng nh&igrave;n người ta l&agrave;m việc thấy tr&agrave;n trề năng lượng v&agrave; như say m&ecirc; với n&oacute; lắm. L&agrave;m thế n&agrave;o họ c&oacute; thể l&agrave;m được điều đ&oacute; đều như vắt chanh 30 ng&agrave;y mỗi th&aacute;ng? B&iacute; quyết sẽ được chia sẻ từ người dẫn đầu, bạn sẽ được truyền cảm hứng để lu&ocirc;n c&oacute; một ng&agrave;y l&agrave;m việc vui vẻ v&agrave; nhiều năng lượng.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Được <strong>phỏng vấn m&ocirc; phỏng</strong> cho c&aacute;c bạn muốn thử sức ở hai vị tr&iacute; c&ocirc;ng việc n&agrave;y. Dĩ nhi&ecirc;n, để đạt ti&ecirc;u chuẩn tốt nghiệp, bạn phải &ldquo;pass&rdquo; phỏng vấn với gi&aacute;o vi&ecirc;n &ndash; NTD kh&oacute; t&iacute;nh của c&aacute;c bạn. Nếu kh&ocirc;ng th&igrave; sao? Ch&uacute;ng t&ocirc;i vẫn d&agrave;nh chỗ cho c&aacute;c bạn học lại đến khi n&agrave;o &ldquo;qua&rdquo; th&igrave; th&ocirc;i. Đ&ugrave;a đấy, ch&uacute;ng t&ocirc;i kh&ocirc;ng muốn bạn l&atilde;ng ph&iacute; th&ecirc;m thời gian v&agrave; c&ocirc;ng sức (học lại miễn ph&iacute; nh&eacute;) v&agrave; cả tuổi thanh xu&acirc;n chỉ để đến lớp học của c&ocirc; gi&aacute;o đ&acirc;u. C&aacute;c chiến binh, d&ugrave; muốn hay kh&ocirc;ng th&igrave; sau khi đ&atilde; được huấn luyện đầy đủ cũng phải ra chiến trường để chiến đấu chứ!</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">- Được n&acirc;ng cao kiến thức về <strong>tiếng Anh xuất nhập khẩu- mảng freight forwarding</strong>. R&otilde; r&agrave;ng, Tiếng Anh l&agrave; thứ ng&ocirc;n ngữ bạn kh&ocirc;ng thể n&oacute;i rằng &ldquo;em kh&ocirc;ng biết tiếng Anh liệu c&oacute; theo được nghề n&agrave;y kh&ocirc;ng?&rdquo; Nếu bạn muốn theo nghề, bạn phải học. Nếu bạn muốn c&acirc;u c&aacute;, &iacute;t nhất bạn phải c&oacute; cần c&acirc;u v&agrave; mồi c&acirc;u chứ? Ch&uacute;ng t&ocirc;i kh&ocirc;ng phải l&agrave; trung t&acirc;m tiếng Anh n&ecirc;n kh&ocirc;ng thể dạy c&aacute;c bạn bảng chữ c&aacute;i hay Hello, How are you? Bạn được dạy để đọc chứng từ, để giao dịch&hellip; v&agrave; kết quả cuối kh&oacute;a l&agrave; bạn phải trở n&ecirc;n <strong>th&agrave;nh thạo v&agrave; quen thuộc với c&aacute;ch giao dịch bằng tiếng Anh trong c&ocirc;ng việc</strong>. Ch&uacute;ng t&ocirc;i sẽ d&agrave;nh nhiều thời gian cho bạn để bạn thực h&agrave;nh phần n&agrave;y.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><a href=\"https://docs.google.com/forms/d/1jLQrZaXxCjJvx2lhoYn9Ld0xaDpkWZRqzL5kjWI2-Nc/edit\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:95px; width:250px\" /></a></span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Tinh thần của ch&uacute;ng t&ocirc;i l&agrave; kh&ocirc;ng những <strong>gi&uacute;p bạn kiếm được một c&ocirc;ng việc m&agrave; gi&uacute;p bạn đứng vững v&agrave; đẩy bạn l&ecirc;n vị tr&iacute; cao hơn.</strong></span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Bằng c&aacute;ch n&agrave;o? Kết hợp việc giảng dạy kiến thức, kỹ năng mềm &aacute;p dụng ngay trong thực địa, th&ecirc;m v&agrave;o đ&oacute; phần lớn thời gian bạn sẽ thực h&agrave;nh theo quy tr&igrave;nh m&ocirc; phỏng như thực tế.</span></span></span></p>\r\n\r\n<p><br />\r\n<span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Kết quả l&agrave; <strong>bạn sẽ l&agrave;m việc như một nh&acirc;n vi&ecirc;n c&oacute; kinh nghiệm trước cả khi bạn thật sự bắt đầu l&agrave;m việc cho c&ocirc;ng ty.</strong></span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><a href=\"https://docs.google.com/forms/d/1jLQrZaXxCjJvx2lhoYn9Ld0xaDpkWZRqzL5kjWI2-Nc/edit\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:95px; width:250px\" /></a></span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Học ph&iacute; 4.000.000 VNĐ đ&oacute;ng tại lớp học (v&agrave;o cuối buổi khai giảng hoặc chuyển khoản trước)</span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><strong>Ch&uacute;ng t&ocirc;i hiểu bạn c&oacute; thể t&igrave;m được kh&oacute;a học với chi ph&iacute; thấp hơn nhưng bạn cũng biết rằng &ldquo; You get what you pay for&rdquo;&rdquo;- Bạn nhận được những g&igrave; bạn trả cho. </strong></span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Lịch học Tối thứ 2-4-6, từ 18:00-20:30.</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\">Số lượng học vi&ecirc;n tối đa: 5 học vi&ecirc;n/lớp</span></span></span></p>\r\n\r\n<p><span style=\"color:#000080\"><span style=\"font-size:16px\"><span style=\"font-family:arial,helvetica,sans-serif\"><a href=\"https://docs.google.com/forms/d/1jLQrZaXxCjJvx2lhoYn9Ld0xaDpkWZRqzL5kjWI2-Nc/edit\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:95px; width:250px\" /></a></span></span></span></p>',1,'vi','khoa-international-freight-forwarding-giao-nhan-hang-hoa-quoc-te','introduction',27,27,'2018-04-22 10:22:38','2018-06-23 15:15:06'),(58,'HƯỚNG DẪN THỰC HÀNH LÀM BÁO CÁO QUYẾT TOÁN THEO THÔNG TƯ 39/2018',NULL,'<p><span style=\"font-size:14px\">Kh&oacute;a học &ldquo;<em><strong>Hướng dẫn thực h&agrave;nh l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n</strong></em>&rdquo; ra đời nhằm đ&aacute;p ứng nhu cầu của những học vi&ecirc;n đang l&agrave;m việc trong c&aacute;c c&ocirc;ng ty gia c&ocirc;ng, c&aacute;c c&ocirc;ng ty chế xuất v&agrave; sản xuất xuất khẩu trong những khu c&ocirc;ng nghiệp, khu chế xuất tại Việt Nam.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">&nbsp;Trong qu&aacute; tr&igrave;nh l&agrave;m việc trực tiếp về b&aacute;o c&aacute;o quyết to&aacute;n tại c&aacute;c c&ocirc;ng ty trong khu c&ocirc;ng nghiệp rất hay xảy ra t&igrave;nh trạng sai lệch về số liệu giữa c&aacute;c bộ phận: bộ phận kế to&aacute;n, bộ phận kho v&agrave; bộ phận Xuất nhập khẩu. Đ&ocirc;i khi c&ograve;n xảy ra t&igrave;nh trạng c&aacute;c bộ phận n&agrave;y đổ lỗi cho nhau về việc b&aacute;o c&aacute;o quyết to&aacute;n bị sai lệch. B&ecirc;n cạnh đ&oacute; c&ograve;n l&agrave; sự yếu k&eacute;m của nh&acirc;n sự của c&aacute;c c&ocirc;ng ty khi chưa hiểu r&otilde; được bản chất của việc l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n, chưa cập nhật kịp thời những ti&ecirc;u ch&iacute; l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n theo những th&ocirc;ng tư, nghị định mới nhất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Thấy r&otilde; được vấn đề n&agrave;y, CLB Y&ecirc;u xuất nhập khẩu H&agrave; Nội - HAN EXIM CLUB đ&atilde; nghi&ecirc;n cứu v&agrave; tổ chức kh&oacute;a học &ldquo;Hướng dẫn thực h&agrave;nh l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n&rdquo; cho c&aacute;c bạn học vi&ecirc;n đang l&agrave;m việc cho c&aacute;c c&ocirc;ng ty gia c&ocirc;ng, sản xuất xuất khẩu v&agrave; c&aacute;c c&ocirc;ng ty chế xuất.</span></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><strong><span style=\"font-size:14px\">ĐIỂM NHẤN CỦA KH&Oacute;A HỌC</span></strong></em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Học qua giảng vi&ecirc;n c&oacute; nhiều kinh nghiệm giảng dạy, đ&atilde; trực tiếp tư vấn l&agrave;m nhiều b&aacute;o c&aacute;o quyết to&aacute;n cho c&aacute;c doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Phương ph&aacute;p dạy &ldquo;cầm tay chỉ việc&rdquo; cho đến khi học vi&ecirc;n hiểu v&agrave; tự l&agrave;m được b&aacute;o c&aacute;o quyết to&aacute;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Tương t&aacute;c trực tiếp với giảng vi&ecirc;n ngay trong buổi học, đi tới tận c&ugrave;ng của vấn đề cần thảo luận.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Chất lượng đ&agrave;o tạo ở mức cao nhất, với gi&aacute;o tr&igrave;nh học đ&atilde; được nghi&ecirc;n cứu v&agrave; bi&ecirc;n soạn tỉ mỉ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Hỗ trợ học vi&ecirc;n xuy&ecirc;n suốt qu&aacute; tr&igrave;nh học v&agrave; sau kh&oacute;a học.</span></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><strong><span style=\"font-size:14px\">AI N&Ecirc;N THAM GIA KH&Oacute;A HỌC N&Agrave;Y?</span></strong></em></span></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><strong><span style=\"font-size:14px\"><img alt=\"\" src=\"/uploads/ckfinder/images/H3.jpg\" style=\"height:375px; width:500px\" /></span></strong></em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- L&atilde;nh đạo, gi&aacute;m đốc doanh nghiệp, trưởng c&aacute;c bộ phận: bộ phận kho, bộ phận kế to&aacute;n, bộ phận xuất nhập khẩu tại c&aacute;c c&ocirc;ng ty trong khu chế xuất, khu c&ocirc;ng nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Nh&acirc;n vi&ecirc;n đang l&agrave;m việc ở c&aacute;c bộ phận kho &ndash; Kế to&aacute;n &ndash; Xuất nhập khẩu của c&aacute;c doanh nghiệp chế xuất, sxxk, gia c&ocirc;ng</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Những bạn c&oacute; &yacute; định xin việc về xuất nhập khẩu tại doanh nghiệp chế xuất, sxxk, gia c&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Những bạn muốn hướng nghề nghiệp với ng&agrave;nh nghề xuất nhập khẩu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><span style=\"color:#FF0000\"><em><strong>KH&Oacute;A HỌC &ldquo;HƯỚNG DẪN THỰC H&Agrave;NH L&Agrave;M B&Aacute;O C&Aacute;O QUYẾT TO&Aacute;N</strong></em></span>&rdquo; tập trung giải quyết những vấn đề sau:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Gi&uacute;p học vi&ecirc;n hiểu được những kh&aacute;i niệm li&ecirc;n quan khi l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- X&acirc;y dựng quy tr&igrave;nh l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n, c&aacute;ch t&iacute;nh to&aacute;n v&agrave; xử l&yacute; đối với Nguy&ecirc;n phụ liệu, sản phẩm, định mức.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Thực h&agrave;nh v&agrave; khai b&aacute;o b&aacute;o c&aacute;o quyết to&aacute;n theo case study thực tế.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"\" src=\"/uploads/ckfinder/images/H5.jpg\" style=\"height:375px; width:500px\" /></span></p>\r\n\r\n<p><em><span style=\"color:#FF0000\"><span style=\"font-size:14px\">Thời lượng: 4 buổi-10 giờ giảng dạy</span></span></em></p>\r\n\r\n<p><em><span style=\"color:#FF0000\"><span style=\"font-size:14px\">Lịch học: 2 ng&agrave;y Chủ nhật&nbsp;</span></span></em></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><span style=\"font-size:14px\">Giờ học: s&aacute;ng 09h00&nbsp;- 11h30, chiều 13h00-15h30</span></em></span></p>\r\n\r\n<p><em><span style=\"color:#FF0000\"><span style=\"font-size:14px\">Lịch khai giảng: Li&ecirc;n hệ hotline<strong> 0974 155 033</strong> để biết th&ecirc;m chi tiết</span></span></em></p>\r\n\r\n<p><span style=\"font-size:14px\">Địa điểm: Trung t&acirc;m đ&agrave;o tạo xuất nhập khẩu-Logistics</span><span style=\"font-size:14px\">- Han Exim Club,&nbsp;cơ sở 2, tầng 4 t&ograve;a nh&agrave; Vietcombank số 52 Đường hồ T&ugrave;ng Mậu, quận Cầu Giấy, H&agrave; Nội.</span></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><strong><span style=\"font-size:14px\">LỢI &Iacute;CH CỦA KH&Oacute;A HỌC:</span></strong></em></span></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><strong><span style=\"font-size:14px\"><a href=\"https://docs.google.com/forms/d/e/1FAIpQLSdK7vPbgEEvrgZY6KBs0npd0igmlaIeMQwIg4r0hAsN34HqQQ/viewform\" target=\"_blank\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:38px; width:150px\" /></a></span></strong></em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Học vi&ecirc;n được trang bị những kiến thức từ cơ bản đến chuy&ecirc;n s&acirc;u của qu&aacute; tr&igrave;nh thực hiện l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n tại c&aacute;c doanh nghiệp gia c&ocirc;ng, sxxk, chế xuất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Nắm vững những kh&aacute;i niệm: gia c&ocirc;ng, nguy&ecirc;n phụ liệu, phế liệu, phế phẩm, sản phẩm, định mức.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Biết r&otilde; quy tr&igrave;nh đăng k&yacute; cơ sở sản xuất, nh&agrave; xưởng với hải quan. C&aacute;ch thức t&iacute;nh phế liệu, phế phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;ch thức đăng k&yacute; danh mục nguy&ecirc;n phụ liệu, sản phẩm, định mức. C&aacute;ch đăng k&yacute; tờ khai xuất, nhập v&agrave; c&aacute;c thủ tục li&ecirc;n quan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;ch khai b&aacute;o b&aacute;o c&aacute;o quyết to&aacute;n theo th&ocirc;ng tư 39 v&agrave; tư vấn thanh khoản.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Gi&uacute;p học vi&ecirc;n kh&ocirc;ng c&ograve;n bối rối khi thực hiện việc l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n cho doanh nghiệp, tự tin đề xuất mức lương tốt hơn với l&atilde;nh đạo.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Mở ra cơ hội cho những c&ocirc;ng việc mới, với mức lương hấp dẫn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"\" src=\"/uploads/ckfinder/images/H7.jpg\" style=\"height:375px; width:500px\" /></span></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><strong><span style=\"font-size:14px\">NỘI DUNG CHI TIẾT KH&Oacute;A HỌC</span></strong></em></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Buổi 1: C&aacute;c kh&aacute;i niệm li&ecirc;n quan v&agrave; đặc điểm của doanh nghiệp gia c&ocirc;ng, Sản xuất xuất khẩu, chế xuất.</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>C&aacute;c kh&aacute;i niệm li&ecirc;n quan:</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Kh&aacute;i niệm về doanh nghiệp gia c&ocirc;ng, sxxk, chế xuất theo quy định của ph&aacute;p luật.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Kh&aacute;i niệm về: b&ecirc;n đặt gia c&ocirc;ng, b&ecirc;n nhận gia c&ocirc;ng, nguy&ecirc;n vật liệu gia c&ocirc;ng sản xuất, quy tr&igrave;nh cung ứng nguy&ecirc;n vật liệu, nh&agrave; xưởng, m&aacute;y m&oacute;c, thiết bị&hellip;</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Đặc điểm của doanh nghiệp gia c&ocirc;ng, sản xuất xuất khẩu, chế xuất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;c quy định về thuế quan, những ưu đ&atilde;i của ch&iacute;nh phủ đối với c&aacute;c doanh nghiệp gia c&ocirc;ng, sxxk, chế xuất</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;c loại h&igrave;nh mở tờ khai hải quan đối với c&aacute;c doanh nghiệp n&agrave;y như: tờ khai nhập nguy&ecirc;n phụ liệu một phần, to&agrave;n phần, nhập m&aacute;y m&oacute;c tạo t&agrave;i sản cố định, tờ khai xuất sản phẩm, th&agrave;nh phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Thủ tục chuyển giao nguy&ecirc;n phụ liệu đối với từng loại h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Hướng dẫn đăng k&yacute; cơ sở sản xuất</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;ch&nbsp; l&agrave;m thủ tục đăng k&yacute; cơ sở sản xuất</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Quy tr&igrave;nh kiểm tra hải quan</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;ch x&acirc;y dựng nh&agrave; xưởng, quy tr&igrave;nh kiểm định ti&ecirc;u chuẩn.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Về phế liệu, phế phẩm.</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;ch t&iacute;nh phế liệu, phế phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Thủ tục ti&ecirc;u hủy phế liệu, phế phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Thủ tục b&aacute;n phế liệu, phế phẩm</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Buổi 2: Định&nbsp; mức, hao hụt v&agrave; thực h&agrave;nh đăng k&yacute; danh mục tr&ecirc;n phần mềm Ecuss5 Vnaccs</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Định mức, hao hụt</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Kh&aacute;i niệm định mức, hao hụt, định mức trung b&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;ch t&iacute;nh định mức trung b&igrave;nh, c&aacute;ch c&acirc;n đối định mức.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;c kinh nghiệm xử l&yacute; khi định mức thực tế kh&aacute;c với định mức kỹ thuật sản xuất.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><em>Thực h&agrave;nh đăng k&yacute; danh mục tr&ecirc;n phần mềm ECUSS5 VNACSS</em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Đăng k&yacute; danh mục Nguy&ecirc;n phụ liệu, sản phẩm, định mức.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Đăng k&yacute; Cơ sở sản xuất khai b&aacute;o điện tử.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Đăng k&yacute; tờ khai xuất khẩu, nhập khẩu v&agrave; c&aacute;c thủ tục li&ecirc;n quan.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><strong>Buổi 3-4: Thực h&agrave;nh về quy tr&igrave;nh l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n v&agrave; c&aacute;c c&acirc;u hỏi li&ecirc;n quan</strong></span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Tham khảo mẫu v&agrave; số liệu b&aacute;o c&aacute;o quyết to&aacute;n của 1 doanh nghiệp đ&atilde; l&agrave;m v&agrave; khai b&aacute;o ho&agrave;n chỉnh</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Thực h&agrave;nh l&agrave;m mẫu 15 &ndash; Nguy&ecirc;n phụ liệu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Thực h&agrave;nh l&agrave;m mẫu 15a &ndash; Sản phẩm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Thực h&agrave;nh l&agrave;m mẫu 16 &ndash; Định mức</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Hướng dẫn c&aacute;ch khớp số liệu giữa 3 bộ phận Kho &ndash; Kế to&aacute;n &ndash; Xuất nhập khẩu trong doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Thực h&agrave;nh khai b&aacute;o B&aacute;o c&aacute;o quyết to&aacute;n theo th&ocirc;ng tư 39.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Tư vấn về Thanh khoản.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Tư vấn về những lỗi thường gặp khi thực hiện l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n v&agrave; c&aacute;ch khắc phục đối với ch&iacute;nh doanh nghiệp của học vi&ecirc;n đang gặp phải.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- C&aacute;c biểu mẫu c&oacute; li&ecirc;n quan theo th&ocirc;ng tư 128, th&ocirc;ng tư 34, th&ocirc;ng tư 22&hellip;</span></p>\r\n\r\n<p><strong><span style=\"font-size:14px\">Th&ocirc;ng tin giảng vi&ecirc;n: Mr Nguyễn Mạnh Huynh</span></strong></p>\r\n\r\n<p><span style=\"font-size:14px\">Kinh nghiệm l&agrave;m việc:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- 5 năm l&agrave;m việc cho c&ocirc;ng ty cổ phần ph&aacute;t triển c&ocirc;ng nghệ Th&aacute;i Sơn, vị tr&iacute; l&agrave;m thanh khoản h&agrave;ng gia c&ocirc;ng, sản xuất xuất khẩu.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Nhiều năm&nbsp;l&agrave;m việc với vị tr&iacute; l&agrave;m v&agrave; r&agrave; so&aacute;t&nbsp;b&aacute;o c&aacute;o quyết to&aacute;n cho c&aacute;c C&ocirc;ng ty.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">- Nắm r&otilde; th&ocirc;ng tư, nghị định của hải quan về việc l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n, đ&atilde; từng tham gia l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n cho những doanh nghiệp sản xuất xuất khẩu, gia c&ocirc;ng v&agrave; chế xuất.</span></p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/bao%20cao%20quyet%20toan.jpg\" style=\"height:375px; width:500px\" /></p>\r\n\r\n<p><span style=\"font-size:14px\">Với những kiến thức, kinh nghiệm c&oacute; được từ những trải nghiệm thực tế l&agrave;m b&aacute;o c&aacute;o quyết to&aacute;n cho c&aacute;c doanh nghiệp gia c&ocirc;ng, chế xuất, sản xuất xuất&nbsp; khẩu. Cũng như sự nhiệt t&igrave;nh trong qu&aacute; tr&igrave;nh giảng dạy, hỗ trợ học vi&ecirc;n sau kh&oacute;a học, ch&uacute;ng t&ocirc;i tự tin c&oacute; thể mang lại nhiều gi&aacute; trị nhất cho c&aacute;c học vi&ecirc;n.</span></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><span style=\"font-size:14px\">Lịch học: Bạn chỉ cần bỏ ra 2 ng&agrave;y Chủ Nhật để l&agrave;m chủ kiến thức của kh&oacute;a học n&agrave;y</span></em></span></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><span style=\"font-size:14px\">Giờ học: s&aacute;ng 09h00&nbsp;- 11h30, chiều 13h00-15h30</span></em></span></p>\r\n\r\n<p><span style=\"color:#FF0000\"><em><span style=\"font-size:14px\">Lịch khai giảng: Li&ecirc;n hệ hotline <strong>0974 155 033</strong> để biết th&ecirc;m chi tiết</span></em></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Địa điểm: CLB Y&ecirc;u xuất nhập khẩu H&agrave; Nội - Han Exim Club,&nbsp;cơ sở 2, t&ograve;a nh&agrave; Vietcombank số 52 Đường hồ T&ugrave;ng Mậu, quận Cầu Giấy, H&agrave; Nội.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hotline:&nbsp;0974155033- Mr Long</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Học ph&iacute;: 2.000.000 VND</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><a href=\"https://docs.google.com/forms/d/e/1FAIpQLSdK7vPbgEEvrgZY6KBs0npd0igmlaIeMQwIg4r0hAsN34HqQQ/viewform\" target=\"_blank\"><img alt=\"\" src=\"/uploads/ckfinder/images/dang-ky-button.png\" style=\"height:57px; width:150px\" /></a></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Hướng dẫn thanh to&aacute;n:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phương &aacute;n 1:&nbsp;Đến đăng k&yacute; học trực tiếp tại cơ sở 1 của CLB Y&ecirc;u xuất nhập khẩu H&agrave; Nội -&nbsp;&nbsp;HAN EXIM CLUB. Địa chỉ Số 4 - ng&aacute;ch 23 &ndash; ng&otilde; 82 &ndash; phố ch&ugrave;a L&aacute;ng &ndash; Quận Đống Đa &ndash; H&agrave; Nội.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Phương &aacute;n 2: Thanh to&aacute;n online:</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ng&acirc;n h&agrave;ng Kỹ Thương Việt Nam Techcombank H&agrave; Nội.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Chủ t&agrave;i khoản: Trần Duy Long</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Số t&agrave;i khoản: 19028609613011</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nội dung chuyển khoản: BCQT_T&Ecirc;N_SĐT</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi nhận được th&ocirc;ng tin chuyển khoản, ch&uacute;ng t&ocirc;i sẽ li&ecirc;n hệ lại để x&aacute;c nhận đăng k&yacute; th&agrave;nh c&ocirc;ng.</span></p>\r\n\r\n<p>&nbsp;</p>',1,'vi','huong-dan-thuc-hanh-lam-bao-cao-quyet-toan-theo-thong-tu-39-2018','introduction',27,26,'2018-12-12 12:02:03','2019-07-04 09:29:52');

/*Table structure for table `ad_link` */

DROP TABLE IF EXISTS `ad_link`;

CREATE TABLE `ad_link` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT 'Tên link',
  `link` varchar(255) DEFAULT NULL COMMENT 'Đường dẫn',
  `priority` bigint(20) DEFAULT NULL,
  `type` bigint(20) DEFAULT '1' COMMENT '1: kiểu link cột bên phải, 2 là link footer',
  `is_active` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'Trạng thái duyệt bài viết',
  `created_by` bigint(20) DEFAULT NULL,
  `updated_by` bigint(20) DEFAULT NULL,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `lang` varchar(25) DEFAULT 'vi',
  PRIMARY KEY (`id`),
  KEY `created_by_idx` (`created_by`),
  KEY `updated_by_idx` (`updated_by`)
) ENGINE=MyISAM AUTO_INCREMENT=36 DEFAULT CHARSET=utf8;

